'use client'

/**
 * Modern 2025 Homepage Design
 * 
 * Features:
 * - Soothing multi-tonal color palettes
 * - Bold, expressive typography
 * - Dark mode support
 * - Micro-interactions and animations
 * - 3D-inspired visual elements
 * - Mobile-first responsive design
 * - Sustainable performance optimizations
 */

import { useState, useEffect, useRef } from 'react'
import { motion, useScroll, useTransform, AnimatePresence } from 'framer-motion'
import { useI18n } from '@/lib/i18n/useI18n'
import { useAuth } from '../../hooks/useAuth'
import Link from 'next/link'
import { Button } from '@/components/ui/button'
import dynamic from 'next/dynamic'

// Lazy load SupportChat to reduce initial bundle size
const SupportChat = dynamic(() => import('@/components').then(mod => ({ default: mod.SupportChat })), {
  ssr: false,
  loading: () => <div className="p-4 text-sm text-muted-foreground">Loading chat...</div>
})
import { 
  Sparkles, 
  Zap, 
  Shield, 
  Globe, 
  ArrowRight, 
  CheckCircle2,
  Search,
  MessageSquare,
  Calendar,
  MapPin,
  TrendingUp
} from 'lucide-react'

export default function HomePageModern2025() {
  const { t, formatCurrency } = useI18n()
  const { isAuthenticated, canBookServices, canOfferServices, _loading } = useAuth()
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 })
  const [isDark, setIsDark] = useState(false)
  const [showSupportChat, setShowSupportChat] = useState(false)
  const helpButtonRef = useRef<HTMLButtonElement>(null)
  const heroRef = useRef<HTMLDivElement>(null)
  const { scrollYProgress } = useScroll()
  const opacity = useTransform(scrollYProgress, [0, 0.5], [1, 0])
  const scale = useTransform(scrollYProgress, [0, 0.5], [1, 0.95])

  // Track mouse for parallax effects
  useEffect(() => {
    const handleMouseMove = (e: MouseEvent) => {
      setMousePosition({
        x: (e.clientX / window.innerWidth - 0.5) * 20,
        y: (e.clientY / window.innerHeight - 0.5) * 20,
      })
    }
    window.addEventListener('mousemove', handleMouseMove)
    return () => window.removeEventListener('mousemove', handleMouseMove)
  }, [])

  // Detect dark mode preference
  useEffect(() => {
    const mediaQuery = window.matchMedia('(prefers-color-scheme: dark)')
    setIsDark(mediaQuery.matches)
    const handler = (e: MediaQueryListEvent) => setIsDark(e.matches)
    mediaQuery.addEventListener('change', handler)
    return () => mediaQuery.removeEventListener('change', handler)
  }, [])

  const features = [
    {
      icon: Zap,
      title: 'AI-Powered Booking',
      description: 'Natural language conversation to find and book services instantly',
      gradient: 'from-yellow-400 via-orange-500 to-red-500',
    },
    {
      icon: Shield,
      title: '$1 Commitment Fee',
      description: 'Guaranteed bookings with minimal commitment, maximum protection',
      gradient: 'from-green-400 via-emerald-500 to-teal-500',
    },
    {
      icon: MapPin,
      title: 'Privacy-First Maps',
      description: 'Smart radius scaling finds nearby services while protecting provider privacy',
      gradient: 'from-blue-400 via-indigo-500 to-purple-500',
    },
    {
      icon: Globe,
      title: 'Universal Platform',
      description: '37 countries, 27 currencies, 17 languages - truly global',
      gradient: 'from-cyan-400 via-blue-500 to-indigo-500',
    },
  ]

  const stats = [
    { value: '37', label: 'Countries', icon: Globe },
    { value: '27', label: 'Currencies', icon: TrendingUp },
    { value: '17', label: 'Languages', icon: MessageSquare },
  ]

  return (
    <div className={`min-h-screen transition-colors duration-500 ${
      isDark 
        ? 'bg-gradient-to-br from-gray-900 via-slate-900 to-zinc-900' 
        : 'bg-gradient-to-br from-slate-50 via-blue-50 to-purple-50'
    }`}>
      {/* Animated Background Elements */}
      <div className="fixed inset-0 overflow-hidden pointer-events-none">
        <motion.div
          className="absolute top-1/4 left-1/4 w-96 h-96 bg-gradient-to-r from-blue-400/20 to-purple-400/20 rounded-full blur-3xl"
          animate={{
            x: mousePosition.x,
            y: mousePosition.y,
          }}
          transition={{ type: 'spring', stiffness: 50, damping: 20 }}
        />
        <motion.div
          className="absolute bottom-1/4 right-1/4 w-96 h-96 bg-gradient-to-r from-pink-400/20 to-orange-400/20 rounded-full blur-3xl"
          animate={{
            x: -mousePosition.x * 0.5,
            y: -mousePosition.y * 0.5,
          }}
          transition={{ type: 'spring', stiffness: 50, damping: 20 }}
        />
      </div>

      {/* Hero Section */}
      <motion.section
        ref={heroRef}
        style={{ opacity, scale }}
        className="relative min-h-screen flex items-center justify-center px-4 sm:px-6 lg:px-8 pt-20"
      >
        <div className="max-w-7xl mx-auto text-center z-10">
          {/* Badge */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            className="inline-flex items-center gap-2 px-4 py-2 rounded-full bg-white/10 backdrop-blur-md border border-white/20 mb-8"
          >
            <Sparkles className="w-4 h-4 text-yellow-400" />
            <span className={`text-sm font-medium ${isDark ? 'text-gray-200' : 'text-gray-700'}`}>
              Universal Booking Platform
            </span>
          </motion.div>

          {/* Main Headline - Bold Typography */}
          <motion.h1
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
            className={`text-5xl md:text-7xl lg:text-8xl font-black mb-6 leading-tight ${
              isDark ? 'text-white' : 'text-gray-900'
            }`}
          >
            <span className="bg-gradient-to-r from-blue-600 via-purple-600 to-pink-600 bg-clip-text text-transparent">
              Book Any Service,
            </span>
            <br />
            <span className={isDark ? 'text-white' : 'text-gray-900'}>
              Anywhere
            </span>
          </motion.h1>

          {/* Subheadline */}
          <motion.p
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
            className={`text-xl md:text-2xl lg:text-3xl mb-4 font-light ${
              isDark ? 'text-gray-300' : 'text-gray-600'
            }`}
          >
            {t('home.tagline', { fee: formatCurrency(1) })}
          </motion.p>

          <motion.p
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.5 }}
            className={`text-lg md:text-xl mb-12 ${
              isDark ? 'text-gray-400' : 'text-gray-500'
            }`}
          >
            AI-powered â€¢ Privacy-first â€¢ Guaranteed bookings
          </motion.p>

          {/* CTA Buttons */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.6 }}
            className="flex flex-col sm:flex-row gap-4 justify-center items-center mb-16"
          >
            {!isAuthenticated ? (
              <>
                <Link href="/register?role=customer">
                  <motion.div
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                  >
                    <Button
                      size="lg"
                      className="group h-16 px-10 text-lg font-semibold bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white shadow-2xl rounded-2xl"
                    >
                      <span className="flex items-center gap-2">
                        {t('cta.start_booking')}
                        <ArrowRight className="w-5 h-5 group-hover:translate-x-1 transition-transform" />
                      </span>
                      <span className="block text-xs mt-1 opacity-90">(Customer)</span>
                    </Button>
                  </motion.div>
                </Link>
                <Link href="/register?role=provider">
                  <motion.div
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                  >
                    <Button
                      size="lg"
                      variant="outline"
                      className="h-16 px-10 text-lg font-semibold border-2 rounded-2xl backdrop-blur-md bg-white/10 hover:bg-white/20"
                    >
                      <span className="flex items-center gap-2">
                        {t('buttons.offer_services')}
                        <ArrowRight className="w-5 h-5" />
                      </span>
                      <span className="block text-xs mt-1 opacity-90">(Provider)</span>
                    </Button>
                  </motion.div>
                </Link>
              </>
            ) : (
              <>
                {canBookServices && (
                  <Link href="/customer/dashboard">
                    <Button size="lg" className="h-16 px-10 text-lg">
                      Go to Dashboard
                    </Button>
                  </Link>
                )}
                {canOfferServices && (
                  <Link href="/vendor/dashboard">
                    <Button size="lg" variant="outline" className="h-16 px-10 text-lg">
                      Provider Portal
                    </Button>
                  </Link>
                )}
              </>
            )}
          </motion.div>

          {/* Search Bar */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.7 }}
            className="max-w-2xl mx-auto mb-12"
          >
            <div className="relative">
              <Search className={`absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 ${
                isDark ? 'text-gray-400' : 'text-gray-500'
              }`} />
              <input
                type="text"
                placeholder={t('home.search_placeholder') || 'Search for services...'}
                className={`w-full pl-12 pr-4 py-4 text-lg rounded-2xl border-2 backdrop-blur-md transition-all focus:outline-none focus:ring-4 focus:ring-blue-500/20 ${
                  isDark
                    ? 'bg-white/10 border-white/20 text-white placeholder-gray-400'
                    : 'bg-white/80 border-gray-200 text-gray-900 placeholder-gray-500'
                }`}
              />
            </div>
          </motion.div>
        </div>
      </motion.section>

      {/* Features Grid */}
      <section className="relative py-24 px-4 sm:px-6 lg:px-8">
        <div className="max-w-7xl mx-auto">
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8 }}
            className="text-center mb-16"
          >
            <h2 className={`text-4xl md:text-5xl font-black mb-4 ${
              isDark ? 'text-white' : 'text-gray-900'
            }`}>
              Why Bookiji?
            </h2>
            <p className={`text-xl ${
              isDark ? 'text-gray-400' : 'text-gray-600'
            }`}>
              The future of service booking is here
            </p>
          </motion.div>

          <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-8">
            {features.map((feature, index) => {
              const Icon = feature.icon
              return (
                <motion.div
                  key={feature.title}
                  initial={{ opacity: 0, y: 30 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  viewport={{ once: true }}
                  transition={{ duration: 0.6, delay: index * 0.1 }}
                  whileHover={{ y: -8 }}
                  className={`group relative p-8 rounded-3xl backdrop-blur-md border-2 transition-all ${
                    isDark
                      ? 'bg-white/5 border-white/10 hover:border-white/30'
                      : 'bg-white/60 border-gray-200 hover:border-gray-300 shadow-xl'
                  }`}
                >
                  {/* Gradient Background on Hover */}
                  <div className={`absolute inset-0 bg-gradient-to-br ${feature.gradient} opacity-0 group-hover:opacity-10 rounded-3xl transition-opacity`} />

                  <div className={`relative z-10 w-16 h-16 rounded-2xl bg-gradient-to-br ${feature.gradient} flex items-center justify-center mb-6 shadow-lg`}>
                    <Icon className="w-8 h-8 text-white" />
                  </div>

                  <h3 className={`text-xl font-bold mb-3 ${
                    isDark ? 'text-white' : 'text-gray-900'
                  }`}>
                    {feature.title}
                  </h3>

                  <p className={`text-sm leading-relaxed ${
                    isDark ? 'text-gray-400' : 'text-gray-600'
                  }`}>
                    {feature.description}
                  </p>
                </motion.div>
              )
            })}
          </div>
        </div>
      </section>

      {/* Stats Section */}
      <section className={`relative py-20 px-4 sm:px-6 lg:px-8 ${
        isDark ? 'bg-white/5' : 'bg-white/40'
      } backdrop-blur-md`}>
        <div className="max-w-6xl mx-auto">
          <div className="grid md:grid-cols-3 gap-12">
            {stats.map((stat, index) => {
              const Icon = stat.icon
              return (
                <motion.div
                  key={stat.label}
                  initial={{ opacity: 0, scale: 0.9 }}
                  whileInView={{ opacity: 1, scale: 1 }}
                  viewport={{ once: true }}
                  transition={{ duration: 0.6, delay: index * 0.1 }}
                  className="text-center"
                >
                  <div className={`inline-flex items-center justify-center w-20 h-20 rounded-2xl mb-6 ${
                    isDark ? 'bg-white/10' : 'bg-gradient-to-br from-blue-500 to-purple-500'
                  }`}>
                    <Icon className={`w-10 h-10 ${
                      isDark ? 'text-blue-400' : 'text-white'
                    }`} />
                  </div>
                  <div className={`text-5xl md:text-6xl font-black mb-2 bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent`}>
                    {stat.value}
                  </div>
                  <div className={`text-lg font-medium ${
                    isDark ? 'text-gray-400' : 'text-gray-600'
                  }`}>
                    {stat.label}
                  </div>
                </motion.div>
              )
            })}
          </div>
        </div>
      </section>

      {/* How It Works */}
      <section className="relative py-24 px-4 sm:px-6 lg:px-8">
        <div className="max-w-6xl mx-auto">
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8 }}
            className="text-center mb-16"
          >
            <h2 className={`text-4xl md:text-5xl font-black mb-4 ${
              isDark ? 'text-white' : 'text-gray-900'
            }`}>
              How It Works
            </h2>
          </motion.div>

          <div className="grid md:grid-cols-4 gap-8">
            {[
              { step: '1', title: 'Search', desc: 'Find services near you', icon: Search },
              { step: '2', title: 'Chat', desc: 'AI helps you book naturally', icon: MessageSquare },
              { step: '3', title: 'Book', desc: 'Secure with $1 commitment', icon: Calendar },
              { step: '4', title: 'Enjoy', desc: 'Get the service you need', icon: CheckCircle2 },
            ].map((item, index) => {
              const Icon = item.icon
              return (
                <motion.div
                  key={item.step}
                  initial={{ opacity: 0, y: 30 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  viewport={{ once: true }}
                  transition={{ duration: 0.6, delay: index * 0.1 }}
                  className="relative"
                >
                  <div className={`absolute -top-4 -left-4 w-12 h-12 rounded-full bg-gradient-to-br from-blue-500 to-purple-500 flex items-center justify-center text-white font-black text-xl shadow-lg`}>
                    {item.step}
                  </div>
                  <div className={`p-8 rounded-3xl backdrop-blur-md border-2 ${
                    isDark
                      ? 'bg-white/5 border-white/10'
                      : 'bg-white/60 border-gray-200'
                  }`}>
                    <Icon className={`w-12 h-12 mb-4 ${
                      isDark ? 'text-blue-400' : 'text-blue-600'
                    }`} />
                    <h3 className={`text-xl font-bold mb-2 ${
                      isDark ? 'text-white' : 'text-gray-900'
                    }`}>
                      {item.title}
                    </h3>
                    <p className={`text-sm ${
                      isDark ? 'text-gray-400' : 'text-gray-600'
                    }`}>
                      {item.desc}
                    </p>
                  </div>
                </motion.div>
              )
            })}
          </div>
        </div>
      </section>

      {/* Final CTA */}
      <section className={`relative py-24 px-4 sm:px-6 lg:px-8 ${
        isDark ? 'bg-gradient-to-r from-blue-900/50 to-purple-900/50' : 'bg-gradient-to-r from-blue-50 to-purple-50'
      }`}>
        <div className="max-w-4xl mx-auto text-center">
          <motion.div
            initial={{ opacity: 0, scale: 0.9 }}
            whileInView={{ opacity: 1, scale: 1 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8 }}
          >
            <h2 className={`text-4xl md:text-5xl font-black mb-6 ${
              isDark ? 'text-white' : 'text-gray-900'
            }`}>
              Ready to Get Started?
            </h2>
            <p className={`text-xl mb-8 ${
              isDark ? 'text-gray-300' : 'text-gray-600'
            }`}>
              Join thousands of users booking services worldwide
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <Link href="/register?role=customer">
                <Button
                  size="lg"
                  className="h-16 px-12 text-lg font-semibold bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white shadow-2xl rounded-2xl"
                >
                  {t('cta.start_booking_now')}
                </Button>
              </Link>
              <Link href="/register?role=provider">
                <Button
                  size="lg"
                  variant="outline"
                  className="h-16 px-12 text-lg font-semibold rounded-2xl"
                >
                  Become a Provider
                </Button>
              </Link>
            </div>
          </motion.div>
        </div>
      </section>

      {/* Support Chat Button (3D Magenta) - Bottom Right */}
      <motion.button
        ref={helpButtonRef}
        onClick={(e: React.MouseEvent<HTMLButtonElement>) => {
          e.preventDefault()
          e.stopPropagation()
          setShowSupportChat(!showSupportChat)
        }}
        className="fixed bottom-6 right-6 w-16 h-16 rounded-full flex items-center justify-center text-white cursor-pointer z-[100]"
        style={{ 
          pointerEvents: 'auto',
          zIndex: 100,
          background: 'linear-gradient(135deg, #d946ef 0%, #a855f7 50%, #9333ea 100%)',
          boxShadow: showSupportChat 
            ? '0 20px 40px rgba(217, 70, 239, 0.4), 0 0 0 4px rgba(217, 70, 239, 0.1), inset 0 2px 4px rgba(255, 255, 255, 0.3), inset 0 -2px 4px rgba(0, 0, 0, 0.2)'
            : '0 10px 30px rgba(217, 70, 239, 0.3), 0 0 0 1px rgba(255, 255, 255, 0.1) inset, inset 0 2px 4px rgba(255, 255, 255, 0.2), inset 0 -2px 4px rgba(0, 0, 0, 0.3)',
          transform: 'perspective(1000px) rotateX(5deg)',
        }}
        whileHover={{ 
          scale: 1.1,
          boxShadow: '0 20px 40px rgba(217, 70, 239, 0.5), 0 0 0 4px rgba(217, 70, 239, 0.2), inset 0 2px 4px rgba(255, 255, 255, 0.4), inset 0 -2px 4px rgba(0, 0, 0, 0.3)',
          y: -2
        }}
        whileTap={{ 
          scale: 0.95,
          boxShadow: '0 5px 15px rgba(217, 70, 239, 0.4), inset 0 2px 4px rgba(0, 0, 0, 0.3)'
        }}
        animate={{
          rotate: [0, 5, -5, 0],
        }}
        transition={{
          rotate: {
            duration: 3,
            repeat: Infinity,
            repeatType: "reverse",
            ease: "easeInOut"
          }
        }}
        aria-label="Open Support Chat"
        type="button"
        data-testid="help-button"
      >
        <span 
          className="text-3xl font-bold pointer-events-none"
          style={{
            textShadow: '0 2px 4px rgba(0, 0, 0, 0.3), 0 0 10px rgba(255, 255, 255, 0.2)',
            filter: 'drop-shadow(0 1px 2px rgba(0, 0, 0, 0.2))'
          }}
        >
          ?
        </span>
        {/* Glossy highlight overlay */}
        <div 
          className="absolute inset-0 rounded-full pointer-events-none"
          style={{
            background: 'radial-gradient(circle at 30% 30%, rgba(255, 255, 255, 0.4) 0%, transparent 60%)',
            mixBlendMode: 'overlay'
          }}
        />
      </motion.button>

      {/* Support Chat Widget */}
      <AnimatePresence>
        {showSupportChat && (
          <motion.div
            className="fixed bottom-24 right-6 z-[99] w-[350px] h-[500px] bg-background rounded-xl shadow-2xl border border-border overflow-hidden"
            initial={{ opacity: 0, y: 20, scale: 0.95 }}
            animate={{ opacity: 1, y: 0, scale: 1 }}
            exit={{ opacity: 0, y: 20, scale: 0.95 }}
          >
            <SupportChat onCloseAction={() => setShowSupportChat(false)} />
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  )
}

import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card'
import { METRICS_REGISTRY } from '@/app/api/ops/controlplane/_lib/simcity-metrics'
import { DEFAULT_DIALS } from '@/app/api/ops/controlplane/_lib/simcity-dials'
import type { DialStatus, MetricId } from '@/app/api/ops/controlplane/_lib/simcity-types'

async function getMetricsData() {
  const baseUrl = process.env.NEXT_PUBLIC_BASE_URL || 'http://localhost:3000'
  
  try {
    const response = await fetch(`${baseUrl}/api/ops/controlplane/simcity/dials/snapshot`, {
      cache: 'no-store',
    })

    if (!response.ok) {
      throw new Error(`API returned ${response.status}`)
    }

    const data = await response.json()
    return {
      tick: data.tick || 0,
      metrics: data.metrics || {} as Record<MetricId, number>,
      dialStatuses: (data.dialStatuses || []) as DialStatus[],
    }
  } catch (error) {
    console.error('Failed to fetch metrics data:', error)
    return {
      tick: 0,
      metrics: {} as Record<MetricId, number>,
      dialStatuses: [] as DialStatus[],
      error: error instanceof Error ? error.message : 'Unknown error',
    }
  }
}

function getZoneColor(zone: 'green' | 'yellow' | 'red') {
  switch (zone) {
    case 'green':
      return 'bg-green-100 text-green-800 border-green-300'
    case 'yellow':
      return 'bg-yellow-100 text-yellow-800 border-yellow-300'
    case 'red':
      return 'bg-red-100 text-red-800 border-red-300'
  }
}

function formatMetricValue(value: number, unit: string): string {
  if (unit === 'ratio') {
    return (value * 100).toFixed(2) + '%'
  }
  return value.toFixed(2) + ' ' + unit
}

export default async function CockpitMetricsPage() {
  const { tick, metrics, dialStatuses, error } = await getMetricsData()

  const _dialMap = new Map(DEFAULT_DIALS.map(d => [d.metric, d]))
  const dialStatusMap = new Map(dialStatuses.map(d => [d.metric, d]))

  if (error) {
    return (
      <Card>
        <CardHeader>
          <CardTitle>Erreur</CardTitle>
        </CardHeader>
        <CardContent>
          <p className="text-red-600">Impossible de charger les donnÃ©es : {error}</p>
        </CardContent>
      </Card>
    )
  }

  return (
    <div className="space-y-6">
      {/* Dials Overview */}
      <Card>
        <CardHeader>
          <CardTitle>Dials (6 dials)</CardTitle>
          <CardDescription>
            Zones de couleur et seuils pour chaque mÃ©trique (read-only)
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {DEFAULT_DIALS.map((dial) => {
              const dialStatus = dialStatusMap.get(dial.metric)
              const metric = METRICS_REGISTRY[dial.metric]
              const value = metrics[dial.metric] ?? 0

              return (
                <div
                  key={dial.metric}
                  className={`p-4 rounded-lg border-2 ${
                    dialStatus
                      ? getZoneColor(dialStatus.zone)
                      : 'bg-gray-100 text-gray-800 border-gray-300'
                  }`}
                >
                  <div className="font-mono text-sm font-semibold mb-2">
                    {dial.metric}
                  </div>
                  <div className="text-2xl font-bold mb-3">
                    {formatMetricValue(value, metric.unit)}
                  </div>
                  <div className="space-y-1 text-xs">
                    <div>
                      ðŸŸ¢ Green: [{dial.green[0]}, {dial.green[1]}]
                    </div>
                    <div>
                      ðŸŸ¡ Yellow: [{dial.yellow[0]}, {dial.yellow[1]}]
                    </div>
                    <div>
                      ðŸ”´ Red: [{dial.red[0]}, {dial.red[1]}]
                    </div>
                  </div>
                  {dialStatus && (
                    <div className="mt-2 text-xs font-semibold">
                      Zone: {dialStatus.zone.toUpperCase()}
                    </div>
                  )}
                </div>
              )
            })}
          </div>
        </CardContent>
      </Card>

      {/* Metrics Registry */}
      <Card>
        <CardHeader>
          <CardTitle>Metrics Registry</CardTitle>
          <CardDescription>
            Liste complÃ¨te de toutes les mÃ©triques avec leurs dÃ©finitions
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="overflow-x-auto">
            <table className="w-full border-collapse">
              <thead>
                <tr className="border-b border-gray-200">
                  <th className="text-left py-3 px-4 font-semibold text-gray-700">Metric ID</th>
                  <th className="text-left py-3 px-4 font-semibold text-gray-700">Domain</th>
                  <th className="text-left py-3 px-4 font-semibold text-gray-700">Description</th>
                  <th className="text-left py-3 px-4 font-semibold text-gray-700">Unit</th>
                  <th className="text-left py-3 px-4 font-semibold text-gray-700">Direction</th>
                  <th className="text-right py-3 px-4 font-semibold text-gray-700">Current Value</th>
                </tr>
              </thead>
              <tbody>
                {Object.entries(METRICS_REGISTRY).map(([metricId, metric]) => {
                  const value = metrics[metricId as MetricId] ?? 0
                  const dialStatus = dialStatusMap.get(metricId as MetricId)

                  return (
                    <tr key={metricId} className="border-b border-gray-100 hover:bg-gray-50">
                      <td className="py-3 px-4">
                        <span className="font-mono text-sm font-semibold">{metricId}</span>
                      </td>
                      <td className="py-3 px-4 text-sm text-gray-600">{metric.domain}</td>
                      <td className="py-3 px-4 text-sm text-gray-700">{metric.description}</td>
                      <td className="py-3 px-4 text-sm text-gray-600">{metric.unit}</td>
                      <td className="py-3 px-4">
                        <span className="text-xs px-2 py-1 rounded bg-blue-100 text-blue-800">
                          {metric.direction === 'higher-is-better' ? 'â†‘ Higher is better' : 'â†“ Lower is better'}
                        </span>
                      </td>
                      <td className="py-3 px-4 text-right">
                        <div className="flex items-center justify-end gap-2">
                          <span className="font-mono font-semibold">
                            {formatMetricValue(value, metric.unit)}
                          </span>
                          {dialStatus && (
                            <span className={`text-xs px-2 py-1 rounded font-semibold ${getZoneColor(dialStatus.zone)}`}>
                              {dialStatus.zone.toUpperCase()}
                            </span>
                          )}
                        </div>
                      </td>
                    </tr>
                  )
                })}
              </tbody>
            </table>
          </div>
        </CardContent>
      </Card>

      {/* System Info */}
      <Card>
        <CardHeader>
          <CardTitle>Informations SystÃ¨me</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-2 gap-4">
            <div>
              <span className="text-sm text-gray-600">Tick actuel:</span>
              <span className="ml-2 font-mono font-semibold">{tick}</span>
            </div>
            <div>
              <span className="text-sm text-gray-600">Nombre de mÃ©triques:</span>
              <span className="ml-2 font-semibold">{Object.keys(METRICS_REGISTRY).length}</span>
            </div>
            <div>
              <span className="text-sm text-gray-600">Nombre de dials:</span>
              <span className="ml-2 font-semibold">{DEFAULT_DIALS.length}</span>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  )
}

'use client';

import React, { useEffect, useState, useRef, useMemo } from 'react';
import { 
  Activity, 
  _Play, 
  Square, 
  Clock, 
  ShieldAlert, 
  _History, 
  _ChevronRight, 
  _Search,
  _Filter,
  _BarChart3,
  List,
  AlertCircle,
  Zap,
  RefreshCcw,
  _Copy,
  _Terminal,
  _Database,
  ArrowRight,
  Globe,
  Cpu
} from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  _LineChart, 
  _Line, 
  _XAxis, 
  _YAxis, 
  _CartesianGrid, 
  _Tooltip, 
  _ResponsiveContainer,
  _AreaChart,
  _Area
} from 'recharts';

import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Input } from '@/components/ui/input';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Slider } from '@/components/ui/slider';

// --- Types ---

interface OpsEvent {
  id: string;
  ts: string;
  source: 'prod' | 'simcity';
  run_id: string | null;
  provider_id: string | null;
  type: string;
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  payload: any;
}

interface OpsMetrics {
  bookings_created_count: number;
  cancellations_count: number;
  reschedules_count: number;
  active_bookings_count: number;
  slots_available_count: number;
  slots_unavailable_count: number;
  invariant_failures_count: number;
}

interface _FusedMetrics {
  prod: OpsMetrics;
  simcity: OpsMetrics;
  fused: OpsMetrics;
}

interface SimCityRunRequest {
  id: string;
  status: string;
  tier: string;
  seed: number;
  created_at: string;
  run_id: string | null;
}

// --- Components ---

const EventDetailModal = ({ event, onClose }: { event: OpsEvent | null, onClose: () => void }) => {
  if (!event) return null;
  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-slate-950/80 backdrop-blur-sm">
      <div className="bg-slate-900 border border-slate-800 rounded-xl w-full max-w-2xl max-h-[90vh] flex flex-col shadow-2xl">
        <div className="p-4 border-b border-slate-800 flex justify-between items-center">
          <h3 className="text-cyan-400 font-mono font-bold">Event Details</h3>
          <Button variant="ghost" size="sm" onClick={onClose} className="text-slate-400 hover:text-white">
            <Square className="w-4 h-4" />
          </Button>
        </div>
        <div className="p-6 overflow-auto font-mono text-xs bg-slate-950 m-4 rounded-lg border border-slate-800">
          <div className="mb-4">
            <span className="text-slate-500">Source:</span> <Badge className={event.source === 'prod' ? 'bg-blue-500/20 text-blue-400' : 'bg-cyan-500/20 text-cyan-400'}>{event.source}</Badge>
          </div>
          <div className="mb-4">
            <span className="text-slate-500">Type:</span> <span className="text-slate-200">{event.type}</span>
          </div>
          <div className="mb-4">
            <span className="text-slate-500">Timestamp:</span> <span className="text-slate-200">{new Date(event.ts).toLocaleString()}</span>
          </div>
          {event.run_id && (
            <div className="mb-4">
              <span className="text-slate-500">Run ID:</span> <span className="text-slate-200">{event.run_id}</span>
            </div>
          )}
          <pre className="text-cyan-200/80">{JSON.stringify(event.payload, null, 2)}</pre>
        </div>
        <div className="p-4 border-t border-slate-800 flex justify-end">
          <Button variant="outline" size="sm" onClick={onClose}>Close</Button>
        </div>
      </div>
    </div>
  );
};

// eslint-disable-next-line @typescript-eslint/no-explicit-any
const KPICard = ({ title, value, splitValue, colorClass, icon: Icon }: any) => (
  <Card className="bg-slate-900/40 border-slate-800 shadow-none overflow-hidden group">
    <CardHeader className="p-4 pb-0 flex flex-row items-center justify-between">
      <CardTitle className="text-[10px] uppercase text-slate-500 font-bold tracking-widest">{title}</CardTitle>
      <Icon className={`w-3 h-3 ${colorClass} opacity-50 group-hover:opacity-100 transition-opacity`} />
    </CardHeader>
    <CardContent className="p-4">
      <div className={`text-3xl font-mono font-bold ${colorClass}`}>
        {value}
      </div>
      {splitValue && (
        <div className="flex items-center mt-2 space-x-3 text-[10px] font-mono text-slate-500">
          <span className="flex items-center"><Globe className="w-2.5 h-2.5 mr-1 text-blue-500/70" /> {splitValue.prod}</span>
          <span className="flex items-center"><Cpu className="w-2.5 h-2.5 mr-1 text-cyan-500/70" /> {splitValue.simcity}</span>
        </div>
      )}
    </CardContent>
  </Card>
);

export default function MissionControlPage() {
  const [mode, setMode] = useState<'FUSED' | 'LIVE' | 'SIMCITY'>('FUSED');
  const [events, setEvents] = useState<OpsEvent[]>([]);
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  const [metrics, setMetrics] = useState<any>(null);
  const [requests, setRequests] = useState<SimCityRunRequest[]>([]);
  const [selectedRunId, setSelectedRunId] = useState<string | null>(null);
  const [isLoading, _setIsLoading] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState<OpsEvent | null>(null);
  const [refreshInterval, _setRefreshInterval] = useState(5000);

  // Mode effects
  useEffect(() => {
    fetchData();
    const interval = setInterval(fetchData, refreshInterval);
    return () => clearInterval(interval);
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [mode, selectedRunId, refreshInterval]);

  useEffect(() => {
    if (mode === 'SIMCITY') {
      fetchRequests();
    }
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [mode]);

  const fetchData = async () => {
    try {
      const sourceMap = { 'FUSED': 'fused', 'LIVE': 'prod', 'SIMCITY': 'simcity' };
      const source = sourceMap[mode];
      
      let eventsUrl = `/api/ops/events?source=${source}&limit=50`;
      if (mode === 'SIMCITY' && selectedRunId) {
        eventsUrl += `&run_id=${selectedRunId}`;
      }
      
      const metricsUrl = `/api/ops/metrics?source=${source}&window_seconds=3600`;
      
      const [eventsRes, metricsRes] = await Promise.all([
        fetch(eventsUrl),
        fetch(metricsUrl)
      ]);
      
      const eventsData = await eventsRes.json();
      const metricsData = await metricsRes.json();
      
      setEvents(eventsData.data || []);
      setMetrics(metricsData.data);
    } catch (err) {
      console.error('Failed to fetch data', err);
    }
  };

  const fetchRequests = async () => {
    try {
      const res = await fetch('/api/ops/simcity/run-requests');
      if (!res.ok) {
        console.error('Failed to fetch requests:', res.statusText);
        setRequests([]);
        return;
      }
      const data = await res.json();
      // Ensure data is an array
      const requestsArray = Array.isArray(data) ? data : [];
      setRequests(requestsArray);
      if (requestsArray.length > 0 && !selectedRunId) {
        setSelectedRunId(requestsArray[0].run_id);
      }
    } catch (err) {
      console.error('Failed to fetch requests', err);
      setRequests([]); // Set to empty array on error
    }
  };

  const handleStartRun = async () => {
    try {
      await fetch('/api/ops/simcity/run', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          tier: 'A',
          seed: Math.floor(Math.random() * 1000000),
          concurrency: 5,
          max_events: 100,
          duration_seconds: 60
        })
      });
      fetchRequests();
    } catch (err) {
      console.error('Failed to start run', err);
    }
  };

  const displayMetrics = useMemo(() => {
    if (!metrics) return null;
    if (mode === 'FUSED') return metrics.fused;
    return metrics;
  }, [metrics, mode]);

  const splitMetrics = useMemo(() => {
    if (mode !== 'FUSED' || !metrics) return null;
    return {
      active: { prod: metrics.prod.active_bookings_count, simcity: metrics.simcity.active_bookings_count },
      created: { prod: metrics.prod.bookings_created_count, simcity: metrics.simcity.bookings_created_count },
      cancelled: { prod: metrics.prod.cancellations_count, simcity: metrics.simcity.cancellations_count },
      failures: { prod: metrics.prod.invariant_failures_count, simcity: metrics.simcity.invariant_failures_count }
    };
  }, [metrics, mode]);

  return (
    <div className="flex h-screen bg-slate-950 text-slate-100 overflow-hidden font-sans selection:bg-cyan-500/30">
      {/* Sidebar for SimCity runs */}
      {mode === 'SIMCITY' && (
        <div className="w-80 border-r border-slate-800 flex flex-col bg-slate-900/50 backdrop-blur-xl shrink-0">
          <div className="p-4 border-b border-slate-800 space-y-4">
            <h2 className="text-xs font-bold text-slate-500 uppercase tracking-widest">SimCity Runs</h2>
            <Button className="w-full bg-cyan-600 hover:bg-cyan-500 text-white shadow-lg" onClick={handleStartRun}>
              <Zap className="w-4 h-4 mr-2" /> New Run
            </Button>
          </div>
          <div className="flex-1 overflow-y-auto p-2 space-y-1">
            {Array.isArray(requests) && requests.length > 0 ? (
              requests.map((req) => (
                <button
                  key={req.id}
                  onClick={() => setSelectedRunId(req.run_id)}
                  className={`w-full text-left p-3 rounded-lg border transition-all ${
                    selectedRunId === req.run_id ? 'bg-cyan-500/10 border-cyan-500/50' : 'hover:bg-slate-800/50 border-transparent'
                  }`}
                >
                  <div className="flex justify-between items-center mb-1">
                    <span className="font-mono text-[10px] text-slate-500">#{req.run_id?.slice(0, 8) || 'PENDING'}</span>
                    <Badge variant="outline" className="text-[8px] uppercase">{req.status}</Badge>
                  </div>
                  <div className="text-sm font-medium">Tier {req.tier} â€¢ Seed {req.seed}</div>
                  <div className="text-[10px] text-slate-600 mt-1">{new Date(req.created_at).toLocaleString()}</div>
                </button>
              ))
            ) : (
              <div className="p-4 text-center text-slate-500 text-sm">
                No SimCity runs found
              </div>
            )}
          </div>
        </div>
      )}

      {/* Main Content Area */}
      <div className="flex-1 flex flex-col min-w-0">
        {/* Header with Mode Selector */}
        <header className="h-20 border-b border-slate-800 flex items-center justify-between px-8 bg-slate-900/30">
          <div className="flex items-center space-x-8">
            <h1 className="text-2xl font-black tracking-tighter bg-gradient-to-r from-blue-400 to-cyan-400 bg-clip-text text-transparent">MISSION CONTROL</h1>
            <div className="flex bg-slate-950 border border-slate-800 rounded-full p-1 p-x-2">
              {(['FUSED', 'LIVE', 'SIMCITY'] as const).map((m) => (
                <button
                  key={m}
                  onClick={() => setMode(m)}
                  className={`px-6 py-1.5 rounded-full text-[10px] font-bold tracking-widest transition-all ${
                    mode === m 
                      ? 'bg-gradient-to-r from-blue-600 to-cyan-600 text-white shadow-lg shadow-cyan-900/40' 
                      : 'text-slate-500 hover:text-slate-300'
                  }`}
                >
                  {m}
                </button>
              ))}
            </div>
          </div>
          <div className="flex items-center space-x-4">
            <div className="flex items-center space-x-2 bg-slate-900/50 border border-slate-800 px-3 py-1.5 rounded-lg">
              <div className={`w-2 h-2 rounded-full ${isLoading ? 'bg-amber-500 animate-pulse' : 'bg-emerald-500 shadow-[0_0_8px_rgba(16,185,129,0.5)]'}`} />
              <span className="text-[10px] font-mono text-slate-400 uppercase tracking-widest">{isLoading ? 'Syncing...' : 'Connected'}</span>
            </div>
            <Button variant="ghost" size="icon" onClick={fetchData} className="hover:bg-slate-800">
              <RefreshCcw className="w-4 h-4 text-slate-500" />
            </Button>
          </div>
        </header>

        {/* Dash Content */}
        <div className="flex-1 overflow-hidden flex flex-col p-8 space-y-8">
          {/* KPI Grid */}
          <div className="grid grid-cols-4 gap-6">
            <KPICard 
              title="Active Bookings" 
              value={displayMetrics?.active_bookings_count || 0} 
              splitValue={splitMetrics?.active}
              colorClass="text-emerald-400" 
              icon={Activity}
            />
            <KPICard 
              title="Bookings Created" 
              value={displayMetrics?.bookings_created_count || 0} 
              splitValue={splitMetrics?.created}
              colorClass="text-blue-400" 
              icon={Zap}
            />
            <KPICard 
              title="Cancellations" 
              value={displayMetrics?.cancellations_count || 0} 
              splitValue={splitMetrics?.cancelled}
              colorClass="text-rose-400" 
              icon={AlertCircle}
            />
            <KPICard 
              title="Invariant Failures" 
              value={displayMetrics?.invariant_failures_count || 0} 
              splitValue={splitMetrics?.failures}
              colorClass="text-amber-400" 
              icon={ShieldAlert}
            />
          </div>

          {/* Timeline Lanes */}
          <div className="bg-slate-900/40 border border-slate-800 rounded-2xl p-6 flex flex-col">
            <h3 className="text-xs font-bold uppercase tracking-widest text-slate-500 mb-6 flex items-center">
              <Clock className="w-4 h-4 mr-2 text-blue-500" />
              Fusion Ops Stream (Last 50 Events)
            </h3>
            
            <div className="space-y-6">
              {/* Prod Lane */}
              {(mode === 'FUSED' || mode === 'LIVE') && (
                <div className="relative group">
                  <div className="absolute -left-12 top-1/2 -translate-y-1/2 -rotate-90 text-[8px] font-black tracking-tighter text-blue-500/40 uppercase group-hover:text-blue-400 transition-colors">PROD</div>
                  <div className="h-16 bg-slate-950/50 rounded-xl border border-slate-800/50 flex items-center px-4 space-x-1 overflow-hidden">
                    {events.filter(e => e.source === 'prod').map(e => (
                      <motion.div
                        key={e.id}
                        initial={{ scale: 0 }}
                        animate={{ scale: 1 }}
                        className={`w-3 h-8 rounded-sm cursor-pointer hover:w-6 hover:brightness-125 transition-all ${
                          e.type.includes('fail') ? 'bg-red-500' :
                          e.type.includes('cancel') ? 'bg-rose-500' :
                          e.type.includes('reschedule') ? 'bg-amber-500' :
                          'bg-blue-500'
                        }`}
                        onClick={() => setSelectedEvent(e)}
                      />
                    ))}
                    {events.filter(e => e.source === 'prod').length === 0 && (
                      <div className="text-[10px] text-slate-700 uppercase tracking-widest font-bold">No production traffic detected</div>
                    )}
                  </div>
                </div>
              )}

              {/* SimCity Lane */}
              {(mode === 'FUSED' || mode === 'SIMCITY') && (
                <div className="relative group">
                  <div className="absolute -left-12 top-1/2 -translate-y-1/2 -rotate-90 text-[8px] font-black tracking-tighter text-cyan-500/40 uppercase group-hover:text-cyan-400 transition-colors">SIMCITY</div>
                  <div className="h-16 bg-slate-950/50 rounded-xl border border-slate-800/50 flex items-center px-4 space-x-1 overflow-hidden">
                    {events.filter(e => e.source === 'simcity').map(e => (
                      <motion.div
                        key={e.id}
                        initial={{ scale: 0 }}
                        animate={{ scale: 1 }}
                        className={`w-3 h-8 rounded-sm cursor-pointer hover:w-6 hover:brightness-125 transition-all ${
                          e.type.includes('fail') ? 'bg-red-500' :
                          e.type.includes('cancel') ? 'bg-rose-500' :
                          e.type.includes('reschedule') ? 'bg-amber-500' :
                          'bg-cyan-500'
                        }`}
                        onClick={() => setSelectedEvent(e)}
                      />
                    ))}
                    {events.filter(e => e.source === 'simcity').length === 0 && (
                      <div className="text-[10px] text-slate-700 uppercase tracking-widest font-bold">No SimCity traffic detected</div>
                    )}
                  </div>
                </div>
              )}
            </div>
          </div>

          {/* Event Feed */}
          <div className="flex-1 min-h-0 bg-slate-900/40 border border-slate-800 rounded-2xl flex flex-col overflow-hidden">
            <div className="p-4 border-b border-slate-800 bg-slate-800/20 flex items-center justify-between">
              <h3 className="text-xs font-bold uppercase tracking-widest text-slate-400 flex items-center">
                <List className="w-4 h-4 mr-2 text-blue-500" />
                Raw Event sequence
              </h3>
              <Badge variant="outline" className="font-mono text-[9px]">{events.length} EVENTS</Badge>
            </div>
            <div className="flex-1 overflow-y-auto font-mono text-[10px]">
              <table className="w-full text-left border-collapse">
                <thead className="bg-slate-950/50 sticky top-0 text-slate-500 border-b border-slate-800">
                  <tr>
                    <th className="p-3 pl-6">TIME</th>
                    <th className="p-3">SOURCE</th>
                    <th className="p-3">TYPE</th>
                    <th className="p-3">PAYLOAD</th>
                    <th className="p-3 pr-6 text-right">ACTION</th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-slate-800/50">
                  {events.map((event) => (
                    <tr key={event.id} className="hover:bg-slate-800/30 group transition-colors">
                      <td className="p-3 pl-6 text-slate-500">{new Date(event.ts).toLocaleTimeString()}</td>
                      <td className="p-3">
                        <span className={`px-1.5 py-0.5 rounded text-[9px] font-bold uppercase ${
                          event.source === 'prod' ? 'bg-blue-500/10 text-blue-400' : 'bg-cyan-500/10 text-cyan-400'
                        }`}>
                          {event.source}
                        </span>
                      </td>
                      <td className="p-3 font-bold">
                        <span className={
                          event.type.includes('fail') ? 'text-red-400' :
                          event.type.includes('cancel') ? 'text-rose-400' :
                          'text-slate-300'
                        }>
                          {event.type}
                        </span>
                      </td>
                      <td className="p-3 text-slate-500 max-w-md truncate group-hover:whitespace-normal group-hover:text-slate-400 transition-all">
                        {JSON.stringify(event.payload)}
                      </td>
                      <td className="p-3 pr-6 text-right">
                        <Button 
                          variant="ghost" 
                          size="icon" 
                          className="h-6 w-6 opacity-0 group-hover:opacity-100"
                          onClick={() => setSelectedEvent(event)}
                        >
                          <ArrowRight className="w-3 h-3" />
                        </Button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
      <EventDetailModal event={selectedEvent} onClose={() => setSelectedEvent(null)} />
    </div>
  );
}

'use client'

import { useState, useEffect } from 'react'
import { motion, AnimatePresence } from 'framer-motion'
import { Search, User, Shield, UserCheck, UserX, Edit, Save, X, AlertCircle } from 'lucide-react'

interface UserProfile {
  id: string
  email: string
  full_name: string | null
  username: string | null
  role: 'customer' | 'vendor' | 'admin'
  created_at: string
  phone: string | null
}

export default function UsersManagementPage() {
  const [users, setUsers] = useState<UserProfile[]>([])
  const [filteredUsers, setFilteredUsers] = useState<UserProfile[]>([])
  const [loading, setLoading] = useState(true)
  const [searchQuery, setSearchQuery] = useState('')
  const [roleFilter, setRoleFilter] = useState<'all' | 'customer' | 'vendor' | 'admin'>('all')

  // Editing state
  const [editingUserId, setEditingUserId] = useState<string | null>(null)
  const [editForm, setEditForm] = useState<{
    role: 'customer' | 'vendor' | 'admin'
    username: string
  }>({ role: 'customer', username: '' })

  const [saving, setSaving] = useState(false)
  const [error, setError] = useState<string | null>(null)

  useEffect(() => {
    loadUsers()
  }, [])

  useEffect(() => {
    filterUsers()
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [users, searchQuery, roleFilter])

  const loadUsers = async () => {
    try {
      setLoading(true)
      const response = await fetch('/api/admin/users/list', {
        method: 'GET',
        credentials: 'include'
      })

      if (!response.ok) {
        if (response.status === 401) {
          window.location.href = '/login'
          return
        }
        const errorData = await response.json()
        throw new Error(errorData.error || 'Failed to load users')
      }

      const { data } = await response.json()
      setUsers(data || [])
    } catch (error) {
      console.error('Error loading users:', error)
      setError(error instanceof Error ? error.message : 'Unknown error')
    } finally {
      setLoading(false)
    }
  }

  const filterUsers = () => {
    let filtered = users

    // Filter by role
    if (roleFilter !== 'all') {
      filtered = filtered.filter(user => user.role === roleFilter)
    }

    // Filter by search query
    if (searchQuery) {
      const query = searchQuery.toLowerCase()
      filtered = filtered.filter(user =>
        user.email?.toLowerCase().includes(query) ||
        user.full_name?.toLowerCase().includes(query) ||
        user.username?.toLowerCase().includes(query) ||
        user.phone?.toLowerCase().includes(query)
      )
    }

    setFilteredUsers(filtered)
  }

  const handleEdit = (user: UserProfile) => {
    setEditingUserId(user.id)
    setEditForm({
      role: user.role,
      username: user.username || ''
    })
    setError(null)
  }

  const handleCancelEdit = () => {
    setEditingUserId(null)
    setEditForm({ role: 'customer', username: '' })
    setError(null)
  }

  const handleSave = async (userId: string) => {
    try {
      setSaving(true)
      setError(null)

      const response = await fetch('/api/admin/users/update', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({ 
          userId, 
          role: editForm.role,
          username: editForm.username 
        })
      })

      if (!response.ok) {
        const errorData = await response.json()
        throw new Error(errorData.error || 'Failed to update user')
      }

      // Update local state
      setUsers(users.map(user =>
        user.id === userId ? { 
          ...user, 
          role: editForm.role,
          username: editForm.username || null 
        } : user
      ))
      setEditingUserId(null)
    } catch (error) {
      console.error('Error saving user:', error)
      setError(error instanceof Error ? error.message : 'Failed to save')
    } finally {
      setSaving(false)
    }
  }

  const getRoleBadgeColor = (role: string) => {
    switch (role) {
      case 'admin':
        return 'bg-purple-100 text-purple-700 border-purple-200'
      case 'vendor':
        return 'bg-blue-100 text-blue-700 border-blue-200'
      case 'customer':
        return 'bg-green-100 text-green-700 border-green-200'
      default:
        return 'bg-gray-100 text-gray-700 border-gray-200'
    }
  }

  const roleCounts = {
    all: users.length,
    customer: users.filter(u => u.role === 'customer').length,
    vendor: users.filter(u => u.role === 'vendor').length,
    admin: users.filter(u => u.role === 'admin').length,
  }

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin w-8 h-8 border-2 border-blue-600 border-t-transparent rounded-full"></div>
      </div>
    )
  }

  return (
    <div className="space-y-6">
      {/* Page Header */}
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="mb-8"
      >
        <h1 className="text-3xl font-bold text-gray-900 mb-2">User Management</h1>
        <p className="text-gray-600">Manage user accounts, roles, and usernames</p>
      </motion.div>

      {/* Stats Cards */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6, delay: 0.2 }}
        className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8"
      >
        <div className="bg-white rounded-2xl p-6 shadow-sm border border-gray-100">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600 mb-1">Total Users</p>
              <p className="text-2xl font-bold text-gray-900">{roleCounts.all}</p>
            </div>
            <div className="w-12 h-12 bg-gray-100 rounded-2xl flex items-center justify-center">
              <User className="text-gray-600" size={20} />
            </div>
          </div>
        </div>
        {/* ... (other stats cards remain same) ... */}
        <div className="bg-white rounded-2xl p-6 shadow-sm border border-gray-100">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600 mb-1">Customers</p>
              <p className="text-2xl font-bold text-green-600">{roleCounts.customer}</p>
            </div>
            <div className="w-12 h-12 bg-green-100 rounded-2xl flex items-center justify-center">
              <UserCheck className="text-green-600" size={20} />
            </div>
          </div>
        </div>
        <div className="bg-white rounded-2xl p-6 shadow-sm border border-gray-100">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600 mb-1">Vendors</p>
              <p className="text-2xl font-bold text-blue-600">{roleCounts.vendor}</p>
            </div>
            <div className="w-12 h-12 bg-blue-100 rounded-2xl flex items-center justify-center">
              <UserCheck className="text-blue-600" size={20} />
            </div>
          </div>
        </div>
        <div className="bg-white rounded-2xl p-6 shadow-sm border border-gray-100">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600 mb-1">Admins</p>
              <p className="text-2xl font-bold text-purple-600">{roleCounts.admin}</p>
            </div>
            <div className="w-12 h-12 bg-purple-100 rounded-2xl flex items-center justify-center">
              <Shield className="text-purple-600" size={20} />
            </div>
          </div>
        </div>
      </motion.div>

      {/* Error Banner */}
      <AnimatePresence>
        {error && (
          <motion.div
            initial={{ opacity: 0, height: 0 }}
            animate={{ opacity: 1, height: 'auto' }}
            exit={{ opacity: 0, height: 0 }}
            className="bg-red-50 border border-red-200 rounded-xl p-4 flex items-center gap-3 text-red-700"
          >
            <AlertCircle size={20} />
            <p>{error}</p>
            <button onClick={() => setError(null)} className="ml-auto">
              <X size={16} />
            </button>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Filters */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6, delay: 0.4 }}
        className="bg-white rounded-2xl p-6 shadow-sm border border-gray-100"
      >
        <div className="flex flex-col md:flex-row gap-4">
          <div className="flex-1 relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
            <input
              type="text"
              placeholder="Search by name, email, username or phone..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
          <div className="flex gap-2">
            {(['all', 'customer', 'vendor', 'admin'] as const).map((role) => (
              <button
                key={role}
                onClick={() => setRoleFilter(role)}
                className={`px-4 py-2 rounded-xl font-medium transition-colors duration-200 ${
                  roleFilter === role
                    ? 'bg-blue-600 text-white'
                    : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                }`}
              >
                {role.charAt(0).toUpperCase() + role.slice(1)} ({roleCounts[role]})
              </button>
            ))}
          </div>
        </div>
      </motion.div>

      {/* Users Table */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6, delay: 0.6 }}
        className="bg-white rounded-2xl shadow-sm border border-gray-100 overflow-hidden"
      >
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead className="bg-gray-50 border-b border-gray-200">
              <tr>
                <th className="px-6 py-4 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider">User</th>
                <th className="px-6 py-4 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider">Email</th>
                <th className="px-6 py-4 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider">Username</th>
                <th className="px-6 py-4 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider">Role</th>
                <th className="px-6 py-4 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider">Joined</th>
                <th className="px-6 py-4 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider">Actions</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-gray-200">
              {filteredUsers.length === 0 ? (
                <tr>
                  <td colSpan={6} className="px-6 py-12 text-center text-gray-500">
                    {searchQuery || roleFilter !== 'all' ? 'No users found matching your filters' : 'No users found'}
                  </td>
                </tr>
              ) : (
                filteredUsers.map((user) => (
                  <tr key={user.id} className="hover:bg-gray-50 transition-colors">
                    {/* User Column */}
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex items-center">
                        <div className="w-10 h-10 bg-gradient-to-r from-blue-500 to-purple-600 rounded-full flex items-center justify-center mr-3">
                          <span className="text-white font-semibold text-sm">
                            {user.full_name?.charAt(0).toUpperCase() || user.email?.charAt(0).toUpperCase() || 'U'}
                          </span>
                        </div>
                        <div>
                          <div className="text-sm font-medium text-gray-900">{user.full_name || 'No name'}</div>
                          <div className="text-xs text-gray-500">{user.id.substring(0, 8)}...</div>
                        </div>
                      </div>
                    </td>

                    {/* Email Column */}
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="text-sm text-gray-900">{user.email}</div>
                    </td>

                    {/* Username Column (Editable) */}
                    <td className="px-6 py-4 whitespace-nowrap">
                      {editingUserId === user.id ? (
                        <input
                          type="text"
                          value={editForm.username}
                          onChange={(e) => setEditForm({...editForm, username: e.target.value})}
                          placeholder="Set username"
                          className="px-3 py-1 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 w-32"
                          disabled={saving}
                        />
                      ) : (
                        <div className="text-sm text-gray-900">{user.username || '-'}</div>
                      )}
                    </td>

                    {/* Role Column (Editable) */}
                    <td className="px-6 py-4 whitespace-nowrap">
                      {editingUserId === user.id ? (
                        <select
                          value={editForm.role}
                          onChange={(e) => setEditForm({...editForm, role: e.target.value as 'customer' | 'vendor' | 'admin'})}
                          className="px-3 py-1 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                          disabled={saving}
                        >
                          <option value="customer">Customer</option>
                          <option value="vendor">Vendor</option>
                          <option value="admin">Admin</option>
                        </select>
                      ) : (
                        <span className={`inline-flex items-center px-3 py-1 rounded-full text-xs font-medium border ${getRoleBadgeColor(user.role)}`}>
                          {user.role === 'vendor' ? 'Provider' : user.role.charAt(0).toUpperCase() + user.role.slice(1)}
                        </span>
                      )}
                    </td>

                    {/* Joined Column */}
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="text-sm text-gray-900">
                        {new Date(user.created_at).toLocaleDateString()}
                      </div>
                    </td>

                    {/* Actions Column */}
                    <td className="px-6 py-4 whitespace-nowrap">
                      {editingUserId === user.id ? (
                        <div className="flex gap-2">
                          <button
                            onClick={() => handleSave(user.id)}
                            disabled={saving}
                            className="p-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors disabled:opacity-50"
                            title="Save"
                          >
                            <Save size={16} />
                          </button>
                          <button
                            onClick={handleCancelEdit}
                            disabled={saving}
                            className="p-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition-colors disabled:opacity-50"
                            title="Cancel"
                          >
                            <X size={16} />
                          </button>
                        </div>
                      ) : (
                        <button
                          onClick={() => handleEdit(user)}
                          className="p-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
                          title="Edit User"
                        >
                          <Edit size={16} />
                        </button>
                      )}
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </motion.div>
    </div>
  )
}

'use client'

import React, { useState } from 'react'
import { motion, AnimatePresence } from 'framer-motion'
import Link from 'next/link'
import { useRouter } from 'next/navigation'
import DataTable from '@/components/admin/DataTable'
import { vendors } from '@/lib/mockData'
import { exportToCSV, exportToJSON } from '@/lib/admin/exportUtils'
import { X } from 'lucide-react'

export default function VendorsPage() {
  const router = useRouter()
  const [_showAddModal, _setShowAddModal] = useState(false)
  const [showBulkModal, setShowBulkModal] = useState(false)
  const [showNewsletterModal, setShowNewsletterModal] = useState(false)
  const [selectedVendors, setSelectedVendors] = useState<Set<string>>(new Set())
  const [exportFormat, setExportFormat] = useState<'csv' | 'json'>('csv')

  const columns = [
    { key: 'name', label: 'Name', sortable: true },
    { key: 'category', label: 'Category', sortable: true },
    { key: 'status', label: 'Status', sortable: true },
    { key: 'email', label: 'Email', sortable: true },
    { key: 'location', label: 'Location', sortable: true },
    { key: 'joinDate', label: 'Join Date', sortable: true }
  ]

  const handleExport = () => {
    if (exportFormat === 'csv') {
      exportToCSV(vendors, 'vendors')
    } else {
      exportToJSON(vendors, 'vendors')
    }
  }

  const handleBulkAction = async (action: 'approve' | 'reject' | 'suspend') => {
    if (selectedVendors.size === 0) {
      alert('Please select vendors first')
      return
    }

    try {
      const response = await fetch('/api/admin/vendors/bulk', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ 
          action, 
          vendorIds: Array.from(selectedVendors) 
        })
      })

      const data = await response.json()
      if (data.success) {
        alert(`Successfully ${action}d ${selectedVendors.size} vendor(s)`)
        setSelectedVendors(new Set())
        setShowBulkModal(false)
        router.refresh()
      } else {
        alert(data.error || 'Action failed')
      }
    } catch (error) {
      console.error('Bulk action error:', error)
      alert('Error performing bulk action')
    }
  }

  const handleSendNewsletter = async (subject: string, message: string) => {
    try {
      const response = await fetch('/api/admin/newsletter', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ 
          subject, 
          message,
          recipientType: 'vendors'
        })
      })

      const data = await response.json()
      if (data.success) {
        alert(`Newsletter sent to ${data.recipientCount} vendors`)
        setShowNewsletterModal(false)
      } else {
        alert(data.error || 'Failed to send newsletter')
      }
    } catch (error) {
      console.error('Newsletter error:', error)
      alert('Error sending newsletter')
    }
  }

  return (
    <div className="space-y-6">
      {/* Page Header */}
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="mb-8"
      >
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Vendors</h1>
        <p className="text-gray-600">Manage and monitor all service providers on your platform.</p>
      </motion.div>

      {/* Stats Cards */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6, delay: 0.2 }}
        className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8"
      >
        <div className="bg-white rounded-2xl p-6 shadow-sm border border-gray-100">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600 mb-1">Total Vendors</p>
              <p className="text-2xl font-bold text-blue-600">{vendors.length}</p>
            </div>
            <div className="w-12 h-12 bg-blue-100 rounded-2xl flex items-center justify-center">
              <span className="text-blue-600 font-semibold">ðŸ‘¥</span>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-2xl p-6 shadow-sm border border-gray-100">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600 mb-1">Active</p>
              <p className="text-2xl font-bold text-green-600">
                {vendors.filter(v => v.status === 'active').length}
              </p>
            </div>
            <div className="w-12 h-12 bg-green-100 rounded-2xl flex items-center justify-center">
              <span className="text-green-600 font-semibold">âœ…</span>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-2xl p-6 shadow-sm border border-gray-100">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600 mb-1">Pending</p>
              <p className="text-2xl font-bold text-yellow-600">
                {vendors.filter(v => v.status === 'pending').length}
              </p>
            </div>
            <div className="w-12 h-12 bg-yellow-100 rounded-2xl flex items-center justify-center">
              <span className="text-yellow-600 font-semibold">â³</span>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-2xl p-6 shadow-sm border border-gray-100">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600 mb-1">Suspended</p>
              <p className="text-2xl font-bold text-red-600">
                {vendors.filter(v => v.status === 'suspended').length}
              </p>
            </div>
            <div className="w-12 h-12 bg-red-100 rounded-2xl flex items-center justify-center">
              <span className="text-red-600 font-semibold">ðŸš«</span>
            </div>
          </div>
        </div>
      </motion.div>

      {/* Vendors Table */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6, delay: 0.4 }}
      >
        <DataTable
          columns={columns}
          data={vendors}
          title="Vendor Management"
        />
      </motion.div>

      {/* Quick Actions */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6, delay: 0.6 }}
        className="bg-white rounded-2xl p-6 shadow-sm border border-gray-100"
      >
        <h3 className="text-lg font-semibold text-gray-900 mb-4">Quick Actions</h3>
        <div className="flex flex-wrap gap-4">
          <button 
            onClick={() => router.push('/vendor/onboarding')}
            className="px-6 py-3 bg-blue-600 text-white rounded-2xl hover:bg-blue-700 transition-colors duration-200 font-medium"
          >
            + Add New Vendor
          </button>
          <div className="flex gap-2">
            <select 
              value={exportFormat}
              onChange={(e) => setExportFormat(e.target.value as 'csv' | 'json')}
              className="px-3 py-3 bg-gray-100 rounded-2xl border border-gray-300 focus:outline-none focus:ring-2 focus:ring-green-500"
            >
              <option value="csv">CSV</option>
              <option value="json">JSON</option>
            </select>
            <button 
              onClick={handleExport}
              className="px-6 py-3 bg-green-600 text-white rounded-2xl hover:bg-green-700 transition-colors duration-200 font-medium"
            >
              Export Data
            </button>
          </div>
          <button 
            onClick={() => setShowBulkModal(true)}
            className="px-6 py-3 bg-purple-600 text-white rounded-2xl hover:bg-purple-700 transition-colors duration-200 font-medium"
          >
            Bulk Actions
          </button>
          <button 
            onClick={() => setShowNewsletterModal(true)}
            className="px-6 py-3 bg-gray-600 text-white rounded-2xl hover:bg-gray-700 transition-colors duration-200 font-medium"
          >
            Send Newsletter
          </button>
        </div>
      </motion.div>

      {/* Bulk Actions Modal */}
      <AnimatePresence>
        {showBulkModal && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center p-4"
            onClick={() => setShowBulkModal(false)}
          >
            <motion.div
              initial={{ scale: 0.95, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.95, opacity: 0 }}
              onClick={(e: React.MouseEvent<HTMLDivElement>) => e.stopPropagation()}
              className="bg-white rounded-2xl p-6 max-w-md w-full"
            >
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-xl font-bold">Bulk Actions</h3>
                <button onClick={() => setShowBulkModal(false)}>
                  <X className="w-5 h-5" />
                </button>
              </div>
              <p className="text-sm text-gray-600 mb-4">
                Select vendors from the table, then choose an action below.
              </p>
              <div className="space-y-2">
                <button
                  onClick={() => handleBulkAction('approve')}
