import { createServerClient } from '@supabase/ssr'
import { getSupabaseConfig } from '@/config/supabase'
import { cookies } from 'next/headers'

/**
 * Returns the authenticated user's id using either the Supabase session cookie
 * or an Authorization header bearer token. Returns null if authentication fails.
 */
export async function getAuthenticatedUserId(request: Request): Promise<string | null> {
  const cookieStore = await cookies()
  const config = getSupabaseConfig()
  
  const supabase = createServerClient(
    config.url,
    config.publishableKey,
    {
      cookies: {
        getAll() {
          return cookieStore.getAll()
        },
        setAll(cookiesToSet) {
          try {
            cookiesToSet.forEach(({ name, value, options }) => {
              cookieStore.set(name, value, options)
            })
          } catch (_error) {
            // The `setAll` method was called from a Server Component or Route Handler.
            // This can be ignored if you have middleware refreshing user sessions.
          }
        }
      }
    }
  )

  // Try cookie-based session first
  const { data: { session } } = await supabase.auth.getSession()
  if (session?.user?.id) {
    return session.user.id
  }

  // Fallback to Authorization header
  const authHeader = request.headers.get('authorization')
  if (authHeader?.startsWith('Bearer ')) {
    const token = authHeader.slice(7)
    const { data: { user } } = await supabase.auth.getUser(token)
    if (user?.id) {
      return user.id
    }
  }

  return null
}

import { NextRequest, NextResponse } from 'next/server'
import { getServerSupabase } from '@/lib/supabaseServer'
import { getAuthenticatedUserId } from '@/app/api/_utils/auth'

export async function POST(request: NextRequest) {
  try {
    const userId = await getAuthenticatedUserId(request)
    if (!userId) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    const { message, _broadcastIds } = await request.json()

    if (!message) {
      return NextResponse.json({ error: 'Message is required' }, { status: 400 })
    }

    const supabase = getServerSupabase()
    
    // Get all vendors
    const { data: vendors, error: vendorError } = await supabase
      .from('profiles')
      .select('id')
      .eq('role', 'vendor')

    if (vendorError) {
      console.error('Vendor fetch error:', vendorError)
      return NextResponse.json({ error: 'Failed to fetch vendors' }, { status: 500 })
    }

    if (!vendors || vendors.length === 0) {
      return NextResponse.json({ error: 'No vendors found' }, { status: 400 })
    }

    // Create notifications for all vendors
    const notifications = vendors.map(vendor => ({
      user_id: vendor.id,
      type: 'broadcast',
      title: 'New Broadcast',
      message: message,
      created_at: new Date().toISOString()
    }))

    const { error: notifError } = await supabase
      .from('notifications')
      .insert(notifications)

    if (notifError) {
      console.error('Notification creation error:', notifError)
      return NextResponse.json({ error: 'Failed to create notifications' }, { status: 500 })
    }

    return NextResponse.json({ 
      success: true, 
      recipientCount: vendors.length,
      message: `Notifications sent to ${vendors.length} vendors` 
    })
  } catch (error) {
    console.error('Notification error:', error)
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 })
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { createServerClient } from '@supabase/ssr'
import { cookies } from 'next/headers'
import { getSupabaseConfig } from '@/config/supabase'
import { requireAdmin } from '@/lib/auth/requireAdmin'
import { bookingStateMachine } from '@/lib/services/bookingStateMachine'
import { processRefund } from '@/lib/services/refundService'

interface ResolveDisputeRequest {
  status: 'resolved' | 'closed' | 'rejected'
  resolution: string
  resolution_amount?: number
  resolution_type: 'refund' | 'reschedule' | 'partial_refund' | 'credit' | 'rejected' | 'other'
  admin_notes?: string
}

export async function POST(
  request: NextRequest,
  context: { params: Promise<Record<string, string>> }
) {
  const { id: disputeId } = await context.params
  try {
    const cookieStore = await cookies()
    const config = getSupabaseConfig()
    const supabase = createServerClient(
      config.url,
      config.publishableKey,
      {
        cookies: {
          getAll() {
            return cookieStore.getAll()
          },
          setAll(cookiesToSet) {
            try {
              cookiesToSet.forEach(({ name, value, options }) => {
                cookieStore.set(name, value, options)
              })
            } catch (_error) {
              // The `setAll` method was called from a Server Component or Route Handler.
              // This can be ignored if you have middleware refreshing user sessions.
            }
          }
        }
      }
    )

    const { data: { user }, error: authError } = await supabase.auth.getUser()
    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    const adminUser = await requireAdmin({ user })

    const body: ResolveDisputeRequest = await request.json()
    const { status, resolution, resolution_amount, resolution_type, admin_notes } = body

    if (!status || !resolution || !resolution_type) {
      return NextResponse.json(
        { error: 'Missing required fields: status, resolution, resolution_type' },
        { status: 400 }
      )
    }

    // Get dispute details
    const { data: dispute, error: disputeError } = await supabase
      .from('disputes')
      .select(`
        *,
        bookings!inner(
          id,
          customer_id,
          provider_id,
          status,
          total_amount_cents,
          commitment_fee_paid,
          payment_intent_id,
          idempotency_key
        )
      `)
      .eq('id', disputeId)
      .single()

    if (disputeError || !dispute) {
      return NextResponse.json(
        { error: 'Dispute not found' },
        { status: 404 }
      )
    }

    if (dispute.status === 'resolved' || dispute.status === 'closed') {
      return NextResponse.json(
        { error: 'Dispute already resolved' },
        { status: 400 }
      )
    }

    // Use database function to resolve dispute
    const { error: resolveError } = await supabase.rpc('resolve_dispute', {
      p_dispute_id: disputeId,
      p_admin_id: adminUser.id,
      p_resolution: resolution,
      p_resolution_amount: resolution_amount || null,
      p_resolution_type: resolution_type,
      p_admin_notes: admin_notes || null
    })

    if (resolveError) {
      console.error('Error resolving dispute:', resolveError)
      return NextResponse.json(
        { error: 'Failed to resolve dispute' },
        { status: 500 }
      )
    }

    // Process refund if needed
    let refundResult = null
    if ((resolution_type === 'refund' || resolution_type === 'partial_refund') && dispute.bookings) {
      const booking = dispute.bookings
      if (booking.commitment_fee_paid && booking.payment_intent_id) {
        try {
          refundResult = await processRefund(booking.id, {
            idempotencyKey: booking.idempotency_key,
            reason: `Dispute resolution: ${resolution}`,
            adminId: adminUser.id
          })
        } catch (refundError) {
          console.error('Refund processing error:', refundError)
          // Don't fail the resolution if refund fails - it can be processed manually
        }
      }
    }

    // Update booking status if no-show
    if (dispute.dispute_type === 'no_show' && dispute.bookings) {
      try {
        await bookingStateMachine.transition(dispute.bookings.id, 'no_show', {
          reason: 'No-show confirmed via dispute resolution',
          adminId: adminUser.id,
          adminOverride: true,
          skipRefund: true // Refund already handled above
        })
      } catch (transitionError) {
        console.error('Booking status transition error:', transitionError)
        // Don't fail the resolution if transition fails
      }
    }

    // Send notifications
    await supabase.from('admin_notifications').insert([{
      type: 'dispute_resolved',
      title: `Dispute Resolved - ${dispute.dispute_type}`,
      message: `Dispute ${disputeId} has been resolved: ${resolution}`,
      metadata: { dispute_id: disputeId, resolution_type },
      priority: 'medium',
      created_at: new Date().toISOString()
    }])

    return NextResponse.json({
      success: true,
      message: 'Dispute resolved successfully',
      dispute_id: disputeId,
      refund_result: refundResult
    })
  } catch (error) {
    console.error('Admin dispute resolution error:', error)
    if (error instanceof Error && error.message.includes('Unauthorized')) {
      return NextResponse.json(
        { error: 'Unauthorized' },
        { status: 401 }
      )
    }
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    )
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { createServerClient } from '@supabase/ssr'
import { cookies } from 'next/headers'
import { getSupabaseConfig } from '@/config/supabase'
import { requireAdmin } from '@/lib/auth/requireAdmin'

export async function GET(request: NextRequest) {
  try {
    await requireAdmin(request)

    const cookieStore = await cookies()
    const config = getSupabaseConfig()
    const supabase = createServerClient(
      config.url,
      config.publishableKey,
      {
        cookies: {
          getAll() {
            return cookieStore.getAll()
          },
          setAll(cookiesToSet) {
            try {
              cookiesToSet.forEach(({ name, value, options }) => {
                cookieStore.set(name, value, options)
              })
            } catch (_error) {
              // The `setAll` method was called from a Server Component or Route Handler.
              // This can be ignored if you have middleware refreshing user sessions.
            }
          }
        }
      }
    )

    const { searchParams } = new URL(request.url)
    const status = searchParams.get('status')
    const dispute_type = searchParams.get('type')
    const page = parseInt(searchParams.get('page') || '1')
    const limit = Math.min(parseInt(searchParams.get('limit') || '50'), 200)
    const offset = (page - 1) * limit

    let query = supabase
      .from('disputes')
      .select(`
        *,
        bookings!inner(
          id,
          customer_id,
          provider_id,
          service_name,
          total_amount,
          status,
          slot_start,
          slot_end
        ),
        users!inner(
          id,
          email,
          full_name
        )
      `, { count: 'exact' })
      .order('created_at', { ascending: false })
      .range(offset, offset + limit - 1)

    if (status) {
      query = query.eq('status', status)
    }
    if (dispute_type) {
      query = query.eq('dispute_type', dispute_type)
    }

    const { data: disputes, error, count } = await query

    if (error) {
      console.error('Error fetching disputes:', error)
      return NextResponse.json(
        { error: 'Failed to fetch disputes' },
        { status: 500 }
      )
    }

    return NextResponse.json({
      success: true,
      disputes: disputes || [],
      pagination: {
        page,
        limit,
        total: count || 0,
        pages: Math.ceil((count || 0) / limit)
      }
    })
  } catch (error) {
    console.error('Admin disputes fetch error:', error)
    if (error instanceof Error && error.message.includes('Unauthorized')) {
      return NextResponse.json(
        { error: 'Unauthorized' },
        { status: 401 }
      )
    }
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    )
  }
}

