const oauth2Client = new google.auth.OAuth2(
  process.env.GOOGLE_CLIENT_ID,
  process.env.GOOGLE_CLIENT_SECRET,
  `${process.env.NEXT_PUBLIC_APP_URL}/api/auth/google/callback`
)

export async function GET(request: NextRequest) {
  try {
    const config = getSupabaseConfig()
    
    // Exchange code for tokens
    const { code, email } = await request.json()
    const { tokens } = await oauth2Client.getToken(code)
    const { access_token, refresh_token, expiry_date } = tokens

    // Get user info from Google
    oauth2Client.setCredentials({ access_token })
    const oauth2 = google.oauth2('v2')
    const { data: userInfo } = await oauth2.userinfo.get({ auth: oauth2Client })

    const cookieStore = await cookies();
    const supabase = createServerClient(
      config.url,
      config.publishableKey,
      {
        cookies: {
          getAll() {
            return cookieStore.getAll()
          },
          setAll(cookiesToSet) {
            try {
              cookiesToSet.forEach(({ name, value, options }) => {
                cookieStore.set(name, value, options)
              })
            } catch (_error) {
              // The `setAll` method was called from a Server Component or Route Handler.
              // This can be ignored if you have middleware refreshing user sessions.
            }
          }
        },
      }
    );

    // Store in external_calendar_connections
    const { data: connection, error } = await supabase
      .from('external_calendar_connections')
      .insert({
        provider: CalendarProvider.GOOGLE,
        provider_user_id: userInfo.id,
        provider_email: email || userInfo.email,
        provider_calendar_id: 'primary', // We'll update this after getting calendar list
        access_token,
        refresh_token,
        token_expiry: new Date(expiry_date!),
        sync_enabled: true
      })
      .select()
      .single()

    if (error) throw error

    return NextResponse.json({
      success: true,
      connection_id: connection.id,
      provider_email: email || userInfo.email
    })
  } catch (error) {
    console.error('Error in Google Calendar callback:', error)
    return NextResponse.json(
      { error: 'Failed to complete Google Calendar connection' },
      { status: 500 }
    )
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { createServerClient } from '@supabase/ssr'
import { cookies } from 'next/headers'
import { getSupabaseConfig } from '@/config/supabase'

/**
 * Server-side profile creation/repair endpoint
 * 
 * Called by authenticated clients when profile doesn't exist
 * Ensures profile creation happens with proper validation and error handling
 * 
 * POST /api/auth/profile/create
 * Auth: Required (user must be authenticated)
 * Body: { role?: 'customer' | 'vendor' }
 */
export async function POST(request: NextRequest) {
  try {
    const cookieStore = await cookies()
    const config = getSupabaseConfig()
    
    const supabase = createServerClient(
      config.url,
      config.publishableKey,
      {
        cookies: {
          getAll() {
            return cookieStore.getAll()
          },
          setAll(cookiesToSet) {
            try {
              cookiesToSet.forEach(({ name, value, options }) => {
                cookieStore.set(name, value, options)
              })
            } catch (_error) {
              // The `setAll` method was called from a Server Component or Route Handler.
              // This can be ignored if you have middleware refreshing user sessions.
            }
          }
        }
      }
    )

    // Get authenticated user
    const { data: { user }, error: authError } = await supabase.auth.getUser()

    if (authError || !user) {
      return NextResponse.json(
        { error: 'Unauthorized - authentication required' },
        { status: 401 }
      )
    }

    const { role = 'customer' } = await request.json()

    // Validate role
    if (!['customer', 'vendor'].includes(role)) {
      return NextResponse.json(
        { error: 'Invalid role - must be customer or vendor' },
        { status: 400 }
      )
    }

    // Check if profile already exists
    const { data: existingProfile, error: checkError } = await supabase
      .from('profiles')
      .select('id')
      .eq('id', user.id)
      .maybeSingle()

    if (checkError) {
      console.error('Error checking existing profile:', checkError)
      return NextResponse.json(
        { error: 'Failed to check profile status' },
        { status: 500 }
      )
    }

    // If profile exists, return it
    if (existingProfile) {
      const { data: profile, error: fetchError } = await supabase
        .from('profiles')
        .select('*')
        .eq('id', user.id)
        .single()

      if (fetchError) {
        return NextResponse.json(
          { error: 'Failed to fetch profile' },
          { status: 500 }
        )
      }

      return NextResponse.json({
        success: true,
        profile,
        message: 'Profile already exists'
      })
    }

    // Create new profile with validated data
    const { data: newProfile, error: createError } = await supabase
      .from('profiles')
      .insert({
        id: user.id,
        email: user.email,
        full_name: user.user_metadata?.full_name || 'User',
        role,
        created_at: new Date().toISOString(),
        updated_at: new Date().toISOString()
      })
      .select()
      .single()

    if (createError) {
      console.error('Error creating profile:', createError)
      return NextResponse.json(
        { error: 'Failed to create profile' },
        { status: 500 }
      )
    }

    return NextResponse.json({
      success: true,
      profile: newProfile,
      message: 'Profile created successfully'
    })
  } catch (error) {
    console.error('Profile creation error:', error)
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    )
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { createServerClient } from '@supabase/ssr'
import { getSupabaseConfig } from '@/config/supabase'
import { cookies } from 'next/headers'

/**
 * Sync Supabase session from Authorization header to cookies
 * Called after client-side login to ensure server can read the session
 */
export async function POST(request: NextRequest) {
  try {
    const { access_token, refresh_token } = await request.json()
    
    if (!access_token) {
      return NextResponse.json({ error: 'access_token required' }, { status: 400 })
    }

    const config = getSupabaseConfig()
    const cookieStore = await cookies()
    
    const supabase = createServerClient(
      config.url,
      config.publishableKey,
      {
        cookies: {
          getAll() {
            return cookieStore.getAll()
          },
          setAll(cookiesToSet) {
            try {
              cookiesToSet.forEach(({ name, value, options }) => {
                cookieStore.set(name, value, options)
              })
            } catch (_error) {
              // The `setAll` method was called from a Server Component or Route Handler.
              // This can be ignored if you have middleware refreshing user sessions.
            }
          }
        }
      }
    )

    // Set the session using the provided tokens
    const { data: { session }, error } = await supabase.auth.setSession({
      access_token,
      refresh_token: refresh_token || ''
    })

    if (error || !session) {
      return NextResponse.json({ error: error?.message || 'Failed to set session' }, { status: 400 })
    }

    return NextResponse.json({ 
      success: true, 
      user: session.user 
    })
  } catch (error) {
    console.error('Session sync error:', error)
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 })
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { createServerClient } from '@supabase/ssr'
import { getSupabaseConfig } from '@/config/supabase'
import { cookies } from 'next/headers';
import { BlockUserRequest } from '@/types/global';

export async function POST(request: NextRequest) {
  try {
    const cookieStore = await cookies();
    const config = getSupabaseConfig()
    const supabase = createServerClient(
      config.url,
      config.publishableKey,
      {
        cookies: {
          getAll() {
            return cookieStore.getAll()
          },
          setAll(cookiesToSet) {
            try {
              cookiesToSet.forEach(({ name, value, options }) => {
                cookieStore.set(name, value, options)
              })
            } catch (_error) {
              // The `setAll` method was called from a Server Component or Route Handler.
              // This can be ignored if you have middleware refreshing user sessions.
            }
          }
        }
      }
    )

    // Get authenticated user
    const { data: { user }, error: authError } = await supabase.auth.getUser();
    if (authError || !user) {
      return NextResponse.json(
        { error: 'Unauthorized' },
        { status: 401 }
      );
    }

    const body = await request.json();
    const { blocked_id, reason } = body as BlockUserRequest;

    if (!blocked_id) {
      return NextResponse.json(
        { error: 'Missing required fields' },
        { status: 400 }
      );
    }

    // Check if user is trying to block themselves
    if (blocked_id === user.id) {
      return NextResponse.json(
        { error: 'Cannot block yourself' },
        { status: 400 }
      );
    }

    // Check if block already exists
    const { data: existingBlock } = await supabase
      .from('user_blocks')
      .select('*')
      .eq('blocker_id', user.id)
      .eq('blocked_id', blocked_id)
      .single();

    if (existingBlock) {
      return NextResponse.json(
        { error: 'Block already exists' },
        { status: 400 }
      );
    }

    // Create the block
    const { data: block, error: blockError } = await supabase
      .from('user_blocks')
      .insert([
        {
          blocker_id: user.id,
          blocked_id,
          reason: reason || null
        }
      ])
      .select()
      .single();

    if (blockError) {
      return NextResponse.json(
        { error: 'Failed to create block' },
        { status: 500 }
      );
    }

    return NextResponse.json(block);
  } catch (error) {
    console.error('Error in blocks/create:', error);
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    );
  }
}

