      // Table doesn't exist - return empty array instead of error
      if (fetchError.code === 'PGRST205') {
        console.warn('Notifications table not found, returning empty array')
        return NextResponse.json({ notifications: [] })
      }
      console.error('Error fetching notifications:', fetchError)
      return NextResponse.json(
        { error: 'Failed to fetch notifications' },
        { status: 500 }
      )
    }

    return NextResponse.json({ notifications: notifications as Notification[] })
  } catch (error) {
    console.error('Error in notifications route:', error)
    if (process.env.NODE_ENV !== 'production') {
      return NextResponse.json({ notifications: [] })
    }
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 })
  }
}

export async function DELETE(request: NextRequest) {
  try {
    if (process.env.NODE_ENV === 'test') {
      try {
        const testUser = request.headers.get('x-test-user') ?? (await headers()).get('x-test-user')
        if (testUser === 'unauth') {
          return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
        }
      } catch {}
    }
    const cookieStore = await cookies()
    const config = getSupabaseConfig()
    const supabase = createServerClient(
      config.url,
      config.publishableKey,
      {
        cookies: {
          getAll() {
            return cookieStore.getAll()
          },
          setAll(cookiesToSet) {
            try {
              cookiesToSet.forEach(({ name, value, options }) => {
                cookieStore.set(name, value, options)
              })
            } catch (_error) {
              // The `setAll` method was called from a Server Component or Route Handler.
              // This can be ignored if you have middleware refreshing user sessions.
            }
          }
        }
      }
    )
    
    const { data: { user }, error: authError } = await supabase.auth.getUser()
    if (authError || !user) {
      return NextResponse.json(
        { error: 'Unauthorized' },
        { status: 401 }
      )
    }

    const { searchParams } = new URL(request.url)
    const notificationId = searchParams.get('id')

    if (!notificationId) {
      return NextResponse.json(
        { error: 'Notification ID is required' },
        { status: 400 }
      )
    }

    // The notifications table doesn't exist - return success for now
    // TODO: Migrate to use notification_intents table
    const { error: deleteError } = await supabase
      .from('notifications')
      .delete()
      .eq('id', notificationId)
      .eq('user_id', user.id)
      .limit(0) // Try to query but expect it to fail gracefully

    if (deleteError && deleteError.code === 'PGRST205') {
      // Table doesn't exist - return success anyway
      return NextResponse.json({ success: true })
    }

    if (deleteError) {
      console.error('Error deleting notification:', deleteError)
      return NextResponse.json(
        { error: 'Failed to delete notification' },
        { status: 500 }
      )
    }

    return NextResponse.json({ success: true })
  } catch (error) {
    console.error('Error in notifications DELETE route:', error)
    if (process.env.NODE_ENV !== 'production') {
      return NextResponse.json({ success: false })
    }
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 })
  }
}

import type {
  SimState,
  SimRunInfo,
  SimMetrics,
  SimEventPayload,
  InvariantViolation
} from '@/lib/simcity/types'
import { DEFAULT_METRICS } from '@/lib/simcity/types'

import type {
  OpsSummary,
  MetricSnapshot,
  HealthStatus,
  SLOStatus,
  Incident,
  AnomalyReport,
  Deployment,
  DeployReadiness,
  LogsQueryResult,
  LogEntry
} from '@/app/admin/ops-ai/lib/ops-types'

function mapSeverityToLevel(severity: InvariantViolation['severity']) {
  return severity === 'critical' || severity === 'high' ? 'ERROR' : 'WARN'
}

function getLatencyMs(metrics: SimMetrics) {
  const raw =
    metrics.p95ResponseTime ??
    metrics.avgBookingLatency ??
    metrics.reindexP95 ??
    metrics.rolloutP99

  if (raw != null) {
    return Math.max(0, raw)
  }

  // Stable synthetic latency between 200-500ms
  return 350
}

export async function fetchSimcitySnapshot(origin?: string) {
  const baseUrl = process.env.NEXT_PUBLIC_SITE_URL || origin

  if (!baseUrl) {
    throw new Error('NEXT_PUBLIC_SITE_URL is required for SimCity mode')
  }

  const [statusRes, summaryRes] = await Promise.all([
    fetch(`${baseUrl}/api/simcity/status`, { cache: 'no-store' }),
    fetch(`${baseUrl}/api/simcity/summary`, { cache: 'no-store' })
  ])

  // Check if SimCity is not running
  if (!statusRes.ok || !summaryRes.ok) {
    const statusText = await statusRes.text().catch(() => '')
    const summaryText = await summaryRes.text().catch(() => '')
    if (statusText.includes('not running') || summaryText.includes('not running')) {
      throw new Error('SimCity simulation is not running')
    }
    throw new Error(`SimCity endpoints returned errors: ${statusRes.status}, ${summaryRes.status}`)
  }

  const statusJson = await statusRes.json()
  const summaryJson = await summaryRes.json()

  // Check if simulation is not running from response
  if (!statusJson.success || !statusJson.data?.state?.running) {
    throw new Error('SimCity simulation is not running')
  }

  const status = statusJson.data ?? statusJson
  const summary = summaryJson.data ?? summaryJson

  const state: SimState =
    (status?.state as SimState) ??
    (summary?.state as SimState) ??
    (summary as SimState)

  const runInfo: SimRunInfo | null =
    (status?.runInfo as SimRunInfo | null) ??
    (summary?.runInfo as SimRunInfo | null) ??
    (state?.runInfo as SimRunInfo | null) ??
    null

  const metrics: SimMetrics = {
    ...DEFAULT_METRICS,
    ...(status?.metrics as SimMetrics | undefined),
    ...(summary?.metrics as SimMetrics | undefined),
    ...(state?.metrics as SimMetrics | undefined)
  }

  const violations: InvariantViolation[] = summary?.violations ?? []

  return { state, runInfo, metrics, violations, summary, status }
}

export function simcityToOpsSummary(
  state: SimState,
  runInfo: SimRunInfo | null,
  violations: InvariantViolation[]
): OpsSummary {
  const metrics = state?.metrics

  const summary: OpsSummary & {
    runInfo?: SimRunInfo | null
    scenario?: string | null
  } = {
    timestamp: new Date().toISOString(),
    health: simcityToHealth(metrics, violations),
    sloSummary: simcityToSLOs(metrics, violations),
    incidents: simcityToIncidents(violations),
    pendingActions: [],
    runInfo,
    scenario: runInfo?.scenario ?? state?.scenario ?? null
  }

  return summary
}

export function simcityToSystemMetrics(metrics: SimMetrics): MetricSnapshot {
  const value =
    metrics.throughput ??
    metrics.activeAgents ??
    metrics.totalAgentsSpawned ??
    0

  return {
    timestamp: new Date().toISOString(),
    value,
    unit: 'agents'
  }
}

export function simcityToBookingMetrics(metrics: SimMetrics): MetricSnapshot {
  return {
    timestamp: new Date().toISOString(),
    value: metrics.completedBookings ?? 0,
    unit: 'bookings'
  }
}

export function simcityToErrorMetrics(metrics: SimMetrics): MetricSnapshot {
  return {
    timestamp: new Date().toISOString(),
    value: metrics.errors ?? metrics.errorCount ?? 0,
    unit: 'errors'
  }
}

export function simcityToLatencyMetrics(metrics: SimMetrics): MetricSnapshot {
  const latency = getLatencyMs(metrics)

  return {
    timestamp: new Date().toISOString(),
    value: latency,
    unit: 'ms'
  }
}

export function simcityToHealth(
  _metrics: SimMetrics,
  violations: InvariantViolation[]
): HealthStatus {
  const hasCritical = violations.some((v) => v.severity === 'critical')
  const hasHigh = violations.some((v) => v.severity === 'high')

  const overall = hasCritical ? 'down' : hasHigh ? 'degraded' : 'ok'

  return {
    overall,
    services: [
      { name: 'simcity-core', status: overall },
      { name: 'booking-sim', status: overall },
      { name: 'vendor-sim', status: overall }
    ]
  }
}

export function simcityToSLOs(
  metrics: SimMetrics,
  __violations: InvariantViolation[]
): SLOStatus[] {
  const completed = metrics.completedBookings ?? 0
  const cancelled = metrics.cancelledBookings ?? 0
  const errors = metrics.errors ?? metrics.errorCount ?? 0
  const total = completed + cancelled || 0

  const successRate = total > 0 ? (completed / total) * 100 : 0
  const errorRate = total > 0 ? (errors / total) * 100 : 0

  const bookingStatus: SLOStatus = {
    name: 'Booking success rate',
    status: successRate > 98 ? 'ok' : successRate > 90 ? 'warning' : 'breach',
    currentValue: Math.round(successRate * 100) / 100,
    target: '99%'
  }

  const errorStatus: SLOStatus = {
    name: 'Error rate',
    status: errorRate <= 0.5 ? 'ok' : errorRate <= 2 ? 'warning' : 'breach',
    currentValue: Math.round(errorRate * 100) / 100,
    target: '0.5%'
  }

  return [bookingStatus, errorStatus]
}

export function simcityToIncidents(
  violations: InvariantViolation[]
): Incident[] {
  return violations.map((violation, idx) => ({
    id: `${violation.code}-${violation.timestamp}-${idx}`,
    title: violation.message || violation.code,
    severity: violation.severity,
    status: 'open',
    summary: violation.details
      ? `${violation.message} (${JSON.stringify(violation.details)})`
      : violation.message
  }))
}

export function simcityToAnomaly(
  violations: InvariantViolation[]
): AnomalyReport {
  const hasCritical = violations.some((v) => v.severity === 'critical')
  const hasHigh = violations.some((v) => v.severity === 'high')
  const hasMedium = violations.some((v) => v.severity === 'medium')

  const status = hasCritical
    ? 'severe'
    : hasHigh
    ? 'moderate'
    : hasMedium
    ? 'minor'
    : 'none'

  return {
    status,
    findings: violations.map((v) => ({
      source: v.code,
      severity: v.severity,
      message: v.message
    }))
  }
}

export function simcityToDeployments(
  runInfo: SimRunInfo | null,
  violations: InvariantViolation[] = []
): Deployment[] {
  const criticalViolation = violations.some((v) => v.severity === 'critical')

  return [
    {
      id: runInfo?.runId ?? 'simcity-run',
      env: 'simcity',
      status: criticalViolation ? 'failed' : 'success'
    }
  ]
}

export function simcityToDeployReadiness(
  violations: InvariantViolation[]
): DeployReadiness {
  const hasCritical = violations.some((v) => v.severity === 'critical')
  const hasHigh = violations.some((v) => v.severity === 'high')

  if (hasCritical) {
    return { status: 'blocked', reasons: ['Critical invariant violations present'] }
  }

  if (hasHigh) {
    return { status: 'risky', reasons: ['High-severity invariant violations detected'] }
  }

  return { status: 'safe', reasons: [] }
}

export function simcityToLogs(
  violations: InvariantViolation[]
): LogsQueryResult {
  const entries: LogEntry[] = violations.map((v) => ({
    timestamp: v.timestamp,
    level: mapSeverityToLevel(v.severity),
    message: `[${v.code}] ${v.message}`
  }))

  return { entries }
}

export function simEventToOpsLog(
  e: SimEventPayload
): LogEntry | null {
  if (
    e.type !== 'error' &&
    e.type !== 'metric_spike' &&
    e.type !== 'invariant_violation'
  ) {
    return null
  }

  const level = e.type === 'error' ? 'ERROR' : 'WARN'
  const message =
    typeof e.data?.message === 'string'
      ? e.data.message
      : e.type === 'metric_spike'
      ? 'Metric spike detected'
      : e.type === 'invariant_violation'
      ? 'Invariant violation detected'
      : 'SimCity event'

  return {
    timestamp: e.timestamp,
    level,
    message
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { createClient } from '@supabase/supabase-js'
import { loadBaselines, saveBaseline, getLatestBaseline, deleteBaseline } from '../../../../../scripts/ops-baseline-store'
import type { BaselineData } from '@/lib/regression/regressionAI'

import { supabaseAdmin as supabase } from '@/lib/supabaseProxies';

/**
 * GET /ops/baseline
 * 
 * List all baselines or get a specific baseline
 */
export async function GET(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url)
    const metricType = searchParams.get('metricType')
    const endpoint = searchParams.get('endpoint')
    const period = searchParams.get('period') as 'normal' | 'peak' | 'low' | null

    if (metricType && period) {
      // Get specific baseline
      const baseline = getLatestBaseline(metricType, endpoint || undefined, period)
      if (!baseline) {
        return NextResponse.json({
          success: false,
          error: 'Baseline not found'
        }, { status: 404 })
      }
      return NextResponse.json({
        success: true,
        baseline
      })
    }

    // List all baselines
    const baselines = loadBaselines(metricType || undefined, endpoint || undefined)

    return NextResponse.json({
      success: true,
      baselines,
      count: baselines.length
    })

  } catch (error) {
    console.error('Baseline GET endpoint error:', error)
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    )
  }
}

/**
 * POST /ops/baseline
 * 
 * Create a new baseline from current metrics
 */
export async function POST(request: NextRequest) {
  try {
    const body = await request.json()
    const { 
      metricType, 
      endpoint, 
      period = 'normal',
      timeRange = '24h',
      deployId,
      commitSha,
      notes
    } = body

    if (!metricType) {
      return NextResponse.json({
        success: false,
        error: 'metricType is required'
      }, { status: 400 })
    }

    if (!['p95', 'booking', 'error', 'system'].includes(metricType)) {
      return NextResponse.json({
        success: false,
        error: 'metricType must be one of: p95, booking, error, system'
      }, { status: 400 })
    }

    // Calculate time range
    const now = new Date()
    let startTime: Date

    switch (timeRange) {
      case '1h':
        startTime = new Date(now.getTime() - 60 * 60 * 1000)
        break
      case '6h':
        startTime = new Date(now.getTime() - 6 * 60 * 60 * 1000)
        break
      case '24h':
        startTime = new Date(now.getTime() - 24 * 60 * 60 * 1000)
        break
      case '7d':
        startTime = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000)
        break
      default:
        startTime = new Date(now.getTime() - 24 * 60 * 60 * 1000)
    }

    // Fetch metrics and calculate baseline data
    const baselineData: BaselineData = {}

    if (metricType === 'p95') {
      let query = supabase
        .from('api_metrics_5m')
        .select('endpoint, method, p95_ms, p99_ms, reqs')
        .gte('bucket', startTime.toISOString())
        .order('bucket', { ascending: true })

      if (endpoint) {
        query = query.eq('endpoint', endpoint)
      }

      const { data: metrics } = await query

      if (metrics && metrics.length > 0) {
        // eslint-disable-next-line @typescript-eslint/no-explicit-any
        const p95Values = metrics.map((m: any) => m.p95_ms).filter((v: number) => v != null && v > 0)
        // eslint-disable-next-line @typescript-eslint/no-explicit-any
        const p99Values = metrics.map((m: any) => m.p99_ms).filter((v: number) => v != null && v > 0)

        if (p95Values.length > 0) {
          baselineData.p95_latency_ms = p95Values.reduce((sum: number, v: number) => sum + v, 0) / p95Values.length
        }
        if (p99Values.length > 0) {
          baselineData.p99_latency_ms = p99Values.reduce((sum: number, v: number) => sum + v, 0) / p99Values.length
        }
      }
    } else if (metricType === 'booking') {
      const { data: bookings } = await supabase
        .from('bookings')
        .select('id, status, created_at, confirmed_at, total_amount_cents, price_cents')
        .gte('created_at', startTime.toISOString())

      if (bookings && bookings.length > 0) {
        const hours = (now.getTime() - startTime.getTime()) / (1000 * 60 * 60)
        baselineData.bookings_per_hour = bookings.length / hours

        const confirmed = bookings.filter(b => b.status === 'confirmed' || b.confirmed_at).length
        baselineData.conversion_rate = bookings.length > 0 ? (confirmed / bookings.length) * 100 : 0
      }
    } else if (metricType === 'error') {
      const { data: errors } = await supabase
        .from('performance_metrics')
        .select('status_code, created_at')
        .gte('created_at', startTime.toISOString())
        .gte('status_code', 400)

      const { count: totalRequests } = await supabase
        .from('performance_metrics')
        .select('*', { count: 'exact', head: true })
        .gte('created_at', startTime.toISOString())

      if (errors && totalRequests) {
        const hours = (now.getTime() - startTime.getTime()) / (1000 * 60 * 60)
        baselineData.error_count_per_hour = errors.length / hours
        baselineData.error_rate_percent = totalRequests > 0 ? (errors.length / totalRequests) * 100 : 0
      }
    } else if (metricType === 'system') {
      const { data: performanceData } = await supabase
        .from('performance_analytics_5min')
        .select('cache_hit_rate_percent, avg_database_queries, request_count')
        .gte('five_minute_bucket', startTime.toISOString())

      if (performanceData && performanceData.length > 0) {
        // eslint-disable-next-line @typescript-eslint/no-explicit-any
        const cpuValues = performanceData.map((item: any) => {
          return Math.min(100,
            (item.request_count || 0) * 0.1 +
            (item.avg_database_queries || 0) * 2
          )
        })
        // eslint-disable-next-line @typescript-eslint/no-explicit-any
        const memoryValues = performanceData.map((item: any) => {
          return Math.min(100,
            30 + (item.request_count || 0) * 0.05
          )
        })
        const cacheValues = performanceData
          // eslint-disable-next-line @typescript-eslint/no-explicit-any
          .map((item: any) => item.cache_hit_rate_percent)
          .filter((v: number) => v != null && !isNaN(v))

        if (cpuValues.length > 0) {
          baselineData.cpu_percent = cpuValues.reduce((sum: number, v: number) => sum + v, 0) / cpuValues.length
        }
        if (memoryValues.length > 0) {
          baselineData.memory_percent = memoryValues.reduce((sum: number, v: number) => sum + v, 0) / memoryValues.length
        }
        if (cacheValues.length > 0) {
          baselineData.cache_hit_rate = cacheValues.reduce((sum: number, v: number) => sum + v, 0) / cacheValues.length
        }
      }
    }

    // Create baseline
    const baseline = saveBaseline({
      metric_type: metricType as 'p95' | 'booking' | 'error' | 'system',
      endpoint: endpoint || undefined,
      period: period as 'normal' | 'peak' | 'low',
      data: baselineData,
      metadata: {
        deploy_id: deployId,
        commit_sha: commitSha,
        notes
      }
    })

    return NextResponse.json({
      success: true,
      baseline,
      message: `Baseline created for ${metricType}${endpoint ? ` (${endpoint})` : ''}`
    })

  } catch (error) {
    console.error('Baseline POST endpoint error:', error)
    return NextResponse.json(
      { error: 'Internal server error', details: error instanceof Error ? error.message : 'Unknown error' },
      { status: 500 }
    )
  }
}

/**
 * DELETE /ops/baseline
 * 
 * Delete a baseline by ID
 */
export async function DELETE(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url)
    const id = searchParams.get('id')

    if (!id) {
      return NextResponse.json({
        success: false,
        error: 'id parameter is required'
      }, { status: 400 })
    }

    const deleted = deleteBaseline(id)

    if (!deleted) {
      return NextResponse.json({
        success: false,
        error: 'Baseline not found'
      }, { status: 404 })
    }

    return NextResponse.json({
      success: true,
      message: 'Baseline deleted'
    })

  } catch (error) {
    console.error('Baseline DELETE endpoint error:', error)
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    )
  }
}

/**
 * SimCity Phase 6: Evaluator
 *
 * Computes metric deltas and evaluates dials to determine if a variant is allowed.
 * All functions are deterministic and pure.
 */

import { stableHash } from './simcity-hash'
import type {
  MetricDelta,
  DialStatus,
  DialDefinition,
  EvaluationResult,
  MetricId,
  SimCityEventEnvelope,
} from './simcity-types'
import { METRICS_REGISTRY } from './simcity-metrics'
import { computeMetricsFromEvents } from './simcity-metric-extractors'

/**
 * Compute metric deltas between base and variant metrics.
 */
export function computeMetricDeltas(
  baseMetrics: Record<MetricId, number>,
  variantMetrics: Record<MetricId, number>
): MetricDelta[] {
  const deltas: MetricDelta[] = []

  for (const metricId of Object.keys(METRICS_REGISTRY) as MetricId[]) {
    const base = baseMetrics[metricId] ?? 0
    const variant = variantMetrics[metricId] ?? 0
    const delta = variant - base

    const metric = METRICS_REGISTRY[metricId]
    let direction: 'improved' | 'degraded' | 'neutral'

    // Determine direction based on metric direction and delta
    if (Math.abs(delta) < 1e-9) {
      direction = 'neutral'
    } else if (metric.direction === 'higher-is-better') {
      direction = delta > 0 ? 'improved' : 'degraded'
    } else {
      // lower-is-better
      direction = delta < 0 ? 'improved' : 'degraded'
    }

    deltas.push({
      id: metricId,
      base,
      variant,
      delta,
      direction,
    })
  }

  // Sort deterministically by metric ID
  deltas.sort((a, b) => a.id.localeCompare(b.id))

  return deltas
}

/**
 * Evaluate dials against metrics and classify zones.
 */
export function evalDials(
  metrics: Record<MetricId, number>,
  dials: DialDefinition[]
): DialStatus[] {
  const statuses: DialStatus[] = []

  for (const dial of dials) {
    const value = metrics[dial.metric] ?? 0
    const metric = METRICS_REGISTRY[dial.metric]

    if (!metric) {
      // Skip dials for unknown metrics
      continue
    }

    let zone: 'green' | 'yellow' | 'red'

    const [greenMin, greenMax] = dial.green
    const [yellowMin, yellowMax] = dial.yellow
    const [_redMin, _redMax] = dial.red

    // Classify zone based on metric direction
    if (metric.direction === 'lower-is-better') {
      // Lower values are better: green is lowest range
      if (value >= greenMin && value <= greenMax) {
        zone = 'green'
      } else if (value >= yellowMin && value <= yellowMax) {
        zone = 'yellow'
      } else {
        zone = 'red'
      }
    } else {
      // Higher values are better: green is highest range
      if (value >= greenMin && value <= greenMax) {
        zone = 'green'
      } else if (value >= yellowMin && value <= yellowMax) {
        zone = 'yellow'
      } else {
        zone = 'red'
      }
    }

    statuses.push({
      metric: dial.metric,
      value,
      zone,
    })
  }

  // Sort deterministically by metric ID
  statuses.sort((a, b) => a.metric.localeCompare(b.metric))

  return statuses
}

/**
 * Generate deterministic summary string.
 */
function generateSummary(
  dialStatuses: DialStatus[],
  deltas?: MetricDelta[]
): string {
  const redCount = dialStatuses.filter((s) => s.zone === 'red').length
  const yellowCount = dialStatuses.filter((s) => s.zone === 'yellow').length
  const greenCount = dialStatuses.filter((s) => s.zone === 'green').length

  const parts: string[] = []

  parts.push(`Status: ${redCount} red, ${yellowCount} yellow, ${greenCount} green`)

  // Add top 3 worst red/yellow by magnitude
  const worst = [...dialStatuses]
    .filter((s) => s.zone === 'red' || s.zone === 'yellow')
    .sort((a, b) => {
      // Sort by zone (red first) then by value magnitude
      if (a.zone !== b.zone) {
        return a.zone === 'red' ? -1 : 1
      }
      return Math.abs(b.value) - Math.abs(a.value)
    })
    .slice(0, 3)

  if (worst.length > 0) {
    parts.push('Top issues:')
    for (const status of worst) {
      const metric = METRICS_REGISTRY[status.metric]
      parts.push(`  ${status.metric}: ${status.value.toFixed(3)} ${metric.unit} (${status.zone})`)
    }
  }

  // Add top deltas if provided
  if (deltas && deltas.length > 0) {
    const topDeltas = [...deltas]
      .filter((d) => d.direction !== 'neutral')
      .sort((a, b) => Math.abs(b.delta) - Math.abs(a.delta))
      .slice(0, 3)

    if (topDeltas.length > 0) {
      parts.push('Top changes:')
      for (const delta of topDeltas) {
        const sign = delta.delta > 0 ? '+' : ''
        parts.push(`  ${delta.id}: ${sign}${delta.delta.toFixed(3)} (${delta.direction})`)
      }
    }
  }

  return parts.join('\n')
}

/**
 * Evaluate a replay variant against dials.
 */
export function evaluateReplayVariant({
  reportHash,
  baseEvents,
  variantEvents,
  dials,
  variantId,
}: {
  reportHash: string
  baseEvents: SimCityEventEnvelope[]
  variantEvents: SimCityEventEnvelope[]
  dials: DialDefinition[]
  variantId?: string
}): EvaluationResult {
  // Compute metrics
  const baseMetrics = computeMetricsFromEvents(baseEvents)
  const variantMetrics = computeMetricsFromEvents(variantEvents)

  // Evaluate dials for variant
  const dialStatuses = evalDials(variantMetrics, dials)

  // Compute deltas if variant is not base
  const deltas = variantId && variantId !== 'base' ? computeMetricDeltas(baseMetrics, variantMetrics) : undefined

  // Determine allowed status
  const violated = dialStatuses.filter((s) => s.zone === 'red')
  const warnings = dialStatuses.filter((s) => s.zone === 'yellow')
  const allowed = violated.length === 0

  // Generate summary
  const summary = generateSummary(dialStatuses, deltas)

  return {
    allowed,
    violated,
    warnings,
    summary,
    reportHash,
    variantId,
  }
}

/**
 * SimCity Phase 7: Governance Rules
 *
 * Deterministic rules that evaluate proposals and produce governance decisions.
 * Rules never throw; they return null if inputs are missing.
 */

import type {
  GovernanceContext,
  GovernanceVerdict,
  GovernanceReason,
  OverrideRequirement,
  _MetricDelta,
} from './simcity-types'
import { METRICS_REGISTRY } from './simcity-metrics'

export type GovernanceRuleResult = {
  verdict?: GovernanceVerdict
  reason?: GovernanceReason
  override?: OverrideRequirement
} | null

export type GovernanceRule = {
  id: string
  description: string
  evaluate: (ctx: GovernanceContext) => GovernanceRuleResult
}

// Thresholds (constants, documented)
const TRUST_VIOLATION_RATE_THRESHOLD = 0.01 // Block if trust violation rate increases by more than 1%
const LATENCY_REGRESSION_THRESHOLD = 0.05 // Warn if latency p95 increases by more than 5%
const ERROR_RATE_REGRESSION_THRESHOLD = 0.02 // Block if error rate increases by more than 2%

/**
 * Rule: Block if any dial status is red.
 */
function blockOnRedDial(ctx: GovernanceContext): GovernanceRuleResult {
  if (!ctx.dialsSnapshot || ctx.dialsSnapshot.length === 0) {
    return null // Missing input, let fail-closed handle it
  }

  const redDials = ctx.dialsSnapshot.filter((d) => d.zone === 'red')

  if (redDials.length > 0) {
    return {
      verdict: 'block',
      reason: {
        ruleId: 'block-on-red-dial',
        severity: 'block',
        message: `${redDials.length} dial(s) in red zone`,
        evidence: {
          redDials: redDials.map((d) => ({ metric: d.metric, value: d.value })),
        },
      },
    }
  }

  return null
}

/**
 * Rule: Warn if any dial status is yellow (unless already blocked).
 */
function warnOnYellowDial(ctx: GovernanceContext): GovernanceRuleResult {
  if (!ctx.dialsSnapshot || ctx.dialsSnapshot.length === 0) {
    return null
  }

  const yellowDials = ctx.dialsSnapshot.filter((d) => d.zone === 'yellow')

  if (yellowDials.length > 0) {
    return {
      verdict: 'warn',
      reason: {
        ruleId: 'warn-on-yellow-dial',
        severity: 'warn',
        message: `${yellowDials.length} dial(s) in yellow zone`,
        evidence: {
          yellowDials: yellowDials.map((d) => ({ metric: d.metric, value: d.value })),
        },
      },
    }
  }

  return null
}

/**
 * Rule: Block if trust violation rate regression exceeds threshold.
 */
function blockOnTrustRegression(ctx: GovernanceContext): GovernanceRuleResult {
  if (!ctx.replayEvaluation?.deltas) {
    return null
  }

  const trustDelta = ctx.replayEvaluation.deltas.find((d) => d.id === 'trust.violation_rate')

  if (!trustDelta || trustDelta.direction === 'neutral') {
    return null
  }

  // Check if trust violation rate worsened (increased for lower-is-better metric)
  if (trustDelta.direction === 'degraded' && trustDelta.delta !== null) {
    const absoluteDelta = Math.abs(trustDelta.delta)
    if (absoluteDelta > TRUST_VIOLATION_RATE_THRESHOLD) {
      return {
        verdict: 'block',
        reason: {
          ruleId: 'block-on-trust-regression',
          severity: 'block',
          message: `Trust violation rate regression exceeds threshold (delta: ${trustDelta.delta.toFixed(4)})`,
          evidence: {
            base: trustDelta.base,
            variant: trustDelta.variant,
            delta: trustDelta.delta,
            threshold: TRUST_VIOLATION_RATE_THRESHOLD,
          },
        },
      }
    }
  }

  return null
}

/**
 * Rule: Warn if latency p95 regression exceeds threshold.
 */
function warnOnLatencyRegression(ctx: GovernanceContext): GovernanceRuleResult {
  if (!ctx.replayEvaluation?.deltas) {
    return null
  }

  const latencyDelta = ctx.replayEvaluation.deltas.find((d) => d.id === 'latency.p95')

  if (!latencyDelta || latencyDelta.direction === 'neutral' || latencyDelta.base === null || latencyDelta.base === 0) {
    return null
  }

  // Calculate percentage increase
  if (latencyDelta.direction === 'degraded' && latencyDelta.delta !== null) {
    const percentIncrease = Math.abs(latencyDelta.delta / latencyDelta.base)

    if (percentIncrease > LATENCY_REGRESSION_THRESHOLD) {
      return {
        verdict: 'warn',
        reason: {
          ruleId: 'warn-on-latency-regression',
          severity: 'warn',
          message: `Latency p95 regression exceeds threshold (${(percentIncrease * 100).toFixed(1)}% increase)`,
          evidence: {
            base: latencyDelta.base,
            variant: latencyDelta.variant,
            delta: latencyDelta.delta,
            percentIncrease,
            threshold: LATENCY_REGRESSION_THRESHOLD,
          },
        },
      }
    }
  }

  return null
}

/**
 * Rule: Block if error rate regression exceeds threshold.
 */
function blockOnErrorRateRegression(ctx: GovernanceContext): GovernanceRuleResult {
  if (!ctx.replayEvaluation?.deltas) {
    return null
  }

  const errorDelta = ctx.replayEvaluation.deltas.find((d) => d.id === 'error.rate')

  if (!errorDelta || errorDelta.direction === 'neutral') {
    return null
  }

  // Check if error rate worsened (increased for lower-is-better metric)
  if (errorDelta.direction === 'degraded' && errorDelta.delta !== null) {
    const absoluteDelta = Math.abs(errorDelta.delta)
    if (absoluteDelta > ERROR_RATE_REGRESSION_THRESHOLD) {
      return {
        verdict: 'block',
        reason: {
          ruleId: 'block-on-error-rate-regression',
          severity: 'block',
          message: `Error rate regression exceeds threshold (delta: ${errorDelta.delta.toFixed(4)})`,
          evidence: {
            base: errorDelta.base,
            variant: errorDelta.variant,
            delta: errorDelta.delta,
            threshold: ERROR_RATE_REGRESSION_THRESHOLD,
          },
        },
      }
    }
  }

  return null
}

/**
 * Rule: Require override for apply/promote actions.
 */
function requireOverrideForApply(ctx: GovernanceContext): GovernanceRuleResult {
  const action = ctx.proposal.action.toLowerCase()

  if (action.includes('apply') || action.includes('promote') || action.includes('execute')) {
    return {
      verdict: 'warn', // Downgrade to warn with override requirement
      reason: {
        ruleId: 'require-override-for-apply',
        severity: 'warn',
        message: 'Action suggests apply/promote/execute - override required',
        evidence: {
          action: ctx.proposal.action,
        },
      },
      override: {
        reason: 'Action requires explicit approval for apply/promote/execute operations',
        roleRequired: 'admin',
        expiresAfterTicks: 100, // Expires after 100 ticks
      },
    }
  }

  return null
}

/**
 * Default governance rules registry.
 * Rules are evaluated in order; first block wins, then warn.
 */
export const DEFAULT_GOVERNANCE_RULES: GovernanceRule[] = [
  {
    id: 'block-on-red-dial',
    description: 'Block if any dial status is red',
    evaluate: blockOnRedDial,
  },
  {
    id: 'block-on-trust-regression',
    description: 'Block if trust violation rate regression exceeds threshold',
    evaluate: blockOnTrustRegression,
  },
  {
    id: 'block-on-error-rate-regression',
    description: 'Block if error rate regression exceeds threshold',
    evaluate: blockOnErrorRateRegression,
  },
  {
    id: 'warn-on-yellow-dial',
    description: 'Warn if any dial status is yellow',
    evaluate: warnOnYellowDial,
  },
  {
    id: 'warn-on-latency-regression',
    description: 'Warn if latency p95 regression exceeds threshold',
    evaluate: warnOnLatencyRegression,
  },
  {
    id: 'require-override-for-apply',
    description: 'Require override for apply/promote/execute actions',
    evaluate: requireOverrideForApply,
  },
]

/**
 * LLM Adapter for SimCity Proposal Generation
 *
 * Rules:
 * - Stateless
 * - No engine imports
 * - No side effects
 * - Fully mockable
 * - Returns empty array on failure (never throws)
 */

export type SimCityLLMSnapshot = {
  tick: number
  recentEvents: Array<{
    id: string
    tick: number
    domain: string
    type: string
    payload: Record<string, unknown>
  }>
  enabledDomains: string[]
  config: {
    seed: number
    scenarios?: string[]
  }
}

export type LLMProposalDraft = {
  domain: string
  action: string
  description: string
  confidence?: number
  evidenceHints?: string[]
}

/**
 * Generate proposal drafts from LLM based on SimCity state snapshot.
 *
 * This function is sandboxed and must not:
 * - Import from simcity.ts or simcity-domains.ts
 * - Mutate any external state
 * - Throw errors (returns [] on failure)
 * - Make network calls in CI (must be mockable)
 *
 * @param snapshot - Current SimCity state snapshot
 * @returns Array of proposal drafts (empty on failure)
 */
export async function generateLLMProposals(
  _snapshot: SimCityLLMSnapshot
): Promise<LLMProposalDraft[]> {
  // Fail-closed: return empty array if LLM is not available or fails
  try {
    // Check if we're in CI or if LLM is disabled
    const isCI = process.env.CI === 'true' || process.env.NODE_ENV === 'test'
    const llmEnabled = process.env.SIMCITY_LLM_ENABLED === 'true'

    if (isCI || !llmEnabled) {
      // In CI or when disabled, return empty array (no network calls)
      return []
    }

    // In non-CI environments with LLM enabled, we would call an LLM service here
    // For now, we return empty array to maintain fail-closed behavior
    // This can be extended later with actual LLM integration

    // Example structure for future implementation:
    // const response = await fetch(LLM_ENDPOINT, {
    //   method: 'POST',
    //   body: JSON.stringify({
    //     tick: snapshot.tick,
    //     events: snapshot.recentEvents,
    //     domains: snapshot.enabledDomains,
    //   }),
    // })
    // if (!response.ok) return []
    // const drafts = await response.json()
    // return normalizeLLMResponse(drafts)

    return []
  } catch {
    // Never throw - fail closed
    return []
  }
}

/**
 * Normalize LLM confidence to [0, 1] range.
 * Handles various formats (0-1, 0-100, etc.)
 */
export function normalizeLLMConfidence(value: unknown): number {
  if (typeof value !== 'number' || !Number.isFinite(value)) {
    return 0.5 // Default confidence if invalid
  }

  // If value is > 1, assume it's 0-100 scale
  if (value > 1) {
    return Math.max(0, Math.min(1, value / 100))
  }

  return Math.max(0, Math.min(1, value))
}

/**
 * SimCity Phase 6: Metric Extractors
 *
 * Pure functions that compute metrics from event streams.
 * All functions are deterministic: same input -> same output.
 */

import type { SimCityEventEnvelope, MetricId } from './simcity-types'
import { METRICS_REGISTRY } from './simcity-metrics'

/**
 * Compute p95 percentile deterministically from a sorted numeric array.
 */
function computeP95(values: number[]): number {
  if (values.length === 0) {
    return 0
  }

  const sorted = [...values].sort((a, b) => a - b)
  const index = Math.ceil(0.95 * sorted.length) - 1
  return sorted[Math.max(0, Math.min(index, sorted.length - 1))]
}

/**
 * Extract latency values from events.
 * Looks for latency_jitter events or payload.latency fields.
 */
function extractLatencies(events: SimCityEventEnvelope[]): number[] {
  const latencies: number[] = []

  for (const envelope of events) {
    const { type, payload } = envelope.event

    // Check for latency_jitter events
    if (type === 'latency_jitter' && typeof payload.ms === 'number') {
      latencies.push(payload.ms)
    }

    // Check for latency in payload
    if (typeof payload.latency === 'number') {
      latencies.push(payload.latency)
    }
    if (typeof payload.latencyMs === 'number') {
      latencies.push(payload.latencyMs)
    }
  }

  return latencies
}

/**
 * Count error events.
 * Events with type containing 'error' or payload.error === true.
 */
function countErrors(events: SimCityEventEnvelope[]): number {
  let count = 0

  for (const envelope of events) {
    const { type, payload } = envelope.event

    if (type.includes('error') || type.includes('failure') || type.includes('fault')) {
      count++
    } else if (payload.error === true || payload.failed === true) {
      count++
    }
  }

  return count
}

/**
 * Extract capacity/utilization signals from booking-load events.
 */
function extractCapacityUtilization(events: SimCityEventEnvelope[]): number {
  // Look for booking-load events with utilization signals
  for (const envelope of events) {
    if (envelope.event.domain === 'booking-load') {
      const { payload } = envelope.event

      if (typeof payload.utilization === 'number') {
        return Math.max(0, Math.min(1, payload.utilization))
      }
      if (typeof payload.pressure === 'number') {
        // Approximate utilization from pressure (0-1 scale)
        return Math.max(0, Math.min(1, payload.pressure))
      }
      if (typeof payload.load === 'number') {
        // Approximate utilization from load (normalize to 0-1)
        return Math.max(0, Math.min(1, payload.load / 10))
      }
    }
  }

  // Default: no utilization signal found
  return 0
}

/**
 * Extract booking success/drop signals.
 * Since we may not have explicit booking.success events yet, approximate from booking-load signals.
 */
function extractBookingMetrics(events: SimCityEventEnvelope[]): {
  successRate: number
  dropRate: number
} {
  let totalBookingSignals = 0
  let successSignals = 0
  let dropSignals = 0

  for (const envelope of events) {
    const { domain, type, payload } = envelope.event

    // Count booking-related events
    if (domain === 'booking-load' || domain === 'booking') {
      totalBookingSignals++

      // Success indicators
      if (type.includes('success') || type.includes('completed') || payload.success === true) {
        successSignals++
      }

      // Drop indicators
      if (type.includes('drop') || type.includes('reject') || payload.dropped === true) {
        dropSignals++
      }
    }
  }

  // If no booking signals, return defaults
  if (totalBookingSignals === 0) {
    return { successRate: 0, dropRate: 0 }
  }

  // Compute rates
  const successRate = successSignals / totalBookingSignals
  const dropRate = dropSignals / totalBookingSignals

  // If no explicit success/drop signals, approximate from load spikes (high load = lower success)
  if (successSignals === 0 && dropSignals === 0) {
    const loadSpikes = events.filter(
      (e) => e.event.domain === 'booking-load' && e.event.type === 'load_spike'
    ).length

    // Approximate: more load spikes = lower success rate
    const estimatedSuccessRate = Math.max(0, Math.min(1, 1 - loadSpikes / Math.max(1, totalBookingSignals) * 0.1))
    return { successRate: estimatedSuccessRate, dropRate: 1 - estimatedSuccessRate }
  }

  return { successRate, dropRate }
}

/**
 * Extract trust violation rate.
 * Look for trust domain events or violation indicators.
 */
function extractTrustViolationRate(events: SimCityEventEnvelope[]): number {
  let violations = 0
  let trustEvents = 0

  for (const envelope of events) {
    const { domain, type, payload } = envelope.event

    if (domain === 'trust' || domain === 'safety') {
      trustEvents++
      if (type.includes('violation') || payload.violation === true) {
        violations++
      }
    }
  }

  if (trustEvents === 0) {
    return 0 // No trust domain events yet
  }

  return violations / trustEvents
}

/**
 * Compute all metrics from an event stream.
 * Returns a record with all metric IDs and their computed values.
 * Missing signals return explicit defaults (0 or neutral values).
 */
export function computeMetricsFromEvents(
  events: SimCityEventEnvelope[]
): Record<MetricId, number> {
  const metrics: Partial<Record<MetricId, number>> = {}

  // Extract latencies and compute p95
  const latencies = extractLatencies(events)
  metrics['latency.p95'] = computeP95(latencies)

  // Count errors
  const errorCount = countErrors(events)
  const totalEvents = events.length
  metrics['error.rate'] = totalEvents > 0 ? errorCount / totalEvents : 0

  // Extract capacity utilization
  metrics['capacity.utilization'] = extractCapacityUtilization(events)

  // Extract booking metrics
  const bookingMetrics = extractBookingMetrics(events)
  metrics['booking.success_rate'] = bookingMetrics.successRate
  metrics['booking.drop_rate'] = bookingMetrics.dropRate

  // Extract trust violation rate
  metrics['trust.violation_rate'] = extractTrustViolationRate(events)

  // Ensure all metrics are present (fill missing with 0)
  const allMetrics: Record<MetricId, number> = {
    'booking.success_rate': metrics['booking.success_rate'] ?? 0,
    'booking.drop_rate': metrics['booking.drop_rate'] ?? 0,
    'capacity.utilization': metrics['capacity.utilization'] ?? 0,
    'trust.violation_rate': metrics['trust.violation_rate'] ?? 0,
    'latency.p95': metrics['latency.p95'] ?? 0,
    'error.rate': metrics['error.rate'] ?? 0,
  }

  return allMetrics
}

/**
 * SimCity Phase 9: Override Validation
 *
 * Validates override requests before appending.
 * Fail-closed: rejects invalid overrides.
 */

import type {
  _OverrideRecord,
  PromotionDecision,
  SimCityProposal,
  OverrideActor,
  OverrideVerdict,
} from './simcity-types'

export interface OverrideRequest {
  proposalId: string
  decisionHash: string
  verdictAfter: OverrideVerdict
  justification: string
  actor: OverrideActor
}

export interface ValidateOverrideParams {
  proposal: SimCityProposal
  decision: PromotionDecision
  override: OverrideRequest
}

/**
 * Validate an override request.
 * Fail-closed: throws on any validation failure.
 *
 * @throws {Error} with message codes:
 * - OVERRIDE_NOT_ALLOWED: decision doesn't require overrides
 * - JUSTIFICATION_REQUIRED: justification is empty
 * - INSUFFICIENT_ROLE: actor role doesn't match requirements
 * - DECISION_HASH_MISMATCH: decisionHash doesn't match decision
 */
export function validateOverride({
  proposal,
  decision,
  override,
}: ValidateOverrideParams): void {
  // Check that decision requires overrides
  if (!decision.requiredOverrides || decision.requiredOverrides.length === 0) {
    throw new Error('OVERRIDE_NOT_ALLOWED')
  }

  // Check justification is provided
  if (!override.justification?.trim()) {
    throw new Error('JUSTIFICATION_REQUIRED')
  }

  // Check actor role matches at least one requirement
  const roleMatches = decision.requiredOverrides.some(
    (r) => r.roleRequired === override.actor.role
  )
  if (!roleMatches) {
    throw new Error('INSUFFICIENT_ROLE')
  }

  // Check decisionHash matches
  if (override.decisionHash !== decision.decisionHash) {
    throw new Error('DECISION_HASH_MISMATCH')
  }

  // Verify proposalId matches
  if (override.proposalId !== proposal.id || override.proposalId !== decision.proposalId) {
    throw new Error('PROPOSAL_ID_MISMATCH')
  }
}

/**
 * Convert GovernanceVerdict to OverrideVerdict.
 */
export function convertToOverrideVerdict(verdict: string): OverrideVerdict {
  const upper = verdict.toUpperCase()
  if (upper === 'ALLOW' || upper === 'WARN' || upper === 'BLOCK') {
    return upper as OverrideVerdict
  }
  throw new Error(`Invalid verdict: ${verdict}`)
}

/**
 * Proposal Engine for SimCity Phase 4
 *
 * Responsibilities:
 * - Collect recent event window
 * - Generate proposal drafts (LLM / rules)
 * - Validate + score proposals
 * - Enforce caps & thresholds
 * - Return finalized proposals
 */

import { stableHash } from './simcity-hash'
import type {
  SimCityProposal,
  SimCityProposalConfig,
  _SimCityEvent,
  SimCityEventEnvelope,
} from './simcity-types'
import type { SimCityConfig } from './simcity'
import { generateLLMProposals, normalizeLLMConfidence, type LLMProposalDraft } from './simcity-llm'
import { resolveActiveDomains } from './simcity-domains'

type ProposalDraftWithSource = LLMProposalDraft & {
  _source?: 'llm' | 'rules'
}

export type SimCityState = {
  tick: number
  config: SimCityConfig
  events: SimCityEventEnvelope[]
}

/**
 * Generate rule-based proposals from recent events.
 * These are deterministic and don't require LLM.
 */
function generateRulesProposals(
  state: SimCityState,
  recentEvents: SimCityEventEnvelope[]
): LLMProposalDraft[] {
  const proposals: LLMProposalDraft[] = []
  const enabledDomains = resolveActiveDomains(state.config).map((d) => d.name)

  // Analyze recent events for patterns
  const eventsByDomain: Record<string, SimCityEventEnvelope[]> = {}
  for (const envelope of recentEvents) {
    const domain = envelope.event.domain
    if (!eventsByDomain[domain]) {
      eventsByDomain[domain] = []
    }
    eventsByDomain[domain].push(envelope)
  }

  // Rule 1: If load spikes detected, suggest throttling
  const loadSpikes = recentEvents.filter(
    (e) => e.event.domain === 'booking-load' && e.event.type === 'load_spike'
  )
  if (loadSpikes.length >= 2) {
    proposals.push({
      domain: 'booking-load',
      action: 'throttle_booking_acceptance',
      description: 'Multiple load spikes detected. Consider throttling booking acceptance rate.',
      confidence: 0.75,
      evidenceHints: loadSpikes.slice(0, 3).map((e) => e.event.id),
    })
  }

  // Rule 2: If latency jitter detected, suggest capacity pre-warming
  const latencyJitters = recentEvents.filter(
    (e) => e.event.domain === 'booking-load' && e.event.type === 'latency_jitter'
  )
  if (latencyJitters.length >= 1) {
    proposals.push({
      domain: 'booking-load',
      action: 'pre_warm_capacity',
      description: 'Latency jitter detected. Consider pre-warming capacity to reduce latency.',
      confidence: 0.7,
      evidenceHints: latencyJitters.slice(0, 2).map((e) => e.event.id),
    })
  }

  // Rule 3: If soft failures detected, suggest enabling contingency path
  const softFailures = recentEvents.filter(
    (e) => e.event.domain === 'booking-load' && e.event.type === 'soft_failure'
  )
  if (softFailures.length >= 1) {
    proposals.push({
      domain: 'booking-load',
      action: 'enable_contingency_path',
      description: 'Soft failures detected. Consider enabling contingency path for resilience.',
      confidence: 0.8,
      evidenceHints: softFailures.slice(0, 2).map((e) => e.event.id),
    })
  }

  // Rule 4: If high event rate in a domain, suggest increasing provider radius
  for (const domain of enabledDomains) {
    const domainEvents = eventsByDomain[domain] || []
    if (domainEvents.length >= 5) {
      proposals.push({
        domain,
        action: 'increase_provider_radius',
        description: `High event rate in ${domain}. Consider increasing provider radius to distribute load.`,
        confidence: 0.65,
        evidenceHints: domainEvents.slice(0, 5).map((e) => e.event.id),
      })
      break // Only one per domain
    }
  }

  return proposals
}

/**
 * Validate and score a proposal draft.
 * Returns null if proposal should be rejected.
 */
function validateProposal(
  draft: LLMProposalDraft,
  state: SimCityState,
  config: SimCityProposalConfig,
  knownEventIds: Set<string>
): SimCityProposal | null {
  const enabledDomains = resolveActiveDomains(state.config).map((d) => d.name)

  // Validation: domain must be enabled
  if (!enabledDomains.includes(draft.domain)) {
    return null
  }

  // Validation: action must be non-empty
  if (!draft.action || typeof draft.action !== 'string' || draft.action.trim().length === 0) {
    return null
  }

  // Validation: description must be non-empty
  if (!draft.description || typeof draft.description !== 'string' || draft.description.trim().length === 0) {
    return null
  }

  // Confidence resolution
  let confidence: number
  if (draft.confidence !== undefined) {
    confidence = normalizeLLMConfidence(draft.confidence)
  } else {
    // Default confidence based on source
    confidence = 0.5
  }

  // Validation: confidence must meet minimum threshold
  const minConfidence = config.minConfidence ?? 0.6
  if (confidence < minConfidence) {
    return null
  }

  // Validation: evidence event IDs must exist
  const evidenceEventIds = (draft.evidenceHints || [])
    .filter((id) => typeof id === 'string' && knownEventIds.has(id))

  // Generate deterministic proposal ID (content-hashed, no timestamps)
  const proposalId = stableHash(
    `${state.config.seed}:${state.tick}:${draft.domain}:${draft.action}:${confidence}:${evidenceEventIds.sort().join(',')}`
  )

  return {
    id: proposalId,
    tick: state.tick,
    domain: draft.domain,
    action: draft.action,
    description: draft.description,
    confidence,
    evidenceEventIds,
    source: 'llm', // Will be overridden if from rules
  }
}

/**
 * Deduplicate proposals by (domain + action) pair.
 * Keeps the proposal with highest confidence.
 */
function deduplicateProposals(proposals: SimCityProposal[]): SimCityProposal[] {
  const seen = new Map<string, SimCityProposal>()

  for (const proposal of proposals) {
    const key = `${proposal.domain}:${proposal.action}`
    const existing = seen.get(key)

    if (!existing || proposal.confidence > existing.confidence) {
      seen.set(key, proposal)
    }
  }

  return Array.from(seen.values())
}

/**
 * Generate proposals for the current SimCity state.
 *
 * This function is deterministic:
 * - Same seed + config + event stream â†’ same proposals
 * - Proposal IDs are content-hashed (no timestamps)
 * - Sorting is deterministic
 *
 * @param state - Current SimCity state
 * @param config - SimCity configuration
 * @returns Array of validated proposals (empty if proposals disabled)
 */
export async function generateProposals(
  state: SimCityState,
  config: SimCityConfig
): Promise<SimCityProposal[]> {
  const proposalConfig = config.proposals
  if (!proposalConfig) {
    return []
  }

  if (proposalConfig.mode === 'off') {
    return []
  }

  // Collect recent event window (last 20 ticks or last 50 events, whichever is smaller)
  const recentWindow = Math.min(20, state.tick)
  const recentEvents = state.events
    .filter((e) => e.generatedAtTick > state.tick - recentWindow)
    .slice(-50)

  // Build set of known event IDs for validation
  const knownEventIds = new Set(state.events.map((e) => e.event.id))

  const allDrafts: ProposalDraftWithSource[] = []

  // Generate LLM proposals if mode is 'llm' or 'hybrid'
  if (proposalConfig.mode === 'llm' || proposalConfig.mode === 'hybrid') {
    try {
      const llmDrafts = await generateLLMProposals({
        tick: state.tick,
        recentEvents: recentEvents.map((e) => ({
          id: e.event.id,
          tick: e.event.tick,
          domain: e.event.domain,
          type: e.event.type,
          payload: e.event.payload,
        })),
        enabledDomains: resolveActiveDomains(config).map((d) => d.name),
        config: {
          seed: config.seed,
          scenarios: config.scenarios,
        },
      })
      for (const draft of llmDrafts) {
        allDrafts.push({ ...draft, _source: 'llm' } as ProposalDraftWithSource)
      }
    } catch {
      // Fail closed: continue with rules proposals if LLM fails
    }
  }

  // Generate rules proposals if mode is 'rules' or 'hybrid'
  if (proposalConfig.mode === 'rules' || proposalConfig.mode === 'hybrid') {
    const rulesDrafts = generateRulesProposals(state, recentEvents)
    // Mark rules proposals with source
    for (const draft of rulesDrafts) {
      allDrafts.push({ ...draft, _source: 'rules' } as ProposalDraftWithSource)
    }
  }

  // Validate and score all drafts
  const validated: SimCityProposal[] = []
  for (const draft of allDrafts) {
    const proposal = validateProposal(draft, state, proposalConfig, knownEventIds)
    if (proposal) {
      // Set source from draft metadata
      proposal.source = draft._source ?? 'llm'
      validated.push(proposal)
    }
  }

  // Deduplicate by (domain + action)
  const deduplicated = deduplicateProposals(validated)

  // Sort deterministically (by domain, then action, then confidence desc)
  deduplicated.sort((a, b) => {
    if (a.domain !== b.domain) {
      return a.domain.localeCompare(b.domain)
    }
    if (a.action !== b.action) {
      return a.action.localeCompare(b.action)
    }
    return b.confidence - a.confidence
  })

  // Cap proposals per tick
  const maxPerTick = proposalConfig.maxPerTick ?? 3
  return deduplicated.slice(0, maxPerTick)
}

/**
 * SimCity Phase 10: Shadow Event Collector
 *
 * Collects production events for shadow simulation.
 * Phase 10 = hook-ready, not connected to production yet.
