import { NextRequest, NextResponse } from 'next/server'
import { ensureSimCityAllowed } from '../../_lib/simcity'
import { simCityStatus, simCityGetProposals, simCityCursor } from '../../_lib/simcity'
import { getReplay } from '../../_lib/simcity-replay-store'
import { DEFAULT_DIALS } from '../../_lib/simcity-dials'
import { evaluateReplayVariant, computeMetricDeltas, evalDials } from '../../_lib/simcity-evaluator'
import { computeMetricsFromEvents } from '../../_lib/simcity-metric-extractors'
import { evaluateAllProposals } from '../../_lib/simcity-governance'
import type { DialStatus, EvaluationResult, MetricDelta } from '../../_lib/simcity-types'

/**
 * GET /api/ops/controlplane/simcity/governance
 *
 * Evaluates all current proposals using governance rules.
 * Query params:
 * - variantRunId (optional): replay run ID to include in evaluation context
 * - variantId (optional): which variant to compare (default: first variant)
 *
 * Read-only, guarded by SimCity allowlist.
 */
export async function GET(request: NextRequest) {
  try {
    ensureSimCityAllowed()
  } catch (error) {
    const message = error instanceof Error ? error.message : 'SimCity is not allowed'
    return NextResponse.json({ error: message }, { status: 403 })
  }

  try {
    const status = simCityStatus()
    const _cursor = simCityCursor()

    if (!status.running) {
      return NextResponse.json({ error: 'SimCity is not running' }, { status: 400 })
    }

    // Get proposals
    const proposals = simCityGetProposals()

    // If no proposals, return empty decisions
    if (proposals.length === 0) {
      return NextResponse.json({
        success: true,
        tick: status.tick,
        decisions: [],
      })
    }

    // Get dials snapshot from live engine
    const metrics = computeMetricsFromEvents(status.events)
    const dialsSnapshot: DialStatus[] = evalDials(metrics, DEFAULT_DIALS)

    // Get replay evaluation if variantRunId provided
    const searchParams = request.nextUrl.searchParams
    const variantRunId = searchParams.get('variantRunId')
    const variantId = searchParams.get('variantId') || undefined

    let replayEvaluation:
      | {
          base?: EvaluationResult
          variant?: EvaluationResult
          deltas?: MetricDelta[]
        }
      | undefined

    let replayReportSummary:
      | {
          reportHash: string
          markdownSummary?: string
          eventDiff?: Record<string, unknown>
        }
      | undefined

    if (variantRunId) {
      const replayEntry = getReplay(variantRunId)
      if (replayEntry && replayEntry.status === 'completed' && replayEntry.report) {
        const report = replayEntry.report
        const baseEvents = replayEntry.response.baseline?.events || []
        const selectedVariant = variantId
          ? replayEntry.response.variants.find((v) => v.name === variantId)
          : replayEntry.response.variants[0]

        if (selectedVariant) {
          const variantEvents = selectedVariant.events

          // Compute metrics and evaluation
          const baseMetrics = computeMetricsFromEvents(baseEvents)
          const variantMetrics = computeMetricsFromEvents(variantEvents)
          const deltas = computeMetricDeltas(baseMetrics, variantMetrics)

          const baseEvaluation = evaluateReplayVariant({
            reportHash: report.reportHash,
            baseEvents,
            variantEvents: baseEvents,
            dials: DEFAULT_DIALS,
            variantId: 'base',
          })

          const variantEvaluation = evaluateReplayVariant({
            reportHash: report.reportHash,
            baseEvents,
            variantEvents,
            dials: DEFAULT_DIALS,
            variantId: selectedVariant.name,
          })

          replayEvaluation = {
            base: baseEvaluation,
            variant: variantEvaluation,
            deltas,
          }

          replayReportSummary = {
            reportHash: report.reportHash,
            markdownSummary: report.markdownSummary,
          }
        }
      }
    }

    // Evaluate all proposals
    const decisions = evaluateAllProposals({
      tick: status.tick,
      proposals,
      dialsSnapshot,
      replayEvaluation,
      replayReportSummary,
    })

    return NextResponse.json({
      success: true,
      tick: status.tick,
      decisions,
    })
  } catch (error) {
    const message = error instanceof Error ? error.message : 'Failed to evaluate governance'
    return NextResponse.json({ error: message }, { status: 500 })
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { ensureSimCityAllowed } from '@/app/api/ops/controlplane/_lib/simcity'
import { simCityStatus, simCityGetProposals } from '@/app/api/ops/controlplane/_lib/simcity'
import { DEFAULT_DIALS } from '@/app/api/ops/controlplane/_lib/simcity-dials'
import { evalDials } from '@/app/api/ops/controlplane/_lib/simcity-evaluator'
import { computeMetricsFromEvents } from '@/app/api/ops/controlplane/_lib/simcity-metric-extractors'
import { evaluateProposal } from '@/app/api/ops/controlplane/_lib/simcity-governance'
import { hashOverrideRecord } from '@/app/api/ops/controlplane/_lib/simcity-governance-hash'
import { validateOverride, convertToOverrideVerdict } from '@/app/api/ops/controlplane/_lib/simcity-overrides'
import { appendOverride } from '@/app/api/ops/controlplane/_lib/simcity-overrides-store'
import type { GovernanceContext, OverrideRecord, OverrideVerdict } from '@/app/api/ops/controlplane/_lib/simcity-types'
import { randomUUID } from 'node:crypto'

/**
 * POST /api/ops/controlplane/simcity/override
 *
 * Creates a human override record.
 * Append-only: never modifies existing decisions.
 * Read-only, guarded by SimCity allowlist.
 */
export async function POST(request: NextRequest) {
  try {
    ensureSimCityAllowed()
  } catch (error) {
    const message = error instanceof Error ? error.message : 'SimCity is not allowed'
    return NextResponse.json({ error: message }, { status: 403 })
  }

  try {
    const body = await request.json()
    const { proposalId, decisionHash, verdictAfter, justification, actor } = body

    // Validate required fields
    if (!proposalId || !decisionHash || !verdictAfter || !justification || !actor) {
      return NextResponse.json(
        { error: 'Missing required fields: proposalId, decisionHash, verdictAfter, justification, actor' },
        { status: 400 }
      )
    }

    // Validate actor structure
    if (!actor.userId || !actor.role) {
      return NextResponse.json(
        { error: 'Actor must have userId and role' },
        { status: 400 }
      )
    }

    const status = simCityStatus()

    if (!status.running) {
      return NextResponse.json({ error: 'SimCity is not running' }, { status: 400 })
    }

    // Get proposal
    const proposals = simCityGetProposals()
    const proposal = proposals.find((p) => p.id === proposalId)

    if (!proposal) {
      return NextResponse.json({ error: `Proposal ${proposalId} not found` }, { status: 404 })
    }

    // Load decision (re-evaluate to get current decision)
    const metrics = computeMetricsFromEvents(status.events)
    const dialsSnapshot = evalDials(metrics, DEFAULT_DIALS)

    const ctx: GovernanceContext = {
      tick: status.tick,
      proposal,
      dialsSnapshot,
    }

    const decision = evaluateProposal(ctx)

    // Validate decisionHash matches
    if (decisionHash !== decision.decisionHash) {
      return NextResponse.json(
        { error: 'DECISION_HASH_MISMATCH', message: 'Decision hash does not match current decision' },
        { status: 400 }
      )
    }

    // Validate override
    try {
      validateOverride({
        proposal,
        decision,
        override: {
          proposalId,
          decisionHash,
          verdictAfter: convertToOverrideVerdict(verdictAfter),
          justification,
          actor,
        },
      })
    } catch (validationError) {
      const message = validationError instanceof Error ? validationError.message : 'Validation failed'
      return NextResponse.json(
        { error: message, message: `Override validation failed: ${message}` },
        { status: 400 }
      )
    }

    // Build override record
    const overrideVerdictAfter = convertToOverrideVerdict(verdictAfter)
    const overrideVerdictBefore = convertToOverrideVerdict(decision.verdict.toUpperCase())

    const overrideRecord: Omit<OverrideRecord, 'overrideHash'> = {
      overrideId: randomUUID(),
      proposalId,
      decisionHash,
      verdictBefore: overrideVerdictBefore,
      verdictAfter: overrideVerdictAfter,
      actor,
      justification: justification.trim(),
      timestamp: new Date().toISOString(),
    }

    // Compute overrideHash
    const overrideHash = hashOverrideRecord(overrideRecord)

    // Append to store
    const completeRecord: OverrideRecord = {
      ...overrideRecord,
      overrideHash,
    }

    appendOverride(completeRecord)

    return NextResponse.json({
      success: true,
      override: completeRecord,
    })
  } catch (error) {
    const message = error instanceof Error ? error.message : 'Failed to create override'
    return NextResponse.json({ error: message }, { status: 500 })
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { ensureSimCityAllowed } from '@/app/api/ops/controlplane/_lib/simcity'
import { getReplay } from '@/app/api/ops/controlplane/_lib/simcity-replay-store'
import { DEFAULT_DIALS } from '@/app/api/ops/controlplane/_lib/simcity-dials'
import { evaluateReplayVariant, computeMetricDeltas, evalDials } from '@/app/api/ops/controlplane/_lib/simcity-evaluator'
import { computeMetricsFromEvents } from '@/app/api/ops/controlplane/_lib/simcity-metric-extractors'
import { stableHash } from '@/app/api/ops/controlplane/_lib/simcity-hash'
import type { MetricDelta, EvaluationResult } from '@/app/api/ops/controlplane/_lib/simcity-types'

/**
 * GET /api/ops/controlplane/simcity/replay/[runId]/evaluation?variant=base|<variantId>
 *
 * Evaluates a replay variant against dials and returns metrics, deltas, and evaluation result.
 * Read-only, guarded by SimCity allowlist.
 */
export async function GET(
  request: NextRequest,
  { params }: { params: Promise<{ runId: string }> }
) {
  try {
    ensureSimCityAllowed()
  } catch (error) {
    const message = error instanceof Error ? error.message : 'SimCity is not allowed'
    return NextResponse.json({ error: message }, { status: 403 })
  }

  try {
    const { runId } = await params
    const entry = getReplay(runId)

    if (!entry) {
      return NextResponse.json({ error: `Replay ${runId} not found` }, { status: 404 })
    }

    if (entry.status !== 'completed') {
      return NextResponse.json(
        { error: `Replay ${runId} is not completed (status: ${entry.status})` },
        { status: 400 }
      )
    }

    // Get variant from query param
    const searchParams = request.nextUrl.searchParams
    const variantParam = searchParams.get('variant') || 'base'

    // Get base events
    const baseEvents = entry.response.baseline?.events || []

    // Get variant events
    let variantEvents: typeof baseEvents
    let variantId: string

    if (variantParam === 'base') {
      variantEvents = baseEvents
      variantId = 'base'
    } else {
      const variant = entry.response.variants.find((v) => v.name === variantParam)
      if (!variant) {
        return NextResponse.json(
          { error: `Variant "${variantParam}" not found in replay ${runId}` },
          { status: 400 }
        )
      }
      variantEvents = variant.events
      variantId = variantParam
    }

    // Compute metrics
    const baseMetrics = computeMetricsFromEvents(baseEvents)
    const variantMetrics = computeMetricsFromEvents(variantEvents)

    // Evaluate dials
    const evaluation = evaluateReplayVariant({
      reportHash: entry.report?.reportHash || stableHash(JSON.stringify({ runId, variantId })),
      baseEvents,
      variantEvents,
      dials: DEFAULT_DIALS,
      variantId,
    })

    // Compute deltas if not base
    const deltas: MetricDelta[] | undefined =
      variantId !== 'base' ? computeMetricDeltas(baseMetrics, variantMetrics) : undefined

    // Get all dial statuses
    const dialStatuses = evalDials(variantMetrics, DEFAULT_DIALS)

    return NextResponse.json({
      success: true,
      reportHash: entry.report?.reportHash || '',
      variant: variantId,
      metrics: variantMetrics,
      deltas,
      dialStatuses,
      evaluation,
    })
  } catch (error) {
    const message = error instanceof Error ? error.message : 'Failed to evaluate replay variant'
    return NextResponse.json({ error: message }, { status: 500 })
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { createClient } from '@supabase/supabase-js'
import { withSLOProbe } from '@/middleware/sloProbe'
import { featureFlags } from '@/config/featureFlags'

import { supabaseAdmin as supabase } from '@/lib/supabaseProxies';

interface ForceCancelRequest {
  booking_id: string
  reason: 'ADMIN_OVERRIDE' | 'SYSTEM_ERROR' | 'FRAUD_DETECTED' | 'COMPLIANCE_VIOLATION'
  admin_notes?: string
  refund_customer?: boolean
}

const forceCancelHandler = async (req: NextRequest) => {
  try {
    // Check feature flag
    if (!featureFlags.beta.core_booking_flow) {
      return NextResponse.json(
        { error: 'Core booking flow not enabled' },
        { status: 503 }
      )
    }

    const body: ForceCancelRequest = await req.json()
    const { booking_id, reason, admin_notes, refund_customer = true } = body

    if (!booking_id || !reason) {
      return NextResponse.json(
        { error: 'Missing required fields: booking_id, reason' },
        { status: 400 }
      )
    }

    // Get booking details
    const { data: booking, error: bookingError } = await supabase
      .from('bookings')
      .select('*')
      .eq('id', booking_id)
      .single()

    if (bookingError || !booking) {
      return NextResponse.json(
        { error: 'Booking not found' },
        { status: 404 }
      )
    }

    // Check if booking can be cancelled
    if (['cancelled', 'refunded'].includes(booking.state)) {
      return NextResponse.json(
        { error: `Booking already in final state: ${booking.state}` },
        { status: 400 }
      )
    }

    // Get current state for audit log
    const fromState = booking.state

    // Update booking state to cancelled
    const { error: updateError } = await supabase
      .from('bookings')
      .update({
        state: 'cancelled',
        cancelled_at: new Date().toISOString(),
        cancelled_reason: reason
      })
      .eq('id', booking_id)

    if (updateError) {
      throw new Error(`Failed to update booking state: ${updateError.message}`)
    }

    // Log the state transition
    await supabase
      .from('booking_audit_log')
      .insert({
        booking_id: booking.id,
        from_state: fromState,
        to_state: 'cancelled',
        action: 'force_cancel',
        actor_type: 'admin',
        actor_id: 'ops_force_cancel_endpoint',
        metadata: {
          reason,
          admin_notes,
          refund_customer,
          cancelled_at: new Date().toISOString(),
          stripe_payment_intent: booking.stripe_payment_intent_id,
          original_state: fromState
        }
      })

    // If customer should be refunded, process refund
    if (refund_customer && booking.price_cents && booking.price_cents > 0) {
      // TODO: Implement actual Stripe refund
      console.log(`Would refund ${booking.price_cents} cents for force-cancelled booking ${booking_id}`)

      // Update to refunded state
      await supabase
        .from('bookings')
        .update({
          state: 'refunded',
          refunded_at: new Date().toISOString()
        })
        .eq('id', booking_id)

      // Log refund transition
      await supabase
        .from('booking_audit_log')
        .insert({
          booking_id: booking.id,
          from_state: 'cancelled',
          to_state: 'refunded',
          action: 'refund_after_force_cancel',
          actor_type: 'system',
          actor_id: 'ops_force_cancel_endpoint',
          metadata: {
            refund_amount_cents: booking.price_cents,
            refunded_at: new Date().toISOString(),
            force_cancel_reason: reason
          }
        })
    }

    console.log(`Force-cancelled booking ${booking_id} with reason: ${reason}`)

    return NextResponse.json({
      ok: true,
      data: {
        booking_id,
        cancelled_at: new Date().toISOString(),
        final_state: refund_customer ? 'refunded' : 'cancelled',
        reason,
        admin_notes
      }
    })

  } catch (error) {
    console.error('Error in force-cancel endpoint:', error)
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    )
  }
}

