// eslint-disable-next-line @typescript-eslint/no-explicit-any
const _supabase = new Proxy({} as any, { get: (target, prop) => (getServerSupabase() as any)[prop] }) as ReturnType<typeof getServerSupabase>

/**
 * GET /ops/slo/errors
 * 
 * SLOAI Agent - Error Rate SLO Assessment
 * 
 * Evaluates error rate SLO compliance, calculates burn rates,
 * and provides risk assessment for error metrics.
 */
export async function GET(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url)
    const metricName = searchParams.get('metric') || 'api_booking' // Default to booking API

    // Get error metrics
    const metrics = await sloai.getErrorMetrics(metricName)

    // Calculate burn rate from violations
    const burnRate = sloai.calculateBurnRate(metrics.violations, 24) // Last 24 hours

    // Assess risk level
    const criticalViolations = metrics.violations.filter(v => v.severity === 'critical')
    const warningViolations = metrics.violations.filter(v => v.severity === 'warning')

    let riskLevel: 'low' | 'medium' | 'high' | 'critical' = 'low'
    if (criticalViolations.length > 0) {
      riskLevel = 'critical'
    } else if (warningViolations.length > 5) {
      riskLevel = 'high'
    } else if (warningViolations.length > 0) {
      riskLevel = 'medium'
    }

    // Check if inside SLO
    const insideSLO = metrics.errorRate <= metrics.targetErrorRate

    // Calculate error budget
    // Error budget = (1 - target_error_rate) * total_requests
    // For time-based budget, we use the target error rate as the SLO
    const sloTarget = 1 - metrics.targetErrorRate // e.g., 0.99 for 1% error rate target
    const errorBudget = sloai.calculateErrorBudget(sloTarget, 720) // 30 days
    errorBudget.consumed = metrics.errorCount
    errorBudget.remaining = Math.max(0, errorBudget.total - errorBudget.consumed)
    errorBudget.burnRate = burnRate
    errorBudget.timeToExhaustion = sloai.calculateTimeToExhaustion(
      errorBudget.remaining,
      burnRate
    )
    errorBudget.riskLevel = sloai.assessRiskLevel(errorBudget, errorBudget.timeToExhaustion)

    // Generate customer impact
    const customerImpact = sloai.generateCustomerImpact(
      riskLevel,
      'errors',
      metrics.errorRate * 100,
      metrics.targetErrorRate * 100
    )

    // Generate recommendations
    const recommendations = sloai.generateRecommendations(riskLevel, 'errors')

    // Calculate error rate percentage
    const errorRatePercent = metrics.errorRate * 100
    const targetErrorRatePercent = metrics.targetErrorRate * 100

    return NextResponse.json({
      agent: 'SLOAI',
      timestamp: new Date().toISOString(),
      metric: metricName,
      status: {
        riskLevel,
        insideSLO,
        violations: {
          critical: criticalViolations.length,
          warning: warningViolations.length,
          total: metrics.violations.length
        }
      },
      metrics: {
        current: {
          errorRate: Math.round(errorRatePercent * 10000) / 10000, // 4 decimal places
          errorRatePercent: Math.round(errorRatePercent * 100) / 100,
          errorCount: metrics.errorCount,
          totalRequests: metrics.totalRequests
        },
        target: {
          errorRate: metrics.targetErrorRate,
          errorRatePercent: Math.round(targetErrorRatePercent * 100) / 100
        },
        deviation: {
          absolute: Math.round((metrics.errorRate - metrics.targetErrorRate) * 10000) / 10000,
          percent: Math.round(((metrics.errorRate / metrics.targetErrorRate - 1) * 100) * 100) / 100
        }
      },
      errorBudget: {
        total: Math.round(errorBudget.total * 100) / 100,
        consumed: errorBudget.consumed,
        remaining: Math.round(errorBudget.remaining * 100) / 100,
        burnRate: Math.round(burnRate * 100) / 100,
        timeToExhaustionHours: errorBudget.timeToExhaustion
          ? Math.round(errorBudget.timeToExhaustion * 100) / 100
          : null,
        riskLevel: errorBudget.riskLevel
      },
      customerImpact,
      humanAttentionNeeded: riskLevel === 'critical' || riskLevel === 'high',
      recommendations,
      interpretation: {
        summary: insideSLO
          ? `âœ… Error rate SLO met. Current: ${Math.round(errorRatePercent * 100) / 100}% (target: ${Math.round(targetErrorRatePercent * 100) / 100}%)`
          : `âš ï¸ Error rate SLO violated. Current: ${Math.round(errorRatePercent * 100) / 100}% (target: ${Math.round(targetErrorRatePercent * 100) / 100}%)`,
        implications: customerImpact,
        burnRateAnalysis: burnRate > 0
          ? `Current burn rate: ${Math.round(burnRate * 100) / 100} violations/hour. ${errorBudget.timeToExhaustion ? `Budget will exhaust in ${Math.round(errorBudget.timeToExhaustion)} hours if current rate continues.` : 'Budget is stable.'}`
          : 'No active violations. Burn rate: 0 violations/hour.',
        requestAnalysis: `Out of ${metrics.totalRequests.toLocaleString()} requests, ${metrics.errorCount.toLocaleString()} resulted in errors.`
      }
    })
  } catch (error) {
    console.error('SLOAI error rate assessment error:', error)
    return NextResponse.json(
      {
        agent: 'SLOAI',
        error: 'Failed to assess error rate SLO',
        message: error instanceof Error ? error.message : 'Unknown error',
        timestamp: new Date().toISOString()
      },
      { status: 500 }
    )
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { sloai } from '@/lib/observability/sloai'
import { getServerSupabase } from '@/lib/supabaseServer'

// eslint-disable-next-line @typescript-eslint/no-explicit-any
const _supabase = new Proxy({} as any, { get: (target, prop) => (getServerSupabase() as any)[prop] }) as ReturnType<typeof getServerSupabase>

/**
 * GET /ops/slo/latency
 * 
 * SLOAI Agent - Latency SLO Assessment
 * 
 * Evaluates latency SLO compliance, calculates burn rates,
 * and provides risk assessment for response time metrics.
 */
export async function GET(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url)
    const metricName = searchParams.get('metric') || 'api_booking' // Default to booking API

    // Get latency metrics
    const metrics = await sloai.getLatencyMetrics(metricName)

    // Calculate burn rate from violations
    const burnRate = sloai.calculateBurnRate(metrics.violations, 24) // Last 24 hours

    // Assess risk level
    const p95Violations = metrics.violations.filter(v => v.violation_type === 'p95')
    const p99Violations = metrics.violations.filter(v => v.violation_type === 'p99')

    let riskLevel: 'low' | 'medium' | 'high' | 'critical' = 'low'
    if (p99Violations.some(v => v.severity === 'critical')) {
      riskLevel = 'critical'
    } else if (p95Violations.some(v => v.severity === 'critical')) {
      riskLevel = 'critical'
    } else if (p99Violations.some(v => v.severity === 'warning')) {
      riskLevel = 'high'
    } else if (p95Violations.some(v => v.severity === 'warning')) {
      riskLevel = 'medium'
    }

    // Check if inside SLO
    const insideP95SLO = metrics.currentP95 <= metrics.targetP95
    const insideP99SLO = metrics.currentP99 <= metrics.targetP99
    const insideSLO = insideP95SLO && insideP99SLO

    // Calculate error budget for latency (using P95 target)
    // For latency, we consider violations as "errors" in the budget
    const errorBudget = sloai.calculateErrorBudget(0.95, 720) // 95% target over 30 days
    errorBudget.consumed = p95Violations.length + p99Violations.length
    errorBudget.remaining = Math.max(0, errorBudget.total - errorBudget.consumed)
    errorBudget.burnRate = burnRate
    errorBudget.timeToExhaustion = sloai.calculateTimeToExhaustion(
      errorBudget.remaining,
      burnRate
    )
    errorBudget.riskLevel = sloai.assessRiskLevel(errorBudget, errorBudget.timeToExhaustion)

    // Generate customer impact
    const customerImpact = sloai.generateCustomerImpact(
      riskLevel,
      'latency',
      metrics.currentP95,
      metrics.targetP95
    )

    // Generate recommendations
    const recommendations = sloai.generateRecommendations(riskLevel, 'latency')

    return NextResponse.json({
      agent: 'SLOAI',
      timestamp: new Date().toISOString(),
      metric: metricName,
      status: {
        riskLevel,
        insideSLO,
        insideP95SLO,
        insideP99SLO,
        violations: {
          p95: p95Violations.length,
          p99: p99Violations.length,
          total: metrics.violations.length
        }
      },
      metrics: {
        current: {
          p50: Math.round(metrics.p50),
          p95: Math.round(metrics.currentP95),
          p99: Math.round(metrics.currentP99)
        },
        targets: {
          p95: metrics.targetP95,
          p99: metrics.targetP99
        },
        deviation: {
          p95: Math.round((metrics.currentP95 / metrics.targetP95 - 1) * 100),
          p99: Math.round((metrics.currentP99 / metrics.targetP99 - 1) * 100)
        }
      },
      errorBudget: {
        total: Math.round(errorBudget.total * 100) / 100,
        consumed: errorBudget.consumed,
        remaining: Math.round(errorBudget.remaining * 100) / 100,
        burnRate: Math.round(burnRate * 100) / 100,
        timeToExhaustionHours: errorBudget.timeToExhaustion
          ? Math.round(errorBudget.timeToExhaustion * 100) / 100
          : null,
        riskLevel: errorBudget.riskLevel
      },
      customerImpact,
      humanAttentionNeeded: riskLevel === 'critical' || riskLevel === 'high',
      recommendations,
      interpretation: {
        summary: insideSLO
          ? `âœ… Latency SLO met. P95: ${Math.round(metrics.currentP95)}ms (target: ${metrics.targetP95}ms), P99: ${Math.round(metrics.currentP99)}ms (target: ${metrics.targetP99}ms)`
          : `âš ï¸ Latency SLO violated. P95: ${Math.round(metrics.currentP95)}ms (target: ${metrics.targetP95}ms), P99: ${Math.round(metrics.currentP99)}ms (target: ${metrics.targetP99}ms)`,
        implications: customerImpact,
        burnRateAnalysis: burnRate > 0
          ? `Current burn rate: ${Math.round(burnRate * 100) / 100} violations/hour. ${errorBudget.timeToExhaustion ? `Budget will exhaust in ${Math.round(errorBudget.timeToExhaustion)} hours if current rate continues.` : 'Budget is stable.'}`
          : 'No active violations. Burn rate: 0 violations/hour.'
      }
    })
  } catch (error) {
    console.error('SLOAI latency assessment error:', error)
    return NextResponse.json(
      {
        agent: 'SLOAI',
        error: 'Failed to assess latency SLO',
        message: error instanceof Error ? error.message : 'Unknown error',
        timestamp: new Date().toISOString()
      },
      { status: 500 }
    )
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { createClient } from '@supabase/supabase-js'
import { sloai } from '@/lib/observability/sloai'

/**
 * GET /ops/slo/status
 * 
 * SLOAI Agent - Overall SLO Status Assessment
 * 
 * Evaluates whether Bookiji is inside or outside error budgets,
 * provides risk assessment, and explains customer impact.
 */
export async function GET(_request: NextRequest) {
  try {
    // Create Supabase client inside handler with safety checks
    const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL
    const supabaseKey = process.env.SUPABASE_SERVICE_ROLE_KEY

    if (!supabaseUrl || !supabaseKey) {
      return NextResponse.json(
        {
          agent: 'SLOAI',
          error: 'Supabase not configured',
          status: 503,
          timestamp: new Date().toISOString()
        },
        { status: 503 }
      )
    }

    const supabase = createClient(supabaseUrl, supabaseKey)

    // Get overall status assessment with timeout
    const assessment = await Promise.race([
      sloai.assessOverallStatus(),
      new Promise<Awaited<ReturnType<typeof sloai.assessOverallStatus>>>((_, reject) =>
        setTimeout(() => reject(new Error('SLO assessment timeout')), 30000)
      )
    ]).catch((error) => {
      console.error('SLOAI assessment timeout or error:', error)
      throw error
    })

    // Get all SLO configs for context with timeout
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    let sloConfigs: any = null
    try {
      const configResult = await Promise.race([
        supabase
          .from('slo_config')
          .select('*')
          .order('metric_name'),
        new Promise<{ data: null; error: { message: string } }>((_, reject) =>
          setTimeout(() => reject(new Error('Database query timeout')), 30000)
        )
      ])
      // eslint-disable-next-line @typescript-eslint/no-explicit-any
      sloConfigs = (configResult as any).data
    } catch (error) {
      console.error('SLOAI config query timeout or error:', error)
      sloConfigs = null
    }

    // Get violation summary with timeout
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    let violations: any = null
    try {
      const violationsResult = await Promise.race([
        supabase
          .from('slo_violations')
          .select('*')
          .is('resolved_at', null)
          .order('last_violation_at', { ascending: false }),
        new Promise<{ data: null; error: { message: string } }>((_, reject) =>
          setTimeout(() => reject(new Error('Database query timeout')), 30000)
        )
      ])
      // eslint-disable-next-line @typescript-eslint/no-explicit-any
      violations = (violationsResult as any).data
    } catch (error) {
      console.error('SLOAI violations query timeout or error:', error)
      violations = null
    }

    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    const criticalCount = (violations || []).filter((v: any) => v.severity === 'critical').length
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    const warningCount = (violations || []).filter((v: any) => v.severity === 'warning').length

    // Calculate compliance rate
    const totalConfigs = sloConfigs?.length || 0
    const compliantConfigs = totalConfigs - criticalCount - (warningCount > 0 ? 1 : 0)
    const complianceRate = totalConfigs > 0 ? (compliantConfigs / totalConfigs) * 100 : 100

    return NextResponse.json({
      agent: 'SLOAI',
      timestamp: new Date().toISOString(),
      status: {
        riskLevel: assessment.riskLevel,
        insideBudget: assessment.insideBudget,
        complianceRate: Math.round(complianceRate * 100) / 100,
        criticalViolations: criticalCount,
        warningViolations: warningCount,
        totalViolations: (violations || []).length
      },
      errorBudget: {
        total: Math.round(assessment.errorBudget.total * 100) / 100,
        consumed: Math.round(assessment.errorBudget.consumed * 100) / 100,
        remaining: Math.round(assessment.errorBudget.remaining * 100) / 100,
        burnRate: Math.round(assessment.errorBudget.burnRate * 100) / 100,
        timeToExhaustionHours: assessment.errorBudget.timeToExhaustion
          ? Math.round(assessment.errorBudget.timeToExhaustion * 100) / 100
          : null,
        riskLevel: assessment.errorBudget.riskLevel
      },
      customerImpact: assessment.customerImpact,
      humanAttentionNeeded: assessment.humanAttentionNeeded,
      recommendations: assessment.recommendations,
      // eslint-disable-next-line @typescript-eslint/no-explicit-any
      sloConfigs: sloConfigs?.map((c: any) => ({
        metricName: c.metric_name,
        targetP95: c.target_p95_ms,
        targetP99: c.target_p99_ms,
        targetErrorRate: c.target_error_rate
      })) || [],
      interpretation: {
        summary: assessment.insideBudget
          ? `âœ… Bookiji is within error budgets. Risk level: ${assessment.riskLevel}.`
          : `âš ï¸ Bookiji is outside error budgets. Risk level: ${assessment.riskLevel}.`,
        implications: assessment.customerImpact,
        actionRequired: assessment.humanAttentionNeeded
          ? 'Yes - Immediate attention recommended'
          : 'No - Continue monitoring'
      }
    })
  } catch (error) {
    console.error('SLOAI status assessment error:', error)
    return NextResponse.json(
      {
        agent: 'SLOAI',
        error: 'Failed to assess SLO status',
        message: error instanceof Error ? error.message : 'Unknown error',
        timestamp: new Date().toISOString()
      },
      { status: 500 }
    )
  }
}

import { NextResponse } from 'next/server'
import { createServerClient } from '@supabase/ssr'
import { cookies } from 'next/headers'
import { getSupabaseConfig } from '@/config/supabase'

export async function GET() {
  try {
    const cookieStore = await cookies()
    const config = getSupabaseConfig()
    const supabase = createServerClient(
      config.url,
      config.publishableKey,
      {
        cookies: {
          getAll() {
            return cookieStore.getAll()
          },
          setAll(cookiesToSet) {
            try {
              cookiesToSet.forEach(({ name, value, options }) => {
                cookieStore.set(name, value, options)
              })
            } catch (_error) {
              // The `setAll` method was called from a Server Component or Route Handler.
              // This can be ignored if you have middleware refreshing user sessions.
            }
          }
        }
      }
    )

    const { data: { user }, error: authError } = await supabase.auth.getUser()
    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    // Check admin via user_role_summary view when available
    const { data: roleSummary } = await supabase
      .from('user_role_summary')
      .select('is_admin')
      .eq('user_id', user.id)
      .maybeSingle()

    if (!roleSummary?.is_admin) {
      return NextResponse.json({ error: 'Forbidden' }, { status: 403 })
    }

    const { data, error } = await supabase
      .from('processed_webhook_events')
      .select('*')
      .order('created_at', { ascending: false })
      .limit(50)

    if (error) {
      if (process.env.NODE_ENV === 'development') {
        console.error('Failed to fetch processed_webhook_events:', error)
      }
      return NextResponse.json({ error: 'Failed to fetch events' }, { status: 500 })
    }

    return NextResponse.json({ events: data ?? [] })
  } catch (error) {
    if (process.env.NODE_ENV === 'development') {
      console.error('processed-events GET error:', error)
    }
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 })
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { createClient } from '@supabase/supabase-js';

import { supabaseAdmin as supabase } from '@/lib/supabaseProxies';

export async function GET(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url);
    const providerId = searchParams.get('provider_id');
    const timeRange = searchParams.get('time_range') || '30d';

    if (!providerId) {
      return NextResponse.json(
        { error: 'Provider ID is required' },
        { status: 400 }
      );
    }

    // Calculate date range
    const startDate = new Date();
    switch (timeRange) {
      case '7d':
        startDate.setDate(startDate.getDate() - 7);
        break;
      case '30d':
        startDate.setDate(startDate.getDate() - 30);
        break;
      case '90d':
        startDate.setDate(startDate.getDate() - 90);
        break;
      case '1y':
        startDate.setFullYear(startDate.getFullYear() - 1);
        break;
      default:
        startDate.setDate(startDate.getDate() - 30);
    }

    // Get basic analytics
    const { data: analytics, error: analyticsError } = await supabase
      .from('reviews')
      .select('rating, overall_quality, would_recommend')
      .eq('provider_id', providerId)
      .eq('status', 'published')
      .gte('created_at', startDate.toISOString());

    if (analyticsError) {
      console.error('Analytics fetch error:', analyticsError);
      return NextResponse.json(
        { error: 'Failed to fetch analytics' },
        { status: 500 }
      );
    }

    // Calculate basic metrics
    const totalReviews = analytics?.length || 0;
    const totalRating = analytics?.reduce((sum, review) => {
      return sum + (review.overall_quality || review.rating || 0);
    }, 0) || 0;
    const averageRating = totalReviews > 0 ? totalRating / totalReviews : 0;

    // Get rating distribution
    const { data: distribution, error: distributionError } = await supabase
      .from('reviews')
      .select('rating, overall_quality')
      .eq('provider_id', providerId)
      .eq('status', 'published')
      .gte('created_at', startDate.toISOString());

    if (distributionError) {
      console.error('Distribution fetch error:', distributionError);
      return NextResponse.json(
        { error: 'Failed to fetch distribution' },
        { status: 500 }
      );
    }

    // Process distribution data
    const ratingCounts = new Map<number, number>();
    distribution?.forEach(review => {
      const rating = Math.round(review.overall_quality || review.rating || 0);
      ratingCounts.set(rating, (ratingCounts.get(rating) || 0) + 1);
    });

    const processedDistribution = Array.from({ length: 5 }, (_, i) => ({
      rating: i + 1,
      count: ratingCounts.get(i + 1) || 0,
      percentage: totalReviews > 0 ? ((ratingCounts.get(i + 1) || 0) / totalReviews) * 100 : 0
    }));

    // Get trends data
    const { data: trends, error: trendsError } = await supabase
      .from('reviews')
      .select('created_at, overall_quality, rating')
      .eq('provider_id', providerId)
      .eq('status', 'published')
      .gte('created_at', startDate.toISOString())
      .order('created_at', { ascending: true });

    if (trendsError) {
      console.error('Trends fetch error:', trendsError);
      return NextResponse.json(
        { error: 'Failed to fetch trends' },
        { status: 500 }
      );
    }

    // Process trends data
    const trendsMap = new Map<string, { count: number; totalRating: number }>();

    trends?.forEach(review => {
      const date = new Date(review.created_at).toISOString().split('T')[0];
      const rating = review.overall_quality || review.rating;

      if (trendsMap.has(date)) {
        const existing = trendsMap.get(date)!;
        existing.count++;
        existing.totalRating += rating;
      } else {
        trendsMap.set(date, { count: 1, totalRating: rating });
      }
    });

    const processedTrends = Array.from(trendsMap.entries()).map(([date, data]) => ({
      date,
      count: data.count,
      avg_rating: data.totalRating / data.count
    }));

    // Get recent reviews for additional insights
    const { data: recentReviews, error: recentError } = await supabase
      .from('reviews')
      .select('rating, overall_quality, would_recommend, created_at')
      .eq('provider_id', providerId)
      .eq('status', 'published')
      .gte('created_at', startDate.toISOString())
      .order('created_at', { ascending: false })
      .limit(100);

    if (recentError) {
      console.error('Recent reviews fetch error:', recentError);
      return NextResponse.json(
        { error: 'Failed to fetch recent reviews' },
        { status: 500 }
      );
    }

    // Calculate additional metrics
    const positiveReviews = recentReviews?.filter(r => (r.overall_quality || r.rating) >= 4.0).length || 0;
    const negativeReviews = recentReviews?.filter(r => (r.overall_quality || r.rating) <= 2.0).length || 0;
    const neutralReviews = recentReviews?.filter(r => {
      const rating = r.overall_quality || r.rating;
      return rating > 2.0 && rating < 4.0;
    }).length || 0;

    const recommendationCount = recentReviews?.filter(r => r.would_recommend === true).length || 0;
    const recommendationPercentage = totalReviews > 0 ? (recommendationCount / totalReviews) * 100 : 0;

    // Enhance analytics with calculated values
    const enhancedAnalytics = {
      total_reviews: totalReviews,
      average_rating: Math.round(averageRating * 10) / 10,
      positive_reviews: positiveReviews,
      negative_reviews: negativeReviews,
      neutral_reviews: neutralReviews,
      recommendation_count: recommendationCount,
      recommendation_percentage: Math.round(recommendationPercentage * 10) / 10
    };

    return NextResponse.json({
      analytics: enhancedAnalytics,
      distribution: processedDistribution,
      trends: processedTrends,
      timeRange,
      generatedAt: new Date().toISOString()
    });

  } catch (error) {
    console.error('Provider analytics error:', error);
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { createServerClient } from '@supabase/ssr'
import { cookies } from 'next/headers'
import { getSupabaseConfig } from '@/config/supabase'

export async function POST(request: NextRequest) {
  try {
    const cookieStore = await cookies()
    const config = getSupabaseConfig()
    const supabase = createServerClient(
      config.url,
      config.publishableKey,
      {
        cookies: {
          getAll() {
            return cookieStore.getAll()
          },
          setAll(cookiesToSet) {
            try {
              cookiesToSet.forEach(({ name, value, options }) => {
                cookieStore.set(name, value, options)
              })
            } catch (_error) {
              // The `setAll` method was called from a Server Component or Route Handler.
              // This can be ignored if you have middleware refreshing user sessions.
            }
          }
        }
      }
    )

    const { data: { user }, error: authError } = await supabase.auth.getUser()
    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    const body = await request.json()
    const { endpoint, keys } = body

    if (!endpoint || !keys || !keys.p256dh || !keys.auth) {
      return NextResponse.json(
        { error: 'Missing required subscription data' },
        { status: 400 }
      )
    }

    const userAgent = request.headers.get('user-agent') || null

    const { data, error } = await supabase
      .from('push_subscriptions')
      .upsert({
        user_id: user.id,
        endpoint,
        p256dh: keys.p256dh,
        auth: keys.auth,
        user_agent: userAgent,
        updated_at: new Date().toISOString()
      }, {
        onConflict: 'endpoint'
      })
      .select()
      .single()

    if (error) {
      console.error('Error saving push subscription:', error)
      return NextResponse.json(
        { error: 'Failed to save subscription' },
        { status: 500 }
      )
    }

    return NextResponse.json({ success: true, subscription: data })
  } catch (error) {
    console.error('Error in push subscription:', error)
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    )
  }
}

