import { NextRequest, NextResponse } from 'next/server';
import { getOrchestrator } from '@/lib/simcity/orchestrator';
import { getSimEngine } from '@/lib/simcity/engine';

export async function GET(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url);
    const _runId = searchParams.get('runId');

    const orchestrator = getOrchestrator();
    const engine = getSimEngine();

    if (!orchestrator.isRunning()) {
      return NextResponse.json({ 
        success: false,
        error: 'Simulation is not running' 
      }, { status: 400 });
    }

    const runInfo = orchestrator.getRunInfo();
    const metrics = orchestrator.getMetrics();
    const violations = orchestrator.getViolations();
    const uptime = orchestrator.getUptime();
    const state = orchestrator.getState();
    const engineSnapshot = engine.getSnapshot();

    // Check invariants if not already done
    if (violations.length === 0) {
      await orchestrator.checkInvariants();
    }

    const summary = {
      runInfo,
      scenario: runInfo.scenario,
      metrics,
      violations,
      uptime,
      state: {
        tick: state.tick,
        simulatedTime: state.nowISO,
        liveAgents: state.liveAgents,
        policies: state.policies,
        runInfo,
        scenario: runInfo.scenario,
        liveEngine: engineSnapshot,
      },
      timestamp: new Date().toISOString()
    };

    return NextResponse.json({
      success: true,
      data: summary
    });
  } catch (error) {
    console.error('SimCity summary error:', error);
    return NextResponse.json({ 
      success: false,
      error: 'Failed to get simulation summary',
      details: error instanceof Error ? error.message : 'Unknown error'
    }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from "next/server";
import { createClient } from "@supabase/supabase-js";

import { supabaseAdmin as supabase } from '@/lib/supabaseProxies';

export async function GET(
  req: NextRequest,
  context: { params: Promise<Record<string, string>> }
) {
  const { id } = await context.params
  try {
    const { data, error } = await supabase
      .from("specialties")
      .select("id,name,slug,parent_id,path,is_active,created_at,updated_at")
      .eq("id", id)
      .single();

    if (error) {
      console.error("Specialty fetch error:", error);
      return NextResponse.json({ error: "Failed to fetch specialty" }, { status: 500 });
    }

    if (!data) {
      return NextResponse.json({ error: "Specialty not found" }, { status: 404 });
    }

    return NextResponse.json(data);
  } catch (error) {
    console.error("Specialty API error:", error);
    return NextResponse.json({ error: "Internal server error" }, { status: 500 });
  }
}

export async function PUT(
  req: NextRequest,
  context: { params: Promise<Record<string, string>> }
) {
  const { id } = await context.params
  try {
    const { name, parent_id, is_active } = await req.json();

    if (!name) {
      return NextResponse.json({ error: "Name is required" }, { status: 400 });
    }

    const { data, error } = await supabase
      .from("specialties")
      .update({ 
        name, 
        parent_id: parent_id || null, 
        is_active: is_active !== undefined ? is_active : true,
        updated_at: new Date().toISOString()
      })
      .eq("id", id)
      .select()
      .single();

    if (error) {
      console.error("Specialty update error:", error);
      return NextResponse.json({ error: "Failed to update specialty" }, { status: 500 });
    }

    return NextResponse.json(data);
  } catch (error) {
    console.error("Specialty update API error:", error);
    return NextResponse.json({ error: "Internal server error" }, { status: 500 });
  }
}

export async function DELETE(
  req: NextRequest,
  context: { params: Promise<Record<string, string>> }
) {
  const { id } = await context.params
  try {
    // Check if specialty has children
    const { data: children, error: childrenError } = await supabase
      .from("specialties")
      .select("id")
      .eq("parent_id", id);

    if (childrenError) {
      console.error("Children check error:", childrenError);
      return NextResponse.json({ error: "Failed to check specialty dependencies" }, { status: 500 });
    }

    if (children && children.length > 0) {
      return NextResponse.json({ 
        error: "Cannot delete specialty with child categories. Please delete or move child categories first." 
      }, { status: 400 });
    }

    // Check if specialty is used by vendors
    const { data: vendorUsage, error: vendorError } = await supabase
      .from("vendor_specialties")
      .select("id")
      .eq("specialty_id", id)
      .limit(1);

    if (vendorError) {
      console.error("Vendor usage check error:", vendorError);
      return NextResponse.json({ error: "Failed to check specialty usage" }, { status: 500 });
    }

    if (vendorUsage && vendorUsage.length > 0) {
      return NextResponse.json({ 
        error: "Cannot delete specialty that is currently used by vendors. Please reassign vendors first." 
      }, { status: 400 });
    }

    // Delete the specialty
    const { error } = await supabase
      .from("specialties")
      .delete()
      .eq("id", id);

    if (error) {
      console.error("Specialty deletion error:", error);
      return NextResponse.json({ error: "Failed to delete specialty" }, { status: 500 });
    }

    return NextResponse.json({ success: true, message: "Specialty deleted successfully" });
  } catch (error) {
    console.error("Specialty deletion API error:", error);
    return NextResponse.json({ error: "Internal server error" }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from "next/server";
import { createClient } from "@supabase/supabase-js";

import { supabaseAdmin as supabase } from '@/lib/supabaseProxies';

export async function GET(req: NextRequest) {
  const q = req.nextUrl.searchParams.get("q")?.trim();
  const parent = req.nextUrl.searchParams.get("parent");

  try {
    let query = supabase
      .from("specialties")
      .select("id,name,slug,parent_id,path,is_active")
      .eq("is_active", true)
      .order("name");

    if (parent === "root") {
      query = query.is("parent_id", null);
    } else if (parent && parent !== "root") {
      query = query.eq("parent_id", parent);
    }

    if (q) {
      // Search across names and aliases
      const { data: aliasResults } = await supabase
        .from("specialty_aliases")
        .select("specialty_id")
        .ilike("alias", `%${q}%`);

      const aliasIds = aliasResults?.map(r => r.specialty_id) || [];

      query = query.or(`name.ilike.%${q}%,id.in.(${aliasIds.join(",")})`);
    }

    const { data, error } = await query;

    if (error) {
      console.error("Specialties query error:", error);
      return NextResponse.json({ error: "Failed to fetch specialties" }, { status: 500 });
    }

    return NextResponse.json({ items: data || [] });
  } catch (error) {
    console.error("Specialties API error:", error);
    return NextResponse.json({ error: "Internal server error" }, { status: 500 });
  }
}

export async function POST(req: NextRequest) {
  try {
    const { name, parent_id } = await req.json();

    if (!name) {
      return NextResponse.json({ error: "Name is required" }, { status: 400 });
    }

    const { data, error } = await supabase
      .from("specialties")
