import { AnalyticsEnvelope } from "@/contracts/analytics/AnalyticsEnvelope";
import { AuditFrame, AuditTimeline, DiffExplanation, TimelineDiff } from "./types";
import {
  computeP95,
  deepFreeze,
  deriveDomain,
  extractLatencyCandidate,
  governanceFallback,
} from "./utils";

interface TimelineSummary {
  eventCounts: Record<string, number>;
  trustSafetySignals: Set<string>;
  latencies: number[];
  bookings: number;
  cancellations: number;
  byDomain: Map<string, AuditFrame>;
}

function summarizeTimeline(timeline: AuditTimeline): TimelineSummary {
  const eventCounts: Record<string, number> = {};
  const trustSafetySignals = new Set<string>();
  const latencies: number[] = [];
  let bookings = 0;
  let cancellations = 0;
  const byDomain = new Map<string, AuditFrame>();

  timeline.frames.forEach((frame) => {
    const type = frame.envelope.event.type;
    eventCounts[type] = (eventCounts[type] ?? 0) + 1;
    if (type === "booking.created") bookings += 1;
    if (type === "booking.cancelled") cancellations += 1;

    frame.trustSafety?.signals.forEach((signal) => trustSafetySignals.add(signal));

    const latency = extractLatencyCandidate(frame.envelope.metadata as Record<string, unknown>);
    if (typeof latency === "number") {
      latencies.push(latency);
    }

    if (!byDomain.has(frame.governance.domain)) {
      byDomain.set(frame.governance.domain, frame);
    }
  });

  return { eventCounts, trustSafetySignals, latencies, bookings, cancellations, byDomain };
}

function diffIds(left: readonly AuditFrame[], right: readonly AuditFrame[]): string[] {
  const rightIds = new Set(right.map((frame) => frame.id));
  return left.filter((frame) => !rightIds.has(frame.id)).map((frame) => frame.id);
}

function createExplanation(input: {
  description: string;
  signals: string[];
  frame?: AuditFrame;
  fallbackDomain: string;
}): DiffExplanation {
  const governance =
    input.frame?.governance ??
    governanceFallback(input.fallbackDomain);

  return deepFreeze({
    description: input.description,
    signals: input.signals,
    governance,
    evolution: input.frame?.evolution,
    syntheticContext: input.frame?.syntheticContext,
  });
}

function normalizeDeltas(base: Record<string, number>, variant: Record<string, number>): Record<string, number> {
  const keys = Array.from(new Set([...Object.keys(base), ...Object.keys(variant)])).sort();
  const result: Record<string, number> = {};
  keys.forEach((key) => {
    result[key] = (variant[key] ?? 0) - (base[key] ?? 0);
  });
  return result;
}

function selectFrameForDomain(timeline: AuditTimeline, domain: string): AuditFrame | undefined {
  return timeline.frames.find((frame) => frame.governance.domain === domain);
}

export function diffTimelines(baseline: AuditTimeline, comparison: AuditTimeline): TimelineDiff {
  const baselineSummary = summarizeTimeline(baseline);
  const comparisonSummary = summarizeTimeline(comparison);

  const missingInComparison = diffIds(baseline.frames, comparison.frames);
  const missingInBaseline = diffIds(comparison.frames, baseline.frames);
  const metricDivergence = normalizeDeltas(baselineSummary.eventCounts, comparisonSummary.eventCounts);

  const trustSafetyChanges: string[] = [];
  comparisonSummary.trustSafetySignals.forEach((signal) => {
    if (!baselineSummary.trustSafetySignals.has(signal)) {
      trustSafetyChanges.push(`Added trust-safety signal "${signal}"`);
    }
  });
  baselineSummary.trustSafetySignals.forEach((signal) => {
    if (!comparisonSummary.trustSafetySignals.has(signal)) {
      trustSafetyChanges.push(`Missing trust-safety signal "${signal}"`);
    }
  });

  const sloImpacts = {
    latencyP95Delta: computeP95(comparisonSummary.latencies) - computeP95(baselineSummary.latencies),
    bookingDelta: comparisonSummary.bookings - baselineSummary.bookings,
    cancellationDelta: comparisonSummary.cancellations - baselineSummary.cancellations,
  };

  const explanations: DiffExplanation[] = [];

  Object.entries(metricDivergence)
    .filter(([, delta]) => delta !== 0)
    .forEach(([eventType, delta]) => {
      const domain = deriveDomain(eventType);
      const frame = selectFrameForDomain(comparison, domain) ?? selectFrameForDomain(baseline, domain);
      explanations.push(
        createExplanation({
          description: `${eventType} diverged by ${delta} events`,
          signals: [eventType],
          frame,
          fallbackDomain: domain,
        })
      );
    });

  trustSafetyChanges.forEach((change) => {
    const frame = comparison.frames.find((candidate) => candidate.trustSafety?.signals.length);
    const domain = frame?.governance.domain ?? "unknown";
    explanations.push(
      createExplanation({
        description: change,
        signals: frame?.trustSafety?.signals ? [...frame.trustSafety.signals] : [],
        frame,
        fallbackDomain: domain,
      })
    );
  });

  missingInComparison.forEach((id) => {
    const frame = baseline.frames.find((candidate) => candidate.id === id);
    const domain = frame?.governance.domain ?? "unknown";
    explanations.push(
      createExplanation({
        description: `Event ${id} missing in comparison timeline`,
        signals: [frame?.envelope.event.type ?? "unknown"],
        frame,
        fallbackDomain: domain,
      })
    );
  });

  missingInBaseline.forEach((id) => {
    const frame = comparison.frames.find((candidate) => candidate.id === id);
    const domain = frame?.governance.domain ?? "unknown";
    explanations.push(
      createExplanation({
        description: `Event ${id} missing in baseline timeline`,
        signals: [frame?.envelope.event.type ?? "unknown"],
        frame,
        fallbackDomain: domain,
      })
    );
  });

  const sortedExplanations = explanations.sort((a, b) => a.description.localeCompare(b.description));

  return deepFreeze({
    baselineId: baseline.id,
    comparisonId: comparison.id,
    missingInComparison,
    missingInBaseline,
    metricDivergence,
    trustSafetyChanges,
    sloImpacts,
    explanations: sortedExplanations,
  });
}

"use client";

import { useState } from "react";
import { useRouter } from "next/navigation";
import { motion } from "framer-motion";
import { supabaseBrowserClient } from "@/lib/supabaseClient";
import { theme, combineClasses } from "@/config/theme";

export default function AuthEntry({ mode = "signup" }: { mode?: "signup" | "login" }) {
  const router = useRouter();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [error, setError] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isSignUp, setIsSignUp] = useState(mode === "signup");

  const handleEmailAuth = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);
    setIsLoading(true);

    const supabase = supabaseBrowserClient()
    if (!supabase) {
      setError('Supabase client not available');
      setIsLoading(false);
      return;
    }

    try {
      if (isSignUp) {
        if (password !== confirmPassword) {
          throw new Error("Passwords do not match");
        }
        const { error } = await supabase.auth.signUp({
          email,
          password,
          options: {
            emailRedirectTo: `${window.location.origin}/auth/callback`,
          },
        });
        if (error) throw error;

        // Redirect immediately - useAuthReady will handle session readiness
        router.push("/choose-role");
      } else {
        const { _data, error } = await supabase.auth.signInWithPassword({
          email,
          password,
        });
        if (error) throw error;

        // Check if user is admin and redirect accordingly
        try {
          const adminCheck = await fetch('/api/auth/check-admin', {
            method: 'GET',
            credentials: 'include'
          })

          if (adminCheck.ok) {
            const { isAdmin } = await adminCheck.json()
            if (isAdmin) {
              // Always redirect admins to admin cockpit
              router.push('/admin')
              return
            }
          }
        } catch (adminError) {
          console.warn('Failed to check admin status:', adminError)
          // Continue with normal redirect
        }

        router.push("/customer/dashboard");
      }
    } catch (err: unknown) {
      setError(err instanceof Error ? err.message : `Failed to ${isSignUp ? "sign up" : "sign in"}`);
    } finally {
      setIsLoading(false);
    }
  };

  const inputClasses = combineClasses(
    theme.components.input.base,
    theme.components.input.focus,
    theme.components.input.rounded,
    theme.components.input.placeholder
  );
  const buttonClasses = combineClasses(
    "w-full px-6 py-4 text-lg font-medium",
    theme.components.button.primary.base,
    theme.components.button.primary.hover,
    theme.components.button.primary.shadow,
    theme.components.button.primary.rounded,
    isLoading ? "opacity-70 cursor-not-allowed" : ""
  );
  const cardClasses = combineClasses(
    theme.components.card.background,
    theme.components.card.border,
    theme.components.card.shadow,
    theme.components.card.rounded,
    "p-8"
  );

  return (
    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="w-full">
      <motion.div
        initial={{ y: 20, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.4 }}
        className={cardClasses}
      >
        {error && (
          <div className="mb-6 p-4 bg-red-50 border border-red-200 text-red-600 rounded-md text-sm">
            {error}
          </div>
        )}
        <form className="space-y-6" onSubmit={handleEmailAuth}>
          <div>
            <label htmlFor="email" className="block text-sm font-medium text-gray-700">
              Email address
            </label>
            <input
              id="email"
              name="email"
              type="email"
              autoComplete="email"
              required
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className={inputClasses}
            />
          </div>
          <div>
            <label htmlFor="password" className="block text-sm font-medium text-gray-700">
              Password
            </label>
            <input
              id="password"
              name="password"
              type="password"
              autoComplete={isSignUp ? "new-password" : "current-password"}
              required
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className={inputClasses}
            />
          </div>
          {isSignUp && (
            <div>
              <label htmlFor="confirmPassword" className="block text-sm font-medium text-gray-700">
                Confirm Password
              </label>
              <input
                id="confirmPassword"
                name="confirmPassword"
                type="password"
                autoComplete="new-password"
                required
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                className={inputClasses}
              />
            </div>
          )}
          <div>
            <button
              type="submit"
              disabled={isLoading}
              className={buttonClasses}
            >
              {isLoading ? (
                <div className="flex items-center justify-center space-x-2">
                  <div className="loading-dots flex space-x-1">
                    <div className="w-2 h-2 bg-white rounded-full animate-pulse"></div>
                    <div className="w-2 h-2 bg-white rounded-full animate-pulse" style={{ animationDelay: '0.1s' }}></div>
                    <div className="w-2 h-2 bg-white rounded-full animate-pulse" style={{ animationDelay: '0.2s' }}></div>
                  </div>
                  <span>{isSignUp ? "Creating account..." : "Signing in..."}</span>
                </div>
              ) : isSignUp ? "Create Account" : "Sign In"}
            </button>
          </div>
        </form>
        <div className="text-center pt-6">
          <p className="text-sm text-gray-600">
            {isSignUp ? "Already have an account?" : "Don't have an account?"}{' '}
            <button
              type="button"
              onClick={() => setIsSignUp(!isSignUp)}
              className="text-blue-600 hover:underline font-medium"
            >
              {isSignUp ? "Sign in" : "Sign up"}
            </button>
          </p>
        </div>
      </motion.div>
    </motion.div>
  );
}

'use client';

import { useState, useEffect, useRef } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
// import { Slider } from '@/components/ui/slider';
import { MapPin, TrendingUp, Users, DollarSign, Calendar, RefreshCw } from 'lucide-react';

interface HeatmapDataPoint {
  postal_code: string;
  latitude: number;
  longitude: number;
  booking_count: number;
  revenue: number;
  avg_rating: number;
  unique_providers: number;
  last_booking: string;
}

interface HeatmapProps {
  userId?: string;
  className?: string;
  height?: number;
  showControls?: boolean;
  defaultTimeRange?: '7d' | '30d' | '90d' | '1y';
}

export default function HeatmapVisualization({ 
  _userId, 
  className = '', 
  height = 400,
  showControls = true,
  defaultTimeRange = '30d'
}: HeatmapProps) {
  const [data, setData] = useState<HeatmapDataPoint[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [timeRange, setTimeRange] = useState(defaultTimeRange);
  const [serviceType, setServiceType] = useState<string>('');
  const [minBookings, setMinBookings] = useState([1]);
  const [selectedPoint, setSelectedPoint] = useState<HeatmapDataPoint | null>(null);
  const mapRef = useRef<HTMLDivElement>(null);

  const fetchHeatmapData = async () => {
    setLoading(true);
    setError(null);

    try {
      const params = new URLSearchParams({
        timeRange,
        minBookings: minBookings[0].toString(),
        ...(serviceType && { serviceType })
      });

      const response = await fetch(`/api/analytics/heatmap?${params}`);
      const result = await response.json();

      if (result.success) {
        setData(result.data || []);
      } else {
        setError(result.error || 'Failed to fetch heatmap data');
      }
    } catch (err) {
      console.error('Heatmap fetch error:', err);
      setError('Error loading heatmap data');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchHeatmapData();
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [timeRange, serviceType, minBookings]);

  // Calculate color intensity based on booking count
  const getHeatColor = (bookingCount: number, maxBookings: number): string => {
    const intensity = bookingCount / maxBookings;
    const red = Math.min(255, Math.floor(255 * intensity));
    const blue = Math.max(0, Math.floor(255 * (1 - intensity)));
    return `rgb(${red}, 100, ${blue})`;
  };

  // Get statistics
  const stats = data.length > 0 ? {
    totalBookings: data.reduce((sum, point) => sum + point.booking_count, 0),
    totalRevenue: data.reduce((sum, point) => sum + point.revenue, 0),
    avgRating: data.reduce((sum, point) => sum + point.avg_rating, 0) / data.length,
    uniqueAreas: data.length,
    maxBookings: Math.max(...data.map(point => point.booking_count))
  } : {
    totalBookings: 0,
    totalRevenue: 0,
    avgRating: 0,
    uniqueAreas: 0,
    maxBookings: 0
  };

  const serviceTypes = [
    { value: '', label: 'All Services' },
    { value: 'haircut', label: 'Haircut' },
    { value: 'massage', label: 'Massage' },
    { value: 'cleaning', label: 'Cleaning' },
    { value: 'tutoring', label: 'Tutoring' },
    { value: 'photography', label: 'Photography' },
    { value: 'fitness', label: 'Fitness Training' },
    { value: 'beauty', label: 'Beauty Services' },
    { value: 'handyman', label: 'Handyman' },
    { value: 'catering', label: 'Catering' },
    { value: 'other', label: 'Other' }
  ];

  return (
    <div className={`space-y-4 ${className}`}>
      {/* Controls */}
      {showControls && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <MapPin className="h-5 w-5" />
              <span>Booking Heatmap Controls</span>
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              <div>
                <label className="block text-sm font-medium mb-2">Time Range</label>
                <Select value={timeRange} onValueChange={(value: string) => setTimeRange(value as typeof timeRange)}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="7d">Last 7 Days</SelectItem>
                    <SelectItem value="30d">Last 30 Days</SelectItem>
                    <SelectItem value="90d">Last 90 Days</SelectItem>
                    <SelectItem value="1y">Last Year</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <label className="block text-sm font-medium mb-2">Service Type</label>
                <Select value={serviceType} onValueChange={setServiceType}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {serviceTypes.map(type => (
                      <SelectItem key={type.value} value={type.value}>
                        {type.label}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div>
                <label className="block text-sm font-medium mb-2">
                  Min Bookings: {minBookings[0]}
                </label>
                <input
                  type="range"
                  value={minBookings[0]}
                  onChange={(e) => setMinBookings([parseInt(e.target.value)])}
                  max={20}
                  min={1}
                  step={1}
                  className="w-full mt-2"
                />
              </div>

              <div className="flex items-end">
                <Button onClick={fetchHeatmapData} disabled={loading} className="w-full">
                  <RefreshCw className={`h-4 w-4 mr-2 ${loading ? 'animate-spin' : ''}`} />
                  Refresh
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Statistics */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <Calendar className="h-4 w-4 text-blue-500" />
              <div>
                <p className="text-sm text-gray-600">Total Bookings</p>
                <p className="text-xl font-bold">{stats.totalBookings.toLocaleString()}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <DollarSign className="h-4 w-4 text-green-500" />
              <div>
                <p className="text-sm text-gray-600">Total Revenue</p>
                <p className="text-xl font-bold">${stats.totalRevenue.toLocaleString()}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <TrendingUp className="h-4 w-4 text-purple-500" />
              <div>
                <p className="text-sm text-gray-600">Avg Rating</p>
                <p className="text-xl font-bold">{stats.avgRating.toFixed(1)}â­</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <MapPin className="h-4 w-4 text-orange-500" />
              <div>
                <p className="text-sm text-gray-600">Areas Covered</p>
                <p className="text-xl font-bold">{stats.uniqueAreas}</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Main Heatmap Display */}
      <Card>
        <CardHeader>
          <CardTitle>Booking Density Heatmap</CardTitle>
        </CardHeader>
        <CardContent>
          {loading ? (
            <div className="flex items-center justify-center h-64">
              <div className="text-center">
                <RefreshCw className="h-8 w-8 animate-spin mx-auto mb-2" />
                <p>Loading heatmap data...</p>
              </div>
            </div>
          ) : error ? (
            <div className="flex items-center justify-center h-64">
              <div className="text-center text-red-500">
                <p>{error}</p>
                <Button onClick={fetchHeatmapData} className="mt-2">
                  Try Again
                </Button>
              </div>
            </div>
          ) : data.length === 0 ? (
            <div className="flex items-center justify-center h-64">
              <div className="text-center text-gray-500">
                <MapPin className="h-12 w-12 mx-auto mb-2 opacity-50" />
                <p>No booking data available for the selected criteria</p>
                <p className="text-sm">Try adjusting the filters above</p>
              </div>
            </div>
          ) : (
            <div className="space-y-4">
              {/* Simple heatmap representation using a grid */}
              <div 
                ref={mapRef}
                className="border rounded-lg p-4 bg-gray-50"
                style={{ height: `${height}px` }}
              >
                <div className="h-full overflow-auto">
                  <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-2">
                    {data.map((point, _index) => (
                      <div
                        key={point.postal_code}
                        className="border rounded p-3 cursor-pointer transition-all hover:shadow-lg"
                        style={{
                          backgroundColor: getHeatColor(point.booking_count, stats.maxBookings),
                          color: point.booking_count > stats.maxBookings * 0.5 ? 'white' : 'black'
                        }}
                        onClick={() => setSelectedPoint(point)}
                      >
                        <div className="text-sm font-bold">{point.postal_code}</div>
                        <div className="text-xs">
                          <div>{point.booking_count} bookings</div>
                          <div>${point.revenue.toFixed(0)}</div>
                          <div>{point.avg_rating.toFixed(1)}â­</div>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              </div>

              {/* Legend */}
              <div className="flex items-center space-x-4 text-sm">
                <span>Heat Intensity:</span>
                <div className="flex items-center space-x-1">
                  <div className="w-4 h-4 bg-blue-500" title="Low activity"></div>
