'use client'

import { useState, useRef, useCallback } from 'react'
import { Button } from '@/components/ui/button'
import { Card, CardContent } from '@/components/ui/card'
import { Badge } from '@/components/ui/badge'
import { Input } from '@/components/ui/input'
import { Label } from '@/components/ui/label'
import { Textarea } from '@/components/ui/textarea'
import { Image, Upload, X, Eye, Download, FileText, Sparkles } from 'lucide-react'
import { cn } from '@/lib/utils'

interface ImageAttachmentProps {
  images: File[];
  onImagesChangeAction: (images: File[]) => void;
  maxImages?: number;
  maxSizeMB?: number;
  acceptedTypes?: string[];
  className?: string;
}

export function ImageAttachment({
  images,
  onImagesChangeAction,
  maxImages = 5,
  maxSizeMB = 10,
  acceptedTypes = ['image/jpeg', 'image/png', 'image/webp'],
  className = ''
}: ImageAttachmentProps) {
  const [isDragOver, setIsDragOver] = useState(false)
  const [isProcessing, setIsProcessing] = useState(false)
  const [summary, setSummary] = useState('')
  const [error, setError] = useState<string | null>(null)

  const fileInputRef = useRef<HTMLInputElement>(null)
  const dropZoneRef = useRef<HTMLDivElement>(null)

  // Handle file selection
  const handleFileSelect = useCallback((files: FileList | null) => {
    if (!files) return

    const newImages: File[] = []
    const errors: string[] = []

    Array.from(files).forEach((file) => {
      // Validate file type
      if (!acceptedTypes.includes(file.type)) {
        errors.push(`${file.name} is not a supported image type`)
        return
      }

      // Validate file size
      if (file.size > maxSizeMB * 1024 * 1024) {
        errors.push(`${file.name} is too large (max ${maxSizeMB}MB)`)
        return
      }

      // Check if we've reached max files
      if (images.length + newImages.length >= maxImages) {
        errors.push(`Maximum ${maxImages} images allowed`)
        return
      }

      newImages.push(file)
    })

    // Show errors if any
    if (errors.length > 0) {
      setError(errors.join(', '))
      setTimeout(() => setError(null), 5000)
    }

    // Add new images
    if (newImages.length > 0) {
      const updatedImages = [...images, ...newImages]
      onImagesChangeAction(updatedImages)
    }
  }, [images, maxImages, maxSizeMB, acceptedTypes, onImagesChangeAction])

  // Handle drag and drop
  const handleDragOver = useCallback((e: React.DragEvent) => {
    e.preventDefault()
    setIsDragOver(true)
  }, [])

  const handleDragLeave = useCallback((e: React.DragEvent) => {
    e.preventDefault()
    setIsDragOver(false)
  }, [])

  const handleDrop = useCallback((e: React.DragEvent) => {
    e.preventDefault()
    setIsDragOver(false)

    const files = e.dataTransfer.files
    handleFileSelect(files)
  }, [handleFileSelect])

  // Remove image
  const removeImage = useCallback((id: string) => {
    const updatedImages = images.filter(img => img.name !== id) // Assuming name is unique for now
    onImagesChangeAction(updatedImages)
  }, [images, onImagesChangeAction])

  // Generate AI summary
  const generateSummary = useCallback(async () => {
    if (images.length === 0) return

    setIsProcessing(true)
    setError(null)

    try {
      // Create FormData with images
      const formData = new FormData()
      images.forEach((img, index) => {
        formData.append(`image_${index}`, img)
      })

      // Call AI summarization API
      const response = await fetch('/api/ai/summarize-images', {
        method: 'POST',
        body: formData
      })

      if (!response.ok) {
        throw new Error('Failed to generate summary')
      }

      const data = await response.json()
      const aiSummary = data.summary

      setSummary(aiSummary)
      // onSummaryChange?.(aiSummary) // This prop was removed from interface

      // Update images with summaries
      // This part of the logic needs to be adapted to the new ImageFile interface
      // For now, we'll just update the summary in the state
      const updatedImages = images.map((img, index) => ({
        ...img,
        summary: data.imageSummaries?.[index] || '',
        isProcessing: false
      }))

      // onImagesChange(updatedImages) // This prop was removed from interface
      onImagesChangeAction(updatedImages) // Use the new prop

    } catch (err) {
      console.error('Failed to generate summary:', err)
      setError('Failed to generate AI summary. Please try again.')
    } finally {
      setIsProcessing(false)
    }
  }, [images, onImagesChangeAction])

  // Format file size
  const formatFileSize = (bytes: number): string => {
    if (bytes === 0) return '0 Bytes'
    const k = 1024
    const sizes = ['Bytes', 'KB', 'MB', 'GB']
    const i = Math.floor(Math.log(bytes) / Math.log(k))
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i]
  }

  // Get file type icon
  const getFileTypeIcon = (type: string) => {
    // eslint-disable-next-line jsx-a11y/alt-text
    if (type.startsWith('image/')) return <Image className="h-4 w-4" />
    return <FileText className="h-4 w-4" />
  }

  return (
    <div className={cn("space-y-4", className)}>
      {/* File Input */}
      <div className="space-y-2">
        <Label htmlFor="image-upload">Attach Images</Label>
        <Input
          id="image-upload"
          ref={fileInputRef}
          type="file"
          multiple
          accept={acceptedTypes.join(',')}
          onChange={(e) => handleFileSelect(e.target.files)}
          disabled={images.length >= maxImages}
          className="hidden"
        />

        {/* Drop Zone */}
        <div
          ref={dropZoneRef}
          className={cn(
            "border-2 border-dashed rounded-lg p-6 text-center transition-colors cursor-pointer",
            isDragOver 
              ? "border-blue-500 bg-blue-50" 
              : "border-gray-300 hover:border-gray-400 hover:bg-gray-50",
            // disabled && "opacity-50 cursor-not-allowed" // This prop was removed from interface
          )}
          onDragOver={handleDragOver}
          onDragLeave={handleDragLeave}
          onDrop={handleDrop}
          onClick={() => fileInputRef.current?.click()}
        >
          <Upload className="h-8 w-8 mx-auto mb-2 text-gray-400" />
          <p className="text-sm text-gray-600">
            {isDragOver 
              ? "Drop images here" 
              : "Drag & drop images here or click to browse"
            }
          </p>
          <p className="text-xs text-gray-500 mt-1">
            Max {maxImages} files, {maxSizeMB}MB each. Supported: JPEG, PNG, WebP
          </p>
        </div>
      </div>

      {/* Error Display */}
      {error && (
        <Card className="border-red-200 bg-red-50">
          <CardContent className="p-3">
            <p className="text-sm text-red-700">{error}</p>
          </CardContent>
        </Card>
      )}

      {/* Image Grid */}
      {images.length > 0 && (
        <div className="space-y-4">
          <div className="flex justify-between items-center">
            <h4 className="font-medium">Attached Images ({images.length}/{maxImages})</h4>
            <Button
              onClick={generateSummary}
              disabled={isProcessing}
              size="sm"
              className="flex items-center gap-2"
            >
              <Sparkles className="h-4 w-4" />
              {isProcessing ? 'Processing...' : 'Generate AI Summary'}
            </Button>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {images.map((image) => (
              <Card key={image.name} className="overflow-hidden">
                <CardContent className="p-0">
                  {/* Image Preview */}
                  <div className="relative aspect-square bg-gray-100">
                    // eslint-disable-next-line @next/next/no-img-element
                    <img
                      src={URL.createObjectURL(image)}
                      alt={image.name}
                      className="w-full h-full object-cover"
                    />

                    {/* Remove Button */}
                    <Button
                      onClick={() => removeImage(image.name)}
                      variant="destructive"
                      size="sm"
                      className="absolute top-2 right-2 h-6 w-6 p-0"
                      // disabled={disabled} // This prop was removed from interface
                    >
                      <X className="h-3 w-3" />
                    </Button>

                    {/* Processing Indicator */}
                    {/* This part of the logic needs to be adapted to the new ImageFile interface */}
                    {/* For now, we'll just show a placeholder */}
                    {/* {image.isProcessing && (
                      <div className="absolute inset-0 bg-black/50 flex items-center justify-center">
                        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-white" />
                      </div>
                    )} */}
                  </div>

                  {/* Image Info */}
                  <div className="p-3 space-y-2">
                    <div className="flex items-center gap-2">
                      {getFileTypeIcon(image.type)}
                      <span className="text-sm font-medium truncate" title={image.name}>
                        {image.name}
                      </span>
                    </div>

                    <div className="flex items-center justify-between text-xs text-gray-500">
                      <span>{formatFileSize(image.size)}</span>
                      <span>{new Date().toLocaleDateString()}</span> {/* Placeholder for uploadedAt */}
                    </div>

                    {/* Individual Image Summary */}
                    {/* This part of the logic needs to be adapted to the new ImageFile interface */}
                    {/* For now, we'll just show a placeholder */}
                    {/* {image.summary && (
                      <div className="text-xs text-gray-600 bg-gray-50 p-2 rounded">
                        <strong>AI Summary:</strong> {image.summary}
                      </div>
                    )} */}

                    {/* Actions */}
                    <div className="flex gap-1">
                      <Button
                        variant="outline"
                        size="sm"
                        className="flex-1 h-8 text-xs"
                        onClick={() => window.open(URL.createObjectURL(image), '_blank')}
                        // disabled={disabled} // This prop was removed from interface
                      >
                        <Eye className="h-3 w-3 mr-1" />
                        Preview
                      </Button>

                      <Button
                        variant="outline"
                        size="sm"
                        className="flex-1 h-8 text-xs"
                        onClick={() => {
                          const link = document.createElement('a')
                          link.href = URL.createObjectURL(image)
                          link.download = image.name
                          link.click()
                        }}
                        // disabled={disabled} // This prop was removed from interface
                      >
                        <Download className="h-3 w-3 mr-1" />
                        Download
                      </Button>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        </div>
      )}

      {/* AI Summary */}
      {summary && (
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-2 mb-3">
              <Sparkles className="h-5 w-5 text-blue-500" />
              <h4 className="font-medium">AI-Generated Summary</h4>
            </div>

            <Textarea
              value={summary}
              onChange={(e: React.ChangeEvent<HTMLTextAreaElement>) => {
                setSummary(e.target.value)
                // onSummaryChange?.(e.target.value) // This prop was removed from interface
              }}
              placeholder="AI summary will appear here..."
              className="min-h-[100px]"
              // disabled={disabled} // This prop was removed from interface
            />

            <p className="text-xs text-gray-500 mt-2">
              This summary was generated by AI based on your attached images. 
              You can edit it to better describe your service request.
            </p>
          </CardContent>
        </Card>
      )}

      {/* Instructions */}
      {images.length === 0 && (
        <Card className="border-dashed">
          <CardContent className="p-4 text-center text-muted-foreground">
            // eslint-disable-next-line jsx-a11y/alt-text
            <Image className="h-8 w-8 mx-auto mb-2 text-gray-400" />
            <p>No images attached yet</p>
            <p className="text-sm mt-1">
              Attach images to help providers understand your service request better
            </p>
          </CardContent>
        </Card>
      )}
    </div>
  )
}

'use client'

import React, { useState } from 'react'
import { Button } from '@/components/ui/button'
import { LoadingSkeleton, ButtonSkeleton } from '@/components/ui/LoadingSkeleton'
import { useOptimisticActionWithTelemetry } from '@/hooks/useOptimisticActionWithTelemetry'
import { useDebouncedClickWithTelemetry } from '@/hooks/useDebouncedClickWithTelemetry'
import { useResilientQuery } from '@/hooks/useResilientQuery'
import { CheckCircle, CreditCard, AlertCircle, Loader2 } from 'lucide-react'

interface PaymentDetails {
  amount: number
  currency: string
  description: string
}

interface ResilientPaymentButtonProps {
  onPaymentSuccessAction: (paymentId: string) => void
  onPaymentErrorAction: (error: Error) => void
  paymentDetails: PaymentDetails
  onSuccess?: (paymentId: string) => void
  onError?: (error: Error) => void
  className?: string
  disabled?: boolean
}

export function ResilientPaymentButton({
  onPaymentSuccessAction,
  onPaymentErrorAction,
  paymentDetails,
  className = '',
  disabled = false
}: ResilientPaymentButtonProps) {
  const [paymentStatus, setPaymentStatus] = useState<'idle' | 'processing' | 'success' | 'failed'>('idle')
  const [paymentId, setPaymentId] = useState<string | null>(null)

  // 1. OPTIMISTIC PAYMENT ACTION with TELEMETRY
  const { execute: executePayment, status, error, rollback } = useOptimisticActionWithTelemetry({
    action: async (details: PaymentDetails) => {
      // Simulate payment processing
      const response = await fetch('/api/payments/process', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(details)
      })

      if (!response.ok) {
        throw new Error('Payment failed')
      }

      const result = await response.json()
      return result.paymentId
    },
    onOptimistic: (_details: PaymentDetails) => {
      setPaymentStatus('processing')
      setPaymentId(null)
    },
    onSuccess: (paymentId: string, _details: PaymentDetails) => {
      setPaymentStatus('success')
      setPaymentId(paymentId)
      onPaymentSuccessAction(paymentId)
    },
    onError: (error: Error, _details: PaymentDetails) => {
      setPaymentStatus('failed')
      onPaymentErrorAction(error)
    },
    onRollback: (_details: PaymentDetails) => {
      setPaymentStatus('idle')
      setPaymentId(null)
    },
    component: 'ResilientPaymentButton' // Required for telemetry
  })

  // 2. DEBOUNCED CLICK (prevents double-payments) with TELEMETRY
  const debouncedPayment = useDebouncedClickWithTelemetry(() => executePayment(paymentDetails), {
    delay: 500,
    onDuplicate: () => {
      console.log('Payment already in progress, ignoring duplicate click')
    },
    component: 'ResilientPaymentButton' // Required for telemetry
  })

  // 3. RESILIENT QUERY for payment status confirmation
  const { data: confirmedPayment, isLoading: isConfirming } = useResilientQuery({
    key: ['payment-confirmation', paymentId || ''],
    fetcher: async () => {
      if (!paymentId) throw new Error('No payment ID')

      const response = await fetch(`/api/payments/${paymentId}/status`)
      if (!response.ok) throw new Error('Failed to confirm payment')

      return response.json()
    },
    enabled: !!paymentId && paymentStatus === 'success',
    retry: { attempts: 3, backoff: 'exponential', delay: 1000 },
    staleTime: 5000
  })

  // 4. LOADING SKELETONS for different states
  if (status === 'optimistic' || paymentStatus === 'processing') {
    return (
      <div className="space-y-2">
        <ButtonSkeleton size="lg" className="w-full" />
        <div className="text-center">
          <LoadingSkeleton width={120} height={14} />
        </div>
      </div>
    )
  }

  // 5. SUCCESS STATE with confirmation
  if (paymentStatus === 'success') {
    return (
      <div className="space-y-3">
        <Button disabled className="w-full bg-green-600 text-white">
          <CheckCircle className="w-4 h-4 mr-2" />
          Payment Successful!
        </Button>

        {isConfirming && (
          <div className="text-center text-sm text-gray-600">
            <Loader2 className="w-4 h-4 inline animate-spin mr-2" />
            Confirming payment...
          </div>
        )}

        {confirmedPayment && (
          <div className="text-center text-sm text-green-600">
            âœ“ Payment confirmed on blockchain
          </div>
        )}
      </div>
    )
  }

  // 6. ERROR STATE with retry
  if (paymentStatus === 'failed' || error) {
    return (
      <div className="space-y-3">
        <Button 
          onClick={() => debouncedPayment()}
          variant="destructive"
          className="w-full"
        >
          <AlertCircle className="w-4 h-4 mr-2" />
          Payment Failed - Try Again
        </Button>

        <div className="text-center text-sm text-red-600">
          {error?.message || 'Something went wrong. Your card was not charged.'}
        </div>

        <Button 
          onClick={() => rollback(paymentDetails)}
          variant="outline"
          size="sm"
          className="w-full"
        >
          Reset Payment
        </Button>
      </div>
    )
  }

  // 7. DEFAULT STATE - Payment button
  return (
    <Button
      onClick={() => debouncedPayment()}
      disabled={disabled || status === 'loading'}
      className={`w-full ${className}`}
      size="lg"
    >
      <CreditCard className="w-4 h-4 mr-2" />
      Pay {paymentDetails.currency} {paymentDetails.amount}
    </Button>
  )
}

