'use client'

import { useState, useEffect } from 'react'
import { Search, Filter, MapPin, Star, Clock, DollarSign } from 'lucide-react'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card'
import { Badge } from '@/components/ui/badge'
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select'


interface Specialty {
  id: string
  name: string
  slug: string
  parent_id: string | null
  path: string
}

interface Provider {
  id: string
  full_name: string
  business_name: string
  average_rating: number
  total_reviews: number
  business_address: string
  distance?: number
  specialties: Array<{
    specialty_id: string
    is_primary: boolean
    specialty: {
      name: string
    }
  }>
}

interface SearchFilters {
  query: string
  location: string
  specialtyIds: string[]
  minRating: number
  maxDistance: number
  maxPrice: number
  availability: string
}

export default function SpecialtySearch() {
  const [specialties, setSpecialties] = useState<Specialty[]>([])
  const [providers, setProviders] = useState<Provider[]>([])
  const [loading, setLoading] = useState(false)
  const [filters, setFilters] = useState<SearchFilters>({
    query: '',
    location: '',
    specialtyIds: [],
    minRating: 0,
    maxDistance: 50,
    maxPrice: 1000,
    availability: ''
  })
  const [showFilters, setShowFilters] = useState(false)
  const [selectedSpecialty, setSelectedSpecialty] = useState<string>('')

  useEffect(() => {
    loadSpecialties()
  }, [])

  useEffect(() => {
    if (filters.query || filters.specialtyIds.length > 0 || filters.location) {
      performSearch()
    }
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [filters])

  const loadSpecialties = async () => {
    try {
      const response = await fetch('/api/specialties')
      if (response.ok) {
        const data = await response.json()
        setSpecialties(data.items || [])
      }
    } catch (error) {
      console.error('Error loading specialties:', error)
    }
  }

  const performSearch = async () => {
    setLoading(true)
    try {
      const queryParams = new URLSearchParams()
      
      if (filters.query) queryParams.append('query', filters.query)
      if (filters.location) queryParams.append('location', filters.location)
      if (filters.specialtyIds.length > 0) queryParams.append('specialty_ids', filters.specialtyIds.join(','))
      if (filters.minRating > 0) queryParams.append('min_rating', filters.minRating.toString())
      if (filters.maxDistance < 50) queryParams.append('maxTravelDistance', filters.maxDistance.toString())
      if (filters.maxPrice < 1000) queryParams.append('max_price', filters.maxPrice.toString())
      if (filters.availability) queryParams.append('availability_date', filters.availability)

      const response = await fetch(`/api/search/providers?${queryParams}`)
      if (response.ok) {
        const data = await response.json()
        setProviders(data.providers || [])
      }
    } catch (error) {
      console.error('Search failed:', error)
    } finally {
      setLoading(false)
    }
  }

  const handleSpecialtyToggle = (specialtyId: string) => {
    setFilters(prev => ({
      ...prev,
      specialtyIds: prev.specialtyIds.includes(specialtyId)
        ? prev.specialtyIds.filter(id => id !== specialtyId)
        : [...prev.specialtyIds, specialtyId]
    }))
  }

  const clearFilters = () => {
    setFilters({
      query: '',
      location: '',
      specialtyIds: [],
      minRating: 0,
      maxDistance: 50,
      maxPrice: 1000,
      availability: ''
    })
  }

  const getSpecialtyPath = (specialty: Specialty): string => {
    if (!specialty.parent_id) return specialty.name
    
    const parent = specialties.find(s => s.id === specialty.parent_id)
    if (parent) {
      return `${parent.name} > ${specialty.name}`
    }
    
    return specialty.name
  }

  const getRatingColor = (rating: number) => {
    if (rating >= 4.5) return 'text-green-600'
    if (rating >= 4.0) return 'text-blue-600'
    if (rating >= 3.5) return 'text-yellow-600'
    return 'text-gray-600'
  }

  return (
    <div className="max-w-7xl mx-auto p-6 space-y-6">
      {/* Search Header */}
      <div className="text-center">
        <h1 className="text-4xl font-bold text-gray-900 mb-4">Find the Perfect Service Provider</h1>
        <p className="text-xl text-gray-600">Search by specialty, location, and more to find exactly what you need</p>
      </div>

      {/* Search Bar */}
      <Card>
        <CardContent className="p-6">
          <div className="flex flex-col lg:flex-row gap-4">
            <div className="flex-1">
              <Input
                placeholder="What service do you need?"
                value={filters.query}
                onChange={(e) => setFilters(prev => ({ ...prev, query: e.target.value }))}
                className="h-12 text-lg"
              />
            </div>
            <div className="flex-1">
              <Input
                placeholder="Where are you located?"
                value={filters.location}
                onChange={(e) => setFilters(prev => ({ ...prev, location: e.target.value }))}
                className="h-12 text-lg"
              />
            </div>
            <Button 
              onClick={() => setShowFilters(!showFilters)}
              variant="outline"
              className="h-12 px-6"
            >
              <Filter className="h-5 w-5 mr-2" />
              Filters
            </Button>
            <Button 
              onClick={performSearch}
              disabled={loading}
              className="h-12 px-8"
            >
              <Search className="h-5 w-5 mr-2" />
              {loading ? 'Searching...' : 'Search'}
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Filters Panel */}
      {showFilters && (
        <Card>
          <CardHeader>
            <CardTitle>Advanced Filters</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
              {/* Specialty Selection */}
              <div>
                <label className="block text-sm font-medium mb-3">Service Specialties</label>
                <div className="space-y-2 max-h-48 overflow-y-auto">
                  {specialties.map(specialty => (
                    <div key={specialty.id} className="flex items-center space-x-2">
                      <input
                        type="checkbox"
                        id={specialty.id}
                        checked={filters.specialtyIds.includes(specialty.id)}
                        onChange={() => handleSpecialtyToggle(specialty.id)}
                        className="rounded"
                      />
                      <label htmlFor={specialty.id} className="text-sm">
                        {getSpecialtyPath(specialty)}
                      </label>
                    </div>
                  ))}
                </div>
              </div>

              {/* Rating Filter */}
              <div>
                <label className="block text-sm font-medium mb-3">Minimum Rating</label>
                <Select
                  value={filters.minRating.toString()}
                  onValueChange={(value) => setFilters(prev => ({ ...prev, minRating: parseInt(value) }))}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="0">Any Rating</SelectItem>
                    <SelectItem value="3">3+ Stars</SelectItem>
                    <SelectItem value="4">4+ Stars</SelectItem>
                    <SelectItem value="4.5">4.5+ Stars</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              {/* Distance Filter */}
              <div>
                <label className="block text-sm font-medium mb-3">Max Distance</label>
                <Select
                  value={filters.maxDistance.toString()}
                  onValueChange={(value) => setFilters(prev => ({ ...prev, maxDistance: parseInt(value) }))}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="5">5 miles</SelectItem>
                    <SelectItem value="10">10 miles</SelectItem>
                    <SelectItem value="25">25 miles</SelectItem>
                    <SelectItem value="50">50+ miles</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              {/* Price Filter */}
              <div>
                <label className="block text-sm font-medium mb-3">Max Price</label>
                <Select
                  value={filters.maxPrice.toString()}
                  onValueChange={(value) => setFilters(prev => ({ ...prev, maxPrice: parseInt(value) }))}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="100">$100</SelectItem>
                    <SelectItem value="250">$250</SelectItem>
                    <SelectItem value="500">$500</SelectItem>
                    <SelectItem value="1000">$1000+</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>

            <div className="mt-6 flex justify-end">
              <Button variant="outline" onClick={clearFilters}>
                Clear All Filters
              </Button>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Results */}
      <div className="space-y-4">
        <div className="flex items-center justify-between">
          <h2 className="text-2xl font-bold text-gray-900">
            {loading ? 'Searching...' : `${providers.length} Providers Found`}
          </h2>
          {providers.length > 0 && (
            <Select value={selectedSpecialty} onValueChange={setSelectedSpecialty}>
              <SelectTrigger className="w-48">
                <SelectValue placeholder="Sort by specialty" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="">All Specialties</SelectItem>
                {specialties.map(specialty => (
                  <SelectItem key={specialty.id} value={specialty.id}>
                    {specialty.name}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          )}
        </div>

        {providers.length > 0 && (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {providers
              .filter(provider => 
                !selectedSpecialty || 
                provider.specialties.some(s => s.specialty_id === selectedSpecialty)
              )
              .map(provider => (
                <Card key={provider.id} className="hover:shadow-lg transition-shadow">
                  <CardHeader>
                    <div className="flex items-start justify-between">
                      <div>
                        <CardTitle className="text-lg">
                          {provider.business_name || provider.full_name}
                        </CardTitle>
                        <p className="text-sm text-gray-600">{provider.business_address}</p>
                      </div>
                      <div className="text-right">
                        <div className={`text-lg font-bold ${getRatingColor(provider.average_rating)}`}>
                          {provider.average_rating.toFixed(1)} â­
                        </div>
                        <p className="text-xs text-gray-500">
                          {provider.total_reviews} reviews
                        </p>
                      </div>
                    </div>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-3">
                      {/* Specialties */}
                      <div>
                        <p className="text-sm font-medium text-gray-700 mb-2">Specialties:</p>
                        <div className="flex flex-wrap gap-1">
                          {provider.specialties.map((spec, index) => (
                            <Badge 
                              key={index} 
                              variant={spec.is_primary ? "default" : "secondary"}
                              className="text-xs"
                            >
                              {spec.specialty.name}
                              {spec.is_primary && " (Primary)"}
                            </Badge>
                          ))}
                        </div>
                      </div>

                      {/* Distance */}
                      {provider.distance && (
                        <div className="flex items-center text-sm text-gray-600">
                          <MapPin className="h-4 w-4 mr-1" />
                          {provider.distance.toFixed(1)} miles away
                        </div>
                      )}

                      {/* Actions */}
                      <div className="flex space-x-2 pt-2">
                        <Button size="sm" className="flex-1">
                          View Profile
                        </Button>
                        <Button size="sm" variant="outline">
                          Book Now
                        </Button>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))}
          </div>
        )}

        {!loading && providers.length === 0 && filters.query && (
          <Card>
            <CardContent className="p-8 text-center">
              <Search className="h-16 w-16 text-gray-400 mx-auto mb-4" />
              <h3 className="text-lg font-medium text-gray-900 mb-2">No providers found</h3>
              <p className="text-gray-600">
                Try adjusting your search criteria or expanding your search area.
              </p>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  )
}

"use client";

import { useState, useEffect, useMemo, useRef, useCallback } from "react";
import { ChevronRight, ArrowLeft, Plus, Search, X, Loader2 } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";

type SpecialtyNode = { 
  id: string; 
  name: string; 
  parent_id: string | null;
  path?: string;
  level?: number;
  breadcrumb?: string[];
  is_leaf?: boolean;
};

type Breadcrumb = { id: string; name: string };

interface SpecialtyTreeSelectProps {
  value?: string;
  onChangeAction: (id: string, name: string) => void;
  placeholder?: string;
  className?: string;
  maxHeight?: string;
}

// Cache for specialty data
const specialtyCache = new Map<string, { data: SpecialtyNode[]; timestamp: number }>();
const CACHE_TTL = 5 * 60 * 1000; // 5 minutes

async function fetchSpecialtiesWithCache(parent: string | null, searchQuery?: string): Promise<SpecialtyNode[]> {
  const cacheKey = `specialties:${parent || 'root'}:${searchQuery || ''}`;
  const cached = specialtyCache.get(cacheKey);
  
  if (cached && Date.now() - cached.timestamp < CACHE_TTL) {
    return cached.data;
  }

  try {
    let url = `/api/specialties`;
    if (parent === 'root') {
      url += '?parent=root';
    } else if (parent) {
      url += `?parent=${parent}`;
    }
    if (searchQuery) {
      url += `${parent ? '&' : '?'}q=${encodeURIComponent(searchQuery)}`;
    }

    const res = await fetch(url, { cache: "no-store" });
    if (!res.ok) throw new Error("Failed to fetch");
    
    const json = await res.json();
    const data = json.items || [];
    
    // Cache the results
    specialtyCache.set(cacheKey, { data, timestamp: Date.now() });
    
    return data;
  } catch (error) {
    console.error("Failed to fetch specialties:", error);
    return [];
  }
}

async function searchSpecialtiesOptimized(q: string): Promise<SpecialtyNode[]> {
  if (q.length < 2) return [];
  
  try {
    const res = await fetch(`/api/specialties?q=${encodeURIComponent(q)}`, { cache: "no-store" });
    if (!res.ok) throw new Error("Failed to fetch");
    const json = await res.json();
    return json.items || [];
  } catch (error) {
    console.error("Failed to search specialties:", error);
    return [];
  }
}

async function submitSuggestion(proposedName: string, parentId?: string, details?: string): Promise<boolean> {
  try {
    const res = await fetch("/api/specialties/suggest", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name: proposedName, parent_id: parentId, details }),
    });
    return res.ok;
  } catch (error) {
    console.error("Failed to submit suggestion:", error);
    return false;
  }
}

export function SpecialtyTreeSelectOptimized({ 
  value, 
  onChangeAction, 
  placeholder = "Select specialty", 
  className,
  maxHeight = "max-h-96"
}: SpecialtyTreeSelectProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [currentParent, setCurrentParent] = useState<string | undefined>(undefined);
  const [breadcrumbs, setBreadcrumbs] = useState<Breadcrumb[]>([]);
  const [children, setChildren] = useState<SpecialtyNode[]>([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState<SpecialtyNode[]>([]);
  const [isSearching, setIsSearching] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [showSuggestionForm, setShowSuggestionForm] = useState(false);
  const [suggestionName, setSuggestionName] = useState("");
  const [suggestionDetails, setSuggestionDetails] = useState("");
  const [isSubmittingSuggestion, setIsSubmittingSuggestion] = useState(false);
  const [virtualizedItems, setVirtualizedItems] = useState<SpecialtyNode[]>([]);
  const [scrollTop, setScrollTop] = useState(0);
  
  // Create a debounced search query using useEffect and setTimeout
  const [debouncedSearchQuery, setDebouncedSearchQuery] = useState("");
  const scrollContainerRef = useRef<HTMLDivElement>(null);

  // Debounce the search query
  useEffect(() => {
    const timer = setTimeout(() => {
      setDebouncedSearchQuery(searchQuery);
    }, 300);

    return () => clearTimeout(timer);
  }, [searchQuery]);

  const selectedSpecialty = useMemo(() => {
    if (!value) return null;
    const allSpecialties = [...children, ...searchResults];
    return allSpecialties.find(s => s.id === value);
  }, [value, children, searchResults]);

  // Virtualization settings
  const itemHeight = 48; // Height of each specialty item
  const visibleItems = 10; // Number of items visible at once
  const totalHeight = children.length * itemHeight;
  const startIndex = Math.floor(scrollTop / itemHeight);
  const endIndex = Math.min(startIndex + visibleItems, children.length);

  useEffect(() => {
    if (isOpen && !currentParent) {
      loadRootSpecialties();
    }
  }, [isOpen, currentParent]);

  useEffect(() => {
    if (debouncedSearchQuery && debouncedSearchQuery.length >= 2) {
      void performSearch();
    } else {
      setSearchResults([]);
    }
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [debouncedSearchQuery]);

  useEffect(() => {
    // Update virtualized items when children change
    setVirtualizedItems(children.slice(startIndex, endIndex));
  }, [children, startIndex, endIndex]);

  const loadRootSpecialties = async () => {
    setIsLoading(true);
    const rootSpecialties = await fetchSpecialtiesWithCache(null);
    setChildren(rootSpecialties);
    setBreadcrumbs([]);
    setCurrentParent(undefined);
    setIsLoading(false);
  };

  const loadChildren = async (parentId: string, parentName: string) => {
    setIsLoading(true);
    const childSpecialties = await fetchSpecialtiesWithCache(parentId);
    setChildren(childSpecialties);
    setCurrentParent(parentId);
    setBreadcrumbs(prev => [...prev, { id: parentId, name: parentName }]);
    setIsLoading(false);
  };

  const navigateToParent = (targetParentId: string) => {
    const targetIndex = breadcrumbs.findIndex(crumb => crumb.id === targetParentId);
    if (targetIndex !== -1) {
      const targetBreadcrumbs = breadcrumbs.slice(0, targetIndex + 1);
      setBreadcrumbs(targetBreadcrumbs);
      setCurrentParent(targetParentId);
      loadChildren(targetParentId, targetBreadcrumbs[targetBreadcrumbs.length - 1].name);
    }
  };

  const performSearch = async () => {
    if (!debouncedSearchQuery || debouncedSearchQuery.length < 2) return;
    
    setIsSearching(true);
    const results = await searchSpecialtiesOptimized(debouncedSearchQuery);
    setSearchResults(results);
    setIsSearching(false);
  };

  const handleSuggestionSubmit = async () => {
    if (!suggestionName.trim()) return;
    
    setIsSubmittingSuggestion(true);
    const success = await submitSuggestion(suggestionName, currentParent, suggestionDetails);
    
    if (success) {
      setShowSuggestionForm(false);
      setSuggestionName("");
      setSuggestionDetails("");
      // Refresh the current view
      if (currentParent) {
        loadChildren(currentParent, breadcrumbs[breadcrumbs.length - 1]?.name || "");
      } else {
        loadRootSpecialties();
      }
    }
    
    setIsSubmittingSuggestion(false);
  };

  const handleScroll = useCallback((e: React.UIEvent<HTMLDivElement>) => {
    const scrollTop = e.currentTarget.scrollTop;
    setScrollTop(scrollTop);
  }, []);

  const renderSpecialtyItem = (specialty: SpecialtyNode, index: number) => {
    const _actualIndex = startIndex + index;
    const hasChildren = specialty.level !== undefined && specialty.level < 5; // Assume max depth of 5
    
    return (
      <div
        key={specialty.id}
        className="flex items-center justify-between p-3 hover:bg-gray-50 cursor-pointer border-b border-gray-100"
        style={{ height: itemHeight }}
        onClick={() => {
          if (hasChildren) {
            loadChildren(specialty.id, specialty.name);
          } else {
            onChangeAction(specialty.id, specialty.name);
            setIsOpen(false);
          }
        }}
      >
        <div className="flex items-center space-x-2 flex-1">
          {hasChildren && (
            <ChevronRight className="h-4 w-4 text-gray-400" />
          )}
          <span className="text-sm font-medium">{specialty.name}</span>
          {specialty.path && (
            <Badge variant="outline" className="text-xs">
              {specialty.path.split('.').slice(-2).join(' > ')}
            </Badge>
          )}
        </div>
                 {hasChildren && (
           <span className="text-xs text-gray-500">
             subcategories
           </span>
         )}
      </div>
    );
  };

  return (
    <div className={`relative ${className}`}>
      {/* Trigger Button */}
      <Button
        variant="outline"
        onClick={() => setIsOpen(!isOpen)}
        className="w-full justify-between"
      >
        <span className={selectedSpecialty ? "" : "text-muted-foreground"}>
          {selectedSpecialty ? selectedSpecialty.name : placeholder}
        </span>
        <ChevronRight className={`h-4 w-4 transition-transform ${isOpen ? "rotate-90" : ""}`} />
      </Button>

      {/* Dropdown */}
      {isOpen && (
        <Card className="absolute top-full left-0 right-0 z-50 mt-1 overflow-hidden" style={{ maxHeight: maxHeight }}>
          <CardContent className="p-0">
            {/* Header with breadcrumbs */}
            <div className="border-b p-3 bg-gray-50">
              <div className="flex items-center gap-2 mb-2">
                {breadcrumbs.length > 0 && (
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => loadRootSpecialties()}
                    className="h-6 px-2"
                  >
                    <ArrowLeft className="h-3 w-3 mr-1" />
                    Root
                  </Button>
                )}
                {breadcrumbs.map((crumb, index) => (
                  <div key={crumb.id} className="flex items-center gap-1">
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => navigateToParent(crumb.id)}
                      className="h-6 px-2 text-xs"
                    >
                      {crumb.name}
                    </Button>
                    {index < breadcrumbs.length - 1 && <ChevronRight className="h-3 w-3" />}
                  </div>
                ))}
              </div>
              
              {/* Search */}
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                <Input
                  placeholder="Search specialties..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pl-10 pr-4 h-8 text-sm"
                />
                {isSearching && (
                  <Loader2 className="absolute right-3 top-1/2 transform -translate-y-1/2 h-4 w-4 animate-spin text-gray-400" />
                )}
              </div>
            </div>

            {/* Content */}
            <div className="flex-1 overflow-hidden">
              {isLoading ? (
                <div className="flex items-center justify-center p-8">
                  <Loader2 className="h-6 w-6 animate-spin text-gray-400" />
                </div>
              ) : searchQuery && searchResults.length > 0 ? (
                // Search results
                <div className="max-h-80 overflow-y-auto">
                  {searchResults.map((specialty) => (
                    <div
                      key={specialty.id}
                      className="flex items-center justify-between p-3 hover:bg-gray-50 cursor-pointer border-b border-gray-100"
                      onClick={() => {
                        onChangeAction(specialty.id, specialty.name);
                        setIsOpen(false);
                      }}
                    >
                      <div className="flex items-center space-x-2">
                        <span className="text-sm font-medium">{specialty.name}</span>
                        {specialty.path && (
                          <Badge variant="outline" className="text-xs">
                            {specialty.path.split('.').slice(-2).join(' > ')}
                          </Badge>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              ) : children.length > 0 ? (
                // Virtualized list for large datasets
                <div 
                  ref={scrollContainerRef}
                  className="overflow-y-auto"
                  style={{ height: Math.min(children.length * itemHeight, 400) }}
                  onScroll={handleScroll}
                >
                  <div style={{ height: totalHeight, position: 'relative' }}>
                    <div
                      style={{
                        position: 'absolute',
                        top: startIndex * itemHeight,
                        left: 0,
                        right: 0
                      }}
                    >
                      {virtualizedItems.map((specialty, index) => 
                        renderSpecialtyItem(specialty, index)
                      )}
                    </div>
                  </div>
                </div>
              ) : (
                // Empty state
                <div className="p-8 text-center text-gray-500">
                  <p className="text-sm">No specialties found</p>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => setShowSuggestionForm(true)}
                    className="mt-2"
                  >
                    <Plus className="h-4 w-4 mr-2" />
                    Suggest New
                  </Button>
                </div>
              )}
            </div>

            {/* Suggestion Form */}
            {showSuggestionForm && (
              <div className="border-t p-4 bg-gray-50">
                <div className="space-y-3">
                  <Input
                    placeholder="New specialty name"
                    value={suggestionName}
                    onChange={(e) => setSuggestionName(e.target.value)}
                  />
                  <Input
                    placeholder="Additional details (optional)"
                    value={suggestionDetails}
                    onChange={(e) => setSuggestionDetails(e.target.value)}
                  />
                  <div className="flex gap-2">
                    <Button
                      size="sm"
                      onClick={handleSuggestionSubmit}
                      disabled={isSubmittingSuggestion || !suggestionName.trim()}
                    >
                      {isSubmittingSuggestion ? (
                        <Loader2 className="h-4 w-4 animate-spin mr-2" />
                      ) : (
                        <Plus className="h-4 w-4 mr-2" />
                      )}
                      Submit
                    </Button>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => setShowSuggestionForm(false)}
                    >
                      Cancel
                    </Button>
                  </div>
                </div>
              </div>
            )}

            {/* Footer */}
            <div className="border-t p-3 bg-gray-50">
              <div className="flex items-center justify-between text-xs text-gray-500">
                <span>
                  {children.length} {children.length === 1 ? 'specialty' : 'specialties'}
                  {currentParent && ` in ${breadcrumbs[breadcrumbs.length - 1]?.name}`}
                </span>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => setShowSuggestionForm(!showSuggestionForm)}
                  className="h-6 px-2"
                >
                  <Plus className="h-3 w-3 mr-1" />
                  Suggest
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

'use client'

import React, { useState, useRef, useEffect } from 'react'
import { motion, AnimatePresence } from 'framer-motion'
import { Button } from '@/components/ui/button'
import Link from 'next/link'

interface Message {
  id: string
  role: 'user' | 'assistant'
  content: string
  timestamp: Date
  sources?: { title: string, score: number, url?: string | null }[]
}

// Render message content with clickable markdown links
function renderMessageWithLinks(content: string) {
  // Match markdown links: [text](/path) or [text](http://url)
  const linkRegex = /\[([^\]]+)\]\(([^)]+)\)/g;
  const parts: (string | JSX.Element)[] = [];
  let lastIndex = 0;
  let key = 0;

  // Find all matches first
  const matches: Array<{ index: number; text: string; url: string; length: number }> = [];
  let match;

  // Reset regex lastIndex to ensure we start from the beginning
  linkRegex.lastIndex = 0;
  while ((match = linkRegex.exec(content)) !== null) {
    matches.push({
      index: match.index,
      text: match[1],
      url: match[2],
      length: match[0].length
    });
  }

  // If no links found, return original content
  if (matches.length === 0) {
    return <>{content}</>;
  }

  // Build parts array with text and links
  matches.forEach((m) => {
    // Add text before the link
    if (m.index > lastIndex) {
      parts.push(content.substring(lastIndex, m.index));
    }

    // Convert full URL to relative path for Next.js Link
    let linkUrl = m.url;
    if (linkUrl.startsWith('http')) {
      try {
        linkUrl = new URL(linkUrl).pathname;
      } catch {
        // Keep original if URL parsing fails
      }
    }

    // Add the link
    parts.push(
      <Link
        key={key++}
        href={linkUrl}
        className="text-blue-600 hover:text-blue-800 hover:underline font-medium"
      >
        {m.text}
      </Link>
    );

    lastIndex = m.index + m.length;
  });

  // Add remaining text
  if (lastIndex < content.length) {
    parts.push(content.substring(lastIndex));
  }

  return <>{parts}</>;
}

export default function SupportChat({ onCloseAction }: { onCloseAction: () => void }) {
  const [messages, setMessages] = useState<Message[]>([
    {
      id: '1',
      role: 'assistant',
      content: 'Hello! I am the Bookiji Support Bot. How can I help you today?',
      timestamp: new Date()
    }
  ])
  const [inputMessage, setInputMessage] = useState('')
  const [isTyping, setIsTyping] = useState(false)
  const messagesEndRef = useRef<HTMLDivElement>(null)

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" })
  }

  useEffect(() => {
    scrollToBottom()
  }, [messages])

  const handleSendMessage = async () => {
    if (!inputMessage.trim() || isTyping) return

    const userMessage: Message = {
      id: Date.now().toString(),
      role: 'user',
      content: inputMessage,
      timestamp: new Date()
    }

    setMessages(prev => [...prev, userMessage])
    setInputMessage('')
    setIsTyping(true)

    try {
      // Build history from previous messages for context
      const history = messages
        .filter(m => m.role === 'assistant' && 'confidence' in m)
        // eslint-disable-next-line @typescript-eslint/no-explicit-any
        .map(m => ({ confidence: (m as any).confidence || 0 }))
        .slice(-5); // Keep last 5 for context

      // Add timeout to prevent infinite spinning
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 30000); // 30 second timeout

      let response;
      try {
        response = await fetch('/api/support/chat', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ 
            message: userMessage.content,
            history: history
          }),
          signal: controller.signal
        });
        clearTimeout(timeoutId);
      } catch (fetchError) {
        clearTimeout(timeoutId);
        if (fetchError instanceof Error && fetchError.name === 'AbortError') {
          throw new Error('Request timed out. Please try again.');
        }
        throw fetchError;
      }

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.error || `Failed to get response: ${response.status}`);
      }

      const data = await response.json()

      const aiMessage: Message = {
        id: (Date.now() + 1).toString(),
        role: 'assistant',
        content: data.reply || "I'm sorry, I couldn't find an answer to that.",
        sources: data.sources,
        timestamp: new Date(),
        // eslint-disable-next-line @typescript-eslint/no-explicit-any
        ...(data.confidence !== undefined && { confidence: data.confidence } as any),
        // eslint-disable-next-line @typescript-eslint/no-explicit-any
        ...(data.escalated && { escalated: true, ticketId: data.ticketId } as any)
      }

      setMessages(prev => [...prev, aiMessage])

      // Show escalation message if ticket was created
      if (data.escalated && data.ticketId) {
        console.log('Ticket created:', data.ticketId);
      }
    } catch (error) {
      console.error('Support chat error:', error)
      const errorMsg = error instanceof Error ? error.message : 'Unknown error occurred';
      const errorMessage: Message = {
        id: (Date.now() + 1).toString(),
        role: 'assistant',
        content: errorMsg.includes('timeout') 
          ? "The request took too long. Please try again with a shorter question."
          : errorMsg.includes('Failed to get response')
          ? "I'm having trouble connecting to the support system right now. Please try again later."
          : `Sorry, I encountered an error: ${errorMsg}. Please try again.`,
        timestamp: new Date()
      }
      setMessages(prev => [...prev, errorMessage])
    } finally {
      setIsTyping(false)
    }
  }

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault()
      handleSendMessage()
    }
  }

  return (
    <div className="flex flex-col h-full bg-background">
      {/* Header */}
      <div className="flex items-center justify-between p-4 border-b">
        <div className="flex items-center gap-2">
          <div className="w-8 h-8 rounded-full bg-fuchsia-600 flex items-center justify-center text-white">
            ?
          </div>
          <div>
            <h3 className="font-semibold">Bookiji Support</h3>
            <span className="text-xs text-muted-foreground">Powered by RAG</span>
          </div>
        </div>
        <Button variant="ghost" size="sm" onClick={onCloseAction}>Ã—</Button>
      </div>

      {/* Messages */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4">
        {messages.map((message) => (
          <motion.div
            key={message.id}
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            className={`flex gap-3 ${message.role === 'user' ? 'justify-end' : ''}`}
          >
            {message.role === 'assistant' && (
              <div className="flex-shrink-0 w-8 h-8 rounded-full bg-fuchsia-100 flex items-center justify-center text-fuchsia-600 font-bold text-xs">
                AI
              </div>
            )}
            <div className={`rounded-2xl p-3 max-w-[85%] text-sm ${
              message.role === 'user' 
                ? 'bg-primary text-primary-foreground rounded-br-none' 
                : 'bg-muted rounded-tl-none'
            }`}>
              <div className="whitespace-pre-wrap">
                {renderMessageWithLinks(message.content)}
              </div>
              {message.sources && message.sources.length > 0 && (
                <div className="mt-2 pt-2 border-t border-muted-foreground/20">
                  <p className="text-xs font-semibold mb-1.5 opacity-70">Sources:</p>
                  <ul className="list-none pl-0 space-y-1">
                    {message.sources.slice(0, 3).map((s, i) => {
                      // Convert full URL to relative path for Next.js Link
                      const url = s.url ? (s.url.startsWith('http') ? new URL(s.url).pathname : s.url) : null;
                      return (
                        <li key={i} className="text-xs">
                          {url ? (
                            <Link 
                              href={url}
                              className="text-blue-600 hover:text-blue-800 hover:underline font-medium flex items-center gap-1.5 group cursor-pointer"
                              title={s.title}
                            >
                              <span className="group-hover:translate-x-0.5 transition-transform">â†’</span>
                              <span className="truncate max-w-[250px]">{s.title}</span>
                            </Link>
                          ) : (
                            <span className="text-muted-foreground truncate block max-w-[250px]" title={s.title}>
                              {s.title}
                            </span>
                          )}
                        </li>
                      );
                    })}
                  </ul>
                </div>
              )}
            </div>
          </motion.div>
        ))}
        {isTyping && (
          <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="flex gap-3">
             <div className="flex-shrink-0 w-8 h-8 rounded-full bg-fuchsia-100 flex items-center justify-center text-fuchsia-600 font-bold text-xs">
                AI
              </div>
              <div className="bg-muted rounded-2xl rounded-tl-none p-3 flex gap-1 items-center">
                <span className="w-1.5 h-1.5 bg-gray-400 rounded-full animate-bounce"></span>
                <span className="w-1.5 h-1.5 bg-gray-400 rounded-full animate-bounce delay-100"></span>
                <span className="w-1.5 h-1.5 bg-gray-400 rounded-full animate-bounce delay-200"></span>
              </div>
          </motion.div>
        )}
        <div ref={messagesEndRef} />
      </div>

      {/* Input */}
      <div className="p-4 border-t">
        <div className="flex gap-2">
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Ask a question..."
            className="flex-1 px-4 py-2 border rounded-full focus:outline-none focus:ring-2 focus:ring-fuchsia-500 bg-background"
            disabled={isTyping}
          />
          <Button 
            onClick={handleSendMessage} 
            disabled={isTyping || !inputMessage.trim()}
            className="rounded-full w-10 h-10 p-0 flex items-center justify-center bg-fuchsia-600 hover:bg-fuchsia-700"
          >
            âž¤
          </Button>
        </div>
      </div>
    </div>
  )
}

