                    <h3 className="font-medium text-gray-900 mb-3">Recent Notifications</h3>
                    <div className="space-y-2 max-h-64 overflow-y-auto">
                      {notifications.data.slice(0, 5).map((notification, index) => (
                        <div key={index} className="text-sm text-gray-600 p-2 hover:bg-gray-50 rounded">
                          {notification.message}
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Status Messages */}
        {notifications.error && (
          <NetworkError 
            error={notifications.error} 
            onRetry={loadNotifications}
            className="mb-6"
          />
        )}

        {notifications.success && (
          <SuccessMessage 
            message="Notifications loaded successfully"
            className="mb-6"
            autoDismiss
          />
        )}

        {/* Tabs */}
        <div className="bg-white rounded-lg shadow-sm border mb-8">
          <div className="border-b border-gray-200">
            <nav className="-mb-px flex flex-nowrap gap-6 px-6 overflow-x-auto overscroll-x-contain">
              {[
                { id: 'overview' as const, label: 'Overview', icon: 'ðŸ“Š' },
                { id: 'bookings' as const, label: 'Bookings', icon: 'ðŸ“…' },
                { id: 'credits' as const, label: 'Credits', icon: 'ðŸ’°' },
                { id: 'favorites' as const, label: 'Favorites', icon: 'â¤ï¸' },
                { id: 'profile' as const, label: 'Profile', icon: 'ðŸ‘¤' }
              ].map((tab) => (
                <button
                  key={tab.id}
