'use client'

import { useState, useEffect } from 'react'
import { motion } from 'framer-motion'
import { MessageSquare, Edit, CheckCircle, AlertCircle } from 'lucide-react'

interface Review {
  id: string
  customer_name: string
  rating: number
  comment: string
  created_at: string
  service_name: string
}

interface ReviewResponse {
  id?: string
  review_id: string
  provider_id: string
  response_text: string
  is_public: boolean
  created_at?: string
  updated_at?: string
}

interface ReviewResponseProps {
  review: Review
  providerId: string
  onResponseSubmitted?: (response: ReviewResponse) => void
}

export default function ReviewResponse({ review, providerId, onResponseSubmitted }: ReviewResponseProps) {
  const [responseText, setResponseText] = useState('')
  const [isPublic, setIsPublic] = useState(true)
  const [submitting, setSubmitting] = useState(false)
  const [error, setError] = useState<string | null>(null)
  const [existingResponse, setExistingResponse] = useState<ReviewResponse | null>(null)
  const [showForm, setShowForm] = useState(false)

  useEffect(() => {
    // Check if there's an existing response
    checkExistingResponse()
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [review.id, providerId])

  const checkExistingResponse = async () => {
    try {
      const response = await fetch(`/api/reviews/${review.id}/response?provider_id=${providerId}`)
      if (response.ok) {
        const data = await response.json()
        if (data.response) {
          setExistingResponse(data.response)
          setResponseText(data.response.response_text)
          setIsPublic(data.response.is_public)
        }
      }
    } catch (error) {
      console.error('Error checking existing response:', error)
    }
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    setSubmitting(true)
    setError(null)

    try {
      const endpoint = existingResponse 
        ? `/api/reviews/${review.id}/response/${existingResponse.id}`
        : `/api/reviews/${review.id}/response`

      const method = existingResponse ? 'PUT' : 'POST'

      const response = await fetch(endpoint, {
        method,
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          review_id: review.id,
          provider_id: providerId,
          response_text: responseText.trim(),
          is_public: isPublic,
        }),
      })

      if (!response.ok) {
        throw new Error('Failed to submit response')
      }

      const data = await response.json()

      if (existingResponse) {
        setExistingResponse({ ...existingResponse, ...data.response })
      } else {
        setExistingResponse(data.response)
      }

      setShowForm(false)
      onResponseSubmitted?.(data.response)

      // Show success message
      setError(null)
    } catch (error) {
      setError('Failed to submit response. Please try again.')
      console.error('Error submitting response:', error)
    } finally {
      setSubmitting(false)
    }
  }

  const handleCancel = () => {
    setShowForm(false)
    if (existingResponse) {
      setResponseText(existingResponse.response_text)
      setIsPublic(existingResponse.is_public)
    } else {
      setResponseText('')
      setIsPublic(true)
    }
    setError(null)
  }

  const handleEdit = () => {
    setShowForm(true)
  }

  // If there's an existing response and we're not editing, show it
  if (existingResponse && !showForm) {
    return (
      <div className="bg-blue-50 p-4 rounded-lg border border-blue-200">
        <div className="flex items-center justify-between mb-3">
          <h4 className="font-medium text-blue-800 flex items-center gap-2">
            <CheckCircle size={16} />
            Your Response
          </h4>
          <button
            onClick={handleEdit}
            className="text-blue-600 hover:text-blue-700 text-sm flex items-center gap-1"
          >
            <Edit size={14} />
            Edit
          </button>
        </div>

        <p className="text-blue-700 mb-2">{existingResponse.response_text}</p>

        <div className="flex items-center gap-4 text-xs text-blue-600">
          <span>
            {existingResponse.is_public ? 'Public response' : 'Private response'}
          </span>
          <span>
            {existingResponse.created_at && new Date(existingResponse.created_at).toLocaleDateString('en-US', {
              year: 'numeric',
              month: 'short',
              day: 'numeric'
            })}
          </span>
        </div>
      </div>
    )
  }

  return (
    <div className="bg-gray-50 p-4 rounded-lg border border-gray-200">
      <h4 className="font-medium text-gray-800 mb-3">
        {existingResponse ? 'Edit Response' : 'Respond to Review'}
      </h4>

      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Your Response *
          </label>
          <textarea
            value={responseText}
            onChange={(e) => setResponseText(e.target.value)}
            rows={4}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            placeholder="Share your perspective on this review..."
            required
          />
          <p className="text-xs text-gray-500 mt-1">
            Keep your response professional and constructive
          </p>
        </div>

        <div className="flex items-center gap-2">
          <input
            type="checkbox"
            id="isPublic"
            checked={isPublic}
            onChange={(e) => setIsPublic(e.target.checked)}
            className="w-4 h-4 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
          />
          <label htmlFor="isPublic" className="text-sm text-gray-700">
            Make this response public (customers can see it)
          </label>
        </div>

        {error && (
          <div className="text-red-600 text-sm bg-red-50 p-2 rounded">
            {error}
          </div>
        )}

        <div className="flex items-center gap-3">
          <button
            type="submit"
            disabled={submitting || !responseText.trim()}
            className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
          >
            {submitting ? 'Submitting...' : (existingResponse ? 'Update Response' : 'Submit Response')}
          </button>

          <button
            type="button"
            onClick={handleCancel}
            className="px-4 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-colors"
          >
            Cancel
          </button>
        </div>
      </form>

      <div className="mt-4 p-3 bg-blue-50 rounded-lg">
        <h5 className="text-sm font-medium text-blue-800 mb-2">Response Guidelines</h5>
        <ul className="text-xs text-blue-700 space-y-1">
          <li>â€¢ Be professional and courteous</li>
          <li>â€¢ Address specific concerns mentioned in the review</li>
          <li>â€¢ Thank customers for positive feedback</li>
          <li>â€¢ Offer solutions for any issues raised</li>
          <li>â€¢ Keep responses concise and helpful</li>
        </ul>
      </div>
    </div>
  )
}

/**
 * Production Performance Guardrails Configuration
 * 
 * This file contains production-ready thresholds and alert configurations
 * for the performance monitoring system.
 */

import { GuardrailConfig } from '@/lib/performance/guardrails'

// Environment-specific configurations
const ENVIRONMENT_CONFIGS = {
  development: {
    maxResponseTime: 10000, // 10 seconds - more lenient for dev
    maxMemoryUsage: 1024, // 1GB - dev environments often have more memory
    maxCpuUsage: 90, // 90% - dev can run hotter
    maxRequestRate: 2000, // 2000 req/min - higher for testing
    maxErrorRate: 10, // 10% - more tolerant in dev
    maxCostPerHour: 50, // $50/hr - dev environment
    alertThreshold: 85, // Alert at 85% of limit
  },
  staging: {
    maxResponseTime: 7000, // 7 seconds
    maxMemoryUsage: 768, // 768MB
    maxCpuUsage: 85, // 85%
    maxRequestRate: 1500, // 1500 req/min
    maxErrorRate: 7, // 7%
    maxCostPerHour: 25, // $25/hr
    alertThreshold: 80, // Alert at 80% of limit
  },
  production: {
    maxResponseTime: 5000, // 5 seconds - strict for production
    maxMemoryUsage: 512, // 512MB - optimize for cost
    maxCpuUsage: 80, // 80% - leave headroom for spikes
    maxRequestRate: 1000, // 1000 req/min - production traffic
    maxErrorRate: 5, // 5% - low error tolerance
    maxCostPerHour: 15, // $15/hr - cost control
    alertThreshold: 75, // Alert at 75% of limit - early warning
  },
} as const

// Alert channels configuration
export interface AlertChannelConfig {
  enabled: boolean
  webhook?: string
  email?: string
  slack?: {
    webhookUrl: string
    channel: string
    username?: string
  }
  teams?: {
    webhookUrl: string
  }
  pagerduty?: {
    integrationKey: string
    severity: 'critical' | 'error' | 'warning' | 'info'
  }
}

export const ALERT_CHANNELS: Record<string, AlertChannelConfig> = {
  development: {
    enabled: false, // Disable alerts in dev to avoid noise
  },
  staging: {
    enabled: true,
    slack: {
      webhookUrl: process.env.SLACK_WEBHOOK_URL_STAGING || '',
      channel: '#staging-alerts',
      username: 'Bookiji Staging Monitor'
    }
  },
  production: {
    enabled: true,
    slack: {
      webhookUrl: process.env.SLACK_WEBHOOK_URL_PROD || '',
      channel: '#production-alerts',
      username: 'Bookiji Production Monitor'
    },
    email: process.env.ALERT_EMAIL || 'alerts@bookiji.com',
    pagerduty: {
      integrationKey: process.env.PAGERDUTY_INTEGRATION_KEY || '',
      severity: 'critical'
    }
  }
}

// Critical thresholds that trigger immediate alerts
export const CRITICAL_THRESHOLDS = {
  responseTime: 15000, // 15 seconds - system is essentially down
  memoryUsage: 2048, // 2GB - memory leak or serious issue
  cpuUsage: 95, // 95% - system overload
  errorRate: 25, // 25% - major system failure
  requestRate: 5000, // 5000 req/min - potential DDoS or runaway process
}

/**
 * Get performance configuration based on environment
 */
export function getPerformanceConfig(): GuardrailConfig {
  const env = process.env.NODE_ENV || 'development'
  const deployEnv = process.env.DEPLOY_ENV || env
  
  // Use production config for any production-like environment
  const configKey = ['production', 'prod'].includes(deployEnv.toLowerCase()) ? 'production' : 
                   ['staging', 'stage'].includes(deployEnv.toLowerCase()) ? 'staging' : 
                   'development'
  
  return ENVIRONMENT_CONFIGS[configKey]
}

/**
 * Get alert configuration based on environment
 */
export function getAlertConfig(): AlertChannelConfig {
  const env = process.env.NODE_ENV || 'development'
  const deployEnv = process.env.DEPLOY_ENV || env
  
  const configKey = ['production', 'prod'].includes(deployEnv.toLowerCase()) ? 'production' : 
                   ['staging', 'stage'].includes(deployEnv.toLowerCase()) ? 'staging' : 
                   'development'
  
  return ALERT_CHANNELS[configKey]
}

/**
 * Alert severity levels
 */
export enum AlertSeverity {
  INFO = 'info',
  WARNING = 'warning', 
  ERROR = 'error',
  CRITICAL = 'critical'
}

/**
 * Determine alert severity based on violation
 */
export function getAlertSeverity(metric: string, value: number, threshold: number): AlertSeverity {
  const criticalThreshold = CRITICAL_THRESHOLDS[metric as keyof typeof CRITICAL_THRESHOLDS]
  
  if (criticalThreshold && value >= criticalThreshold) {
    return AlertSeverity.CRITICAL
  }
  
  const ratio = value / threshold
  if (ratio >= 1.2) return AlertSeverity.CRITICAL
  if (ratio >= 1.0) return AlertSeverity.ERROR
  if (ratio >= 0.9) return AlertSeverity.WARNING
  return AlertSeverity.INFO
}

/**
 * Format alert message for different channels
 */
export function formatAlertMessage(
  violations: string[], 
  severity: AlertSeverity,
  environment: string
): {
  title: string
  message: string
  color?: string
  emoji?: string
} {
  const emoji = {
    [AlertSeverity.CRITICAL]: 'ðŸš¨',
    [AlertSeverity.ERROR]: 'âŒ', 
    [AlertSeverity.WARNING]: 'âš ï¸',
    [AlertSeverity.INFO]: 'â„¹ï¸'
  }[severity]

  const color = {
    [AlertSeverity.CRITICAL]: '#FF0000',
    [AlertSeverity.ERROR]: '#FF4444',
    [AlertSeverity.WARNING]: '#FFA500', 
    [AlertSeverity.INFO]: '#0066CC'
  }[severity]

  const title = `${emoji} ${severity.toUpperCase()} - Performance Alert (${environment})`
  
  const message = [
    `Environment: ${environment}`,
    `Severity: ${severity}`,
    `Time: ${new Date().toISOString()}`,
    '',
    'Violations:',
    ...violations.map(v => `â€¢ ${v}`),
    '',
    'Please investigate immediately if this is a production environment.'
  ].join('\n')

  return { title, message, color, emoji }
}

// Export default configuration
export default {
  getPerformanceConfig,
  getAlertConfig,
  getAlertSeverity,
  formatAlertMessage,
  CRITICAL_THRESHOLDS,
  AlertSeverity
}

import { useState, useEffect, useCallback } from 'react'
import { supabaseBrowserClient } from '@/lib/supabaseClient'
import { Notification } from '@/types/notification'
import { NotificationService } from '@/lib/services/notificationService'

interface NotificationState {
  data: Notification[];
  isLoading: boolean;
  error: string | null;
}

export function useNotifications() {
  const [state, setState] = useState<NotificationState>({
    data: [],
    isLoading: false,
    error: null
  })

  const notificationService = NotificationService.getInstance()

  const fetchNotifications = useCallback(async () => {
    const supabase = supabaseBrowserClient()
    if (!supabase) {
      setState(prev => ({ ...prev, isLoading: false, error: 'Supabase client not available' }))
      return
    }

    const { data: { session } } = await supabase.auth.getSession()
    if (!session) {
      // Don't set error for unauthenticated users - just return empty state
      setState(prev => ({
        ...prev,
        isLoading: false,
        error: null,
        data: []
      }))
      return
    }

    setState(prev => ({ ...prev, isLoading: true, error: null }))

    try {
      const notifications = await notificationService.fetchNotifications(session.access_token)
      setState({
        data: notifications,
        isLoading: false,
        error: null
      })
    } catch (error) {
      setState(prev => ({
        ...prev,
        isLoading: false,
        error: error instanceof Error ? error.message : 'Failed to load notifications'
      }))
    }
  }, [notificationService])

  const markAsRead = useCallback(async (notificationId: string) => {
    const supabase = supabaseBrowserClient()
    if (!supabase) return

    const { data: { session } } = await supabase.auth.getSession()
    if (!session) return
    try {
      await notificationService.markAsRead(notificationId, session.access_token)
      setState(prev => ({
        ...prev,
        data: prev.data.map(notification =>
          notification.id === notificationId

            ? { ...notification, read: true, read_at: new Date().toISOString() }

            : notification
        )
      }))
    } catch (error) {
      console.error('Failed to mark notification as read:', error)
    }
  }, [notificationService])

  const markAllAsRead = useCallback(async () => {
    const supabase = supabaseBrowserClient()
    if (!supabase) return

    const { data: { session } } = await supabase.auth.getSession()
    if (!session) return
    try {
      await notificationService.markAllAsRead(session.access_token)
      const timestamp = new Date().toISOString()
      setState(prev => ({
        ...prev,

        data: prev.data.map(notification => ({ ...notification, read: true, read_at: timestamp }))

      }))
    } catch (error) {
      console.error('Failed to mark all notifications as read:', error)
    }
  }, [notificationService])

  const deleteNotification = useCallback(async (notificationId: string) => {
    const supabase = supabaseBrowserClient()
    if (!supabase) return

    const { data: { session } } = await supabase.auth.getSession()
    if (!session) return
    try {
      await notificationService.deleteNotification(notificationId, session.access_token)
      setState(prev => ({
        ...prev,
        data: prev.data.filter(notification => notification.id !== notificationId)
      }))
    } catch (error) {
      console.error('Failed to delete notification:', error)
    }
  }, [notificationService])

  // Set up real-time notifications with reconnection logic
  useEffect(() => {
    let channel: ReturnType<NonNullable<ReturnType<typeof supabaseBrowserClient>>['channel']> | null = null
    let reconnectTimeout: ReturnType<typeof setTimeout> | null = null
    let currentSupabase: ReturnType<typeof supabaseBrowserClient> | null = null
    let reconnectAttempts = 0
    let lastErrorTime = 0
    const MAX_RECONNECT_ATTEMPTS = 5
    const INITIAL_RECONNECT_DELAY = 2000 // 2 seconds
    const MAX_RECONNECT_DELAY = 30000 // 30 seconds
    let _isSubscribed = false

    const subscribe = async () => {
      const supabase = supabaseBrowserClient()
      if (!supabase) return
      currentSupabase = supabase

      const { data: { session } } = await supabase.auth.getSession()
      if (!session?.user?.id) return

      // Clean up existing channel if any
      if (channel) {
        supabase.removeChannel(channel)
        channel = null
      }

      try {
        channel = supabase
          .channel(`notifications-${session.user.id}`, {
            config: {
              broadcast: { self: false },
              presence: { key: session.user.id }
            }
          })
          .on(
            'postgres_changes',
            {
              event: 'INSERT',
              schema: 'public',
              table: 'notifications',
              filter: `user_id=eq.${session.user.id}`
            },
            (payload) => {
              setState(prev => ({
                ...prev,
                data: [payload.new as Notification, ...prev.data]
              }))
            }
          )
          .on(
            'postgres_changes',
            {
              event: 'UPDATE',
              schema: 'public',
              table: 'notifications',
              filter: `user_id=eq.${session.user.id}`
            },
            (payload) => {
              setState(prev => ({
                ...prev,
                data: prev.data.map(notification =>
                  notification.id === (payload.new as Notification).id
                    ? payload.new as Notification
                    : notification
                )
              }))
            }
          )
          .on(
            'postgres_changes',
            {
              event: 'DELETE',
              schema: 'public',
              table: 'notifications',
              filter: `user_id=eq.${session.user.id}`
            },
            (payload) => {
              setState(prev => ({
                ...prev,
                data: prev.data.filter(notification => notification.id !== payload.old.id)
              }))
            }
          )
          .subscribe((status) => {
            if (status === 'SUBSCRIBED') {
              isSubscribed = true
              reconnectAttempts = 0 // Reset on successful subscription
              lastErrorTime = 0
            } else if (status === 'CHANNEL_ERROR') {
              isSubscribed = false
              // Only log if it's been more than 10 seconds since last error (avoid spam)
              const now = Date.now()
              if (now - lastErrorTime > 10000) {
                console.warn('Notifications real-time channel error. Will retry automatically.')
                lastErrorTime = now
              }
              if (reconnectAttempts < MAX_RECONNECT_ATTEMPTS) {
                attemptReconnect()
              } else {
                console.warn('Max reconnection attempts reached. Real-time notifications disabled. Polling will continue.')
              }
            } else if (status === 'CLOSED') {
              isSubscribed = false
              // Only attempt reconnect if it wasn't intentionally closed
              if (reconnectAttempts < MAX_RECONNECT_ATTEMPTS) {
                attemptReconnect()
              }
            } else if (status === 'TIMED_OUT') {
              isSubscribed = false
              if (reconnectAttempts < MAX_RECONNECT_ATTEMPTS) {
                attemptReconnect()
              }
            }
          })
      } catch (error) {
        console.error('Failed to set up notifications channel:', error)
        isSubscribed = false
        if (reconnectAttempts < MAX_RECONNECT_ATTEMPTS) {
          attemptReconnect()
        }
      }
    }

    const attemptReconnect = () => {
      if (reconnectTimeout) return

      reconnectAttempts++
      // Exponential backoff: 2s, 4s, 8s, 16s, 30s (max)
      const delay = Math.min(
        INITIAL_RECONNECT_DELAY * Math.pow(2, reconnectAttempts - 1),
        MAX_RECONNECT_DELAY
      )

      reconnectTimeout = setTimeout(() => {
        reconnectTimeout = null
        const supabase = supabaseBrowserClient()
        if (!supabase) return

        subscribe()
      }, delay)
    }

    subscribe()

    return () => {
      if (channel && currentSupabase) {
        currentSupabase.removeChannel(channel)
        channel = null
      }
      if (reconnectTimeout) {
        clearTimeout(reconnectTimeout)
        reconnectTimeout = null
      }
      isSubscribed = false
    }
  }, [])

  // Initial fetch - only run once on mount
  useEffect(() => {
    fetchNotifications()
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []) // Intentionally empty - only fetch once on mount

  return {
    notifications: state.data,
    unreadCount: state.data.filter(n => !n.read).length,
    isLoading: state.isLoading,
    error: state.error,
    markAsRead,
    markAllAsRead,
    deleteNotification,
    refresh: fetchNotifications
  }
}

import { useState, useCallback } from 'react'
import { telemetry } from '@/lib/telemetry/resilienceTelemetry'

interface UseOptimisticActionWithTelemetryOptions<T, P = void> {
  action: (params: P) => Promise<T>
  onOptimistic?: (params: P) => void
  onSuccess?: (result: T, params: P) => void
  onError?: (error: Error, params: P) => void
  onRollback?: (params: P) => void
  component: string // Required for telemetry
}

interface UseOptimisticActionWithTelemetryReturn<T, P> {
  execute: (params: P) => Promise<T | null>
  isLoading: boolean
  error: Error | null
  status: 'idle' | 'optimistic' | 'loading' | 'success' | 'error'
  rollback: (params: P) => void
}

export function useOptimisticActionWithTelemetry<T, P = void>({
  action,
  onOptimistic,
  onSuccess,
  onError,
  onRollback,
  component
}: UseOptimisticActionWithTelemetryOptions<T, P>): UseOptimisticActionWithTelemetryReturn<T, P> {
  const [status, setStatus] = useState<'idle' | 'optimistic' | 'loading' | 'success' | 'error'>('idle')
  const [error, setError] = useState<Error | null>(null)
  const [_actionStartTime, setActionStartTime] = useState<number>(0)

  const execute = useCallback(async (params: P): Promise<T | null> => {
    const actionId = `${component}_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`
    const startTime = Date.now()
    
    try {
      // 1. OPTIMISTIC UPDATE
      setStatus('optimistic')
      setActionStartTime(startTime)
      
      // Track optimistic action start
      telemetry.optimisticActionStart(component, actionId)
      
      onOptimistic?.(params)

      // 2. EXECUTE ACTION
      setStatus('loading')
      const result = await action(params)

      // 3. SUCCESS
      const duration = Date.now() - startTime
      setStatus('success')
      setError(null)
      
      // Track successful action
      telemetry.optimisticActionSuccess(component, actionId, duration)
      
      onSuccess?.(result, params)
      return result

    } catch (err) {
      // 4. ERROR - ROLLBACK
      const duration = Date.now() - startTime
      const error = err instanceof Error ? err : new Error('Action failed')
      
      setStatus('error')
      setError(error)
      
      // Track rollback with reason
      const rollbackReason = error.message || 'Unknown error'
      telemetry.optimisticActionRollback(component, actionId, duration, rollbackReason)
      
      onError?.(error, params)
      onRollback?.(params)
      return null
    }
  }, [action, onOptimistic, onSuccess, onError, onRollback, component])

  const rollback = useCallback((params: P) => {
    setStatus('idle')
    setError(null)
    onRollback?.(params)
  }, [onRollback])

  return {
    execute,
    isLoading: status === 'loading',
    error,
    status,
    rollback
  }
}

'use client'

import { useState, useEffect, useCallback } from 'react'
import { pushNotificationManager } from '@/lib/notifications/pushNotifications'

export interface PushSubscriptionState {
  isSupported: boolean
  isSubscribed: boolean
  isSubscribing: boolean
  error: string | null
}

/**
 * Hook for managing Web Push subscriptions
 */
export function usePushSubscription() {
  const [state, setState] = useState<PushSubscriptionState>({
    isSupported: false,
    isSubscribed: false,
    isSubscribing: false,
    error: null
  })

  useEffect(() => {
    // Check if push is supported
    const isSupported = 'serviceWorker' in navigator && 'PushManager' in window
    setState(prev => ({ ...prev, isSupported }))

    if (isSupported) {
      checkSubscriptionStatus()
    }
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [])

  const checkSubscriptionStatus = useCallback(async () => {
    try {
      if ('serviceWorker' in navigator) {
        const registration = await navigator.serviceWorker.ready
        const subscription = await registration.pushManager.getSubscription()
        setState(prev => ({ ...prev, isSubscribed: !!subscription }))
      }
    } catch (error) {
      console.error('Error checking subscription status:', error)
      setState(prev => ({ ...prev, error: 'Failed to check subscription status' }))
    }
  }, [])

  const subscribe = useCallback(async () => {
    if (!state.isSupported) {
      setState(prev => ({ ...prev, error: 'Push notifications are not supported' }))
      return false
    }

    setState(prev => ({ ...prev, isSubscribing: true, error: null }))

    try {
      // Get VAPID public key
      const vapidResponse = await fetch('/api/notifications/push/vapid-public-key')
      const { publicKey } = await vapidResponse.json()

      if (!publicKey) {
        throw new Error('VAPID public key not available')
      }

      // Register service worker
      const registration = await navigator.serviceWorker.ready

      // Subscribe to push
      const keyArray = urlBase64ToUint8Array(publicKey)
      const subscription = await registration.pushManager.subscribe({
        userVisibleOnly: true,
        applicationServerKey: keyArray as BufferSource
      })

      const p256dhKey = subscription.getKey('p256dh')
      const authKey = subscription.getKey('auth')

      if (!p256dhKey || !authKey) {
        throw new Error('Push subscription keys missing')
      }

      // Send subscription to server
      const response = await fetch('/api/notifications/push/subscribe', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          endpoint: subscription.endpoint,
          keys: {
            p256dh: uint8ArrayToBase64(new Uint8Array(p256dhKey)),
            auth: uint8ArrayToBase64(new Uint8Array(authKey))
          }
        })
      })

      if (!response.ok) {
        throw new Error('Failed to save subscription')
      }

      setState(prev => ({ ...prev, isSubscribed: true, isSubscribing: false }))
      return true
    } catch (error) {
      console.error('Error subscribing to push:', error)
      setState(prev => ({
        ...prev,
        error: error instanceof Error ? error.message : 'Failed to subscribe',
        isSubscribing: false
      }))
      return false
    }
  }, [state.isSupported])

  const unsubscribe = useCallback(async () => {
    setState(prev => ({ ...prev, isSubscribing: true, error: null }))

    try {
      const registration = await navigator.serviceWorker.ready
      const subscription = await registration.pushManager.getSubscription()

      if (subscription) {
        // Delete from server
        await fetch('/api/notifications/push/subscribe', {
          method: 'DELETE',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ endpoint: subscription.endpoint })
        })

        // Unsubscribe from push
        await subscription.unsubscribe()
      }

      setState(prev => ({ ...prev, isSubscribed: false, isSubscribing: false }))
      return true
    } catch (error) {
      console.error('Error unsubscribing from push:', error)
      setState(prev => ({
        ...prev,
        error: error instanceof Error ? error.message : 'Failed to unsubscribe',
        isSubscribing: false
      }))
      return false
    }
  }, [])

  return {
    ...state,
    subscribe,
    unsubscribe,
    refresh: checkSubscriptionStatus
  }
}

// Helper functions for VAPID key conversion
function urlBase64ToUint8Array(base64String: string): Uint8Array {
  const padding = '='.repeat((4 - (base64String.length % 4)) % 4)
  const base64 = (base64String + padding).replace(/-/g, '+').replace(/_/g, '/')
  const rawData = window.atob(base64)
  const outputArray = new Uint8Array(rawData.length)

  for (let i = 0; i < rawData.length; ++i) {
    outputArray[i] = rawData.charCodeAt(i)
  }
  return outputArray
}

function uint8ArrayToBase64(array: Uint8Array): string {
  const bytes = new Uint8Array(array)
  let binary = ''
  for (let i = 0; i < bytes.byteLength; i++) {
    binary += String.fromCharCode(bytes[i])
  }
  return window.btoa(binary)
}

import { useState, useEffect } from 'react'
import { createClient } from '@supabase/supabase-js'
import { getSupabaseConfig } from '@/config/supabase'

interface ShoutOutData {
  date: string
  count: number
  revenue: number
}

interface UseShoutOutTimeseriesReturn {
  data: ShoutOutData[]
  loading: boolean
  error: string | null
  refetch: () => void
}

export function useShoutOutTimeseries(timeRange: '7d' | '30d' | '90d' = '7d'): UseShoutOutTimeseriesReturn {
  const [data, setData] = useState<ShoutOutData[]>([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)

  const fetchData = async () => {
    try {
      setLoading(true)
      setError(null)

      const config = getSupabaseConfig()
      if (!config?.url || !config?.publishableKey) {
        throw new Error('Invalid Supabase configuration')
      }

      const _supabase = createClient(config.url, config.publishableKey)
      
      // Calculate date range
      const now = new Date()
      const startDate = new Date()
      switch (timeRange) {
        case '7d':
          startDate.setDate(now.getDate() - 7)
          break
        case '30d':
          startDate.setDate(now.getDate() - 30)
          break
        case '90d':
          startDate.setDate(now.getDate() - 90)
          break
      }

      // Mock data for now - replace with actual query when shout_out table exists
      const mockData: ShoutOutData[] = []
      const days = Math.ceil((now.getTime() - startDate.getTime()) / (1000 * 60 * 60 * 24))
      
      for (let i = 0; i < days; i++) {
        const date = new Date(startDate)
        date.setDate(date.getDate() + i)
        mockData.push({
          date: date.toISOString().split('T')[0],
          count: Math.floor(Math.random() * 10) + 1,
          revenue: Math.floor(Math.random() * 100) + 10,
        })
      }

      setData(mockData)
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to fetch shout out data')
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    fetchData()
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [timeRange])

  return {
    data,
    loading,
    error,
    refetch: fetchData,
  }
}

import OpenAI from 'openai'

// Configuration
const OPENAI_API_KEY = process.env.OPENAI_API_KEY
const MODERATION_ENABLED = process.env.AI_MODERATION_ENABLED === 'true'
const CONFIDENCE_THRESHOLD = 0.7

// Initialize OpenAI client
const openai = OPENAI_API_KEY ? new OpenAI({ apiKey: OPENAI_API_KEY }) : null

export interface ModerationResult {
  isFlagged: boolean
  confidence: number
  categories: {
    spam: boolean
    inappropriate: boolean
    fake: boolean
    harassment: boolean
    other: boolean
  }
  reasons: string[]
  suggestedAction: 'approve' | 'flag' | 'reject' | 'review'
  score: number
}

export interface ReviewData {
  text: string
  rating: number
  reviewerId: string
  providerId: string
  reviewerHistory: {
    totalReviews: number
    avgRating: number
    recentReviews: number
    suspiciousPatterns: string[]
  }
}

// Keyword-based spam detection patterns
const SPAM_PATTERNS = {
  financial: [
    'free money', 'cash', 'earn', 'profit', 'investment', 'opportunity',
    'work from home', 'make money', 'get rich', 'financial freedom',
    'bitcoin', 'crypto', 'forex', 'trading', 'mlm', 'pyramid scheme'
  ],
  promotional: [
    'buy now', 'limited time', 'special offer', 'discount', 'sale',
    'click here', 'visit website', 'call now', 'text me', 'dm me'
  ],
  suspicious: [
    'urgent', 'act now', 'don\'t miss out', 'exclusive', 'secret',
    'guaranteed', '100%', 'instant', 'quick', 'easy money'
  ],
  inappropriate: [
    'hate', 'racist', 'sexist', 'discriminatory', 'violent',
    'threat', 'harassment', 'bullying', 'abuse'
  ]
}

// Behavioral pattern detection
const BEHAVIORAL_FLAGS = {
  excessiveReviews: 10, // More than 10 reviews in short time
  ratingExtremity: 0.8, // All 5-star or all 1-star reviews
  shortTextThreshold: 10, // Very short review text
  repetitivePatterns: 0.3, // High repetition in text
  suspiciousTiming: 24 * 60 * 60 * 1000 // Multiple reviews within 24 hours
}

export class AIModerationService {
  private static instance: AIModerationService

  static getInstance(): AIModerationService {
    if (!AIModerationService.instance) {
      AIModerationService.instance = new AIModerationService()
    }
    return AIModerationService.instance
  }

  /**
   * Comprehensive review moderation combining keyword detection and AI analysis
   */
  async moderateReview(reviewData: ReviewData): Promise<ModerationResult> {
    try {
      // Step 1: Basic keyword and pattern detection
      const keywordResult = this.detectKeywordPatterns(reviewData.text)
      
      // Step 2: Behavioral analysis
      const behavioralResult = this.analyzeBehavioralPatterns(reviewData)
      
      // Step 3: AI-powered content analysis (if enabled and available)
      let aiResult: Partial<ModerationResult> = {}
      if (MODERATION_ENABLED && openai) {
        try {
          aiResult = await this.analyzeWithAI(reviewData.text)
        } catch (error) {
          console.warn('AI moderation failed, falling back to keyword detection:', error)
        }
      }

      // Step 4: Combine all results for final decision
      return this.combineModerationResults(keywordResult, behavioralResult, aiResult)
    } catch (error) {
      console.error('Moderation failed:', error)
      // Fallback to basic keyword detection
      return this.detectKeywordPatterns(reviewData.text)
    }
  }

  /**
   * Keyword-based pattern detection
   */
  private detectKeywordPatterns(text: string): ModerationResult {
    const lowerText = text.toLowerCase()
    const reasons: string[] = []
    let score = 0
    const flaggedCategories = {
      spam: false,
      inappropriate: false,
      fake: false,
      harassment: false,
      other: false
    }

    // Check for spam patterns
    for (const category of Object.keys(SPAM_PATTERNS) as Array<keyof typeof SPAM_PATTERNS>) {
      for (const pattern of SPAM_PATTERNS[category]) {
        if (lowerText.includes(pattern)) {
          reasons.push(`Detected ${category} pattern: "${pattern}"`)
          score += 0.3
          
          if (category === 'financial' || category === 'promotional') {
            flaggedCategories.spam = true
          } else if (category === 'inappropriate') {
            flaggedCategories.inappropriate = true
          }
        }
      }
    }

    // Check for excessive capitalization
    if (text.length > 0) {
      const capsRatio = (text.length - text.toLowerCase().length) / text.length
      if (capsRatio > 0.3) {
        reasons.push('Excessive capitalization detected')
        score += 0.2
        flaggedCategories.spam = true
      }
    }

    // Check for repeated characters
    if (/([a-zA-Z])\1{2,}/.test(text)) {
      reasons.push('Repeated characters detected')
      score += 0.15
      flaggedCategories.spam = true
    }

    // Check for suspicious rating patterns
    if (text.length < BEHAVIORAL_FLAGS.shortTextThreshold) {
      reasons.push('Very short review text')
      score += 0.1
      flaggedCategories.fake = true
    }

    const confidence = Math.min(score, 1.0)
    const isFlagged = confidence > CONFIDENCE_THRESHOLD

    return {
      isFlagged,
      confidence,
      categories: flaggedCategories,
      reasons,
      suggestedAction: this.suggestAction(confidence, flaggedCategories),
      score: confidence
    }
  }

  /**
   * Analyze behavioral patterns of the reviewer
   */
  private analyzeBehavioralPatterns(reviewData: ReviewData): Partial<ModerationResult> {
    const reasons: string[] = []
    let score = 0
    const flaggedCategories = {
      spam: false,
      inappropriate: false,
      fake: false,
      harassment: false,
      other: false
    }

    const { totalReviews, avgRating, recentReviews, suspiciousPatterns } = reviewData.reviewerHistory

    // Multiple reviews in short time
    if (recentReviews > BEHAVIORAL_FLAGS.excessiveReviews) {
      reasons.push(`Excessive recent reviews: ${recentReviews}`)
      score += 0.25
      flaggedCategories.spam = true
    }

    // Suspicious rating patterns
    if (totalReviews > 5) {
      if (avgRating === 5.0) {
        reasons.push('All 5-star reviews from this user')
        score += 0.3
        flaggedCategories.fake = true
      } else if (avgRating === 1.0) {
        reasons.push('All 1-star reviews from this user')
        score += 0.2
        flaggedCategories.fake = true
      }
    }

    // Check for suspicious patterns in reviewer history
    if (suspiciousPatterns.length > 0) {
      reasons.push(`Suspicious patterns in user history: ${suspiciousPatterns.join(', ')}`)
      score += 0.2
      flaggedCategories.other = true
    }

    return {
      confidence: Math.min(score, 1.0),
      reasons,
      categories: flaggedCategories
    }
  }

  /**
   * AI-powered content analysis using OpenAI
   */
  private async analyzeWithAI(text: string): Promise<Partial<ModerationResult>> {
    if (!openai) {
      throw new Error('OpenAI client not available')
    }

    try {
      // Use OpenAI's moderation API
      const moderation = await openai.moderations.create({
        input: text
      })

      const result = moderation.results[0]
      const categories = result.categories
      const categoryScores = result.category_scores

      const reasons: string[] = []
      let score = 0
      const flaggedCategories = {
        spam: false,
        inappropriate: false,
        fake: false,
        harassment: false,
        other: false
      }

      // Map OpenAI categories to our categories
      if (categories.harassment || categories.hate || categories['self-harm']) {
        flaggedCategories.harassment = true
        reasons.push('Content flagged as harassment or hate speech')
        score += Math.max(categoryScores.harassment || 0, categoryScores.hate || 0, categoryScores['self-harm'] || 0)
      }

      if (categories.sexual || categories.violence) {
        flaggedCategories.inappropriate = true
        reasons.push('Content flagged as inappropriate')
        score += Math.max(categoryScores.sexual || 0, categoryScores.violence || 0)
      }

      if (categories['self-harm']) {
        flaggedCategories.inappropriate = true
        reasons.push('Content flagged as concerning')
        score += categoryScores['self-harm'] || 0
      }

      // Check for potential spam indicators
      const spamIndicators = ['spam', 'fraud', 'deception']
      let spamScore = 0
      for (const indicator of spamIndicators) {
        if (categoryScores[indicator as keyof typeof categoryScores]) {
          spamScore = Math.max(spamScore, categoryScores[indicator as keyof typeof categoryScores] || 0)
        }
      }

      if (spamScore > 0.5) {
        flaggedCategories.spam = true
        reasons.push('AI detected potential spam content')
        score += spamScore
      }

      return {
        confidence: Math.min(score, 1.0),
        reasons,
        categories: flaggedCategories
      }
    } catch (error) {
      console.error('OpenAI moderation failed:', error)
      throw error
    }
  }

  /**
   * Combine all moderation results for final decision
   */
  private combineModerationResults(
    keywordResult: ModerationResult,
    behavioralResult: Partial<ModerationResult>,
    aiResult: Partial<ModerationResult>
  ): ModerationResult {
    // Combine scores with weights
    let finalScore = keywordResult.score * 0.4 // 40% weight to keyword detection
    
    if (behavioralResult.confidence) {
      finalScore += behavioralResult.confidence * 0.3 // 30% weight to behavioral analysis
    }
    
    if (aiResult.confidence) {
      finalScore += aiResult.confidence * 0.3 // 30% weight to AI analysis
    }

    // Combine all reasons
    const allReasons = [
      ...keywordResult.reasons,
      ...(behavioralResult.reasons || []),
      ...(aiResult.reasons || [])
    ]

    // Combine categories (if any category is flagged, mark it as flagged)
    const combinedCategories = {
      spam: keywordResult.categories.spam || behavioralResult.categories?.spam || aiResult.categories?.spam || false,
      inappropriate: keywordResult.categories.inappropriate || behavioralResult.categories?.inappropriate || aiResult.categories?.inappropriate || false,
      fake: keywordResult.categories.fake || behavioralResult.categories?.fake || aiResult.categories?.fake || false,
      harassment: keywordResult.categories.harassment || behavioralResult.categories?.harassment || aiResult.categories?.harassment || false,
      other: keywordResult.categories.other || behavioralResult.categories?.other || aiResult.categories?.other || false
    }

    const finalConfidence = Math.min(finalScore, 1.0)
    const isFlagged = finalConfidence > CONFIDENCE_THRESHOLD

    return {
      isFlagged,
      confidence: finalConfidence,
      categories: combinedCategories,
      reasons: allReasons,
      suggestedAction: this.suggestAction(finalConfidence, combinedCategories),
      score: finalConfidence
    }
  }

  /**
   * Suggest moderation action based on confidence and categories
   */
  private suggestAction(confidence: number, _categories: ModerationResult['categories']): ModerationResult['suggestedAction'] {
    if (confidence > 0.9) {
      return 'reject'
    } else if (confidence > 0.7) {
      return 'flag'
    } else if (confidence > 0.5) {
      return 'review'
    } else {
      return 'approve'
    }
  }

  /**
   * Get moderation statistics for monitoring
   */
  getModerationStats(): {
    enabled: boolean
    hasOpenAI: boolean
    confidenceThreshold: number
    patterns: Record<string, string[]>
  } {
    return {
      enabled: MODERATION_ENABLED,
      hasOpenAI: !!openai,
      confidenceThreshold: CONFIDENCE_THRESHOLD,
      patterns: SPAM_PATTERNS
    }
  }
}

