    } else {
      checks.push(...anomalies.flatMap(a => a.recommended_checks))
    }

    return checks
  }

  private static groupByEndpoint(metrics: P95Metrics[]): Map<string, P95Metrics[]> {
    const groups = new Map<string, P95Metrics[]>()
    for (const metric of metrics) {
      const key = `${metric.method} ${metric.endpoint}`
      if (!groups.has(key)) {
        groups.set(key, [])
      }
      groups.get(key)!.push(metric)
    }
    return groups
  }

  private static identifySlowEndpoints(
    groups: Map<string, P95Metrics[]>
  ): Array<{ endpoint: string; method: string; avgP95: number; requestCount: number }> {
    const slow: Array<{ endpoint: string; method: string; avgP95: number; requestCount: number }> = []

    for (const [key, metrics] of groups.entries()) {
      const [method, ...endpointParts] = key.split(' ')
      const endpoint = endpointParts.join(' ')
      const avgP95 = this.calculateAverage(metrics, 'p95_latency_ms')
      const requestCount = metrics.reduce((sum, m) => sum + m.request_count, 0)

      if (avgP95 > 500) { // SLO threshold
        slow.push({ endpoint, method, avgP95, requestCount })
      }
    }

    return slow.sort((a, b) => b.avgP95 - a.avgP95)
  }

  private static detectLatencyTrends(
    current: P95Metrics[],
    previous?: P95Metrics[]
  ): MetricsSummary['trends'] {
    if (!previous || previous.length === 0) {
      return {
        direction: 'stable',
        description: 'No historical data for trend comparison.',
        evidence: []
      }
    }

    const currentAvgP95 = this.calculateAverage(current, 'p95_latency_ms')
    const previousAvgP95 = this.calculateAverage(previous, 'p95_latency_ms')
    const changePercent = previousAvgP95 > 0 
      ? ((currentAvgP95 - previousAvgP95) / previousAvgP95) * 100 
      : 0

    const evidence: string[] = []
    if (Math.abs(changePercent) > 10) {
      evidence.push(`P95 latency ${changePercent > 0 ? 'increased' : 'decreased'} by ${Math.abs(changePercent).toFixed(1)}%`)
      evidence.push(`Current: ${currentAvgP95.toFixed(0)}ms, Previous: ${previousAvgP95.toFixed(0)}ms`)
    }

    if (changePercent > 20) {
      return {
        direction: 'degrading',
        description: 'P95 latency is significantly increasing.',
        evidence
      }
    } else if (changePercent < -20) {
      return {
        direction: 'improving',
        description: 'P95 latency is significantly improving.',
        evidence
      }
    }

    return {
      direction: 'stable',
      description: 'P95 latency is relatively stable.',
      evidence: evidence.length > 0 ? evidence : ['No significant changes detected']
    }
  }

  private static detectLatencyAnomalies(
    metrics: P95Metrics[],
    avgP95: number,
    maxP95: number,
    slowEndpoints: Array<{ endpoint: string; method: string; avgP95: number; requestCount: number }>
  ): MetricsSummary['anomalies'] {
    const anomalies: MetricsSummary['anomalies'] = []

    if (maxP95 > 1000) {
      anomalies.push({
        severity: 'critical',
        type: 'High P95 Latency',
        description: `Maximum P95 latency is ${maxP95.toFixed(0)}ms, exceeding 1s threshold.`,
        evidence: [
          `Max P95: ${maxP95.toFixed(0)}ms`,
          `Average P95: ${avgP95.toFixed(0)}ms`
        ],
        recommended_checks: [
          'Identify slow endpoints',
          'Review database query performance',
          'Check for N+1 query patterns',
          'Investigate cache effectiveness'
        ]
      })
    } else if (avgP95 > 500) {
      anomalies.push({
        severity: 'high',
        type: 'Elevated P95 Latency',
        description: `Average P95 latency is ${avgP95.toFixed(0)}ms, above 500ms SLO threshold.`,
        evidence: [
          `Average P95: ${avgP95.toFixed(0)}ms`
        ],
        recommended_checks: [
          'Review slow endpoints',
          'Check database connection pool',
          'Investigate external API dependencies'
        ]
      })
    }

    if (slowEndpoints.length > 0) {
      const topSlow = slowEndpoints.slice(0, 3)
      anomalies.push({
        severity: 'medium',
        type: 'Slow Endpoints Detected',
        description: `${slowEndpoints.length} endpoint(s) exceed 500ms P95 threshold.`,
        evidence: topSlow.map(e => `${e.method} ${e.endpoint}: ${e.avgP95.toFixed(0)}ms (${e.requestCount} requests)`),
        recommended_checks: [
          'Profile slow endpoint handlers',
          'Review database queries for these endpoints',
          'Check for missing indexes',
          'Consider adding caching'
        ]
      })
    }

    return anomalies
  }

  private static generateP95Summary(
    avgP95: number,
    avgP99: number,
    maxP95: number,
    totalRequests: number,
    slowEndpoints: Array<{ endpoint: string; method: string; avgP95: number; requestCount: number }>
  ): string {
    const parts: string[] = []
    parts.push(`P95 latency analysis: Average P95: ${avgP95.toFixed(0)}ms, Average P99: ${avgP99.toFixed(0)}ms, Max P95: ${maxP95.toFixed(0)}ms.`)
    parts.push(`Total requests analyzed: ${totalRequests.toLocaleString()}.`)
    if (slowEndpoints.length > 0) {
      parts.push(`${slowEndpoints.length} endpoint(s) exceed 500ms threshold.`)
    }
    return parts.join(' ')
  }

  private static generateLatencyChecks(
    anomalies: MetricsSummary['anomalies'],
    slowEndpoints: Array<{ endpoint: string; method: string; avgP95: number; requestCount: number }>,
    _avgP95: number
  ): string[] {
    const checks: string[] = []

    if (anomalies.length === 0) {
      checks.push('P95 latency metrics appear healthy')
      checks.push('Continue monitoring for trends')
    } else {
      checks.push(...anomalies.flatMap(a => a.recommended_checks))
    }

    if (slowEndpoints.length > 0) {
      checks.push(`Review ${slowEndpoints.length} slow endpoint(s)`)
    }

    return checks
  }

  private static aggregateTopErrorEndpoints(
    metrics: ErrorMetrics[]
  ): Array<{ endpoint: string; method: string; error_count: number; error_rate_percent: number }> {
    const endpointMap = new Map<string, { count: number; total: number }>()

    for (const metric of metrics) {
      for (const endpoint of metric.top_error_endpoints) {
        const key = `${endpoint.method} ${endpoint.endpoint}`
        if (!endpointMap.has(key)) {
          endpointMap.set(key, { count: 0, total: 0 })
        }
        const entry = endpointMap.get(key)!
        entry.count += endpoint.error_count
        entry.total += endpoint.error_count / (endpoint.error_rate_percent / 100) // Estimate total requests
      }
    }

    const aggregated: Array<{ endpoint: string; method: string; error_count: number; error_rate_percent: number }> = []
    for (const [key, stats] of endpointMap.entries()) {
      const [method, ...endpointParts] = key.split(' ')
      const endpoint = endpointParts.join(' ')
      const errorRate = stats.total > 0 ? (stats.count / stats.total) * 100 : 0
      aggregated.push({
        endpoint,
        method,
        error_count: stats.count,
        error_rate_percent: errorRate
      })
    }

    return aggregated.sort((a, b) => b.error_count - a.error_count).slice(0, 10)
  }

  private static detectErrorTrends(
    current: ErrorMetrics[],
    previous?: ErrorMetrics[]
  ): MetricsSummary['trends'] {
    if (!previous || previous.length === 0) {
      return {
        direction: 'stable',
        description: 'No historical data for trend comparison.',
        evidence: []
      }
    }

    const currentAvgRate = this.calculateAverage(current, 'error_rate_percent')
    const previousAvgRate = this.calculateAverage(previous, 'error_rate_percent')
    const change = currentAvgRate - previousAvgRate

    const evidence: string[] = []
    if (Math.abs(change) > 1) {
      evidence.push(`Error rate ${change > 0 ? 'increased' : 'decreased'} by ${Math.abs(change).toFixed(2)}%`)
      evidence.push(`Current: ${currentAvgRate.toFixed(2)}%, Previous: ${previousAvgRate.toFixed(2)}%`)
    }

    if (change > 2) {
      return {
        direction: 'degrading',
        description: 'Error rate is significantly increasing.',
        evidence
      }
    } else if (change < -2) {
      return {
        direction: 'improving',
        description: 'Error rate is significantly improving.',
        evidence
      }
    }

    return {
      direction: 'stable',
      description: 'Error rate is relatively stable.',
      evidence: evidence.length > 0 ? evidence : ['No significant changes detected']
    }
  }

  private static detectErrorAnomalies(
    metrics: ErrorMetrics[],
    avgErrorRate: number,
    total5xx: number,
    topErrors: Array<{ endpoint: string; method: string; error_count: number; error_rate_percent: number }>
  ): MetricsSummary['anomalies'] {
    const anomalies: MetricsSummary['anomalies'] = []

    if (avgErrorRate > 5) {
      anomalies.push({
        severity: 'critical',
        type: 'High Error Rate',
        description: `Error rate is ${avgErrorRate.toFixed(2)}%, exceeding 5% threshold.`,
        evidence: [
          `Average error rate: ${avgErrorRate.toFixed(2)}%`
        ],
        recommended_checks: [
          'Review error logs for patterns',
          'Check for service degradation',
          'Investigate recent deployments',
          'Review database connection issues'
        ]
      })
    } else if (avgErrorRate > 2) {
      anomalies.push({
        severity: 'high',
        type: 'Elevated Error Rate',
        description: `Error rate is ${avgErrorRate.toFixed(2)}%, above 2% threshold.`,
        evidence: [
          `Average error rate: ${avgErrorRate.toFixed(2)}%`
        ],
        recommended_checks: [
          'Monitor error trends',
          'Review top error endpoints',
          'Check for intermittent issues'
        ]
      })
    }

    if (total5xx > 10) {
      anomalies.push({
        severity: 'critical',
        type: 'High 5xx Server Errors',
        description: `${total5xx} server errors detected, indicating system issues.`,
        evidence: [
          `Total 5xx errors: ${total5xx}`
        ],
        recommended_checks: [
          'Check application logs for stack traces',
          'Review database connection pool',
          'Investigate external service dependencies',
          'Check for resource exhaustion'
        ]
      })
    }

    if (topErrors.length > 0 && topErrors[0].error_rate_percent > 10) {
      anomalies.push({
        severity: 'high',
        type: 'Endpoint with High Error Rate',
        description: `${topErrors[0].method} ${topErrors[0].endpoint} has ${topErrors[0].error_rate_percent.toFixed(1)}% error rate.`,
        evidence: [
          `${topErrors[0].method} ${topErrors[0].endpoint}: ${topErrors[0].error_count} errors (${topErrors[0].error_rate_percent.toFixed(1)}%)`
        ],
        recommended_checks: [
          'Review endpoint implementation',
          'Check input validation',
          'Investigate database queries',
          'Review error handling logic'
        ]
      })
    }

    return anomalies
  }

  private static generateErrorSummary(
    totalErrors: number,
    avgErrorRate: number,
    total4xx: number,
    total5xx: number,
    topErrors: Array<{ endpoint: string; method: string; error_count: number; error_rate_percent: number }>
  ): string {
    const parts: string[] = []
    parts.push(`Error analysis: ${totalErrors} total errors detected, ${avgErrorRate.toFixed(2)}% average error rate.`)
    parts.push(`${total4xx} client errors (4xx), ${total5xx} server errors (5xx).`)
    if (topErrors.length > 0) {
      parts.push(`Top error endpoint: ${topErrors[0].method} ${topErrors[0].endpoint} (${topErrors[0].error_count} errors, ${topErrors[0].error_rate_percent.toFixed(1)}%).`)
    }
    return parts.join(' ')
  }

  private static generateErrorChecks(
    anomalies: MetricsSummary['anomalies'],
    avgErrorRate: number,
    total5xx: number,
    topErrors: Array<{ endpoint: string; method: string; error_count: number; error_rate_percent: number }>
  ): string[] {
    const checks: string[] = []

    if (anomalies.length === 0) {
      checks.push('Error metrics appear healthy')
      checks.push('Continue monitoring error rates')
    } else {
      checks.push(...anomalies.flatMap(a => a.recommended_checks))
    }

    if (topErrors.length > 0) {
      checks.push(`Review top ${Math.min(3, topErrors.length)} error endpoint(s)`)
    }

    return checks
  }
}

import { createClient } from '@supabase/supabase-js'
import { sendEmail, sendSMS, sendPushNotification } from './providers'

const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL!
const supabaseServiceKey = process.env.SUPABASE_SERVICE_ROLE_KEY!

// Use service role to bypass RLS for preference checks
const supabase = createClient(supabaseUrl, supabaseServiceKey)

export interface NotificationResult {
  type: 'email' | 'sms' | 'push'
  success: boolean
  error?: string
}

/**
 * Unified Notification Center
 * Dispatches notifications based on user preferences
 */
export async function notifyUser(
  userId: string, 
  template: string, 
  data: Record<string, unknown>
): Promise<NotificationResult[]> {
  
  console.log(`ðŸ”” Notification Center: Sending '${template}' to user ${userId}`)

  // 1. Get preferences
  const { data: prefs, error: prefError } = await supabase
    .from('notification_preferences')
    .select('*')
    .eq('user_id', userId)
    .single()

  // Default to email enabled if no preferences set (safe default)
  const preferences = prefs || { 
    email_enabled: true, 
    sms_enabled: false, 
    push_enabled: false,
    phone_number: null 
  }

  if (prefError && prefError.code !== 'PGRST116') {
    console.warn('Error fetching preferences, using defaults:', prefError)
  }

  // 2. Get user contact info from Auth
  const { data: userData, error: userError } = await supabase.auth.admin.getUserById(userId)
  
  if (userError || !userData.user) {
    console.error('User not found for notification:', userId)
    return [{ type: 'email', success: false, error: 'User not found' }]
  }

  const email = userData.user.email
  const phone = preferences.phone_number || userData.user.phone 
  
  const results: NotificationResult[] = []

  // 3. Send Email
  if (preferences.email_enabled && email) {
    const res = await sendEmail(email, template, data)
    results.push({ type: 'email', ...res })
    await logNotification(userId, 'email', email, template, res)
  }

  // 4. Send SMS
  if (preferences.sms_enabled && phone) {
    const res = await sendSMS(phone, template, data)
    results.push({ type: 'sms', ...res })
    await logNotification(userId, 'sms', phone, template, res)
  }

  // 5. Send Push Notification (if enabled and user has subscription)
  if (preferences.push_enabled) {
    // Check if user has push subscriptions
    const { data: subscriptions } = await supabase
      .from('push_subscriptions')
      .select('*')
      .eq('user_id', userId)
      .limit(1)

    if (subscriptions && subscriptions.length > 0) {
      // Check if batching is enabled (from preferences or default)
      const shouldBatch = true // TODO: Add batching preference to notification_preferences table
      
      if (shouldBatch) {
        // Queue for batching
        const { queueNotification } = await import('./batching')
        await queueNotification(userId, {
          title: formatTitle(template, data),
          body: formatMessage(template, data),
          type: template,
          data: data
        })
      } else {
        // Send immediately
        const res = await sendPushNotification(subscriptions[0].endpoint, template, data)
        results.push({ type: 'push', ...res })
        await logNotification(userId, 'push', subscriptions[0].endpoint, template, res)
      }
    }
  }

  // 6. In-App Notification (Always insert into notifications table)
  // This assumes there is a 'notifications' table for the UI inbox
  try {
    await supabase.from('notifications').insert({
      user_id: userId,
      title: formatTitle(template, data),
      message: formatMessage(template, data),
      type: template,
      read: false,
      data: data
    })
  } catch (e) {
    console.warn('Failed to create in-app notification:', e)
  }

  return results
}

// eslint-disable-next-line @typescript-eslint/no-explicit-any
async function logNotification(userId: string, type: string, recipient: string, template: string, result: any) {
  try {
    await supabase.from('notification_logs').insert({
      user_id: userId,
      type,
      recipient,
      template,
      status: result.success ? 'sent' : 'failed',
      provider_response: result,
      metadata: result.data // safely ignore if undefined
    })
  } catch (e) {
    console.error('Failed to log notification:', e)
  }
}

// Helper to format titles for in-app notifications
function formatTitle(template: string, _data: Record<string, unknown>): string {
  const titles: Record<string, string> = {
    booking_confirmation: 'Booking Confirmed',
    booking_cancelled: 'Booking Cancelled',
    reminder: 'Upcoming Appointment',
    welcome: 'Welcome to Bookiji'
  }
  return titles[template] || 'New Notification'
}

function formatMessage(template: string, data: Record<string, unknown>): string {
  // Simple formatting, can be expanded
  if (template === 'booking_confirmation') return `Your appointment on ${data.date} is confirmed.`
  return 'You have a new update.'
}

/**
 * AnomalyAI - Cross-domain anomaly detection specialist for Bookiji
 * 
 * Agent 8: General anomaly detector across all signals
 * 
 * Responsibilities:
 * - Watch for sudden changes across ANY metric or health indicator
 * - Combine signals into unified anomaly reports
 * - Prioritize severity
 * - Recommend who (which agent) should investigate
 * 
 * Scope:
 * - ALL read-only endpoints from metrics, health, logs, incidents, SLOs
 * 
 * Output style:
 * - Cross-agent awareness
 * - Early-warning system
 * - Escalate only when meaningful
 */

export interface AnomalySignal {
  source: 'health' | 'metrics' | 'slo' | 'incidents' | 'events' | 'logs'
  endpoint: string
  severity: 'low' | 'medium' | 'high' | 'critical'
  type: string
  description: string
  timestamp: string
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  value?: any
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  baseline?: any
  deviation?: number
  affectedServices?: string[]
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  metadata?: Record<string, any>
}

export interface UnifiedAnomaly {
  id: string
  severity: 'low' | 'medium' | 'high' | 'critical'
  title: string
  description: string
  detectedAt: string
  signals: AnomalySignal[]
  affectedServices: string[]
  recommendedAgent: 'HealthAI' | 'SLOAI' | 'MetricsAI' | 'IncidentsAI' | 'AnomalyAI'
  recommendedAction: string
  confidence: number // 0-1
  correlationScore?: number // How many signals correlate
}

export interface AnomalyReport {
  timestamp: string
  totalAnomalies: number
  criticalCount: number
  highCount: number
  mediumCount: number
  lowCount: number
  anomalies: UnifiedAnomaly[]
  summary: string
  recommendations: {
    immediate: string[]
    monitor: string[]
    investigate: Array<{
      agent: string
      reason: string
      priority: number
    }>
  }
  crossSignalCorrelations: Array<{
    signals: string[]
    pattern: string
    severity: 'low' | 'medium' | 'high' | 'critical'
  }>
}

export class AnomalyAI {
  private static readonly SEVERITY_WEIGHTS = {
    critical: 4,
    high: 3,
    medium: 2,
    low: 1
  }

  private static readonly ANOMALY_THRESHOLDS = {
    // Percentage change thresholds
    suddenChange: 0.5, // 50% change
    significantChange: 0.25, // 25% change
    moderateChange: 0.10, // 10% change

    // Absolute thresholds
    errorRateCritical: 0.05, // 5% error rate
    errorRateHigh: 0.02, // 2% error rate
    latencySpike: 2.0, // 2x latency increase
    healthDegradation: 0.3, // 30% subsystem degradation

    // Log thresholds
    logVolumeSpike: 3.0, // 3x log volume increase
    logVolumeHigh: 100, // 100+ logs in time window
    criticalLogThreshold: 10, // 10+ critical logs
    errorLogThreshold: 50, // 50+ error logs

    // Event thresholds
    eventVolumeSpike: 2.0, // 2x event volume increase
    criticalEventThreshold: 5, // 5+ critical events
  }

  /**
   * Detect anomalies from health signals
   */
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  static detectHealthAnomalies(healthData: any): AnomalySignal[] {
    const anomalies: AnomalySignal[] = []
    const timestamp = new Date().toISOString()

    if (!healthData || !healthData.subsystems) {
      return anomalies
    }

    // Check overall health
    if (healthData.status === 'unhealthy') {
      anomalies.push({
        source: 'health',
        endpoint: '/api/ops/health',
        severity: 'critical',
        type: 'system_unhealthy',
        description: 'Overall system health is unhealthy',
        timestamp,
        value: healthData.status,
        affectedServices: Object.keys(healthData.subsystems || {})
      })
    } else if (healthData.status === 'degraded') {
      anomalies.push({
        source: 'health',
        endpoint: '/api/ops/health',
        severity: 'high',
        type: 'system_degraded',
        description: 'Overall system health is degraded',
        timestamp,
        value: healthData.status
      })
    }

    // Check individual subsystems
    const subsystems = healthData.subsystems || {}
    for (const [subsystem, data] of Object.entries(subsystems)) {
      // eslint-disable-next-line @typescript-eslint/no-explicit-any
      const subData = data as any
      if (subData.status === 'unhealthy') {
        anomalies.push({
          source: 'health',
          endpoint: `/api/ops/health/${subsystem}`,
          severity: 'critical',
          type: 'subsystem_unhealthy',
          description: `${subsystem} subsystem is unhealthy`,
          timestamp,
          value: subData,
          affectedServices: [subsystem]
        })
      } else if (subData.status === 'degraded') {
        anomalies.push({
          source: 'health',
          endpoint: `/api/ops/health/${subsystem}`,
          severity: 'high',
          type: 'subsystem_degraded',
          description: `${subsystem} subsystem is degraded`,
          timestamp,
          value: subData
        })
      }
    }

    return anomalies
  }

  /**
   * Detect anomalies from metrics signals
   */
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  static detectMetricsAnomalies(metricsData: any, endpoint: string): AnomalySignal[] {
    const anomalies: AnomalySignal[] = []
    const timestamp = new Date().toISOString()

    if (!metricsData) {
      return anomalies
    }

    // Error rate anomalies
    if (metricsData.analysis?.error_rate) {
      const errorRate = metricsData.analysis.error_rate.current
      if (errorRate >= this.ANOMALY_THRESHOLDS.errorRateCritical) {
        anomalies.push({
          source: 'metrics',
          endpoint,
          severity: 'critical',
          type: 'error_rate_spike',
          description: `Error rate is critically high: ${(errorRate * 100).toFixed(2)}%`,
          timestamp,
          value: errorRate,
          baseline: metricsData.analysis.error_rate.previous,
          deviation: metricsData.analysis.error_rate.previous 
            ? ((errorRate - metricsData.analysis.error_rate.previous) / metricsData.analysis.error_rate.previous) * 100
            : undefined
        })
      } else if (errorRate >= this.ANOMALY_THRESHOLDS.errorRateHigh) {
        anomalies.push({
          source: 'metrics',
          endpoint,
          severity: 'high',
          type: 'error_rate_elevated',
          description: `Error rate is elevated: ${(errorRate * 100).toFixed(2)}%`,
          timestamp,
          value: errorRate
        })
      }
    }

    // Latency anomalies
    if (metricsData.analysis?.latency) {
      const latency = metricsData.analysis.latency
      if (latency.p95 && latency.p95_previous) {
        const increase = (latency.p95 - latency.p95_previous) / latency.p95_previous
        if (increase >= this.ANOMALY_THRESHOLDS.latencySpike - 1) {
          anomalies.push({
            source: 'metrics',
            endpoint,
            severity: 'critical',
            type: 'latency_spike',
            description: `P95 latency increased by ${(increase * 100).toFixed(1)}%`,
            timestamp,
            value: latency.p95,
            baseline: latency.p95_previous,
            deviation: increase * 100
          })
        } else if (increase >= 0.5) {
          anomalies.push({
            source: 'metrics',
            endpoint,
            severity: 'high',
            type: 'latency_increase',
            description: `P95 latency increased by ${(increase * 100).toFixed(1)}%`,
            timestamp,
            value: latency.p95,
            baseline: latency.p95_previous
          })
        }
      }
    }

    // System metrics anomalies
    if (metricsData.analysis?.system) {
      const system = metricsData.analysis.system
      if (system.cpu_percent && system.cpu_percent > 90) {
        anomalies.push({
          source: 'metrics',
          endpoint,
          severity: 'high',
          type: 'cpu_high',
          description: `CPU usage is high: ${system.cpu_percent.toFixed(1)}%`,
          timestamp,
          value: system.cpu_percent
        })
      }
      if (system.memory_percent && system.memory_percent > 90) {
        anomalies.push({
          source: 'metrics',
          endpoint,
          severity: 'high',
          type: 'memory_high',
          description: `Memory usage is high: ${system.memory_percent.toFixed(1)}%`,
          timestamp,
          value: system.memory_percent
        })
      }
    }

    return anomalies
  }

  /**
   * Detect anomalies from SLO signals
   */
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  static detectSLOAnomalies(sloData: any): AnomalySignal[] {
    const anomalies: AnomalySignal[] = []
    const timestamp = new Date().toISOString()

    if (!sloData) {
      return anomalies
    }

    // Check overall SLO status
    if (sloData.status?.riskLevel === 'critical') {
      anomalies.push({
        source: 'slo',
        endpoint: '/api/ops/slo/status',
        severity: 'critical',
        type: 'slo_critical',
        description: 'SLO risk level is critical',
        timestamp,
        value: sloData.status,
        metadata: {
          criticalViolations: sloData.status.criticalViolations,
          insideBudget: sloData.status.insideBudget
        }
      })
    } else if (sloData.status?.riskLevel === 'high') {
      anomalies.push({
        source: 'slo',
        endpoint: '/api/ops/slo/status',
        severity: 'high',
        type: 'slo_high_risk',
        description: 'SLO risk level is high',
        timestamp,
        value: sloData.status
      })
    }

    // Check error budget
    if (sloData.errorBudget) {
      const budget = sloData.errorBudget
      if (budget.remaining <= 0) {
        anomalies.push({
          source: 'slo',
          endpoint: '/api/ops/slo/status',
          severity: 'critical',
          type: 'error_budget_exhausted',
          description: 'Error budget has been exhausted',
          timestamp,
          value: budget
        })
      } else if (budget.timeToExhaustionHours && budget.timeToExhaustionHours < 24) {
        anomalies.push({
          source: 'slo',
          endpoint: '/api/ops/slo/status',
          severity: 'high',
          type: 'error_budget_low',
          description: `Error budget will be exhausted in ${budget.timeToExhaustionHours.toFixed(1)} hours`,
          timestamp,
          value: budget
        })
      }
    }

    return anomalies
  }

  /**
   * Detect anomalies from incidents signals
   */
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  static detectIncidentAnomalies(incidentsData: any): AnomalySignal[] {
    const anomalies: AnomalySignal[] = []
    const timestamp = new Date().toISOString()

    if (!incidentsData || !incidentsData.data) {
      return anomalies
    }

    const data = incidentsData.data

    // Check for critical incidents
    if (data.criticalCount > 0) {
      anomalies.push({
        source: 'incidents',
        endpoint: '/api/ops/incidents/ai-triage',
        severity: 'critical',
        type: 'critical_incidents',
        description: `${data.criticalCount} critical incident(s) open`,
        timestamp,
        value: data.criticalCount,
        metadata: {
          openIncidents: data.openIncidentsCount,
          // eslint-disable-next-line @typescript-eslint/no-explicit-any
          summaries: data.summaries?.filter((s: any) => s.severity === 'critical') || []
        }
      })
    }

    // Check for high severity incidents
    if (data.highCount > 0 && data.criticalCount === 0) {
      anomalies.push({
        source: 'incidents',
        endpoint: '/api/ops/incidents/ai-triage',
        severity: 'high',
        type: 'high_incidents',
        description: `${data.highCount} high severity incident(s) open`,
        timestamp,
        value: data.highCount
      })
    }

    // Check for sudden increase in incidents
    if (data.openIncidentsCount > 5) {
      anomalies.push({
        source: 'incidents',
        endpoint: '/api/ops/incidents/ai-triage',
        severity: 'medium',
        type: 'incident_volume',
        description: `High volume of open incidents: ${data.openIncidentsCount}`,
        timestamp,
        value: data.openIncidentsCount
      })
    }

    return anomalies
  }

  /**
   * Detect anomalies from log signals
   */
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  static detectLogAnomalies(logData: any, endpoint: string): AnomalySignal[] {
    const anomalies: AnomalySignal[] = []
    const timestamp = new Date().toISOString()

    if (!logData || !logData.data) {
      return anomalies
    }

    const data = logData.data

    // Error log anomalies
    if (endpoint.includes('/logs/errors')) {
      const errorCount = data.errorCount || 0
      const criticalCount = data.criticalCount || 0
      const totalLogs = data.totalLogs || 0

      // Critical log volume
      if (criticalCount >= this.ANOMALY_THRESHOLDS.criticalLogThreshold) {
        anomalies.push({
          source: 'logs',
          endpoint,
          severity: 'critical',
          type: 'critical_log_spike',
          description: `${criticalCount} critical log entries detected`,
          timestamp,
          value: criticalCount,
          metadata: {
            totalLogs,
            errorCount,
            topErrors: data.topErrors?.slice(0, 5) || []
          }
        })
      }

      // Error log volume spike
      if (errorCount >= this.ANOMALY_THRESHOLDS.errorLogThreshold) {
        anomalies.push({
          source: 'logs',
          endpoint,
          severity: errorCount >= this.ANOMALY_THRESHOLDS.errorLogThreshold * 2 ? 'critical' : 'high',
          type: 'error_log_volume',
          description: `${errorCount} error log entries detected`,
          timestamp,
          value: errorCount,
          metadata: {
            totalLogs,
            criticalCount
          }
        })
      }

      // New error patterns detected
      if (data.newPatterns && data.newPatterns.length > 0) {
        anomalies.push({
          source: 'logs',
          endpoint,
          severity: data.newPatterns.length > 3 ? 'high' : 'medium',
          type: 'new_error_patterns',
          description: `${data.newPatterns.length} new error pattern(s) detected`,
          timestamp,
          value: data.newPatterns.length,
          metadata: {
            patterns: data.newPatterns.slice(0, 3)
          }
        })
      }

      // Regressions detected
      if (data.regressions && data.regressions.length > 0) {
        anomalies.push({
          source: 'logs',
          endpoint,
          severity: 'high',
          type: 'error_regression',
          description: `${data.regressions.length} error regression(s) detected`,
          timestamp,
          value: data.regressions.length,
          metadata: {
            regressions: data.regressions.slice(0, 3)
          }
        })
      }
    }

    // System log anomalies
    if (endpoint.includes('/logs/system')) {
      const totalLogs = data.totalLogs || 0
      const warningCount = data.warningCount || 0

      // System log volume spike
      if (totalLogs >= this.ANOMALY_THRESHOLDS.logVolumeHigh) {
        anomalies.push({
          source: 'logs',
          endpoint,
          severity: totalLogs >= this.ANOMALY_THRESHOLDS.logVolumeHigh * 2 ? 'high' : 'medium',
          type: 'system_log_volume',
          description: `High system log volume: ${totalLogs} entries`,
          timestamp,
          value: totalLogs,
          metadata: {
            warningCount
          }
        })
      }

      // New patterns in system logs
      if (data.newPatterns && data.newPatterns.length > 2) {
        anomalies.push({
          source: 'logs',
          endpoint,
          severity: 'medium',
          type: 'system_log_patterns',
          description: `${data.newPatterns.length} new system log pattern(s) detected`,
          timestamp,
          value: data.newPatterns.length
        })
      }
    }

    // Booking log anomalies
    if (endpoint.includes('/logs/booking')) {
      const totalLogs = data.totalLogs || 0
      const errorCount = data.errorCount || 0

      // Booking log errors
      if (errorCount > 0) {
        const errorRate = totalLogs > 0 ? errorCount / totalLogs : 0
        if (errorRate >= this.ANOMALY_THRESHOLDS.errorRateHigh) {
          anomalies.push({
            source: 'logs',
            endpoint,
            severity: errorRate >= this.ANOMALY_THRESHOLDS.errorRateCritical ? 'critical' : 'high',
            type: 'booking_log_errors',
            description: `High error rate in booking logs: ${(errorRate * 100).toFixed(2)}%`,
            timestamp,
            value: errorRate,
            metadata: {
              errorCount,
              totalLogs
            }
          })
        }
      }
    }

    return anomalies
  }

  /**
   * Detect anomalies from event signals
   */
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  static detectEventAnomalies(eventsData: any, endpoint: string): AnomalySignal[] {
    const anomalies: AnomalySignal[] = []
    const timestamp = new Date().toISOString()

    if (!eventsData || !eventsData.data) {
      return anomalies
    }

    const events = Array.isArray(eventsData.data) ? eventsData.data : []
    const eventCount = events.length

    // Critical events detected
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    const criticalEvents = events.filter((e: any) => e.severity === 'critical' || e.severity === 'error')
    if (criticalEvents.length >= this.ANOMALY_THRESHOLDS.criticalEventThreshold) {
      anomalies.push({
        source: 'events',
        endpoint,
        severity: 'critical',
        type: 'critical_events_spike',
        description: `${criticalEvents.length} critical event(s) detected`,
        timestamp,
        value: criticalEvents.length,
        metadata: {
          totalEvents: eventCount,
          // eslint-disable-next-line @typescript-eslint/no-explicit-any
          criticalEvents: criticalEvents.slice(0, 5).map((e: any) => ({
            type: e.type,
            title: e.title,
            timestamp: e.timestamp
          }))
        }
      })
    }

    // High event volume
    if (eventCount >= 50) {
      anomalies.push({
        source: 'events',
        endpoint,
        severity: eventCount >= 100 ? 'high' : 'medium',
        type: 'event_volume_spike',
        description: `High event volume: ${eventCount} events`,
        timestamp,
        value: eventCount
      })
    }

    // Deployment-related events
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    const deployEvents = events.filter((e: any) => 
      e.type === 'deploy' || 
      e.type === 'deployment' ||
      (e.title && e.title.toLowerCase().includes('deploy'))
    )
    if (deployEvents.length > 0) {
      // eslint-disable-next-line @typescript-eslint/no-explicit-any
      const failedDeploys = deployEvents.filter((e: any) => 
        e.severity === 'error' || 
        e.severity === 'critical' ||
        (e.data && e.data.status === 'failed')
      )
      if (failedDeploys.length > 0) {
        anomalies.push({
          source: 'events',
          endpoint,
          severity: 'high',
          type: 'deployment_failures',
          description: `${failedDeploys.length} failed deployment(s) detected`,
          timestamp,
          value: failedDeploys.length,
          metadata: {
            deployments: failedDeploys.slice(0, 3)
          }
        })
      }
    }

    // Health check events
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    const healthEvents = events.filter((e: any) => 
      e.type === 'health-check' || 
      (e.title && e.title.toLowerCase().includes('health'))
    )
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    const failedHealthChecks = healthEvents.filter((e: any) => 
      e.severity === 'error' || e.severity === 'critical'
    )
    if (failedHealthChecks.length >= 3) {
      anomalies.push({
        source: 'events',
        endpoint,
        severity: 'high',
        type: 'health_check_failures',
        description: `${failedHealthChecks.length} health check failure(s) detected`,
        timestamp,
        value: failedHealthChecks.length,
        affectedServices: failedHealthChecks
          // eslint-disable-next-line @typescript-eslint/no-explicit-any
          .map((e: any) => e.service)
          // eslint-disable-next-line @typescript-eslint/no-explicit-any
          .filter((s: any) => s)
          .slice(0, 5)
      })
    }

    return anomalies
  }

  /**
   * Unify and correlate anomalies into a single report
   */
  static unifyAnomalies(signals: AnomalySignal[]): UnifiedAnomaly[] {
    if (signals.length === 0) {
      return []
    }

    // Group signals by type and affected services
    const grouped = new Map<string, AnomalySignal[]>()

    for (const signal of signals) {
      const key = `${signal.type}:${(signal.affectedServices || []).join(',')}`
      if (!grouped.has(key)) {
        grouped.set(key, [])
      }
      grouped.get(key)!.push(signal)
    }

    const unified: UnifiedAnomaly[] = []

    for (const [_key, groupSignals] of grouped.entries()) {
      // Determine overall severity (highest in group)
      const severities = groupSignals.map(s => s.severity)
      const severityOrder = ['critical', 'high', 'medium', 'low']
      // eslint-disable-next-line @typescript-eslint/no-explicit-any
      const maxSeverity = severityOrder.find(s => severities.includes(s as any)) || 'low'

      // Collect all affected services
      const affectedServices = new Set<string>()
      groupSignals.forEach(s => {
        s.affectedServices?.forEach(service => affectedServices.add(service))
      })

      // Determine recommended agent
      const recommendedAgent = this.recommendAgent(groupSignals)

      // Calculate confidence based on signal count and correlation
      const confidence = Math.min(1.0, 0.5 + (groupSignals.length * 0.15))

      // Generate title and description
      const title = this.generateAnomalyTitle(groupSignals[0], groupSignals.length)
      const description = this.generateAnomalyDescription(groupSignals)

      unified.push({
        id: `${groupSignals[0].type}-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
        // eslint-disable-next-line @typescript-eslint/no-explicit-any
        severity: maxSeverity as any,
        title,
        description,
        detectedAt: new Date().toISOString(),
        signals: groupSignals,
        affectedServices: Array.from(affectedServices),
        recommendedAgent,
        recommendedAction: this.generateRecommendedAction(groupSignals, recommendedAgent),
        confidence,
        correlationScore: groupSignals.length > 1 ? groupSignals.length : undefined
      })
    }

    // Sort by severity (critical first)
    unified.sort((a, b) => {
      return this.SEVERITY_WEIGHTS[b.severity] - this.SEVERITY_WEIGHTS[a.severity]
    })

    return unified
  }

  /**
   * Recommend which agent should investigate
   */
  private static recommendAgent(signals: AnomalySignal[]): 'HealthAI' | 'SLOAI' | 'MetricsAI' | 'IncidentsAI' | 'AnomalyAI' {
    // Count signals by source
    const sourceCounts = new Map<string, number>()
    signals.forEach(s => {
      sourceCounts.set(s.source, (sourceCounts.get(s.source) || 0) + 1)
    })

    // Priority: incidents > slo > health > logs > events > metrics
    if (sourceCounts.get('incidents') || signals.some(s => s.type.includes('incident'))) {
      return 'IncidentsAI'
    }
    if (sourceCounts.get('slo') || signals.some(s => s.type.includes('slo') || s.type.includes('error_budget'))) {
      return 'SLOAI'
    }
    if (sourceCounts.get('health') || signals.some(s => s.type.includes('health') || s.type.includes('subsystem'))) {
      return 'HealthAI'
    }
    if (sourceCounts.get('logs') || signals.some(s => s.type.includes('log') || s.type.includes('error_pattern'))) {
      return 'MetricsAI' // Logs are typically analyzed by MetricsAI
    }
    if (sourceCounts.get('events') || signals.some(s => s.type.includes('event') || s.type.includes('deployment'))) {
      return 'AnomalyAI' // Events are cross-domain, AnomalyAI handles them
    }
    if (sourceCounts.get('metrics') || signals.some(s => s.type.includes('error_rate') || s.type.includes('latency'))) {
      return 'MetricsAI'
    }

    return 'AnomalyAI'
  }

  /**
   * Generate anomaly title
   */
  private static generateAnomalyTitle(signal: AnomalySignal, signalCount: number): string {
    const countSuffix = signalCount > 1 ? ` (${signalCount} signals)` : ''

    switch (signal.type) {
      case 'system_unhealthy':
        return `System Health Critical${countSuffix}`
      case 'subsystem_unhealthy':
        return `${signal.affectedServices?.[0] || 'Subsystem'} Unhealthy${countSuffix}`
      case 'error_rate_spike':
        return `Error Rate Spike${countSuffix}`
      case 'latency_spike':
        return `Latency Spike${countSuffix}`
      case 'slo_critical':
        return `SLO Critical Violation${countSuffix}`
      case 'error_budget_exhausted':
        return `Error Budget Exhausted${countSuffix}`
      case 'critical_incidents':
        return `Critical Incidents Detected${countSuffix}`
      case 'critical_log_spike':
        return `Critical Log Spike${countSuffix}`
      case 'error_log_volume':
        return `Error Log Volume Spike${countSuffix}`
      case 'new_error_patterns':
        return `New Error Patterns${countSuffix}`
      case 'error_regression':
        return `Error Regression${countSuffix}`
      case 'critical_events_spike':
        return `Critical Events Spike${countSuffix}`
      case 'deployment_failures':
        return `Deployment Failures${countSuffix}`
      case 'health_check_failures':
        return `Health Check Failures${countSuffix}`
      default:
        return `${signal.type.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase())}${countSuffix}`
    }
  }

  /**
   * Generate anomaly description
   */
  private static generateAnomalyDescription(signals: AnomalySignal[]): string {
    if (signals.length === 1) {
      return signals[0].description
    }

    const descriptions = signals.map(s => s.description)
    return `Multiple related anomalies detected: ${descriptions.join('; ')}`
  }

  /**
   * Generate recommended action
   */
  private static generateRecommendedAction(signals: AnomalySignal[], agent: string): string {
    const signal = signals[0]

    switch (agent) {
      case 'HealthAI':
        return `Investigate subsystem health issues. Check ${signal.affectedServices?.join(', ') || 'affected services'}`
      case 'SLOAI':
        return `Review SLO compliance and error budget status. Assess customer impact.`
      case 'MetricsAI':
        return `Analyze metrics trends and identify root cause of ${signal.type.replace(/_/g, ' ')}`
      case 'IncidentsAI':
        return `Review incident triage summary and prioritize critical incidents`
      default:
        if (signal.source === 'logs') {
          return `Analyze log patterns and identify root cause of ${signal.type.replace(/_/g, ' ')}`
        }
        if (signal.source === 'events') {
          return `Review event patterns and correlate with system changes`
        }
        return `Monitor and investigate ${signal.type} anomaly`
    }
  }

  /**
   * Generate comprehensive anomaly report
   */
  static generateReport(unifiedAnomalies: UnifiedAnomaly[]): AnomalyReport {
    const timestamp = new Date().toISOString()

    // Count by severity
    const criticalCount = unifiedAnomalies.filter(a => a.severity === 'critical').length
    const highCount = unifiedAnomalies.filter(a => a.severity === 'high').length
    const mediumCount = unifiedAnomalies.filter(a => a.severity === 'medium').length
    const lowCount = unifiedAnomalies.filter(a => a.severity === 'low').length

    // Generate summary
    let summary = 'No anomalies detected.'
    if (unifiedAnomalies.length > 0) {
      if (criticalCount > 0) {
        summary = `ðŸš¨ ${criticalCount} critical anomaly(ies) detected requiring immediate attention.`
      } else if (highCount > 0) {
        summary = `âš ï¸ ${highCount} high-severity anomaly(ies) detected.`
      } else if (mediumCount > 0) {
        summary = `ðŸ“Š ${mediumCount} medium-severity anomaly(ies) detected.`
      } else {
        summary = `â„¹ï¸ ${lowCount} low-severity anomaly(ies) detected.`
      }
    }

    // Generate recommendations
    const immediate: string[] = []
    const monitor: string[] = []
    const investigate: Array<{ agent: string; reason: string; priority: number }> = []

    const agentPriorities = new Map<string, { count: number; maxSeverity: string; reasons: string[] }>()

    for (const anomaly of unifiedAnomalies) {
      if (anomaly.severity === 'critical') {
        immediate.push(anomaly.recommendedAction)
      } else if (anomaly.severity === 'high') {
        monitor.push(anomaly.recommendedAction)
      }

      const agent = anomaly.recommendedAgent
      if (!agentPriorities.has(agent)) {
        agentPriorities.set(agent, {
          count: 0,
          maxSeverity: anomaly.severity,
          reasons: []
        })
      }

      const agentData = agentPriorities.get(agent)!
      agentData.count++
      if (this.SEVERITY_WEIGHTS[anomaly.severity as keyof typeof this.SEVERITY_WEIGHTS] > 
          this.SEVERITY_WEIGHTS[agentData.maxSeverity as keyof typeof this.SEVERITY_WEIGHTS]) {
        agentData.maxSeverity = anomaly.severity
      }
      agentData.reasons.push(anomaly.title)
    }

    // Convert agent priorities to investigate list
    for (const [agent, data] of agentPriorities.entries()) {
      investigate.push({
        agent,
        reason: `${data.count} anomaly(ies): ${data.reasons.slice(0, 2).join(', ')}`,
        priority: this.SEVERITY_WEIGHTS[data.maxSeverity as keyof typeof this.SEVERITY_WEIGHTS]
      })
    }

    // Sort investigate by priority
    investigate.sort((a, b) => b.priority - a.priority)

    // Find cross-signal correlations
    const correlations = this.findCorrelations(unifiedAnomalies)

    return {
      timestamp,
      totalAnomalies: unifiedAnomalies.length,
      criticalCount,
      highCount,
      mediumCount,
      lowCount,
      anomalies: unifiedAnomalies,
      summary,
      recommendations: {
        immediate,
        monitor,
        investigate
      },
      crossSignalCorrelations: correlations
    }
  }

  /**
   * Find correlations between anomalies
   */
  private static findCorrelations(anomalies: UnifiedAnomaly[]): Array<{
    signals: string[]
    pattern: string
    severity: 'low' | 'medium' | 'high' | 'critical'
  }> {
    const correlations: Array<{
      signals: string[]
      pattern: string
      severity: 'low' | 'medium' | 'high' | 'critical'
    }> = []

    // Group by affected services
    const serviceGroups = new Map<string, UnifiedAnomaly[]>()
    for (const anomaly of anomalies) {
      for (const service of anomaly.affectedServices) {
        if (!serviceGroups.has(service)) {
          serviceGroups.set(service, [])
        }
        serviceGroups.get(service)!.push(anomaly)
      }
    }

    // Find services with multiple anomalies
    for (const [service, serviceAnomalies] of serviceGroups.entries()) {
      if (serviceAnomalies.length > 1) {
        const maxSeverity = serviceAnomalies.reduce((max, a) => {
          return this.SEVERITY_WEIGHTS[a.severity] > this.SEVERITY_WEIGHTS[max.severity as keyof typeof this.SEVERITY_WEIGHTS]
            ? a : max
        }, serviceAnomalies[0])

        correlations.push({
          signals: serviceAnomalies.map(a => a.id),
          pattern: `Multiple anomalies affecting ${service}`,
          severity: maxSeverity.severity
        })
      }
    }

    // Group by recommended agent
    const agentGroups = new Map<string, UnifiedAnomaly[]>()
    for (const anomaly of anomalies) {
      const agent = anomaly.recommendedAgent
      if (!agentGroups.has(agent)) {
        agentGroups.set(agent, [])
      }
      agentGroups.get(agent)!.push(anomaly)
    }

    // Find agents with multiple related anomalies
    for (const [agent, agentAnomalies] of agentGroups.entries()) {
      if (agentAnomalies.length > 1) {
        const maxSeverity = agentAnomalies.reduce((max, a) => {
          return this.SEVERITY_WEIGHTS[a.severity] > this.SEVERITY_WEIGHTS[max.severity as keyof typeof this.SEVERITY_WEIGHTS]
            ? a : max
        }, agentAnomalies[0])

        correlations.push({
          signals: agentAnomalies.map(a => a.id),
          pattern: `${agent} should investigate ${agentAnomalies.length} related anomalies`,
          severity: maxSeverity.severity
        })
      }
    }

    return correlations
  }
}

export const anomalyai = new AnomalyAI()

import { getServerSupabase } from '@/lib/supabaseServer'
import { sloai } from './sloai'

// eslint-disable-next-line @typescript-eslint/no-explicit-any
const supabase = new Proxy({} as any, { get: (target, prop) => (getServerSupabase() as any)[prop] }) as ReturnType<typeof getServerSupabase>

export interface Deployment {
  id: string
  version: string
  environment: 'canary' | 'production' | 'staging'
  commit_sha?: string
  branch?: string
  deployed_by?: string
  deployed_at: string
  status: 'deploying' | 'active' | 'promoted' | 'rolled_back' | 'failed'
  url?: string
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  metadata?: Record<string, any>
  promoted_at?: string
  rolled_back_at?: string
}

export interface DeploymentMetrics {
  error_rate: number
  p95_latency: number
  p99_latency: number
  throughput: number
  availability: number
  sample_count: number
  time_window_minutes: number
  collected_at: string
}

export interface CanaryComparison {
  canary: Deployment
  baseline: Deployment | null
  metrics: {
    canary: DeploymentMetrics
    baseline: DeploymentMetrics | null
  }
  comparison: {
    error_rate_diff: number // canary - baseline (positive = worse)
    p95_latency_diff: number
    p99_latency_diff: number
    throughput_diff: number
    availability_diff: number
  }
  slo_alignment: {
    canary_meets_slo: boolean
    baseline_meets_slo: boolean | null
    canary_violations: number
    baseline_violations: number
  }
}

export interface DeploymentRecommendation {
  action: 'promote' | 'rollback' | 'monitor' | 'extend_canary'
  confidence: 'high' | 'medium' | 'low'
  risk_level: 'low' | 'medium' | 'high' | 'critical'
  reasoning: string[]
  metrics: {
    error_rate: { canary: number; baseline: number | null; threshold: number }
    p95_latency: { canary: number; baseline: number | null; threshold: number }
    p99_latency: { canary: number; baseline: number | null; threshold: number }
  }
  slo_status: {
    canary: boolean
    baseline: boolean | null
  }
  customer_impact: string
}

/**
 * DeployAI - Deployment and Canary Advisor
 * 
 * Interprets canary signals, compares canary vs baseline performance,
 * checks error rates, p95 latency, and SLO alignment.
 * Recommends promotion or rollback (but NEVER performs actions).
 */
export class DeployAI {
  /**
   * Get current deployment status
   */
  async getDeploymentStatus(): Promise<{
    canary: Deployment | null
    production: Deployment | null
    staging: Deployment | null
  }> {
    const { data: deployments, error } = await supabase
      .from('deployments')
      .select('*')
      .in('status', ['active', 'deploying'])
      .order('deployed_at', { ascending: false })

    if (error) {
      console.error('Failed to fetch deployments:', error)
      return { canary: null, production: null, staging: null }
    }

    const canary = deployments?.find(d => d.environment === 'canary') || null
    const production = deployments?.find(d => d.environment === 'production') || null
    const staging = deployments?.find(d => d.environment === 'staging') || null

    return { canary, production, staging }
  }

  /**
   * Get canary deployment details
   */
  async getCanaryDeployment(): Promise<Deployment | null> {
    const { data, error } = await supabase
      .from('deployments')
      .select('*')
      .eq('environment', 'canary')
      .in('status', ['active', 'deploying'])
      .order('deployed_at', { ascending: false })
      .limit(1)
      .maybeSingle()

    if (error || !data) {
      return null
    }

    return data as Deployment
  }

  /**
   * Get baseline (production) deployment details
   */
  async getBaselineDeployment(): Promise<Deployment | null> {
    const { data, error } = await supabase
      .from('deployments')
      .select('*')
      .eq('environment', 'production')
      .eq('status', 'active')
      .order('deployed_at', { ascending: false })
      .limit(1)
      .maybeSingle()

    if (error || !data) {
      return null
    }

    return data as Deployment
  }

  /**
   * Get metrics for a deployment
   */
  async getDeploymentMetrics(deploymentId: string, timeWindowMinutes: number = 15): Promise<DeploymentMetrics | null> {
    const windowStart = new Date(Date.now() - timeWindowMinutes * 60 * 1000)

    const { data: metrics, error } = await supabase
      .from('deployment_metrics')
      .select('*')
      .eq('deployment_id', deploymentId)
      .gte('collected_at', windowStart.toISOString())
      .order('collected_at', { ascending: false })

    if (error || !metrics || metrics.length === 0) {
      return null
    }

    // Aggregate metrics by type
    const aggregated: Partial<DeploymentMetrics> = {
      sample_count: 0,
      time_window_minutes: timeWindowMinutes,
      collected_at: metrics[0].collected_at
    }

    metrics.forEach(metric => {
      if (metric.metric_type === 'error_rate') {
        aggregated.error_rate = Number(metric.metric_value)
      } else if (metric.metric_type === 'p95_latency') {
        aggregated.p95_latency = Number(metric.metric_value)
      } else if (metric.metric_type === 'p99_latency') {
        aggregated.p99_latency = Number(metric.metric_value)
      } else if (metric.metric_type === 'throughput') {
        aggregated.throughput = Number(metric.metric_value)
      } else if (metric.metric_type === 'availability') {
        aggregated.availability = Number(metric.metric_value)
      }
      aggregated.sample_count = (aggregated.sample_count || 0) + (metric.sample_count || 0)
    })

    // If metrics are missing, fetch from live metrics endpoints
    if (!aggregated.error_rate || !aggregated.p95_latency) {
      const liveMetrics = await this.fetchLiveMetrics(deploymentId)
      return {
        error_rate: aggregated.error_rate ?? liveMetrics.error_rate ?? 0,
        p95_latency: aggregated.p95_latency ?? liveMetrics.p95_latency ?? 0,
        p99_latency: aggregated.p99_latency ?? liveMetrics.p99_latency ?? 0,
        throughput: aggregated.throughput ?? liveMetrics.throughput ?? 0,
        availability: aggregated.availability ?? liveMetrics.availability ?? 100,
        sample_count: aggregated.sample_count ?? 0,
        time_window_minutes: timeWindowMinutes,
        collected_at: new Date().toISOString()
      }
    }

    return aggregated as DeploymentMetrics
  }

  /**
   * Fetch live metrics from metrics endpoints
   */
  private async fetchLiveMetrics(_deploymentId: string): Promise<Partial<DeploymentMetrics>> {
    // This would fetch from /ops/metrics/* endpoints
    // For now, return defaults - in production, this would make HTTP requests
    return {
      error_rate: 0,
      p95_latency: 0,
      p99_latency: 0,
      throughput: 0,
      availability: 100
    }
  }

  /**
   * Compare canary vs baseline
   */
  async compareCanaryBaseline(): Promise<CanaryComparison | null> {
    const canary = await this.getCanaryDeployment()
    if (!canary) {
      return null
    }

    const baseline = await this.getBaselineDeployment()
    const canaryMetrics = await this.getDeploymentMetrics(canary.id) || this.getDefaultMetrics()
    const baselineMetrics = baseline ? (await this.getDeploymentMetrics(baseline.id) || this.getDefaultMetrics()) : null

    // Get SLO alignment
    const sloStatus = await sloai.assessOverallStatus()

    return {
      canary,
      baseline,
      metrics: {
        canary: canaryMetrics,
        baseline: baselineMetrics
      },
      comparison: {
        error_rate_diff: baselineMetrics ? canaryMetrics.error_rate - baselineMetrics.error_rate : canaryMetrics.error_rate,
        p95_latency_diff: baselineMetrics ? canaryMetrics.p95_latency - baselineMetrics.p95_latency : canaryMetrics.p95_latency,
        p99_latency_diff: baselineMetrics ? canaryMetrics.p99_latency - baselineMetrics.p99_latency : canaryMetrics.p99_latency,
        throughput_diff: baselineMetrics ? canaryMetrics.throughput - baselineMetrics.throughput : canaryMetrics.throughput,
        availability_diff: baselineMetrics ? canaryMetrics.availability - baselineMetrics.availability : canaryMetrics.availability - 100
      },
      slo_alignment: {
        canary_meets_slo: sloStatus.insideBudget,
        baseline_meets_slo: baseline ? sloStatus.insideBudget : null,
        canary_violations: sloStatus.riskLevel === 'critical' ? 10 : sloStatus.riskLevel === 'high' ? 5 : 0,
        baseline_violations: 0
      }
    }
  }

  /**
   * Get default metrics (used when no metrics available)
   */
  private getDefaultMetrics(): DeploymentMetrics {
    return {
      error_rate: 0,
      p95_latency: 0,
      p99_latency: 0,
      throughput: 0,
      availability: 100,
      sample_count: 0,
      time_window_minutes: 15,
      collected_at: new Date().toISOString()
    }
  }

  /**
   * Generate deployment recommendation
   */
  async generateRecommendation(): Promise<DeploymentRecommendation> {
    const comparison = await this.compareCanaryBaseline()

    if (!comparison) {
      return {
        action: 'monitor',
        confidence: 'low',
        risk_level: 'low',
        reasoning: ['No active canary deployment found'],
        metrics: {
          error_rate: { canary: 0, baseline: null, threshold: 0.01 },
          p95_latency: { canary: 0, baseline: null, threshold: 500 },
          p99_latency: { canary: 0, baseline: null, threshold: 1000 }
        },
        slo_status: {
          canary: false,
          baseline: null
        },
        customer_impact: 'No canary deployment active'
      }
    }

    const { _canary, baseline, metrics, comparison: comp, slo_alignment } = comparison

    // Thresholds (conservative - fact-based, not optimistic)
    const ERROR_RATE_THRESHOLD = 0.01 // 1% error rate
    const P95_LATENCY_THRESHOLD = 500 // 500ms
    const P99_LATENCY_THRESHOLD = 1000 // 1s
    const ERROR_RATE_DEGRADATION_THRESHOLD = 0.005 // 0.5% worse than baseline
    const LATENCY_DEGRADATION_THRESHOLD = 100 // 100ms worse than baseline

    const reasoning: string[] = []
    let action: 'promote' | 'rollback' | 'monitor' | 'extend_canary' = 'monitor'
    let confidence: 'high' | 'medium' | 'low' = 'medium'
    let risk_level: 'low' | 'medium' | 'high' | 'critical' = 'low'
    let customer_impact = ''

    // Check error rate
    const errorRateExceedsThreshold = metrics.canary.error_rate > ERROR_RATE_THRESHOLD
    const errorRateWorseThanBaseline = baseline && comp.error_rate_diff > ERROR_RATE_DEGRADATION_THRESHOLD

    if (errorRateExceedsThreshold) {
      reasoning.push(`Error rate ${(metrics.canary.error_rate * 100).toFixed(2)}% exceeds threshold ${(ERROR_RATE_THRESHOLD * 100).toFixed(2)}%`)
      risk_level = 'high'
      action = 'rollback'
      confidence = 'high'
    } else if (errorRateWorseThanBaseline) {
      reasoning.push(`Error rate ${(comp.error_rate_diff * 100).toFixed(2)}% worse than baseline`)
      risk_level = 'medium'
      action = 'rollback'
      confidence = 'medium'
    }

    // Check P95 latency
    const p95ExceedsThreshold = metrics.canary.p95_latency > P95_LATENCY_THRESHOLD
    const p95WorseThanBaseline = baseline && comp.p95_latency_diff > LATENCY_DEGRADATION_THRESHOLD

    if (p95ExceedsThreshold) {
      reasoning.push(`P95 latency ${metrics.canary.p95_latency.toFixed(0)}ms exceeds threshold ${P95_LATENCY_THRESHOLD}ms`)
      if (risk_level === 'low') risk_level = 'high'
      if (action === 'monitor') action = 'rollback'
      confidence = 'high'
    } else if (p95WorseThanBaseline) {
      reasoning.push(`P95 latency ${comp.p95_latency_diff.toFixed(0)}ms worse than baseline`)
      if (risk_level === 'low') risk_level = 'medium'
      if (action === 'monitor') action = 'rollback'
    }

    // Check P99 latency
    const p99ExceedsThreshold = metrics.canary.p99_latency > P99_LATENCY_THRESHOLD
    const p99WorseThanBaseline = baseline && comp.p99_latency_diff > LATENCY_DEGRADATION_THRESHOLD

    if (p99ExceedsThreshold) {
      reasoning.push(`P99 latency ${metrics.canary.p99_latency.toFixed(0)}ms exceeds threshold ${P99_LATENCY_THRESHOLD}ms`)
      if (risk_level === 'low') risk_level = 'high'
      if (action === 'monitor') action = 'rollback'
      confidence = 'high'
    } else if (p99WorseThanBaseline) {
      reasoning.push(`P99 latency ${comp.p99_latency_diff.toFixed(0)}ms worse than baseline`)
      if (risk_level === 'low') risk_level = 'medium'
    }

    // Check SLO alignment
    if (!slo_alignment.canary_meets_slo) {
      reasoning.push(`Canary violates SLO targets (${slo_alignment.canary_violations} violations)`)
      risk_level = 'critical'
      action = 'rollback'
      confidence = 'high'
    }

    // If all checks pass, recommend promotion
    if (action === 'monitor' && !errorRateExceedsThreshold && !p95ExceedsThreshold && !p99ExceedsThreshold && slo_alignment.canary_meets_slo) {
      // But be conservative - need sufficient sample size
      if (metrics.canary.sample_count < 100) {
        action = 'extend_canary'
        reasoning.push(`Insufficient sample size (${metrics.canary.sample_count} samples). Need at least 100 samples for promotion.`)
        confidence = 'low'
      } else {
        action = 'promote'
        reasoning.push('All metrics within acceptable thresholds')
        reasoning.push('SLO targets met')
        reasoning.push(`Sample size sufficient (${metrics.canary.sample_count} samples)`)
        confidence = 'high'
        risk_level = 'low'
      }
    }

    // Generate customer impact
    if (risk_level === 'critical') {
      customer_impact = 'Critical degradation detected. Users are experiencing significant issues. Immediate rollback recommended.'
    } else if (risk_level === 'high') {
      customer_impact = 'Significant performance degradation. Many users may experience errors or slow responses.'
    } else if (risk_level === 'medium') {
      customer_impact = 'Moderate performance degradation. Some users may notice slower responses.'
    } else {
      customer_impact = 'Performance within acceptable limits. No significant customer impact expected.'
    }

    return {
      action,
      confidence,
      risk_level,
      reasoning,
      metrics: {
        error_rate: {
          canary: metrics.canary.error_rate,
          baseline: baseline ? metrics.baseline?.error_rate ?? null : null,
          threshold: ERROR_RATE_THRESHOLD
        },
        p95_latency: {
          canary: metrics.canary.p95_latency,
          baseline: baseline ? metrics.baseline?.p95_latency ?? null : null,
          threshold: P95_LATENCY_THRESHOLD
        },
        p99_latency: {
          canary: metrics.canary.p99_latency,
          baseline: baseline ? metrics.baseline?.p99_latency ?? null : null,
          threshold: P99_LATENCY_THRESHOLD
        }
      },
      slo_status: {
        canary: slo_alignment.canary_meets_slo,
        baseline: baseline ? slo_alignment.baseline_meets_slo : null
      },
      customer_impact
    }
  }

  /**
   * Get deployment events
   */
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  async getDeploymentEvents(deploymentId?: string, limit: number = 50): Promise<any[]> {
    let query = supabase
      .from('deployment_events')
      .select('*')
      .order('created_at', { ascending: false })
      .limit(limit)

    if (deploymentId) {
      query = query.eq('deployment_id', deploymentId)
    }

    const { data, error } = await query

    if (error) {
      console.error('Failed to fetch deployment events:', error)
      return []
    }

    return data || []
  }
}

// Export singleton instance
export const deployai = new DeployAI()

import { getServerSupabase } from '@/lib/supabaseServer'

// eslint-disable-next-line @typescript-eslint/no-explicit-any
const supabase = new Proxy({} as any, { get: (target, prop) => (getServerSupabase() as any)[prop] }) as ReturnType<typeof getServerSupabase>

export interface SLOConfig {
  metric_name: string
  target_p95_ms: number
  target_p99_ms: number
  target_error_rate: number
  target_cache_hit_rate: number
  warning_threshold_multiplier: number
  critical_threshold_multiplier: number
}

export interface SLOViolation {
  id: number
  metric_name: string
  violation_type: 'p95' | 'p99' | 'error_rate' | 'cache_hit_rate'
  current_value: number
  threshold_value: number
  severity: 'warning' | 'critical'
  endpoint?: string
  bucket: string
  violation_count: number
  first_violation_at: string
  last_violation_at: string
  resolved_at?: string
  resolved_by?: string
}

export interface SLOComplianceResult {
  violations: SLOViolation[]
  summary: {
    total_violations: number
    warning_count: number
    critical_count: number
    compliance_rate: number
  }
}

export class SLOMonitor {
  private static instance: SLOMonitor
  private checkInterval?: NodeJS.Timeout
  private isMonitoring = false

  static getInstance(): SLOMonitor {
    if (!SLOMonitor.instance) {
      SLOMonitor.instance = new SLOMonitor()
    }
    return SLOMonitor.instance
  }

  /**
   * Start continuous SLO monitoring
   */
  startMonitoring(intervalMs: number = 5 * 60 * 1000): void {
    if (this.isMonitoring) {
      console.log('SLO monitoring already active')
      return
    }

    this.isMonitoring = true
    this.checkInterval = setInterval(async () => {
      try {
        await this.checkSLOCompliance()
      } catch (error) {
        console.error('SLO compliance check failed:', error)
      }
    }, intervalMs)

    console.log(`SLO monitoring started with ${intervalMs}ms interval`)
  }

  /**
   * Stop SLO monitoring
   */
  stopMonitoring(): void {
    if (this.checkInterval) {
      clearInterval(this.checkInterval)
      this.checkInterval = undefined
    }
    this.isMonitoring = false
    console.log('SLO monitoring stopped')
  }

  /**
   * Check SLO compliance and record violations
   */
  async checkSLOCompliance(): Promise<SLOComplianceResult> {
    try {
      // Call the database function to check SLO compliance
      const { _data, error } = await supabase.rpc('check_slo_compliance')

      if (error) {
        throw new Error(`SLO compliance check failed: ${error.message}`)
      }

      // Get current violations
      const { data: violations, error: violationsError } = await supabase
        .from('slo_violations')
        .select('*')
        .is('resolved_at', null)
        .order('last_violation_at', { ascending: false })

      if (violationsError) {
        throw new Error(`Failed to fetch SLO violations: ${violationsError.message}`)
      }

      const result: SLOComplianceResult = {
        violations: violations || [],
        summary: {
          total_violations: violations?.length || 0,
          warning_count: violations?.filter(v => v.severity === 'warning').length || 0,
          critical_count: violations?.filter(v => v.severity === 'critical').length || 0,
          compliance_rate: 100 // Will be calculated based on total checks
        }
      }

      // Alert on critical violations
      const criticalViolations = violations?.filter(v => v.severity === 'critical') || []
      if (criticalViolations.length > 0) {
        await this.alertCriticalViolations(criticalViolations)
      }

      return result
    } catch (error) {
      console.error('SLO compliance check failed:', error)
      throw error
    }
  }

  /**
   * Get SLO configuration
   */
  async getSLOConfig(): Promise<SLOConfig[]> {
    const { data, error } = await supabase
      .from('slo_config')
      .select('*')
      .order('metric_name')

    if (error) {
      throw new Error(`Failed to fetch SLO config: ${error.message}`)
    }

    return data || []
  }

  /**
   * Update SLO configuration
   */
  async updateSLOConfig(config: Partial<SLOConfig> & { metric_name: string }): Promise<void> {
    const { error } = await supabase
      .from('slo_config')
      .upsert(config, { onConflict: 'metric_name' })

    if (error) {
      throw new Error(`Failed to update SLO config: ${error.message}`)
    }
  }

  /**
   * Resolve SLO violation
   */
  async resolveViolation(violationId: number, resolvedBy: string): Promise<void> {
    const { error } = await supabase
      .from('slo_violations')
      .update({ 
        resolved_at: new Date().toISOString(),
        resolved_by: resolvedBy
      })
      .eq('id', violationId)

    if (error) {
      throw new Error(`Failed to resolve SLO violation: ${error.message}`)
    }
  }

  /**
   * Get violation history
   */
  async getViolationHistory(
    metricName?: string,
    severity?: 'warning' | 'critical',
    days: number = 7
  ): Promise<SLOViolation[]> {
    let query = supabase
      .from('slo_violations')
      .select('*')
      .gte('first_violation_at', new Date(Date.now() - days * 24 * 60 * 60 * 1000).toISOString())
      .order('first_violation_at', { ascending: false })

    if (metricName) {
      query = query.eq('metric_name', metricName)
    }

    if (severity) {
      query = query.eq('severity', severity)
    }

    const { data, error } = await query

    if (error) {
      throw new Error(`Failed to fetch violation history: ${error.message}`)
    }

    return data || []
  }

  /**
   * Alert on critical violations
   */
  private async alertCriticalViolations(violations: SLOViolation[]): Promise<void> {
    // Log critical violations
    console.error(`ðŸš¨ CRITICAL SLO VIOLATIONS DETECTED:`, violations)

    // TODO: Implement actual alerting (email, Slack, PagerDuty, etc.)
    // For now, just log to console and could send to error monitoring service

    // Example: Send to Sentry or other error monitoring
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    if (typeof window !== 'undefined' && (window as any).Sentry) {
      violations.forEach(violation => {
        // eslint-disable-next-line @typescript-eslint/no-explicit-any
        (window as any).Sentry.captureMessage(`Critical SLO Violation: ${violation.metric_name}`, {
          level: 'error',
          tags: {
            metric: violation.metric_name,
            type: violation.violation_type,
            endpoint: violation.endpoint,
            severity: violation.severity
          },
          extra: {
            current_value: violation.current_value,
            threshold_value: violation.threshold_value,
            violation_count: violation.violation_count
          }
        })
      })
    }
  }

  /**
   * Get compliance dashboard data
   */
  async getComplianceDashboard(): Promise<{
    currentStatus: 'healthy' | 'warning' | 'critical'
    metrics: Array<{
      name: string
      current_p95: number
      target_p95: number
      current_p99: number
      target_p99: number
      current_error_rate: number
      target_error_rate: number
      status: 'healthy' | 'warning' | 'critical'
    }>
    recentViolations: SLOViolation[]
  }> {
    try {
      const [config, violations] = await Promise.all([
        this.getSLOConfig(),
        this.getViolationHistory(undefined, undefined, 1) // Last 24 hours
      ])

      // Get current performance metrics (this would need to be implemented based on your metrics collection)
      const currentMetrics = await this.getCurrentPerformanceMetrics()

      const dashboard = {
        currentStatus: 'healthy' as 'healthy' | 'warning' | 'critical',
        metrics: config.map(cfg => {
          const current = currentMetrics.find(m => m.metric_name === cfg.metric_name)
          const status = this.calculateMetricStatus(cfg, current)

          return {
            name: cfg.metric_name,
            current_p95: current?.p95_ms || 0,
            target_p95: cfg.target_p95_ms,
            current_p99: current?.p99_ms || 0,
            target_p99: cfg.target_p99_ms,
            current_error_rate: current?.error_rate || 0,
            target_error_rate: cfg.target_error_rate,
            status
          }
        }),
        recentViolations: violations
      }

      // Determine overall status
      if (dashboard.metrics.some(m => m.status === 'critical')) {
        dashboard.currentStatus = 'critical'
      } else if (dashboard.metrics.some(m => m.status === 'warning')) {
        dashboard.currentStatus = 'warning'
      }

      return dashboard
    } catch (error) {
      console.error('Failed to get compliance dashboard:', error)
      throw error
    }
  }

  /**
   * Calculate metric status based on current values vs targets
   */
  private calculateMetricStatus(
    config: SLOConfig, 
    current?: { p95_ms: number; p99_ms: number; error_rate: number }
  ): 'healthy' | 'warning' | 'critical' {
    if (!current) return 'healthy'

    const p95Status = this.calculateThresholdStatus(
      current.p95_ms, 
      config.target_p95_ms, 
      config.warning_threshold_multiplier, 
      config.critical_threshold_multiplier
    )

    const p99Status = this.calculateThresholdStatus(
      current.p99_ms, 
      config.target_p99_ms, 
      config.warning_threshold_multiplier, 
      config.critical_threshold_multiplier
    )

    const errorRateStatus = this.calculateThresholdStatus(
      current.error_rate, 
      config.target_error_rate, 
      config.warning_threshold_multiplier, 
      config.critical_threshold_multiplier
    )

    if ([p95Status, p99Status, errorRateStatus].includes('critical')) {
      return 'critical'
    } else if ([p95Status, p99Status, errorRateStatus].includes('warning')) {
      return 'warning'
    }

    return 'healthy'
  }

  /**
   * Calculate threshold status for a single metric
   */
  private calculateThresholdStatus(
    current: number, 
    target: number, 
    warningMultiplier: number, 
    criticalMultiplier: number
  ): 'healthy' | 'warning' | 'critical' {
    if (current > target * criticalMultiplier) {
      return 'critical'
    } else if (current > target * warningMultiplier) {
      return 'warning'
    }
    return 'healthy'
  }

  /**
   * Get current performance metrics (placeholder - implement based on your metrics collection)
   */
  private async getCurrentPerformanceMetrics(): Promise<Array<{
    metric_name: string
    p95_ms: number
    p99_ms: number
    error_rate: number
  }>> {
    // This would need to be implemented based on how you collect performance metrics
    // For now, return empty array
    return []
  }
}

// Export singleton instance
export const sloMonitor = SLOMonitor.getInstance()

import { getServerSupabase } from '@/lib/supabaseServer'
import type { SLOConfig, SLOViolation } from './sloMonitor'

// eslint-disable-next-line @typescript-eslint/no-explicit-any
const supabase = new Proxy({} as any, { get: (target, prop) => (getServerSupabase() as any)[prop] }) as ReturnType<typeof getServerSupabase>

export interface ErrorBudget {
  total: number // Total error budget for the period
  consumed: number // Error budget consumed
  remaining: number // Error budget remaining
  burnRate: number // Current burn rate (errors per hour)
  timeToExhaustion?: number // Hours until budget exhausted (if burn rate continues)
  riskLevel: 'low' | 'medium' | 'high' | 'critical'
}

export interface SLORiskAssessment {
  riskLevel: 'low' | 'medium' | 'high' | 'critical'
  insideBudget: boolean
  errorBudget: ErrorBudget
  customerImpact: string
  humanAttentionNeeded: boolean
  recommendations: string[]
}

export interface LatencyMetrics {
  p50: number
  p95: number
  p99: number
  targetP95: number
  targetP99: number
  currentP95: number
  currentP99: number
  violations: SLOViolation[]
}

export interface ErrorMetrics {
  errorRate: number
  targetErrorRate: number
  totalRequests: number
  errorCount: number
  violations: SLOViolation[]
}

export interface AvailabilityMetrics {
  uptime: number
  targetUptime: number
  downtimeMinutes: number
  totalMinutes: number
  violations: SLOViolation[]
}

/**
 * SLOAI - SLO/SLA Interpreter for Bookiji
 * 
 * Evaluates SLO compliance, calculates error budgets, tracks burn rates,
 * and provides risk assessments with customer impact analysis.
 */
export class SLOAI {
  /**
   * Calculate error budget for a given SLO target
   * 
   * Error budget = (1 - SLO target) * time window
   * For example: 99.9% uptime over 30 days = 0.1% * 30 days = 43.2 minutes
   */
  calculateErrorBudget(
    sloTarget: number, // e.g., 0.999 for 99.9%
    timeWindowHours: number = 720 // 30 days default
  ): ErrorBudget {
    const totalBudget = (1 - sloTarget) * timeWindowHours * 60 // minutes
    const consumed = 0 // Will be calculated from actual violations
    const remaining = totalBudget - consumed
    const burnRate = 0 // Will be calculated from recent violations

    return {
      total: totalBudget,
      consumed,
      remaining,
      burnRate,
      riskLevel: 'low'
    }
  }

  /**
   * Calculate burn rate from recent violations
   */
  calculateBurnRate(
    violations: SLOViolation[],
    timeWindowHours: number = 1
  ): number {
    if (violations.length === 0) return 0

    // Count violations in the time window
    const now = new Date()
    const windowStart = new Date(now.getTime() - timeWindowHours * 60 * 60 * 1000)

    const recentViolations = violations.filter(v => {
      const violationTime = new Date(v.last_violation_at)
      return violationTime >= windowStart
    })

    // Burn rate = violations per hour
    return recentViolations.length / timeWindowHours
  }

  /**
   * Calculate time to exhaustion of error budget
   */
  calculateTimeToExhaustion(
    remainingBudget: number,
    burnRate: number
  ): number | undefined {
    if (burnRate <= 0) return undefined
    return remainingBudget / (burnRate * 60) // Convert to hours
  }

  /**
   * Assess risk level based on error budget consumption
   */
  assessRiskLevel(
    errorBudget: ErrorBudget,
    timeToExhaustion?: number
  ): 'low' | 'medium' | 'high' | 'critical' {
    const budgetConsumedPercent = (errorBudget.consumed / errorBudget.total) * 100

    // Critical: >80% consumed or will exhaust in <24 hours
    if (budgetConsumedPercent > 80 || (timeToExhaustion && timeToExhaustion < 24)) {
      return 'critical'
    }

    // High: >50% consumed or will exhaust in <7 days
    if (budgetConsumedPercent > 50 || (timeToExhaustion && timeToExhaustion < 168)) {
      return 'high'
    }

    // Medium: >25% consumed or will exhaust in <30 days
    if (budgetConsumedPercent > 25 || (timeToExhaustion && timeToExhaustion < 720)) {
      return 'medium'
    }

    return 'low'
  }

  /**
   * Get latency metrics for a specific metric name
   */
  async getLatencyMetrics(metricName: string): Promise<LatencyMetrics> {
    // Get SLO config
    const { data: configs, error: configError } = await supabase
      .from('slo_config')
      .select('*')
      .eq('metric_name', metricName)
      .single()

    if (configError || !configs) {
      throw new Error(`SLO config not found for ${metricName}`)
    }

    const config = configs as SLOConfig

    // Get recent performance metrics
    const { data: metrics, error: metricsError } = await supabase
      .from('performance_analytics_5min')
      .select('p95_response_time_ms, avg_response_time_ms, endpoint, five_minute_bucket')
      .gte('five_minute_bucket', new Date(Date.now() - 15 * 60 * 1000).toISOString())
      .order('five_minute_bucket', { ascending: false })
      .limit(100)

    if (metricsError) {
      console.warn('Failed to fetch performance metrics:', metricsError)
    }

    // Calculate percentiles from recent metrics
    // Use p95 from view as approximation, and avg for p50
    const responseTimes = (metrics || [])
      .map(m => m.p95_response_time_ms)
      .filter((t): t is number => typeof t === 'number')
      .sort((a, b) => a - b)

    const avgResponseTimes = (metrics || [])
      .map(m => m.avg_response_time_ms)
      .filter((t): t is number => typeof t === 'number')

    const p50 = avgResponseTimes.length > 0 
      ? avgResponseTimes.reduce((a, b) => a + b, 0) / avgResponseTimes.length 
      : 0
    const p95 = responseTimes[Math.floor(responseTimes.length * 0.95)] || responseTimes[responseTimes.length - 1] || 0
    // P99 is not available in the view, use p95 * 1.5 as approximation
    const p99 = p95 * 1.5

    // Get violations
    const { data: violations } = await supabase
      .from('slo_violations')
      .select('*')
      .eq('metric_name', metricName)
      .in('violation_type', ['p95', 'p99'])
      .is('resolved_at', null)
      .order('last_violation_at', { ascending: false })

    return {
      p50,
      p95,
      p99,
      targetP95: config.target_p95_ms,
      targetP99: config.target_p99_ms,
      currentP95: p95,
      currentP99: p99,
      violations: violations || []
    }
  }

  /**
   * Get error rate metrics for a specific metric name
   */
  async getErrorMetrics(metricName: string): Promise<ErrorMetrics> {
    // Get SLO config
    const { data: configs, error: configError } = await supabase
      .from('slo_config')
      .select('*')
      .eq('metric_name', metricName)
      .single()

    if (configError || !configs) {
      throw new Error(`SLO config not found for ${metricName}`)
    }

    const config = configs as SLOConfig

    // Get recent performance metrics from hourly view for better aggregation
    const { data: metrics, error: metricsError } = await supabase
      .from('performance_analytics_hourly')
      .select('request_count, error_count, error_rate_percent')
      .gte('hour', new Date(Date.now() - 60 * 60 * 1000).toISOString())
      .order('hour', { ascending: false })

    if (metricsError) {
      console.warn('Failed to fetch performance metrics:', metricsError)
    }

    // Aggregate metrics
    const totalRequests = (metrics || []).reduce((sum, m) => sum + (m.request_count || 0), 0)
    const totalErrors = (metrics || []).reduce((sum, m) => sum + (m.error_count || 0), 0)
    const errorRate = totalRequests > 0 ? totalErrors / totalRequests : 0

    // Get violations
    const { data: violations } = await supabase
      .from('slo_violations')
      .select('*')
      .eq('metric_name', metricName)
      .eq('violation_type', 'error_rate')
      .is('resolved_at', null)
      .order('last_violation_at', { ascending: false })

    return {
      errorRate,
      targetErrorRate: config.target_error_rate,
      totalRequests,
      errorCount: totalErrors,
      violations: violations || []
    }
  }

  /**
   * Get availability metrics
   */
  async getAvailabilityMetrics(): Promise<AvailabilityMetrics> {
    // Calculate uptime from recent performance metrics
    const oneDayAgo = new Date(Date.now() - 24 * 60 * 60 * 1000)

    const { data: metrics, error: metricsError } = await supabase
      .from('performance_analytics_hourly')
      .select('request_count, error_count')
      .gte('hour', oneDayAgo.toISOString())
      .order('hour', { ascending: false })

    if (metricsError) {
      console.warn('Failed to fetch performance metrics:', metricsError)
    }

    // Calculate uptime
    const totalRequests = (metrics || []).reduce((sum, m) => sum + (m.request_count || 0), 0)
    const totalErrors = (metrics || []).reduce((sum, m) => sum + (m.error_count || 0), 0)
    const successfulRequests = totalRequests - totalErrors
    const uptime = totalRequests > 0 ? (successfulRequests / totalRequests) * 100 : 100

    // Target uptime (99.9% default)
    const targetUptime = 99.9
    const totalMinutes = 24 * 60
    const downtimeMinutes = totalMinutes * (1 - uptime / 100)

    // Get violations
    const { data: violations } = await supabase
      .from('slo_violations')
      .select('*')
      .eq('violation_type', 'error_rate')
      .is('resolved_at', null)
      .order('last_violation_at', { ascending: false })

    return {
      uptime,
      targetUptime,
      downtimeMinutes,
      totalMinutes,
      violations: violations || []
    }
  }

  /**
   * Generate customer impact explanation
   */
  generateCustomerImpact(
    riskLevel: 'low' | 'medium' | 'high' | 'critical',
    metricType: 'latency' | 'errors' | 'availability',
    _currentValue: number,
    _targetValue: number
  ): string {
    const impactMap: Record<string, Record<string, string>> = {
      latency: {
        low: 'Response times are within acceptable limits. Users experience smooth interactions.',
        medium: 'Some users may notice slightly slower response times. Most interactions remain smooth.',
        high: 'Many users are experiencing noticeable delays. Booking flows may feel sluggish.',
        critical: 'Severe performance degradation. Users are experiencing significant delays that may cause frustration and abandonment.'
      },
      errors: {
        low: 'Error rates are minimal. Users can complete bookings reliably.',
        medium: 'Some users may encounter occasional errors. Most bookings complete successfully.',
        high: 'Elevated error rates. A significant portion of users may experience failed requests.',
        critical: 'Critical error rates. Many users cannot complete bookings. Immediate action required.'
      },
      availability: {
        low: 'Service is highly available. Users can access Bookiji reliably.',
        medium: 'Minor availability issues. Most users can access the service without problems.',
        high: 'Significant availability problems. Many users may experience service unavailability.',
        critical: 'Critical availability issues. Service may be partially or fully unavailable.'
      }
    }

    return impactMap[metricType]?.[riskLevel] || 'Status unknown.'
  }

  /**
   * Generate recommendations based on risk level
   */
  generateRecommendations(
    riskLevel: 'low' | 'medium' | 'high' | 'critical',
    _metricType: 'latency' | 'errors' | 'availability'
  ): string[] {
    const recommendations: string[] = []

    if (riskLevel === 'critical') {
      recommendations.push('ðŸš¨ IMMEDIATE ACTION REQUIRED: Escalate to on-call engineer')
      recommendations.push('Review recent deployments and consider rollback')
      recommendations.push('Check infrastructure health (database, cache, external services)')
      recommendations.push('Monitor error logs for root cause')
    } else if (riskLevel === 'high') {
      recommendations.push('âš ï¸ High risk detected: Schedule investigation within 4 hours')
      recommendations.push('Review performance metrics and identify degradation patterns')
      recommendations.push('Check for resource constraints (CPU, memory, database connections)')
    } else if (riskLevel === 'medium') {
      recommendations.push('ðŸ“Š Monitor closely: Review trends over next 24 hours')
      recommendations.push('Consider performance optimizations if trend continues')
    } else {
      recommendations.push('âœ… Within SLO targets: Continue monitoring')
    }

    return recommendations
  }

  /**
   * Assess overall SLO status
   */
  async assessOverallStatus(): Promise<SLORiskAssessment> {
    // Get all SLO configs
    const { data: configs } = await supabase
      .from('slo_config')
      .select('*')

    if (!configs || configs.length === 0) {
      return {
        riskLevel: 'low',
        insideBudget: true,
        errorBudget: {
          total: 0,
          consumed: 0,
          remaining: 0,
          burnRate: 0,
          riskLevel: 'low'
        },
        customerImpact: 'SLO monitoring not configured.',
        humanAttentionNeeded: false,
        recommendations: ['Configure SLO targets in slo_config table']
      }
    }

    // Get all unresolved violations
    const { data: violations } = await supabase
      .from('slo_violations')
      .select('*')
      .is('resolved_at', null)
      .order('last_violation_at', { ascending: false })

    const criticalViolations = (violations || []).filter(v => v.severity === 'critical')
    const warningViolations = (violations || []).filter(v => v.severity === 'warning')

    // Determine overall risk level
    let riskLevel: 'low' | 'medium' | 'high' | 'critical' = 'low'
    if (criticalViolations.length > 0) {
      riskLevel = 'critical'
    } else if (criticalViolations.length === 0 && warningViolations.length > 5) {
      riskLevel = 'high'
    } else if (warningViolations.length > 0) {
      riskLevel = 'medium'
    }

    // Calculate error budget (using 99.9% uptime target)
    const errorBudget = this.calculateErrorBudget(0.999, 720) // 30 days
    const burnRate = this.calculateBurnRate(violations || [], 24) // Last 24 hours
    const timeToExhaustion = this.calculateTimeToExhaustion(errorBudget.remaining, burnRate)
    errorBudget.burnRate = burnRate
    errorBudget.timeToExhaustion = timeToExhaustion
    errorBudget.riskLevel = this.assessRiskLevel(errorBudget, timeToExhaustion)

    const insideBudget = riskLevel === 'low' || riskLevel === 'medium'

    return {
      riskLevel,
      insideBudget,
      errorBudget,
      customerImpact: this.generateCustomerImpact(riskLevel, 'availability', 0, 99.9),
      humanAttentionNeeded: riskLevel === 'critical' || riskLevel === 'high',
      recommendations: this.generateRecommendations(riskLevel, 'availability')
    }
  }
}

// Export singleton instance
export const sloai = new SLOAI()

import { SupabaseClient } from '@supabase/supabase-js';

export interface PaginationParams {
  page?: number;
  limit?: number;
  sortBy?: string;
  sortOrder?: 'asc' | 'desc';
}

export interface PaginationResult<T> {
  data: T[];
  pagination: {
    page: number;
    limit: number;
    total: number;
    totalPages: number;
    hasNext: boolean;
    hasPrev: boolean;
  };
}

export interface PaginationConfig {
  maxLimit: number;
  defaultLimit: number;
  defaultSortBy?: string;
  defaultSortOrder: 'asc' | 'desc';
  allowedSortFields?: string[];
}

const DEFAULT_CONFIG: PaginationConfig = {
  maxLimit: 100,
  defaultLimit: 20,
  defaultSortOrder: 'desc',
  allowedSortFields: ['created_at', 'updated_at', 'name', 'id']
};

export class PaginationHelper {
  private config: PaginationConfig;

  constructor(config: Partial<PaginationConfig> = {}) {
    this.config = { ...DEFAULT_CONFIG, ...config };
  }

  /**
   * Validate and normalize pagination parameters
   */
  public validateParams(params: PaginationParams): Required<PaginationParams> {
    const page = Math.max(1, params.page || 1);
    const limit = Math.min(
      this.config.maxLimit,
      Math.max(1, params.limit || this.config.defaultLimit)
    );

    let sortBy = params.sortBy || this.config.defaultSortBy || 'created_at';

    // Validate sort field if allowedSortFields is specified
    if (this.config.allowedSortFields && !this.config.allowedSortFields.includes(sortBy)) {
      sortBy = this.config.allowedSortFields[0] || 'created_at';
    }

    const sortOrder = params.sortOrder || this.config.defaultSortOrder;

    return { page, limit, sortBy, sortOrder };
  }

  /**
   * Calculate offset for database queries
   */
  public calculateOffset(page: number, limit: number): number {
    return (page - 1) * limit;
  }

  /**
   * Build pagination metadata
   */
  public buildPaginationMeta(
    page: number,
    limit: number,
    total: number
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  ): PaginationResult<any>['pagination'] {
    const totalPages = Math.ceil(total / limit);

    return {
      page,
      limit,
      total,
      totalPages,
      hasNext: page < totalPages,
      hasPrev: page > 1
    };
  }

  /**
   * Apply pagination to a Supabase query
   */
  public applyToQuery<_T>(
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    query: any, // Supabase query builder
    params: PaginationParams,
    options: {
      // eslint-disable-next-line @typescript-eslint/no-explicit-any
      countQuery?: any; // Separate query for counting (optional)
      selectCount?: boolean; // Whether to include count in the main query
    } = {}
  ) {
    const { page, limit, sortBy, sortOrder } = this.validateParams(params);
    const offset = this.calculateOffset(page, limit);

    // Apply sorting
    query = query.order(sortBy, { ascending: sortOrder === 'asc' });

    // Apply pagination
    query = query.range(offset, offset + limit - 1);

    // If we need count, add it to the query
    if (options.selectCount !== false) {
      query = query.select('*', { count: 'exact' });
    }

    return {
      query,
      paginationParams: { page, limit, sortBy, sortOrder },
      offset
    };
  }

  /**
   * Execute a paginated query and return formatted result
   */
  public async executePaginatedQuery<T>(
    supabase: SupabaseClient,
    tableName: string,
    params: PaginationParams,
    options: {
      select?: string;
      // eslint-disable-next-line @typescript-eslint/no-explicit-any
      filters?: (query: any) => any;
      // eslint-disable-next-line @typescript-eslint/no-explicit-any
      countFilters?: (query: any) => any;
    } = {}
  ): Promise<PaginationResult<T>> {
    const { page, limit, sortBy, sortOrder } = this.validateParams(params);
    const offset = this.calculateOffset(page, limit);

    // Build base query
    let query = supabase
      .from(tableName)
      .select(options.select || '*', { count: 'exact' })
      .order(sortBy, { ascending: sortOrder === 'asc' })
      .range(offset, offset + limit - 1);

    // Apply filters if provided
    if (options.filters) {
      query = options.filters(query);
    }

    const { data, error, count } = await query;

    if (error) {
      throw new Error(`Pagination query failed: ${error.message}`);
    }

    const total = count || 0;
    const pagination = this.buildPaginationMeta(page, limit, total);

    return {
      data: (data as T[]) || [],
      pagination
    };
  }

  /**
   * Create pagination links/URLs
   */
  public createPaginationUrls(
    baseUrl: string,
    currentParams: Required<PaginationParams>,
    total: number
  ): {
    first: string;
    prev: string | null;
    next: string | null;
    last: string;
  } {
    const { page, limit, sortBy, sortOrder } = currentParams;
    const totalPages = Math.ceil(total / limit);

    const createUrl = (targetPage: number) => {
      const url = new URL(baseUrl);
      url.searchParams.set('page', targetPage.toString());
      url.searchParams.set('limit', limit.toString());
      url.searchParams.set('sortBy', sortBy);
      url.searchParams.set('sortOrder', sortOrder);
      return url.toString();
    };

    return {
      first: createUrl(1),
      prev: page > 1 ? createUrl(page - 1) : null,
      next: page < totalPages ? createUrl(page + 1) : null,
      last: createUrl(totalPages)
    };
  }
}

