// Utility function to extract pagination params from URL search params
export function extractPaginationParams(searchParams: URLSearchParams): PaginationParams {
  return {
    page: searchParams.get('page') ? parseInt(searchParams.get('page')!) : undefined,
    limit: searchParams.get('limit') ? parseInt(searchParams.get('limit')!) : undefined,
    sortBy: searchParams.get('sortBy') || undefined,
    sortOrder: (searchParams.get('sortOrder') as 'asc' | 'desc') || undefined
  };
}

// Utility function for cursor-based pagination (for very large datasets)
export interface CursorPaginationParams {
  cursor?: string;
  limit?: number;
  direction?: 'forward' | 'backward';
}

export interface CursorPaginationResult<T> {
  data: T[];
  nextCursor?: string;
  prevCursor?: string;
  hasNext: boolean;
  hasPrev: boolean;
}

export class CursorPagination {
  public static async execute<T extends { id: string; created_at: string }>(
    supabase: SupabaseClient,
    tableName: string,
    params: CursorPaginationParams,
    options: {
      select?: string;
      // eslint-disable-next-line @typescript-eslint/no-explicit-any
      filters?: (query: any) => any;
      cursorField?: string;
    } = {}
  ): Promise<CursorPaginationResult<T>> {
    const limit = Math.min(100, Math.max(1, params.limit || 20));
    const direction = params.direction || 'forward';
    const cursorField = options.cursorField || 'created_at';

    let query = supabase
      .from(tableName)
      .select(options.select || '*')
      .order(cursorField, { ascending: direction === 'forward' })
      .limit(limit + 1); // Fetch one extra to check if there's more

    // Apply cursor filtering
    if (params.cursor) {
      if (direction === 'forward') {
        query = query.gt(cursorField, params.cursor);
      } else {
        query = query.lt(cursorField, params.cursor);
      }
    }

    // Apply additional filters
    if (options.filters) {
      query = options.filters(query);
    }

    const { data, error } = await query;

    if (error) {
      throw new Error(`Cursor pagination query failed: ${error.message}`);
    }

    const items = (data as unknown as T[]) || [];
    const hasMore = items.length > limit;

    if (hasMore) {
      items.pop(); // Remove the extra item
    }

    const nextCursor = hasMore && items.length > 0 
      // eslint-disable-next-line @typescript-eslint/no-explicit-any
      ? (items[items.length - 1] as any)[cursorField] as string
      : undefined;

    const prevCursor = items.length > 0 
      // eslint-disable-next-line @typescript-eslint/no-explicit-any
      ? (items[0] as any)[cursorField] as string
      : undefined;

    return {
      data: items,
      nextCursor,
      prevCursor,
      hasNext: direction === 'forward' ? hasMore : !!params.cursor,
      hasPrev: direction === 'backward' ? hasMore : !!params.cursor
    };
  }
}

/**
 * Performance Alert Service
 * 
 * Handles sending performance alerts to various channels including
 * Slack, Teams, PagerDuty, and email.
 */

import { AlertChannelConfig, AlertSeverity, formatAlertMessage, getAlertConfig } from '@/config/performance'

export interface AlertPayload {
  violations: string[]
  severity: AlertSeverity
  environment: string
  timestamp: string
  metrics?: Record<string, number>
}

export class AlertService {
  private config: AlertChannelConfig
  
  constructor() {
    this.config = getAlertConfig()
  }

  /**
   * Send alert to all configured channels
   */
  async sendAlert(payload: AlertPayload): Promise<boolean> {
    if (!this.config.enabled) {
      console.log('Alerts disabled for this environment')
      return true
    }

    const results = await Promise.allSettled([
      this.sendSlackAlert(payload),
      this.sendTeamsAlert(payload),
      this.sendEmailAlert(payload),
      this.sendPagerDutyAlert(payload)
    ])

    // Return true if at least one alert was sent successfully
    return results.some(result => result.status === 'fulfilled')
  }

  /**
   * Send alert to Slack
   */
  private async sendSlackAlert(payload: AlertPayload): Promise<boolean> {
    if (!this.config.slack?.webhookUrl) {
      return false
    }

    try {
      const { title, message, color } = formatAlertMessage(
        payload.violations,
        payload.severity,
        payload.environment
      )

      const slackPayload = {
        channel: this.config.slack.channel,
        username: this.config.slack.username || 'Bookiji Monitor',
        icon_emoji: ':warning:',
        attachments: [{
          color: color,
          title: title,
          text: message,
          footer: 'Bookiji Performance Monitor',
          ts: Math.floor(Date.parse(payload.timestamp) / 1000),
          fields: payload.metrics ? Object.entries(payload.metrics).map(([key, value]) => ({
            title: key,
            value: value.toString(),
            short: true
          })) : []
        }]
      }

      const response = await fetch(this.config.slack.webhookUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(slackPayload)
      })

      return response.ok
    } catch (error) {
      console.error('Failed to send Slack alert:', error)
      return false
    }
  }

  /**
   * Send alert to Microsoft Teams
   */
  private async sendTeamsAlert(payload: AlertPayload): Promise<boolean> {
    if (!this.config.teams?.webhookUrl) {
      return false
    }

    try {
      const { title, message } = formatAlertMessage(
        payload.violations,
        payload.severity,
        payload.environment
      )

      const teamsPayload = {
        '@type': 'MessageCard',
        '@context': 'http://schema.org/extensions',
        summary: title,
        themeColor: payload.severity === AlertSeverity.CRITICAL ? 'FF0000' : 
                   payload.severity === AlertSeverity.ERROR ? 'FF4444' : 'FFA500',
        sections: [{
          activityTitle: title,
          activitySubtitle: `Environment: ${payload.environment}`,
          text: message,
          facts: payload.metrics ? Object.entries(payload.metrics).map(([key, value]) => ({
            name: key,
            value: value.toString()
          })) : []
        }]
      }

      const response = await fetch(this.config.teams.webhookUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(teamsPayload)
      })

      return response.ok
    } catch (error) {
      console.error('Failed to send Teams alert:', error)
      return false
    }
  }

  /**
   * Send email alert
   */
  private async sendEmailAlert(payload: AlertPayload): Promise<boolean> {
    if (!this.config.email) {
      return false
    }

    try {
      const { title, message } = formatAlertMessage(
        payload.violations,
        payload.severity,
        payload.environment
      )

      // In a real implementation, you would integrate with your email service
      // For now, we'll log it and potentially integrate with a service like SendGrid
      console.log('EMAIL ALERT:', { to: this.config.email, subject: title, body: message })
      
      // TODO: Integrate with actual email service
      // Example with SendGrid:
      // const sgMail = require('@sendgrid/mail')
      // sgMail.setApiKey(process.env.SENDGRID_API_KEY)
      // await sgMail.send({
      //   to: this.config.email,
      //   from: 'noreply@bookiji.com',
      //   subject: title,
      //   text: message
      // })

      return true
    } catch (error) {
      console.error('Failed to send email alert:', error)
      return false
    }
  }

  /**
   * Send PagerDuty alert
   */
  private async sendPagerDutyAlert(payload: AlertPayload): Promise<boolean> {
    if (!this.config.pagerduty?.integrationKey || payload.severity !== AlertSeverity.CRITICAL) {
      return false
    }

    try {
      const { title, _message } = formatAlertMessage(
        payload.violations,
        payload.severity,
        payload.environment
      )

      const pdPayload = {
        routing_key: this.config.pagerduty.integrationKey,
        event_action: 'trigger',
        payload: {
          summary: title,
          severity: this.config.pagerduty.severity,
          source: 'Bookiji Performance Monitor',
          component: 'Performance Guardrails',
          group: payload.environment,
          class: 'performance',
          custom_details: {
            violations: payload.violations,
            metrics: payload.metrics || {},
            environment: payload.environment,
            timestamp: payload.timestamp
          }
        }
      }

      const response = await fetch('https://events.pagerduty.com/v2/enqueue', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(pdPayload)
      })

      return response.ok
    } catch (error) {
      console.error('Failed to send PagerDuty alert:', error)
      return false
    }
  }

  /**
   * Test alert configuration
   */
  async testAlerts(): Promise<{ channel: string; success: boolean; error?: string }[]> {
    const testPayload: AlertPayload = {
      violations: ['Test alert - all systems normal'],
      severity: AlertSeverity.INFO,
      environment: process.env.DEPLOY_ENV || 'development',
      timestamp: new Date().toISOString(),
      metrics: { testMetric: 123 }
    }

    const results = []

    if (this.config.slack?.webhookUrl) {
      try {
        const success = await this.sendSlackAlert(testPayload)
        results.push({ channel: 'Slack', success })
      } catch (error) {
        results.push({ channel: 'Slack', success: false, error: String(error) })
      }
    }

    if (this.config.teams?.webhookUrl) {
      try {
        const success = await this.sendTeamsAlert(testPayload)
        results.push({ channel: 'Teams', success })
      } catch (error) {
        results.push({ channel: 'Teams', success: false, error: String(error) })
      }
    }

    if (this.config.email) {
      try {
        const success = await this.sendEmailAlert(testPayload)
        results.push({ channel: 'Email', success })
      } catch (error) {
        results.push({ channel: 'Email', success: false, error: String(error) })
      }
    }

    return results
  }
}

// Global instance
export const alertService = new AlertService()

// Helper function to send alerts from the performance guardrails
export async function sendPerformanceAlert(
  violations: string[],
  metrics?: Record<string, number>
): Promise<void> {
  try {
    const environment = process.env.DEPLOY_ENV || process.env.NODE_ENV || 'development'
    
    // Determine severity based on violations
    const severity = violations.some(v => v.includes('exceeds limit')) 
      ? AlertSeverity.ERROR 
      : AlertSeverity.WARNING

    await alertService.sendAlert({
      violations,
      severity,
      environment,
      timestamp: new Date().toISOString(),
      metrics
    })
  } catch (error) {
    console.error('Failed to send performance alert:', error)
  }
}

/**
 * Performance & Cost Guardrails
 * 
 * This module provides performance monitoring and cost control mechanisms
 * to prevent runaway costs and ensure application performance.
 */

import { getPerformanceConfig } from '@/config/performance'
import { sendPerformanceAlert } from './alertService'

export interface PerformanceMetrics {
  responseTime: number
  memoryUsage: number
  cpuUsage: number
  requestCount: number
  errorCount: number
  costEstimate: number
  timestamp?: number
}

export interface GuardrailConfig {
  maxResponseTime: number // milliseconds
  maxMemoryUsage: number // MB
  maxCpuUsage: number // percentage
  maxRequestRate: number // requests per minute
  maxErrorRate: number // percentage
  maxCostPerHour: number // USD
  alertThreshold: number // percentage of limit
}

export class PerformanceGuardrails {
  private metrics: PerformanceMetrics[] = []
  private config: GuardrailConfig
  private alerts: string[] = []
  private isEnabled: boolean = true

  constructor(config: Partial<GuardrailConfig> = {}) {
    // Use production configuration as base, then override with any provided config
    const productionConfig = getPerformanceConfig()
    this.config = {
      ...productionConfig,
      ...config
    }
  }

  /**
   * Record performance metrics
   */
  recordMetrics(metrics: Partial<PerformanceMetrics>) {
    if (!this.isEnabled) return

    const timestamp = Date.now()
    const fullMetrics: PerformanceMetrics = {
      responseTime: 0,
      memoryUsage: 0,
      cpuUsage: 0,
      requestCount: 0,
      errorCount: 0,
      costEstimate: 0,
      ...metrics,
      timestamp
    }

    this.metrics.push(fullMetrics)

    // Keep only last 1000 metrics to prevent memory bloat
    if (this.metrics.length > 1000) {
      this.metrics = this.metrics.slice(-1000)
    }

    // Check guardrails
    this.checkGuardrails(fullMetrics)
  }

  /**
   * Check if any guardrails are being violated
   */
  private checkGuardrails(metrics: PerformanceMetrics) {
    const violations: string[] = []

    if (metrics.responseTime > this.config.maxResponseTime) {
      violations.push(`Response time ${metrics.responseTime}ms exceeds limit ${this.config.maxResponseTime}ms`)
    }

    if (metrics.memoryUsage > this.config.maxMemoryUsage) {
      violations.push(`Memory usage ${metrics.memoryUsage}MB exceeds limit ${this.config.maxMemoryUsage}MB`)
    }

    if (metrics.cpuUsage > this.config.maxCpuUsage) {
      violations.push(`CPU usage ${metrics.cpuUsage}% exceeds limit ${this.config.maxCpuUsage}%`)
    }

    if (metrics.costEstimate > this.config.maxCostPerHour) {
      violations.push(`Cost estimate $${metrics.costEstimate}/hr exceeds limit $${this.config.maxCostPerHour}/hr`)
    }

    // Check rates over time
    const recentMetrics = this.metrics.slice(-60) // Last 60 seconds
    const requestRate = recentMetrics.reduce((sum, m) => sum + m.requestCount, 0)
    if (requestRate > this.config.maxRequestRate / 60) {
      violations.push(`Request rate ${requestRate}/min exceeds limit ${this.config.maxRequestRate}/min`)
    }

    const errorRate = recentMetrics.length > 0 
      ? (recentMetrics.reduce((sum, m) => sum + m.errorCount, 0) / requestRate) * 100
      : 0
    if (errorRate > this.config.maxErrorRate) {
      violations.push(`Error rate ${errorRate.toFixed(2)}% exceeds limit ${this.config.maxErrorRate}%`)
    }

    // Record violations
    if (violations.length > 0) {
      this.alerts.push(`[${new Date().toISOString()}] ${violations.join(', ')}`)
      console.warn('ðŸš¨ Performance guardrail violations:', violations)

      // Send alerts if configured
      this.sendAlerts(violations)
    }
  }

  /**
   * Send performance alerts
   */
  private async sendAlerts(violations: string[]) {
    try {
      // Extract metrics for alert context
      const latestMetric = this.metrics[this.metrics.length - 1]
      const metricsContext = latestMetric ? {
        responseTime: latestMetric.responseTime,
        memoryUsage: latestMetric.memoryUsage,
        cpuUsage: latestMetric.cpuUsage,
        requestCount: latestMetric.requestCount,
        errorCount: latestMetric.errorCount
      } : undefined

      // Send to alert service (Slack, PagerDuty, etc.)
      await sendPerformanceAlert(violations, metricsContext)

      // Send to analytics/error tracking
      if (typeof window !== 'undefined' && typeof fetch !== 'undefined') {
        // Client-side: send to analytics
        fetch('/api/analytics/track', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            event: 'performance_guardrail_violation',
            properties: {
              violations,
              timestamp: new Date().toISOString(),
              url: window.location.href,
              metrics: metricsContext
            }
          })
        }).catch(() => {
          // Silently fail if analytics endpoint is unavailable
        })
      } else {
        // Server-side: log to console and send to monitoring service
        console.error('ðŸš¨ Server-side performance guardrail violations:', violations)
      }
    } catch (error) {
      console.error('Failed to send performance alert:', error)
    }
  }

  /**
   * Get current performance summary
   */
  getPerformanceSummary(): {
    current: PerformanceMetrics | null
    average: Partial<PerformanceMetrics>
    violations: string[]
    recommendations: string[]
  } {
    if (this.metrics.length === 0) {
      return {
        current: null,
        average: {},
        violations: [],
        recommendations: []
      }
    }

    const current = this.metrics[this.metrics.length - 1]
    const recent = this.metrics.slice(-100) // Last 100 metrics

    const average = {
      responseTime: recent.reduce((sum, m) => sum + m.responseTime, 0) / recent.length,
      memoryUsage: recent.reduce((sum, m) => sum + m.memoryUsage, 0) / recent.length,
      cpuUsage: recent.reduce((sum, m) => sum + m.cpuUsage, 0) / recent.length,
      requestCount: recent.reduce((sum, m) => sum + m.requestCount, 0) / recent.length,
      errorCount: recent.reduce((sum, m) => sum + m.errorCount, 0) / recent.length,
      costEstimate: recent.reduce((sum, m) => sum + m.costEstimate, 0) / recent.length
    }

    const recommendations: string[] = []

    if (average.responseTime > this.config.maxResponseTime * 0.8) {
      recommendations.push('Consider optimizing database queries or adding caching')
    }

    if (average.memoryUsage > this.config.maxMemoryUsage * 0.8) {
      recommendations.push('Monitor memory leaks and consider memory optimization')
    }

    if (average.cpuUsage > this.config.maxCpuUsage * 0.8) {
      recommendations.push('Consider scaling horizontally or optimizing CPU-intensive operations')
    }

    const errorRate = average.requestCount > 0 ? (average.errorCount / average.requestCount) * 100 : 0
    if (errorRate > this.config.maxErrorRate * 0.8) {
      recommendations.push('Investigate error patterns and improve error handling')
    }

    return {
      current,
      average,
      violations: this.alerts.slice(-10), // Last 10 violations
      recommendations
    }
  }

  /**
   * Enable/disable guardrails
   */
  setEnabled(enabled: boolean) {
    this.isEnabled = enabled
  }

  /**
   * Update guardrail configuration
   */
  updateConfig(newConfig: Partial<GuardrailConfig>) {
    this.config = { ...this.config, ...newConfig }
  }

  /**
   * Clear all metrics and alerts
   */
  clear() {
    this.metrics = []
    this.alerts = []
  }
}

// Global instance
export const performanceGuardrails = new PerformanceGuardrails()

// Performance monitoring middleware
// eslint-disable-next-line @typescript-eslint/no-explicit-any
export function withPerformanceMonitoring<T extends any[], R>(
  fn: (...args: T) => R | Promise<R>,
  _operationName: string
): (...args: T) => Promise<R> {
  return async (...args: T): Promise<R> => {
    const startTime = Date.now()
    const startMemory = process.memoryUsage?.()?.heapUsed || 0

    try {
      const result = await fn(...args)

      // Record success metrics
      performanceGuardrails.recordMetrics({
        responseTime: Date.now() - startTime,
        memoryUsage: process.memoryUsage?.()?.heapUsed 
          ? (process.memoryUsage().heapUsed - startMemory) / 1024 / 1024 
          : 0,
        requestCount: 1,
        errorCount: 0,
        costEstimate: 0 // Could be calculated based on operation type
      })

      return result
    } catch (error) {
      // Record error metrics
      performanceGuardrails.recordMetrics({
        responseTime: Date.now() - startTime,
        memoryUsage: process.memoryUsage?.()?.heapUsed 
          ? (process.memoryUsage().heapUsed - startMemory) / 1024 / 1024 
          : 0,
        requestCount: 1,
        errorCount: 1,
        costEstimate: 0
      })

      throw error
    }
  }
}

// Cost estimation utilities
export class CostEstimator {
  private static readonly COST_PER_1000_REQUESTS = 0.01 // $0.01 per 1000 requests
  private static readonly COST_PER_GB_HOUR = 0.10 // $0.10 per GB-hour
  private static readonly COST_PER_CPU_HOUR = 0.05 // $0.05 per CPU-hour

  /**
   * Estimate cost for API operations
   */
  static estimateApiCost(requestCount: number): number {
    return (requestCount / 1000) * this.COST_PER_1000_REQUESTS
  }

  /**
   * Estimate cost for compute resources
   */
  static estimateComputeCost(memoryGB: number, cpuHours: number): number {
    return (memoryGB * this.COST_PER_GB_HOUR) + (cpuHours * this.COST_PER_CPU_HOUR)
  }

  /**
   * Get cost breakdown for current usage
   */
  static getCostBreakdown(metrics: PerformanceMetrics[]): {
    apiCost: number
    computeCost: number
    totalCost: number
  } {
    const totalRequests = metrics.reduce((sum, m) => sum + m.requestCount, 0)
    const totalMemoryHours = metrics.reduce((sum, m) => sum + (m.memoryUsage / 1024), 0) / 3600 // Convert to GB-hours
    const totalCpuHours = metrics.reduce((sum, m) => sum + (m.cpuUsage / 100), 0) / 3600 // Convert to CPU-hours

    const apiCost = this.estimateApiCost(totalRequests)
    const computeCost = this.estimateComputeCost(totalMemoryHours, totalCpuHours)

    return {
      apiCost,
      computeCost,
      totalCost: apiCost + computeCost
    }
  }
}

/**
 * RegressionAI - Bookiji's regression detection expert
 * 
 * Responsibilities:
 * - Compare current metrics to historical baselines
 * - Detect P95 degradations
 * - Identify booking flow slowdowns
 * - Detect error bumps after deploys
 * - Recommend investigation targets
 * 
 * Output style:
 * - Compare "Now vs Normal"
 * - Quantify impact
 */

import type { P95Metrics, BookingMetrics, ErrorMetrics, SystemMetrics } from '../metrics/metricsAI'

export interface Baseline {
  id: string
  metric_type: 'p95' | 'booking' | 'error' | 'system'
  endpoint?: string // For endpoint-specific baselines
  period: 'normal' | 'peak' | 'low'
  timestamp: string
  data: BaselineData
  metadata?: {
    deploy_id?: string
    commit_sha?: string
    notes?: string
  }
}

export interface BaselineData {
  // P95 baseline
  p95_latency_ms?: number
  p99_latency_ms?: number
  avg_latency_ms?: number

  // Booking baseline
  bookings_per_hour?: number
  conversion_rate?: number
  avg_booking_duration_ms?: number

  // Error baseline
  error_rate_percent?: number
  error_count_per_hour?: number

  // System baseline
  cpu_percent?: number
  memory_percent?: number
  cache_hit_rate?: number
}

export interface Regression {
  id: string
  metric_type: 'p95' | 'booking' | 'error' | 'system'
  endpoint?: string
  severity: 'low' | 'medium' | 'high' | 'critical'
  current_value: number
  baseline_value: number
  percent_change: number
  absolute_change: number
  impact: string
  detected_at: string
  related_deploy_id?: string
  recommendation: string[]
}

export interface RegressionReport {
  timestamp: string
  status: 'ok' | 'degraded' | 'critical'
  summary: string
  regressions: Regression[]
  booking_flow_slowdowns: Array<{
    endpoint: string
    current_p95_ms: number
    baseline_p95_ms: number
    slowdown_percent: number
    impact: string
  }>
  error_bumps: Array<{
    endpoint?: string
    current_error_rate: number
    baseline_error_rate: number
    increase_percent: number
    related_deploy_id?: string
    detected_after_deploy_hours?: number
  }>
  investigation_targets: Array<{
    priority: 'high' | 'medium' | 'low'
    metric_type: string
    endpoint?: string
    reason: string
    evidence: string[]
  }>
}

export class RegressionAI {
  /**
   * Detect regressions by comparing current metrics to baselines
   */
  static detectRegressions(
    currentMetrics: {
      p95?: P95Metrics[]
      booking?: BookingMetrics[]
      error?: ErrorMetrics[]
      system?: SystemMetrics[]
    },
    baselines: Baseline[]
  ): RegressionReport {
    const regressions: Regression[] = []
    const bookingFlowSlowdowns: RegressionReport['booking_flow_slowdowns'] = []
    const errorBumps: RegressionReport['error_bumps'] = []
    const investigationTargets: RegressionReport['investigation_targets'] = []

    // Detect P95 regressions
    if (currentMetrics.p95 && currentMetrics.p95.length > 0) {
      const p95Baselines = baselines.filter(b => b.metric_type === 'p95')
      const p95Regressions = this.detectP95Regressions(currentMetrics.p95, p95Baselines)
      regressions.push(...p95Regressions)

      // Identify booking flow slowdowns (critical booking endpoints)
      const bookingEndpoints = ['/api/bookings', '/api/bookings/create', '/api/quote', '/api/availability']
      for (const endpoint of bookingEndpoints) {
        const endpointMetrics = currentMetrics.p95.filter(m => m.endpoint.includes(endpoint))
        if (endpointMetrics.length > 0) {
          const endpointBaseline = p95Baselines.find(b => b.endpoint === endpoint || b.endpoint?.includes(endpoint))
          if (endpointBaseline && endpointBaseline.data.p95_latency_ms) {
            const currentP95 = this.calculateAverage(endpointMetrics, 'p95_latency_ms')
            const baselineP95 = endpointBaseline.data.p95_latency_ms
            const slowdownPercent = ((currentP95 - baselineP95) / baselineP95) * 100

            if (slowdownPercent > 20) {
              bookingFlowSlowdowns.push({
                endpoint,
                current_p95_ms: currentP95,
                baseline_p95_ms: baselineP95,
                slowdown_percent: slowdownPercent,
                impact: this.quantifyBookingImpact(slowdownPercent, currentP95)
              })
            }
          }
        }
      }
    }

    // Detect booking regressions
    if (currentMetrics.booking && currentMetrics.booking.length > 0) {
      const bookingBaselines = baselines.filter(b => b.metric_type === 'booking')
      const bookingRegressions = this.detectBookingRegressions(currentMetrics.booking, bookingBaselines)
      regressions.push(...bookingRegressions)
    }

    // Detect error regressions
    if (currentMetrics.error && currentMetrics.error.length > 0) {
      const errorBaselines = baselines.filter(b => b.metric_type === 'error')
      const errorRegressions = this.detectErrorRegressions(currentMetrics.error, errorBaselines)
      regressions.push(...errorRegressions)

      // Detect error bumps after deploys
      const deployBaselines = baselines.filter(b => b.metadata?.deploy_id)
      for (const baseline of deployBaselines) {
        if (baseline.metric_type === 'error' && baseline.data.error_rate_percent) {
          const currentErrorRate = this.calculateAverage(currentMetrics.error, 'error_rate_percent')
          const baselineErrorRate = baseline.data.error_rate_percent
          const increasePercent = ((currentErrorRate - baselineErrorRate) / baselineErrorRate) * 100

          if (increasePercent > 50) {
            errorBumps.push({
              endpoint: baseline.endpoint,
              current_error_rate: currentErrorRate,
              baseline_error_rate: baselineErrorRate,
              increase_percent: increasePercent,
              related_deploy_id: baseline.metadata?.deploy_id,
              detected_after_deploy_hours: this.hoursSinceBaseline(baseline.timestamp)
            })
          }
        }
      }
    }

    // Detect system regressions
    if (currentMetrics.system && currentMetrics.system.length > 0) {
      const systemBaselines = baselines.filter(b => b.metric_type === 'system')
      const systemRegressions = this.detectSystemRegressions(currentMetrics.system, systemBaselines)
      regressions.push(...systemRegressions)
    }

    // Generate investigation targets
    investigationTargets.push(...this.generateInvestigationTargets(regressions, bookingFlowSlowdowns, errorBumps))

    // Determine overall status
    const criticalCount = regressions.filter(r => r.severity === 'critical').length
    const highCount = regressions.filter(r => r.severity === 'high').length
    const status = criticalCount > 0 ? 'critical' : highCount > 0 ? 'degraded' : 'ok'

    // Generate summary
    const summary = this.generateSummary(regressions, bookingFlowSlowdowns, errorBumps, status)

    return {
      timestamp: new Date().toISOString(),
      status,
      summary,
      regressions,
      booking_flow_slowdowns: bookingFlowSlowdowns,
      error_bumps: errorBumps,
      investigation_targets: investigationTargets
    }
  }

  /**
   * Detect P95 latency regressions
   */
  private static detectP95Regressions(
    current: P95Metrics[],
    baselines: Baseline[]
  ): Regression[] {
    const regressions: Regression[] = []

    // Group current metrics by endpoint
    const endpointGroups = this.groupP95ByEndpoint(current)

    for (const [endpointKey, metrics] of endpointGroups.entries()) {
      const [_method, ...endpointParts] = endpointKey.split(' ')
      const endpoint = endpointParts.join(' ')

      // Find matching baseline
      const baseline = baselines.find(b => 
        b.endpoint === endpoint || 
        b.endpoint === endpointKey ||
        (!b.endpoint && baselines.length === 1) // Use global baseline if no endpoint-specific
      )

      if (!baseline || !baseline.data.p95_latency_ms) {
        continue
      }

      const currentP95 = this.calculateAverage(metrics, 'p95_latency_ms')
      const baselineP95 = baseline.data.p95_latency_ms
      const percentChange = ((currentP95 - baselineP95) / baselineP95) * 100
      const absoluteChange = currentP95 - baselineP95

      // Threshold: >30% increase or >200ms absolute increase
      if (percentChange > 30 || absoluteChange > 200) {
        const severity = percentChange > 100 || absoluteChange > 500 
          ? 'critical' 
          : percentChange > 50 || absoluteChange > 300 
          ? 'high' 
          : 'medium'

        regressions.push({
          id: `p95-${endpointKey}-${Date.now()}`,
          metric_type: 'p95',
          endpoint,
          severity,
          current_value: currentP95,
          baseline_value: baselineP95,
          percent_change: percentChange,
          absolute_change: absoluteChange,
          impact: this.quantifyP95Impact(percentChange, currentP95),
          detected_at: new Date().toISOString(),
          related_deploy_id: baseline.metadata?.deploy_id,
          recommendation: this.generateP95Recommendations(endpoint, percentChange, currentP95)
        })
      }
    }

    return regressions
  }

  /**
   * Detect booking flow regressions
   */
  private static detectBookingRegressions(
    current: BookingMetrics[],
    baselines: Baseline[]
  ): Regression[] {
    const regressions: Regression[] = []

    const baseline = baselines.find(b => b.metric_type === 'booking' && b.period === 'normal')
    if (!baseline || !baseline.data.bookings_per_hour) {
      return regressions
    }

    // Calculate current bookings per hour
    const totalBookings = current.reduce((sum, m) => sum + m.bookings_created, 0)
    const hours = current.length * (5 / 60) // 5-minute buckets
    const currentBookingsPerHour = hours > 0 ? totalBookings / hours : 0

    const baselineBookingsPerHour = baseline.data.bookings_per_hour
    const percentChange = baselineBookingsPerHour > 0 
      ? ((currentBookingsPerHour - baselineBookingsPerHour) / baselineBookingsPerHour) * 100 
      : 0

    // Threshold: >20% decrease
    if (percentChange < -20) {
      regressions.push({
        id: `booking-${Date.now()}`,
        metric_type: 'booking',
        severity: percentChange < -50 ? 'critical' : 'high',
        current_value: currentBookingsPerHour,
        baseline_value: baselineBookingsPerHour,
        percent_change: percentChange,
        absolute_change: currentBookingsPerHour - baselineBookingsPerHour,
        impact: `Booking throughput decreased by ${Math.abs(percentChange).toFixed(1)}%`,
        detected_at: new Date().toISOString(),
        recommendation: [
          'Check booking API endpoints for errors',
          'Review payment processing flow',
          'Investigate user abandonment points',
          'Check for service degradation'
        ]
      })
    }

    // Check conversion rate
    if (baseline.data.conversion_rate) {
      const totalCreated = current.reduce((sum, m) => sum + m.bookings_created, 0)
      const totalConfirmed = current.reduce((sum, m) => sum + m.bookings_confirmed, 0)
      const currentConversionRate = totalCreated > 0 ? (totalConfirmed / totalCreated) * 100 : 0
      const baselineConversionRate = baseline.data.conversion_rate
      const conversionChange = currentConversionRate - baselineConversionRate

      if (conversionChange < -10) {
        regressions.push({
          id: `booking-conversion-${Date.now()}`,
          metric_type: 'booking',
          severity: conversionChange < -20 ? 'critical' : 'high',
          current_value: currentConversionRate,
          baseline_value: baselineConversionRate,
          percent_change: conversionChange,
          absolute_change: conversionChange,
          impact: `Conversion rate dropped by ${Math.abs(conversionChange).toFixed(1)}%`,
          detected_at: new Date().toISOString(),
          recommendation: [
            'Review booking confirmation flow',
            'Check payment processing errors',
            'Investigate user drop-off points',
            'Review error logs for booking endpoints'
          ]
        })
      }
    }

    return regressions
  }

  /**
   * Detect error rate regressions
   */
  private static detectErrorRegressions(
    current: ErrorMetrics[],
    baselines: Baseline[]
  ): Regression[] {
    const regressions: Regression[] = []

    const baseline = baselines.find(b => b.metric_type === 'error' && b.period === 'normal')
    if (!baseline || !baseline.data.error_rate_percent) {
      return regressions
    }

    const currentErrorRate = this.calculateAverage(current, 'error_rate_percent')
    const baselineErrorRate = baseline.data.error_rate_percent
    const percentChange = baselineErrorRate > 0 
      ? ((currentErrorRate - baselineErrorRate) / baselineErrorRate) * 100 
      : 0

    // Threshold: >50% increase or >2% absolute increase
    if (percentChange > 50 || (currentErrorRate - baselineErrorRate) > 2) {
      regressions.push({
        id: `error-${Date.now()}`,
        metric_type: 'error',
        severity: percentChange > 200 || currentErrorRate > 10 
          ? 'critical' 
          : percentChange > 100 || currentErrorRate > 5 
          ? 'high' 
          : 'medium',
        current_value: currentErrorRate,
        baseline_value: baselineErrorRate,
        percent_change: percentChange,
        absolute_change: currentErrorRate - baselineErrorRate,
        impact: `Error rate increased by ${percentChange.toFixed(1)}% (${currentErrorRate.toFixed(2)}% vs ${baselineErrorRate.toFixed(2)}%)`,
        detected_at: new Date().toISOString(),
        related_deploy_id: baseline.metadata?.deploy_id,
        recommendation: [
          'Review error logs for patterns',
          'Check recent deployments',
          'Investigate top error endpoints',
          'Review database connection issues',
          'Check external service dependencies'
        ]
      })
    }

    return regressions
  }

  /**
   * Detect system resource regressions
   */
  private static detectSystemRegressions(
    current: SystemMetrics[],
    baselines: Baseline[]
  ): Regression[] {
    const regressions: Regression[] = []

    const baseline = baselines.find(b => b.metric_type === 'system' && b.period === 'normal')
    if (!baseline) {
      return regressions
    }

    // Check CPU
    if (baseline.data.cpu_percent) {
      const currentCpu = this.calculateAverage(current, 'cpu_percent')
      const baselineCpu = baseline.data.cpu_percent
      const percentChange = ((currentCpu - baselineCpu) / baselineCpu) * 100

      if (percentChange > 30 && currentCpu > 70) {
        regressions.push({
          id: `system-cpu-${Date.now()}`,
          metric_type: 'system',
          severity: currentCpu > 90 ? 'critical' : 'high',
          current_value: currentCpu,
          baseline_value: baselineCpu,
          percent_change: percentChange,
          absolute_change: currentCpu - baselineCpu,
          impact: `CPU usage increased by ${percentChange.toFixed(1)}% (${currentCpu.toFixed(1)}% vs ${baselineCpu.toFixed(1)}%)`,
          detected_at: new Date().toISOString(),
          recommendation: [
            'Check for runaway processes',
            'Review database query performance',
            'Investigate API endpoint load',
            'Check for resource-intensive operations'
          ]
        })
      }
    }

    // Check memory
    if (baseline.data.memory_percent) {
      const currentMemory = this.calculateAverage(current, 'memory_percent')
      const baselineMemory = baseline.data.memory_percent
      const percentChange = ((currentMemory - baselineMemory) / baselineMemory) * 100

      if (percentChange > 30 && currentMemory > 80) {
        regressions.push({
          id: `system-memory-${Date.now()}`,
          metric_type: 'system',
          severity: currentMemory > 90 ? 'critical' : 'high',
          current_value: currentMemory,
          baseline_value: baselineMemory,
          percent_change: percentChange,
          absolute_change: currentMemory - baselineMemory,
          impact: `Memory usage increased by ${percentChange.toFixed(1)}% (${currentMemory.toFixed(1)}% vs ${baselineMemory.toFixed(1)}%)`,
          detected_at: new Date().toISOString(),
          recommendation: [
            'Check for memory leaks',
            'Review cache sizes',
            'Investigate connection pool usage',
            'Check for large data processing operations'
          ]
        })
      }
    }

    return regressions
  }

  /**
   * Generate investigation targets based on regressions
   */
  private static generateInvestigationTargets(
    regressions: Regression[],
    bookingSlowdowns: RegressionReport['booking_flow_slowdowns'],
    errorBumps: RegressionReport['error_bumps']
  ): RegressionReport['investigation_targets'] {
    const targets: RegressionReport['investigation_targets'] = []

    // Critical P95 regressions
    const criticalP95 = regressions.filter(r => r.metric_type === 'p95' && r.severity === 'critical')
    for (const regression of criticalP95) {
      targets.push({
        priority: 'high',
        metric_type: 'p95',
        endpoint: regression.endpoint,
        reason: `Critical P95 degradation: ${regression.percent_change.toFixed(1)}% increase`,
        evidence: [
          `Current: ${regression.current_value.toFixed(0)}ms`,
          `Baseline: ${regression.baseline_value.toFixed(0)}ms`,
          `Impact: ${regression.impact}`
        ]
      })
    }

    // Booking flow slowdowns
    for (const slowdown of bookingSlowdowns) {
      targets.push({
        priority: 'high',
        metric_type: 'booking_flow',
        endpoint: slowdown.endpoint,
        reason: `Booking flow slowdown: ${slowdown.slowdown_percent.toFixed(1)}% slower`,
        evidence: [
          `Current P95: ${slowdown.current_p95_ms.toFixed(0)}ms`,
          `Baseline P95: ${slowdown.baseline_p95_ms.toFixed(0)}ms`,
          `Impact: ${slowdown.impact}`
        ]
      })
    }

    // Error bumps after deploys
    for (const errorBump of errorBumps) {
      targets.push({
        priority: errorBump.increase_percent > 200 ? 'high' : 'medium',
        metric_type: 'error',
        endpoint: errorBump.endpoint,
        reason: `Error spike after deploy: ${errorBump.increase_percent.toFixed(1)}% increase`,
        evidence: [
          `Current error rate: ${errorBump.current_error_rate.toFixed(2)}%`,
          `Baseline error rate: ${errorBump.baseline_error_rate.toFixed(2)}%`,
          `Deploy ID: ${errorBump.related_deploy_id || 'unknown'}`,
          `Detected ${errorBump.detected_after_deploy_hours?.toFixed(1)}h after deploy`
        ]
      })
    }

    // High severity regressions
    const highSeverity = regressions.filter(r => r.severity === 'high')
    for (const regression of highSeverity.slice(0, 5)) {
      targets.push({
        priority: 'medium',
        metric_type: regression.metric_type,
        endpoint: regression.endpoint,
        reason: `High severity regression: ${regression.percent_change.toFixed(1)}% change`,
        evidence: [
          `Current: ${regression.current_value.toFixed(2)}`,
          `Baseline: ${regression.baseline_value.toFixed(2)}`,
          `Impact: ${regression.impact}`
        ]
      })
    }

    return targets
  }

  // Helper methods

  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  private static calculateAverage(metrics: any[], field: string): number {
    const values = metrics.map(m => m[field]).filter(v => v != null && !isNaN(v) && v > 0)
    if (values.length === 0) return 0
    return values.reduce((sum, v) => sum + v, 0) / values.length
  }

  private static groupP95ByEndpoint(metrics: P95Metrics[]): Map<string, P95Metrics[]> {
    const groups = new Map<string, P95Metrics[]>()
    for (const metric of metrics) {
      const key = `${metric.method} ${metric.endpoint}`
      if (!groups.has(key)) {
        groups.set(key, [])
      }
      groups.get(key)!.push(metric)
    }
    return groups
  }

  private static quantifyP95Impact(percentChange: number, currentP95: number): string {
    if (percentChange > 100) {
      return `Critical: P95 latency more than doubled (${currentP95.toFixed(0)}ms)`
    } else if (percentChange > 50) {
      return `High: P95 latency increased significantly (${currentP95.toFixed(0)}ms)`
    } else if (percentChange > 30) {
      return `Moderate: P95 latency increased (${currentP95.toFixed(0)}ms)`
    }
    return `P95 latency at ${currentP95.toFixed(0)}ms`
  }

  private static quantifyBookingImpact(slowdownPercent: number, currentP95: number): string {
    if (slowdownPercent > 100) {
      return `Critical: Booking flow more than 2x slower, likely causing user abandonment`
    } else if (slowdownPercent > 50) {
      return `High: Booking flow significantly slower, may impact conversion`
    } else if (slowdownPercent > 20) {
      return `Moderate: Booking flow slower, monitor for user impact`
    }
    return `Booking flow at ${currentP95.toFixed(0)}ms`
  }

  private static generateP95Recommendations(endpoint: string, percentChange: number, currentP95: number): string[] {
    const recommendations: string[] = []

    if (percentChange > 100) {
      recommendations.push('URGENT: Investigate immediately - latency more than doubled')
    }

    recommendations.push(`Profile ${endpoint} endpoint handler`)
    recommendations.push('Review database queries for N+1 patterns')
    recommendations.push('Check for missing database indexes')
    recommendations.push('Review cache hit rates for this endpoint')
    recommendations.push('Check external API dependencies')

    if (currentP95 > 1000) {
      recommendations.push('Consider adding request timeout handling')
      recommendations.push('Review async operation patterns')
    }

    return recommendations
  }

  private static hoursSinceBaseline(timestamp: string): number {
    const baselineTime = new Date(timestamp).getTime()
    const now = Date.now()
    return (now - baselineTime) / (1000 * 60 * 60)
  }

  private static generateSummary(
    regressions: Regression[],
    bookingSlowdowns: RegressionReport['booking_flow_slowdowns'],
    errorBumps: RegressionReport['error_bumps'],
    status: RegressionReport['status']
  ): string {
    const parts: string[] = []

    if (status === 'ok') {
      return 'No regressions detected. System performance is within normal baselines.'
    }

    parts.push(`Detected ${regressions.length} regression(s)`)

    if (bookingSlowdowns.length > 0) {
      parts.push(`${bookingSlowdowns.length} booking flow slowdown(s)`)
    }

    if (errorBumps.length > 0) {
      parts.push(`${errorBumps.length} error spike(s) after deploy(s)`)
    }

    const criticalCount = regressions.filter(r => r.severity === 'critical').length
    if (criticalCount > 0) {
      parts.push(`${criticalCount} critical regression(s) requiring immediate attention`)
    }

    return parts.join(', ') + '.'
  }
}

import { createClient } from '@supabase/supabase-js'
import { aiModeration, ModerationResult, ReviewData } from './aiModeration'

// Initialize Supabase client
import { supabaseAdmin as supabase } from '@/lib/supabaseProxies';

export interface ModerationAuditLog {
  id: string
  review_id: string
  moderator_id?: string
  action: 'auto_flag' | 'manual_review' | 'approve' | 'reject' | 'flag' | 'remove'
  reason: string
  confidence_score: number
  ai_categories: string[]
  keyword_matches: string[]
  behavioral_flags: string[]
  processing_time_ms: number
  created_at: string
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  metadata: Record<string, any>
}

export interface ModerationMetrics {
  total_reviews: number
  flagged_reviews: number
  auto_flagged: number
  manual_reviews: number
  false_positives: number
  false_negatives: number
  average_processing_time: number
  ai_accuracy: number
  category_distribution: Record<string, number>
}

export interface ReviewModerationEvent {
  type: 'review_submitted' | 'auto_flagged' | 'manual_review' | 'decision_made'
  review_id: string
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  data: Record<string, any>
  timestamp: string
}

export class ReviewMonitoringService {
  private static instance: ReviewMonitoringService

  static getInstance(): ReviewMonitoringService {
    if (!ReviewMonitoringService.instance) {
      ReviewMonitoringService.instance = new ReviewMonitoringService()
    }
    return ReviewMonitoringService.instance
  }

  /**
   * Log moderation decision for audit trail
   */
  async logModerationDecision(
    reviewId: string,
    action: ModerationAuditLog['action'],
    reason: string,
    confidenceScore: number,
    aiCategories: string[],
    keywordMatches: string[],
    behavioralFlags: string[],
    processingTimeMs: number,
    moderatorId?: string,
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    metadata: Record<string, any> = {}
  ): Promise<void> {
    try {
      const auditLog: Omit<ModerationAuditLog, 'id' | 'created_at'> = {
        review_id: reviewId,
        moderator_id: moderatorId,
        action,
        reason,
        confidence_score: confidenceScore,
        ai_categories: aiCategories,
        keyword_matches: keywordMatches,
        behavioral_flags: behavioralFlags,
        processing_time_ms: processingTimeMs,
        metadata
      }

      const { error } = await supabase
        .from('review_moderation_log')
        .insert(auditLog)

      if (error) {
        console.error('Failed to log moderation decision:', error)
        // Fallback to console logging
        console.log('Moderation Audit Log:', auditLog)
      }
    } catch (error) {
      console.error('Error logging moderation decision:', error)
    }
  }

  /**
   * Process review with comprehensive monitoring
   */
  async processReviewWithMonitoring(
    reviewData: ReviewData,
    reviewId: string
  ): Promise<ModerationResult> {
    const startTime = Date.now()

    try {
      // Get reviewer history for behavioral analysis
      const reviewerHistory = await this.getReviewerHistory(reviewData.reviewerId)

      // Enhanced review data with history
      const enhancedReviewData: ReviewData = {
        ...reviewData,
        reviewerHistory
      }

      // Run AI moderation
      const moderationResult = await aiModeration.moderateReview(enhancedReviewData)

      const processingTime = Date.now() - startTime

      // Extract categories and flags for logging
      const aiCategories = Object.keys(moderationResult.categories).filter(
        key => moderationResult.categories[key as keyof typeof moderationResult.categories]
      )

      const keywordMatches = moderationResult.reasons.filter(
        reason => reason.includes('pattern:') || reason.includes('Detected')
      )

      const behavioralFlags = moderationResult.reasons.filter(
        reason => reason.includes('Excessive') || reason.includes('All') || reason.includes('Suspicious')
      )

      // Log the moderation decision
      await this.logModerationDecision(
        reviewId,
        moderationResult.isFlagged ? 'auto_flag' : 'approve',
        moderationResult.reasons.join('; '),
        moderationResult.confidence,
        aiCategories,
        keywordMatches,
        behavioralFlags,
        processingTime,
        undefined, // Auto-moderation
        {
          original_rating: reviewData.rating,
          text_length: reviewData.text.length,
          reviewer_total_reviews: reviewerHistory.totalReviews,
          reviewer_avg_rating: reviewerHistory.avgRating
        }
      )

      // Emit monitoring event
      this.emitModerationEvent({
        type: moderationResult.isFlagged ? 'auto_flagged' : 'review_submitted',
        review_id: reviewId,
        data: {
          confidence: moderationResult.confidence,
          categories: aiCategories,
          processing_time: processingTime,
          suggested_action: moderationResult.suggestedAction
        },
        timestamp: new Date().toISOString()
      })

      return moderationResult
    } catch (error) {
      const processingTime = Date.now() - startTime

      // Log error for monitoring
      await this.logModerationDecision(
        reviewId,
        'manual_review',
        `Moderation failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
        0,
        [],
        [],
        [],
        processingTime,
        undefined,
        { error: true, error_message: error instanceof Error ? error.message : 'Unknown error' }
      )

      // Emit error event
      this.emitModerationEvent({
        type: 'manual_review',
        review_id: reviewId,
        data: {
          error: true,
          error_message: error instanceof Error ? error.message : 'Unknown error',
          processing_time: processingTime
        },
        timestamp: new Date().toISOString()
      })

      // Return fallback result
      return {
        isFlagged: false,
        confidence: 0,
        categories: {
          spam: false,
          inappropriate: false,
          fake: false,
          harassment: false,
          other: false
        },
        reasons: ['Moderation failed, manual review required'],
        suggestedAction: 'review',
        score: 0
      }
    }
  }

  /**
   * Get reviewer history for behavioral analysis
   */
  private async getReviewerHistory(reviewerId: string) {
    try {
      const { data: reviews, error } = await supabase
        .from('reviews')
        .select('rating, created_at, comment')
        .eq('reviewer_id', reviewerId)
        .order('created_at', { ascending: false })

      if (error) {
        console.warn('Failed to fetch reviewer history:', error)
        return {
          totalReviews: 0,
          avgRating: 0,
          recentReviews: 0,
          suspiciousPatterns: []
        }
      }

      const totalReviews = reviews?.length || 0
      const avgRating = reviews && reviews.length > 0 
        ? reviews.reduce((sum, r) => sum + (r.rating || 0), 0) / reviews.length 
        : 0

      // Count recent reviews (last 30 days)
      const thirtyDaysAgo = new Date(Date.now() - 30 * 24 * 60 * 60 * 1000)
      const recentReviews = reviews?.filter(r => new Date(r.created_at) > thirtyDaysAgo).length || 0

      // Detect suspicious patterns
      const suspiciousPatterns: string[] = []

      if (reviews && reviews.length > 0) {
        // All same rating
        const allSameRating = reviews.every(r => r.rating === reviews[0].rating)
        if (allSameRating && reviews.length > 3) {
          suspiciousPatterns.push(`All ${reviews[0].rating}-star reviews`)
        }

        // Very short reviews
        const shortReviews = reviews.filter(r => r.comment && r.comment.length < 10).length
        if (shortReviews > reviews.length * 0.5) {
          suspiciousPatterns.push('High percentage of very short reviews')
        }

        // Rapid posting
        if (recentReviews > 10) {
          suspiciousPatterns.push('Excessive recent review activity')
        }
      }

      return {
        totalReviews,
        avgRating,
        recentReviews,
        suspiciousPatterns
      }
    } catch (error) {
      console.warn('Error fetching reviewer history:', error)
      return {
        totalReviews: 0,
        avgRating: 0,
        recentReviews: 0,
        suspiciousPatterns: []
      }
    }
  }

  /**
   * Get moderation metrics for dashboard
   */
  async getModerationMetrics(timeRange: '24h' | '7d' | '30d' = '7d'): Promise<ModerationMetrics> {
    try {
      const startDate = new Date()
      switch (timeRange) {
        case '24h':
          startDate.setDate(startDate.getDate() - 1)
          break
        case '7d':
          startDate.setDate(startDate.getDate() - 7)
          break
        case '30d':
          startDate.setDate(startDate.getDate() - 30)
          break
      }

      // Get review counts
      const { count: totalReviews } = await supabase
        .from('reviews')
        .select('*', { count: 'exact', head: true })
        .gte('created_at', startDate.toISOString())

      const { count: flaggedReviews } = await supabase
        .from('reviews')
        .select('*', { count: 'exact', head: true })
        .gte('created_at', startDate.toISOString())
        .in('status', ['flagged', 'pending'])

      // Get moderation log data
      const { data: moderationLogs } = await supabase
        .from('review_moderation_log')
        .select('*')
        .gte('created_at', startDate.toISOString())

      // Calculate metrics
      const autoFlagged = moderationLogs?.filter(log => log.action === 'auto_flag').length || 0
      const manualReviews = moderationLogs?.filter(log => log.action === 'manual_review').length || 0

      const averageProcessingTime = moderationLogs && moderationLogs.length > 0
        ? moderationLogs.reduce((sum, log) => sum + log.processing_time_ms, 0) / moderationLogs.length
        : 0

      // Calculate category distribution
      const categoryDistribution: Record<string, number> = {}
      moderationLogs?.forEach(log => {
        log.ai_categories.forEach((category: string) => {
          categoryDistribution[category] = (categoryDistribution[category] || 0) + 1
        })
      })

      // Estimate AI accuracy (this would need manual review data for true accuracy)
      const aiAccuracy = totalReviews ? ((totalReviews - manualReviews) / totalReviews) * 100 : 0

      return {
        total_reviews: totalReviews || 0,
        flagged_reviews: flaggedReviews || 0,
        auto_flagged: autoFlagged,
        manual_reviews: manualReviews,
        false_positives: 0, // Would need manual review data
        false_negatives: 0, // Would need manual review data
        average_processing_time: averageProcessingTime,
        ai_accuracy: aiAccuracy,
        category_distribution: categoryDistribution
      }
    } catch (error) {
      console.error('Error fetching moderation metrics:', error)
      return {
        total_reviews: 0,
        flagged_reviews: 0,
        auto_flagged: 0,
        manual_reviews: 0,
        false_positives: 0,
        false_negatives: 0,
        average_processing_time: 0,
        ai_accuracy: 0,
        category_distribution: {}
      }
    }
  }

  /**
   * Get moderation audit trail
   */
  async getModerationAuditTrail(
    reviewId?: string,
    limit: number = 100,
    offset: number = 0
  ): Promise<ModerationAuditLog[]> {
    try {
      let query = supabase
        .from('review_moderation_log')
        .select('*')
        .order('created_at', { ascending: false })
        .range(offset, offset + limit - 1)

      if (reviewId) {
        query = query.eq('review_id', reviewId)
      }

      const { data, error } = await query

      if (error) {
        console.error('Error fetching moderation audit trail:', error)
        return []
      }

      return data || []
    } catch (error) {
      console.error('Error fetching moderation audit trail:', error)
      return []
    }
  }

  /**
   * Emit moderation event for real-time monitoring
   */
  private emitModerationEvent(event: ReviewModerationEvent): void {
    try {
      // In a real implementation, this would emit to a WebSocket or event system
      // For now, we'll log it and could integrate with your existing event system
      console.log('Moderation Event:', event)

      // You could integrate this with:
      // - WebSocket for real-time admin dashboard updates
      // - Event streaming for analytics
      // - Notification system for urgent flags
      // - Metrics aggregation service
    } catch (error) {
      console.error('Error emitting moderation event:', error)
    }
  }

  /**
   * Generate moderation report
   */
  async generateModerationReport(
    startDate: Date,
    endDate: Date
  ): Promise<{
    summary: ModerationMetrics
    trends: Array<{ date: string; metrics: Partial<ModerationMetrics> }>
    topIssues: Array<{ category: string; count: number; examples: string[] }>
    recommendations: string[]
  }> {
    try {
      const summary = await this.getModerationMetrics('30d')

      // Generate daily trends (simplified - in production you'd want more sophisticated aggregation)
      const trends: Array<{ date: string; metrics: Partial<ModerationMetrics> }> = []
      const currentDate = new Date(startDate)

      while (currentDate <= endDate) {
        const dayMetrics = await this.getModerationMetrics('24h')
        trends.push({
          date: currentDate.toISOString().split('T')[0],
          metrics: {
            total_reviews: dayMetrics.total_reviews,
            flagged_reviews: dayMetrics.flagged_reviews,
            auto_flagged: dayMetrics.auto_flagged
          }
        })
        currentDate.setDate(currentDate.getDate() + 1)
      }

      // Analyze top issues
      const { data: recentLogs } = await supabase
        .from('review_moderation_log')
        .select('ai_categories, keyword_matches, behavioral_flags')
        .gte('created_at', startDate.toISOString())
        .lte('created_at', endDate.toISOString())

      const issueCounts: Record<string, number> = {}
      const issueExamples: Record<string, string[]> = {}

      recentLogs?.forEach(log => {
        const allIssues = [
          ...log.ai_categories,
          ...log.keyword_matches,
          ...log.behavioral_flags
        ]

        allIssues.forEach((issue: string) => {
          issueCounts[issue] = (issueCounts[issue] || 0) + 1
          if (!issueExamples[issue]) {
            issueExamples[issue] = []
          }
          if (issueExamples[issue].length < 3) {
            issueExamples[issue].push(issue)
          }
        })
      })

      const topIssues = Object.entries(issueCounts)
        .sort(([, a], [, b]) => b - a)
        .slice(0, 10)
        .map(([category, count]) => ({
          category,
          count,
          examples: issueExamples[category] || []
        }))

      // Generate recommendations
      const recommendations: string[] = []

      if (summary.ai_accuracy < 80) {
        recommendations.push('Consider retraining AI models or adjusting confidence thresholds')
      }

      if (summary.average_processing_time > 5000) {
        recommendations.push('Review moderation pipeline performance and optimize processing')
      }

      if (summary.flagged_reviews > summary.total_reviews * 0.3) {
        recommendations.push('High flag rate detected - review moderation criteria and thresholds')
      }

      if (topIssues.some(issue => issue.category.includes('financial') || issue.category.includes('spam'))) {
        recommendations.push('Increase monitoring for financial spam and promotional content')
      }

      return {
        summary,
        trends,
        topIssues,
        recommendations
      }
    } catch (error) {
      console.error('Error generating moderation report:', error)
      throw error
    }
  }

  /**
   * Health check for monitoring system
   */
  async healthCheck(): Promise<{
    status: 'healthy' | 'degraded' | 'unhealthy'
    checks: Record<string, boolean>
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    details: Record<string, any>
  }> {
    const checks: Record<string, boolean> = {}
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    const details: Record<string, any> = {}

    try {
      // Check database connectivity
      const { data: dbCheck } = await supabase
        .from('review_moderation_log')
        .select('count', { count: 'exact', head: true })
        .limit(1)

      checks.database = !!dbCheck
      details.database = { connected: !!dbCheck }

      // Check AI moderation service
      const aiStats = aiModeration.getModerationStats()
      checks.aiService = aiStats.enabled
      details.aiService = aiStats

      // Check recent processing
      const { data: recentLogs } = await supabase
        .from('review_moderation_log')
        .select('created_at')
        .gte('created_at', new Date(Date.now() - 24 * 60 * 60 * 1000).toISOString())
        .limit(1)

      checks.recentActivity = !!recentLogs && recentLogs.length > 0
      details.recentActivity = { 
        hasRecentLogs: !!recentLogs && recentLogs.length > 0,
        lastLogTime: recentLogs?.[0]?.created_at 
      }

      // Determine overall status
      const allChecksPassed = Object.values(checks).every(check => check)
      const status: 'healthy' | 'degraded' | 'unhealthy' = allChecksPassed 
        ? 'healthy' 
        : checks.database 
          ? 'degraded' 
          : 'unhealthy'

      return { status, checks, details }
    } catch (error) {
      console.error('Health check failed:', error)
      return {
        status: 'unhealthy',
        checks: { database: false, aiService: false, recentActivity: false },
        details: { error: error instanceof Error ? error.message : 'Unknown error' }
      }
    }
  }
}

