            <button
              type="submit"
              disabled={sending}
              className="flex-1 px-4 py-2 bg-purple-600 text-white rounded-xl hover:bg-purple-700 disabled:opacity-50"
            >
              {sending ? 'Sending...' : 'Send Notifications'}
