          action, 
          vendorIds: Array.from(selectedVendors) 
        })
      })

      const data = await response.json()
      if (data.success) {
        alert(`Successfully ${action}d ${selectedVendors.size} vendor(s)`)
        setSelectedVendors(new Set())
        setShowBulkModal(false)
        router.refresh()
