function NewsletterModal({ onClose, onSend }: { onClose: () => void; onSend: (subject: string, message: string) => void }) {
  const [subject, setSubject] = useState('')
  const [message, setMessage] = useState('')
  const [sending, setSending] = useState(false)

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    if (!subject || !message) {
      alert('Please fill in all fields')
      return
    }
    setSending(true)
    await onSend(subject, message)
    setSending(false)
  }

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center p-4"
      onClick={onClose}
    >
      <motion.div
        initial={{ scale: 0.95, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        exit={{ scale: 0.95, opacity: 0 }}
        onClick={(e: React.MouseEvent<HTMLDivElement>) => e.stopPropagation()}
        className="bg-white rounded-2xl p-6 max-w-lg w-full"
      >
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-xl font-bold">Send Newsletter</h3>
          <button onClick={onClose}>
            <X className="w-5 h-5" />
          </button>
        </div>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium mb-1">Subject</label>
            <input
              type="text"
              value={subject}
              onChange={(e) => setSubject(e.target.value)}
              className="w-full px-4 py-2 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-gray-500"
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium mb-1">Message</label>
            <textarea
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              rows={6}
              className="w-full px-4 py-2 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-gray-500"
              required
            />
          </div>
          <div className="flex gap-2">
            <button
              type="button"
              onClick={onClose}
              className="flex-1 px-4 py-2 border border-gray-300 rounded-xl hover:bg-gray-50"
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={sending}
              className="flex-1 px-4 py-2 bg-gray-600 text-white rounded-xl hover:bg-gray-700 disabled:opacity-50"
            >
              {sending ? 'Sending...' : 'Send Newsletter'}
            </button>
          </div>
        </form>
      </motion.div>
    </motion.div>
  )
}

import { NextResponse } from 'next/server'
import { createClient } from '@supabase/supabase-js'
import { getSupabaseConfig } from '@/config/supabase'
import crypto from 'node:crypto'

function stableUuid(seed: string) {
  const hex = crypto.createHash('sha256').update(seed).digest('hex').slice(0, 32)
  return `${hex.slice(0, 8)}-${hex.slice(8, 12)}-${hex.slice(12, 16)}-${hex.slice(16, 20)}-${hex.slice(20)}`
}

export async function POST(request: Request) {
  if (process.env.NODE_ENV === 'production') return NextResponse.json({ error: 'Disabled in production' }, { status: 403 })
  if (process.env.E2E !== 'true') return NextResponse.json({ error: 'E2E=true is required for /api/test/seed' }, { status: 403 })

  const { url, secretKey } = getSupabaseConfig() as { url: string; secretKey?: string }
  if (!url || !/^https?:\/\/(localhost|127\.0\.0\.1)(:\d+)?$/i.test(url)) {
    return NextResponse.json({ error: `Refusing to seed non-local Supabase: ${url ?? '(missing)'}` }, { status: 400 })
  }
  if (!secretKey) {
    return NextResponse.json({ error: 'Missing SUPABASE_SERVICE_ROLE_KEY for E2E seeding' }, { status: 500 })
  }

  const admin = createClient(url, secretKey, { auth: { persistSession: false, autoRefreshToken: false } })

  try {
    try {
      const health = await fetch(`${url}/auth/v1/health`)
      if (!health.ok) {
        return NextResponse.json(
          { error: 'Supabase auth not reachable at http://localhost:54321 (run `pnpm supabase:start`)' },
          { status: 500 }
        )
      }
    } catch {
      return NextResponse.json(
        { error: 'Supabase not reachable at http://localhost:54321 (run `pnpm supabase:start`)' },
        { status: 500 }
      )
    }

    // Fail fast with a clear error if local schema/compat wasn't applied.
    {
      const usersCheck = await admin.from('users').select('id').limit(1)
      if (usersCheck.error) {
        return NextResponse.json({ error: `Schema not ready (public.users missing?): ${usersCheck.error.message}` }, { status: 500 })
      }
      const servicesCheck = await admin.from('services').select('id,vendor_id').limit(1)
      if (servicesCheck.error) {
        return NextResponse.json({ error: `Schema not ready (services.vendor_id missing?): ${servicesCheck.error.message}` }, { status: 500 })
      }
      const bookingsCheck = await admin.from('bookings').select('id,slot_start,slot_end,total_amount_cents').limit(1)
      if (bookingsCheck.error) {
        return NextResponse.json({ error: `Schema not ready (booking slot_* columns missing?): ${bookingsCheck.error.message}` }, { status: 500 })
      }
    }

    const vendorEmail = 'vendor@test.dev'
    const customerEmail = 'customer@test.dev'
    const expectationText = 'Arrive 10 minutes early'

    const body = await request.json().catch(() => ({} as any))
    const seedIdRaw = typeof body?.seedId === 'string' ? body.seedId : typeof body?.runId === 'string' ? body.runId : 'default'
    const seedId = seedIdRaw && seedIdRaw.length > 0 ? seedIdRaw : 'default'

    async function ensureAuthUser(email: string, fullName: string, role: 'vendor' | 'customer') {
      const password = 'test-password-1234'
      const created = await admin.auth.admin.createUser({
        email,
        password,
        email_confirm: true,
        user_metadata: { full_name: fullName, role },
      })

      if (created.data.user) return created.data.user

      const list = await admin.auth.admin.listUsers({ page: 1, perPage: 2000 })
      const existing = (list.data?.users ?? []).find((u) => u.email?.toLowerCase() === email.toLowerCase())
      if (!existing) throw new Error(created.error?.message ?? `Failed to create or find auth user for ${email}`)
      return existing
    }

    const vendorAuth = await ensureAuthUser(vendorEmail, 'Test Vendor', 'vendor')
    const customerAuth = await ensureAuthUser(customerEmail, 'Test Customer', 'customer')

    const vendorId = vendorAuth.id
    const customerId = customerAuth.id

    // Idempotent: safe to run repeatedly (upserts by primary key).
    await admin.from('users').upsert({ id: vendorId, email: vendorEmail, role: 'vendor', full_name: 'Test Vendor' }, { onConflict: 'id' })
    await admin.from('users').upsert({ id: customerId, email: customerEmail, role: 'customer', full_name: 'Test Customer' }, { onConflict: 'id' })

    const serviceId = '8b3a1cf5-3e2d-4e86-aed9-50f2d9b2b4a1'
    const { error: serviceErr } = await admin.from('services').upsert(
      {
        id: serviceId,
        vendor_id: vendorId,
        name: 'Test Service',
        description: expectationText,
        category: 'test',
        duration_minutes: 60,
        is_active: true,
        price_cents: 0,
      },
      { onConflict: 'id' }
    )
    if (serviceErr) return NextResponse.json({ error: `Service upsert failed: ${serviceErr.message}` }, { status: 500 })

    const start = new Date(Date.now() + 60 * 60 * 1000)
    const end = new Date(start.getTime() + 60 * 60 * 1000)

    const slotId =
      seedId === 'default'
        ? '4f7f7d2a-7d7e-4c7f-a8ff-1a8c72a0c6c2'
        : stableUuid(`slot:${seedId}`)
    const { error: slotErr } = await admin.from('availability_slots').upsert(
      {
        id: slotId,
        vendor_id: vendorId,
        service_id: serviceId,
        start_time: start.toISOString(),
        end_time: end.toISOString(),
        is_booked: false,
      },
      { onConflict: 'id' }
    )
    if (slotErr) return NextResponse.json({ error: `Slot upsert failed: ${slotErr.message}` }, { status: 500 })

    const bookingId =
      seedId === 'default'
        ? '54d4a6d0-2b07-42bb-9b8c-5e5be4f1e6a7'
        : stableUuid(`booking:${seedId}`)
    const { error: bookingErr } = await admin.from('bookings').upsert(
      {
        id: bookingId,
        customer_id: customerId,
        vendor_id: vendorId,
        service_id: serviceId,
        slot_id: slotId,
        slot_start: start.toISOString(),
        slot_end: end.toISOString(),
        status: 'pending',
        total_amount_cents: 0,
      },
      { onConflict: 'id' }
    )
    if (bookingErr) return NextResponse.json({ error: `Booking upsert failed: ${bookingErr.message}` }, { status: 500 })

    return NextResponse.json({ vendorId, customerId, serviceId, slotId, bookingId, expectationText, seedId })
  } catch (error) {
    console.error('Test seed error:', error)
    return NextResponse.json(
      { error: `Unexpected error: ${error instanceof Error ? error.message : 'Unknown error'}` },
      { status: 500 }
    )
  }
}

import { createServerClient } from '@supabase/ssr'
import { getSupabaseConfig } from '@/config/supabase'
import { cookies } from 'next/headers'

/**
 * Returns the authenticated user's id using either the Supabase session cookie
 * or an Authorization header bearer token. Returns null if authentication fails.
 */
export async function getAuthenticatedUserId(request: Request): Promise<string | null> {
  const cookieStore = await cookies()
  const config = getSupabaseConfig()
  
  const supabase = createServerClient(
    config.url,
    config.publishableKey,
    {
      cookies: {
        getAll() {
          return cookieStore.getAll()
        },
        setAll(cookiesToSet) {
          try {
            cookiesToSet.forEach(({ name, value, options }) => {
              cookieStore.set(name, value, options)
            })
          } catch (error) {
            // The `setAll` method was called from a Server Component or Route Handler.
            // This can be ignored if you have middleware refreshing user sessions.
          }
        }
      }
    }
  )

  // Try cookie-based session first
  const { data: { session } } = await supabase.auth.getSession()
  if (session?.user?.id) {
    return session.user.id
  }

  // Fallback to Authorization header
  const authHeader = request.headers.get('authorization')
  if (authHeader?.startsWith('Bearer ')) {
    const token = authHeader.slice(7)
    const { data: { user } } = await supabase.auth.getUser(token)
    if (user?.id) {
      return user.id
    }
  }

  return null
}

import { NextRequest, NextResponse } from 'next/server'
import { getServerSupabase } from '@/lib/supabaseServer'
import { getAuthenticatedUserId } from '@/app/api/_utils/auth'

export async function POST(request: NextRequest) {
  try {
    const userId = await getAuthenticatedUserId(request)
    if (!userId) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    const { message, broadcastIds } = await request.json()

    if (!message) {
      return NextResponse.json({ error: 'Message is required' }, { status: 400 })
    }

    const supabase = getServerSupabase()
    
    // Get all vendors
    const { data: vendors, error: vendorError } = await supabase
      .from('profiles')
      .select('id')
      .eq('role', 'vendor')

    if (vendorError) {
      console.error('Vendor fetch error:', vendorError)
      return NextResponse.json({ error: 'Failed to fetch vendors' }, { status: 500 })
    }

    if (!vendors || vendors.length === 0) {
      return NextResponse.json({ error: 'No vendors found' }, { status: 400 })
    }

    // Create notifications for all vendors
    const notifications = vendors.map(vendor => ({
      user_id: vendor.id,
      type: 'broadcast',
      title: 'New Broadcast',
      message: message,
      created_at: new Date().toISOString()
    }))

    const { error: notifError } = await supabase
      .from('notifications')
      .insert(notifications)

    if (notifError) {
      console.error('Notification creation error:', notifError)
      return NextResponse.json({ error: 'Failed to create notifications' }, { status: 500 })
    }

    return NextResponse.json({ 
      success: true, 
      recipientCount: vendors.length,
      message: `Notifications sent to ${vendors.length} vendors` 
    })
  } catch (error) {
    console.error('Notification error:', error)
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 })
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { createServerClient } from '@supabase/ssr'
import { cookies } from 'next/headers'
import { getSupabaseConfig } from '@/config/supabase'
import { requireAdmin } from '@/lib/auth/requireAdmin'
import { bookingStateMachine } from '@/lib/services/bookingStateMachine'
import { processRefund } from '@/lib/services/refundService'

interface ResolveDisputeRequest {
  status: 'resolved' | 'closed' | 'rejected'
  resolution: string
  resolution_amount?: number
  resolution_type: 'refund' | 'reschedule' | 'partial_refund' | 'credit' | 'rejected' | 'other'
  admin_notes?: string
}

export async function POST(
  request: NextRequest,
  context: { params: Promise<Record<string, string>> }
) {
  const { id: disputeId } = await context.params
  try {
    const cookieStore = await cookies()
    const config = getSupabaseConfig()
    const supabase = createServerClient(
      config.url,
      config.publishableKey,
      {
        cookies: {
          getAll() {
            return cookieStore.getAll()
          },
          setAll(cookiesToSet) {
            try {
              cookiesToSet.forEach(({ name, value, options }) => {
                cookieStore.set(name, value, options)
              })
            } catch (error) {
              // The `setAll` method was called from a Server Component or Route Handler.
              // This can be ignored if you have middleware refreshing user sessions.
            }
          }
        }
      }
    )

    const { data: { user }, error: authError } = await supabase.auth.getUser()
    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    const adminUser = await requireAdmin({ user })

    const body: ResolveDisputeRequest = await request.json()
    const { status, resolution, resolution_amount, resolution_type, admin_notes } = body

    if (!status || !resolution || !resolution_type) {
      return NextResponse.json(
        { error: 'Missing required fields: status, resolution, resolution_type' },
        { status: 400 }
      )
    }

    // Get dispute details
    const { data: dispute, error: disputeError } = await supabase
      .from('disputes')
      .select(`
        *,
        bookings!inner(
          id,
          customer_id,
          provider_id,
          status,
          total_amount_cents,
          commitment_fee_paid,
          payment_intent_id,
          idempotency_key
        )
      `)
      .eq('id', disputeId)
      .single()

    if (disputeError || !dispute) {
      return NextResponse.json(
        { error: 'Dispute not found' },
        { status: 404 }
      )
    }

    if (dispute.status === 'resolved' || dispute.status === 'closed') {
      return NextResponse.json(
        { error: 'Dispute already resolved' },
        { status: 400 }
      )
    }

    // Use database function to resolve dispute
    const { error: resolveError } = await supabase.rpc('resolve_dispute', {
      p_dispute_id: disputeId,
      p_admin_id: adminUser.id,
      p_resolution: resolution,
      p_resolution_amount: resolution_amount || null,
      p_resolution_type: resolution_type,
      p_admin_notes: admin_notes || null
    })

    if (resolveError) {
      console.error('Error resolving dispute:', resolveError)
      return NextResponse.json(
        { error: 'Failed to resolve dispute' },
        { status: 500 }
      )
    }

    // Process refund if needed
    let refundResult = null
    if ((resolution_type === 'refund' || resolution_type === 'partial_refund') && dispute.bookings) {
      const booking = dispute.bookings
      if (booking.commitment_fee_paid && booking.payment_intent_id) {
        try {
          refundResult = await processRefund(booking.id, {
            idempotencyKey: booking.idempotency_key,
            reason: `Dispute resolution: ${resolution}`,
            adminId: adminUser.id
          })
        } catch (refundError) {
          console.error('Refund processing error:', refundError)
          // Don't fail the resolution if refund fails - it can be processed manually
        }
      }
    }

    // Update booking status if no-show
    if (dispute.dispute_type === 'no_show' && dispute.bookings) {
      try {
        await bookingStateMachine.transition(dispute.bookings.id, 'no_show', {
          reason: 'No-show confirmed via dispute resolution',
          adminId: adminUser.id,
          adminOverride: true,
          skipRefund: true // Refund already handled above
        })
      } catch (transitionError) {
        console.error('Booking status transition error:', transitionError)
        // Don't fail the resolution if transition fails
      }
    }

    // Send notifications
    await supabase.from('admin_notifications').insert([{
      type: 'dispute_resolved',
      title: `Dispute Resolved - ${dispute.dispute_type}`,
      message: `Dispute ${disputeId} has been resolved: ${resolution}`,
      metadata: { dispute_id: disputeId, resolution_type },
      priority: 'medium',
      created_at: new Date().toISOString()
    }])

    return NextResponse.json({
      success: true,
      message: 'Dispute resolved successfully',
      dispute_id: disputeId,
      refund_result: refundResult
    })
  } catch (error) {
    console.error('Admin dispute resolution error:', error)
    if (error instanceof Error && error.message.includes('Unauthorized')) {
      return NextResponse.json(
        { error: 'Unauthorized' },
        { status: 401 }
      )
    }
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    )
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { createServerClient } from '@supabase/ssr'
import { cookies } from 'next/headers'
import { getSupabaseConfig } from '@/config/supabase'
import { requireAdmin } from '@/lib/auth/requireAdmin'

export async function GET(request: NextRequest) {
  try {
    await requireAdmin(request)

    const cookieStore = await cookies()
    const config = getSupabaseConfig()
    const supabase = createServerClient(
      config.url,
      config.publishableKey,
      {
        cookies: {
          getAll() {
            return cookieStore.getAll()
          },
          setAll(cookiesToSet) {
            try {
              cookiesToSet.forEach(({ name, value, options }) => {
                cookieStore.set(name, value, options)
              })
            } catch (error) {
              // The `setAll` method was called from a Server Component or Route Handler.
              // This can be ignored if you have middleware refreshing user sessions.
            }
          }
        }
      }
    )

    const { searchParams } = new URL(request.url)
    const status = searchParams.get('status')
    const dispute_type = searchParams.get('type')
    const page = parseInt(searchParams.get('page') || '1')
    const limit = Math.min(parseInt(searchParams.get('limit') || '50'), 200)
    const offset = (page - 1) * limit

    let query = supabase
      .from('disputes')
      .select(`
        *,
        bookings!inner(
          id,
          customer_id,
          provider_id,
          service_name,
          total_amount,
          status,
          slot_start,
          slot_end
        ),
        users!inner(
          id,
          email,
          full_name
        )
      `, { count: 'exact' })
      .order('created_at', { ascending: false })
      .range(offset, offset + limit - 1)

    if (status) {
      query = query.eq('status', status)
    }
    if (dispute_type) {
      query = query.eq('dispute_type', dispute_type)
    }

    const { data: disputes, error, count } = await query

    if (error) {
      console.error('Error fetching disputes:', error)
      return NextResponse.json(
        { error: 'Failed to fetch disputes' },
        { status: 500 }
      )
    }

    return NextResponse.json({
      success: true,
      disputes: disputes || [],
      pagination: {
        page,
        limit,
        total: count || 0,
        pages: Math.ceil((count || 0) / limit)
      }
    })
  } catch (error) {
    console.error('Admin disputes fetch error:', error)
    if (error instanceof Error && error.message.includes('Unauthorized')) {
      return NextResponse.json(
        { error: 'Unauthorized' },
        { status: 401 }
      )
    }
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    )
  }
}

