export async function POST(request: NextRequest) {
  try {
    const config = getSupabaseConfig()
    
    // Create Supabase client with cookie access
    const supabase = createServerClient(
      config.url,
      config.publishableKey,
      {
        cookies: {
          getAll() {
            return request.cookies.getAll()
          },
          setAll(cookiesToSet) {
          }
        }
      }
    )
    
    // Check authentication
    const { data: { user }, error: authError } = await supabase.auth.getUser()
    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    // Check admin status
    const { data: profile } = await supabase
      .from('profiles')
      .select('role')
      .eq('id', user.id)
      .single()

    const isAdmin = profile?.role === 'admin'
    
    // Also check explicit admin emails
    const ADMIN_EMAILS = process.env.ADMIN_EMAILS?.split(',') || ['admin@bookiji.com', 'patri@bookiji.com']
    const isEmailAdmin = user.email && ADMIN_EMAILS.includes(user.email)

    if (!isAdmin && !isEmailAdmin) {
      return NextResponse.json({ error: 'Forbidden - Admin access required' }, { status: 403 })
    }

    // Parse request body
    const body = await request.json()
    const { userId, role, username } = body

    if (!userId) {
      return NextResponse.json({ error: 'Missing userId' }, { status: 400 })
    }

    const updates: any = {}

    // Handle role update
    if (role) {
      if (!['customer', 'vendor', 'admin'].includes(role)) {
        return NextResponse.json({ error: 'Invalid role' }, { status: 400 })
      }
      updates.role = role
    }

    // Handle username update
    if (username !== undefined) {
      // Allow empty string to clear username (if nullable), or validate length
      if (username && username.length < 3) {
        return NextResponse.json({ error: 'Username must be at least 3 characters' }, { status: 400 })
      }
      updates.username = username || null // Convert empty string to null if desired
      
      // Check uniqueness
      if (updates.username) {
        const { data: existingUser } = await supabase
          .from('profiles')
          .select('id')
          .eq('username', updates.username)
          .neq('id', userId) // Exclude current user
          .single()
        
        if (existingUser) {
          return NextResponse.json({ error: 'Username already taken' }, { status: 409 })
        }
      }
    }

    if (Object.keys(updates).length === 0) {
      return NextResponse.json({ error: 'No updates provided' }, { status: 400 })
    }

    // Update user
    const { error: updateError } = await supabase
      .from('profiles')
      .update(updates)
      .eq('id', userId)

    if (updateError) {
      console.error('Error updating user:', updateError)
      return NextResponse.json(
        { error: 'Failed to update user: ' + updateError.message },
        { status: 500 }
      )
    }

    return NextResponse.json({ success: true, message: 'User updated successfully' })
  } catch (error) {
    console.error('Error in update-user endpoint:', error)
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    )
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { getServerSupabase } from '@/lib/supabaseServer'
import { getAuthenticatedUserId } from '@/app/api/_utils/auth'

export async function POST(request: NextRequest) {
  try {
    const userId = await getAuthenticatedUserId(request)
    if (!userId) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    const { action, vendorIds } = await request.json()

    if (!action || !vendorIds || !Array.isArray(vendorIds) || vendorIds.length === 0) {
      return NextResponse.json({ error: 'Invalid request' }, { status: 400 })
    }

    const supabase = getServerSupabase()
    
    // Update vendor statuses based on action
    let status: string
    switch (action) {
      case 'approve':
        status = 'active'
        break
      case 'reject':
        status = 'suspended'
        break
      case 'suspend':
        status = 'suspended'
        break
      default:
        return NextResponse.json({ error: 'Invalid action' }, { status: 400 })
    }

    const { error } = await supabase
      .from('profiles')
      .update({ 
        role: 'vendor',
        updated_at: new Date().toISOString()
      })
      .in('id', vendorIds)
      .eq('role', 'vendor')

    if (error) {
      console.error('Bulk update error:', error)
      return NextResponse.json({ error: 'Failed to update vendors' }, { status: 500 })
    }

    return NextResponse.json({ 
      success: true, 
      message: `Successfully ${action}d ${vendorIds.length} vendor(s)` 
    })
  } catch (error) {
    console.error('Bulk action error:', error)
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 })
  }
}

import { NextResponse } from 'next/server'
import { limitRequest } from '@/middleware/requestLimiter'
import { ollamaService, BOOKIJI_PROMPTS } from '@/lib/ollama'
import { ADSENSE_APPROVAL_MODE } from "@/lib/adsense"

export async function POST(request: Request) {
  try {
    const limited = await limitRequest(request, { windowMs: 10_000, max: 20 })
    if (limited) return limited
    let body;
    try {
      body = await request.json()
    } catch (e) {
      if (process.env.NODE_ENV === 'development' && !ADSENSE_APPROVAL_MODE) {
        console.error('‚ùå AI Radius Scaling: Failed to parse request JSON:', e)
      }
      throw e; // Re-throw to be caught by outer catch
    }
    const { service, location, providerDensity, currentRadius } = body

    if (!service || !location) {
      return NextResponse.json({ 
        error: 'Service and location are required' 
      }, { status: 400 })
    }

    if (process.env.NODE_ENV === 'development' && !ADSENSE_APPROVAL_MODE) {
      console.log('üó∫Ô∏è AI Radius Scaling Request:', { service, location, providerDensity, currentRadius })
    }

    // Try AI-powered radius recommendation, gracefully fall back if unavailable
    let recommendedRadius = 5
    let explanation = 'Default radius applied.'
    try {
      const aiResponse = await ollamaService.generate(
        BOOKIJI_PROMPTS.radiusScaling(providerDensity || 'medium', service)
      )
      const radiusMatch = aiResponse.match(/(\d+(?:\.\d+)?)\s*km/i)
      recommendedRadius = radiusMatch ? parseFloat(radiusMatch[1]) : 5
      explanation = aiResponse
    } catch {
      // Fallback heuristic if AI is unavailable or fails
      const density = (providerDensity || 'medium').toLowerCase()
      if (density === 'dense') recommendedRadius = 3
      else if (density === 'sparse') recommendedRadius = 15 // Increased to match AI recommendation
      else recommendedRadius = 5
      explanation = `AI unavailable. Applied heuristic for ${density} areas.`
    }

    if (process.env.NODE_ENV === 'development' && !ADSENSE_APPROVAL_MODE) {
      console.log('üó∫Ô∏è AI Radius Recommendation:', recommendedRadius, 'km')
    }

    return NextResponse.json({ 
      success: true, 
      recommendedRadius,
      explanation,
      service,
      location,
      providerDensity: providerDensity || 'medium'
    })

  } catch (error) {
    if (process.env.NODE_ENV === 'development' && !ADSENSE_APPROVAL_MODE) {
      console.error('‚ùå AI Radius Scaling error:', error)
    }
    // Final safety fallback
    const density = 'medium'
    const recommendedRadius = 5
    const explanation = 'Service temporarily unavailable. Using default radius.'

    const isAborted = error instanceof Error && 
      (error.name === 'AbortError' || 
       error.message.includes('aborted') || 
       (error as any).code === 'ECONNRESET');

    if (process.env.NODE_ENV === 'development' && !ADSENSE_APPROVAL_MODE) {
      if (isAborted) {
        console.warn('‚ö†Ô∏è AI Radius Scaling: Request aborted or connection reset by client.');
      } else {
        console.error('‚ùå AI Radius Scaling error:', error)
      }
    }

    return NextResponse.json({ 
      success: true,
      recommendedRadius,
      explanation,
      service: 'unknown',
      location: 'unknown',
      providerDensity: density
    })
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { getServerSupabase } from '@/lib/supabaseServer';
import { withQueryLogging } from '@/lib/performance/queryLogger';

interface HeatmapData {
  postal_code: string;
  latitude: number;
  longitude: number;
  booking_count: number;
  revenue: number;
  avg_rating: number;
  unique_providers: number;
  last_booking: string;
}

interface HeatmapParams {
  timeRange: '7d' | '30d' | '90d' | '1y';
  serviceType?: string;
  minBookings?: number;
  radius?: number; // in km
  centerLat?: number;
  centerLng?: number;
}

export async function GET(request: NextRequest) {
  try {
    const supabase = new Proxy({} as any, { get: (target, prop) => (getServerSupabase() as any)[prop] }) as ReturnType<typeof getServerSupabase>;

    // Check authentication
    const { data: { user }, error: authError } = await supabase.auth.getUser();
    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    // Parse query parameters
    const { searchParams } = new URL(request.url);
    const params: HeatmapParams = {
      timeRange: (searchParams.get('timeRange') as HeatmapParams['timeRange']) || '30d',
      serviceType: searchParams.get('serviceType') || undefined,
      minBookings: searchParams.get('minBookings') ? parseInt(searchParams.get('minBookings')!) : 1,
      radius: searchParams.get('radius') ? parseFloat(searchParams.get('radius')!) : undefined,
      centerLat: searchParams.get('centerLat') ? parseFloat(searchParams.get('centerLat')!) : undefined,
      centerLng: searchParams.get('centerLng') ? parseFloat(searchParams.get('centerLng')!) : undefined
    };

    // Calculate date range
    const timeRangeMap = {
      '7d': 7,
      '30d': 30,
      '90d': 90,
      '1y': 365
    };
    const daysBack = timeRangeMap[params.timeRange];
    const startDate = new Date();
    startDate.setDate(startDate.getDate() - daysBack);

    // Build heatmap query using a stored procedure for better performance
    const heatmapData = await withQueryLogging(
      async () => {
        const { data, error } = await supabase.rpc('get_booking_heatmap_data', {
          p_start_date: startDate.toISOString(),
          p_end_date: new Date().toISOString(),
          p_service_type: params.serviceType,
          p_min_bookings: params.minBookings,
          p_radius_km: params.radius,
          p_center_lat: params.centerLat,
          p_center_lng: params.centerLng
        });

        if (error) {
          console.error('Heatmap query error:', error);
          throw error;
        }

        return data as HeatmapData[];
      },
      {
        query: 'get_booking_heatmap_data',
        table: 'bookings',
        operation: 'RPC',
        userId: user.id,
        endpoint: '/api/analytics/heatmap'
      }
    );

    // If the RPC doesn't exist, fall back to a regular query
    if (!heatmapData || heatmapData.length === 0) {
      console.log('Falling back to direct query...');

      const fallbackData = await withQueryLogging(
        async () => {
          let query = supabase
            .from('bookings')
            .select(`
              postal_code,
              latitude,
              longitude,
              status,
              total_amount,
              service_type,
              created_at,
              provider:provider_id(rating)
            `)
            .gte('created_at', startDate.toISOString())
            .not('postal_code', 'is', null)
            .not('latitude', 'is', null)
            .not('longitude', 'is', null);

          if (params.serviceType) {
            query = query.eq('service_type', params.serviceType);
          }

          const { data, error } = await query;

          if (error) throw error;

          // Group by postal code and aggregate
          interface Booking {
            postal_code?: string
            latitude?: number
            longitude?: number
            total_amount?: number
            created_at: string
            provider_id?: string
            provider?: {
              rating?: number
            } | Array<{
              rating?: number
            }>
            [key: string]: unknown
          }
          interface GroupedBooking {
            postal_code?: string
            latitude?: number
            longitude?: number
            booking_count: number
            revenue: number
            ratings: number[]
            provider_ids: Set<string>
            last_booking: string
          }
          const bookings = (data || []) as Booking[];
          const grouped = bookings.reduce((acc: Record<string, GroupedBooking>, booking: Booking) => {
            const key = booking.postal_code;
            if (!key) return acc; // Skip if postal_code is undefined

            if (!acc[key]) {
              acc[key] = {
                postal_code: booking.postal_code,
                latitude: booking.latitude,
                longitude: booking.longitude,
                booking_count: 0,
                revenue: 0,
                ratings: [],
                provider_ids: new Set(),
                last_booking: booking.created_at
              };
            }

            acc[key].booking_count++;
            acc[key].revenue += booking.total_amount || 0;
            const providerRating = Array.isArray(booking.provider) 
              ? booking.provider[0]?.rating 
              : (booking.provider as { rating?: number } | undefined)?.rating;
            if (providerRating) {
              acc[key].ratings.push(providerRating);
            }
            if (booking.provider_id) {
              acc[key].provider_ids.add(booking.provider_id);
            }

            if (booking.created_at > acc[key].last_booking) {
              acc[key].last_booking = booking.created_at;
            }

            return acc;
          }, {});

          // Convert to array and calculate averages
          return Object.values(grouped)
            .filter((item: GroupedBooking) => item.booking_count >= (params.minBookings || 1))
            .map((item: GroupedBooking) => ({
              postal_code: item.postal_code,
              latitude: item.latitude,
              longitude: item.longitude,
              booking_count: item.booking_count,
              revenue: item.revenue,
              avg_rating: item.ratings.length > 0 
                ? item.ratings.reduce((sum: number, rating: number) => sum + rating, 0) / item.ratings.length 
                : 0,
              unique_providers: item.provider_ids.size,
              last_booking: item.last_booking
            }));
        },
        {
          query: 'SELECT bookings with aggregation',
          table: 'bookings',
          operation: 'SELECT',
          userId: user.id,
          endpoint: '/api/analytics/heatmap'
        }
      );

      return NextResponse.json({
        success: true,
        data: fallbackData,
        params,
        source: 'fallback_query',
        count: fallbackData?.length || 0
      });
    }

    return NextResponse.json({
      success: true,
      data: heatmapData,
      params,
      source: 'rpc',
      count: heatmapData.length
    });

  } catch (error) {
    console.error('Heatmap API error:', error);
    return NextResponse.json(
      { error: 'Failed to fetch heatmap data' },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { createClient } from '@supabase/supabase-js'

import { supabaseAdmin as supabase } from '@/lib/supabaseProxies';

export async function GET(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url)
    const timeRange = searchParams.get('timeRange') || '24h'
    const endpoint = searchParams.get('endpoint')
    const method = searchParams.get('method')

    // Calculate date range
    const now = new Date()
    let startDate: Date

    switch (timeRange) {
      case '1h':
        startDate = new Date(now.getTime() - 60 * 60 * 1000)
        break
      case '6h':
        startDate = new Date(now.getTime() - 6 * 60 * 60 * 1000)
        break
      case '24h':
        startDate = new Date(now.getTime() - 24 * 60 * 60 * 1000)
        break
      case '7d':
        startDate = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000)
        break
      case '30d':
        startDate = new Date(now.getTime() - 30 * 24 * 60 * 60 * 1000)
        break
      default:
        startDate = new Date(now.getTime() - 24 * 60 * 60 * 1000)
    }

    // Get performance metrics from materialized view
    const { data: performanceData, error: performanceError } = await supabase
      .from('performance_analytics_hourly')
      .select('*')
      .gte('hour', startDate.toISOString())
      .order('hour', { ascending: true })

    if (performanceError) {
      console.error('Performance analytics error:', performanceError)
      return NextResponse.json({ error: 'Failed to fetch performance data' }, { status: 500 })
    }

    // Get real-time metrics for the last hour
    const { data: realtimeMetrics, error: realtimeError } = await supabase
      .from('performance_metrics')
      .select('*')
      .gte('created_at', new Date(now.getTime() - 60 * 60 * 1000).toISOString())
      .order('created_at', { ascending: false })

    if (realtimeError) {
      console.error('Realtime metrics error:', realtimeError)
      return NextResponse.json({ error: 'Failed to fetch realtime data' }, { status: 500 })
    }

    // Calculate summary statistics
    const summary = calculatePerformanceSummary(performanceData || [])

    // Get endpoint-specific performance if requested
    let endpointPerformance = null
    if (endpoint) {
      endpointPerformance = await getEndpointPerformance(endpoint, method, startDate)
    }

    // Get top slow endpoints
    const topSlowEndpoints = await getTopSlowEndpoints(startDate)

    // Get error analysis
    const errorAnalysis = await getErrorAnalysis(startDate)

    return NextResponse.json({
      success: true,
      timeRange,
      summary,
      endpointPerformance,
      topSlowEndpoints,
      errorAnalysis,
      dataPoints: performanceData?.length || 0
    })

  } catch (error) {
    console.error('Performance analytics API error:', error)
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 })
  }
}

interface PerformanceDataItem {
  request_count?: number
  error_count?: number
  avg_response_time_ms?: number
  p95_response_time_ms?: number
  endpoint?: string
  [key: string]: unknown
}

function calculatePerformanceSummary(performanceData: PerformanceDataItem[]) {
  if (performanceData.length === 0) {
    return {
      totalRequests: 0,
      avgResponseTime: 0,
      p95ResponseTime: 0,
      errorRate: 0,
      activeEndpoints: 0
    }
  }

  const totalRequests = performanceData.reduce((sum, hour) => sum + (hour.request_count || 0), 0)
  const totalErrors = performanceData.reduce((sum, hour) => sum + (hour.error_count || 0), 0)
  const avgResponseTime = performanceData.reduce((sum, hour) => sum + (hour.avg_response_time_ms || 0), 0) / performanceData.length
  const p95ResponseTime = Math.max(...performanceData.map(hour => hour.p95_response_time_ms || 0))
  const errorRate = totalRequests > 0 ? (totalErrors / totalRequests) * 100 : 0
  const activeEndpoints = new Set(performanceData.map(hour => hour.endpoint).filter(Boolean)).size

  return {
    totalRequests,
    avgResponseTime: Math.round(avgResponseTime),
    p95ResponseTime,
    errorRate: Math.round(errorRate * 100) / 100,
    activeEndpoints
  }
}

async function getEndpointPerformance(endpoint: string, method: string | null, startDate: Date) {
  try {
    let query = supabase
      .from('performance_metrics')
      .select('*')
      .eq('endpoint', endpoint)
      .gte('created_at', startDate.toISOString())

    if (method) {
      query = query.eq('method', method)
    }

    const { data, error } = await query
      .order('created_at', { ascending: true })

    if (error) {
      console.error('Endpoint performance error:', error)
      return null
    }

    if (!data || data.length === 0) {
      return null
    }

    // Group by hour for trend analysis
    const hourlyData = data.reduce((acc, metric) => {
      const hour = new Date(metric.created_at).toISOString().slice(0, 13) + ':00:00Z'
      if (!acc[hour]) {
        acc[hour] = {
          hour,
          request_count: 0,
          total_response_time: 0,
          error_count: 0,
          status_codes: {}
        }
      }

      acc[hour].request_count++
      acc[hour].total_response_time += metric.response_time_ms
      if (metric.status_code >= 400) {
        acc[hour].error_count++
      }

      const statusKey = Math.floor(metric.status_code / 100) + 'xx'
      acc[hour].status_codes[statusKey] = (acc[hour].status_codes[statusKey] || 0) + 1

      return acc
    }, {} as Record<string, {
      hour: string
      request_count: number
      total_response_time: number
      error_count: number
      status_codes: Record<string, number>
    }>)

    // Convert to array and calculate averages
    interface HourlyDataItem {
      hour: string
      request_count: number
      total_response_time: number
      error_count: number
      status_codes: Record<string, number>
    }
    const hourlyArray = (Object.values(hourlyData) as HourlyDataItem[]).map((hour: HourlyDataItem) => ({
      hour: hour.hour,
      request_count: hour.request_count,
      avg_response_time_ms: Math.round(hour.total_response_time / hour.request_count),
      error_rate_percent: Math.round((hour.error_count / hour.request_count) * 100 * 100) / 100,
      status_codes: hour.status_codes
    }))

    return {
      endpoint,
      method,
      total_requests: data.length,
      avg_response_time_ms: Math.round(data.reduce((sum, m) => sum + m.response_time_ms, 0) / data.length),
      p95_response_time_ms: calculatePercentile(data.map(m => m.response_time_ms), 95),
      error_rate_percent: Math.round((data.filter(m => m.status_code >= 400).length / data.length) * 100 * 100) / 100,
      hourly_trends: hourlyArray
    }
  } catch (error) {
    console.error('Endpoint performance calculation error:', error)
    return null
  }
}

async function getTopSlowEndpoints(startDate: Date) {
  try {
    const { data, error } = await supabase
      .from('performance_metrics')
      .select('endpoint, method, response_time_ms')
      .gte('created_at', startDate.toISOString())
      .order('response_time_ms', { ascending: false })
      .limit(10)

    if (error) {
      console.error('Top slow endpoints error:', error)
      return []
    }

    if (!data) return []

    // Group by endpoint and calculate average response time
    const endpointStats = data.reduce((acc, metric) => {
      const key = `${metric.method} ${metric.endpoint}`
      if (!acc[key]) {
        acc[key] = {
          endpoint: metric.endpoint,
          method: metric.method,
          total_time: 0,
          count: 0,
          max_time: 0
        }
      }

      acc[key].total_time += metric.response_time_ms
      acc[key].count++
      acc[key].max_time = Math.max(acc[key].max_time, metric.response_time_ms)

      return acc
    }, {} as Record<string, {
      endpoint: string
      method: string
      total_time: number
      count: number
      max_time: number
    }>)

    // Convert to array and sort by average response time
    interface EndpointStat {
      endpoint: string
      method: string
      total_time: number
      count: number
      max_time: number
    }
    return Object.values(endpointStats)
      .map((stat: EndpointStat) => ({
        endpoint: stat.endpoint,
        method: stat.method,
        avg_response_time_ms: Math.round(stat.total_time / stat.count),
        max_response_time_ms: stat.max_time,
        request_count: stat.count
      }))
      .sort((a, b) => b.avg_response_time_ms - a.avg_response_time_ms)
      .slice(0, 10)

  } catch (error) {
    console.error('Top slow endpoints error:', error)
    return []
  }
}

async function getErrorAnalysis(startDate: Date) {
  try {
    const { data, error } = await supabase
      .from('performance_metrics')
      .select('endpoint, method, status_code, response_time_ms, created_at')
      .gte('created_at', startDate.toISOString())
      .gte('status_code', 400)
      .order('created_at', { ascending: false })

    if (error) {
      console.error('Error analysis error:', error)
      return null
    }

    if (!data || data.length === 0) {
      return {
        total_errors: 0,
        error_rate_percent: 0,
        status_code_distribution: {},
        error_trends: []
      }
    }

    // Group errors by status code
    const statusCodeDistribution = data.reduce((acc, metric) => {
      const statusKey = Math.floor(metric.status_code / 100) + 'xx'
      acc[statusKey] = (acc[statusKey] || 0) + 1
      return acc
    }, {} as Record<string, number>)

    // Group errors by hour for trend analysis
    const hourlyErrors = data.reduce((acc, metric) => {
      const hour = new Date(metric.created_at).toISOString().slice(0, 13) + ':00:00Z'
      acc[hour] = (acc[hour] || 0) + 1
      return acc
    }, {} as Record<string, number>)

    const errorTrends = Object.entries(hourlyErrors)
      .map(([hour, count]) => ({ hour, error_count: count }))
      .sort((a, b) => a.hour.localeCompare(b.hour))

    // Get total requests for error rate calculation
    const { count: totalRequests } = await supabase
      .from('performance_metrics')
      .select('*', { count: 'exact', head: true })
      .gte('created_at', startDate.toISOString())

    const errorRate = totalRequests ? (data.length / totalRequests) * 100 : 0

    return {
      total_errors: data.length,
      error_rate_percent: Math.round(errorRate * 100) / 100,
      status_code_distribution: statusCodeDistribution,
      error_trends: errorTrends,
      top_error_endpoints: getTopErrorEndpoints(data)
    }

  } catch (error) {
    console.error('Error analysis calculation error:', error)
    return null
  }
}

interface ErrorDataItem {
  endpoint?: string
  method?: string
  error_count?: number
  [key: string]: unknown
}

function getTopErrorEndpoints(errorData: ErrorDataItem[]) {
  const endpointErrors = errorData.reduce((acc, metric) => {
    const key = `${metric.method} ${metric.endpoint}`
    acc[key] = (acc[key] || 0) + 1
    return acc
  }, {} as Record<string, number>)

      return Object.entries(endpointErrors)
      .map(([endpoint, count]) => {
        const [method, path] = endpoint.split(' ', 2)
        return { endpoint: path, method, error_count: count as number }
      })
      .sort((a, b) => (b.error_count as number) - (a.error_count as number))
      .slice(0, 10)
}

function calculatePercentile(values: number[], percentile: number): number {
  if (values.length === 0) return 0

  const sorted = values.sort((a, b) => a - b)
  const index = Math.ceil((percentile / 100) * sorted.length) - 1
  return sorted[Math.max(0, index)]
}

// POST endpoint to record performance metrics
export async function POST(request: NextRequest) {
  try {
    const body = await request.json()
    const { endpoint, method, responseTimeMs, statusCode, userId, ipAddress, userAgent } = body

    if (!endpoint || !method || !responseTimeMs || !statusCode) {
      return NextResponse.json({ error: 'Missing required fields' }, { status: 400 })
    }

    // Insert performance metric
    const { error } = await supabase
      .from('performance_metrics')
      .insert({
        endpoint,
        method,
        response_time_ms: responseTimeMs,
        status_code: statusCode,
        user_id: userId || null,
        ip_address: ipAddress || null,
        user_agent: userAgent || null
      })

    if (error) {
      console.error('Performance metric insert error:', error)
      return NextResponse.json({ error: 'Failed to record metric' }, { status: 500 })
    }

    return NextResponse.json({ success: true })

  } catch (error) {
    console.error('Performance metric recording error:', error)
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 })
  }
}

import { NextRequest, NextResponse } from "next/server";
import { createClient } from "@supabase/supabase-js";

import { supabaseAdmin as supabase } from '@/lib/supabaseProxies';

export async function GET(req: NextRequest) {
  try {
    const { searchParams } = new URL(req.url);
    const timeRange = searchParams.get("timeRange") || "30d";
    const specialtyId = searchParams.get("specialtyId");

    // Calculate date range
    const now = new Date();
    const days = timeRange === "7d" ? 7 : timeRange === "30d" ? 30 : timeRange === "90d" ? 90 : 365;
    const startDate = new Date(now.getTime() - (days * 24 * 60 * 60 * 1000));

    let analytics: any = {};

    if (specialtyId) {
      // Single specialty analytics
      analytics = await getSpecialtyAnalytics(specialtyId, startDate);
    } else {
      // Overall specialty analytics
      analytics = await getAllSpecialtiesAnalytics(startDate);
    }

    return NextResponse.json({
      success: true,
      analytics,
      timeRange,
      generatedAt: new Date().toISOString()
    });

  } catch (error) {
    console.error("Specialty analytics error:", error);
    return NextResponse.json({ error: "Internal server error" }, { status: 500 });
  }
}

async function getSpecialtyAnalytics(specialtyId: string, startDate: Date) {
  // Get specialty details
  const { data: specialty } = await supabase
    .from("specialties")
    .select("id, name, parent_id, path")
    .eq("id", specialtyId)
    .single();

  if (!specialty) {
    throw new Error("Specialty not found");
  }

  // Get vendor count for this specialty
  const { count: vendorCount } = await supabase
    .from("vendor_specialties")
    .select("*", { count: "exact", head: true })
    .eq("specialty_id", specialtyId);

  // Get booking analytics for this specialty
  const { data: bookings } = await supabase
    .from("bookings")
    .select(`
      *,
      services!inner(*),
      profiles!inner(
        vendor_specialties!inner(
          specialty_id
        )
      )
    `)
    .eq("profiles.vendor_specialties.specialty_id", specialtyId)
    .gte("created_at", startDate.toISOString());

  // Get revenue analytics
  const totalRevenue = bookings?.reduce((sum, booking) => 
    sum + (booking.total_amount_cents || 0), 0) || 0;

  // Get booking trends over time
  const bookingTrends = getBookingTrends(bookings || [], startDate);

  // Get top performing vendors in this specialty
  const { data: topVendors } = await supabase
    .from("profiles")
    .select(`
      id,
      full_name,
      business_name,
      average_rating,
      total_bookings,
      vendor_specialties!inner(
        specialty_id,
        is_primary
      )
    `)
    .eq("vendor_specialties.specialty_id", specialtyId)
    .eq("role", "vendor")
    .order("total_bookings", { ascending: false })
    .limit(10);

  // Get geographic distribution
  const { data: locations } = await supabase
    .from("provider_locations")
    .select(`
      latitude,
      longitude,
      profiles!inner(
        vendor_specialties!inner(
          specialty_id
        )
      )
    `)
    .eq("profiles.vendor_specialties.specialty_id", specialtyId);

  const geographicDistribution = getGeographicDistribution(locations || []);

  return {
    specialty,
    metrics: {
      vendorCount: vendorCount || 0,
      totalBookings: bookings?.length || 0,
      totalRevenue: Math.round(totalRevenue / 100), // Convert to dollars
      averageRating: calculateAverageRating(bookings || []),
      bookingTrends,
      topVendors: topVendors || [],
      geographicDistribution
    }
  };
}

async function getAllSpecialtiesAnalytics(startDate: Date) {
  // Get all specialties with their metrics
  const { data: specialties } = await supabase
    .from("specialties")
    .select("id, name, parent_id, path")
    .eq("is_active", true)
    .order("name");

  const specialtyMetrics = await Promise.all(
    (specialties || []).map(async (specialty) => {
      const { count: vendorCount } = await supabase
        .from("vendor_specialties")
        .select("*", { count: "exact", head: true })
        .eq("specialty_id", specialty.id);

      const { count: bookingCount } = await supabase
        .from("bookings")
        .select("*", { count: "exact", head: true })
        .gte("created_at", startDate.toISOString());

      return {
        ...specialty,
        vendorCount: vendorCount || 0,
        bookingCount: bookingCount || 0
      };
    })
  );

  // Get overall trends
  const { data: allBookings } = await supabase
    .from("bookings")
    .select("created_at, total_amount_cents")
    .gte("created_at", startDate.toISOString());

  const overallTrends = getBookingTrends(allBookings || [], startDate);

  // Get specialty popularity ranking
  const popularityRanking = specialtyMetrics
    .sort((a, b) => b.bookingCount - a.bookingCount)
    .slice(0, 10);

  return {
    overview: {
      totalSpecialties: specialties?.length || 0,
      totalVendors: specialtyMetrics.reduce((sum, s) => sum + s.vendorCount, 0),
      totalBookings: allBookings?.length || 0,
      overallTrends
    },
    specialtyMetrics,
    popularityRanking
  };
}

function getBookingTrends(bookings: any[], startDate: Date) {
  const trends: Record<string, { count: number; revenue: number }> = {};

  // Group by day
  bookings.forEach(booking => {
    const date = new Date(booking.created_at).toISOString().split('T')[0];
    if (!trends[date]) {
      trends[date] = { count: 0, revenue: 0 };
    }
    trends[date].count++;
    trends[date].revenue += booking.total_amount_cents || 0;
  });

  // Fill in missing dates
  const dates = [];
  const current = new Date(startDate);
  const end = new Date();

  while (current <= end) {
    const dateStr = current.toISOString().split('T')[0];
    if (!trends[dateStr]) {
      trends[dateStr] = { count: 0, revenue: 0 };
    }
    dates.push(dateStr);
    current.setDate(current.getDate() + 1);
  }

  // Convert to sorted array
  return dates.map(date => ({
    date,
    ...trends[date]
  }));
}

function calculateAverageRating(bookings: any[]) {
  const ratings = bookings
    .map(b => b.rating)
    .filter(r => r && r > 0);

  if (ratings.length === 0) return 0;

  const sum = ratings.reduce((acc, r) => acc + r, 0);
  return Math.round((sum / ratings.length) * 10) / 10;
}

function getGeographicDistribution(locations: any[]) {
  const countries: Record<string, number> = {};
  const cities: Record<string, number> = {};

  locations.forEach(location => {
    if (location.latitude && location.longitude) {
      // This is a simplified approach - in production you'd use a geocoding service
      // to get actual country/city from coordinates
      const country = "Unknown"; // Placeholder
      const city = "Unknown"; // Placeholder

      countries[country] = (countries[country] || 0) + 1;
      cities[city] = (cities[city] || 0) + 1;
    }
  });

  return {
    countries: Object.entries(countries).map(([name, count]) => ({ name, count })),
    cities: Object.entries(cities).map(([name, count]) => ({ name, count }))
  };
}

import { NextRequest, NextResponse } from 'next/server'
import { createClient } from '@supabase/supabase-js'
import { getSupabaseConfig } from '@/config/supabase'

export async function GET(request: NextRequest) {
  try {
    const config = getSupabaseConfig()
    if (!config?.url || !config?.publishableKey) {
      return NextResponse.json({ error: 'Invalid Supabase configuration' }, { status: 500 })
    }
    const supabase = createClient(config.url, config.publishableKey)

    const { searchParams } = new URL(request.url)
    const timeRange = searchParams.get('range') || '7d'

    // Calculate date range
    const now = new Date()
    const startDate = new Date()
    switch (timeRange) {
      case '24h':
        startDate.setHours(now.getHours() - 24)
        break
      case '7d':
        startDate.setDate(now.getDate() - 7)
        break
      case '30d':
        startDate.setDate(now.getDate() - 30)
        break
      default:
        startDate.setDate(now.getDate() - 7)
    }

    // Get conversion funnel data
    const { data: funnelData } = await supabase
      .from('conversion_funnels')
      .select('*')
      .gte('created_at', startDate.toISOString())
      .order('created_at', { ascending: false })

    // Calculate conversion rate
    let conversionRate = 0
    if (funnelData && funnelData.length > 0) {
      const started = funnelData.filter(f => f.step_name === 'started').length
      const completed = funnelData.filter(f => f.step_name === 'completed').length
      conversionRate = started > 0 ? (completed / started) * 100 : 0
    }

    // Get revenue data from bookings
    const { data: revenueData } = await supabase
      .from('bookings')
      .select('total_amount, status')
      .eq('status', 'completed')
      .gte('created_at', startDate.toISOString())

    const revenue = revenueData?.reduce((sum, booking) => sum + (booking.total_amount || 0), 0) || 0

    // Get geographic distribution
    const { data: geoData } = await supabase
      .from('analytics_events')
      .select('properties')
      .not('properties->country', 'is', null)
      .gte('created_at', startDate.toISOString())

    const geographicDistribution: Record<string, number> = {}
    geoData?.forEach(event => {
      const country = (event.properties as any)?.country
      if (country && country !== 'unknown') {
        geographicDistribution[country] = (geographicDistribution[country] || 0) + 1
      }
    })

    // Get device breakdown
    const { data: deviceData } = await supabase
      .from('analytics_events')
      .select('properties')
      .not('properties->device_type', 'is', null)
      .gte('created_at', startDate.toISOString())

    const deviceBreakdown: Record<string, number> = {}
    deviceData?.forEach(event => {
      const device = (event.properties as any)?.device_type
      if (device) {
        deviceBreakdown[device] = (deviceBreakdown[device] || 0) + 1
      }
    })

    // Get error rate and critical errors
    const { data: errorData } = await supabase
      .from('analytics_events')
      .select('*')
      .or('event_name.like.%error%,event_name.like.%failed%')
      .gte('created_at', startDate.toISOString())

    const totalEvents = await supabase
      .from('analytics_events')
      .select('id', { count: 'exact' })
      .gte('created_at', startDate.toISOString())

    const errorRate = totalEvents.count && totalEvents.count > 0 
      ? (errorData?.length || 0) / totalEvents.count * 100 
      : 0

    // Group errors by type and severity
    const errorGroups: Record<string, { count: number; lastOccurrence: string }> = {}
    errorData?.forEach(event => {
      const errorType = event.event_name
      if (errorType) {
        if (!errorGroups[errorType]) {
          errorGroups[errorType] = { count: 0, lastOccurrence: event.created_at }
        }
        errorGroups[errorType].count++
        if (new Date(event.created_at) > new Date(errorGroups[errorType].lastOccurrence)) {
          errorGroups[errorType].lastOccurrence = event.created_at
        }
      }
    })

    // Convert to critical errors array with severity
    const criticalErrors = Object.entries(errorGroups).map(([error, data]) => ({
      error,
      count: data.count,
      lastOccurrence: data.lastOccurrence,
      severity: data.count > 10 ? 'high' : data.count > 5 ? 'medium' : 'low' as const
    })).sort((a, b) => b.count - a.count)

    // Get active users (unique users in time range)
    const { data: userData } = await supabase
      .from('analytics_events')
      .select('properties')
      .not('properties->user_id', 'is', null)
      .gte('created_at', startDate.toISOString())

    const uniqueUsers = new Set(userData?.map(event => (event.properties as any)?.user_id).filter(Boolean))
    const activeUsers = uniqueUsers.size

    // Calculate p95 session duration (placeholder - would need session tracking)
    const p95SessionDuration = 180 // 3 minutes average

    const systemMetrics = {
      p95SessionDuration,
      errorRate: parseFloat(errorRate.toFixed(2)),
      activeUsers,
      conversionRate: parseFloat(conversionRate.toFixed(1)),
      revenue,
      geographicDistribution,
      deviceBreakdown,
      criticalErrors
    }

    return NextResponse.json({ 
      success: true, 
      data: systemMetrics 
    })

  } catch (error) {
    console.error('System metrics error:', error)
    return NextResponse.json({ 
      success: false, 
      error: 'Failed to load system metrics' 
    }, { status: 500 })
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { createServerClient } from '@supabase/ssr'
import { getSupabaseConfig } from '@/config/supabase'
import { cookies } from 'next/headers'

// Optional: Admin org IDs via environment variable (for organization-based admin access)
const ADMIN_ORG_IDS = process.env.ADMIN_ORG_IDS?.split(',').filter(Boolean) || []

export async function GET(request: NextRequest) {
  try {
    const config = getSupabaseConfig()
    const cookieStore = await cookies()

    // Use createServerClient to properly read Supabase session cookies
    const supabase = createServerClient(
      config.url,
      config.publishableKey,
      {
        cookies: {
          getAll() {
            return cookieStore.getAll()
          },
          setAll(cookiesToSet) {
            try {
              cookiesToSet.forEach(({ name, value, options }) => {
                cookieStore.set(name, value, options)
              })
            } catch (error) {
              // The `setAll` method was called from a Server Component or Route Handler.
              // This can be ignored if you have middleware refreshing user sessions.
            }
          }
        }
      }
    )

    // Get user from session
    const { data: { user }, error: authError } = await supabase.auth.getUser()
    if (authError || !user) {
      return NextResponse.json({ isAdmin: false, error: authError?.message }, { status: 200 })
    }

    // Check profiles table for admin role (primary role source)
    const { data: profile } = await supabase
      .from('profiles')
      .select('id, role, org_id')
      .eq('id', user.id)
      .maybeSingle()

    if (profile?.role === 'admin') {
      return NextResponse.json({ isAdmin: true, userId: user.id, email: user.email, reason: 'profile_role' })
    }

    // Check user_roles table (flexible role management)
    // user_roles.app_user_id references app_users.id, so we need to join through app_users
    const { data: appUser } = await supabase
      .from('app_users')
      .select('id')
      .eq('auth_user_id', user.id)
      .maybeSingle()

    if (appUser?.id) {
      const { data: userRole } = await supabase
        .from('user_roles')
        .select('role')
        .eq('app_user_id', appUser.id)
        .eq('role', 'admin')
        .maybeSingle()

      if (userRole?.role === 'admin') {
        return NextResponse.json({ isAdmin: true, userId: user.id, email: user.email, reason: 'user_roles' })
      }
    }

    // Optional: Check if user is admin via org_id (only if ADMIN_ORG_IDS is configured)
    if (ADMIN_ORG_IDS.length > 0 && profile?.org_id && ADMIN_ORG_IDS.includes(profile.org_id)) {
      return NextResponse.json({ isAdmin: true, userId: user.id, email: user.email, reason: 'org_id' })
    }

    return NextResponse.json({ isAdmin: false, userId: user.id, email: user.email })
  } catch (error) {
    console.error('Admin check error:', error)
    return NextResponse.json({ isAdmin: false }, { status: 200 })
  }
}

import { createServerClient } from '@supabase/ssr'
import { cookies } from 'next/headers'
import { NextRequest, NextResponse } from 'next/server'
import { google } from 'googleapis'
import { CalendarProvider } from '@/lib/calendar-adapters/types'
import { getSupabaseConfig } from '@/config/supabase'

const oauth2Client = new google.auth.OAuth2(
  process.env.GOOGLE_CLIENT_ID,
  process.env.GOOGLE_CLIENT_SECRET,
  `${process.env.NEXT_PUBLIC_APP_URL}/api/auth/google/callback`
)

export async function GET(request: NextRequest) {
  try {
    const config = getSupabaseConfig()
    
    // Exchange code for tokens
    const { code, email } = await request.json()
    const { tokens } = await oauth2Client.getToken(code)
    const { access_token, refresh_token, expiry_date } = tokens

    // Get user info from Google
    oauth2Client.setCredentials({ access_token })
    const oauth2 = google.oauth2('v2')
    const { data: userInfo } = await oauth2.userinfo.get({ auth: oauth2Client })

    const cookieStore = await cookies();
    const supabase = createServerClient(
      config.url,
      config.publishableKey,
      {
        cookies: {
          getAll() {
            return cookieStore.getAll()
          },
          setAll(cookiesToSet) {
            try {
              cookiesToSet.forEach(({ name, value, options }) => {
                cookieStore.set(name, value, options)
              })
            } catch (error) {
              // The `setAll` method was called from a Server Component or Route Handler.
              // This can be ignored if you have middleware refreshing user sessions.
            }
          }
        },
      }
    );

    // Store in external_calendar_connections
    const { data: connection, error } = await supabase
      .from('external_calendar_connections')
      .insert({
        provider: CalendarProvider.GOOGLE,
        provider_user_id: userInfo.id,
        provider_email: email || userInfo.email,
        provider_calendar_id: 'primary', // We'll update this after getting calendar list
        access_token,
        refresh_token,
        token_expiry: new Date(expiry_date!),
        sync_enabled: true
      })
      .select()
      .single()

    if (error) throw error

    return NextResponse.json({
      success: true,
      connection_id: connection.id,
      provider_email: email || userInfo.email
    })
  } catch (error) {
    console.error('Error in Google Calendar callback:', error)
    return NextResponse.json(
      { error: 'Failed to complete Google Calendar connection' },
      { status: 500 }
    )
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { getServerSupabase } from '@/lib/supabaseServer'
import { GoogleCalendarAdapterFactory } from '@/lib/calendar-adapters/google'
import type { CalendarSystemConfig, CalendarCredentials } from '@/lib/calendar-adapters/types'

export async function POST(request: NextRequest) {
  try {
    const { profileId } = await request.json()

    if (!profileId) {
      return NextResponse.json({ error: 'Profile ID is required' }, { status: 400 })
    }

    const supabase = new Proxy({} as any, { get: (target, prop) => (getServerSupabase() as any)[prop] }) as ReturnType<typeof getServerSupabase>

    // Get the calendar connection details
    const { data: connection, error: fetchError } = await supabase
      .from('provider_calendars')
      .select('*')
      .eq('profile_id', profileId)
      .single()

    if (fetchError) {
      console.error('Error fetching calendar connection:', fetchError)
      return NextResponse.json({ error: 'Failed to fetch calendar connection' }, { status: 500 })
    }

    if (!connection) {
      return NextResponse.json({ error: 'No calendar connection found' }, { status: 404 })
    }

    // Get the calendar system config
    const { data: systemConfig, error: configError } = await supabase
      .from('external_calendar_systems')
      .select('*')
      .eq('type', connection.system_type)
      .single()

    if (configError) {
      console.error('Error fetching calendar system config:', configError)
      return NextResponse.json({ error: 'Failed to fetch calendar system config' }, { status: 500 })
    }

    // Create and use the calendar adapter
    const factory = new GoogleCalendarAdapterFactory()
    const adapter = factory.createAdapter(systemConfig as CalendarSystemConfig)

    // Connect with the existing credentials to perform the disconnect
    const credentials: CalendarCredentials = {
      access_token: connection.access_token,
      refresh_token: connection.refresh_token,
      token_expiry: new Date(connection.expiry_date),
      provider_email: connection.google_email,
      provider_user_id: connection.provider_user_id
    }

    await adapter.connect(credentials.access_token)
    await adapter.disconnect(connection.id)

    // Delete the calendar connection record
    const { error: deleteError } = await supabase
      .from('provider_calendars')
      .delete()
      .eq('profile_id', profileId)

    if (deleteError) {
      console.error('Error deleting calendar connection:', deleteError)
      return NextResponse.json({ error: 'Failed to delete calendar connection' }, { status: 500 })
    }

    return NextResponse.json({ 
      success: true, 
      message: 'Calendar disconnected successfully' 
    })

  } catch (error) {
    console.error('Calendar disconnect error:', error)
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 })
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { getServerSupabase } from '@/lib/supabaseServer'

export async function GET(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url)
    const profileId = searchParams.get('profileId')

