      return NextResponse.json({ error: 'Profile ID is required' }, { status: 400 })
    }

    const supabase = new Proxy({} as any, { get: (target, prop) => (getServerSupabase() as any)[prop] }) as ReturnType<typeof getServerSupabase>

    // Check if Google Calendar is connected
    const { data: connection, error } = await supabase
      .from('provider_calendars')
      .select('id, created_at, expiry_date, google_email, system_type')
      .eq('profile_id', profileId)
      .eq('system_type', 'google')
      .single()

    if (error && error.code !== 'PGRST116') { // PGRST116 is "no rows returned"
      console.error('Error checking Google Calendar status:', error)
      return NextResponse.json({ error: 'Failed to check connection status' }, { status: 500 })
    }

    const isConnected = !!connection
    const connectionInfo = connection ? {
      connectedAt: connection.created_at,
      expiresAt: connection.expiry_date,
      isExpired: new Date() > new Date(connection.expiry_date),
      googleEmail: connection.google_email
    } : null

    return NextResponse.json({ 
      success: true,
      isConnected,
      connectionInfo
    })

  } catch (error) {
    console.error('Google Calendar status check error:', error)
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 })
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { createServerClient } from '@supabase/ssr'
import { cookies } from 'next/headers'
import { getSupabaseConfig } from '@/config/supabase'

/**
 * Server-side profile creation/repair endpoint
 * 
 * Called by authenticated clients when profile doesn't exist
 * Ensures profile creation happens with proper validation and error handling
 * 
 * POST /api/auth/profile/create
 * Auth: Required (user must be authenticated)
 * Body: { role?: 'customer' | 'vendor' }
 */
export async function POST(request: NextRequest) {
  try {
    const cookieStore = await cookies()
    const config = getSupabaseConfig()
    
    const supabase = createServerClient(
      config.url,
      config.publishableKey,
      {
        cookies: {
          getAll() {
            return cookieStore.getAll()
          },
          setAll(cookiesToSet) {
            try {
              cookiesToSet.forEach(({ name, value, options }) => {
                cookieStore.set(name, value, options)
              })
            } catch (error) {
              // The `setAll` method was called from a Server Component or Route Handler.
              // This can be ignored if you have middleware refreshing user sessions.
            }
          }
        }
      }
    )

    // Get authenticated user
    const { data: { user }, error: authError } = await supabase.auth.getUser()

    if (authError || !user) {
      return NextResponse.json(
        { error: 'Unauthorized - authentication required' },
        { status: 401 }
      )
    }

    const { role = 'customer' } = await request.json()

    // Validate role
    if (!['customer', 'vendor'].includes(role)) {
      return NextResponse.json(
        { error: 'Invalid role - must be customer or vendor' },
        { status: 400 }
      )
    }

    // Check if profile already exists
    const { data: existingProfile, error: checkError } = await supabase
      .from('profiles')
      .select('id')
      .eq('id', user.id)
      .maybeSingle()

    if (checkError) {
      console.error('Error checking existing profile:', checkError)
      return NextResponse.json(
        { error: 'Failed to check profile status' },
        { status: 500 }
      )
    }

    // If profile exists, return it
    if (existingProfile) {
      const { data: profile, error: fetchError } = await supabase
        .from('profiles')
        .select('*')
        .eq('id', user.id)
        .single()

      if (fetchError) {
        return NextResponse.json(
          { error: 'Failed to fetch profile' },
          { status: 500 }
        )
      }

      return NextResponse.json({
        success: true,
        profile,
        message: 'Profile already exists'
      })
    }

    // Create new profile with validated data
    const { data: newProfile, error: createError } = await supabase
      .from('profiles')
      .insert({
        id: user.id,
        email: user.email,
        full_name: user.user_metadata?.full_name || 'User',
        role,
        created_at: new Date().toISOString(),
        updated_at: new Date().toISOString()
      })
      .select()
      .single()

    if (createError) {
      console.error('Error creating profile:', createError)
      return NextResponse.json(
        { error: 'Failed to create profile' },
        { status: 500 }
      )
    }

    return NextResponse.json({
      success: true,
      profile: newProfile,
      message: 'Profile created successfully'
    })
  } catch (error) {
    console.error('Profile creation error:', error)
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    )
  }
}

import { NextResponse } from 'next/server'
import { limitRequest } from '@/middleware/requestLimiter'
import { getServerSupabase } from '@/lib/supabaseServer'

const supabase = new Proxy({} as any, { get: (target, prop) => (getServerSupabase() as any)[prop] }) as ReturnType<typeof getServerSupabase>
import { referralService } from '@/lib/referrals'

export async function POST(request: Request) {
  try {
    const limited = await limitRequest(request, { windowMs: 60_000, max: 5 })
    if (limited) return limited
    const { email, password, full_name, role = 'customer' } = await request.json()

    // Validate input
    if (!email || !password) {
      return NextResponse.json({ 
        error: 'Email and password are required' 
      }, { status: 400 })
    }

    // Create user in Supabase Auth
    const { data: authData, error: authError } = await supabase.auth.signUp({
      email,
      password,
      options: {
        data: {
          full_name,
          role
        }
      }
    })

    if (authError) {
      console.error('Auth registration error:', authError)
      return NextResponse.json({
        error: authError.message
      }, { status: 400 })
    }

    if (!authData.user) {
      return NextResponse.json({ 
        error: 'Failed to create user' 
      }, { status: 500 })
    }

    // Create profile in database
    // Don't use userService.upsertProfile since we don't have auth context yet
    const { error: profileError } = await supabase
      .from('profiles')
      .upsert({
        id: authData.user.id,
        full_name,
        email,
        role,
        created_at: new Date().toISOString(),
        updated_at: new Date().toISOString()
      })
      .select()
      .single()

    if (profileError) {
      console.error('Failed to create user profile:', profileError)
      // Note: User was created in auth but profile failed
      // You might want to handle this cleanup
    }

    // Credit referrer if there is a pending referral for this email
    await referralService.completeReferral(email, authData.user.id, role)

    try {
      const token = authData.session?.access_token || ''
      await fetch(`${process.env.NEXT_PUBLIC_BASE_URL || 'http://localhost:3000'}/api/notifications/send`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          type: 'email',
          recipient: email,
          template: 'verify_email',
          data: { name: full_name, token }
        })
      })
    } catch {}

    console.log('‚úÖ User registered successfully:', authData.user.id)

    return NextResponse.json({
      success: true,
      user: {
        id: authData.user.id,
        email: authData.user.email,
        role
      },
      message: 'Registration successful! Please check your email to verify your account.'
    })

  } catch (error) {
    console.error('Registration error:', error)
    return NextResponse.json({ 
      error: error instanceof Error ? error.message : 'Registration failed',
      success: false
    }, { status: 500 })
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { createServerClient } from '@supabase/ssr'
import { getSupabaseConfig } from '@/config/supabase'
import { cookies } from 'next/headers'

/**
 * Sync Supabase session from Authorization header to cookies
 * Called after client-side login to ensure server can read the session
 */
export async function POST(request: NextRequest) {
  try {
    const { access_token, refresh_token } = await request.json()
    
    if (!access_token) {
      return NextResponse.json({ error: 'access_token required' }, { status: 400 })
    }

    const config = getSupabaseConfig()
    const cookieStore = await cookies()
    
    const supabase = createServerClient(
      config.url,
      config.publishableKey,
      {
        cookies: {
          getAll() {
            return cookieStore.getAll()
          },
          setAll(cookiesToSet) {
            try {
              cookiesToSet.forEach(({ name, value, options }) => {
                cookieStore.set(name, value, options)
              })
            } catch (error) {
              // The `setAll` method was called from a Server Component or Route Handler.
              // This can be ignored if you have middleware refreshing user sessions.
            }
          }
        }
      }
    )

    // Set the session using the provided tokens
    const { data: { session }, error } = await supabase.auth.setSession({
      access_token,
      refresh_token: refresh_token || ''
    })

    if (error || !session) {
      return NextResponse.json({ error: error?.message || 'Failed to set session' }, { status: 400 })
    }

    return NextResponse.json({ 
      success: true, 
      user: session.user 
    })
  } catch (error) {
    console.error('Session sync error:', error)
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 })
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { getServerSupabase } from '@/lib/supabaseServer';

const supabase = new Proxy({} as any, { get: (target, prop) => (getServerSupabase() as any)[prop] }) as ReturnType<typeof getServerSupabase>
import { google } from 'googleapis';

interface ProviderSchedule {
  profile_id: string
  day_of_week: number
  start_time: string
  end_time: string
}

interface AvailabilitySlot {
  provider_id: string
  start_time: string
  end_time: string
  status: 'available' | 'booked' | 'blocked'
}

interface GoogleCalendarBusySlot {
  start: string
  end: string
}

// This function will be the core of our availability generation.
// For now, it's a placeholder.
async function generateAvailability(providerId: string) {
    console.log(`Generating availability for provider: ${providerId}`);

    // 1. Fetch Provider's Schedule Template from our DB
    const { data: schedule, error: scheduleError } = await supabase
        .from('provider_schedules')
        .select('*')
        .eq('profile_id', providerId);

    if (scheduleError) {
        throw new Error(`Failed to fetch schedule: ${scheduleError.message}`);
    }

    if (!schedule || schedule.length === 0) {
        console.log('Provider has no schedule set. Skipping.');
        return { message: 'Provider has no schedule template.' };
    }

    // 2. Fetch Provider's Google Calendar Tokens from our DB
    const { data: tokenData, error: tokenError } = await supabase
        .from('provider_google_calendar')
        .select('access_token, refresh_token')
        .eq('profile_id', providerId)
        .single();

    if (tokenError || !tokenData) {
        throw new Error('Provider has not connected their Google Calendar.');
    }

    // 3. Use tokens to fetch busy times from Google Calendar API
    const oauth2Client = new google.auth.OAuth2(
        process.env.GOOGLE_CLIENT_ID,
        process.env.GOOGLE_CLIENT_SECRET,
        process.env.GOOGLE_REDIRECT_URI
    );

    oauth2Client.setCredentials({
        access_token: tokenData.access_token,
        refresh_token: tokenData.refresh_token,
    });

    const calendar = google.calendar({ version: 'v3', auth: oauth2Client });

    // Define the time range for the next 30 days
    const timeMin = new Date().toISOString();
    const timeMax = new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString();

    const freeBusyResponse = await calendar.freebusy.query({
        requestBody: {
            timeMin,
            timeMax,
            items: [{ id: 'primary' }],
        },
    });

    const busySlots = (freeBusyResponse.data.calendars?.primary.busy || []) as GoogleCalendarBusySlot[];

    console.log('Fetched schedule:', schedule);
    console.log('Fetched busy slots from Google:', busySlots);

    // 4. Implement the logic to "subtract" busy times from the schedule template.
    const serviceDurationMinutes = 60; // Hardcoded for now
    const finalSlots: AvailabilitySlot[] = [];
    const today = new Date();

    for (let i = 0; i < 30; i++) { // Generate for the next 30 days
        const currentDate = new Date(today);
        currentDate.setDate(today.getDate() + i);
        currentDate.setHours(0, 0, 0, 0);

        const dayOfWeek = currentDate.getDay();
        const scheduleForDay = (schedule as ProviderSchedule[]).filter(s => s.day_of_week === dayOfWeek);
        if (scheduleForDay.length === 0) continue; // Not working on this day

        for (const block of scheduleForDay) {
            const startHour = parseInt(block.start_time.split(':')[0]);
            const startMinute = parseInt(block.start_time.split(':')[1]);
            const endHour = parseInt(block.end_time.split(':')[0]);
            const endMinute = parseInt(block.end_time.split(':')[1]);

            const slotStart = new Date(currentDate);
            slotStart.setHours(startHour, startMinute);

            const slotEnd = new Date(slotStart);
            slotEnd.setMinutes(slotStart.getMinutes() + serviceDurationMinutes);

            while(slotEnd.getHours() < endHour || (slotEnd.getHours() === endHour && slotEnd.getMinutes() <= endMinute)) {
                // Check for conflicts with Google Calendar busy slots
                const isOverlapping = busySlots.some(busy => {
                    const busyStart = new Date(busy.start);
                    const busyEnd = new Date(busy.end);
                    // Check if the potential slot is contained within a busy slot
                    return (slotStart < busyEnd && slotEnd > busyStart);
                });

                if (!isOverlapping) {
                    finalSlots.push({
                        provider_id: providerId,
                        start_time: new Date(slotStart.getTime() - (slotStart.getTimezoneOffset() * 60000)).toISOString(),
                        end_time: new Date(slotEnd.getTime() - (slotEnd.getTimezoneOffset() * 60000)).toISOString(),
                        status: 'available',
                    });
                }

                // Move to the next slot
                slotStart.setMinutes(slotStart.getMinutes() + serviceDurationMinutes);
                slotEnd.setMinutes(slotEnd.getMinutes() + serviceDurationMinutes);
            }
        }
    }

    // 5. Save the final, bookable slots into the `availability_slots` table.

    // First, delete all future availability for this provider to avoid duplicates.
    const { error: deleteError } = await supabase
        .from('availability_slots')
        .delete()
        .eq('provider_id', providerId)
        .gte('start_time', new Date().toISOString());

    if (deleteError) {
        throw new Error(`Failed to delete old slots: ${deleteError.message}`);
    }

    // Then, insert the new slots.
    if (finalSlots.length > 0) {
        const { error: insertError } = await supabase
            .from('availability_slots')
            .insert(finalSlots);

        if (insertError) {
            throw new Error(`Failed to insert new slots: ${insertError.message}`);
        }
    }

    return { message: `Successfully generated ${finalSlots.length} slots.`, finalSlots };
}


export async function POST(req: NextRequest) {
    try {
        // Be resilient in dev/e2e: tolerate empty/malformed bodies
        let providerId: string | undefined
        try {
            const body = await req.text()
            if (body) {
                const parsed = JSON.parse(body)
                providerId = parsed?.providerId
            }
        } catch {}

        // In non-production (dev/tests/e2e), return a stub instead of hitting external services
        if (process.env.NODE_ENV !== 'production' || process.env.E2E === '1') {
            return NextResponse.json({ message: 'Stubbed availability generation (dev/e2e)', finalSlots: [] })
        }

        if (!providerId) {
            return NextResponse.json({ error: 'Missing providerId' }, { status: 400 });
        }

        const result = await generateAvailability(providerId);

        return NextResponse.json(result);
    } catch (error) {
        console.error('Error in /api/availability/generate:', error);
        return NextResponse.json({ 
            error: error instanceof Error ? error.message : 'Internal Server Error' 
        }, { status: 500 });
    }
}

import { NextRequest, NextResponse } from 'next/server'
import { getServerSupabase } from '@/lib/supabaseServer'

const supabase = new Proxy({} as any, { get: (target, prop) => (getServerSupabase() as any)[prop] }) as ReturnType<typeof getServerSupabase>

// Free availability search: no payment intent required
export async function POST(req: NextRequest) {
  try {
    const {
      providerId,
      date,
      serviceDetails,
      desiredDateTime,
      customerLocation
    } = await req.json()

    if (!providerId) {
      return NextResponse.json({ error: 'Missing providerId' }, { status: 400 })
    }

    // Fetch all open slots for the provider on or after the specified date
    const { data: slots, error } = await supabase
      .from('availability_slots')
      .select('id, start_time, end_time')
      .eq('provider_id', providerId)
      .eq('is_booked', false)
      .gte('start_time', date ? `${date}T00:00:00Z` : new Date().toISOString())
      .order('start_time')

    if (error) {
      console.error('Error fetching slots:', error)
      return NextResponse.json({ error: 'Failed to fetch availability' }, { status: 500 })
    }

    if ((slots?.length || 0) === 0 && serviceDetails && desiredDateTime && customerLocation) {
      try {
        // Calculate optimal radius based on provider density
        const calculateRadiusZone = async (lat: number, lng: number) => {
          // Get counts at different radii to determine density
          const [twoKm, fiveKm] = await Promise.all([
            supabase.rpc('get_providers_within_radius', {
              p_latitude: lat,
              p_longitude: lng,
              p_radius_km: 2
            }),
            supabase.rpc('get_providers_within_radius', {
              p_latitude: lat,
              p_longitude: lng,
              p_radius_km: 5
            })
          ]);

          const twoKmCount = twoKm.data?.length || 0;
          const fiveKmCount = fiveKm.data?.length || 0;

          if (twoKmCount >= 8) {
            return { radius: 2, density: 'dense' };
          } else if (fiveKmCount >= 4) {
            return { radius: 5, density: 'medium' };
          } else {
            return { radius: 10, density: 'sparse' };
          }
        };

        // Get optimal radius for this location
        const radiusZone = await calculateRadiusZone(customerLocation.lat, customerLocation.lng);
        const optimalRadius = radiusZone.radius;

        // Try AI-powered radius scaling for even better optimization
        let recommendedRadius = optimalRadius;
        try {
          const aiRadiusResponse = await fetch(`${req.nextUrl.origin}/api/ai-radius-scaling`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
              service: serviceDetails,
              location: customerLocation,
              providerDensity: radiusZone.density,
              currentRadius: optimalRadius
            })
          });

          if (aiRadiusResponse.ok) {
            const aiData = await aiRadiusResponse.json();
            if (aiData.success && aiData.recommendedRadius) {
              recommendedRadius = aiData.recommendedRadius;
            }
          }
        } catch (aiError) {
          console.log('AI radius scaling failed, using density-based radius:', aiError);
          // Fall back to density-based radius
        }

        // Create service request
        await supabase.from('service_requests').insert({
          service_details: serviceDetails,
          desired_datetime: desiredDateTime,
          customer_lat: customerLocation.lat,
          customer_lng: customerLocation.lng,
          status: 'pending'
        })

        // Find vendors within the optimal dynamic radius
        const { data: providers } = await supabase.rpc('get_providers_within_radius', {
          p_latitude: customerLocation.lat,
          p_longitude: customerLocation.lng,
          p_radius_km: recommendedRadius
        })

        const notificationUrl = new URL('/api/notifications/send', req.url)

        await Promise.all(
          (providers || []).map((p: { user_id?: string; id?: string }) =>
            fetch(notificationUrl.toString(), {
              method: 'POST',
              headers: { 'Content-Type': 'application/json' },
              body: JSON.stringify({
                type: 'push',
                recipient: p.user_id || p.id,
                template: 'admin_alert',
                data: {
                  message: `New service request for ${serviceDetails} at ${desiredDateTime} within ${recommendedRadius}km`
                }
              })
            }).catch((err) => console.error('Failed to send notification', err))
          )
        )

        return NextResponse.json({ 
          success: true, 
          broadcasted: true,
          radiusUsed: recommendedRadius,
          providerDensity: radiusZone.density,
          providersNotified: providers?.length || 0
        })
      } catch (requestError) {
        console.error('Failed to create service request:', requestError)
        return NextResponse.json({ success: true, broadcasted: false })
      }
    }

    return NextResponse.json({ success: true, slots: slots || [] })
  } catch (err) {
    console.error('availability search error:', err)
    return NextResponse.json({ error: 'Internal Server Error' }, { status: 500 })
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { createServerClient } from '@supabase/ssr'
import { getSupabaseConfig } from '@/config/supabase'
import { cookies } from 'next/headers';
import { BlockUserRequest } from '@/types/global';

export async function POST(request: NextRequest) {
  try {
    const cookieStore = await cookies();
    const config = getSupabaseConfig()
    const supabase = createServerClient(
      config.url,
      config.publishableKey,
      {
        cookies: {
          getAll() {
            return cookieStore.getAll()
          },
          setAll(cookiesToSet) {
            try {
              cookiesToSet.forEach(({ name, value, options }) => {
                cookieStore.set(name, value, options)
              })
            } catch (error) {
              // The `setAll` method was called from a Server Component or Route Handler.
              // This can be ignored if you have middleware refreshing user sessions.
            }
          }
        }
      }
    )

    // Get authenticated user
    const { data: { user }, error: authError } = await supabase.auth.getUser();
    if (authError || !user) {
      return NextResponse.json(
        { error: 'Unauthorized' },
        { status: 401 }
      );
    }

    const body = await request.json();
    const { blocked_id, reason } = body as BlockUserRequest;

    if (!blocked_id) {
      return NextResponse.json(
        { error: 'Missing required fields' },
        { status: 400 }
      );
    }

    // Check if user is trying to block themselves
    if (blocked_id === user.id) {
      return NextResponse.json(
        { error: 'Cannot block yourself' },
        { status: 400 }
      );
    }

    // Check if block already exists
    const { data: existingBlock } = await supabase
      .from('user_blocks')
      .select('*')
      .eq('blocker_id', user.id)
      .eq('blocked_id', blocked_id)
      .single();

    if (existingBlock) {
      return NextResponse.json(
        { error: 'Block already exists' },
        { status: 400 }
      );
    }

    // Create the block
    const { data: block, error: blockError } = await supabase
      .from('user_blocks')
      .insert([
        {
          blocker_id: user.id,
          blocked_id,
          reason: reason || null
        }
      ])
      .select()
      .single();

    if (blockError) {
      return NextResponse.json(
        { error: 'Failed to create block' },
        { status: 500 }
      );
    }

    return NextResponse.json(block);
  } catch (error) {
    console.error('Error in blocks/create:', error);
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { createServerClient } from '@supabase/ssr'
import { getSupabaseConfig } from '@/config/supabase'
import { cookies } from 'next/headers';

export async function DELETE(request: NextRequest) {
  try {
    const cookieStore = await cookies();
    const config = getSupabaseConfig()
    const supabase = createServerClient(
      config.url,
      config.publishableKey,
      {
        cookies: {
          getAll() {
            return cookieStore.getAll()
          },
          setAll(cookiesToSet) {
            try {
              cookiesToSet.forEach(({ name, value, options }) => {
                cookieStore.set(name, value, options)
              })
            } catch (error) {
              // The `setAll` method was called from a Server Component or Route Handler.
              // This can be ignored if you have middleware refreshing user sessions.
            }
          }
        }
      }
    )

    // Get authenticated user
    const { data: { user }, error: authError } = await supabase.auth.getUser();
    if (authError || !user) {
      return NextResponse.json(
        { error: 'Unauthorized' },
        { status: 401 }
      );
    }

    // Get block ID from URL
    const url = new URL(request.url);
    const blockId = url.searchParams.get('id');

    if (!blockId) {
      return NextResponse.json(
        { error: 'Block ID is required' },
        { status: 400 }
      );
    }

    // Verify the user owns this block
    const { data: existingBlock } = await supabase
      .from('user_blocks')
      .select('*')
      .eq('id', blockId)
      .eq('blocker_id', user.id)
      .single();

    if (!existingBlock) {
      return NextResponse.json(
        { error: 'Block not found or unauthorized' },
        { status: 404 }
      );
    }

    // Delete the block
    const { error: deleteError } = await supabase
      .from('user_blocks')
      .delete()
      .eq('id', blockId)
      .eq('blocker_id', user.id);

    if (deleteError) {
      return NextResponse.json(
        { error: 'Failed to delete block' },
        { status: 500 }
      );
    }

    return NextResponse.json({ success: true });
  } catch (error) {
    console.error('Error in blocks/delete:', error);
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    );
  }
}

