import { NextRequest, NextResponse } from 'next/server';
import { getServerSupabase } from '@/lib/supabaseServer';
import { CreditsResponse, AddCreditsRequest } from '@/types/credits';

export async function POST(request: NextRequest): Promise<NextResponse<CreditsResponse>> {
  try {
    const supabase = new Proxy({} as any, { get: (target, prop) => (getServerSupabase() as any)[prop] }) as ReturnType<typeof getServerSupabase>;

    // Get user from auth
    const { data: { user }, error: authError } = await supabase.auth.getUser();
    if (authError || !user) {
      return NextResponse.json({
        success: false,
        error: 'Unauthorized'
      }, { status: 401 });
    }

    // Parse request body
    const body: AddCreditsRequest = await request.json();

    // Validate required fields
    if (!body.user_id || !body.amount || !body.transaction_type || !body.description) {
      return NextResponse.json({
        success: false,
        error: 'Missing required fields'
      }, { status: 400 });
    }

    // Only allow admins to add credits to other users
    if (body.user_id !== user.id) {
      const { data: userProfile } = await supabase
        .from('users')
        .select('role')
        .eq('id', user.id)
        .single();

      if (userProfile?.role !== 'admin') {
        return NextResponse.json({
          success: false,
          error: 'Insufficient permissions'
        }, { status: 403 });
      }
    }

    // Add credits using the database function
    const { data, error } = await supabase.rpc('add_user_credits', {
      p_user_id: body.user_id,
      p_amount: body.amount,
      p_transaction_type: body.transaction_type,
      p_description: body.description,
      p_reference_type: body.reference_type,
      p_reference_id: body.reference_id,
      p_metadata: body.metadata || {},
      p_created_by: user.id
    });

    if (error) {
      console.error('Error adding credits:', error);
      return NextResponse.json({
        success: false,
        error: 'Failed to add credits'
      }, { status: 500 });
    }

    return NextResponse.json({
      success: true,
      data: { new_balance: data },
      message: 'Credits added successfully'
    });

  } catch (error) {
    console.error('Unexpected error in add credits API:', error);
    return NextResponse.json({
      success: false,
      error: 'Internal server error'
    }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { getServerSupabase } from '@/lib/supabaseServer';
import { CreditsResponse, SpendCreditsRequest } from '@/types/credits';

export async function POST(request: NextRequest): Promise<NextResponse<CreditsResponse>> {
  try {
    const supabase = new Proxy({} as any, { get: (target, prop) => (getServerSupabase() as any)[prop] }) as ReturnType<typeof getServerSupabase>;

    // Get user from auth
    const { data: { user }, error: authError } = await supabase.auth.getUser();
    if (authError || !user) {
      return NextResponse.json({
        success: false,
        error: 'Unauthorized'
      }, { status: 401 });
    }

    // Parse request body
    const body: SpendCreditsRequest = await request.json();

    // Validate required fields
    if (!body.amount || !body.description) {
      return NextResponse.json({
        success: false,
        error: 'Missing required fields'
      }, { status: 400 });
    }

    // Validate amount
    if (body.amount <= 0) {
      return NextResponse.json({
        success: false,
        error: 'Amount must be positive'
      }, { status: 400 });
    }

    // Check user has enough credits
    const { data: userCredits, error: creditsError } = await supabase
      .from('user_credits_summary')
      .select('credits_balance')
      .eq('id', user.id)
      .single();

    if (creditsError || !userCredits) {
      return NextResponse.json({
        success: false,
        error: 'Unable to fetch user credits'
      }, { status: 500 });
    }

    if (userCredits.credits_balance < body.amount) {
      return NextResponse.json({
        success: false,
        error: 'Insufficient credits'
      }, { status: 400 });
    }

    // Spend credits using the database function
    const { data, error } = await supabase.rpc('spend_user_credits', {
      p_user_id: user.id,
      p_amount: body.amount,
      p_description: body.description,
      p_reference_type: body.reference_type,
      p_reference_id: body.reference_id,
      p_metadata: body.metadata || {}
    });

    if (error) {
      console.error('Error spending credits:', error);
      return NextResponse.json({
        success: false,
        error: 'Failed to spend credits'
      }, { status: 500 });
    }

    if (!data) {
      return NextResponse.json({
        success: false,
        error: 'Failed to process credit redemption'
      }, { status: 500 });
    }

    return NextResponse.json({
      success: true,
      data: { credits_spent: body.amount },
      message: 'Credits spent successfully'
    });

  } catch (error) {
    console.error('Unexpected error in spend credits API:', error);
    return NextResponse.json({
      success: false,
      error: 'Internal server error'
    }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { getServerSupabase } from '@/lib/supabaseServer';
import { CreditsResponse } from '@/types/credits';

export async function GET(request: NextRequest): Promise<NextResponse<CreditsResponse>> {
  try {
    const supabase = new Proxy({} as any, { get: (target, prop) => (getServerSupabase() as any)[prop] }) as ReturnType<typeof getServerSupabase>;

    // Get user from auth
    const { data: { user }, error: authError } = await supabase.auth.getUser();
    if (authError || !user) {
      return NextResponse.json({
        success: false,
        error: 'Unauthorized'
      }, { status: 401 });
    }

    // Get query parameters
    const { searchParams } = new URL(request.url);
    const page = parseInt(searchParams.get('page') || '1');
    const limit = parseInt(searchParams.get('limit') || '20');
    const offset = (page - 1) * limit;

    // Get user transactions
    const { data, error, count } = await supabase
      .from('credits_transactions')
      .select('*', { count: 'exact' })
      .eq('user_id', user.id)
      .order('created_at', { ascending: false })
      .range(offset, offset + limit - 1);

    if (error) {
      console.error('Error fetching transactions:', error);
      return NextResponse.json({
        success: false,
        error: 'Failed to fetch transactions'
      }, { status: 500 });
    }

    const total = count || 0;
    const totalPages = Math.ceil(total / limit);

    return NextResponse.json({
      success: true,
      data: {
        transactions: data || [],
        pagination: {
          page,
          limit,
          total,
          total_pages: totalPages
        }
      }
    });

  } catch (error) {
    console.error('Unexpected error in transactions API:', error);
    return NextResponse.json({
      success: false,
      error: 'Internal server error'
    }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { getServerSupabase } from '@/lib/supabaseServer'

const supabase = new Proxy({} as any, { get: (target, prop) => (getServerSupabase() as any)[prop] }) as ReturnType<typeof getServerSupabase>

export async function POST(request: NextRequest) {
  try {
    const { credits, bookingId, serviceType } = await request.json();

    if (!credits || !bookingId || !serviceType) {
      return NextResponse.json(
        { error: 'Missing required fields: credits, bookingId, serviceType' },
        { status: 400 }
      );
    }

    // Validate credits amount
    if (credits <= 0) {
      return NextResponse.json(
        { error: 'Credits amount must be greater than 0' },
        { status: 400 }
      );
    }

    // Get user from session
    const { data: { user } } = await supabase.auth.getUser();

    if (!user) {
      return NextResponse.json(
        { error: 'Unauthorized' },
        { status: 401 }
      );
    }

    // Check if user has enough credits
    const { data: creditBalance } = await supabase
      .from('user_credits')
      .select('balance')
      .eq('user_id', user.id)
      .single();

    if (!creditBalance || creditBalance.balance < credits) {
      return NextResponse.json(
        { error: 'Insufficient credits' },
        { status: 400 }
      );
    }

    // Deduct credits
    const { error: updateError } = await supabase
      .from('user_credits')
      .update({ 
        balance: creditBalance.balance - credits,
        updated_at: new Date().toISOString()
      })
      .eq('user_id', user.id);

    if (updateError) {
      console.error('Error updating credits:', updateError);
      return NextResponse.json(
        { error: 'Failed to update credits' },
        { status: 500 }
      );
    }

    // Record credit usage
    const { error: usageError } = await supabase
      .from('credit_usage')
      .insert({
        user_id: user.id,
        credits_used: credits,
        booking_id: bookingId,
        service_type: serviceType,
        used_at: new Date().toISOString()
      });

    if (usageError) {
      console.error('Error recording credit usage:', usageError);
      // Note: We don't rollback the credit deduction here as the usage was successful
    }

    return NextResponse.json({
      success: true,
      remainingCredits: creditBalance.balance - credits,
      creditsUsed: credits
    });

  } catch (error) {
    console.error('Error using credits:', error);
    return NextResponse.json(
      { error: 'Failed to use credits' },
      { status: 500 }
    );
  }
}

