import { createClient } from '@supabase/supabase-js'
import { NextResponse } from 'next/server'
import { supabaseAdmin as supabase } from '@/lib/supabaseProxies';

export async function POST() {
  try {
    console.log('üö® Emergency fix: Removing profiles table recursion...')

    // 1. Drop all problematic recursive policies
    const dropPolicies = `
      DROP POLICY IF EXISTS "profiles_public_read" ON profiles;
      DROP POLICY IF EXISTS "profiles_own_update" ON profiles;
      DROP POLICY IF EXISTS "Public profiles are viewable by everyone" ON profiles;
      DROP POLICY IF EXISTS "Users can insert their own profile" ON profiles;
      DROP POLICY IF EXISTS "Users can update own profile" ON profiles;
    `

    // 2. Create simple, non-recursive policies
    const createSimplePolicies = `
      CREATE POLICY "profiles_simple_select" ON profiles
        FOR SELECT USING (
          id = auth.uid() OR true
        );

      CREATE POLICY "profiles_simple_insert" ON profiles
        FOR INSERT WITH CHECK (
          id = auth.uid()
        );

      CREATE POLICY "profiles_simple_update" ON profiles
        FOR UPDATE USING (
          id = auth.uid()
        );
    `

    // 3. Fix other tables with similar issues
    const fixOtherTables = `
      DROP POLICY IF EXISTS "security_logs_admin_read" ON security_logs;
      CREATE POLICY "security_logs_admin_read" ON security_logs
        FOR SELECT USING (auth.uid() IS NOT NULL);

      DROP POLICY IF EXISTS "rate_limits_user_read" ON rate_limits;
      CREATE POLICY "rate_limits_user_read" ON rate_limits
        FOR SELECT USING (
          identifier = auth.uid()::text OR auth.uid() IS NOT NULL
        );

      DROP POLICY IF EXISTS "admin_permissions_read" ON admin_permissions;
      CREATE POLICY "admin_permissions_read" ON admin_permissions
        FOR SELECT USING (
          user_id = auth.uid() OR auth.uid() IS NOT NULL
        );
    `

    // Execute the fixes
    const { error: dropError } = await supabase.rpc('exec_sql', { sql: dropPolicies })
    if (dropError) console.log('Drop policies result:', dropError)

    const { error: createError } = await supabase.rpc('exec_sql', { sql: createSimplePolicies })
    if (createError) console.log('Create policies result:', createError)

    const { error: fixError } = await supabase.rpc('exec_sql', { sql: fixOtherTables })
    if (fixError) console.log('Fix other tables result:', fixError)

    // Test if the fix worked
    const { data: testData, error: testError } = await supabase
      .from('profiles')
      .select('id')
      .limit(1)

    if (testError) {
      console.error('‚ùå Fix failed:', testError)
      return NextResponse.json({ 
        success: false, 
        error: 'Fix failed - manual intervention required',
        details: testError
      }, { status: 500 })
    }

    console.log('‚úÖ Profiles table recursion fixed successfully!')
    return NextResponse.json({ 
      success: true, 
      message: 'Profiles table recursion fixed',
      testResult: testData
    })

  } catch (error) {
    console.error('‚ùå Emergency fix failed:', error)
    return NextResponse.json({ 
      success: false, 
      error: 'Emergency fix failed',
      details: error instanceof Error ? error.message : 'Unknown error'
    }, { status: 500 })
  }
}

import { NextRequest } from 'next/server';
import { Kb } from '@/lib/kb/provider';
import { buildCorsHeaders, preflightResponse } from '@/lib/kb/cors';
import { requireApiKey, withCommonHeaders } from '@/lib/kb/request';
import { rateLimit } from '@/lib/kb/limiter';

export async function OPTIONS(req: NextRequest) {
  const origin = req.headers.get('origin');
  return preflightResponse(origin, 'POST, OPTIONS');
}

export async function POST(req: NextRequest) {
  const origin = req.headers.get('origin');
  const cors = buildCorsHeaders(origin, 'POST, OPTIONS');

  if (!(await requireApiKey(process.env.KB_API_KEY))) {
    return withCommonHeaders(new Response(JSON.stringify({ error: 'Unauthorized', code: 'UNAUTH' }), { status: 401, headers: cors }));
  }

  const ip = req.headers.get('x-forwarded-for') ?? 'anon';
  const limit = Number(process.env.KB_RATE_LIMIT_PER_MIN ?? 30);
  if (!rateLimit(`answer:${ip}`, limit)) {
    const res = new Response(JSON.stringify({ error: 'Rate limited', code: 'RATE_LIMIT' }), { status: 429, headers: { ...cors, 'Retry-After': '60' } });
    return withCommonHeaders(res);
  }

  const body = await req.json().catch(() => null);
  if (!body || !body.query) {
    return withCommonHeaders(new Response(JSON.stringify({ error: 'Missing query', code: 'BAD_REQUEST' }), { status: 400, headers: cors }));
  }
  const { query, locale = 'en', section } = body;

  try {
    // Convert legacy params to new interface
    const answerResult = await Kb.answer(query, locale as any, section);
    const data = {
      answer: answerResult.text,
      citations: answerResult.sources.map(s => ({
        id: s.id,
        title: s.title,
        url: s.url || '',
        excerpt: s.snippet
      })),
      followups: ['Check FAQ', 'Contact support']
    };
    const res = new Response(JSON.stringify(data), { status: 200, headers: { ...cors, 'Cache-Control': 'no-store' } });
    return withCommonHeaders(res);
  } catch (e: any) {
    if (e?.message === 'NO_CONTENT') {
      return withCommonHeaders(new Response(JSON.stringify({ error: 'No relevant content', code: 'NO_CONTENT' }), { status: 422, headers: cors }));
    }
    return withCommonHeaders(new Response(JSON.stringify({ error: 'Server error', code: 'SERVER' }), { status: 500, headers: cors }));
  }
}

import { NextRequest } from 'next/server';
import { Kb } from '@/lib/kb/provider';
import { buildCorsHeaders, preflightResponse } from '@/lib/kb/cors';
import { requireApiKey, withCommonHeaders } from '@/lib/kb/request';
import { rateLimit } from '@/lib/kb/limiter';

export async function OPTIONS(req: NextRequest) {
  const origin = req.headers.get('origin');
  return preflightResponse(origin, 'GET, OPTIONS');
}

export async function GET(
  req: NextRequest,
  context: { params: Promise<Record<string, string>> }
) {
  const { id } = await context.params
  const origin = req.headers.get('origin');
  const cors = buildCorsHeaders(origin, 'GET, OPTIONS');

  if (!(await requireApiKey(process.env.KB_API_KEY))) {
    return withCommonHeaders(new Response(JSON.stringify({ error: 'Unauthorized', code: 'UNAUTH' }), { status: 401, headers: cors }));
  }

  const ip = req.headers.get('x-forwarded-for') ?? 'anon';
  const limit = Number(process.env.KB_RATE_LIMIT_PER_MIN ?? 30);
  if (!rateLimit(`article:${ip}`, limit)) {
    const res = new Response(JSON.stringify({ error: 'Rate limited', code: 'RATE_LIMIT' }), { status: 429, headers: { ...cors, 'Retry-After': '60' } });
    return withCommonHeaders(res);
  }

  try {
    const article = await Kb.getArticle(id, 'en'); // Default to English for now
    if (!article) {
      return withCommonHeaders(new Response(JSON.stringify({ error: 'Not found', code: 'NOT_FOUND' }), { status: 404, headers: cors }));
    }
    // Convert to legacy format for backward compatibility
    const legacyArticle = {
      id: article.id,
      title: article.title,
      url: article.url || '',
      content: article.content
    };
    const res = new Response(JSON.stringify(legacyArticle), { status: 200, headers: { ...cors, 'Cache-Control': 'public, max-age=300' } });
    return withCommonHeaders(res);
  } catch (e: any) {
    if (e?.message === 'NOT_FOUND') {
      return withCommonHeaders(new Response(JSON.stringify({ error: 'Not found', code: 'NOT_FOUND' }), { status: 404, headers: cors }));
    }
    return withCommonHeaders(new Response(JSON.stringify({ error: 'Server error', code: 'SERVER' }), { status: 500, headers: cors }));
  }
}

