  MetricSnapshot,
  HealthStatus,
  SLOStatus,
  Incident,
  AnomalyReport,
  Deployment,
  DeployReadiness,
  LogsQueryResult,
  LogEntry
} from '@/app/admin/ops-ai/lib/ops-types'

function mapSeverityToLevel(severity: InvariantViolation['severity']) {
  return severity === 'critical' || severity === 'high' ? 'ERROR' : 'WARN'
}

function getLatencyMs(metrics: SimMetrics) {
  const raw =
    metrics.p95ResponseTime ??
    metrics.avgBookingLatency ??
    metrics.reindexP95 ??
    metrics.rolloutP99

  if (raw != null) {
    return Math.max(0, raw)
  }

  // Stable synthetic latency between 200-500ms
  return 350
}

export async function fetchSimcitySnapshot(origin?: string) {
  const baseUrl = process.env.NEXT_PUBLIC_SITE_URL || origin

  if (!baseUrl) {
    throw new Error('NEXT_PUBLIC_SITE_URL is required for SimCity mode')
  }

  const [statusRes, summaryRes] = await Promise.all([
    fetch(`${baseUrl}/api/simcity/status`, { cache: 'no-store' }),
    fetch(`${baseUrl}/api/simcity/summary`, { cache: 'no-store' })
  ])

  // Check if SimCity is not running
  if (!statusRes.ok || !summaryRes.ok) {
    const statusText = await statusRes.text().catch(() => '')
    const summaryText = await summaryRes.text().catch(() => '')
    if (statusText.includes('not running') || summaryText.includes('not running')) {
      throw new Error('SimCity simulation is not running')
    }
    throw new Error(`SimCity endpoints returned errors: ${statusRes.status}, ${summaryRes.status}`)
  }

  const statusJson = await statusRes.json()
  const summaryJson = await summaryRes.json()

  // Check if simulation is not running from response
  if (!statusJson.success || !statusJson.data?.state?.running) {
    throw new Error('SimCity simulation is not running')
  }

  const status = statusJson.data ?? statusJson
  const summary = summaryJson.data ?? summaryJson

  const state: SimState =
    (status?.state as SimState) ??
    (summary?.state as SimState) ??
    (summary as SimState)

  const runInfo: SimRunInfo | null =
    (status?.runInfo as SimRunInfo | null) ??
    (summary?.runInfo as SimRunInfo | null) ??
    (state?.runInfo as SimRunInfo | null) ??
    null

  const metrics: SimMetrics = {
    ...DEFAULT_METRICS,
    ...(status?.metrics as SimMetrics | undefined),
    ...(summary?.metrics as SimMetrics | undefined),
    ...(state?.metrics as SimMetrics | undefined)
  }

  const violations: InvariantViolation[] = summary?.violations ?? []

  return { state, runInfo, metrics, violations, summary, status }
}

export function simcityToOpsSummary(
  state: SimState,
  runInfo: SimRunInfo | null,
  violations: InvariantViolation[]
): OpsSummary {
  const metrics = state?.metrics

  const summary: OpsSummary & {
    runInfo?: SimRunInfo | null
    scenario?: string | null
  } = {
    timestamp: new Date().toISOString(),
    health: simcityToHealth(metrics, violations),
    sloSummary: simcityToSLOs(metrics, violations),
    incidents: simcityToIncidents(violations),
    pendingActions: [],
    runInfo,
    scenario: runInfo?.scenario ?? state?.scenario ?? null
  }

  return summary
}

export function simcityToSystemMetrics(metrics: SimMetrics): MetricSnapshot {
  const value =
    metrics.throughput ??
    metrics.activeAgents ??
    metrics.totalAgentsSpawned ??
    0

  return {
    timestamp: new Date().toISOString(),
    value,
    unit: 'agents'
  }
}

export function simcityToBookingMetrics(metrics: SimMetrics): MetricSnapshot {
  return {
    timestamp: new Date().toISOString(),
    value: metrics.completedBookings ?? 0,
    unit: 'bookings'
  }
}

export function simcityToErrorMetrics(metrics: SimMetrics): MetricSnapshot {
  return {
    timestamp: new Date().toISOString(),
    value: metrics.errors ?? metrics.errorCount ?? 0,
    unit: 'errors'
  }
}

export function simcityToLatencyMetrics(metrics: SimMetrics): MetricSnapshot {
  const latency = getLatencyMs(metrics)

  return {
    timestamp: new Date().toISOString(),
    value: latency,
    unit: 'ms'
  }
}

export function simcityToHealth(
  _metrics: SimMetrics,
  violations: InvariantViolation[]
): HealthStatus {
  const hasCritical = violations.some((v) => v.severity === 'critical')
  const hasHigh = violations.some((v) => v.severity === 'high')

  const overall = hasCritical ? 'down' : hasHigh ? 'degraded' : 'ok'

  return {
    overall,
    services: [
      { name: 'simcity-core', status: overall },
      { name: 'booking-sim', status: overall },
      { name: 'vendor-sim', status: overall }
    ]
  }
}

export function simcityToSLOs(
  metrics: SimMetrics,
  _violations: InvariantViolation[]
): SLOStatus[] {
  const completed = metrics.completedBookings ?? 0
  const cancelled = metrics.cancelledBookings ?? 0
  const errors = metrics.errors ?? metrics.errorCount ?? 0
  const total = completed + cancelled || 0

  const successRate = total > 0 ? (completed / total) * 100 : 0
  const errorRate = total > 0 ? (errors / total) * 100 : 0

  const bookingStatus: SLOStatus = {
    name: 'Booking success rate',
    status: successRate > 98 ? 'ok' : successRate > 90 ? 'warning' : 'breach',
    currentValue: Math.round(successRate * 100) / 100,
    target: '99%'
  }

  const errorStatus: SLOStatus = {
    name: 'Error rate',
    status: errorRate <= 0.5 ? 'ok' : errorRate <= 2 ? 'warning' : 'breach',
    currentValue: Math.round(errorRate * 100) / 100,
    target: '0.5%'
  }

  return [bookingStatus, errorStatus]
}

export function simcityToIncidents(
  violations: InvariantViolation[]
): Incident[] {
  return violations.map((violation, idx) => ({
    id: `${violation.code}-${violation.timestamp}-${idx}`,
    title: violation.message || violation.code,
    severity: violation.severity,
    status: 'open',
    summary: violation.details
      ? `${violation.message} (${JSON.stringify(violation.details)})`
      : violation.message
  }))
}

export function simcityToAnomaly(
  violations: InvariantViolation[]
): AnomalyReport {
  const hasCritical = violations.some((v) => v.severity === 'critical')
  const hasHigh = violations.some((v) => v.severity === 'high')
  const hasMedium = violations.some((v) => v.severity === 'medium')

  const status = hasCritical
    ? 'severe'
    : hasHigh
    ? 'moderate'
    : hasMedium
    ? 'minor'
    : 'none'

  return {
    status,
    findings: violations.map((v) => ({
      source: v.code,
      severity: v.severity,
      message: v.message
    }))
  }
}

export function simcityToDeployments(
  runInfo: SimRunInfo | null,
  violations: InvariantViolation[] = []
): Deployment[] {
  const criticalViolation = violations.some((v) => v.severity === 'critical')

  return [
    {
      id: runInfo?.runId ?? 'simcity-run',
      env: 'simcity',
      status: criticalViolation ? 'failed' : 'success'
    }
  ]
}

export function simcityToDeployReadiness(
  violations: InvariantViolation[]
): DeployReadiness {
  const hasCritical = violations.some((v) => v.severity === 'critical')
  const hasHigh = violations.some((v) => v.severity === 'high')

  if (hasCritical) {
    return { status: 'blocked', reasons: ['Critical invariant violations present'] }
  }

  if (hasHigh) {
    return { status: 'risky', reasons: ['High-severity invariant violations detected'] }
  }

  return { status: 'safe', reasons: [] }
}

export function simcityToLogs(
  violations: InvariantViolation[]
): LogsQueryResult {
  const entries: LogEntry[] = violations.map((v) => ({
    timestamp: v.timestamp,
    level: mapSeverityToLevel(v.severity),
    message: `[${v.code}] ${v.message}`
  }))

  return { entries }
}

export function simEventToOpsLog(
  e: SimEventPayload
): LogEntry | null {
  if (
    e.type !== 'error' &&
    e.type !== 'metric_spike' &&
    e.type !== 'invariant_violation'
  ) {
    return null
  }

  const level = e.type === 'error' ? 'ERROR' : 'WARN'
  const message =
    typeof e.data?.message === 'string'
      ? e.data.message
      : e.type === 'metric_spike'
      ? 'Metric spike detected'
      : e.type === 'invariant_violation'
      ? 'Invariant violation detected'
      : 'SimCity event'

  return {
    timestamp: e.timestamp,
    level,
    message
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { getOpsMode } from '../_config'
import {
  fetchSimcitySnapshot,
  simcityToAnomaly
} from '../_simcity/ops-from-simcity'
import { AnomalyAI, AnomalySignal } from '@/lib/observability/anomalyai'

/**
 * GET /api/ops/anomaly
 * 
 * AnomalyAI - Cross-domain anomaly detection specialist
 * 
 * Monitors all read-only endpoints (health, metrics, SLO, incidents, events, logs)
 * and generates unified anomaly reports with severity prioritization
 * and agent recommendations.
 * 
 * Query Parameters:
 * - baseUrl (optional): Base URL for endpoints
 * - lookbackMinutes (optional): Time window for analysis (default: 15)
 */
export async function GET(request: NextRequest) {
  if (getOpsMode() === 'simcity') {
    try {
      const { violations } = await fetchSimcitySnapshot(request.nextUrl.origin)
      return NextResponse.json(simcityToAnomaly(violations))
    } catch (error) {
      return NextResponse.json(
        {
          error: 'Failed to load SimCity anomaly report',
          message: error instanceof Error ? error.message : 'Unknown error'
        },
        { status: 503 }
      )
    }
  }

  try {
    const { searchParams } = new URL(request.url)

    // Base URL resolution: prefer request.nextUrl.origin -> env -> explicit param
    // In serverless, NEXT_PUBLIC_SITE_URL is required
    let baseUrl = searchParams.get('baseUrl')

    if (!baseUrl) {
      // Prefer request.nextUrl.origin when available (works in most environments)
      baseUrl = request.nextUrl.origin || process.env.NEXT_PUBLIC_SITE_URL || null
    }

    // Validate baseUrl - must be a valid URL
    if (!baseUrl) {
      return NextResponse.json(
        {
          agent: 'AnomalyAI',
          error: 'Base URL cannot be determined',
          message: 'NEXT_PUBLIC_SITE_URL environment variable is required in serverless environments',
          timestamp: new Date().toISOString()
        },
        { status: 503 }
      )
    }

    // Validate URL format
    try {
      new URL(baseUrl)
    } catch {
      return NextResponse.json(
        {
          agent: 'AnomalyAI',
          error: 'Invalid base URL format',
          message: `Base URL "${baseUrl}" is not a valid URL`,
          timestamp: new Date().toISOString()
        },
        { status: 400 }
      )
    }

    const allSignals: AnomalySignal[] = []

    // Define all endpoints to probe
    const endpoints = [
      { 
        name: 'health',
        url: `${baseUrl}/api/ops/health`, 
        handler: handleHealth,
        source: 'health'
      },
      { 
        name: 'metrics.system',
        url: `${baseUrl}/api/ops/metrics/system?timeRange=1h`, 
        handler: handleMetricsSystem,
        source: 'metrics.system'
      },
      { 
        name: 'metrics.errors',
        url: `${baseUrl}/api/ops/metrics/errors?timeRange=1h`, 
        handler: handleMetricsErrors,
        source: 'metrics.errors'
      },
      { 
        name: 'metrics.p95',
        url: `${baseUrl}/api/ops/metrics/p95?timeRange=1h`, 
        handler: handleMetricsP95,
        source: 'metrics.p95'
      },
      { 
        name: 'slo',
        url: `${baseUrl}/api/ops/slo/status`, 
        handler: handleSLO,
        source: 'slo'
      },
      { 
        name: 'incidents',
        url: `${baseUrl}/api/ops/incidents/ai-triage`, 
        handler: handleIncidents,
        source: 'incidents'
      },
      { 
        name: 'logs.errors',
        url: `${baseUrl}/api/ops/logs/errors?lookbackHours=1`, 
        handler: handleLogsErrors,
        source: 'logs.errors'
      },
      { 
        name: 'logs.system',
        url: `${baseUrl}/api/ops/logs/system?lookbackHours=1`, 
        handler: handleLogsSystem,
        source: 'logs.system'
      },
      { 
        name: 'logs.booking',
        url: `${baseUrl}/api/ops/logs/booking?lookbackHours=1`, 
        handler: handleLogsBooking,
        source: 'logs.booking'
      },
      { 
        name: 'events',
        url: `${baseUrl}/api/ops/events?limit=100`, 
        handler: handleEvents,
        source: 'events'
      },
    ]

    // Probe all endpoints in parallel with individual error handling
    const results = await Promise.allSettled(
      endpoints.map(async ({ url, handler, name, source }) => {
        try {
          const response = await fetch(url, {
            method: 'GET',
            headers: {
              'Accept': 'application/json',
              'User-Agent': 'AnomalyAI/1.0'
            },
            signal: AbortSignal.timeout(10000) // 10 second timeout per endpoint
          })

          if (!response.ok) {
            console.warn(`AnomalyAI: ${name} returned ${response.status}`)
            // Return missing signal instead of null
            return {
              signals: [],
              missing: {
                source,
                status: 'missing',
                reason: `endpoint returned ${response.status}`,
                endpoint: name
              }
            }
          }

          const data = await response.json()
          const signals = handler(data, url)
          return { signals, missing: null }
        } catch (error) {
          const errorMessage = error instanceof Error ? error.message : 'Unknown error'
          console.warn(`AnomalyAI: Failed to fetch ${name}:`, errorMessage)

          // Return missing signal instead of null
          return {
            signals: [],
            missing: {
              source,
              status: 'missing',
              reason: errorMessage.includes('timeout') || errorMessage.includes('AbortError')
                ? 'endpoint timeout'
                : errorMessage.includes('ECONNREFUSED') || errorMessage.includes('fetch failed')
                ? 'endpoint unreachable'
                : 'endpoint error',
              endpoint: name
            }
          }
        }
      })
    )

    // Collect all signals and track missing endpoints
    const missingEndpoints: Array<{ source: string; status: string; reason: string; endpoint: string }> = []

    for (const result of results) {
      if (result.status === 'fulfilled') {
        const { signals, missing } = result.value
        if (signals && signals.length > 0) {
          allSignals.push(...signals)
        }
        if (missing) {
          missingEndpoints.push(missing)
          // Add a signal indicating missing data
          allSignals.push({
            source: missing.source as AnomalySignal['source'],
            endpoint: missing.endpoint,
            type: 'endpoint_unavailable',
            severity: 'low' as const,
            description: `Endpoint ${missing.endpoint} unavailable: ${missing.reason}`,
            timestamp: new Date().toISOString(),
            metadata: { endpoint: missing.endpoint, reason: missing.reason }
          })
        }
      } else {
        // Promise rejected (shouldn't happen with Promise.allSettled, but handle it)
        console.error('AnomalyAI: Unexpected promise rejection:', result.reason)
      }
    }

    // Unify anomalies (works even with partial data)
    const unifiedAnomalies = AnomalyAI.unifyAnomalies(allSignals)

    // Generate report
    const report = AnomalyAI.generateReport(unifiedAnomalies)

    return NextResponse.json({
      agent: 'AnomalyAI',
      timestamp: new Date().toISOString(),
      report,
      rawSignalsCount: allSignals.length,
      unifiedAnomaliesCount: unifiedAnomalies.length,
      endpointsProbed: endpoints.length,
      endpointsAvailable: endpoints.length - missingEndpoints.length,
      missingEndpoints: missingEndpoints.length > 0 ? missingEndpoints : undefined
    })

  } catch (error) {
    console.error('AnomalyAI error:', error)
    return NextResponse.json(
      {
        agent: 'AnomalyAI',
        error: 'Failed to generate anomaly report',
        message: error instanceof Error ? error.message : 'Unknown error',
        timestamp: new Date().toISOString()
      },
      { status: 500 }
    )
  }
}

/**
 * Handle health endpoint data
 */
function handleHealth(data: any): AnomalySignal[] {
  return AnomalyAI.detectHealthAnomalies(data)
}

/**
 * Handle system metrics endpoint data
 */
function handleMetricsSystem(data: any, endpoint: string): AnomalySignal[] {
  return AnomalyAI.detectMetricsAnomalies(data, endpoint)
