import { NextRequest, NextResponse } from 'next/server'
import { createClient } from '@supabase/supabase-js'
import { loadBaselines, saveBaseline, getLatestBaseline, deleteBaseline } from '../../../../../scripts/ops-baseline-store'
import type { BaselineData } from '@/lib/regression/regressionAI'

import { supabaseAdmin as supabase } from '@/lib/supabaseProxies';

/**
 * GET /ops/baseline
 * 
 * List all baselines or get a specific baseline
 */
export async function GET(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url)
    const metricType = searchParams.get('metricType')
    const endpoint = searchParams.get('endpoint')
    const period = searchParams.get('period') as 'normal' | 'peak' | 'low' | null

    if (metricType && period) {
      // Get specific baseline
      const baseline = getLatestBaseline(metricType, endpoint || undefined, period)
      if (!baseline) {
        return NextResponse.json({
          success: false,
          error: 'Baseline not found'
        }, { status: 404 })
      }
      return NextResponse.json({
        success: true,
        baseline
      })
    }

    // List all baselines
    const baselines = loadBaselines(metricType || undefined, endpoint || undefined)

    return NextResponse.json({
      success: true,
      baselines,
      count: baselines.length
    })

  } catch (error) {
    console.error('Baseline GET endpoint error:', error)
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    )
  }
}

/**
 * POST /ops/baseline
 * 
 * Create a new baseline from current metrics
 */
export async function POST(request: NextRequest) {
  try {
    const body = await request.json()
    const { 
      metricType, 
      endpoint, 
      period = 'normal',
      timeRange = '24h',
      deployId,
      commitSha,
      notes
    } = body

    if (!metricType) {
      return NextResponse.json({
        success: false,
        error: 'metricType is required'
      }, { status: 400 })
    }

    if (!['p95', 'booking', 'error', 'system'].includes(metricType)) {
      return NextResponse.json({
        success: false,
        error: 'metricType must be one of: p95, booking, error, system'
      }, { status: 400 })
    }

    // Calculate time range
    const now = new Date()
    let startTime: Date

    switch (timeRange) {
      case '1h':
        startTime = new Date(now.getTime() - 60 * 60 * 1000)
        break
      case '6h':
        startTime = new Date(now.getTime() - 6 * 60 * 60 * 1000)
        break
      case '24h':
        startTime = new Date(now.getTime() - 24 * 60 * 60 * 1000)
        break
      case '7d':
        startTime = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000)
        break
      default:
        startTime = new Date(now.getTime() - 24 * 60 * 60 * 1000)
    }

    // Fetch metrics and calculate baseline data
    const baselineData: BaselineData = {}

    if (metricType === 'p95') {
      let query = supabase
        .from('api_metrics_5m')
        .select('endpoint, method, p95_ms, p99_ms, reqs')
        .gte('bucket', startTime.toISOString())
        .order('bucket', { ascending: true })

      if (endpoint) {
        query = query.eq('endpoint', endpoint)
      }

      const { data: metrics } = await query

      if (metrics && metrics.length > 0) {
        const p95Values = metrics.map((m: any) => m.p95_ms).filter((v: number) => v != null && v > 0)
        const p99Values = metrics.map((m: any) => m.p99_ms).filter((v: number) => v != null && v > 0)

        if (p95Values.length > 0) {
          baselineData.p95_latency_ms = p95Values.reduce((sum: number, v: number) => sum + v, 0) / p95Values.length
        }
        if (p99Values.length > 0) {
          baselineData.p99_latency_ms = p99Values.reduce((sum: number, v: number) => sum + v, 0) / p99Values.length
        }
      }
    } else if (metricType === 'booking') {
      const { data: bookings } = await supabase
        .from('bookings')
        .select('id, status, created_at, confirmed_at, total_amount_cents, price_cents')
        .gte('created_at', startTime.toISOString())

      if (bookings && bookings.length > 0) {
        const hours = (now.getTime() - startTime.getTime()) / (1000 * 60 * 60)
        baselineData.bookings_per_hour = bookings.length / hours

        const confirmed = bookings.filter(b => b.status === 'confirmed' || b.confirmed_at).length
        baselineData.conversion_rate = bookings.length > 0 ? (confirmed / bookings.length) * 100 : 0
      }
    } else if (metricType === 'error') {
      const { data: errors } = await supabase
        .from('performance_metrics')
        .select('status_code, created_at')
        .gte('created_at', startTime.toISOString())
        .gte('status_code', 400)

      const { count: totalRequests } = await supabase
        .from('performance_metrics')
        .select('*', { count: 'exact', head: true })
        .gte('created_at', startTime.toISOString())

      if (errors && totalRequests) {
        const hours = (now.getTime() - startTime.getTime()) / (1000 * 60 * 60)
        baselineData.error_count_per_hour = errors.length / hours
        baselineData.error_rate_percent = totalRequests > 0 ? (errors.length / totalRequests) * 100 : 0
      }
    } else if (metricType === 'system') {
      const { data: performanceData } = await supabase
        .from('performance_analytics_5min')
        .select('cache_hit_rate_percent, avg_database_queries, request_count')
        .gte('five_minute_bucket', startTime.toISOString())

      if (performanceData && performanceData.length > 0) {
        const cpuValues = performanceData.map((item: any) => {
          return Math.min(100,
            (item.request_count || 0) * 0.1 +
            (item.avg_database_queries || 0) * 2
          )
        })
        const memoryValues = performanceData.map((item: any) => {
          return Math.min(100,
            30 + (item.request_count || 0) * 0.05
          )
        })
        const cacheValues = performanceData
          .map((item: any) => item.cache_hit_rate_percent)
          .filter((v: number) => v != null && !isNaN(v))

        if (cpuValues.length > 0) {
          baselineData.cpu_percent = cpuValues.reduce((sum: number, v: number) => sum + v, 0) / cpuValues.length
        }
        if (memoryValues.length > 0) {
          baselineData.memory_percent = memoryValues.reduce((sum: number, v: number) => sum + v, 0) / memoryValues.length
        }
        if (cacheValues.length > 0) {
          baselineData.cache_hit_rate = cacheValues.reduce((sum: number, v: number) => sum + v, 0) / cacheValues.length
        }
      }
    }

    // Create baseline
    const baseline = saveBaseline({
      metric_type: metricType as 'p95' | 'booking' | 'error' | 'system',
      endpoint: endpoint || undefined,
      period: period as 'normal' | 'peak' | 'low',
      data: baselineData,
      metadata: {
        deploy_id: deployId,
        commit_sha: commitSha,
        notes
      }
    })

    return NextResponse.json({
      success: true,
      baseline,
      message: `Baseline created for ${metricType}${endpoint ? ` (${endpoint})` : ''}`
    })

  } catch (error) {
    console.error('Baseline POST endpoint error:', error)
    return NextResponse.json(
      { error: 'Internal server error', details: error instanceof Error ? error.message : 'Unknown error' },
      { status: 500 }
    )
  }
}

/**
 * DELETE /ops/baseline
 * 
 * Delete a baseline by ID
 */
export async function DELETE(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url)
    const id = searchParams.get('id')

    if (!id) {
      return NextResponse.json({
        success: false,
        error: 'id parameter is required'
      }, { status: 400 })
    }

    const deleted = deleteBaseline(id)

    if (!deleted) {
      return NextResponse.json({
        success: false,
        error: 'Baseline not found'
      }, { status: 404 })
    }

    return NextResponse.json({
      success: true,
      message: 'Baseline deleted'
    })

  } catch (error) {
    console.error('Baseline DELETE endpoint error:', error)
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    )
  }
}

import { runSyntheticCheck } from '../../../../../../packages/opsai-l7/src/synthetic'
import {
  flushCache,
  restartComponent
} from '../../../../../../packages/opsai-l7/src/actions'
import { recommendActions } from '../../../../../../packages/opsai-helpdesk/src/engine'
import { createEvent } from '@/scripts/ops-events-store'
import type { CommandRequest, CommandResponse } from './types'

type CommandHandler = (args: Record<string, any>) => Promise<CommandResponse>

function normalize(command: string) {
  return command.trim().toLowerCase()
}

const handlers: Record<string, CommandHandler> = {
  'restart api': async () => {
    const result = restartComponent('api')
    return {
      accepted: true,
      message: result.detail,
      executedAt: result.timestamp,
      result
    }
  },
  'restart worker': async () => {
    const result = restartComponent('worker')
    return {
      accepted: true,
      message: result.detail,
      executedAt: result.timestamp,
      result
    }
  },
  'flush cache': async () => {
    const result = flushCache()
    return {
      accepted: true,
      message: result.detail,
      executedAt: result.timestamp,
      result
    }
  },
  'trigger synthetic-check': async () => {
    const report = runSyntheticCheck()
    return {
      accepted: true,
      message: 'Synthetic reliability check executed',
      executedAt: new Date().toISOString(),
      result: report
    }
  },
  'recalc risk': async () => {
    const report = runSyntheticCheck()
    const actions = recommendActions({
      metrics: {
        bookings: {
          baseline: report.summary.bookings + 10,
          current: report.summary.bookings
        },
        health: { trend: report.predictions.health.trend }
      },
      summary: {
        health: { overall: report.summary.healthScore > 0.75 ? 'green' : 'yellow' }
      }
    })
    return {
      accepted: true,
      message: `Risk recalculated. ${actions[0] || 'No new actions recommended.'}`,
      executedAt: new Date().toISOString(),
      result: { actions, predictions: report.predictions }
    }
  }
}

async function handleFallback(command: string): Promise<CommandResponse> {
  const executedAt = new Date().toISOString()
  return {
    accepted: true,
    message: `Command '${command}' acknowledged (simulated)`,
    executedAt,
    result: { simulated: true }
  }
}

export async function runControlCommand(
  payload: CommandRequest
): Promise<CommandResponse> {
  const commandKey = normalize(payload.command)
  const handler =
    handlers[commandKey] ||
    handlers[commandKey.replace(' run ', ' ')] ||
    handlers[commandKey.replace('execute ', '')]

  const response = handler ? await handler(payload.args || {}) : await handleFallback(commandKey)

  try {
    createEvent({
      type: 'user-action',
      severity: response.accepted ? 'info' : 'warning',
      title: `Control command: ${payload.command}`,
      description: response.message,
      source: 'controlplane',
      data: { args: payload.args, result: response.result },
      relatedActionIds: [],
      relatedIncidentIds: []
    })
  } catch {
    // non-blocking
  }

  return response
}

import { NextRequest } from 'next/server'
import type { Incident } from '@/types/incidents'
import { predictBookingThroughput, predictHealthTrend } from '../../../../../../packages/opsai-l7/src/predict'
import { diagnose } from '../../../../../../packages/opsai-helpdesk/src/engine'
import type { ControlPlaneOverview, DeploymentRecord } from './types'
import { buildAgents } from './agents'
import { createOpsClient } from './ops-client'

function normalizeIncidents(payload: unknown): Incident[] {
  if (!payload) return []
  if (Array.isArray(payload)) return payload as Incident[]
  if (typeof payload === 'object' && Array.isArray((payload as any).incidents)) {
    return (payload as any).incidents as Incident[]
  }
  return []
}

function sampleSeriesFromMetrics(metrics: any): number[] {
  if (!metrics) return []
  const points =
    metrics?.analysis?.series ||
    metrics?.raw_metrics ||
    metrics?.series ||
    metrics?.data ||
    []
  if (Array.isArray(points)) {
    return points
      .map((p: any) =>
        typeof p === 'number'
          ? p
          : typeof p?.value === 'number'
          ? p.value
          : typeof p?.cpu_percent === 'number'
          ? p.cpu_percent
          : null
      )
      .filter((v: number | null): v is number => v !== null)
      .slice(-5)
  }
  return []
}

export async function fetchControlPlaneOverview(
  request: NextRequest
): Promise<ControlPlaneOverview> {
  const baseUrl =
    process.env.OPS_API_BASE ||
    process.env.NEXT_PUBLIC_OPS_BASE ||
    process.env.NEXT_PUBLIC_SITE_URL ||
    new URL(request.url).origin

  const opsai = createOpsClient(baseUrl)

  const [summary, bookingsMetrics, systemMetrics, deploymentsRaw, incidentsRaw] =
    await Promise.all([
      opsai.summary(),
      opsai.metrics('bookings').catch(() => ({})),
      opsai.metrics('system').catch(() => ({})),
      opsai.deployments().catch(() => []),
      opsai.incidents().catch(() => [])
    ])

  const deployments: DeploymentRecord[] = Array.isArray(deploymentsRaw)
    ? (deploymentsRaw as DeploymentRecord[])
    : Array.isArray((deploymentsRaw as any)?.deployments)
    ? ((deploymentsRaw as any).deployments as DeploymentRecord[])
    : []

  const incidents = normalizeIncidents(incidentsRaw)

  const healthSeries = sampleSeriesFromMetrics(systemMetrics)
  const bookingSamples =
    Array.isArray((bookingsMetrics as any)?.raw_metrics) &&
    (bookingsMetrics as any).raw_metrics.length >= 2
      ? ((bookingsMetrics as any).raw_metrics as Array<{ timestamp?: string; value?: number }>)
      : [
          { timestamp: new Date(Date.now() - 3600_000).toISOString(), value: 120 },
          { timestamp: new Date().toISOString(), value: 95 }
        ]

  const predictions = {
    healthTrend: predictHealthTrend(
      healthSeries.length ? healthSeries : [0.82, 0.76, 0.71]
    ),
    bookingsTrend: predictBookingThroughput(
      bookingSamples
        .filter((p) => typeof p.value === 'number' && p.timestamp)
        .map((p) => ({ timestamp: p.timestamp as string, value: p.value as number }))
    )
  }

  // Lightweight helpdesk check to surface an insight
  const helpdesk = diagnose('', {
    summary: { deployments, health: { overall: summary?.health?.overall } },
    metrics: {
      bookings: {
        baseline: bookingSamples?.[0]?.value,
        current: bookingSamples?.[bookingSamples.length - 1]?.value
      },
      health: { trend: predictions.healthTrend.trend }
    }
  })

  const agents = buildAgents({
    summary,
    incidents,
    predictions,
    helpdesk
  })

  return {
    timestamp: new Date().toISOString(),
    health: summary?.health || { overall: 'unknown', services: [] },
    metrics: {
      bookings: bookingsMetrics,
      system: systemMetrics
    },
    deployments,
    incidents,
    predictions,
    agents,
    eventStream: {
      endpoint: '/api/ops/controlplane/events',
      status: 'ready'
    }
  }
}

