type EvaluationInput = {
  health?: string
  metrics?: any
  incidents?: Incident[]
}

export function listPlaybooks(): Playbook[] {
  return PLAYBOOKS
}

export function getPlaybook(id: string): Playbook | undefined {
  return PLAYBOOKS.find((p) => p.id === id)
}

export function evaluatePlaybooks(input: EvaluationInput): EvaluatedPlaybook[] {
  const incidents = input.incidents || []
  const recommendations = recommendActions({
    summary: { health: { overall: input.health } },
    metrics: input.metrics
  })

  return PLAYBOOKS.map((playbook) => {
    const reasons: string[] = []
    let hot = false

    if (playbook.trigger.type === 'health' && input.health && input.health.toLowerCase() !== 'green') {
      hot = true
      reasons.push(`Health is ${input.health}`)
    }

    if (
      playbook.trigger.type === 'metric' &&
      typeof input.metrics?.bookings?.current === 'number' &&
      typeof input.metrics?.bookings?.baseline === 'number'
    ) {
      const drop = input.metrics.bookings.current / Math.max(1, input.metrics.bookings.baseline)
      if (drop < 0.8) {
        hot = true
        reasons.push('Bookings below 80% baseline')
      }
    }

    if (playbook.trigger.type === 'incident') {
      const severe = incidents.some((i) => i.severity === 'high' || i.severity === 'critical')
      if (severe) {
        hot = true
        reasons.push('High severity incident present')
      }
    }

    if (playbook.trigger.type === 'deployment') {
      const deploymentIncident = incidents.some((i) =>
        (i.tags || []).includes('deployment') || i.description.toLowerCase().includes('deploy')
      )
      if (deploymentIncident) {
        hot = true
        reasons.push('Deployment related incident detected')
      }
    }

    const matchingRecommendation = recommendations.find((r) =>
      r.toLowerCase().includes(playbook.trigger.type)
    )
    if (matchingRecommendation) {
      hot = true
      reasons.push(matchingRecommendation)
    }

    return {
      ...playbook,
      hot,
      reasons
    }
  })
}

/**
 * SimCity Phase 6: Evaluator
 *
 * Computes metric deltas and evaluates dials to determine if a variant is allowed.
 * All functions are deterministic and pure.
 */

import { stableHash } from './simcity-hash'
import type {
  MetricDelta,
  DialStatus,
  DialDefinition,
  EvaluationResult,
  MetricId,
  SimCityEventEnvelope,
} from './simcity-types'
import { METRICS_REGISTRY } from './simcity-metrics'
import { computeMetricsFromEvents } from './simcity-metric-extractors'

/**
 * Compute metric deltas between base and variant metrics.
 */
export function computeMetricDeltas(
  baseMetrics: Record<MetricId, number>,
  variantMetrics: Record<MetricId, number>
): MetricDelta[] {
  const deltas: MetricDelta[] = []

  for (const metricId of Object.keys(METRICS_REGISTRY) as MetricId[]) {
    const base = baseMetrics[metricId] ?? 0
    const variant = variantMetrics[metricId] ?? 0
    const delta = variant - base

    const metric = METRICS_REGISTRY[metricId]
    let direction: 'improved' | 'degraded' | 'neutral'

    // Determine direction based on metric direction and delta
    if (Math.abs(delta) < 1e-9) {
      direction = 'neutral'
    } else if (metric.direction === 'higher-is-better') {
      direction = delta > 0 ? 'improved' : 'degraded'
    } else {
      // lower-is-better
      direction = delta < 0 ? 'improved' : 'degraded'
    }

    deltas.push({
      id: metricId,
      base,
      variant,
      delta,
      direction,
    })
  }

  // Sort deterministically by metric ID
  deltas.sort((a, b) => a.id.localeCompare(b.id))

  return deltas
}

/**
 * Evaluate dials against metrics and classify zones.
 */
export function evalDials(
  metrics: Record<MetricId, number>,
  dials: DialDefinition[]
): DialStatus[] {
  const statuses: DialStatus[] = []

  for (const dial of dials) {
    const value = metrics[dial.metric] ?? 0
    const metric = METRICS_REGISTRY[dial.metric]

    if (!metric) {
      // Skip dials for unknown metrics
      continue
    }

    let zone: 'green' | 'yellow' | 'red'

    const [greenMin, greenMax] = dial.green
    const [yellowMin, yellowMax] = dial.yellow
    const [redMin, redMax] = dial.red

    // Classify zone based on metric direction
    if (metric.direction === 'lower-is-better') {
      // Lower values are better: green is lowest range
      if (value >= greenMin && value <= greenMax) {
        zone = 'green'
      } else if (value >= yellowMin && value <= yellowMax) {
        zone = 'yellow'
      } else {
        zone = 'red'
      }
    } else {
      // Higher values are better: green is highest range
      if (value >= greenMin && value <= greenMax) {
        zone = 'green'
      } else if (value >= yellowMin && value <= yellowMax) {
        zone = 'yellow'
      } else {
        zone = 'red'
      }
    }

    statuses.push({
      metric: dial.metric,
      value,
      zone,
    })
  }

  // Sort deterministically by metric ID
  statuses.sort((a, b) => a.metric.localeCompare(b.metric))

  return statuses
}

/**
 * Generate deterministic summary string.
 */
function generateSummary(
  dialStatuses: DialStatus[],
  deltas?: MetricDelta[]
): string {
  const redCount = dialStatuses.filter((s) => s.zone === 'red').length
  const yellowCount = dialStatuses.filter((s) => s.zone === 'yellow').length
  const greenCount = dialStatuses.filter((s) => s.zone === 'green').length

  const parts: string[] = []

  parts.push(`Status: ${redCount} red, ${yellowCount} yellow, ${greenCount} green`)

  // Add top 3 worst red/yellow by magnitude
  const worst = [...dialStatuses]
    .filter((s) => s.zone === 'red' || s.zone === 'yellow')
    .sort((a, b) => {
      // Sort by zone (red first) then by value magnitude
      if (a.zone !== b.zone) {
        return a.zone === 'red' ? -1 : 1
      }
      return Math.abs(b.value) - Math.abs(a.value)
    })
    .slice(0, 3)

  if (worst.length > 0) {
    parts.push('Top issues:')
    for (const status of worst) {
      const metric = METRICS_REGISTRY[status.metric]
      parts.push(`  ${status.metric}: ${status.value.toFixed(3)} ${metric.unit} (${status.zone})`)
    }
  }

  // Add top deltas if provided
  if (deltas && deltas.length > 0) {
    const topDeltas = [...deltas]
      .filter((d) => d.direction !== 'neutral')
      .sort((a, b) => Math.abs(b.delta) - Math.abs(a.delta))
      .slice(0, 3)

    if (topDeltas.length > 0) {
      parts.push('Top changes:')
      for (const delta of topDeltas) {
        const sign = delta.delta > 0 ? '+' : ''
        parts.push(`  ${delta.id}: ${sign}${delta.delta.toFixed(3)} (${delta.direction})`)
      }
    }
  }

  return parts.join('\n')
}

/**
 * Evaluate a replay variant against dials.
 */
export function evaluateReplayVariant({
  reportHash,
  baseEvents,
  variantEvents,
  dials,
  variantId,
}: {
  reportHash: string
  baseEvents: SimCityEventEnvelope[]
  variantEvents: SimCityEventEnvelope[]
  dials: DialDefinition[]
  variantId?: string
}): EvaluationResult {
  // Compute metrics
  const baseMetrics = computeMetricsFromEvents(baseEvents)
  const variantMetrics = computeMetricsFromEvents(variantEvents)

  // Evaluate dials for variant
  const dialStatuses = evalDials(variantMetrics, dials)

  // Compute deltas if variant is not base
  const deltas = variantId && variantId !== 'base' ? computeMetricDeltas(baseMetrics, variantMetrics) : undefined

  // Determine allowed status
  const violated = dialStatuses.filter((s) => s.zone === 'red')
  const warnings = dialStatuses.filter((s) => s.zone === 'yellow')
  const allowed = violated.length === 0

  // Generate summary
  const summary = generateSummary(dialStatuses, deltas)

  return {
    allowed,
    violated,
    warnings,
    summary,
    reportHash,
    variantId,
  }
}

/**
 * SimCity Phase 7: Governance Rules
 *
 * Deterministic rules that evaluate proposals and produce governance decisions.
 * Rules never throw; they return null if inputs are missing.
 */

import type {
  GovernanceContext,
  GovernanceVerdict,
  GovernanceReason,
  OverrideRequirement,
  MetricDelta,
} from './simcity-types'
import { METRICS_REGISTRY } from './simcity-metrics'

export type GovernanceRuleResult = {
  verdict?: GovernanceVerdict
  reason?: GovernanceReason
  override?: OverrideRequirement
} | null

export type GovernanceRule = {
  id: string
  description: string
  evaluate: (ctx: GovernanceContext) => GovernanceRuleResult
}

// Thresholds (constants, documented)
const TRUST_VIOLATION_RATE_THRESHOLD = 0.01 // Block if trust violation rate increases by more than 1%
const LATENCY_REGRESSION_THRESHOLD = 0.05 // Warn if latency p95 increases by more than 5%
const ERROR_RATE_REGRESSION_THRESHOLD = 0.02 // Block if error rate increases by more than 2%

/**
 * Rule: Block if any dial status is red.
 */
function blockOnRedDial(ctx: GovernanceContext): GovernanceRuleResult {
  if (!ctx.dialsSnapshot || ctx.dialsSnapshot.length === 0) {
    return null // Missing input, let fail-closed handle it
  }

  const redDials = ctx.dialsSnapshot.filter((d) => d.zone === 'red')

  if (redDials.length > 0) {
    return {
      verdict: 'block',
      reason: {
        ruleId: 'block-on-red-dial',
        severity: 'block',
        message: `${redDials.length} dial(s) in red zone`,
        evidence: {
          redDials: redDials.map((d) => ({ metric: d.metric, value: d.value })),
        },
      },
    }
  }

  return null
}

/**
 * Rule: Warn if any dial status is yellow (unless already blocked).
 */
function warnOnYellowDial(ctx: GovernanceContext): GovernanceRuleResult {
  if (!ctx.dialsSnapshot || ctx.dialsSnapshot.length === 0) {
    return null
  }

  const yellowDials = ctx.dialsSnapshot.filter((d) => d.zone === 'yellow')

  if (yellowDials.length > 0) {
    return {
      verdict: 'warn',
      reason: {
        ruleId: 'warn-on-yellow-dial',
        severity: 'warn',
        message: `${yellowDials.length} dial(s) in yellow zone`,
        evidence: {
          yellowDials: yellowDials.map((d) => ({ metric: d.metric, value: d.value })),
        },
      },
    }
  }

  return null
}

/**
 * Rule: Block if trust violation rate regression exceeds threshold.
 */
function blockOnTrustRegression(ctx: GovernanceContext): GovernanceRuleResult {
  if (!ctx.replayEvaluation?.deltas) {
    return null
  }

  const trustDelta = ctx.replayEvaluation.deltas.find((d) => d.id === 'trust.violation_rate')

  if (!trustDelta || trustDelta.direction === 'neutral') {
    return null
  }

  // Check if trust violation rate worsened (increased for lower-is-better metric)
  if (trustDelta.direction === 'degraded' && trustDelta.delta !== null) {
    const absoluteDelta = Math.abs(trustDelta.delta)
    if (absoluteDelta > TRUST_VIOLATION_RATE_THRESHOLD) {
      return {
        verdict: 'block',
        reason: {
          ruleId: 'block-on-trust-regression',
          severity: 'block',
          message: `Trust violation rate regression exceeds threshold (delta: ${trustDelta.delta.toFixed(4)})`,
          evidence: {
            base: trustDelta.base,
            variant: trustDelta.variant,
            delta: trustDelta.delta,
            threshold: TRUST_VIOLATION_RATE_THRESHOLD,
          },
        },
      }
    }
  }

  return null
}

/**
 * Rule: Warn if latency p95 regression exceeds threshold.
 */
function warnOnLatencyRegression(ctx: GovernanceContext): GovernanceRuleResult {
  if (!ctx.replayEvaluation?.deltas) {
    return null
  }

  const latencyDelta = ctx.replayEvaluation.deltas.find((d) => d.id === 'latency.p95')

  if (!latencyDelta || latencyDelta.direction === 'neutral' || latencyDelta.base === null || latencyDelta.base === 0) {
    return null
  }

  // Calculate percentage increase
  if (latencyDelta.direction === 'degraded' && latencyDelta.delta !== null) {
    const percentIncrease = Math.abs(latencyDelta.delta / latencyDelta.base)

    if (percentIncrease > LATENCY_REGRESSION_THRESHOLD) {
      return {
        verdict: 'warn',
        reason: {
          ruleId: 'warn-on-latency-regression',
          severity: 'warn',
          message: `Latency p95 regression exceeds threshold (${(percentIncrease * 100).toFixed(1)}% increase)`,
          evidence: {
            base: latencyDelta.base,
            variant: latencyDelta.variant,
            delta: latencyDelta.delta,
            percentIncrease,
            threshold: LATENCY_REGRESSION_THRESHOLD,
          },
        },
      }
    }
  }

  return null
}

/**
 * Rule: Block if error rate regression exceeds threshold.
 */
function blockOnErrorRateRegression(ctx: GovernanceContext): GovernanceRuleResult {
  if (!ctx.replayEvaluation?.deltas) {
    return null
  }

  const errorDelta = ctx.replayEvaluation.deltas.find((d) => d.id === 'error.rate')

  if (!errorDelta || errorDelta.direction === 'neutral') {
    return null
  }

  // Check if error rate worsened (increased for lower-is-better metric)
  if (errorDelta.direction === 'degraded' && errorDelta.delta !== null) {
    const absoluteDelta = Math.abs(errorDelta.delta)
    if (absoluteDelta > ERROR_RATE_REGRESSION_THRESHOLD) {
      return {
        verdict: 'block',
        reason: {
          ruleId: 'block-on-error-rate-regression',
          severity: 'block',
          message: `Error rate regression exceeds threshold (delta: ${errorDelta.delta.toFixed(4)})`,
          evidence: {
            base: errorDelta.base,
            variant: errorDelta.variant,
            delta: errorDelta.delta,
            threshold: ERROR_RATE_REGRESSION_THRESHOLD,
          },
        },
      }
    }
  }

  return null
}

/**
 * Rule: Require override for apply/promote actions.
 */
function requireOverrideForApply(ctx: GovernanceContext): GovernanceRuleResult {
  const action = ctx.proposal.action.toLowerCase()

  if (action.includes('apply') || action.includes('promote') || action.includes('execute')) {
    return {
      verdict: 'warn', // Downgrade to warn with override requirement
      reason: {
        ruleId: 'require-override-for-apply',
        severity: 'warn',
        message: 'Action suggests apply/promote/execute - override required',
        evidence: {
          action: ctx.proposal.action,
        },
      },
      override: {
        reason: 'Action requires explicit approval for apply/promote/execute operations',
        roleRequired: 'admin',
        expiresAfterTicks: 100, // Expires after 100 ticks
      },
    }
  }

  return null
}

/**
 * Default governance rules registry.
 * Rules are evaluated in order; first block wins, then warn.
 */
export const DEFAULT_GOVERNANCE_RULES: GovernanceRule[] = [
  {
    id: 'block-on-red-dial',
    description: 'Block if any dial status is red',
