/**
 * Normalize LLM confidence to [0, 1] range.
 * Handles various formats (0-1, 0-100, etc.)
 */
export function normalizeLLMConfidence(value: unknown): number {
  if (typeof value !== 'number' || !Number.isFinite(value)) {
    return 0.5 // Default confidence if invalid
  }

  // If value is > 1, assume it's 0-100 scale
  if (value > 1) {
    return Math.max(0, Math.min(1, value / 100))
  }

  return Math.max(0, Math.min(1, value))
}

/**
 * SimCity Phase 6: Metric Extractors
 *
 * Pure functions that compute metrics from event streams.
 * All functions are deterministic: same input -> same output.
 */

import type { SimCityEventEnvelope, MetricId } from './simcity-types'
import { METRICS_REGISTRY } from './simcity-metrics'

/**
 * Compute p95 percentile deterministically from a sorted numeric array.
 */
function computeP95(values: number[]): number {
  if (values.length === 0) {
    return 0
  }

  const sorted = [...values].sort((a, b) => a - b)
  const index = Math.ceil(0.95 * sorted.length) - 1
  return sorted[Math.max(0, Math.min(index, sorted.length - 1))]
}

/**
 * Extract latency values from events.
 * Looks for latency_jitter events or payload.latency fields.
 */
function extractLatencies(events: SimCityEventEnvelope[]): number[] {
  const latencies: number[] = []

  for (const envelope of events) {
    const { type, payload } = envelope.event

    // Check for latency_jitter events
    if (type === 'latency_jitter' && typeof payload.ms === 'number') {
      latencies.push(payload.ms)
    }

    // Check for latency in payload
    if (typeof payload.latency === 'number') {
      latencies.push(payload.latency)
    }
    if (typeof payload.latencyMs === 'number') {
      latencies.push(payload.latencyMs)
    }
  }

  return latencies
}

/**
 * Count error events.
 * Events with type containing 'error' or payload.error === true.
 */
function countErrors(events: SimCityEventEnvelope[]): number {
  let count = 0

  for (const envelope of events) {
    const { type, payload } = envelope.event

    if (type.includes('error') || type.includes('failure') || type.includes('fault')) {
      count++
    } else if (payload.error === true || payload.failed === true) {
      count++
    }
  }

  return count
}

/**
 * Extract capacity/utilization signals from booking-load events.
 */
function extractCapacityUtilization(events: SimCityEventEnvelope[]): number {
  // Look for booking-load events with utilization signals
  for (const envelope of events) {
    if (envelope.event.domain === 'booking-load') {
      const { payload } = envelope.event

      if (typeof payload.utilization === 'number') {
        return Math.max(0, Math.min(1, payload.utilization))
      }
      if (typeof payload.pressure === 'number') {
        // Approximate utilization from pressure (0-1 scale)
        return Math.max(0, Math.min(1, payload.pressure))
      }
      if (typeof payload.load === 'number') {
        // Approximate utilization from load (normalize to 0-1)
        return Math.max(0, Math.min(1, payload.load / 10))
      }
    }
  }

  // Default: no utilization signal found
  return 0
}

/**
 * Extract booking success/drop signals.
 * Since we may not have explicit booking.success events yet, approximate from booking-load signals.
 */
function extractBookingMetrics(events: SimCityEventEnvelope[]): {
  successRate: number
  dropRate: number
} {
  let totalBookingSignals = 0
  let successSignals = 0
  let dropSignals = 0

  for (const envelope of events) {
    const { domain, type, payload } = envelope.event

    // Count booking-related events
    if (domain === 'booking-load' || domain === 'booking') {
      totalBookingSignals++

      // Success indicators
      if (type.includes('success') || type.includes('completed') || payload.success === true) {
        successSignals++
      }

      // Drop indicators
      if (type.includes('drop') || type.includes('reject') || payload.dropped === true) {
        dropSignals++
      }
    }
  }

  // If no booking signals, return defaults
  if (totalBookingSignals === 0) {
    return { successRate: 0, dropRate: 0 }
  }

  // Compute rates
  const successRate = successSignals / totalBookingSignals
  const dropRate = dropSignals / totalBookingSignals

  // If no explicit success/drop signals, approximate from load spikes (high load = lower success)
  if (successSignals === 0 && dropSignals === 0) {
    const loadSpikes = events.filter(
      (e) => e.event.domain === 'booking-load' && e.event.type === 'load_spike'
    ).length

    // Approximate: more load spikes = lower success rate
    const estimatedSuccessRate = Math.max(0, Math.min(1, 1 - loadSpikes / Math.max(1, totalBookingSignals) * 0.1))
    return { successRate: estimatedSuccessRate, dropRate: 1 - estimatedSuccessRate }
  }

  return { successRate, dropRate }
}

/**
 * Extract trust violation rate.
 * Look for trust domain events or violation indicators.
 */
function extractTrustViolationRate(events: SimCityEventEnvelope[]): number {
  let violations = 0
  let trustEvents = 0

  for (const envelope of events) {
    const { domain, type, payload } = envelope.event

    if (domain === 'trust' || domain === 'safety') {
      trustEvents++
      if (type.includes('violation') || payload.violation === true) {
        violations++
      }
    }
  }

  if (trustEvents === 0) {
    return 0 // No trust domain events yet
  }

  return violations / trustEvents
}

/**
 * Compute all metrics from an event stream.
 * Returns a record with all metric IDs and their computed values.
 * Missing signals return explicit defaults (0 or neutral values).
 */
export function computeMetricsFromEvents(
  events: SimCityEventEnvelope[]
): Record<MetricId, number> {
  const metrics: Partial<Record<MetricId, number>> = {}

  // Extract latencies and compute p95
  const latencies = extractLatencies(events)
  metrics['latency.p95'] = computeP95(latencies)

  // Count errors
  const errorCount = countErrors(events)
  const totalEvents = events.length
  metrics['error.rate'] = totalEvents > 0 ? errorCount / totalEvents : 0

  // Extract capacity utilization
  metrics['capacity.utilization'] = extractCapacityUtilization(events)

  // Extract booking metrics
  const bookingMetrics = extractBookingMetrics(events)
  metrics['booking.success_rate'] = bookingMetrics.successRate
  metrics['booking.drop_rate'] = bookingMetrics.dropRate

  // Extract trust violation rate
  metrics['trust.violation_rate'] = extractTrustViolationRate(events)

  // Ensure all metrics are present (fill missing with 0)
  const allMetrics: Record<MetricId, number> = {
    'booking.success_rate': metrics['booking.success_rate'] ?? 0,
    'booking.drop_rate': metrics['booking.drop_rate'] ?? 0,
    'capacity.utilization': metrics['capacity.utilization'] ?? 0,
    'trust.violation_rate': metrics['trust.violation_rate'] ?? 0,
    'latency.p95': metrics['latency.p95'] ?? 0,
    'error.rate': metrics['error.rate'] ?? 0,
  }

  return allMetrics
}

/**
 * SimCity Phase 9: Override Validation
 *
 * Validates override requests before appending.
 * Fail-closed: rejects invalid overrides.
 */

import type {
  OverrideRecord,
  PromotionDecision,
  SimCityProposal,
  OverrideActor,
  OverrideVerdict,
} from './simcity-types'

export interface OverrideRequest {
  proposalId: string
  decisionHash: string
  verdictAfter: OverrideVerdict
  justification: string
  actor: OverrideActor
}

export interface ValidateOverrideParams {
  proposal: SimCityProposal
  decision: PromotionDecision
  override: OverrideRequest
}

/**
 * Validate an override request.
 * Fail-closed: throws on any validation failure.
 *
 * @throws {Error} with message codes:
 * - OVERRIDE_NOT_ALLOWED: decision doesn't require overrides
 * - JUSTIFICATION_REQUIRED: justification is empty
 * - INSUFFICIENT_ROLE: actor role doesn't match requirements
 * - DECISION_HASH_MISMATCH: decisionHash doesn't match decision
 */
export function validateOverride({
  proposal,
  decision,
  override,
}: ValidateOverrideParams): void {
  // Check that decision requires overrides
  if (!decision.requiredOverrides || decision.requiredOverrides.length === 0) {
    throw new Error('OVERRIDE_NOT_ALLOWED')
  }

  // Check justification is provided
  if (!override.justification?.trim()) {
    throw new Error('JUSTIFICATION_REQUIRED')
  }

  // Check actor role matches at least one requirement
  const roleMatches = decision.requiredOverrides.some(
    (r) => r.roleRequired === override.actor.role
  )
  if (!roleMatches) {
    throw new Error('INSUFFICIENT_ROLE')
  }

  // Check decisionHash matches
  if (override.decisionHash !== decision.decisionHash) {
    throw new Error('DECISION_HASH_MISMATCH')
  }

  // Verify proposalId matches
  if (override.proposalId !== proposal.id || override.proposalId !== decision.proposalId) {
    throw new Error('PROPOSAL_ID_MISMATCH')
  }
}

/**
 * Convert GovernanceVerdict to OverrideVerdict.
 */
export function convertToOverrideVerdict(verdict: string): OverrideVerdict {
  const upper = verdict.toUpperCase()
  if (upper === 'ALLOW' || upper === 'WARN' || upper === 'BLOCK') {
    return upper as OverrideVerdict
  }
  throw new Error(`Invalid verdict: ${verdict}`)
}

/**
 * Proposal Engine for SimCity Phase 4
 *
 * Responsibilities:
 * - Collect recent event window
 * - Generate proposal drafts (LLM / rules)
 * - Validate + score proposals
 * - Enforce caps & thresholds
 * - Return finalized proposals
 */

import { stableHash } from './simcity-hash'
import type {
  SimCityProposal,
  SimCityProposalConfig,
  SimCityEvent,
  SimCityEventEnvelope,
} from './simcity-types'
import type { SimCityConfig } from './simcity'
import { generateLLMProposals, normalizeLLMConfidence, type LLMProposalDraft } from './simcity-llm'
import { resolveActiveDomains } from './simcity-domains'

type ProposalDraftWithSource = LLMProposalDraft & {
  _source?: 'llm' | 'rules'
}

export type SimCityState = {
  tick: number
  config: SimCityConfig
  events: SimCityEventEnvelope[]
}

/**
 * Generate rule-based proposals from recent events.
 * These are deterministic and don't require LLM.
 */
function generateRulesProposals(
  state: SimCityState,
  recentEvents: SimCityEventEnvelope[]
): LLMProposalDraft[] {
  const proposals: LLMProposalDraft[] = []
  const enabledDomains = resolveActiveDomains(state.config).map((d) => d.name)

  // Analyze recent events for patterns
  const eventsByDomain: Record<string, SimCityEventEnvelope[]> = {}
  for (const envelope of recentEvents) {
    const domain = envelope.event.domain
    if (!eventsByDomain[domain]) {
      eventsByDomain[domain] = []
    }
    eventsByDomain[domain].push(envelope)
  }

  // Rule 1: If load spikes detected, suggest throttling
  const loadSpikes = recentEvents.filter(
    (e) => e.event.domain === 'booking-load' && e.event.type === 'load_spike'
  )
  if (loadSpikes.length >= 2) {
    proposals.push({
      domain: 'booking-load',
      action: 'throttle_booking_acceptance',
      description: 'Multiple load spikes detected. Consider throttling booking acceptance rate.',
      confidence: 0.75,
      evidenceHints: loadSpikes.slice(0, 3).map((e) => e.event.id),
    })
  }

  // Rule 2: If latency jitter detected, suggest capacity pre-warming
  const latencyJitters = recentEvents.filter(
    (e) => e.event.domain === 'booking-load' && e.event.type === 'latency_jitter'
  )
  if (latencyJitters.length >= 1) {
    proposals.push({
      domain: 'booking-load',
      action: 'pre_warm_capacity',
      description: 'Latency jitter detected. Consider pre-warming capacity to reduce latency.',
      confidence: 0.7,
      evidenceHints: latencyJitters.slice(0, 2).map((e) => e.event.id),
    })
  }

  // Rule 3: If soft failures detected, suggest enabling contingency path
  const softFailures = recentEvents.filter(
    (e) => e.event.domain === 'booking-load' && e.event.type === 'soft_failure'
  )
  if (softFailures.length >= 1) {
    proposals.push({
      domain: 'booking-load',
      action: 'enable_contingency_path',
      description: 'Soft failures detected. Consider enabling contingency path for resilience.',
      confidence: 0.8,
      evidenceHints: softFailures.slice(0, 2).map((e) => e.event.id),
    })
  }

  // Rule 4: If high event rate in a domain, suggest increasing provider radius
  for (const domain of enabledDomains) {
    const domainEvents = eventsByDomain[domain] || []
    if (domainEvents.length >= 5) {
      proposals.push({
        domain,
        action: 'increase_provider_radius',
        description: `High event rate in ${domain}. Consider increasing provider radius to distribute load.`,
        confidence: 0.65,
        evidenceHints: domainEvents.slice(0, 5).map((e) => e.event.id),
      })
      break // Only one per domain
    }
  }

  return proposals
}

/**
 * Validate and score a proposal draft.
 * Returns null if proposal should be rejected.
 */
function validateProposal(
  draft: LLMProposalDraft,
  state: SimCityState,
  config: SimCityProposalConfig,
  knownEventIds: Set<string>
): SimCityProposal | null {
  const enabledDomains = resolveActiveDomains(state.config).map((d) => d.name)

  // Validation: domain must be enabled
  if (!enabledDomains.includes(draft.domain)) {
    return null
  }

  // Validation: action must be non-empty
  if (!draft.action || typeof draft.action !== 'string' || draft.action.trim().length === 0) {
    return null
  }

  // Validation: description must be non-empty
  if (!draft.description || typeof draft.description !== 'string' || draft.description.trim().length === 0) {
    return null
  }

  // Confidence resolution
  let confidence: number
  if (draft.confidence !== undefined) {
    confidence = normalizeLLMConfidence(draft.confidence)
  } else {
    // Default confidence based on source
    confidence = 0.5
  }

  // Validation: confidence must meet minimum threshold
  const minConfidence = config.minConfidence ?? 0.6
  if (confidence < minConfidence) {
    return null
  }

  // Validation: evidence event IDs must exist
  const evidenceEventIds = (draft.evidenceHints || [])
    .filter((id) => typeof id === 'string' && knownEventIds.has(id))

  // Generate deterministic proposal ID (content-hashed, no timestamps)
  const proposalId = stableHash(
    `${state.config.seed}:${state.tick}:${draft.domain}:${draft.action}:${confidence}:${evidenceEventIds.sort().join(',')}`
  )

  return {
    id: proposalId,
    tick: state.tick,
    domain: draft.domain,
    action: draft.action,
    description: draft.description,
    confidence,
    evidenceEventIds,
    source: 'llm', // Will be overridden if from rules
  }
}

/**
 * Deduplicate proposals by (domain + action) pair.
 * Keeps the proposal with highest confidence.
 */
function deduplicateProposals(proposals: SimCityProposal[]): SimCityProposal[] {
  const seen = new Map<string, SimCityProposal>()

  for (const proposal of proposals) {
    const key = `${proposal.domain}:${proposal.action}`
    const existing = seen.get(key)

    if (!existing || proposal.confidence > existing.confidence) {
      seen.set(key, proposal)
    }
  }

  return Array.from(seen.values())
}

/**
 * Generate proposals for the current SimCity state.
 *
 * This function is deterministic:
 * - Same seed + config + event stream ‚Üí same proposals
 * - Proposal IDs are content-hashed (no timestamps)
 * - Sorting is deterministic
 *
 * @param state - Current SimCity state
 * @param config - SimCity configuration
 * @returns Array of validated proposals (empty if proposals disabled)
 */
export async function generateProposals(
  state: SimCityState,
  config: SimCityConfig
): Promise<SimCityProposal[]> {
  const proposalConfig = config.proposals
  if (!proposalConfig) {
    return []
  }

  if (proposalConfig.mode === 'off') {
    return []
  }

  // Collect recent event window (last 20 ticks or last 50 events, whichever is smaller)
  const recentWindow = Math.min(20, state.tick)
  const recentEvents = state.events
    .filter((e) => e.generatedAtTick > state.tick - recentWindow)
    .slice(-50)

  // Build set of known event IDs for validation
  const knownEventIds = new Set(state.events.map((e) => e.event.id))

  const allDrafts: ProposalDraftWithSource[] = []

  // Generate LLM proposals if mode is 'llm' or 'hybrid'
  if (proposalConfig.mode === 'llm' || proposalConfig.mode === 'hybrid') {
    try {
      const llmDrafts = await generateLLMProposals({
        tick: state.tick,
        recentEvents: recentEvents.map((e) => ({
          id: e.event.id,
          tick: e.event.tick,
          domain: e.event.domain,
          type: e.event.type,
          payload: e.event.payload,
        })),
        enabledDomains: resolveActiveDomains(config).map((d) => d.name),
        config: {
          seed: config.seed,
          scenarios: config.scenarios,
        },
      })
      for (const draft of llmDrafts) {
        allDrafts.push({ ...draft, _source: 'llm' } as ProposalDraftWithSource)
      }
    } catch {
      // Fail closed: continue with rules proposals if LLM fails
    }
  }

  // Generate rules proposals if mode is 'rules' or 'hybrid'
  if (proposalConfig.mode === 'rules' || proposalConfig.mode === 'hybrid') {
    const rulesDrafts = generateRulesProposals(state, recentEvents)
    // Mark rules proposals with source
    for (const draft of rulesDrafts) {
      allDrafts.push({ ...draft, _source: 'rules' } as ProposalDraftWithSource)
    }
  }

  // Validate and score all drafts
  const validated: SimCityProposal[] = []
  for (const draft of allDrafts) {
    const proposal = validateProposal(draft, state, proposalConfig, knownEventIds)
    if (proposal) {
      // Set source from draft metadata
      proposal.source = draft._source ?? 'llm'
      validated.push(proposal)
    }
  }

  // Deduplicate by (domain + action)
  const deduplicated = deduplicateProposals(validated)

  // Sort deterministically (by domain, then action, then confidence desc)
  deduplicated.sort((a, b) => {
    if (a.domain !== b.domain) {
      return a.domain.localeCompare(b.domain)
    }
    if (a.action !== b.action) {
      return a.action.localeCompare(b.action)
    }
    return b.confidence - a.confidence
  })

  // Cap proposals per tick
  const maxPerTick = proposalConfig.maxPerTick ?? 3
  return deduplicated.slice(0, maxPerTick)
}

