import { NextResponse } from 'next/server'
import { aggregateLogs } from '@/scripts/logs-store'
import {
  detectPatterns,
  correlateWithDeployments,
  getTopErrors,
  generateInsights
} from '@/scripts/logs-analyzer'
import { loadPatterns } from '@/scripts/logs-store'
import { loadEvents } from '@/scripts/ops-events-store'
import type { LogAnalysis } from '@/types/logs'

/**
 * LogsAI - Pattern-based event log analyzer
 * 
 * Analyzes logs matching a specific pattern or filter.
 * Output style: Analytical, pattern-focused, correlational (not causal unless clear).
 * 
 * Route: /api/ops/events/logs/[pattern]
 * 
 * Query parameters:
 * - pattern: Pattern to match (optional, if not in path)
 * - startTime: ISO timestamp for start of time range
 * - endTime: ISO timestamp for end of time range
 * - lookbackHours: Hours to look back (default: 24)
 * - source: Filter by source
 * - service: Filter by service
 * - severity: Filter by severity
 */
export async function GET(
  request: Request,
  context: { params: Promise<{ pattern: string[] }> }
) {
  try {
    const { searchParams } = new URL(request.url)
    const patternParam = (await context.params).pattern
    const patternFromPath = Array.isArray(patternParam) ? patternParam.join('/') : (patternParam || '')
    const patternFromQuery = searchParams.get('pattern')
    const pattern = patternFromPath || patternFromQuery || ''

    const startTime = searchParams.get('startTime')
    const endTime = searchParams.get('endTime')
    const lookbackHoursParam = searchParams.get('lookbackHours') || '24'
    const lookbackHours = parseInt(lookbackHoursParam, 10)

    // Validate lookbackHours
    if (isNaN(lookbackHours) || lookbackHours < 1 || lookbackHours > 168) {
      return NextResponse.json(
        { ok: false, error: 'Invalid lookbackHours parameter. Must be between 1 and 168 (hours)' },
        { status: 400 }
      )
    }

    // Validate time range if provided
    if (startTime) {
      const startDate = new Date(startTime)
      if (isNaN(startDate.getTime())) {
        return NextResponse.json(
          { ok: false, error: 'Invalid startTime format. Use ISO 8601 format.' },
          { status: 400 }
        )
      }
    }

    if (endTime) {
      const endDate = new Date(endTime)
      if (isNaN(endDate.getTime())) {
        return NextResponse.json(
          { ok: false, error: 'Invalid endTime format. Use ISO 8601 format.' },
          { status: 400 }
        )
      }
    }
    const source = searchParams.get('source')
    const service = searchParams.get('service')
    const severity = searchParams.get('severity')

    // Calculate time range
    const end = endTime ? new Date(endTime) : new Date()
    const start = startTime 
      ? new Date(startTime)
      : new Date(end.getTime() - lookbackHours * 60 * 60 * 1000)

    // Get all logs
    let logs = aggregateLogs(start.toISOString(), end.toISOString())

    // Filter by pattern if provided
    if (pattern) {
      const patternRegex = new RegExp(pattern, 'i')
      logs = logs.filter((log: any) => 
        patternRegex.test(log.message) ||
        patternRegex.test(log.source) ||
        (log.service && patternRegex.test(log.service))
      )
    }

    // Filter by source
    if (source) {
      logs = logs.filter((log: any) => log.source === source)
    }

    // Filter by service
    if (service) {
      logs = logs.filter((log: any) => log.service === service)
    }

    // Filter by severity
    if (severity) {
      logs = logs.filter((log: any) => log.severity === severity)
    }

    // Load existing patterns
    const existingPatterns = loadPatterns()

    // Detect patterns
    const detectedPatterns = detectPatterns(logs, existingPatterns)

    // Correlate with deployments
    const events = loadEvents()
    const correlations = correlateWithDeployments(logs, events)

    // Get top errors
    const topErrors = getTopErrors(logs)

    // Generate insights
    const insights = generateInsights(logs, detectedPatterns, correlations)

    // Calculate statistics
    const errorCount = logs.filter((l: any) => l.severity === 'error').length
    const criticalCount = logs.filter((l: any) => l.severity === 'critical').length
    const warningCount = logs.filter((l: any) => l.severity === 'warning').length

    const analysis: LogAnalysis = {
      timestamp: new Date().toISOString(),
      category: 'event',
      timeRange: {
        start: start.toISOString(),
        end: end.toISOString()
      },
      totalLogs: logs.length,
      errorCount,
      warningCount,
      criticalCount,
      newPatterns: detectedPatterns.newPatterns,
      recurringPatterns: detectedPatterns.recurringPatterns,
      regressions: detectedPatterns.regressions,
      deploymentCorrelations: correlations,
      topErrors: topErrors.slice(0, 10),
      insights
    }

    return NextResponse.json({
      ok: true,
      data: analysis,
      filters: {
        pattern: pattern || undefined,
        source: source || undefined,
        service: service || undefined,
        severity: severity || undefined
      }
    })
  } catch (error) {
    console.error('Error analyzing event logs:', error)
    return NextResponse.json(
      { ok: false, error: 'Failed to analyze event logs' },
      { status: 500 }
    )
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { createClient } from '@supabase/supabase-js'
import { withSLOProbe } from '@/middleware/sloProbe'
import { featureFlags } from '@/config/featureFlags'

import { supabaseAdmin as supabase } from '@/lib/supabaseProxies';

interface ForceCancelRequest {
  booking_id: string
  reason: 'ADMIN_OVERRIDE' | 'SYSTEM_ERROR' | 'FRAUD_DETECTED' | 'COMPLIANCE_VIOLATION'
  admin_notes?: string
  refund_customer?: boolean
}

const forceCancelHandler = async (req: NextRequest) => {
  try {
    // Check feature flag
    if (!featureFlags.beta.core_booking_flow) {
      return NextResponse.json(
        { error: 'Core booking flow not enabled' },
        { status: 503 }
      )
    }

    const body: ForceCancelRequest = await req.json()
    const { booking_id, reason, admin_notes, refund_customer = true } = body

    if (!booking_id || !reason) {
      return NextResponse.json(
        { error: 'Missing required fields: booking_id, reason' },
        { status: 400 }
      )
    }

    // Get booking details
    const { data: booking, error: bookingError } = await supabase
      .from('bookings')
      .select('*')
      .eq('id', booking_id)
      .single()

    if (bookingError || !booking) {
      return NextResponse.json(
        { error: 'Booking not found' },
        { status: 404 }
      )
    }

    // Check if booking can be cancelled
    if (['cancelled', 'refunded'].includes(booking.state)) {
      return NextResponse.json(
        { error: `Booking already in final state: ${booking.state}` },
        { status: 400 }
      )
    }

    // Get current state for audit log
    const fromState = booking.state

    // Update booking state to cancelled
    const { error: updateError } = await supabase
      .from('bookings')
      .update({
        state: 'cancelled',
        cancelled_at: new Date().toISOString(),
        cancelled_reason: reason
      })
      .eq('id', booking_id)

    if (updateError) {
      throw new Error(`Failed to update booking state: ${updateError.message}`)
    }

    // Log the state transition
    await supabase
      .from('booking_audit_log')
      .insert({
        booking_id: booking.id,
        from_state: fromState,
        to_state: 'cancelled',
        action: 'force_cancel',
        actor_type: 'admin',
        actor_id: 'ops_force_cancel_endpoint',
        metadata: {
          reason,
          admin_notes,
          refund_customer,
          cancelled_at: new Date().toISOString(),
          stripe_payment_intent: booking.stripe_payment_intent_id,
          original_state: fromState
        }
      })

    // If customer should be refunded, process refund
    if (refund_customer && booking.price_cents && booking.price_cents > 0) {
      // TODO: Implement actual Stripe refund
      console.log(`Would refund ${booking.price_cents} cents for force-cancelled booking ${booking_id}`)

      // Update to refunded state
      await supabase
        .from('bookings')
        .update({
          state: 'refunded',
          refunded_at: new Date().toISOString()
        })
        .eq('id', booking_id)

      // Log refund transition
      await supabase
        .from('booking_audit_log')
        .insert({
          booking_id: booking.id,
          from_state: 'cancelled',
          to_state: 'refunded',
          action: 'refund_after_force_cancel',
          actor_type: 'system',
          actor_id: 'ops_force_cancel_endpoint',
          metadata: {
            refund_amount_cents: booking.price_cents,
            refunded_at: new Date().toISOString(),
            force_cancel_reason: reason
          }
        })
    }

    console.log(`Force-cancelled booking ${booking_id} with reason: ${reason}`)

    return NextResponse.json({
      ok: true,
      data: {
        booking_id,
        cancelled_at: new Date().toISOString(),
        final_state: refund_customer ? 'refunded' : 'cancelled',
        reason,
        admin_notes
      }
    })

  } catch (error) {
    console.error('Error in force-cancel endpoint:', error)
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    )
  }
}

export const POST = withSLOProbe(forceCancelHandler, '/api/ops/force-cancel')

import { NextResponse } from 'next/server'

/**
 * Authentication health check endpoint
 * HealthAI monitors this to detect auth service issues, session management problems, and OAuth provider connectivity
 */
export async function GET() {
  const timestamp = new Date().toISOString()

  try {
    const { getServerSupabase } = await import('@/lib/supabaseServer')
    const supabase = new Proxy({} as any, { get: (target, prop) => (getServerSupabase() as any)[prop] }) as ReturnType<typeof getServerSupabase>

    const checks: Record<string, any> = {}
    const recommendations: string[] = []
    let overallStatus: 'healthy' | 'degraded' | 'unhealthy' = 'healthy'

    // Test 1: Auth service availability
    const authStart = Date.now()
    const { data: { session }, error: authError } = await supabase.auth.getSession()
    const authLatency = Date.now() - authStart

    if (authError) {
      // JWT errors are expected when no session exists - not a failure
      if (authError.message.includes('JWT') || authError.message.includes('session')) {
        checks.authService = { 
          status: 'passed', 
          latency: authLatency,
          message: 'Auth service responding (no active session expected)'
        }
      } else {
        checks.authService = { status: 'failed', error: authError.message, latency: authLatency }
        overallStatus = 'unhealthy'
        recommendations.push('Auth service unavailable - check Supabase Auth configuration')
      }
    } else {
      checks.authService = { 
        status: 'passed', 
        latency: authLatency,
        hasSession: !!session,
        message: 'Auth service operational'
      }
      if (authLatency > 1000) {
        recommendations.push(`Auth service latency elevated: ${authLatency}ms`)
        overallStatus = overallStatus === 'healthy' ? 'degraded' : overallStatus
      }
    }

    // Test 2: User profile access (RLS check)
    const profileStart = Date.now()
    const { error: profileError } = await supabase
      .from('profiles')
      .select('id, role')
      .limit(1)
    const profileLatency = Date.now() - profileStart

    if (profileError) {
      // Some errors are expected (e.g., RLS blocking when not authenticated)
      if (profileError.code === 'PGRST116' || profileError.message.includes('permission')) {
        checks.profileAccess = { 
          status: 'passed', 
          latency: profileLatency,
          message: 'Profile access working (RLS policies active)'
        }
      } else {
        checks.profileAccess = { status: 'failed', error: profileError.message, latency: profileLatency }
        overallStatus = overallStatus === 'healthy' ? 'degraded' : 'unhealthy'
        recommendations.push('Profile table access issues - check RLS policies')
      }
    } else {
      checks.profileAccess = { 
        status: 'passed', 
        latency: profileLatency,
        message: 'Profile access operational'
      }
      if (profileLatency > 1000) {
        recommendations.push(`Profile access latency elevated: ${profileLatency}ms`)
        overallStatus = overallStatus === 'healthy' ? 'degraded' : overallStatus
      }
    }

    // Test 3: OAuth providers configuration (check if Google OAuth is configured)
    const oauthConfig = {
      google: !!process.env.GOOGLE_CLIENT_ID && !!process.env.GOOGLE_CLIENT_SECRET,
      // Add other OAuth providers as needed
    }

    checks.oauthProviders = {
      status: Object.values(oauthConfig).some(v => v) ? 'passed' : 'warning',
      configured: oauthConfig,
      message: Object.values(oauthConfig).some(v => v) 
        ? 'OAuth providers configured' 
        : 'No OAuth providers configured'
    }

    if (!Object.values(oauthConfig).some(v => v)) {
      recommendations.push('No OAuth providers configured - users limited to email/password')
      // This is not necessarily unhealthy, just informational
    }

    // Test 4: Environment variables check
    const envCheck = {
      supabaseUrl: !!process.env.NEXT_PUBLIC_SUPABASE_URL,
      supabaseAnonKey: !!process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY,
      supabaseServiceKey: !!process.env.SUPABASE_SERVICE_ROLE_KEY
    }

    const allEnvPresent = Object.values(envCheck).every(v => v)
    checks.environment = {
      status: allEnvPresent ? 'passed' : 'failed',
      variables: envCheck,
      message: allEnvPresent 
        ? 'All required environment variables present' 
        : 'Some environment variables missing'
    }

    if (!allEnvPresent) {
      overallStatus = 'unhealthy'
      recommendations.push('Missing required Supabase environment variables')
      recommendations.push('Verify NEXT_PUBLIC_SUPABASE_URL and SUPABASE_SERVICE_ROLE_KEY are set')
    }

    if (recommendations.length === 0) {
      recommendations.push('Authentication system operating normally')
    }

    return NextResponse.json({
      status: overallStatus,
      timestamp,
      checks,
      metrics: {
        authLatency,
        profileLatency
      },
      recommendations
    }, {
      status: overallStatus === 'healthy' ? 200 : overallStatus === 'degraded' ? 200 : 503
    })
  } catch (error) {
    return NextResponse.json({
      status: 'unhealthy',
      timestamp,
      error: error instanceof Error ? error.message : 'Auth health check failed',
      recommendations: [
        'Auth health check system unavailable',
        'Review application logs for auth-related errors',
        'Verify Supabase configuration and environment variables'
      ]
    }, { status: 503 })
  }
}

import { NextResponse } from 'next/server'

/**
 * Cache health check endpoint
 * HealthAI monitors this to detect cache invalidation issues, queue buildup, and performance degradation
 */
export async function GET() {
  const timestamp = new Date().toISOString()

  try {
    const { getServerSupabase } = await import('@/lib/supabaseServer')
    const supabase = new Proxy({} as any, { get: (target, prop) => (getServerSupabase() as any)[prop] }) as ReturnType<typeof getServerSupabase>

    // Check cache invalidation queue
    let queueStats = null
    try {
      const { data: pending, error: queueError } = await supabase
        .from('cache_invalidation_queue')
        .select('id, tag, enqueued_at, processed_at, retry_count')
        .is('processed_at', null)
        .order('enqueued_at', { ascending: true })
        .limit(100)

      if (!queueError && pending) {
        const oldestPending = pending.length > 0 ? pending[0] : null
        const highRetryCount = pending.filter(p => (p.retry_count || 0) >= 3).length

        queueStats = {
          pending: pending.length,
          oldestPendingAge: oldestPending 
            ? Math.floor((Date.now() - new Date(oldestPending.enqueued_at).getTime()) / 1000)
            : null,
          highRetryCount,
          tags: [...new Set(pending.map(p => p.tag))].slice(0, 10)
        }
      }
    } catch (error) {
      // cache_invalidation_queue might not exist
      queueStats = { available: false, error: error instanceof Error ? error.message : 'Unknown error' }
    }

    // Try to get cache performance metrics if available
    let performanceMetrics = null
    try {
      const { cachePerformanceMonitor } = await import('@/lib/cache/monitoring')
      const healthSummary = await cachePerformanceMonitor.getCacheHealthSummary()
      performanceMetrics = healthSummary
    } catch {
      // Cache monitoring might not be available
      performanceMetrics = { available: false }
    }

    // Determine status
    let status: 'healthy' | 'degraded' | 'unhealthy' = 'healthy'
    const recommendations: string[] = []

    if (queueStats && 'pending' in queueStats) {
      if (queueStats.pending > 100) {
        status = 'unhealthy'
        recommendations.push(`Cache invalidation queue has ${queueStats.pending} pending items`)
        recommendations.push('Queue processing may be stuck - investigate cache worker')
      } else if (queueStats.pending > 50) {
        status = 'degraded'
        recommendations.push(`Cache invalidation queue elevated: ${queueStats.pending} pending`)
        recommendations.push('Monitor queue processing rate')
      }

      if (queueStats.oldestPendingAge && queueStats.oldestPendingAge > 3600) {
        status = status === 'healthy' ? 'degraded' : status
        recommendations.push(`Oldest pending invalidation is ${Math.floor(queueStats.oldestPendingAge / 60)} minutes old`)
      }

      if (queueStats.highRetryCount > 0) {
        status = status === 'healthy' ? 'degraded' : status
        recommendations.push(`${queueStats.highRetryCount} cache invalidations with high retry count`)
      }
    }

    if (performanceMetrics && 'overallHitRate' in performanceMetrics) {
      const hitRate = performanceMetrics.overallHitRate as number
      if (hitRate < 0.3) {
        status = status === 'healthy' ? 'degraded' : status
        recommendations.push(`Cache hit rate low: ${(hitRate * 100).toFixed(1)}% (target: ‚â•30%)`)
        recommendations.push('Consider reviewing cache TTLs and warming strategies')
      }
    }

    if (recommendations.length === 0) {
      recommendations.push('Cache system operating normally')
    }

    return NextResponse.json({
      status,
      timestamp,
      queue: queueStats,
      performance: performanceMetrics,
      recommendations
    }, {
      status: status === 'healthy' ? 200 : status === 'degraded' ? 200 : 503
    })
  } catch (error) {
    return NextResponse.json({
      status: 'unhealthy',
      timestamp,
      error: error instanceof Error ? error.message : 'Cache health check failed',
      recommendations: [
        'Cache health check system unavailable',
        'Review application logs for cache-related errors',
        'Verify cache invalidation queue table exists'
      ]
    }, { status: 503 })
  }
}

import { NextResponse } from 'next/server'

/**
 * Database health check endpoint
 * HealthAI monitors this to detect DB connectivity issues, latency spikes, and connection pool problems
 */
export async function GET() {
  const timestamp = new Date().toISOString()

  try {
    const { getServerSupabase } = await import('@/lib/supabaseServer')
    const supabase = new Proxy({} as any, { get: (target, prop) => (getServerSupabase() as any)[prop] }) as ReturnType<typeof getServerSupabase>

    // Test 1: Basic connectivity
    const connectStart = Date.now()
    const { error: connectError } = await supabase
      .from('profiles')
      .select('id')
      .limit(1)
    const connectLatency = Date.now() - connectStart

    if (connectError) {
      return NextResponse.json({
        status: 'unhealthy',
        timestamp,
        checks: {
          connectivity: { status: 'failed', error: connectError.message, code: connectError.code }
        },
        latency: connectLatency,
        recommendations: [
          'Verify Supabase connection string',
          'Check database server status',
          'Review network connectivity',
          'Check RLS policies for profiles table'
        ]
      }, { status: 503 })
    }

    // Test 2: Write capability (read-only check)
    const writeStart = Date.now()
    const { error: writeError } = await supabase
      .from('profiles')
      .select('id, updated_at')
      .limit(1)
    const writeLatency = Date.now() - writeStart

    // Test 3: Complex query performance
    const complexStart = Date.now()
    const { error: complexError } = await supabase
      .from('bookings')
      .select('id, status, created_at')
      .limit(1)
    const complexLatency = Date.now() - complexStart

    const allChecksPassed = !writeError && !complexError
    const status = allChecksPassed ? 'healthy' : 'degraded'
    const maxLatency = Math.max(connectLatency, writeLatency, complexLatency)

    return NextResponse.json({
      status,
      timestamp,
      checks: {
        connectivity: { status: 'passed', latency: connectLatency },
        readCapability: { status: writeError ? 'failed' : 'passed', latency: writeLatency, error: writeError?.message },
        complexQueries: { status: complexError ? 'failed' : 'passed', latency: complexLatency, error: complexError?.message }
      },
      metrics: {
        maxLatency,
        avgLatency: (connectLatency + writeLatency + complexLatency) / 3,
        connectLatency,
        writeLatency,
        complexLatency
      },
      recommendations: maxLatency > 1000 
        ? ['Database latency elevated - consider connection pooling optimization', 'Review slow query logs', 'Check database load']
        : []
    }, {
      status: status === 'healthy' ? 200 : 200 // Return 200 even if degraded (HealthAI will interpret)
    })
  } catch (error) {
    return NextResponse.json({
      status: 'unhealthy',
      timestamp,
      error: error instanceof Error ? error.message : 'Database health check failed',
      recommendations: [
        'Database connection completely unavailable',
        'Check Supabase service status',
        'Verify environment variables',
        'Review application logs for connection errors'
      ]
    }, { status: 503 })
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { getOpsMode } from '../_config'
import {
  fetchSimcitySnapshot,
  simcityToHealth
} from '../_simcity/ops-from-simcity'

/**
 * Main health endpoint - aggregates all subsystem health checks
 * HealthAI monitors this endpoint to get overall system status
 */
export async function GET(request: NextRequest) {
  if (getOpsMode() === 'simcity') {
    try {
      const { metrics, violations } = await fetchSimcitySnapshot(request.nextUrl.origin)
      return NextResponse.json(simcityToHealth(metrics, violations))
    } catch (error) {
      return NextResponse.json(
        {
          error: 'Failed to load SimCity health',
          message: error instanceof Error ? error.message : 'Unknown error'
        },
        { status: 503 }
      )
    }
  }

  const timestamp = new Date().toISOString()

  try {
    // Check all subsystems in parallel
    const [dbHealth, webhookHealth, cacheHealth, searchHealth, authHealth] = await Promise.allSettled([
      checkDatabase(),
      checkWebhooks(),
      checkCache(),
      checkSearch(),
      checkAuth()
    ])

    const subsystems = {
      db: dbHealth.status === 'fulfilled' ? dbHealth.value : { status: 'unhealthy', error: 'check failed' },
      webhooks: webhookHealth.status === 'fulfilled' ? webhookHealth.value : { status: 'unhealthy', error: 'check failed' },
      cache: cacheHealth.status === 'fulfilled' ? cacheHealth.value : { status: 'unhealthy', error: 'check failed' },
      search: searchHealth.status === 'fulfilled' ? searchHealth.value : { status: 'unhealthy', error: 'check failed' },
      auth: authHealth.status === 'fulfilled' ? authHealth.value : { status: 'unhealthy', error: 'check failed' }
    }

    // Determine overall status
    const allHealthy = Object.values(subsystems).every(s => s.status === 'healthy')
    const anyUnhealthy = Object.values(subsystems).some(s => s.status === 'unhealthy')
    const overallStatus = allHealthy ? 'healthy' : anyUnhealthy ? 'unhealthy' : 'degraded'

    return NextResponse.json({
      status: overallStatus,
      timestamp,
      subsystems,
      version: process.env.npm_package_version || 'unknown',
      environment: process.env.NODE_ENV || 'development'
    }, {
      status: overallStatus === 'healthy' ? 200 : overallStatus === 'degraded' ? 200 : 503
    })
  } catch (error) {
    return NextResponse.json({
      status: 'unhealthy',
      timestamp,
      error: error instanceof Error ? error.message : 'Unknown error',
      subsystems: {}
    }, { status: 503 })
  }
}

