import { NextRequest, NextResponse } from 'next/server'
import { createClient } from '@supabase/supabase-js'
import { getOpsMode } from '../../_config'
import {
  fetchSimcitySnapshot,
  simcityToBookingMetrics
} from '../../_simcity/ops-from-simcity'
import { MetricsAI, BookingMetrics } from '@/lib/metrics/metricsAI'

import { supabaseAdmin as supabase } from '@/lib/supabaseProxies';

/**
 * GET /ops/metrics/bookings
 * 
 * Returns booking throughput metrics analysis
 * MetricsAI analyzes trends and detects anomalies
 */
export async function GET(request: NextRequest) {
  if (getOpsMode() === 'simcity') {
    try {
      const { metrics } = await fetchSimcitySnapshot(request.nextUrl.origin)
      return NextResponse.json(simcityToBookingMetrics(metrics))
    } catch (error) {
      return NextResponse.json(
        {
          error: 'Failed to load SimCity booking metrics',
          message: error instanceof Error ? error.message : 'Unknown error'
        },
        { status: 503 }
      )
    }
  }

  try {
    const { searchParams } = new URL(request.url)
    const timeRange = searchParams.get('timeRange') || '1h'
    const compareWith = searchParams.get('compareWith') // Optional: previous time range for comparison

    // Calculate time range
    const now = new Date()
    let startTime: Date
    let compareStartTime: Date | null = null

    switch (timeRange) {
      case '15m':
        startTime = new Date(now.getTime() - 15 * 60 * 1000)
        break
      case '1h':
        startTime = new Date(now.getTime() - 60 * 60 * 1000)
        break
      case '6h':
        startTime = new Date(now.getTime() - 6 * 60 * 60 * 1000)
        break
      case '24h':
        startTime = new Date(now.getTime() - 24 * 60 * 60 * 1000)
        break
      case '7d':
        startTime = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000)
        break
      default:
        startTime = new Date(now.getTime() - 60 * 60 * 1000)
    }

    // If compareWith is provided, calculate previous period
    if (compareWith) {
      const duration = now.getTime() - startTime.getTime()
      compareStartTime = new Date(startTime.getTime() - duration)
    }

    // Fetch booking metrics grouped by 5-minute buckets
    // We'll aggregate from the bookings table
    // Note: bookings table has total_amount (DECIMAL), not total_amount_cents
    const { data: bookings, error: bookingsError } = await supabase
      .from('bookings')
      .select('id, status, created_at, updated_at, total_amount')
      .gte('created_at', startTime.toISOString())
      .order('created_at', { ascending: true })

    if (bookingsError) {
      console.error('Failed to fetch bookings:', bookingsError)
      return NextResponse.json(
        { error: 'Failed to fetch booking metrics' },
        { status: 500 }
      )
    }

    // Group bookings into 5-minute buckets
    const bucketMap = new Map<string, {
      bookings_created: number
      bookings_confirmed: number
      bookings_cancelled: number
      bookings_completed: number
      total_revenue_cents: number
    }>()

    for (const booking of bookings || []) {
      const bucketTime = new Date(booking.created_at)
      bucketTime.setMinutes(Math.floor(bucketTime.getMinutes() / 5) * 5, 0, 0)
      const bucketKey = bucketTime.toISOString()

      if (!bucketMap.has(bucketKey)) {
        bucketMap.set(bucketKey, {
          bookings_created: 0,
          bookings_confirmed: 0,
          bookings_cancelled: 0,
          bookings_completed: 0,
          total_revenue_cents: 0
        })
      }

      const bucket = bucketMap.get(bucketKey)!
      bucket.bookings_created++

      // Convert total_amount (DECIMAL) to cents
      const amount = booking.total_amount ? Math.round(Number(booking.total_amount) * 100) : 0
      bucket.total_revenue_cents += amount

      if (booking.status === 'confirmed') {
        bucket.bookings_confirmed++
      }
      if (booking.status === 'cancelled') {
        bucket.bookings_cancelled++
      }
      if (booking.status === 'completed') {
        bucket.bookings_completed++
      }
    }

    // Convert to BookingMetrics array
    const bookingMetrics: BookingMetrics[] = Array.from(bucketMap.entries())
      .map(([timestamp, data]) => ({
        timestamp,
        bookings_created: data.bookings_created,
        bookings_confirmed: data.bookings_confirmed,
        bookings_cancelled: data.bookings_cancelled,
        bookings_completed: data.bookings_completed,
        total_revenue_cents: data.total_revenue_cents,
        avg_booking_value_cents: data.bookings_created > 0
          ? data.total_revenue_cents / data.bookings_created
          : 0
      }))
      .sort((a, b) => a.timestamp.localeCompare(b.timestamp))

    // Fetch previous period if requested
    let previousMetrics: BookingMetrics[] | undefined
    if (compareStartTime && compareWith) {
      const { data: prevBookings } = await supabase
        .from('bookings')
        .select('id, status, created_at, updated_at, total_amount')
        .gte('created_at', compareStartTime.toISOString())
        .lt('created_at', startTime.toISOString())
        .order('created_at', { ascending: true })

      const prevBucketMap = new Map<string, {
        bookings_created: number
        bookings_confirmed: number
        bookings_cancelled: number
        bookings_completed: number
        total_revenue_cents: number
      }>()

      for (const booking of prevBookings || []) {
        const bucketTime = new Date(booking.created_at)
        bucketTime.setMinutes(Math.floor(bucketTime.getMinutes() / 5) * 5, 0, 0)
        const bucketKey = bucketTime.toISOString()

        if (!prevBucketMap.has(bucketKey)) {
          prevBucketMap.set(bucketKey, {
            bookings_created: 0,
            bookings_confirmed: 0,
            bookings_cancelled: 0,
            bookings_completed: 0,
            total_revenue_cents: 0
          })
        }

        const bucket = prevBucketMap.get(bucketKey)!
        bucket.bookings_created++

        // Convert total_amount (DECIMAL) to cents
        const amount = booking.total_amount ? Math.round(Number(booking.total_amount) * 100) : 0
        bucket.total_revenue_cents += amount

        if (booking.status === 'confirmed') {
          bucket.bookings_confirmed++
        }
        if (booking.status === 'cancelled') {
          bucket.bookings_cancelled++
        }
        if (booking.status === 'completed') {
          bucket.bookings_completed++
        }
      }

      previousMetrics = Array.from(prevBucketMap.entries())
        .map(([timestamp, data]) => ({
          timestamp,
          bookings_created: data.bookings_created,
          bookings_confirmed: data.bookings_confirmed,
          bookings_cancelled: data.bookings_cancelled,
          bookings_completed: data.bookings_completed,
          total_revenue_cents: data.total_revenue_cents,
          avg_booking_value_cents: data.bookings_created > 0
            ? data.total_revenue_cents / data.bookings_created
            : 0
        }))
        .sort((a, b) => a.timestamp.localeCompare(b.timestamp))
    }

    // Analyze with MetricsAI
    const analysis = MetricsAI.analyzeBookingMetrics(bookingMetrics, previousMetrics)

    return NextResponse.json({
      success: true,
      analysis,
      raw_metrics: bookingMetrics,
      time_range: {
        start: startTime.toISOString(),
        end: now.toISOString(),
        duration: timeRange
      },
      comparison: compareWith ? {
        enabled: true,
        previous_range: compareStartTime ? {
          start: compareStartTime.toISOString(),
          end: startTime.toISOString()
        } : null
      } : { enabled: false }
    })

  } catch (error) {
    console.error('Booking metrics endpoint error:', error)
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    )
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { createClient } from '@supabase/supabase-js'
import { getOpsMode } from '../../_config'
import {
  fetchSimcitySnapshot,
  simcityToLatencyMetrics
} from '../../_simcity/ops-from-simcity'
import { MetricsAI, P95Metrics } from '@/lib/metrics/metricsAI'

/**
 * GET /ops/metrics/p95
 * 
 * Returns P95 latency metrics analysis
 * MetricsAI analyzes trends and detects anomalies
 */
export async function GET(request: NextRequest) {
  if (getOpsMode() === 'simcity') {
    try {
      const { metrics } = await fetchSimcitySnapshot(request.nextUrl.origin)
      return NextResponse.json(simcityToLatencyMetrics(metrics))
    } catch (error) {
      return NextResponse.json(
        {
          error: 'Failed to load SimCity latency metrics',
          message: error instanceof Error ? error.message : 'Unknown error'
        },
        { status: 503 }
      )
    }
  }

  try {
    // Create Supabase client inside handler with safety checks
    const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL
    const supabaseKey = process.env.SUPABASE_SERVICE_ROLE_KEY

    if (!supabaseUrl || !supabaseKey) {
      return NextResponse.json(
        {
          agent: 'MetricsAI',
          error: 'Supabase not configured',
          message: 'Database connection unavailable',
          timestamp: new Date().toISOString()
        },
        { status: 503 }
      )
    }

    const supabase = createClient(supabaseUrl, supabaseKey)

    const { searchParams } = new URL(request.url)
    const timeRange = searchParams.get('timeRange') || '1h'
    const endpoint = searchParams.get('endpoint') // Optional: filter by endpoint
    const compareWith = searchParams.get('compareWith') // Optional: previous time range for comparison

    // Validate timeRange parameter
    const validTimeRanges = ['15m', '1h', '6h', '24h']
    if (!validTimeRanges.includes(timeRange)) {
      return NextResponse.json(
        { error: 'Invalid timeRange parameter', validValues: validTimeRanges },
        { status: 400 }
      )
    }

    // Calculate time range
    const now = new Date()
    let startTime: Date
    let compareStartTime: Date | null = null

    switch (timeRange) {
      case '15m':
        startTime = new Date(now.getTime() - 15 * 60 * 1000)
        break
      case '1h':
        startTime = new Date(now.getTime() - 60 * 60 * 1000)
        break
      case '6h':
        startTime = new Date(now.getTime() - 6 * 60 * 60 * 1000)
        break
      case '24h':
        startTime = new Date(now.getTime() - 24 * 60 * 60 * 1000)
        break
      default:
        startTime = new Date(now.getTime() - 60 * 60 * 1000)
    }

    // If compareWith is provided, calculate previous period
    if (compareWith) {
      const duration = now.getTime() - startTime.getTime()
      compareStartTime = new Date(startTime.getTime() - duration)
    }

    // Fetch P95 metrics from api_metrics_5m materialized view
    let query = supabase
      .from('api_metrics_5m')
      .select('bucket, endpoint, method, p95_ms, p99_ms, reqs')
      .gte('bucket', startTime.toISOString())
      .order('bucket', { ascending: true })

    if (endpoint) {
      query = query.eq('endpoint', endpoint)
    }

    const { data: apiMetrics, error: apiError } = await query

    if (apiError) {
      console.error('Failed to fetch API metrics:', apiError)
      // Fallback to performance_analytics_5min if api_metrics_5m is not available
      let fallbackQuery = supabase
        .from('performance_analytics_5min')
        .select('five_minute_bucket, endpoint, method, p95_response_time_ms, request_count')
        .gte('five_minute_bucket', startTime.toISOString())
        .order('five_minute_bucket', { ascending: true })

      if (endpoint) {
        fallbackQuery = fallbackQuery.eq('endpoint', endpoint)
      }

      const { data: fallbackData, error: fallbackError } = await fallbackQuery

      if (fallbackError) {
        return NextResponse.json(
          {
            agent: 'MetricsAI',
            error: 'Failed to fetch P95 metrics',
            message: fallbackError.message || 'Database query failed',
            timestamp: new Date().toISOString()
          },
          { status: 500 }
        )
      }

      // Transform fallback data
      const p95Metrics: P95Metrics[] = (fallbackData || []).map((item: any) => ({
        timestamp: item.five_minute_bucket,
        endpoint: item.endpoint || 'unknown',
        method: item.method || 'GET',
        p95_latency_ms: item.p95_response_time_ms || 0,
        p99_latency_ms: item.p95_response_time_ms ? item.p95_response_time_ms * 1.2 : 0, // Estimate
        avg_latency_ms: item.avg_response_time_ms || 0,
        request_count: item.request_count || 0
      }))

      const analysis = MetricsAI.analyzeP95Metrics(p95Metrics)

      return NextResponse.json({
        success: true,
        analysis,
        raw_metrics: p95Metrics,
        time_range: {
          start: startTime.toISOString(),
          end: now.toISOString(),
          duration: timeRange
        },
        source: 'fallback'
      })
    }

    // Transform to P95Metrics format
    const p95Metrics: P95Metrics[] = (apiMetrics || []).map((item: any) => ({
      timestamp: item.bucket,
      endpoint: item.endpoint || 'unknown',
      method: item.method || 'GET',
      p95_latency_ms: item.p95_ms || 0,
      p99_latency_ms: item.p99_ms || 0,
      avg_latency_ms: 0, // Not available in api_metrics_5m
      request_count: item.reqs || 0
    }))

    // Fetch previous period if requested
    let previousMetrics: P95Metrics[] | undefined
    if (compareStartTime && compareWith) {
      let prevQuery = supabase
        .from('api_metrics_5m')
        .select('bucket, endpoint, method, p95_ms, p99_ms, reqs')
        .gte('bucket', compareStartTime.toISOString())
        .lt('bucket', startTime.toISOString())
        .order('bucket', { ascending: true })

      if (endpoint) {
        prevQuery = prevQuery.eq('endpoint', endpoint)
      }

      const { data: prevData } = await prevQuery

      previousMetrics = (prevData || []).map((item: any) => ({
        timestamp: item.bucket,
        endpoint: item.endpoint || 'unknown',
        method: item.method || 'GET',
        p95_latency_ms: item.p95_ms || 0,
        p99_latency_ms: item.p99_ms || 0,
        avg_latency_ms: 0,
        request_count: item.reqs || 0
      }))
    }

    // Analyze with MetricsAI
    const analysis = MetricsAI.analyzeP95Metrics(p95Metrics, previousMetrics)

    return NextResponse.json({
      success: true,
      analysis,
      raw_metrics: p95Metrics,
      time_range: {
        start: startTime.toISOString(),
        end: now.toISOString(),
        duration: timeRange
      },
      comparison: compareWith ? {
        enabled: true,
        previous_range: compareStartTime ? {
          start: compareStartTime.toISOString(),
          end: startTime.toISOString()
        } : null
      } : { enabled: false }
    })

  } catch (error) {
    console.error('P95 metrics endpoint error:', error)
    return NextResponse.json(
      {
        agent: 'MetricsAI',
        error: 'Internal server error',
        message: error instanceof Error ? error.message : 'Unknown error',
        timestamp: new Date().toISOString()
      },
      { status: 500 }
    )
  }
}

import { NextRequest, NextResponse } from 'next/server'

export async function POST(request: NextRequest) {
  // Resolve base URL: prefer env vars, fall back to request origin for local dev
  const OPS_API_BASE =
    process.env.OPS_API_BASE ||
    process.env.NEXT_PUBLIC_OPS_BASE ||
    process.env.NEXT_PUBLIC_SITE_URL ||
    request.nextUrl.origin ||
    null

  if (!OPS_API_BASE) {
    return NextResponse.json(
      { error: 'OPS_API_BASE not configured and cannot determine base URL' },
      { status: 503 }
    )
  }

  const body = await request.json().catch(() => ({}))
  const target = `${OPS_API_BASE.replace(/\/$/, '')}/ops/metrics/query`

  try {
    const res = await fetch(target, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(body),
      cache: 'no-store'
    })
    const raw = await res.text()
    let data: any
    try {
      data = JSON.parse(raw)
    } catch {
      data = raw
    }
    return NextResponse.json(data, { status: res.status })
  } catch (error) {
    return NextResponse.json(
      { 
        error: 'Failed to fetch from Ops Fabric',
        message: error instanceof Error ? error.message : 'Unknown error',
        target
      },
      { status: 503 }
    )
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { createClient } from '@supabase/supabase-js'
import { RegressionAI } from '@/lib/regression/regressionAI'
import { loadBaselines } from '../../../../../../scripts/ops-baseline-store'
import type { P95Metrics, BookingMetrics, ErrorMetrics, SystemMetrics } from '@/lib/metrics/metricsAI'

/**
 * GET /ops/metrics/regression
 * 
 * RegressionAI endpoint - compares current metrics to historical baselines
 * Detects P95 degradations, booking flow slowdowns, and error bumps after deploys
 */
export async function GET(request: NextRequest) {
  try {
    // Create Supabase client inside handler with safety checks
    const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL
    const supabaseKey = process.env.SUPABASE_SERVICE_ROLE_KEY

    if (!supabaseUrl || !supabaseKey) {
      return NextResponse.json(
        {
          agent: 'RegressionAI',
          error: 'Supabase not configured',
          message: 'Database connection unavailable',
          timestamp: new Date().toISOString()
        },
        { status: 503 }
      )
    }

    const supabase = createClient(supabaseUrl, supabaseKey)

    const { searchParams } = new URL(request.url)
    const timeRange = searchParams.get('timeRange') || '1h'
    const endpoint = searchParams.get('endpoint') // Optional: filter by endpoint
    const metricType = searchParams.get('metricType') // Optional: 'p95' | 'booking' | 'error' | 'system'

    // Validate timeRange parameter
    const validTimeRanges = ['15m', '1h', '6h', '24h']
    if (!validTimeRanges.includes(timeRange)) {
      return NextResponse.json(
        { success: false, error: 'Invalid timeRange parameter', validValues: validTimeRanges },
        { status: 400 }
      )
    }

    // Validate metricType if provided
    if (metricType) {
      const validMetricTypes = ['p95', 'booking', 'error', 'system']
      if (!validMetricTypes.includes(metricType)) {
        return NextResponse.json(
          { success: false, error: 'Invalid metricType parameter', validValues: validMetricTypes },
          { status: 400 }
        )
      }
    }

    // Calculate time range
    const now = new Date()
    let startTime: Date

    switch (timeRange) {
      case '15m':
        startTime = new Date(now.getTime() - 15 * 60 * 1000)
        break
      case '1h':
        startTime = new Date(now.getTime() - 60 * 60 * 1000)
        break
      case '6h':
        startTime = new Date(now.getTime() - 6 * 60 * 60 * 1000)
        break
      case '24h':
        startTime = new Date(now.getTime() - 24 * 60 * 60 * 1000)
        break
      default:
        startTime = new Date(now.getTime() - 60 * 60 * 1000)
    }

    // Load baselines
    const baselines = loadBaselines()

    if (baselines.length === 0) {
      return NextResponse.json({
        success: false,
        error: 'No baselines available. Please establish baselines first.',
        recommendation: 'Use POST /api/ops/baseline to create baselines from historical data'
      }, { status: 400 })
    }

    // Fetch current metrics
    const currentMetrics: {
      p95?: P95Metrics[]
      booking?: BookingMetrics[]
      error?: ErrorMetrics[]
      system?: SystemMetrics[]
    } = {}

    // Fetch P95 metrics if requested
    if (!metricType || metricType === 'p95') {
      let p95Query = supabase
        .from('api_metrics_5m')
        .select('bucket, endpoint, method, p95_ms, p99_ms, reqs')
        .gte('bucket', startTime.toISOString())
        .order('bucket', { ascending: true })

      if (endpoint) {
        p95Query = p95Query.eq('endpoint', endpoint)
      }

      const { data: apiMetrics, error: p95Error } = await Promise.race([
        p95Query,
        new Promise<{ data: null; error: { message: string } }>((_, reject) =>
          setTimeout(() => reject(new Error('P95 metrics query timeout')), 30000)
        )
      ]).catch((error) => {
        console.warn('RegressionAI: P95 query timeout or error:', error)
        return { data: null, error: { message: error instanceof Error ? error.message : 'Query timeout' } }
      }) as { data: any; error: any }

      if (p95Error && !apiMetrics) {
        // Continue with fallback, don't fail completely
        console.warn('RegressionAI: P95 query failed, trying fallback:', p95Error)
      }

      if (apiMetrics && apiMetrics.length > 0) {
        currentMetrics.p95 = apiMetrics.map((item: any) => ({
          timestamp: item.bucket,
          endpoint: item.endpoint || 'unknown',
          method: item.method || 'GET',
          p95_latency_ms: item.p95_ms || 0,
          p99_latency_ms: item.p99_ms || 0,
          avg_latency_ms: 0,
          request_count: item.reqs || 0
        }))
      } else {
        // Fallback to performance_analytics_5min
        let fallbackQuery = supabase
          .from('performance_analytics_5min')
          .select('five_minute_bucket, endpoint, method, p95_response_time_ms, request_count')
          .gte('five_minute_bucket', startTime.toISOString())
          .order('five_minute_bucket', { ascending: true })

        if (endpoint) {
          fallbackQuery = fallbackQuery.eq('endpoint', endpoint)
        }

        const { data: fallbackData } = await fallbackQuery

        if (fallbackData && fallbackData.length > 0) {
          currentMetrics.p95 = fallbackData.map((item: any) => ({
            timestamp: item.five_minute_bucket,
            endpoint: item.endpoint || 'unknown',
            method: item.method || 'GET',
            p95_latency_ms: item.p95_response_time_ms || 0,
            p99_latency_ms: item.p95_response_time_ms ? item.p95_response_time_ms * 1.2 : 0,
            avg_latency_ms: 0,
            request_count: item.request_count || 0
          }))
        }
      }
    }

    // Fetch booking metrics if requested
    if (!metricType || metricType === 'booking') {
      const { data: bookings, error: bookingError } = await Promise.race([
        supabase
          .from('bookings')
          .select('id, status, created_at, confirmed_at, cancelled_at, total_amount_cents, price_cents')
          .gte('created_at', startTime.toISOString())
          .order('created_at', { ascending: true }),
        new Promise<{ data: null; error: { message: string } }>((_, reject) =>
          setTimeout(() => reject(new Error('Booking metrics query timeout')), 30000)
        )
      ]).catch((error) => {
        console.warn('RegressionAI: Booking query timeout or error:', error)
        return { data: null, error: { message: error instanceof Error ? error.message : 'Query timeout' } }
      }) as { data: any; error: any }

      if (bookingError) {
        console.warn('RegressionAI: Booking query failed:', bookingError)
      }

      if (bookings && Array.isArray(bookings) && bookings.length > 0) {
        const bucketMap = new Map<string, {
          bookings_created: number
          bookings_confirmed: number
          bookings_cancelled: number
          bookings_completed: number
          total_revenue_cents: number
        }>()

        for (const booking of bookings) {
          const bucketTime = new Date(booking.created_at)
          bucketTime.setMinutes(Math.floor(bucketTime.getMinutes() / 5) * 5, 0, 0)
          const bucketKey = bucketTime.toISOString()

          if (!bucketMap.has(bucketKey)) {
            bucketMap.set(bucketKey, {
              bookings_created: 0,
              bookings_confirmed: 0,
              bookings_cancelled: 0,
              bookings_completed: 0,
              total_revenue_cents: 0
            })
          }

          const bucket = bucketMap.get(bucketKey)!
          bucket.bookings_created++

          const amount = booking.total_amount_cents || booking.price_cents || 0
          bucket.total_revenue_cents += amount

          if (booking.status === 'confirmed' || booking.confirmed_at) {
            bucket.bookings_confirmed++
          }
          if (booking.status === 'cancelled') {
            bucket.bookings_cancelled++
          }
          if (booking.status === 'completed') {
            bucket.bookings_completed++
          }
        }

        currentMetrics.booking = Array.from(bucketMap.entries())
          .map(([timestamp, data]) => ({
            timestamp,
            bookings_created: data.bookings_created,
            bookings_confirmed: data.bookings_confirmed,
            bookings_cancelled: data.bookings_cancelled,
            bookings_completed: data.bookings_completed,
            total_revenue_cents: data.total_revenue_cents,
            avg_booking_value_cents: data.bookings_created > 0
              ? data.total_revenue_cents / data.bookings_created
              : 0
          }))
          .sort((a, b) => a.timestamp.localeCompare(b.timestamp))
      }
    }

    // Fetch error metrics if requested
    if (!metricType || metricType === 'error') {
      let errorQuery = supabase
        .from('performance_metrics')
        .select('endpoint, method, status_code, created_at')
        .gte('created_at', startTime.toISOString())
        .gte('status_code', 400)
        .order('created_at', { ascending: true })

      if (endpoint) {
        errorQuery = errorQuery.eq('endpoint', endpoint)
      }

      const { data: errors, error: errorQueryError } = await Promise.race([
        errorQuery,
        new Promise<{ data: null; error: { message: string } }>((_, reject) =>
          setTimeout(() => reject(new Error('Error metrics query timeout')), 30000)
        )
      ]).catch((error) => {
        console.warn('RegressionAI: Error query timeout or error:', error)
        return { data: null, error: { message: error instanceof Error ? error.message : 'Query timeout' } }
      }) as { data: any; error: any }

      const { count: totalRequests } = await Promise.race([
        supabase
          .from('performance_metrics')
          .select('*', { count: 'exact', head: true })
          .gte('created_at', startTime.toISOString()),
        new Promise<{ count: null }>((_, reject) =>
          setTimeout(() => reject(new Error('Total requests query timeout')), 30000)
        )
      ]).catch((error) => {
        console.warn('RegressionAI: Total requests query timeout or error:', error)
        return { count: null }
      }) as { count: number | null }

      if (errorQueryError) {
        console.warn('RegressionAI: Error query failed:', errorQueryError)
      }

      if (errors && Array.isArray(errors) && errors.length > 0) {
        const bucketMap = new Map<string, {
          total_errors: number
          status_4xx: number
          status_5xx: number
          endpoint_errors: Map<string, { count: number; method: string }>
        }>()

        for (const error of errors) {
          const bucketTime = new Date(error.created_at)
          bucketTime.setMinutes(Math.floor(bucketTime.getMinutes() / 5) * 5, 0, 0)
          const bucketKey = bucketTime.toISOString()

          if (!bucketMap.has(bucketKey)) {
            bucketMap.set(bucketKey, {
              total_errors: 0,
              status_4xx: 0,
              status_5xx: 0,
              endpoint_errors: new Map()
            })
          }

          const bucket = bucketMap.get(bucketKey)!
          bucket.total_errors++

          if (error.status_code >= 400 && error.status_code < 500) {
            bucket.status_4xx++
          } else if (error.status_code >= 500) {
            bucket.status_5xx++
          }

          const endpointKey = `${error.method} ${error.endpoint}`
          if (!bucket.endpoint_errors.has(endpointKey)) {
            bucket.endpoint_errors.set(endpointKey, { count: 0, method: error.method || 'GET' })
          }
          bucket.endpoint_errors.get(endpointKey)!.count++
        }

        currentMetrics.error = Array.from(bucketMap.entries())
          .map(([timestamp, data]) => {
            const errorRate = totalRequests && totalRequests > 0
              ? (data.total_errors / totalRequests) * 100
              : 0

            const topErrorEndpoints = Array.from(data.endpoint_errors.entries())
              .map(([endpointKey, info]) => {
                const [method, ...endpointParts] = endpointKey.split(' ')
                const endpoint = endpointParts.join(' ')
                return {
                  endpoint,
                  method: info.method,
                  error_count: info.count,
                  error_rate_percent: totalRequests && totalRequests > 0
                    ? (info.count / totalRequests) * 100
                    : 0
                }
              })
              .sort((a, b) => b.error_count - a.error_count)
              .slice(0, 10)

            return {
              timestamp,
              total_errors: data.total_errors,
              error_rate_percent: errorRate,
              status_4xx: data.status_4xx,
              status_5xx: data.status_5xx,
              top_error_endpoints: topErrorEndpoints
            }
          })
          .sort((a, b) => a.timestamp.localeCompare(b.timestamp))
      }
    }

    // Fetch system metrics if requested
    if (!metricType || metricType === 'system') {
      const { data: performanceData, error: systemError } = await Promise.race([
        supabase
          .from('performance_analytics_5min')
          .select('five_minute_bucket, cache_hit_rate_percent, avg_database_queries, request_count')
          .gte('five_minute_bucket', startTime.toISOString())
          .order('five_minute_bucket', { ascending: true }),
        new Promise<{ data: null; error: { message: string } }>((_, reject) =>
          setTimeout(() => reject(new Error('System metrics query timeout')), 30000)
        )
      ]).catch((error) => {
        console.warn('RegressionAI: System query timeout or error:', error)
        return { data: null, error: { message: error instanceof Error ? error.message : 'Query timeout' } }
      }) as { data: any; error: any }

      if (systemError) {
        console.warn('RegressionAI: System query failed:', systemError)
      }

      if (performanceData && performanceData.length > 0) {
        currentMetrics.system = performanceData.map((item: any) => {
          const estimatedCpu = Math.min(100,
            (item.request_count || 0) * 0.1 +
            (item.avg_database_queries || 0) * 2
          )
          const estimatedMemory = Math.min(100,
            30 + (item.request_count || 0) * 0.05
          )

          return {
            timestamp: item.five_minute_bucket,
            cpu_percent: estimatedCpu,
            memory_percent: estimatedMemory,
            cache_hit_rate: item.cache_hit_rate_percent || null,
            active_connections: null,
            database_size_mb: null
          }
        })
      }
    }

    // Run regression detection
    const report = RegressionAI.detectRegressions(currentMetrics, baselines)

    return NextResponse.json({
      success: true,
      report,
      time_range: {
        start: startTime.toISOString(),
        end: now.toISOString(),
        duration: timeRange
      },
      metrics_analyzed: {
        p95: currentMetrics.p95?.length || 0,
        booking: currentMetrics.booking?.length || 0,
        error: currentMetrics.error?.length || 0,
        system: currentMetrics.system?.length || 0
      }
    })

  } catch (error) {
    console.error('Regression detection endpoint error:', error)
    return NextResponse.json(
      {
        agent: 'RegressionAI',
        error: 'Internal server error',
        message: error instanceof Error ? error.message : 'Unknown error',
        timestamp: new Date().toISOString()
      },
      { status: 500 }
    )
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { createClient } from '@supabase/supabase-js'
import { getOpsMode } from '../../_config'
import {
  fetchSimcitySnapshot,
  simcityToSystemMetrics
} from '../../_simcity/ops-from-simcity'
import { MetricsAI, SystemMetrics } from '@/lib/metrics/metricsAI'

/**
 * GET /ops/metrics/system
 * 
 * Returns system-level metrics analysis (CPU/memory, database, cache)
 * MetricsAI analyzes trends and detects anomalies
 */
export async function GET(request: NextRequest) {
  if (getOpsMode() === 'simcity') {
    try {
      const { metrics } = await fetchSimcitySnapshot(request.nextUrl.origin)
      return NextResponse.json(simcityToSystemMetrics(metrics))
    } catch (error) {
      return NextResponse.json(
        {
          error: 'Failed to load SimCity system metrics',
          message: error instanceof Error ? error.message : 'Unknown error'
        },
        { status: 503 }
      )
    }
  }

  try {
    // Create Supabase client inside handler with safety checks
    const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL
    const supabaseKey = process.env.SUPABASE_SERVICE_ROLE_KEY

    if (!supabaseUrl || !supabaseKey) {
      return NextResponse.json(
        {
          agent: 'MetricsAI',
          error: 'Supabase not configured',
          message: 'Database connection unavailable',
          timestamp: new Date().toISOString()
        },
        { status: 503 }
      )
    }

    const supabase = createClient(supabaseUrl, supabaseKey)

    const { searchParams } = new URL(request.url)
    const timeRange = searchParams.get('timeRange') || '1h'
    const compareWith = searchParams.get('compareWith') // Optional: previous time range for comparison

    // Validate timeRange parameter
    const validTimeRanges = ['15m', '1h', '6h', '24h']
    if (!validTimeRanges.includes(timeRange)) {
      return NextResponse.json(
        { error: 'Invalid timeRange parameter', validValues: validTimeRanges },
        { status: 400 }
      )
    }

    // Calculate time range
    const now = new Date()
    let startTime: Date
    let compareStartTime: Date | null = null

    switch (timeRange) {
      case '15m':
        startTime = new Date(now.getTime() - 15 * 60 * 1000)
        break
      case '1h':
        startTime = new Date(now.getTime() - 60 * 60 * 1000)
        break
      case '6h':
        startTime = new Date(now.getTime() - 6 * 60 * 60 * 1000)
        break
      case '24h':
        startTime = new Date(now.getTime() - 24 * 60 * 60 * 1000)
        break
      default:
        startTime = new Date(now.getTime() - 60 * 60 * 1000)
    }

    // If compareWith is provided, calculate previous period
    if (compareWith) {
      const duration = now.getTime() - startTime.getTime()
      compareStartTime = new Date(startTime.getTime() - duration)
    }

    // Fetch system metrics from performance_analytics_5min with timeout
    // We'll derive CPU/memory from database metrics and cache hit rates
    let performanceData: any = null
    let perfError: any = null

    try {
      const queryResult = await Promise.race([
        supabase
          .from('performance_analytics_5min')
          .select('five_minute_bucket, cache_hit_rate_percent, avg_database_queries, request_count')
          .gte('five_minute_bucket', startTime.toISOString())
          .order('five_minute_bucket', { ascending: true }),
        new Promise<{ data: null; error: { message: string } }>((_, reject) =>
          setTimeout(() => reject(new Error('Database query timeout')), 30000)
        )
      ])
      performanceData = (queryResult as any).data
      perfError = (queryResult as any).error
    } catch (error) {
      console.error('Metrics system query timeout or error:', error)
      perfError = { message: error instanceof Error ? error.message : 'Query timeout' }
    }

    if (perfError) {
      console.error('Failed to fetch performance data:', perfError)
      return NextResponse.json(
        {
          agent: 'MetricsAI',
          error: 'Failed to fetch system metrics',
          message: perfError.message || 'Database query failed',
          timestamp: new Date().toISOString()
        },
        { status: 500 }
      )
    }

    // Get database connection metrics (if available)
    // For now, we'll estimate system load from request patterns
    // Note: get_database_stats function may not exist - handle gracefully
    let dbMetrics: any = null 
    try {
      const { data } = await supabase.rpc('get_database_stats', {}).single()
      dbMetrics = data
    } catch (err) {
      // Function doesn't exist or not available - that's okay
      dbMetrics = null
    }

    // Transform to SystemMetrics format
    const systemMetrics: SystemMetrics[] = (performanceData || []).map((item: any) => {
      // Estimate CPU from request count and database queries
      // This is a simplified model - in production, you'd have actual system metrics
      const estimatedCpu = Math.min(100, 
        (item.request_count || 0) * 0.1 + 
        (item.avg_database_queries || 0) * 2
      )

      // Estimate memory from request patterns
      const estimatedMemory = Math.min(100,
        30 + (item.request_count || 0) * 0.05
      )

      return {
        timestamp: item.five_minute_bucket,
        cpu_percent: estimatedCpu,
        memory_percent: estimatedMemory,
        cache_hit_rate: item.cache_hit_rate_percent != null ? item.cache_hit_rate_percent : null,
        // Database stats are current snapshots only, not time-series - cannot be applied to historical data points
        active_connections: null,
        database_size_mb: null
      }
    })

    // Fetch previous period if requested
    let previousMetrics: SystemMetrics[] | undefined
    if (compareStartTime && compareWith) {
      const { data: prevData } = await supabase
        .from('performance_analytics_5min')
        .select('five_minute_bucket, cache_hit_rate_percent, avg_database_queries, request_count')
        .gte('five_minute_bucket', compareStartTime.toISOString())
        .lt('five_minute_bucket', startTime.toISOString())
        .order('five_minute_bucket', { ascending: true })

      previousMetrics = (prevData || []).map((item: any) => {
        const estimatedCpu = Math.min(100,
          (item.request_count || 0) * 0.1 +
          (item.avg_database_queries || 0) * 2
        )
        const estimatedMemory = Math.min(100,
          30 + (item.request_count || 0) * 0.05
        )

        return {
          timestamp: item.five_minute_bucket,
          cpu_percent: estimatedCpu,
          memory_percent: estimatedMemory,
          cache_hit_rate: item.cache_hit_rate_percent != null ? item.cache_hit_rate_percent : undefined,
          // Historical database stats are not available - these are current-period only metrics
          active_connections: undefined,
          database_size_mb: undefined
        }
      })
    }

    // Analyze with MetricsAI
    const analysis = MetricsAI.analyzeSystemMetrics(systemMetrics, previousMetrics)

    return NextResponse.json({
      success: true,
      analysis,
      raw_metrics: systemMetrics,
      time_range: {
        start: startTime.toISOString(),
        end: now.toISOString(),
        duration: timeRange
      },
      comparison: compareWith ? {
        enabled: true,
        previous_range: compareStartTime ? {
          start: compareStartTime.toISOString(),
          end: startTime.toISOString()
        } : null
      } : { enabled: false }
    })

  } catch (error) {
    console.error('System metrics endpoint error:', error)
    return NextResponse.json(
      {
        agent: 'MetricsAI',
        error: 'Internal server error',
        message: error instanceof Error ? error.message : 'Unknown error',
        timestamp: new Date().toISOString()
      },
      { status: 500 }
    )
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { createClient } from '@supabase/supabase-js'
import { withSLOProbe } from '@/middleware/sloProbe'
import { featureFlags } from '@/config/featureFlags'

import { supabaseAdmin as supabase } from '@/lib/supabaseProxies';

interface RefundRequest {
  booking_id: string
  reason: 'CUSTOMER_REQUEST' | 'PROVIDER_CANCELLED' | 'SERVICE_ISSUE' | 'ADMIN_OVERRIDE'
  amount_cents?: number // Optional partial refund
  admin_notes?: string
}

const refundHandler = async (req: NextRequest) => {
  try {
    // Check feature flag
    if (!featureFlags.beta.core_booking_flow) {
      return NextResponse.json(
        { error: 'Core booking flow not enabled' },
        { status: 503 }
      )
    }

    const body: RefundRequest = await req.json()
    const { booking_id, reason, amount_cents, admin_notes } = body

    if (!booking_id || !reason) {
      return NextResponse.json(
        { error: 'Missing required fields: booking_id, reason' },
        { status: 400 }
      )
    }

    // Get booking details
    const { data: booking, error: bookingError } = await supabase
      .from('bookings')
      .select('*')
      .eq('id', booking_id)
      .single()

    if (bookingError || !booking) {
      return NextResponse.json(
        { error: 'Booking not found' },
        { status: 404 }
      )
    }

    // Check if booking can be refunded
    if (!['provider_confirmed', 'receipt_issued'].includes(booking.state)) {
      return NextResponse.json(
        { error: `Cannot refund booking in state: ${booking.state}` },
        { status: 400 }
      )
    }

    // Determine refund amount
    const refundAmount = amount_cents || booking.price_cents || 0

    if (refundAmount <= 0 || refundAmount > (booking.price_cents || 0)) {
      return NextResponse.json(
        { error: 'Invalid refund amount' },
        { status: 400 }
      )
    }

    // TODO: Implement actual Stripe refund
    // For now, simulate successful refund
    console.log(`Would refund ${refundAmount} cents for booking ${booking_id}`)

    // Update booking state
    const { error: updateError } = await supabase
      .from('bookings')
      .update({
        state: 'refunded',
        refunded_at: new Date().toISOString(),
        cancelled_reason: reason
      })
      .eq('id', booking_id)

    if (updateError) {
      throw new Error(`Failed to update booking state: ${updateError.message}`)
    }

    // Log the state transition
    await supabase
      .from('booking_audit_log')
      .insert({
        booking_id: booking.id,
        from_state: booking.state,
        to_state: 'refunded',
        action: 'refund',
        actor_type: 'admin',
        actor_id: 'ops_refund_endpoint',
        metadata: {
          reason,
          refund_amount_cents: refundAmount,
          original_price_cents: booking.price_cents,
          admin_notes,
          refunded_at: new Date().toISOString(),
          stripe_payment_intent: booking.stripe_payment_intent_id
        }
      })

    // If this was a partial refund, transition back to provider_confirmed
    if (amount_cents && amount_cents < (booking.price_cents || 0)) {
      await supabase
        .from('bookings')
        .update({
          state: 'provider_confirmed',
          price_cents: (booking.price_cents || 0) - amount_cents
        })
        .eq('id', booking_id)

      await supabase
        .from('booking_audit_log')
        .insert({
          booking_id: booking.id,
          from_state: 'refunded',
          to_state: 'provider_confirmed',
          action: 'partial_refund_adjustment',
          actor_type: 'system',
          actor_id: 'ops_refund_endpoint',
          metadata: {
            partial_refund_amount: amount_cents,
            new_price_cents: (booking.price_cents || 0) - amount_cents,
            adjusted_at: new Date().toISOString()
          }
        })
    }

    console.log(`Refund processed for booking ${booking_id}: ${refundAmount} cents`)

    return NextResponse.json({
      ok: true,
      data: {
        booking_id,
        refund_amount_cents: refundAmount,
        new_state: amount_cents && amount_cents < (booking.price_cents || 0) 
          ? 'provider_confirmed' 
          : 'refunded',
        refunded_at: new Date().toISOString()
      }
    })

  } catch (error) {
    console.error('Error in refund endpoint:', error)
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    )
  }
}

