import { NextRequest, NextResponse } from 'next/server'
import { registerWebhook, WebhookEvent } from '../_registry'

const DEFAULT_EVENTS: WebhookEvent[] = [
  'health.degraded',
  'bookings.anomaly',
  'deployments.new'
]

export async function POST(req: NextRequest) {
  let body: any
  try {
    body = await req.json()
  } catch {
    return NextResponse.json({ error: 'Invalid JSON' }, { status: 400 })
  }

  const url = body?.url
  const events = (Array.isArray(body?.events) ? body.events : DEFAULT_EVENTS).filter(
    (e: string): e is WebhookEvent => DEFAULT_EVENTS.includes(e as WebhookEvent) || e === 'ops.test'
  )

  if (!url || typeof url !== 'string') {
    return NextResponse.json({ error: 'url is required' }, { status: 400 })
  }

  let parsed: URL | null = null
  try {
    parsed = new URL(url)
  } catch {
    return NextResponse.json({ error: 'Invalid URL' }, { status: 400 })
  }

  // Disallow localhost registrations from production, but allow in dev
  if (
    process.env.NODE_ENV === 'production' &&
    ['localhost', '127.0.0.1'].includes(parsed.hostname)
  ) {
    return NextResponse.json({ error: 'Localhost webhooks are not allowed in production' }, { status: 400 })
  }

  const registration = registerWebhook(url, events.length ? events : DEFAULT_EVENTS)

  return NextResponse.json({
    success: true,
    registration,
    message: 'Webhook registered for OpsAI events'
  })
}

import { NextRequest, NextResponse } from 'next/server'
import { samplePayload, WebhookEvent } from '../_registry'

export async function POST(req: NextRequest) {
  let body: any
  try {
    body = await req.json()
  } catch {
    return NextResponse.json({ error: 'Invalid JSON' }, { status: 400 })
  }

  const url = body?.url
  if (!url || typeof url !== 'string') {
    return NextResponse.json({ error: 'url is required' }, { status: 400 })
  }

  const type: WebhookEvent =
    body?.type && typeof body.type === 'string' ? (body.type as WebhookEvent) : 'ops.test'
  const payload = body?.payload || samplePayload(type)

  let delivered = false
  let status: number | undefined
  let error: string | undefined

  try {
    const res = await fetch(url, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(payload)
    })
    status = res.status
    delivered = res.ok
  } catch (err) {
    error = err instanceof Error ? err.message : 'Failed to deliver webhook'
  }

  return NextResponse.json({
    success: true,
    delivered,
    status,
    error,
    payload
  })
}

import { NextResponse } from 'next/server'
import { createServerClient } from '@supabase/ssr'
import { cookies } from 'next/headers'
import { getSupabaseConfig } from '@/config/supabase'

export async function GET() {
  try {
    const cookieStore = await cookies()
    const config = getSupabaseConfig()
    const supabase = createServerClient(
      config.url,
      config.publishableKey,
      {
        cookies: {
          getAll() {
            return cookieStore.getAll()
          },
          setAll(cookiesToSet) {
            try {
              cookiesToSet.forEach(({ name, value, options }) => {
                cookieStore.set(name, value, options)
              })
            } catch (error) {
              // The `setAll` method was called from a Server Component or Route Handler.
              // This can be ignored if you have middleware refreshing user sessions.
            }
          }
        }
      }
    )

    const { data: { user }, error: authError } = await supabase.auth.getUser()
    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    // Check admin via user_role_summary view when available
    const { data: roleSummary } = await supabase
      .from('user_role_summary')
      .select('is_admin')
      .eq('user_id', user.id)
      .maybeSingle()

    if (!roleSummary?.is_admin) {
      return NextResponse.json({ error: 'Forbidden' }, { status: 403 })
    }

    const { data, error } = await supabase
      .from('processed_webhook_events')
      .select('*')
      .order('created_at', { ascending: false })
      .limit(50)

    if (error) {
      if (process.env.NODE_ENV === 'development') {
        console.error('Failed to fetch processed_webhook_events:', error)
      }
      return NextResponse.json({ error: 'Failed to fetch events' }, { status: 500 })
    }

    return NextResponse.json({ events: data ?? [] })
  } catch (error) {
    if (process.env.NODE_ENV === 'development') {
      console.error('processed-events GET error:', error)
    }
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 })
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { getServerSupabase } from '@/lib/supabaseServer';
import { withQueryLogging } from '@/lib/performance/queryLogger';
import { withRateLimit, apiRateLimiter } from '@/middleware/rateLimiter';
import { RichProviderProfile, ProviderProfileUpdate } from '@/types/provider';

export const GET = withRateLimit(apiRateLimiter, async (request: NextRequest) => {
  try {
    const supabase = new Proxy({} as any, { get: (target, prop) => (getServerSupabase() as any)[prop] }) as ReturnType<typeof getServerSupabase>;

    // Check authentication
    const { data: { user }, error: authError } = await supabase.auth.getUser();
    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    // Get provider ID from query params or use current user
    const { searchParams } = new URL(request.url);
    const providerId = searchParams.get('id') || user.id;

    // Get provider profile with all related data
    const profile = await withQueryLogging(
      async () => {
        // Get basic profile data
        const { data: profileData, error: profileError } = await supabase
          .from('provider_profile_summary')
          .select('*')
          .eq('id', providerId)
          .single();

        if (profileError) throw profileError;

        // Get detailed related data
        const [
          { data: certifications },
          { data: education },
          { data: portfolio },
          { data: languages }
        ] = await Promise.all([
          supabase
            .from('provider_certifications')
            .select('*')
            .eq('provider_id', providerId)
            .order('created_at', { ascending: false }),

          supabase
            .from('provider_education')
            .select('*')
            .eq('provider_id', providerId)
            .order('end_year', { ascending: false }),

          supabase
            .from('provider_portfolio')
            .select('*')
            .eq('provider_id', providerId)
            .order('display_order', { ascending: true }),

          supabase
            .from('provider_languages')
            .select('*')
            .eq('provider_id', providerId)
            .order('is_primary', { ascending: false })
        ]);

        return {
          ...profileData,
          certifications: certifications || [],
          education: education || [],
          portfolio: portfolio || [],
          provider_languages: languages || []
        } as RichProviderProfile;
      },
      {
        query: 'get_provider_profile_with_relations',
        table: 'provider_profile_summary',
        operation: 'SELECT',
        userId: user.id,
        endpoint: '/api/provider/profile'
      }
    );

    return NextResponse.json({
      success: true,
      data: profile
    });

  } catch (error) {
    console.error('Provider profile fetch error:', error);
    return NextResponse.json(
      { error: 'Failed to fetch provider profile' },
      { status: 500 }
    );
  }
});

export const PUT = withRateLimit(apiRateLimiter, async (request: NextRequest) => {
  try {
    const supabase = new Proxy({} as any, { get: (target, prop) => (getServerSupabase() as any)[prop] }) as ReturnType<typeof getServerSupabase>;

    // Check authentication
    const { data: { user }, error: authError } = await supabase.auth.getUser();
    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    // Verify user is a provider
    const { data: userProfile } = await supabase
      .from('users')
      .select('role')
      .eq('id', user.id)
      .single();

    if (userProfile?.role !== 'provider') {
      return NextResponse.json({ error: 'Only providers can update profiles' }, { status: 403 });
    }

    const body = await request.json();
    const updateData: ProviderProfileUpdate = body;

    // Update provider profile
    const updatedProfile = await withQueryLogging(
      async () => {
        const { data, error } = await supabase
          .from('users')
          .update({
            bio: updateData.bio,
            portfolio_images: updateData.portfolio_images,
            specializations: updateData.specializations,
            experience_years: updateData.experience_years,
            hourly_rate: updateData.hourly_rate,
            availability_schedule: updateData.availability_schedule,
            professional_summary: updateData.professional_summary,
            service_area_radius: updateData.service_area_radius,
            social_links: updateData.social_links,
            updated_at: new Date().toISOString()
          })
          .eq('id', user.id)
          .select()
          .single();

        if (error) throw error;
        return data;
      },
      {
        query: 'update_provider_profile',
        table: 'users',
        operation: 'UPDATE',
        userId: user.id,
        endpoint: '/api/provider/profile'
      }
    );

    return NextResponse.json({
      success: true,
      data: updatedProfile
    });

  } catch (error) {
    console.error('Provider profile update error:', error);
    return NextResponse.json(
      { error: 'Failed to update provider profile' },
      { status: 500 }
    );
  }
});

export const PATCH = withRateLimit(apiRateLimiter, async (request: NextRequest) => {
  try {
    const supabase = new Proxy({} as any, { get: (target, prop) => (getServerSupabase() as any)[prop] }) as ReturnType<typeof getServerSupabase>;

    // Check authentication
    const { data: { user }, error: authError } = await supabase.auth.getUser();
    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const body = await request.json();
    const { action, data: actionData } = body;

    switch (action) {
      case 'add_certification':
        const { data: newCert, error: certError } = await supabase
          .from('provider_certifications')
          .insert({
            provider_id: user.id,
            ...actionData
          })
          .select()
          .single();

        if (certError) throw certError;
        return NextResponse.json({ success: true, data: newCert });

      case 'add_education':
        const { data: newEdu, error: eduError } = await supabase
          .from('provider_education')
          .insert({
            provider_id: user.id,
            ...actionData
          })
          .select()
          .single();

        if (eduError) throw eduError;
        return NextResponse.json({ success: true, data: newEdu });

      case 'add_portfolio':
        const { data: newPortfolio, error: portfolioError } = await supabase
          .from('provider_portfolio')
          .insert({
            provider_id: user.id,
            ...actionData
          })
          .select()
          .single();

        if (portfolioError) throw portfolioError;
        return NextResponse.json({ success: true, data: newPortfolio });

      case 'add_language':
        const { data: newLang, error: langError } = await supabase
          .from('provider_languages')
          .insert({
