import { NextRequest, NextResponse } from 'next/server'
import { createServerClient } from '@supabase/ssr'
import { cookies } from 'next/headers'
import { getSupabaseConfig } from '@/config/supabase'
import { detectLocaleFromHeaders } from '@/lib/i18n/config'
import { t } from '@/lib/i18n/server'
import { isHalfStarRating, isValidRatingComment } from '@/lib/ratings/validation'

interface RatingRequest {
  booking_id: string
  stars: number
  comment?: string
}

function mapProfileRole(role: string): 'customer' | 'provider' | null {
  if (role === 'customer') return 'customer'
  if (role === 'vendor') return 'provider'
  return null
}

export async function POST(request: NextRequest) {
  const cookieStore = await cookies()
  const config = getSupabaseConfig()
  const supabase = createServerClient(config.url, config.publishableKey, {
    cookies: {
      getAll() {
        return cookieStore.getAll()
      },
      setAll(cookiesToSet) {
        try {
          cookiesToSet.forEach(({ name, value, options }) => {
            cookieStore.set(name, value, options)
          })
        } catch (error) {
          // The `setAll` method was called from a Server Component or Route Handler.
          // This can be ignored if you have middleware refreshing user sessions.
        }
      }
    }
  })
  const locale = detectLocaleFromHeaders(request.headers.get('accept-language') ?? undefined).code

  try {
    const { data: { user }, error: authError } = await supabase.auth.getUser()
    if (authError || !user) {
      return NextResponse.json({ error: t(locale, 'error.auth_required') }, { status: 401 })
    }

    const body: RatingRequest = await request.json()
    const bookingId = body.booking_id
    const stars = body.stars
    const comment = body.comment?.trim() || null

    if (!bookingId || stars === undefined) {
      return NextResponse.json({ error: t(locale, 'error.missing_fields') }, { status: 400 })
    }

    if (!isHalfStarRating(stars)) {
      return NextResponse.json({ error: t(locale, 'error.rating_invalid') }, { status: 400 })
    }

    if (!isValidRatingComment(comment)) {
      return NextResponse.json({ error: t(locale, 'error.rating_comment_too_long') }, { status: 400 })
    }

    const { data: profile, error: profileError } = await supabase
      .from('profiles')
      .select('id, role')
      .eq('auth_user_id', user.id)
      .single()

    if (profileError || !profile) {
      return NextResponse.json({ error: t(locale, 'error.profile_not_found') }, { status: 404 })
    }

    const mappedRole = mapProfileRole(profile.role)
    if (!mappedRole) {
      return NextResponse.json({ error: t(locale, 'error.rating_not_allowed') }, { status: 403 })
    }

    const { data: booking, error: bookingError } = await supabase
      .from('bookings')
      .select('*')
      .eq('id', bookingId)
      .single()

    if (bookingError || !booking) {
      return NextResponse.json({ error: t(locale, 'error.booking_not_found') }, { status: 404 })
    }

    if (!['confirmed', 'completed'].includes(booking.status)) {
      return NextResponse.json({ error: t(locale, 'error.rating_not_allowed') }, { status: 403 })
    }

    const vendorId = (booking as { vendor_id?: string; provider_id?: string }).vendor_id
      ?? (booking as { vendor_id?: string; provider_id?: string }).provider_id

    let raterRole: 'customer' | 'provider' | null = null
    let rateeRole: 'customer' | 'provider' | null = null
    let rateeId: string | null = null

    if (booking.customer_id === profile.id) {
      raterRole = 'customer'
      rateeRole = 'provider'
      rateeId = vendorId || null
    } else if (vendorId === profile.id) {
      raterRole = 'provider'
      rateeRole = 'customer'
      rateeId = booking.customer_id
    }

    if (!raterRole || !rateeRole || !rateeId) {
      return NextResponse.json({ error: t(locale, 'error.rating_not_allowed') }, { status: 403 })
    }

    const { data: existingRating } = await supabase
      .from('ratings')
      .select('id')
      .eq('booking_id', bookingId)
      .eq('rater_role', raterRole)
      .maybeSingle()

    if (existingRating?.id) {
      return NextResponse.json({ error: t(locale, 'error.rating_duplicate') }, { status: 409 })
    }

    const { data: rating, error: ratingError } = await supabase
      .from('ratings')
      .insert({
        booking_id: bookingId,
        rater_user_id: profile.id,
        ratee_user_id: rateeId,
        rater_role: raterRole,
        ratee_role: rateeRole,
        stars,
        comment
      })
      .select('id, booking_id, stars, comment, created_at')
      .single()

    if (ratingError || !rating) {
      return NextResponse.json({ error: t(locale, 'error.rating_create_failed') }, { status: 500 })
    }

    await supabase.from('analytics_events').insert({
      event_type: 'rating.submitted',
      user_id: profile.id,
      event_data: {
        booking_id: bookingId,
        stars,
        rater_role: raterRole
      },
      created_at: new Date().toISOString()
    })

    return NextResponse.json({
      success: true,
      rating
    })
  } catch (error) {
    console.error('Rating creation error:', error)
    return NextResponse.json({ error: t(locale, 'error.rating_create_failed') }, { status: 500 })
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { createClient } from '@supabase/supabase-js'
import { reviewMonitoring } from '@/lib/reviewMonitoring'

// Initialize Supabase client
import { supabaseAdmin as supabase } from '@/lib/supabaseProxies';

export async function POST(request: NextRequest) {
  try {
    const body = await request.json()
    const {
      booking_id,
      reviewer_id,
      provider_id,
      rating,
      comment,
      service_quality,
      communication,
      punctuality,
      value_for_money,
      would_recommend,
      photos = []
    } = body

    // Validate required fields
    if (!booking_id || !reviewer_id || !provider_id || !rating) {
      return NextResponse.json(
        { error: 'Missing required fields' },
        { status: 400 }
      )
    }

    // Validate rating range (1.0 to 5.0 with half-star support)
    if (rating < 1.0 || rating > 5.0 || (rating * 2) % 1 !== 0) {
      return NextResponse.json(
        { error: 'Rating must be between 1.0 and 5.0 with half-star increments' },
        { status: 400 }
      )
    }

    // Validate detailed ratings if provided
    const detailedRatings = [service_quality, communication, punctuality, value_for_money]
    const hasDetailedRatings = detailedRatings.every(r => r !== undefined && r !== null)

    if (hasDetailedRatings) {
      for (const rating of detailedRatings) {
        if (rating < 1.0 || rating > 5.0 || (rating * 2) % 1 !== 0) {
          return NextResponse.json(
            { error: 'Detailed ratings must be between 1.0 and 5.0 with half-star increments' },
            { status: 400 }
          )
        }
      }
    }

    // Check if user has already reviewed this booking
    const { data: existingReview } = await supabase
      .from('reviews')
      .select('id')
      .eq('booking_id', booking_id)
      .eq('reviewer_id', reviewer_id)
      .single()

    if (existingReview) {
      return NextResponse.json(
        { error: 'You have already reviewed this booking' },
        { status: 409 }
      )
    }

    // Verify booking exists and belongs to the user
    const { data: booking, error: bookingError } = await supabase
      .from('bookings')
      .select('id, customer_id, status, service_date')
      .eq('id', booking_id)
      .single()

    if (bookingError || !booking) {
      return NextResponse.json(
        { error: 'Booking not found' },
        { status: 404 }
      )
    }

    if (booking.customer_id !== reviewer_id) {
      return NextResponse.json(
        { error: 'You can only review your own bookings' },
        { status: 403 }
      )
    }

    // Check if booking is completed
    if (booking.status !== 'completed') {
      return NextResponse.json(
        { error: 'You can only review completed bookings' },
        { status: 400 }
      )
    }

    // Create review data for moderation
    const reviewData = {
      text: comment || '',
      rating,
      reviewerId: reviewer_id,
      providerId: provider_id,
      reviewerHistory: {
        totalReviews: 0,
        avgRating: 0,
        recentReviews: 0,
        suspiciousPatterns: []
      }
    }

    // Process review with AI moderation and monitoring
    const moderationResult = await reviewMonitoring.processReviewWithMonitoring(
      reviewData,
      'temp-id' // Will be replaced with actual review ID
    )

    // Determine initial status based on moderation
    let initialStatus = 'pending'
    let moderationStatus = 'pending'

    if (moderationResult.isFlagged) {
      initialStatus = 'flagged'
      moderationStatus = 'needs_review'
    } else if (moderationResult.confidence > 0.9) {
      initialStatus = 'pending'
      moderationStatus = 'needs_review'
    } else {
      initialStatus = 'pending'
      moderationStatus = 'pending'
    }

    // Insert the review
    const { data: review, error: reviewError } = await supabase
      .from('reviews')
      .insert({
        booking_id,
        reviewer_id,
        provider_id,
        rating,
        comment,
        service_quality,
        communication,
        punctuality,
        value_for_money,
        would_recommend,
        photos,
        status: initialStatus,
        moderation_status: moderationStatus,
        spam_score: moderationResult.score,
        created_at: new Date().toISOString(),
        updated_at: new Date().toISOString()
      })
      .select()
      .single()

    if (reviewError) {
      console.error('Error creating review:', reviewError)
      return NextResponse.json(
        { error: 'Failed to create review' },
        { status: 500 }
      )
    }

    // Update the moderation log with the actual review ID
    if (moderationResult.isFlagged) {
      await reviewMonitoring.logModerationDecision(
        review.id,
        'auto_flag',
        moderationResult.reasons.join('; '),
        moderationResult.confidence,
        Object.keys(moderationResult.categories).filter(
          key => moderationResult.categories[key as keyof typeof moderationResult.categories]
        ),
        moderationResult.reasons.filter(
          reason => reason.includes('pattern:') || reason.includes('Detected')
        ),
        moderationResult.reasons.filter(
          reason => reason.includes('Excessive') || reason.includes('All') || reason.includes('Suspicious')
        ),
        0, // Processing time will be updated
        undefined,
        {
          review_id: review.id,
          original_rating: rating,
          text_length: comment?.length || 0,
          has_detailed_ratings: hasDetailedRatings
        }
      )
    }

    // Update provider rating statistics
    await updateProviderRatingStats(provider_id)

    // Track analytics
    try {
      await supabase.from('analytics_events').insert({
        event_type: 'review_submitted',
        user_id: reviewer_id,
        provider_id,
        metadata: {
          has_detailed_ratings: hasDetailedRatings,
          moderation_flagged: moderationResult.isFlagged,
          moderation_confidence: moderationResult.confidence,
          moderation_categories: Object.keys(moderationResult.categories).filter(
            key => moderationResult.categories[key as keyof typeof moderationResult.categories]
          )
        },
        created_at: new Date().toISOString()
      })
    } catch (analyticsError) {
      console.warn('Analytics tracking failed:', analyticsError)
    }

    return NextResponse.json({
      success: true,
      review: {
        id: review.id,
        status: review.status,
        moderation_status: review.moderation_status,
        spam_score: review.spam_score,
        moderation_message: moderationResult.isFlagged 
          ? `Review flagged for moderation: ${moderationResult.reasons.join(', ')}`
          : 'Review submitted successfully and pending approval'
      }
    })

  } catch (error) {
    console.error('Error in review creation:', error)
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    )
  }
}

