async function updateProviderRatingStats(providerId: string) {
  try {
    // Get all published reviews for this provider
    const { data: reviews, error } = await supabase
      .from('reviews')
      .select('overall_quality, rating')
      .eq('provider_id', providerId)
      .eq('status', 'published')

    if (error || !reviews) {
      console.error('Error fetching reviews for rating update:', error)
      return
    }

    if (reviews.length === 0) {
      // No reviews, set default values
      await supabase
        .from('profiles')
        .update({
          rating: 0.0,
          updated_at: new Date().toISOString()
        })
        .eq('id', providerId)
      return
    }

    // Calculate average rating using overall_quality if available, fallback to rating
    const validRatings = reviews
      .map(r => r.overall_quality || r.rating)
      .filter(r => r !== null && r !== undefined)

    if (validRatings.length === 0) {
      console.warn('No valid ratings found for provider:', providerId)
      return
    }

    const averageRating = validRatings.reduce((sum, rating) => sum + rating, 0) / validRatings.length

    // Calculate rating distribution for half-star ranges
    const ratingDistribution = {
      '5.0': 0, '4.5': 0, '4.0': 0, '3.5': 0, '3.0': 0,
      '2.5': 0, '2.0': 0, '1.5': 0, '1.0': 0
    }

    validRatings.forEach(rating => {
      const roundedRating = Math.round(rating * 2) / 2
      const key = roundedRating.toFixed(1)
      if (ratingDistribution[key as keyof typeof ratingDistribution] !== undefined) {
        ratingDistribution[key as keyof typeof ratingDistribution]++
      }
    })

    // Update provider profile
    const { error: updateError } = await supabase
      .from('profiles')
      .update({
        rating: Math.round(averageRating * 10) / 10, // Round to 1 decimal place
        updated_at: new Date().toISOString()
      })
      .eq('id', providerId)

    if (updateError) {
      console.error('Error updating provider rating:', updateError)
    }

  } catch (error) {
    console.error('Error in updateProviderRatingStats:', error)
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { createClient } from '@supabase/supabase-js';

import { supabaseAdmin as supabase } from '@/lib/supabaseProxies';

export async function POST(request: NextRequest) {
  try {
    const { review_id, provider_id, response_text, is_public = true } = await request.json();

    if (!review_id || !provider_id || !response_text) {
      return NextResponse.json(
        { error: 'Missing required fields: review_id, provider_id, response_text' },
        { status: 400 }
      );
    }

    // Verify the review exists and belongs to the provider
    const { data: review, error: reviewError } = await supabase
      .from('reviews')
      .select('id, reviewer_id, status')
      .eq('id', review_id)
      .eq('provider_id', provider_id)
      .single();

    if (reviewError || !review) {
      return NextResponse.json(
        { error: 'Review not found or access denied' },
        { status: 404 }
      );
    }

    if (review.status !== 'published') {
      return NextResponse.json(
        { error: 'Cannot respond to unpublished review' },
        { status: 400 }
      );
    }

    // Check if provider already responded
    const { data: existingResponse } = await supabase
      .from('review_responses')
      .select('id')
      .eq('review_id', review_id)
      .eq('provider_id', provider_id)
      .single();

    if (existingResponse) {
      return NextResponse.json(
        { error: 'Provider has already responded to this review' },
        { status: 400 }
      );
    }

    // Create the response
    const { data: response, error: responseError } = await supabase
      .from('review_responses')
      .insert({
        review_id,
        provider_id,
        response_text: response_text.trim(),
        is_public,
        status: 'published',
        created_at: new Date().toISOString()
      })
      .select()
      .single();

    if (responseError) {
      console.error('Response creation error:', responseError);
      return NextResponse.json(
        { error: 'Failed to create response' },
        { status: 500 }
      );
    }

    // Notify the customer
    await supabase
      .from('notifications')
      .insert({
        user_id: review.reviewer_id,
        type: 'provider_response',
        title: 'Provider Responded to Your Review',
        message: 'The provider has responded to your review',
        data: {
          review_id,
          response_id: response.id,
          provider_id
        },
        created_at: new Date().toISOString()
      });

    // Track analytics
    await fetch('/api/analytics/track', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        event_type: 'provider_response_created',
        user_id: provider_id,
        session_id: 'server-side',
        page_url: '/api/reviews/response',
        properties: {
          review_id,
          is_public,
          response_length: response_text.length
        }
      })
    });

    return NextResponse.json({
      success: true,
      response,
      message: 'Response created successfully'
    });

  } catch (error) {
    console.error('Review response creation error:', error);
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    );
  }
}

