      specialty_ids: searchParams.get('specialty_ids')?.split(',').filter(Boolean) || undefined,
      min_rating: searchParams.get('min_rating') ? parseFloat(searchParams.get('min_rating')!) : undefined,
      max_price: searchParams.get('max_price') ? parseFloat(searchParams.get('max_price')!) : undefined,
      availability_date: searchParams.get('availability_date') || undefined,
      availability_time: searchParams.get('availability_time') || undefined,
      sort_by: (searchParams.get('sort_by') as 'distance' | 'rating' | 'price' | 'availability' | 'popularity') || 'rating',
      limit: parseInt(searchParams.get('limit') || '20'),
      offset: parseInt(searchParams.get('offset') || '0')
    }

    // Generate cache key for this search
    const cacheKey = generateCacheKey(filters)

    // Try to get cached results first
    const cachedResults = await getCachedResults(cacheKey)
    if (cachedResults) {
      return NextResponse.json(cachedResults)
    }

    // Perform optimized search using database function
    const searchResults = await performOptimizedSearch(filters)

    // Cache the results for 15 minutes
    await cacheResults(cacheKey, searchResults, 15)

    return NextResponse.json(searchResults)
  } catch (error) {
    console.error('Optimized search error:', error)
    return NextResponse.json(
      { error: 'Search failed', details: error instanceof Error ? error.message : 'Unknown error' },
      { status: 500 }
    )
  }
}

async function performOptimizedSearch(filters: SearchFilters) {
  try {
    // Convert specialty IDs to UUID array if provided
    const specialtyUuids = filters.specialty_ids?.map(id => id) || null

    // Convert price to cents if provided
    const maxPriceCents = filters.max_price ? Math.round(filters.max_price * 100) : null

    // Use the optimized database function
    const { data: providers, error } = await supabase
      .rpc('search_providers_optimized', {
        search_query: filters.query || null,
        specialty_ids: specialtyUuids,
        min_rating: filters.min_rating || null,
        max_price_cents: maxPriceCents,
        search_lat: filters.latitude || null,
        search_lon: filters.longitude || null,
        radius_km: filters.maxTravelDistance || 20,
        limit_count: filters.limit || 20,
        offset_count: filters.offset || 0
      })

    if (error) {
      console.error('Database function error:', error)
      throw new Error('Database query failed')
    }

    if (!providers) {
      return { providers: [], total: 0, pagination: {} }
    }

    // Apply additional filters that aren't handled by the database function
    let filteredProviders = providers as ProviderWithDistance[]

    // Apply availability filtering if needed
    if (filters.availability_date) {
      filteredProviders = await filterByAvailability(
        filteredProviders,
        filters.availability_date,
        filters.availability_time
      )
    }

    // Apply service category filtering if needed
    if (filters.service_category) {
      filteredProviders = await filterByServiceCategory(
        filteredProviders,
        filters.service_category
      )
    }

    // Get total count for pagination
    const total = filteredProviders.length

    // Apply final sorting
    filteredProviders = sortProviders(filteredProviders, filters.sort_by || 'rating')

    // Apply final pagination
    const startIndex = filters.offset || 0
    const endIndex = startIndex + (filters.limit || 20)
    const paginatedProviders = filteredProviders.slice(startIndex, endIndex)

    return {
      providers: paginatedProviders.map(provider => ({
        id: provider.provider_id,
        full_name: provider.full_name,
        business_name: provider.business_name,
        average_rating: provider.average_rating,
        total_bookings: provider.total_bookings,
        distance: provider.distance_km,
        min_price: provider.min_price_cents / 100, // Convert back to dollars
        specialties: provider.specialty_names
      })),
      total,
      pagination: {
        limit: filters.limit || 20,
        offset: filters.offset || 0,
        has_more: endIndex < total
      }
    }
  } catch (error) {
    console.error('Optimized search execution error:', error)
    throw error
  }
}

async function filterByAvailability(
  providers: ProviderWithDistance[],
  date: string,
  time?: string
): Promise<ProviderWithDistance[]> {
  try {
    const providerIds = providers.map(p => p.provider_id)

    // Query availability slots for the specified date
    const { data: availabilitySlots, error } = await supabase
      .from('availability_slots')
      .select('provider_id, start_time, end_time, is_booked')
      .in('provider_id', providerIds)
      .eq('date', date)
      .eq('is_booked', false)

    if (error) {
      console.error('Availability filter error:', error)
      return providers // Return all providers if filtering fails
    }

    // Filter providers who have available slots
    const availableProviderIds = new Set(
      availabilitySlots?.map(slot => slot.provider_id) || []
    )

    return providers.filter(provider => availableProviderIds.has(provider.provider_id))
  } catch (error) {
    console.error('Availability filtering error:', error)
    return providers // Return all providers if filtering fails
  }
}

async function filterByServiceCategory(
  providers: ProviderWithDistance[],
  category: string
): Promise<ProviderWithDistance[]> {
  try {
    const providerIds = providers.map(p => p.provider_id)

    // Query services for the specified category
    const { data: services, error } = await supabase
      .from('services')
      .select('provider_id')
      .in('provider_id', providerIds)
      .eq('category', category)
      .eq('available', true)

    if (error) {
      console.error('Service category filter error:', error)
      return providers // Return all providers if filtering fails
    }

    // Filter providers who have services in the specified category
    const categoryProviderIds = new Set(
      services?.map(service => service.provider_id) || []
    )

    return providers.filter(provider => categoryProviderIds.has(provider.provider_id))
  } catch (error) {
    console.error('Service category filtering error:', error)
    return providers // Return all providers if filtering fails
  }
}

function sortProviders(
  providers: ProviderWithDistance[], 
  sortBy: 'distance' | 'rating' | 'price' | 'availability' | 'popularity'
): ProviderWithDistance[] {
  switch (sortBy) {
    case 'distance':
      return providers.sort((a, b) => {
        const distA = a.distance_km || Infinity
        const distB = b.distance_km || Infinity
        return distA - distB
      })
    case 'rating':
      return providers.sort((a, b) => (b.average_rating || 0) - (a.average_rating || 0))
    case 'price':
      return providers.sort((a, b) => (a.min_price_cents || 0) - (b.min_price_cents || 0))
    case 'popularity':
      return providers.sort((a, b) => (b.total_bookings || 0) - (a.total_bookings || 0))
    default:
      return providers
  }
}

function generateCacheKey(filters: SearchFilters): string {
  const keyParts = [
    'search',
    filters.query || '',
    filters.latitude?.toString() || '',
    filters.longitude?.toString() || '',
    filters.maxTravelDistance?.toString() || '',
    filters.specialty_ids?.sort().join(',') || '',
    filters.min_rating?.toString() || '',
    filters.max_price?.toString() || '',
    filters.limit?.toString() || '',
    filters.offset?.toString() || ''
  ]
  return `search:${keyParts.join(':')}`
}

async function getCachedResults(cacheKey: string) {
  try {
    const { data, error } = await supabase
      .rpc('get_cached_query', { cache_key: cacheKey })

    if (error || !data) {
      return null
    }

    return data
  } catch (error) {
    console.error('Cache retrieval error:', error)
    return null
  }
}

async function cacheResults(cacheKey: string, data: any, ttlMinutes: number) {
  try {
    await supabase
      .rpc('set_cached_query', {
        cache_key: cacheKey,
        cache_data: data,
        ttl_minutes: ttlMinutes
      })
  } catch (error) {
    console.error('Cache storage error:', error)
    // Don't fail the request if caching fails
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { getServerSupabase } from '@/lib/supabaseServer'

const supabase = new Proxy({} as any, { get: (target, prop) => (getServerSupabase() as any)[prop] }) as ReturnType<typeof getServerSupabase>

// Constants for dynamic radius logic (in kilometers)
const MIN_PROVIDERS_THRESHOLD = 3
const RADIUS_STEP = 2
const START_RADIUS = 3

interface Provider {
  id: string
  name: string
  location: {
    lat: number
    lng: number
  }
  services: Service[]
  availability: AvailabilitySlot[]
  rating?: number
  total_bookings?: number
  provider_locations?: Array<{
    id: string
    name: string
    address: string
    latitude: number
    longitude: number
    is_primary: boolean
  }>
}

interface Service {
  id: string
  name: string
  price: number
  duration: number
}

interface AvailabilitySlot {
  id: string
  start_time: string
  end_time: string
  is_booked: boolean
}

interface SearchFilters {
  query?: string
  location?: string
  latitude?: number
  longitude?: number
  radius?: number // in miles (legacy)
  maxTravelDistance?: number // in kilometers
  service_category?: string
  specialty_ids?: string[] // New: filter by specific specialties
  min_rating?: number
  max_price?: number
  availability_date?: string
  availability_time?: string
  sort_by?: 'distance' | 'rating' | 'price' | 'availability' | 'popularity'
  limit?: number
  offset?: number
}

interface ProviderWithDistance extends Provider {
  distance?: number
}

interface UserPreferences {
  preferred_categories?: string[]
  preferred_price_range?: {
    min: number
    max: number
  }
  preferred_locations?: string[]
}

export async function GET(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url)

    const filters: SearchFilters = {
      query: searchParams.get('query') || undefined,
      location: searchParams.get('location') || undefined,
      latitude: searchParams.get('userLat')
        ? parseFloat(searchParams.get('userLat')!)
        : searchParams.get('latitude')
          ? parseFloat(searchParams.get('latitude')!)
          : undefined,
      longitude: searchParams.get('userLon')
        ? parseFloat(searchParams.get('userLon')!)
        : searchParams.get('longitude')
          ? parseFloat(searchParams.get('longitude')!)
          : undefined,
      radius: searchParams.get('radius') ? parseFloat(searchParams.get('radius')!) : undefined,
      maxTravelDistance: searchParams.get('maxTravelDistance')
        ? parseFloat(searchParams.get('maxTravelDistance')!)
        : 20,
      service_category: searchParams.get('service_category') || searchParams.get('category') || undefined,
      specialty_ids: searchParams.get('specialty_ids')?.split(',').filter(Boolean) || undefined,
      min_rating: searchParams.get('min_rating') ? parseFloat(searchParams.get('min_rating')!) : undefined,
      max_price: searchParams.get('max_price') ? parseFloat(searchParams.get('max_price')!) : undefined,
      availability_date: searchParams.get('availability_date') || undefined,
      availability_time: searchParams.get('availability_time') || undefined,
      sort_by: (searchParams.get('sort_by') as 'distance' | 'rating' | 'price' | 'availability' | 'popularity') || 'rating',
      limit: parseInt(searchParams.get('limit') || '20'),
      offset: parseInt(searchParams.get('offset') || '0')
    }

    const bookingStatus = searchParams.get('bookingStatus') || undefined

    // Build the search query
    const searchResults = await performAdvancedSearch(filters)
    const finalProviders = applyPrivacy(searchResults.providers, bookingStatus)
    const matchFound = finalProviders.length >= MIN_PROVIDERS_THRESHOLD

    // Track search analytics
    await fetch('/api/analytics/track', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        event_type: 'search_performed',
        session_id: 'server-side',
        page_url: '/api/search/providers',
        properties: {
          query: filters.query,
          location: filters.location,
          service_category: filters.service_category,
          results_count: finalProviders.length,
          has_geo_location: !!(filters.latitude && filters.longitude)
        }
      })
    })

    return NextResponse.json({
      success: true,
      matchFound,
      providers: finalProviders,
      total: finalProviders.length,
      radiusUsed: searchResults.radiusUsed,
      message: matchFound ? undefined : `No providers available within ${filters.maxTravelDistance || 20} km.`,
      suggestion: matchFound ? undefined : 'Try expanding your search or changing service type.',
      pagination: searchResults.pagination,
      filters_applied: filters
    })

  } catch (error) {
    console.error('Provider search error:', error)
    return NextResponse.json(
      { error: 'Search failed' },
      { status: 500 }
    )
  }
}

async function performAdvancedSearch(filters: SearchFilters) {
  let query = supabase
    .from('profiles')
    .select(`
      *,
      services:services(
        id,
        name,
        description,
        category,
        price,
        duration,
        available
      ),
      provider_locations:provider_locations(
        id,
        name,
        address,
        latitude,
        longitude,
        is_primary
      ),
      reviews:reviews(
        rating,
        review_text,
        created_at
      ),
      vendor_specialties:vendor_specialties(
        specialty_id,
        is_primary
      )
    `)
    .eq('user_type', 'provider')
    .eq('is_active', true)

  // Text search on name, business_name, and service descriptions
  if (filters.query) {
    // Use full-text search if available, otherwise use ilike
    query = query.or(`
      name.ilike.%${filters.query}%,
      business_name.ilike.%${filters.query}%,
      bio.ilike.%${filters.query}%,
      services.name.ilike.%${filters.query}%,
      services.description.ilike.%${filters.query}%
    `)
  }

  // Service category filter
  if (filters.service_category) {
    query = query.eq('services.category', filters.service_category)
  }

  // Specialty filter
  if (filters.specialty_ids && filters.specialty_ids.length > 0) {
    query = query.in('vendor_specialties.specialty_id', filters.specialty_ids)
  }

  // Rating filter
  if (filters.min_rating) {
    query = query.gte('average_rating', filters.min_rating)
  }

  // Execute the base query
  const { data: providers, error } = await query
    .range(filters.offset || 0, (filters.offset || 0) + (filters.limit || 20) - 1)

  if (error) {
    console.error('Search query error:', error)
    throw new Error('Database query failed')
  }

  if (!providers) {
    return { providers: [], total: 0, pagination: {} }
  }

  // Apply geo-location filtering and distance calculation
  let filteredProviders = providers
  if (filters.latitude && filters.longitude) {
    filteredProviders = providers.map((provider: Provider) => {
      // Calculate distance to each provider location
      const distances = provider.provider_locations?.map((location: { latitude: number; longitude: number }) => {
        if (location.latitude && location.longitude) {
          return calculateDistance(
            filters.latitude!,
            filters.longitude!,
            location.latitude,
            location.longitude
          )
        }
        return Infinity
      }) || [Infinity]

      const minDistance = Math.min(...distances)
      return {
        ...provider,
        distance: minDistance
      }
    })
  }

  // Apply price filtering
  if (filters.max_price) {
    filteredProviders = filteredProviders.filter((provider: Provider) => {
      const hasAffordableService = provider.services?.some((service: Service) => 
        service.price <= filters.max_price!
      )
      return hasAffordableService
    })
  }

  // Apply availability filtering
  if (filters.availability_date) {
    filteredProviders = await filterByAvailability(
      filteredProviders,
      filters.availability_date,
      filters.availability_time
    )
  }

  // Apply sorting
  filteredProviders = sortProviders(filteredProviders, filters.sort_by || 'rating')

  // Dynamic radius expansion
  const { providers: radiusProviders, radiusUsed } = dynamicRadiusFilter(
    filteredProviders,
    filters.maxTravelDistance || 20
  )

  const total = radiusProviders.length

  const startIndex = filters.offset || 0
  const endIndex = startIndex + (filters.limit || 20)
  const paginatedProviders = radiusProviders.slice(startIndex, endIndex)

  return {
    providers: paginatedProviders,
    total,
    radiusUsed,
    pagination: {
      limit: filters.limit || 20,
      offset: filters.offset || 0,
      has_more: endIndex < total
    }
  }
}

function calculateDistance(lat1: number, lon1: number, lat2: number, lon2: number): number {
  const R = 6371 // Earth's radius in kilometers
  const dLat = toRadians(lat2 - lat1)
  const dLon = toRadians(lon2 - lon1)
  const a = 
    Math.sin(dLat/2) * Math.sin(dLat/2) +
    Math.cos(toRadians(lat1)) * Math.cos(toRadians(lat2)) * 
    Math.sin(dLon/2) * Math.sin(dLon/2)
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a))
  return R * c
}

function toRadians(degrees: number): number {
  return degrees * (Math.PI / 180)
}

async function filterByAvailability(
  providers: Provider[],
  date: string,
  time?: string
): Promise<Provider[]> {
  const availableProviders: Provider[] = []

  for (const provider of providers) {
    // Get provider's availability for the date
    const { data: slots } = await supabase
      .from('availability')
      .select('*')
      .eq('provider_id', provider.id)
      .eq('date', date)
      .eq('is_booked', false)

    if (slots && slots.length > 0) {
      // If time is specified, check for specific time slot
      if (time) {
        const hasTimeSlot = slots.some((slot: AvailabilitySlot) => {
          const slotStart = new Date(`${date}T${slot.start_time}`)
          const slotEnd = new Date(`${date}T${slot.end_time}`)
          const requestedTime = new Date(`${date}T${time}`)
          return slotStart <= requestedTime && requestedTime < slotEnd
        })
        if (hasTimeSlot) {
          availableProviders.push(provider)
        }
      } else {
        // If no time specified, provider is available if they have any slots
        availableProviders.push(provider)
      }
    }
  }

  return availableProviders
}

function sortProviders(providers: ProviderWithDistance[], sortBy: 'distance' | 'rating' | 'price' | 'availability' | 'popularity'): ProviderWithDistance[] {
  switch (sortBy) {
    case 'distance':
      return providers.sort((a, b) => (a.distance || Infinity) - (b.distance || Infinity))
    case 'rating':
      return providers.sort((a, b) => (b.rating || 0) - (a.rating || 0))
    case 'price':
      return providers.sort((a, b) => {
        const aMinPrice = Math.min(...(a.services?.map(s => s.price) || [Infinity]))
        const bMinPrice = Math.min(...(b.services?.map(s => s.price) || [Infinity]))
        return aMinPrice - bMinPrice
      })
    case 'availability':
      return providers.sort((a, b) => (b.availability?.length || 0) - (a.availability?.length || 0))
    case 'popularity':
      return providers.sort((a, b) => (b.total_bookings || 0) - (a.total_bookings || 0))
    default:
      return providers
  }
}

function dynamicRadiusFilter(providers: ProviderWithDistance[], maxTravelDistance: number) {
  let currentRadius = START_RADIUS
  while (currentRadius <= maxTravelDistance) {
    const within = providers.filter(p => (p.distance || Infinity) <= currentRadius)
    if (within.length >= MIN_PROVIDERS_THRESHOLD) {
      return { providers: within, radiusUsed: currentRadius }
    }
    currentRadius += RADIUS_STEP
  }
  return { providers: [], radiusUsed: maxTravelDistance }
}

function applyPrivacy(providers: ProviderWithDistance[], bookingStatus?: string) {
  if (bookingStatus === 'committed') return providers
  return providers.map(p => ({
    distance: p.distance,
    id: p.id
  }))
}

// POST endpoint for AI-powered search suggestions
export async function POST(request: NextRequest) {
  try {
    const { query, location, user_preferences } = await request.json();

    // Generate AI-powered search suggestions
    const suggestions = await generateSearchSuggestions(query, location, user_preferences)

    return NextResponse.json({
      success: true,
      suggestions
    })

  } catch (error) {
    console.error('Error searching providers:', error);
    return NextResponse.json(
      { error: 'Failed to search providers' },
      { status: 500 }
    );
  }
}

async function generateSearchSuggestions(
  query: string,
  location?: string,
  userPreferences?: UserPreferences
): Promise<string[]> {
  // Get popular searches
  const { data: popularSearches } = await supabase
    .from('search_history')
    .select('query, count')
    .ilike('query', `%${query}%`)
    .order('count', { ascending: false })
    .limit(5)

  // Get matching service categories
  const { data: categories } = await supabase
    .from('service_categories')
    .select('name')
    .ilike('name', `%${query}%`)
    .limit(3)

  // Get matching provider names
  const { data: providers } = await supabase
    .from('profiles')
    .select('business_name')
    .eq('user_type', 'provider')
    .ilike('business_name', `%${query}%`)
    .limit(3)

  // Combine and deduplicate suggestions
  const suggestions = new Set<string>()

  popularSearches?.forEach((search: { query: string }) => suggestions.add(search.query))
  categories?.forEach((category: { name: string }) => suggestions.add(category.name))
  providers?.forEach((provider: { business_name: string }) => suggestions.add(provider.business_name))

  // Add location-based suggestions if location provided
  if (location) {
    const { data: locationProviders } = await supabase
      .from('profiles')
      .select('business_name')
      .eq('user_type', 'provider')
      .ilike('service_area', `%${location}%`)
      .limit(2)

    locationProviders?.forEach((provider: { business_name: string }) => {
      suggestions.add(`${provider.business_name} in ${location}`)
    })
  }

  // Add personalized suggestions based on user preferences
  if (userPreferences?.preferred_categories) {
    userPreferences.preferred_categories.forEach(category => {
      if (category.toLowerCase().includes(query.toLowerCase())) {
        suggestions.add(category)
      }
    })
  }

  return Array.from(suggestions)
}

import { NextRequest, NextResponse } from 'next/server'
import { createSupabaseServerClient } from '@/lib/supabaseServerClient'
import { getSupabaseConfig } from '@/config/supabase'
import { createServerClient } from '@supabase/ssr'
import { cookies } from 'next/headers'

type TableResult = {
  table: string
  count: number
  error?: string
}

const TARGET_TABLES = ['bookings', 'profiles', 'sessions', 'analytics_events', 'events'] as const

async function resolveAdmin(request: NextRequest) {
  const config = getSupabaseConfig()
  const cookieStore = await cookies()
  const supabase = createServerClient(config.url, config.publishableKey, {
    cookies: {
      getAll() {
        return cookieStore.getAll()
      },
      setAll(cookiesToSet) {
        try {
          cookiesToSet.forEach(({ name, value, options }) => {
            cookieStore.set(name, value, options)
          })
        } catch (error) {
          // The `setAll` method was called from a Server Component or Route Handler.
          // This can be ignored if you have middleware refreshing user sessions.
        }
      }
    },
  })

  const { data, error } = await supabase.auth.getUser()
  if (error || !data.user) return null

  const { data: profile } = await supabase.from('profiles').select('role').eq('id', data.user.id).single()
  if (profile?.role === 'admin') {
    return data.user
  }

  return null
}

async function authenticate(request: NextRequest) {
  const internalToken = request.headers.get('x-internal-token')
  if (internalToken && internalToken === process.env.INTERNAL_API_TOKEN) {
    return true
  }

  const admin = await resolveAdmin(request)
  return Boolean(admin)
}

async function countSynthetic(client: any, table: string): Promise<TableResult> {
  try {
    const { count, error } = await client.from(table).select('*', { count: 'exact', head: true }).eq('synthetic_source', 'simcity')
    if (error) return { table, count: 0, error: error.message }
    return { table, count: count ?? 0 }
  } catch (error) {
    return { table, count: 0, error: error instanceof Error ? error.message : 'unknown_error' }
  }
}

async function purgeSynthetic(client: any, table: string): Promise<TableResult> {
  const countResult = await countSynthetic(client, table)
  if (countResult.error) return countResult

  if (countResult.count === 0) return countResult

  try {
    const { error } = await client.from(table).delete().eq('synthetic_source', 'simcity')
    if (error) {
      return { ...countResult, error: error.message }
    }
    return countResult
  } catch (error) {
    return { ...countResult, error: error instanceof Error ? error.message : 'unknown_error' }
  }
}

export async function POST(request: NextRequest) {
  if (process.env.BOOKIJI_ENV === 'prod') {
    return NextResponse.json({ error: 'SimCity purge is blocked in production' }, { status: 403 })
  }

  const authorized = await authenticate(request)
  if (!authorized) {
    return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
  }

  const body = (await request.json().catch(() => ({}))) as { dryRun?: boolean }
  const dryRun = body?.dryRun === true

  const supabase = createSupabaseServerClient()
  const results: Record<string, TableResult> = {}

  for (const table of TARGET_TABLES) {
    const outcome = dryRun ? await countSynthetic(supabase, table) : await purgeSynthetic(supabase, table)
    results[table] = outcome
  }

  const summary = Object.values(results).reduce(
    (acc, result) => {
      acc.total += result.count
      if (result.error) acc.errors.push({ table: result.table, error: result.error })
      return acc
    },
    { total: 0, errors: [] as Array<{ table: string; error: string }> },
  )

  return NextResponse.json({
    synthetic: true,
    synthetic_source: 'simcity',
    dryRun,
    env: process.env.BOOKIJI_ENV || process.env.NODE_ENV,
    results,
    total: summary.total,
    errors: summary.errors,
  })
}

