      let answer: string;
      try {
        console.log('ü§ñ Generating LLM answer with context...');
        answer = await llmService.generateAnswer(systemPrompt, message) || "I don't have enough information to answer that based on the current documentation.";
        console.log('‚úÖ Generated LLM answer:', { 
          answerLength: answer.length, 
          answerPreview: answer.substring(0, 100), 
          confidence: top 
        });
      } catch (llmError) {
        console.error('‚ùå LLM generation failed:', llmError);
        // Fallback to best chunk if LLM fails
        answer = validChunks[0]?.snippet || "I'm having trouble processing your question right now. Please try again later.";
        console.log('‚ö†Ô∏è Using fallback answer from chunk');
      }

      // Persist lightweight conversation for suggestion engine
      // Only create ticket if user is authenticated (user_id is required)
      if (userId) {
        try {
          // Create ticket for traceability even if not escalated
          // Note: intent is stored in description or can be added via category_id later
          const t = await admin.from('support_tickets').insert({
            user_id: userId,
            title: message.slice(0, 80),
            description: `${message}\n\n[Intent: ${intent}]`, // Store intent in description for now
            priority: 'medium', // Use 'medium' instead of 'normal' to match schema
            status: 'resolved'
          }).select('id').single();
          if (!t.error && t.data?.id) {
            // ensure conversation and write messages
            const conv = await admin.from('support_conversations')
              .insert({ ticket_id: t.data.id })
              .select('id').single();
            if (!conv.error && conv.data?.id) {
              await admin.from('support_messages').insert([
                { conversation_id: conv.data.id, sender_type: 'user', content: message },
                { conversation_id: conv.data.id, sender_type: 'agent', content: answer }
              ]);
            }
          }
        } catch (e) {
          console.warn('Non-fatal: failed to persist kb conversation', e);
        }
      } else {
        console.log('‚ö†Ô∏è Skipping ticket creation - user not authenticated');
      }

      // Get sources for citation
      const sources = validChunks
        .map((c: any) => ({
          title: c.title,
          url: c.url || null,
          score: c.score
        }))
        .filter((s: any, i: number, arr: any[]) => 
          arr.findIndex((x: any) => x.url === s.url) === i // Dedupe by URL
        );

      return NextResponse.json({ 
        reply: answer, 
        intent, 
        confidence: top,
        source: 'kb',
        sources: sources.slice(0, 3) // Return top 3 sources
      });
    }

    // auto-escalate (no CTA)
    // Only create ticket if user is authenticated (user_id is required)
    let ticket;
    if (userId) {
      const ticketIns = await admin.from('support_tickets').insert({
        user_id: userId,
        title: message.slice(0, 80),
        description: `${message}\n\n[Intent: ${intent}]`, // Store intent in description for now
        priority: restricted ? 'high' : 'medium', // Use 'medium' instead of 'normal' to match schema
        status: 'open'
      }).select().single();

      if (ticketIns.error) throw ticketIns.error;
      ticket = ticketIns.data;
    } else {
      // For unauthenticated users, create a minimal ticket object for response
      ticket = { id: 'anonymous-' + Date.now() };
      console.log('‚ö†Ô∏è Skipping ticket creation for escalation - user not authenticated');
    }

    // Persist conversation with the initial user message to enable suggestion generation later
    // Only if ticket was actually created (user authenticated)
    if (userId && ticket.id && !ticket.id.toString().startsWith('anonymous-')) {
      try {
        const conv = await admin.from('support_conversations')
          .insert({ ticket_id: ticket.id })
          .select('id').single();
        if (!conv.error && conv.data?.id) {
          await admin.from('support_messages').insert([
            { conversation_id: conv.data.id, sender_type: 'user', content: message },
            { conversation_id: conv.data.id, sender_type: 'agent', content: 'Thanks, we are escalating this to our support team.' }
          ]);
        }
      } catch (e) {
        console.warn('Non-fatal: failed to persist escalated conversation', e);
      }
    }

    if (email) {
      try {
        await sendSupportEmail(process.env.SUPPORT_ESCALATION_EMAIL!,
          `New ticket ${ticket.id}`, `User: ${email}\nIntent: ${intent}\nMessage: ${message}`);
      } catch (e) {
        console.error('Failed to send support email:', e);
      }
    }

    const replyMessage = userId 
      ? `I'm forwarding this to our support team now. Your ticket ID is ${ticket.id}. We'll email you as soon as possible.`
      : `I'm forwarding this to our support team. Please sign in to track your support request, or provide your email for updates.`;

    return NextResponse.json({
      reply: replyMessage,
      escalated: true,
      ticketId: ticket.id
    });
  } catch (e) {
    console.error('‚ùå Support chat error in handleRequest:', e);
    const errorMessage = e instanceof Error ? e.message : String(e);
    const errorStack = e instanceof Error ? e.stack : undefined;

    console.error('Error details:', {
      message: errorMessage,
      stack: errorStack,
      type: e?.constructor?.name
    });

    // Re-throw to be handled by outer catch
    throw e;
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { getServerSupabase } from '@/lib/supabaseServer'

/**
 * GET /api/support/faq?search=booking&category=<uuid>&limit=10
 * Returns published knowledge base articles matching the query.
 */
export async function GET(request: NextRequest) {
  const { searchParams } = new URL(request.url)
  const query = searchParams.get('search') || ''
  const categoryId = searchParams.get('category')
  const limit = Number(searchParams.get('limit') || 10)

  const supabase = new Proxy({} as any, { get: (target, prop) => (getServerSupabase() as any)[prop] }) as ReturnType<typeof getServerSupabase>

  try {
    let data
    if (query) {
      // Use full-text search - search in title and content
      let q = supabase
        .from('kb_articles')
        .select('id,title,content,url,section,locale')
        .or(`title.ilike.%${query}%,content.ilike.%${query}%`)
        .order('created_at', { ascending: false })
        .limit(limit)

      if (categoryId) {
        // If categoryId maps to section, filter by section
        q = q.eq('section', categoryId)
      }

      const { data: searchData, error } = await q
      if (error) throw error
      data = searchData
    } else {
      // Simple select - all published articles (all kb_articles are effectively published)
      let q = supabase
        .from('kb_articles')
        .select('id,title,content,url,section,locale')
        .order('created_at', { ascending: false })
        .limit(limit)

      if (categoryId) {
        // If categoryId maps to section, filter by section
        q = q.eq('section', categoryId)
      }

      const { data: listData, error } = await q
      if (error) throw error
      data = listData
    }

    // Transform data to match expected format (add slug from url or id)
    const transformedData = (data || []).map((article: any) => ({
      id: article.id,
      title: article.title,
      content: article.content,
      slug: article.url || article.id, // Use url as slug, fallback to id
      section: article.section,
      locale: article.locale
    }))

    return NextResponse.json({ ok: true, data: transformedData })
  } catch (error) {
    console.error('Error fetching FAQ:', error);
    return NextResponse.json(
      { error: 'Failed to fetch FAQ', details: error instanceof Error ? error.message : 'Unknown error' },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { createClient } from '@supabase/supabase-js';
import { getSupabaseConfig } from '@/config/supabase';

const config = getSupabaseConfig();
const supabase = createClient(config.url, config.secretKey!);

/**
 * Lightweight observability endpoint for KB crawler status
 * Returns: last crawl time, article count, chunk count
 */
export async function GET(req: NextRequest) {
  try {
    // Get last crawl timestamp (most recent last_crawled_at)
    const { data: lastCrawl, error: crawlError } = await supabase
      .from('kb_articles')
      .select('last_crawled_at')
      .order('last_crawled_at', { ascending: false })
      .limit(1)
      .single();

    // Get article count
    const { count: articleCount, error: countError } = await supabase
      .from('kb_articles')
      .select('*', { count: 'exact', head: true });

    // Get chunk count
    const { count: chunkCount, error: chunkCountError } = await supabase
      .from('kb_article_chunks')
      .select('*', { count: 'exact', head: true });

    // Get embeddings count (to verify vectorization)
    const { count: embeddingCount, error: embeddingCountError } = await supabase
      .from('kb_embeddings')
      .select('*', { count: 'exact', head: true });

    // Get last RAG usage timestamp
    const { data: lastRagUsage, error: ragError } = await supabase
      .from('kb_rag_usage')
      .select('used_at')
      .order('used_at', { ascending: false })
      .limit(1)
      .single();

    // Check if vectorization is complete (embeddings should match chunks)
    const safeChunkCount = chunkCount ?? 0
    const safeEmbeddingCount = embeddingCount ?? 0
    const isVectorized = safeEmbeddingCount === safeChunkCount && safeChunkCount > 0;

    return NextResponse.json({
      lastCrawlTime: lastCrawl?.last_crawled_at || null,
      lastRagTime: lastRagUsage?.used_at || null,
      articleCount: articleCount || 0,
      chunkCount: safeChunkCount,
      embeddingCount: safeEmbeddingCount,
      isVectorized: isVectorized,
      vectorizationStatus: isVectorized ? 'complete' : (safeEmbeddingCount === 0 ? 'not_started' : 'partial'),
      status: 'ok'
    });
  } catch (error) {
    console.error('KB observability error:', error);
    return NextResponse.json({ 
      error: 'Failed to fetch KB status',
      status: 'error' 
    }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { createServerClient } from '@supabase/ssr'
import { cookies } from 'next/headers'
import { getSupabaseConfig } from '@/config/supabase'
import { z } from 'zod'

// Helper to get or create a conversation for a ticket
async function ensureConversationId(supabase: any, ticketId: string, userId: string) {
  // Try to find existing conversation
  const { data: existing } = await supabase
    .from('support_conversations')
    .select('id')
    .eq('ticket_id', ticketId)
    .maybeSingle()

  if (existing?.id) {
    return existing.id
  }

  // Create new conversation
  const { data: newConv, error } = await supabase
    .from('support_conversations')
    .insert({
      ticket_id: ticketId,
      user_id: userId,
      message: 'Conversation started',
      is_from_user: true
    })
    .select('id')
    .single()

  if (error) throw error
  return newConv.id
}

// GET /api/support/tickets/:ticketId/messages
export async function GET(req: NextRequest) {
  const ticketId = new URL(req.url).pathname.split('/')[4]
  const cookieStore = await cookies()
  const config = getSupabaseConfig()

  try {
    const supabase = createServerClient(
      config.url,
      config.publishableKey,
      {
        cookies: {
          getAll() {
            return cookieStore.getAll()
          },
          setAll(cookiesToSet) {
            try {
              cookiesToSet.forEach(({ name, value, options }) => {
                cookieStore.set(name, value, options)
              })
            } catch (error) {
              // The `setAll` method was called from a Server Component or Route Handler.
              // This can be ignored if you have middleware refreshing user sessions.
            }
          }
        }
      }
    )

    // Get user session
    const { data: { user }, error: authError } = await supabase.auth.getUser()
    if (authError || !user) {
      return NextResponse.json(
        { error: 'Unauthorized' },
        { status: 401 }
      )
    }

    // Get or create conversation
    const conversationId = await ensureConversationId(supabase, ticketId, user.id)

    // Fetch messages using conversation_id
    const { data, error } = await supabase
      .from('support_messages')
      .select('id,message,sender_id,message_type,created_at')
      .eq('conversation_id', conversationId)
      .order('created_at', { ascending: true })
      .limit(500)

    if (error) throw error

    // Transform data to match frontend expectations
    const transformed = data?.map(msg => ({
      id: msg.id,
      message: msg.message,
      sender_id: msg.sender_id,
      sender_type: msg.sender_id === user.id ? 'customer' : 'agent',
      is_internal: false,
      created_at: msg.created_at
    })) || []

    return NextResponse.json({ ok: true, data: transformed })
  } catch (error: any) {
    console.error('Error fetching ticket messages:', error);
    return NextResponse.json(
      { error: 'Failed to fetch messages', details: error.message },
      { status: 500 }
    );
  }
}

// POST /api/support/tickets/:ticketId/messages
const postSchema = z.object({
  message: z.string().min(1).max(5000),
  sender_type: z.enum(['customer', 'agent', 'system']).optional()
})

export async function POST(req: NextRequest) {
  const ticketId = new URL(req.url).pathname.split('/')[4]
  const cookieStore = await cookies()
  const config = getSupabaseConfig()

  try {
    const supabase = createServerClient(
      config.url,
      config.publishableKey,
      {
        cookies: {
          getAll() {
            return cookieStore.getAll()
          },
          setAll(cookiesToSet) {
            try {
              cookiesToSet.forEach(({ name, value, options }) => {
                cookieStore.set(name, value, options)
              })
            } catch (error) {
              // The `setAll` method was called from a Server Component or Route Handler.
              // This can be ignored if you have middleware refreshing user sessions.
            }
          }
        }
      }
    )

    // Get user session
    const { data: { user }, error: authError } = await supabase.auth.getUser()
    if (authError || !user) {
      return NextResponse.json(
        { error: 'Unauthorized' },
        { status: 401 }
      )
    }

    const body = await req.json()
    const { message } = postSchema.parse(body)

    // Get or create conversation
    const conversationId = await ensureConversationId(supabase, ticketId, user.id)

    // Insert message using conversation_id and sender_id from session
    const { data, error } = await supabase
      .from('support_messages')
      .insert({
        conversation_id: conversationId,
        sender_id: user.id,
        message,
        message_type: 'text'
      })
      .select('id,message,sender_id,message_type,created_at')
      .single()

    if (error) throw error

    // Transform response to match frontend expectations
    const transformed = {
      id: data.id,
      message: data.message,
      sender_id: data.sender_id,
      sender_type: 'customer',
      is_internal: false,
      created_at: data.created_at
    }

    return NextResponse.json({ ok: true, data: transformed })
  } catch (error: any) {
    console.error('Error creating ticket message:', error);
    return NextResponse.json(
      { error: 'Failed to create message', details: error.message },
      { status: 500 }
    );
  }
}

