'use client';

import { useState } from 'react';
import Link from 'next/link';
import { supabaseBrowserClient } from '@/lib/supabaseClient';

export default function ForgotPasswordPage() {
  const [email, setEmail] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);
    setIsLoading(true);

    const supabase = supabaseBrowserClient()
    if (!supabase) {
      setError('Supabase client not available');
      setIsLoading(false);
      return;
    }

    try {
      // First, check if the user exists
      const { data: { users }, error: userError } = await supabase.auth.admin.listUsers();

      if (userError) {
        // If admin access fails, try to send a password reset email
        // This will work for existing users but won't reveal if email exists
        const { error } = await supabase.auth.resetPasswordForEmail(email, {
          redirectTo: `${window.location.origin}/auth/reset`,
        });

        if (error) throw error;
      } else {
        // Check if user exists
        const userExists = users?.some(user => user.email === email);

        if (!userExists) {
          throw new Error('No account found with this email address');
        }

        // User exists, send password reset email
        const { error } = await supabase.auth.resetPasswordForEmail(email, {
          redirectTo: `${window.location.origin}/auth/reset`,
        });

        if (error) throw error;
      }

      setSuccess(true);
    } catch (error: unknown) {
      if (error instanceof Error) {
        setError(error.message || 'Failed to send reset password email');
      } else {
        setError('Failed to send reset password email');
      }
    } finally {
      setIsLoading(false);
    }
  };

  if (success) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
        <div className="max-w-md w-full space-y-8">
          <div className="text-center">
            <h2 className="mt-6 text-3xl font-extrabold text-gray-900">
              Check your email
            </h2>
            <p className="mt-2 text-sm text-gray-600">
              We've sent you a link to reset your password. Please check your email and click the link to continue.
            </p>
            <div className="mt-4">
              <Link
                href="/login"
                className="text-sm font-medium text-blue-600 hover:text-blue-500"
              >
                Back to login
              </Link>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div>
          <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900">
            Reset your password
          </h2>
          <p className="mt-2 text-center text-sm text-gray-600">
            Enter your email address and we'll send you a link to reset your password.
          </p>
        </div>

        <form className="mt-8 space-y-6" onSubmit={handleSubmit}>
          {error && (
            <div className="rounded-md bg-red-50 p-4">
              <div className="text-sm text-red-700">{error}</div>
            </div>
          )}

          <div>
            <label htmlFor="email" className="block text-sm font-medium text-gray-700">
              Email address
            </label>
            <div className="mt-1">
              <input
                id="email"
                name="email"
                type="email"
                autoComplete="email"
                required
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="block w-full appearance-none rounded-md border border-gray-300 px-3 py-2 placeholder-gray-400 shadow-sm focus:border-blue-500 focus:outline-none focus:ring-blue-500 sm:text-sm"
              />
            </div>
          </div>

          <div>
            <button
              type="submit"
              disabled={isLoading}
              className={`flex w-full justify-center rounded-md border border-transparent bg-blue-600 py-2 px-4 text-sm font-medium text-white shadow-sm hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 ${
                isLoading ? 'opacity-75 cursor-not-allowed' : ''
              }`}
            >
              {isLoading ? 'Sending...' : 'Send reset link'}
            </button>
          </div>

          <div className="mt-6 text-center">
            <Link
              href="/login"
              className="text-sm font-medium text-blue-600 hover:text-blue-500"
            >
              Back to login
            </Link>
          </div>
        </form>
      </div>
    </div>
  );
}

import 'server-only'

import fs from 'node:fs'
import path from 'node:path'
import { loadActions } from '@/scripts/ops-actions-store'
import type { OpsAction } from '@/types/opsActions'
import { ActionCard } from './ActionCard'

type HealthCardProps = {
  title: string
  description: string
  status: 'ok' | 'degraded' | 'down' | 'unknown' | string
  details?: any
}

function readJsonFile<T>(relativePath: string): T | null {
  const filePath = path.join(process.cwd(), relativePath)
  if (!fs.existsSync(filePath)) return null

  try {
    return JSON.parse(fs.readFileSync(filePath, 'utf8')) as T
  } catch {
    return null
  }
}

function HealthCard({ title, description, status, details }: HealthCardProps) {
  const color =
    status === 'ok'
      ? 'bg-emerald-500'
      : status === 'degraded'
      ? 'bg-amber-500'
      : status === 'down'
      ? 'bg-red-500'
      : 'bg-slate-500'

  return (
    <div className="rounded-xl border border-slate-800 bg-slate-900/70 p-4 shadow-sm shadow-slate-900/30 space-y-2">
      <div className="flex items-center justify-between">
        <h3 className="font-medium">{title}</h3>
        <span className={`inline-flex h-2.5 w-2.5 rounded-full ${color}`} />
      </div>
      <p className="text-xs text-slate-400">{description}</p>
      {details && (
        <pre className="mt-2 text-[10px] whitespace-pre-wrap text-slate-400 bg-slate-950/50 rounded-md p-2 border border-slate-800/60">
          {JSON.stringify(details, null, 2)}
        </pre>
      )}
    </div>
  )
}

