import { supabaseBrowserClient } from "@/lib/supabaseClient";
import { theme, combineClasses } from "@/config/theme";

export default function AuthEntry({ mode = "signup" }: { mode?: "signup" | "login" }) {
  const router = useRouter();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [error, setError] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isSignUp, setIsSignUp] = useState(mode === "signup");

  const handleEmailAuth = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);
    setIsLoading(true);

    const supabase = supabaseBrowserClient()
    if (!supabase) {
      setError('Supabase client not available');
      setIsLoading(false);
      return;
    }

    try {
      if (isSignUp) {
        if (password !== confirmPassword) {
          throw new Error("Passwords do not match");
        }
        const { error } = await supabase.auth.signUp({
          email,
          password,
          options: {
            emailRedirectTo: `${window.location.origin}/auth/callback`,
          },
        });
        if (error) throw error;

        // Redirect immediately - useAuthReady will handle session readiness
        router.push("/choose-role");
      } else {
        const { data, error } = await supabase.auth.signInWithPassword({
          email,
          password,
        });
        if (error) throw error;

        // Check if user is admin and redirect accordingly
        try {
          const adminCheck = await fetch('/api/auth/check-admin', {
            method: 'GET',
            credentials: 'include'
          })

          if (adminCheck.ok) {
            const { isAdmin } = await adminCheck.json()
            if (isAdmin) {
              // Always redirect admins to admin cockpit
              router.push('/admin')
              return
            }
          }
        } catch (adminError) {
          console.warn('Failed to check admin status:', adminError)
          // Continue with normal redirect
        }

        router.push("/customer/dashboard");
      }
    } catch (err: unknown) {
      setError(err instanceof Error ? err.message : `Failed to ${isSignUp ? "sign up" : "sign in"}`);
    } finally {
      setIsLoading(false);
    }
  };

  const inputClasses = combineClasses(
    theme.components.input.base,
    theme.components.input.focus,
    theme.components.input.rounded,
    theme.components.input.placeholder
  );
  const buttonClasses = combineClasses(
    "w-full px-6 py-4 text-lg font-medium",
    theme.components.button.primary.base,
    theme.components.button.primary.hover,
    theme.components.button.primary.shadow,
    theme.components.button.primary.rounded,
    isLoading ? "opacity-70 cursor-not-allowed" : ""
  );
  const cardClasses = combineClasses(
    theme.components.card.background,
    theme.components.card.border,
    theme.components.card.shadow,
    theme.components.card.rounded,
    "p-8"
  );

  return (
    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="w-full">
      <motion.div
        initial={{ y: 20, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.4 }}
        className={cardClasses}
      >
        {error && (
          <div className="mb-6 p-4 bg-red-50 border border-red-200 text-red-600 rounded-md text-sm">
            {error}
          </div>
        )}
        <form className="space-y-6" onSubmit={handleEmailAuth}>
          <div>
            <label htmlFor="email" className="block text-sm font-medium text-gray-700">
              Email address
            </label>
            <input
              id="email"
              name="email"
              type="email"
              autoComplete="email"
              required
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className={inputClasses}
            />
          </div>
          <div>
            <label htmlFor="password" className="block text-sm font-medium text-gray-700">
              Password
            </label>
            <input
              id="password"
              name="password"
              type="password"
              autoComplete={isSignUp ? "new-password" : "current-password"}
              required
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className={inputClasses}
            />
          </div>
          {isSignUp && (
            <div>
              <label htmlFor="confirmPassword" className="block text-sm font-medium text-gray-700">
                Confirm Password
              </label>
              <input
                id="confirmPassword"
                name="confirmPassword"
                type="password"
                autoComplete="new-password"
                required
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                className={inputClasses}
              />
            </div>
          )}
          <div>
            <button
              type="submit"
              disabled={isLoading}
              className={buttonClasses}
            >
              {isLoading ? (
                <div className="flex items-center justify-center space-x-2">
                  <div className="loading-dots flex space-x-1">
                    <div className="w-2 h-2 bg-white rounded-full animate-pulse"></div>
                    <div className="w-2 h-2 bg-white rounded-full animate-pulse" style={{ animationDelay: '0.1s' }}></div>
                    <div className="w-2 h-2 bg-white rounded-full animate-pulse" style={{ animationDelay: '0.2s' }}></div>
                  </div>
                  <span>{isSignUp ? "Creating account..." : "Signing in..."}</span>
                </div>
              ) : isSignUp ? "Create Account" : "Sign In"}
            </button>
          </div>
        </form>
        <div className="text-center pt-6">
          <p className="text-sm text-gray-600">
            {isSignUp ? "Already have an account?" : "Don't have an account?"}{' '}
            <button
              type="button"
              onClick={() => setIsSignUp(!isSignUp)}
              className="text-blue-600 hover:underline font-medium"
            >
              {isSignUp ? "Sign in" : "Sign up"}
            </button>
          </p>
        </div>
      </motion.div>
    </motion.div>
  );
}

'use client'

import React from 'react'
import { Button } from '@/components/ui/button'
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card'

interface ErrorBoundaryState {
  hasError: boolean
  error?: Error
  errorInfo?: React.ErrorInfo
}

interface ErrorBoundaryProps {
  children: React.ReactNode
  fallback?: React.ComponentType<{ error: Error; resetError: () => void }>
}

class ErrorBoundaryClass extends React.Component<ErrorBoundaryProps, ErrorBoundaryState> {
  constructor(props: ErrorBoundaryProps) {
    super(props)
    this.state = { hasError: false }
  }

  static getDerivedStateFromError(error: Error): ErrorBoundaryState {
    // Handle chunk loading errors gracefully (network issues, code splitting)
    if (error.name === 'ChunkLoadError' || error.message?.includes('chunk') || error.message?.includes('Loading chunk')) {
      console.warn('Chunk loading error detected, will retry on next navigation:', error.message)
      // Don't show error UI for chunk loading errors - they're usually transient
      return { hasError: false }
    }

    // Handle lazy component errors
    if (error.message?.includes('Lazy component') || error.message?.includes('lazy')) {
      console.warn('Lazy component loading error:', error.message)
      // These are usually recoverable, don't show error UI
      return { hasError: false }
    }

    return { hasError: true, error }
  }

  componentDidCatch(error: Error, errorInfo: React.ErrorInfo) {
    this.setState({
      error,
      errorInfo,
    })

    // Log error to console in development
    if (process.env.NODE_ENV === 'development') {
      console.error('ErrorBoundary caught an error:', error, errorInfo)
    }

    // Send to Sentry if available
    if (typeof window !== 'undefined' && (window as any).Sentry) {
      try {
        ;(window as any).Sentry.captureException(error, {
          contexts: {
            react: {
              componentStack: errorInfo.componentStack,
            },
          },
        })
      } catch (sentryError) {
        console.warn('Failed to send error to Sentry:', sentryError)
      }
    }
  }

  resetError = () => {
    this.setState({ hasError: false, error: undefined, errorInfo: undefined })
  }

  render() {
    if (this.state.hasError) {
      if (this.props.fallback) {
        return React.createElement(this.props.fallback, {
          error: this.state.error!,
          resetError: this.resetError,
        })
      }

      return <DefaultErrorFallback error={this.state.error!} resetError={this.resetError} />
    }

    return this.props.children
  }
}

function DefaultErrorFallback({ error, resetError }: { error: Error; resetError: () => void }) {
  // Use useEffect with error tracking - ensure hooks are always called
  React.useEffect(() => {
    // Track error asynchronously to avoid blocking render
    const trackErrorAsync = async () => {
      try {
        const response = await fetch('/api/analytics/track', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            event: 'error_occurred',
            properties: {
              error: error.message,
              context: {
                stack: error.stack,
                name: error.name,
                component: 'ErrorBoundary',
              },
              url: typeof window !== 'undefined' ? window.location.href : undefined,
            },
          }),
        })
        if (!response.ok) {
          console.warn('Analytics tracking failed:', response.status)
        }
      } catch (err) {
        // Silently fail - don't break error UI
        if (process.env.NODE_ENV === 'development') {
          console.warn('Analytics tracking error:', err)
        }
      }
    }
    trackErrorAsync()
  }, [error.message, error.stack, error.name])

  return (
    <div className="min-h-screen flex items-center justify-center p-4">
      <Card className="w-full max-w-md">
        <CardHeader>
          <CardTitle className="text-destructive">Something went wrong</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <p className="text-sm text-muted-foreground">
            We encountered an unexpected error. Our team has been notified.
          </p>

          {process.env.NODE_ENV === 'development' && (
            <details className="text-xs">
              <summary className="cursor-pointer text-muted-foreground">
                Error details (development only)
              </summary>
              <pre className="mt-2 p-2 bg-muted rounded text-xs overflow-auto">
                {error.message}
                {error.stack && `\n\n${error.stack}`}
              </pre>
            </details>
          )}

          <div className="flex space-x-2">
            <Button onClick={resetError} variant="outline" size="sm">
              Try again
            </Button>
            <Button onClick={() => window.location.reload()} size="sm">
              Reload page
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  )
}

// Wrapper component to provide hooks context
export function ErrorBoundary(props: ErrorBoundaryProps) {
  return <ErrorBoundaryClass {...props} />
}

'use client';

import { useState, useEffect, useRef } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
// import { Slider } from '@/components/ui/slider';
import { MapPin, TrendingUp, Users, DollarSign, Calendar, RefreshCw } from 'lucide-react';

interface HeatmapDataPoint {
  postal_code: string;
  latitude: number;
  longitude: number;
  booking_count: number;
  revenue: number;
  avg_rating: number;
  unique_providers: number;
  last_booking: string;
}

interface HeatmapProps {
  userId?: string;
  className?: string;
  height?: number;
  showControls?: boolean;
  defaultTimeRange?: '7d' | '30d' | '90d' | '1y';
}

export default function HeatmapVisualization({ 
  userId, 
  className = '', 
  height = 400,
  showControls = true,
  defaultTimeRange = '30d'
}: HeatmapProps) {
  const [data, setData] = useState<HeatmapDataPoint[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [timeRange, setTimeRange] = useState(defaultTimeRange);
  const [serviceType, setServiceType] = useState<string>('');
  const [minBookings, setMinBookings] = useState([1]);
  const [selectedPoint, setSelectedPoint] = useState<HeatmapDataPoint | null>(null);
  const mapRef = useRef<HTMLDivElement>(null);

  const fetchHeatmapData = async () => {
    setLoading(true);
    setError(null);

    try {
      const params = new URLSearchParams({
        timeRange,
        minBookings: minBookings[0].toString(),
        ...(serviceType && { serviceType })
      });

      const response = await fetch(`/api/analytics/heatmap?${params}`);
      const result = await response.json();

      if (result.success) {
        setData(result.data || []);
      } else {
        setError(result.error || 'Failed to fetch heatmap data');
      }
    } catch (err) {
      console.error('Heatmap fetch error:', err);
      setError('Error loading heatmap data');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchHeatmapData();
  }, [timeRange, serviceType, minBookings]);

  // Calculate color intensity based on booking count
  const getHeatColor = (bookingCount: number, maxBookings: number): string => {
    const intensity = bookingCount / maxBookings;
    const red = Math.min(255, Math.floor(255 * intensity));
    const blue = Math.max(0, Math.floor(255 * (1 - intensity)));
    return `rgb(${red}, 100, ${blue})`;
  };

  // Get statistics
  const stats = data.length > 0 ? {
    totalBookings: data.reduce((sum, point) => sum + point.booking_count, 0),
    totalRevenue: data.reduce((sum, point) => sum + point.revenue, 0),
    avgRating: data.reduce((sum, point) => sum + point.avg_rating, 0) / data.length,
    uniqueAreas: data.length,
    maxBookings: Math.max(...data.map(point => point.booking_count))
  } : {
    totalBookings: 0,
    totalRevenue: 0,
    avgRating: 0,
    uniqueAreas: 0,
    maxBookings: 0
  };

  const serviceTypes = [
    { value: '', label: 'All Services' },
    { value: 'haircut', label: 'Haircut' },
    { value: 'massage', label: 'Massage' },
    { value: 'cleaning', label: 'Cleaning' },
    { value: 'tutoring', label: 'Tutoring' },
    { value: 'photography', label: 'Photography' },
    { value: 'fitness', label: 'Fitness Training' },
    { value: 'beauty', label: 'Beauty Services' },
    { value: 'handyman', label: 'Handyman' },
    { value: 'catering', label: 'Catering' },
    { value: 'other', label: 'Other' }
  ];

  return (
    <div className={`space-y-4 ${className}`}>
      {/* Controls */}
      {showControls && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <MapPin className="h-5 w-5" />
              <span>Booking Heatmap Controls</span>
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              <div>
                <label className="block text-sm font-medium mb-2">Time Range</label>
                <Select value={timeRange} onValueChange={(value: string) => setTimeRange(value as typeof timeRange)}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="7d">Last 7 Days</SelectItem>
                    <SelectItem value="30d">Last 30 Days</SelectItem>
                    <SelectItem value="90d">Last 90 Days</SelectItem>
                    <SelectItem value="1y">Last Year</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <label className="block text-sm font-medium mb-2">Service Type</label>
                <Select value={serviceType} onValueChange={setServiceType}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {serviceTypes.map(type => (
                      <SelectItem key={type.value} value={type.value}>
                        {type.label}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div>
                <label className="block text-sm font-medium mb-2">
                  Min Bookings: {minBookings[0]}
                </label>
                <input
                  type="range"
                  value={minBookings[0]}
                  onChange={(e) => setMinBookings([parseInt(e.target.value)])}
                  max={20}
                  min={1}
                  step={1}
                  className="w-full mt-2"
                />
              </div>

              <div className="flex items-end">
                <Button onClick={fetchHeatmapData} disabled={loading} className="w-full">
                  <RefreshCw className={`h-4 w-4 mr-2 ${loading ? 'animate-spin' : ''}`} />
                  Refresh
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Statistics */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <Calendar className="h-4 w-4 text-blue-500" />
              <div>
                <p className="text-sm text-gray-600">Total Bookings</p>
                <p className="text-xl font-bold">{stats.totalBookings.toLocaleString()}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <DollarSign className="h-4 w-4 text-green-500" />
              <div>
                <p className="text-sm text-gray-600">Total Revenue</p>
                <p className="text-xl font-bold">${stats.totalRevenue.toLocaleString()}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <TrendingUp className="h-4 w-4 text-purple-500" />
              <div>
                <p className="text-sm text-gray-600">Avg Rating</p>
                <p className="text-xl font-bold">{stats.avgRating.toFixed(1)}‚≠ê</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <MapPin className="h-4 w-4 text-orange-500" />
              <div>
                <p className="text-sm text-gray-600">Areas Covered</p>
                <p className="text-xl font-bold">{stats.uniqueAreas}</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Main Heatmap Display */}
      <Card>
        <CardHeader>
          <CardTitle>Booking Density Heatmap</CardTitle>
        </CardHeader>
        <CardContent>
          {loading ? (
            <div className="flex items-center justify-center h-64">
              <div className="text-center">
                <RefreshCw className="h-8 w-8 animate-spin mx-auto mb-2" />
                <p>Loading heatmap data...</p>
              </div>
            </div>
          ) : error ? (
            <div className="flex items-center justify-center h-64">
              <div className="text-center text-red-500">
                <p>{error}</p>
                <Button onClick={fetchHeatmapData} className="mt-2">
                  Try Again
                </Button>
              </div>
            </div>
          ) : data.length === 0 ? (
            <div className="flex items-center justify-center h-64">
              <div className="text-center text-gray-500">
                <MapPin className="h-12 w-12 mx-auto mb-2 opacity-50" />
                <p>No booking data available for the selected criteria</p>
                <p className="text-sm">Try adjusting the filters above</p>
              </div>
            </div>
          ) : (
            <div className="space-y-4">
              {/* Simple heatmap representation using a grid */}
              <div 
                ref={mapRef}
                className="border rounded-lg p-4 bg-gray-50"
                style={{ height: `${height}px` }}
              >
                <div className="h-full overflow-auto">
                  <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-2">
                    {data.map((point, index) => (
                      <div
                        key={point.postal_code}
                        className="border rounded p-3 cursor-pointer transition-all hover:shadow-lg"
                        style={{
                          backgroundColor: getHeatColor(point.booking_count, stats.maxBookings),
                          color: point.booking_count > stats.maxBookings * 0.5 ? 'white' : 'black'
                        }}
                        onClick={() => setSelectedPoint(point)}
                      >
                        <div className="text-sm font-bold">{point.postal_code}</div>
                        <div className="text-xs">
                          <div>{point.booking_count} bookings</div>
                          <div>${point.revenue.toFixed(0)}</div>
                          <div>{point.avg_rating.toFixed(1)}‚≠ê</div>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              </div>

              {/* Legend */}
              <div className="flex items-center space-x-4 text-sm">
                <span>Heat Intensity:</span>
                <div className="flex items-center space-x-1">
                  <div className="w-4 h-4 bg-blue-500" title="Low activity"></div>
                  <span>Low</span>
                </div>
                <div className="flex items-center space-x-1">
                  <div className="w-4 h-4 bg-purple-500" title="Medium activity"></div>
                  <span>Medium</span>
                </div>
                <div className="flex items-center space-x-1">
                  <div className="w-4 h-4 bg-red-500" title="High activity"></div>
                  <span>High</span>
                </div>
              </div>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Selected Point Details */}
      {selectedPoint && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center justify-between">
              <span>Area Details: {selectedPoint.postal_code}</span>
              <Button variant="outline" size="sm" onClick={() => setSelectedPoint(null)}>
                Close
              </Button>
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
              <div>
                <p className="text-sm text-gray-600">Bookings</p>
                <p className="text-xl font-bold">{selectedPoint.booking_count}</p>
              </div>
              <div>
                <p className="text-sm text-gray-600">Revenue</p>
                <p className="text-xl font-bold">${selectedPoint.revenue.toFixed(2)}</p>
              </div>
              <div>
                <p className="text-sm text-gray-600">Avg Rating</p>
                <p className="text-xl font-bold">{selectedPoint.avg_rating.toFixed(1)}‚≠ê</p>
              </div>
              <div>
                <p className="text-sm text-gray-600">Providers</p>
                <p className="text-xl font-bold">{selectedPoint.unique_providers}</p>
              </div>
            </div>
            <div className="mt-4">
              <p className="text-sm text-gray-600">Location</p>
              <p className="font-medium">
                {selectedPoint.latitude.toFixed(6)}, {selectedPoint.longitude.toFixed(6)}
              </p>
              <p className="text-sm text-gray-500 mt-2">
                Last booking: {new Date(selectedPoint.last_booking).toLocaleDateString()}
              </p>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

'use client'

import { useState, useRef, useCallback } from 'react'
import { Button } from '@/components/ui/button'
import { Card, CardContent } from '@/components/ui/card'
import { Badge } from '@/components/ui/badge'
import { Input } from '@/components/ui/input'
import { Label } from '@/components/ui/label'
import { Textarea } from '@/components/ui/textarea'
import { Image, Upload, X, Eye, Download, FileText, Sparkles } from 'lucide-react'
import { cn } from '@/lib/utils'

interface ImageAttachmentProps {
  images: File[];
  onImagesChangeAction: (images: File[]) => void;
  maxImages?: number;
  maxSizeMB?: number;
  acceptedTypes?: string[];
  className?: string;
}

export function ImageAttachment({
  images,
  onImagesChangeAction,
  maxImages = 5,
  maxSizeMB = 10,
  acceptedTypes = ['image/jpeg', 'image/png', 'image/webp'],
  className = ''
}: ImageAttachmentProps) {
  const [isDragOver, setIsDragOver] = useState(false)
  const [isProcessing, setIsProcessing] = useState(false)
  const [summary, setSummary] = useState('')
  const [error, setError] = useState<string | null>(null)

  const fileInputRef = useRef<HTMLInputElement>(null)
  const dropZoneRef = useRef<HTMLDivElement>(null)

  // Handle file selection
  const handleFileSelect = useCallback((files: FileList | null) => {
    if (!files) return

    const newImages: File[] = []
    const errors: string[] = []

    Array.from(files).forEach((file) => {
      // Validate file type
      if (!acceptedTypes.includes(file.type)) {
        errors.push(`${file.name} is not a supported image type`)
        return
      }

      // Validate file size
      if (file.size > maxSizeMB * 1024 * 1024) {
        errors.push(`${file.name} is too large (max ${maxSizeMB}MB)`)
        return
      }

      // Check if we've reached max files
      if (images.length + newImages.length >= maxImages) {
        errors.push(`Maximum ${maxImages} images allowed`)
        return
      }

      newImages.push(file)
    })

    // Show errors if any
    if (errors.length > 0) {
      setError(errors.join(', '))
      setTimeout(() => setError(null), 5000)
    }

    // Add new images
    if (newImages.length > 0) {
      const updatedImages = [...images, ...newImages]
      onImagesChangeAction(updatedImages)
    }
  }, [images, maxImages, maxSizeMB, acceptedTypes, onImagesChangeAction])

  // Handle drag and drop
  const handleDragOver = useCallback((e: React.DragEvent) => {
    e.preventDefault()
    setIsDragOver(true)
  }, [])

  const handleDragLeave = useCallback((e: React.DragEvent) => {
    e.preventDefault()
    setIsDragOver(false)
  }, [])

  const handleDrop = useCallback((e: React.DragEvent) => {
    e.preventDefault()
    setIsDragOver(false)

    const files = e.dataTransfer.files
    handleFileSelect(files)
  }, [handleFileSelect])

  // Remove image
  const removeImage = useCallback((id: string) => {
    const updatedImages = images.filter(img => img.name !== id) // Assuming name is unique for now
    onImagesChangeAction(updatedImages)
  }, [images, onImagesChangeAction])

  // Generate AI summary
  const generateSummary = useCallback(async () => {
    if (images.length === 0) return

    setIsProcessing(true)
    setError(null)

    try {
      // Create FormData with images
      const formData = new FormData()
      images.forEach((img, index) => {
        formData.append(`image_${index}`, img)
      })

      // Call AI summarization API
      const response = await fetch('/api/ai/summarize-images', {
        method: 'POST',
        body: formData
      })

      if (!response.ok) {
        throw new Error('Failed to generate summary')
      }

      const data = await response.json()
      const aiSummary = data.summary

      setSummary(aiSummary)
      // onSummaryChange?.(aiSummary) // This prop was removed from interface

      // Update images with summaries
      // This part of the logic needs to be adapted to the new ImageFile interface
      // For now, we'll just update the summary in the state
      const updatedImages = images.map((img, index) => ({
        ...img,
        summary: data.imageSummaries?.[index] || '',
        isProcessing: false
      }))

      // onImagesChange(updatedImages) // This prop was removed from interface
      onImagesChangeAction(updatedImages) // Use the new prop

    } catch (err) {
      console.error('Failed to generate summary:', err)
      setError('Failed to generate AI summary. Please try again.')
    } finally {
      setIsProcessing(false)
    }
  }, [images, onImagesChangeAction])

  // Format file size
  const formatFileSize = (bytes: number): string => {
    if (bytes === 0) return '0 Bytes'
    const k = 1024
    const sizes = ['Bytes', 'KB', 'MB', 'GB']
    const i = Math.floor(Math.log(bytes) / Math.log(k))
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i]
  }

  // Get file type icon
  const getFileTypeIcon = (type: string) => {
    if (type.startsWith('image/')) return <Image className="h-4 w-4" />
    return <FileText className="h-4 w-4" />
  }

  return (
    <div className={cn("space-y-4", className)}>
      {/* File Input */}
      <div className="space-y-2">
        <Label htmlFor="image-upload">Attach Images</Label>
        <Input
          id="image-upload"
          ref={fileInputRef}
          type="file"
          multiple
          accept={acceptedTypes.join(',')}
          onChange={(e) => handleFileSelect(e.target.files)}
          disabled={images.length >= maxImages}
          className="hidden"
        />

        {/* Drop Zone */}
        <div
          ref={dropZoneRef}
          className={cn(
            "border-2 border-dashed rounded-lg p-6 text-center transition-colors cursor-pointer",
            isDragOver 
              ? "border-blue-500 bg-blue-50" 
              : "border-gray-300 hover:border-gray-400 hover:bg-gray-50",
            // disabled && "opacity-50 cursor-not-allowed" // This prop was removed from interface
          )}
          onDragOver={handleDragOver}
          onDragLeave={handleDragLeave}
          onDrop={handleDrop}
          onClick={() => fileInputRef.current?.click()}
        >
          <Upload className="h-8 w-8 mx-auto mb-2 text-gray-400" />
          <p className="text-sm text-gray-600">
            {isDragOver 
              ? "Drop images here" 
              : "Drag & drop images here or click to browse"
            }
          </p>
          <p className="text-xs text-gray-500 mt-1">
            Max {maxImages} files, {maxSizeMB}MB each. Supported: JPEG, PNG, WebP
          </p>
        </div>
      </div>

      {/* Error Display */}
      {error && (
        <Card className="border-red-200 bg-red-50">
          <CardContent className="p-3">
            <p className="text-sm text-red-700">{error}</p>
          </CardContent>
        </Card>
      )}

      {/* Image Grid */}
      {images.length > 0 && (
        <div className="space-y-4">
          <div className="flex justify-between items-center">
            <h4 className="font-medium">Attached Images ({images.length}/{maxImages})</h4>
            <Button
              onClick={generateSummary}
              disabled={isProcessing}
              size="sm"
              className="flex items-center gap-2"
            >
              <Sparkles className="h-4 w-4" />
              {isProcessing ? 'Processing...' : 'Generate AI Summary'}
            </Button>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {images.map((image) => (
              <Card key={image.name} className="overflow-hidden">
                <CardContent className="p-0">
                  {/* Image Preview */}
                  <div className="relative aspect-square bg-gray-100">
                    <img
                      src={URL.createObjectURL(image)}
                      alt={image.name}
                      className="w-full h-full object-cover"
                    />

                    {/* Remove Button */}
                    <Button
                      onClick={() => removeImage(image.name)}
                      variant="destructive"
                      size="sm"
                      className="absolute top-2 right-2 h-6 w-6 p-0"
                      // disabled={disabled} // This prop was removed from interface
                    >
                      <X className="h-3 w-3" />
                    </Button>

                    {/* Processing Indicator */}
                    {/* This part of the logic needs to be adapted to the new ImageFile interface */}
                    {/* For now, we'll just show a placeholder */}
                    {/* {image.isProcessing && (
                      <div className="absolute inset-0 bg-black/50 flex items-center justify-center">
                        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-white" />
                      </div>
                    )} */}
                  </div>

                  {/* Image Info */}
                  <div className="p-3 space-y-2">
                    <div className="flex items-center gap-2">
                      {getFileTypeIcon(image.type)}
                      <span className="text-sm font-medium truncate" title={image.name}>
                        {image.name}
                      </span>
                    </div>

                    <div className="flex items-center justify-between text-xs text-gray-500">
                      <span>{formatFileSize(image.size)}</span>
                      <span>{new Date().toLocaleDateString()}</span> {/* Placeholder for uploadedAt */}
                    </div>

                    {/* Individual Image Summary */}
                    {/* This part of the logic needs to be adapted to the new ImageFile interface */}
                    {/* For now, we'll just show a placeholder */}
                    {/* {image.summary && (
                      <div className="text-xs text-gray-600 bg-gray-50 p-2 rounded">
                        <strong>AI Summary:</strong> {image.summary}
                      </div>
                    )} */}

                    {/* Actions */}
                    <div className="flex gap-1">
                      <Button
                        variant="outline"
                        size="sm"
                        className="flex-1 h-8 text-xs"
                        onClick={() => window.open(URL.createObjectURL(image), '_blank')}
                        // disabled={disabled} // This prop was removed from interface
                      >
                        <Eye className="h-3 w-3 mr-1" />
                        Preview
                      </Button>

                      <Button
                        variant="outline"
                        size="sm"
                        className="flex-1 h-8 text-xs"
                        onClick={() => {
                          const link = document.createElement('a')
                          link.href = URL.createObjectURL(image)
                          link.download = image.name
                          link.click()
                        }}
                        // disabled={disabled} // This prop was removed from interface
                      >
                        <Download className="h-3 w-3 mr-1" />
                        Download
                      </Button>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        </div>
      )}

      {/* AI Summary */}
      {summary && (
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-2 mb-3">
              <Sparkles className="h-5 w-5 text-blue-500" />
              <h4 className="font-medium">AI-Generated Summary</h4>
            </div>

            <Textarea
              value={summary}
              onChange={(e: React.ChangeEvent<HTMLTextAreaElement>) => {
                setSummary(e.target.value)
                // onSummaryChange?.(e.target.value) // This prop was removed from interface
              }}
              placeholder="AI summary will appear here..."
              className="min-h-[100px]"
              // disabled={disabled} // This prop was removed from interface
            />

            <p className="text-xs text-gray-500 mt-2">
              This summary was generated by AI based on your attached images. 
              You can edit it to better describe your service request.
            </p>
          </CardContent>
        </Card>
      )}

      {/* Instructions */}
      {images.length === 0 && (
        <Card className="border-dashed">
          <CardContent className="p-4 text-center text-muted-foreground">
            <Image className="h-8 w-8 mx-auto mb-2 text-gray-400" />
            <p>No images attached yet</p>
            <p className="text-sm mt-1">
              Attach images to help providers understand your service request better
            </p>
          </CardContent>
        </Card>
      )}
    </div>
  )
}

'use client'

import React, { useState } from 'react'
import { Button } from '@/components/ui/button'
import { LoadingSkeleton, ButtonSkeleton } from '@/components/ui/LoadingSkeleton'
import { useOptimisticActionWithTelemetry } from '@/hooks/useOptimisticActionWithTelemetry'
import { useDebouncedClickWithTelemetry } from '@/hooks/useDebouncedClickWithTelemetry'
import { useResilientQuery } from '@/hooks/useResilientQuery'
import { CheckCircle, CreditCard, AlertCircle, Loader2 } from 'lucide-react'

interface PaymentDetails {
  amount: number
  currency: string
  description: string
}

interface ResilientPaymentButtonProps {
  onPaymentSuccessAction: (paymentId: string) => void
  onPaymentErrorAction: (error: Error) => void
  paymentDetails: PaymentDetails
  onSuccess?: (paymentId: string) => void
  onError?: (error: Error) => void
  className?: string
  disabled?: boolean
}

export function ResilientPaymentButton({
  onPaymentSuccessAction,
  onPaymentErrorAction,
  paymentDetails,
  className = '',
  disabled = false
}: ResilientPaymentButtonProps) {
  const [paymentStatus, setPaymentStatus] = useState<'idle' | 'processing' | 'success' | 'failed'>('idle')
  const [paymentId, setPaymentId] = useState<string | null>(null)

  // 1. OPTIMISTIC PAYMENT ACTION with TELEMETRY
  const { execute: executePayment, status, error, rollback } = useOptimisticActionWithTelemetry({
    action: async (details: PaymentDetails) => {
      // Simulate payment processing
      const response = await fetch('/api/payments/process', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(details)
      })

      if (!response.ok) {
        throw new Error('Payment failed')
      }

      const result = await response.json()
      return result.paymentId
    },
    onOptimistic: (details: PaymentDetails) => {
      setPaymentStatus('processing')
      setPaymentId(null)
    },
    onSuccess: (paymentId: string, details: PaymentDetails) => {
      setPaymentStatus('success')
      setPaymentId(paymentId)
      onPaymentSuccessAction(paymentId)
    },
    onError: (error: Error, details: PaymentDetails) => {
      setPaymentStatus('failed')
      onPaymentErrorAction(error)
    },
    onRollback: (details: PaymentDetails) => {
      setPaymentStatus('idle')
      setPaymentId(null)
    },
    component: 'ResilientPaymentButton' // Required for telemetry
  })

  // 2. DEBOUNCED CLICK (prevents double-payments) with TELEMETRY
  const debouncedPayment = useDebouncedClickWithTelemetry(() => executePayment(paymentDetails), {
    delay: 500,
    onDuplicate: () => {
      console.log('Payment already in progress, ignoring duplicate click')
    },
    component: 'ResilientPaymentButton' // Required for telemetry
  })

  // 3. RESILIENT QUERY for payment status confirmation
  const { data: confirmedPayment, isLoading: isConfirming } = useResilientQuery({
    key: ['payment-confirmation', paymentId || ''],
    fetcher: async () => {
      if (!paymentId) throw new Error('No payment ID')

      const response = await fetch(`/api/payments/${paymentId}/status`)
      if (!response.ok) throw new Error('Failed to confirm payment')

      return response.json()
    },
    enabled: !!paymentId && paymentStatus === 'success',
    retry: { attempts: 3, backoff: 'exponential', delay: 1000 },
    staleTime: 5000
  })

  // 4. LOADING SKELETONS for different states
  if (status === 'optimistic' || paymentStatus === 'processing') {
    return (
      <div className="space-y-2">
        <ButtonSkeleton size="lg" className="w-full" />
        <div className="text-center">
          <LoadingSkeleton width={120} height={14} />
        </div>
      </div>
    )
  }

  // 5. SUCCESS STATE with confirmation
  if (paymentStatus === 'success') {
    return (
      <div className="space-y-3">
        <Button disabled className="w-full bg-green-600 text-white">
          <CheckCircle className="w-4 h-4 mr-2" />
          Payment Successful!
        </Button>

        {isConfirming && (
          <div className="text-center text-sm text-gray-600">
            <Loader2 className="w-4 h-4 inline animate-spin mr-2" />
            Confirming payment...
          </div>
        )}

        {confirmedPayment && (
          <div className="text-center text-sm text-green-600">
            ‚úì Payment confirmed on blockchain
          </div>
        )}
      </div>
    )
  }

  // 6. ERROR STATE with retry
  if (paymentStatus === 'failed' || error) {
    return (
      <div className="space-y-3">
        <Button 
          onClick={() => debouncedPayment()}
          variant="destructive"
          className="w-full"
        >
          <AlertCircle className="w-4 h-4 mr-2" />
          Payment Failed - Try Again
        </Button>

        <div className="text-center text-sm text-red-600">
          {error?.message || 'Something went wrong. Your card was not charged.'}
        </div>

        <Button 
          onClick={() => rollback(paymentDetails)}
          variant="outline"
          size="sm"
          className="w-full"
        >
          Reset Payment
        </Button>
      </div>
    )
  }

  // 7. DEFAULT STATE - Payment button
  return (
    <Button
      onClick={() => debouncedPayment()}
      disabled={disabled || status === 'loading'}
      className={`w-full ${className}`}
      size="lg"
    >
      <CreditCard className="w-4 h-4 mr-2" />
      Pay {paymentDetails.currency} {paymentDetails.amount}
    </Button>
  )
}

