'use client'

import React, { useState, useEffect } from 'react'
import { Button } from '@/components/ui/button'
import { LoadingSkeleton, CardSkeleton, GridSkeleton } from '@/components/ui/LoadingSkeleton'
import { useOptimisticActionWithTelemetry } from '@/hooks/useOptimisticActionWithTelemetry'
import { useDebouncedClickWithTelemetry } from '@/hooks/useDebouncedClickWithTelemetry'
import { useResilientQuery } from '@/hooks/useResilientQuery'
import { useErrorBoundary } from '@/components/ui/ErrorBoundary'
import { CheckCircle, XCircle, AlertTriangle, Loader2, RefreshCw } from 'lucide-react'

interface ServiceRequest {
  id: string
  customer_id: string
  customer_name: string
  service_type: string
  location: string
  details?: string
  status: 'pending' | 'accepted' | 'declined' | 'expired'
  created_at: string
}

interface ResilientVendorInboxProps {
  vendorId: string
  className?: string
}

export function ResilientVendorInbox({
  vendorId,
  className = ''
}: ResilientVendorInboxProps) {
  const [requests, setRequests] = useState<ServiceRequest[]>([])
  const [optimisticUpdates, setOptimisticUpdates] = useState<Map<string, Partial<ServiceRequest>>>(new Map())
  const { error, handleError, resetError, hasError } = useErrorBoundary()

  // 1. RESILIENT QUERY for fetching service requests
  const { 
    data: fetchedRequests, 
    isLoading, 
    error: queryError, 
    refetch, 
    retry 
  } = useResilientQuery({
    key: ['vendor-requests', vendorId],
    fetcher: async () => {
      const response = await fetch(`/api/vendor/requests?vendor_id=${vendorId}`)
      if (!response.ok) throw new Error('Failed to fetch requests')
      return response.json()
    },
    retry: { attempts: 3, backoff: 'exponential', delay: 1000 },
    staleTime: 10000, // 10 seconds
    cacheTime: 60000   // 1 minute
  })

  // 2. OPTIMISTIC ACCEPT ACTION with TELEMETRY
  const { execute: executeAccept, status: acceptStatus } = useOptimisticActionWithTelemetry({
    action: async (requestId: string) => {
      const response = await fetch('/api/vendor/requests/accept', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ requestId, vendorId })
      })

      if (!response.ok) {
        throw new Error('Failed to accept request')
      }

      return response.json()
    },
    onOptimistic: (requestId: string) => {
      // Optimistically update the request status
      setOptimisticUpdates(prev => new Map(prev).set(requestId, { status: 'accepted' }))

      // Update local state immediately
      setRequests(prev => prev.map(req => 
        req.id === requestId ? { ...req, status: 'accepted' } : req
      ))
    },
    onSuccess: () => {
      // Clear optimistic update
      setOptimisticUpdates(prev => {
        const newMap = new Map(prev)
        newMap.clear()
        return newMap
      })

      // Refresh data to ensure consistency
      refetch()
    },
    onError: (error: Error, requestId: string) => {
      // Rollback optimistic update
      setOptimisticUpdates(prev => {
        const newMap = new Map(prev)
        newMap.delete(requestId)
        return newMap
      })

      // Rollback local state
      setRequests(prev => prev.map(req => 
        req.id === requestId ? { ...req, status: 'pending' } : req
      ))

      handleError(error)
    },
    component: 'ResilientVendorInbox' // Required for telemetry
  })

  // 3. OPTIMISTIC DECLINE ACTION with TELEMETRY
  const { execute: executeDecline, status: declineStatus } = useOptimisticActionWithTelemetry({
    action: async (requestId: string) => {
      const response = await fetch('/api/vendor/requests/decline', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ requestId, vendorId })
      })

      if (!response.ok) {
        throw new Error('Failed to decline request')
      }

      return response.json()
    },
    onOptimistic: (requestId: string) => {
      // Optimistically update the request status
      setOptimisticUpdates(prev => new Map(prev).set(requestId, { status: 'declined' }))

      // Update local state immediately
      setRequests(prev => prev.map(req => 
        req.id === requestId ? { ...req, status: 'declined' } : req
      ))
    },
    onSuccess: () => {
      // Clear optimistic update
      setOptimisticUpdates(prev => {
        const newMap = new Map(prev)
        newMap.clear()
        return newMap
      })

      // Refresh data to ensure consistency
      refetch()
    },
    onError: (error: Error, requestId: string) => {
      // Rollback optimistic update
      setOptimisticUpdates(prev => {
        const newMap = new Map(prev)
        newMap.delete(requestId)
        return newMap
      })

      // Rollback local state
      setRequests(prev => prev.map(req => 
        req.id === requestId ? { ...req, status: 'pending' } : req
      ))

      handleError(error)
    },
    component: 'ResilientVendorInbox' // Required for telemetry
  })

  // 4. DEBOUNCED CLICKS (prevents duplicate actions) with TELEMETRY
  const debouncedAccept = useDebouncedClickWithTelemetry(executeAccept, {
    delay: 300,
    onDuplicate: () => {
      console.log('Accept already in progress, ignoring duplicate click')
    },
    component: 'ResilientVendorInbox' // Required for telemetry
  })

  const debouncedDecline = useDebouncedClickWithTelemetry(executeDecline, {
    delay: 300,
    onDuplicate: () => {
      console.log('Decline already in progress, ignoring duplicate click')
    },
    component: 'ResilientVendorInbox' // Required for telemetry
  })

  // 5. UPDATE REQUESTS WHEN DATA CHANGES
  useEffect(() => {
    if (fetchedRequests?.requests) {
      setRequests(fetchedRequests.requests)
    }
  }, [fetchedRequests])

  // 6. ERROR HANDLING
  useEffect(() => {
    if (queryError) {
      handleError(queryError)
    }
  }, [queryError, handleError])

  // 7. LOADING STATE
  if (isLoading) {
    return (
      <div className={`space-y-4 ${className}`}>
        <div className="flex items-center justify-between">
          <h2 className="text-xl font-semibold">Service Requests</h2>
          <LoadingSkeleton width={80} height={32} />
        </div>
        <GridSkeleton rows={3} cols={1} />
      </div>
    )
  }

  // 8. ERROR STATE
  if (hasError) {
    return (
      <div className={`text-center p-8 ${className}`}>
        <AlertTriangle className="w-12 h-12 text-red-500 mx-auto mb-4" />
        <h3 className="text-lg font-semibold text-gray-900 mb-2">
          Failed to load requests
        </h3>
        <p className="text-gray-600 mb-4">
          {error?.message || 'Something went wrong while loading your service requests.'}
        </p>
        <div className="flex gap-3 justify-center">
          <Button onClick={retry} variant="default">
            <RefreshCw className="w-4 h-4 mr-2" />
            Try Again
          </Button>
          <Button onClick={resetError} variant="outline">
            Reset
          </Button>
        </div>
      </div>
    )
  }

  // 9. EMPTY STATE
  if (requests.length === 0) {
    return (
      <div className={`text-center p-8 ${className}`}>
        <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
          <Loader2 className="w-8 h-8 text-gray-400" />
        </div>
        <h3 className="text-lg font-semibold text-gray-900 mb-2">
          No service requests yet
        </h3>
        <p className="text-gray-600">
          When customers request your services, they'll appear here.
        </p>
      </div>
    )
  }

  // 10. REQUESTS LIST
  return (
    <div className={`space-y-4 ${className}`}>
      <div className="flex items-center justify-between">
        <h2 className="text-xl font-semibold">Service Requests ({requests.length})</h2>
        <Button onClick={() => refetch()} variant="outline" size="sm">
          <RefreshCw className="w-4 h-4 mr-2" />
          Refresh
        </Button>
      </div>

      <div className="space-y-3">
        {requests.map((request) => {
          const isOptimistic = optimisticUpdates.has(request.id)
          const optimisticStatus = optimisticUpdates.get(request.id)?.status
          const displayStatus = optimisticStatus || request.status

          return (
            <div
              key={request.id}
              className={`p-4 border rounded-lg transition-all ${
                isOptimistic ? 'opacity-75 bg-blue-50' : 'bg-white'
              }`}
            >
              <div className="flex items-start justify-between">
                <div className="flex-1">
                  <h4 className="font-medium text-gray-900">
                    {request.customer_name}
                  </h4>
                  <p className="text-sm text-gray-600">
                    {request.service_type} ‚Ä¢ {request.location}
                  </p>
                  {request.details && (
                    <p className="text-sm text-gray-500 mt-1">
                      {request.details}
                    </p>
                  )}
                  <p className="text-xs text-gray-400 mt-2">
                    {new Date(request.created_at).toLocaleDateString()}
                  </p>
                </div>

                <div className="ml-4 flex gap-2">
                  {displayStatus === 'pending' && (
                    <>
                      <Button
                        onClick={() => debouncedAccept(request.id)}
                        disabled={acceptStatus === 'loading'}
                        size="sm"
                        className="bg-green-600 hover:bg-green-700"
                      >
                        {acceptStatus === 'loading' ? (
                          <Loader2 className="w-4 h-4 animate-spin" />
                        ) : (
                          <CheckCircle className="w-4 h-4" />
                        )}
                      </Button>
                      <Button
                        onClick={() => debouncedDecline(request.id)}
                        disabled={declineStatus === 'loading'}
                        size="sm"
                        variant="outline"
                      >
                        {declineStatus === 'loading' ? (
                          <Loader2 className="w-4 h-4 animate-spin" />
                        ) : (
                          <XCircle className="w-4 h-4" />
                        )}
                      </Button>
                    </>
                  )}

                  {displayStatus === 'accepted' && (
                    <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-green-100 text-green-800">
                      <CheckCircle className="w-3 h-3 mr-1" />
                      Accepted
                    </span>
                  )}

                  {displayStatus === 'declined' && (
                    <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-red-100 text-red-800">
                      <XCircle className="w-3 h-3 mr-1" />
                      Declined
                    </span>
                  )}
                </div>
              </div>

              {isOptimistic && (
                <div className="mt-2 text-xs text-blue-600">
                  Processing...
                </div>
              )}
            </div>
          )
        })}
      </div>
    </div>
  )
}

'use client'

import React, { useState, useEffect, useCallback } from 'react'
import Image from 'next/image'
import StarRating from '@/components/ui/StarRating'

interface Review {
  id: string
  rating: number
  overall_quality?: number
  review_text?: string
  service_quality?: number
  communication?: number
  punctuality?: number
  value_for_money?: number
  would_recommend?: boolean
  photos?: string[]
  created_at: string
  status: string
  spam_score?: number
  helpful_count?: number
  reported_count?: number
  users: {
    name: string
    avatar_url?: string
  }
  bookings: {
    service_name: string
    service_date: string
  }
  responses?: ReviewResponse[]
}

interface ReviewResponse {
  id: string
  response_text: string
  is_public: boolean
  created_at: string
  provider: {
    name: string
    avatar_url?: string
  }
}

interface ReviewSystemProps {
  vendorId: string
  showSubmissionForm?: boolean
  bookingId?: string
  userId?: string
  showModerationTools?: boolean
}

export default function ReviewSystem({ 
  vendorId, 
  showSubmissionForm = false,
  bookingId,
  userId,
  showModerationTools = false
}: ReviewSystemProps) {
  const [reviews, setReviews] = useState<Review[]>([])
  const [loading, setLoading] = useState(true)
  const [submitting, setSubmitting] = useState(false)
  const [currentPage, setCurrentPage] = useState(0)
  const [hasMore, setHasMore] = useState(true)
  const [showForm, setShowForm] = useState(showSubmissionForm)

  // Form state
  const [rating, setRating] = useState(0)
  const [reviewText, setReviewText] = useState('')
  const [serviceQuality, setServiceQuality] = useState(0)
  const [communication, setCommunication] = useState(0)
  const [punctuality, setPunctuality] = useState(0)
  const [valueForMoney, setValueForMoney] = useState(0)
  const [wouldRecommend, setWouldRecommend] = useState<boolean | null>(null)
  const [photos, setPhotos] = useState<string[]>([])

  const loadReviews = useCallback(async (page = 0) => {
    try {
      setLoading(true)
      const response = await fetch(
        `/api/reviews/create?vendor_id=${vendorId}&limit=10&offset=${page * 10}`
      )
      const data = await response.json()

      if (data.reviews) {
        if (page === 0) {
          setReviews(data.reviews)
        } else {
          setReviews(prev => [...prev, ...data.reviews])
        }
        setHasMore(data.pagination.has_more)
      }
    } catch (error) {
      console.error('Failed to load reviews:', error)
    } finally {
      setLoading(false)
    }
  }, [vendorId])

  // Load reviews on mount or when vendorId changes
  useEffect(() => {
    loadReviews()
  }, [loadReviews])

  const submitReview = async () => {
    if (!rating || !bookingId || !userId) return

    try {
      setSubmitting(true)

      const response = await fetch('/api/reviews/create', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          booking_id: bookingId,
          user_id: userId,
          vendor_id: vendorId,
          rating,
          review_text: reviewText || null,
          service_quality: serviceQuality || null,
          communication: communication || null,
          punctuality: punctuality || null,
          value_for_money: valueForMoney || null,
          would_recommend: wouldRecommend,
          photos: photos.length > 0 ? photos : null
        })
      })

      const result = await response.json()

      if (result.success) {
        // Reset form
        setRating(0)
        setReviewText('')
        setServiceQuality(0)
        setCommunication(0)
        setPunctuality(0)
        setValueForMoney(0)
        setWouldRecommend(null)
        setPhotos([])
        setShowForm(false)

        // Reload reviews
        loadReviews(0)

        alert('Review submitted successfully!')
      } else {
        alert(`Error: ${result.error}`)
      }
    } catch (error) {
      console.error('Failed to submit review:', error)
      alert('Failed to submit review. Please try again.')
    } finally {
      setSubmitting(false)
    }
  }

  const handlePhotoUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = event.target.files
    if (files) {
      // In a real implementation, you'd upload these to a storage service
      // For now, we'll just create placeholder URLs
      const newPhotos = Array.from(files).map(file => URL.createObjectURL(file))
      setPhotos(prev => [...prev, ...newPhotos])
    }
  }

  const removePhoto = (index: number) => {
    setPhotos(prev => prev.filter((_, i) => i !== index))
  }

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    })
  }

  const calculateAverageRating = () => {
    if (reviews.length === 0) return 0
    const publishedReviews = reviews.filter(r => r.status === 'published')
    if (publishedReviews.length === 0) return 0

    const sum = publishedReviews.reduce((acc, review) => acc + (review.overall_quality || review.rating), 0)
    return (sum / publishedReviews.length).toFixed(1)
  }

  const getRatingDistribution = () => {
    const distribution = { 5: 0, 4: 0, 3: 0, 2: 0, 1: 0 }
    const publishedReviews = reviews.filter(r => r.status === 'published')

    publishedReviews.forEach(review => {
      const rating = Math.round(review.overall_quality || review.rating)
      if (rating >= 1 && rating <= 5) {
        distribution[rating as keyof typeof distribution]++
      }
    })
    return distribution
  }

  const getPublishedReviews = () => {
    return reviews.filter(review => review.status === 'published')
  }

  const getPendingReviews = () => {
    return reviews.filter(review => review.status === 'pending')
  }

  const getFlaggedReviews = () => {
    return reviews.filter(review => review.status === 'flagged')
  }

  return (
    <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
      {/* Header */}
      <div className="flex items-center justify-between mb-6">
        <div>
          <h3 className="text-2xl font-bold text-gray-900">
            Customer Reviews
          </h3>
          <div className="flex items-center gap-4 mt-2">
            <div className="flex items-center gap-2">
              <StarRating 
                value={Number(calculateAverageRating())} 
                readonly 
                size="large" 
                allowHalfStars={true}
              />
              <span className="text-2xl font-bold text-gray-900">
                {calculateAverageRating()}
              </span>
            </div>
            <span className="text-gray-600">
              ({getPublishedReviews().length} review{getPublishedReviews().length !== 1 ? 's' : ''})
            </span>
          </div>
        </div>

        {showSubmissionForm && !showForm && (
          <button
            onClick={() => setShowForm(true)}
            className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
          >
            Write Review
          </button>
        )}
      </div>

      {/* Rating Distribution */}
      {getPublishedReviews().length > 0 && (
        <div className="mb-8">
          <h4 className="text-lg font-semibold mb-4">Rating Breakdown</h4>
          <div className="space-y-2">
            {Object.entries(getRatingDistribution())
              .reverse()
              .map(([rating, count]) => (
                <div key={rating} className="flex items-center gap-3">
                  <span className="text-sm font-medium w-8">{rating} ‚òÖ</span>
                  <div className="flex-1 bg-gray-200 rounded-full h-2">
                    <div
                      className="bg-yellow-400 h-2 rounded-full transition-all duration-300"
                      style={{
                        width: `${getPublishedReviews().length > 0 ? (count / getPublishedReviews().length) * 100 : 0}%`
                      }}
                    />
                  </div>
                  <span className="text-sm text-gray-600 w-8">{count}</span>
                </div>
              ))}
          </div>
        </div>
      )}

      {/* Moderation Summary (if enabled) */}
      {showModerationTools && (
        <div className="mb-6 p-4 bg-yellow-50 border border-yellow-200 rounded-lg">
          <h4 className="text-lg font-semibold text-yellow-800 mb-2">Moderation Summary</h4>
          <div className="grid grid-cols-3 gap-4 text-sm">
            <div>
              <span className="text-yellow-700">Pending:</span>
              <span className="ml-2 font-semibold">{getPendingReviews().length}</span>
            </div>
            <div>
              <span className="text-yellow-700">Flagged:</span>
              <span className="ml-2 font-semibold">{getFlaggedReviews().length}</span>
            </div>
            <div>
              <span className="text-yellow-700">Published:</span>
              <span className="ml-2 font-semibold">{getPublishedReviews().length}</span>
            </div>
          </div>
        </div>
      )}

      {/* Review Submission Form */}
      {showForm && (
        <div className="bg-gray-50 rounded-xl p-6 mb-8">
          <h4 className="text-lg font-semibold mb-4">Share Your Experience</h4>

          <div className="space-y-6">
            {/* Overall Rating */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Overall Rating *
              </label>
              <StarRating 
                value={rating} 
                onChange={setRating} 
                size="large" 
                allowHalfStars={true}
              />
            </div>

            {/* Detailed Ratings */}
            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Service Quality
                </label>
                <StarRating 
                  value={serviceQuality} 
                  onChange={setServiceQuality} 
                  allowHalfStars={true}
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Communication
                </label>
                <StarRating 
                  value={communication} 
                  onChange={setCommunication} 
                  allowHalfStars={true}
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Punctuality
                </label>
                <StarRating 
                  value={punctuality} 
                  onChange={setPunctuality} 
                  allowHalfStars={true}
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Value for Money
                </label>
                <StarRating 
                  value={valueForMoney} 
                  onChange={setValueForMoney} 
                  allowHalfStars={true}
                />
              </div>
            </div>

            {/* Review Text */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Your Review
              </label>
              <textarea
                value={reviewText}
                onChange={(e) => setReviewText(e.target.value)}
                rows={4}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                placeholder="Share details about your experience..."
              />
            </div>

            {/* Photo Upload */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Add Photos (Optional)
              </label>
              <input
                type="file"
                multiple
                accept="image/*"
                onChange={handlePhotoUpload}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
              {photos.length > 0 && (
                <div className="mt-3 flex flex-wrap gap-2">
                  {photos.map((photo, index) => (
                    <div key={index} className="relative">
                      <img
                        src={photo}
                        alt={`Review photo ${index + 1}`}
                        className="w-20 h-20 object-cover rounded-lg"
                      />
                      <button
                        onClick={() => removePhoto(index)}
                        className="absolute -top-2 -right-2 w-6 h-6 bg-red-500 text-white rounded-full text-xs hover:bg-red-600"
                      >
                        √ó
                      </button>
                    </div>
                  ))}
                </div>
              )}
            </div>

            {/* Recommendation */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Would you recommend this provider?
              </label>
              <div className="flex gap-4">
                <button
                  type="button"
                  onClick={() => setWouldRecommend(true)}
                  className={`px-4 py-2 rounded-lg border transition-colors ${
                    wouldRecommend === true
                      ? 'bg-green-100 border-green-500 text-green-700'
                      : 'border-gray-300 text-gray-700 hover:bg-gray-50'
                  }`}
                >
                  üëç Yes
                </button>
                <button
                  type="button"
                  onClick={() => setWouldRecommend(false)}
                  className={`px-4 py-2 rounded-lg border transition-colors ${
                    wouldRecommend === false
                      ? 'bg-red-100 border-red-500 text-red-700'
                      : 'border-gray-300 text-gray-700 hover:bg-gray-50'
                  }`}
                >
                  üëé No
                </button>
              </div>
            </div>

            {/* Submit Buttons */}
            <div className="flex gap-3">
              <button
                onClick={submitReview}
                disabled={!rating || submitting}
                className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
              >
                {submitting ? 'Submitting...' : 'Submit Review'}
              </button>
              <button
                onClick={() => setShowForm(false)}
                className="px-6 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-colors"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Reviews List */}
      <div className="space-y-6">
        {loading && reviews.length === 0 ? (
          <div className="text-center py-8">
            <div className="animate-spin w-8 h-8 border-2 border-blue-600 border-t-transparent rounded-full mx-auto mb-4"></div>
            <p className="text-gray-600">Loading reviews...</p>
          </div>
        ) : getPublishedReviews().length === 0 ? (
          <div className="text-center py-8">
            <p className="text-gray-600">No reviews yet. Be the first to share your experience!</p>
          </div>
        ) : (
          getPublishedReviews().map((review) => (
            <div key={review.id} className="border-b border-gray-200 pb-6">
              <div className="flex items-start gap-4">
                <div className="w-10 h-10 bg-blue-100 rounded-full flex items-center justify-center">
                  {review.users.avatar_url ? (
                    <Image
                      src={review.users.avatar_url}
                      alt={review.users.name}
                      width={40}
                      height={40}
                      className="w-10 h-10 rounded-full"
                    />
                  ) : (
                    <span className="text-blue-600 font-semibold">
                      {review.users.name.charAt(0).toUpperCase()}
                    </span>
                  )}
                </div>

                <div className="flex-1">
                  <div className="flex items-center justify-between mb-2">
                    <div>
                      <h5 className="font-semibold text-gray-900">
                        {review.users.name}
                      </h5>
                      <p className="text-sm text-gray-600">
                        {review.bookings.service_name} ‚Ä¢ {formatDate(review.bookings.service_date)}
                      </p>
                    </div>
                    <div className="text-right">
                      <span className="text-sm text-gray-500">
                        {formatDate(review.created_at)}
                      </span>
                      {review.spam_score && review.spam_score > 0.7 && (
                        <div className="text-xs text-red-600 mt-1">
                          ‚ö†Ô∏è High spam score
                        </div>
                      )}
                    </div>
                  </div>

                  <div className="mb-3">
                    <StarRating 
                      value={review.overall_quality || review.rating} 
                      readonly 
                      allowHalfStars={true}
                    />
                  </div>

                  {review.review_text && (
                    <p className="text-gray-700 mb-3">{review.review_text}</p>
                  )}

                  {/* Detailed Ratings */}
                  {(review.service_quality || review.communication || review.punctuality || review.value_for_money) && (
