        )}

        {/* Load More */}
        {hasMore && getPublishedReviews().length > 0 && (
          <div className="text-center">
            <button
              onClick={() => {
                const nextPage = currentPage + 1
                setCurrentPage(nextPage)
                loadReviews(nextPage)
              }}
