                // Empty state
                <div className="p-8 text-center text-gray-500">
                  <p className="text-sm">No specialties found</p>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => setShowSuggestionForm(true)}
                    className="mt-2"
                  >
                    <Plus className="h-4 w-4 mr-2" />
                    Suggest New
                  </Button>
                </div>
              )}
