export default function UserDashboard() {
  const [activeTab, setActiveTab] = useState<'overview' | 'bookings' | 'credits' | 'favorites' | 'profile'>('overview')
  const [userProfile, setUserProfile] = useState<UserProfile | null>(null)
  const [bookings, setBookings] = useState<Booking[]>([])
  const [credits, setCredits] = useState<CreditBalance | null>(null)
  const [favoriteProviders, setFavoriteProviders] = useState<Provider[]>([])
  const [searchQuery, setSearchQuery] = useState('')
  const [filterStatus, setFilterStatus] = useState<'all' | 'upcoming' | 'completed' | 'cancelled'>('all')
  const router = useRouter()
  const { t } = useI18n()
  // Tour functionality removed - users can start tour manually via GuidedTourManager button
  // const { startTour, hasCompletedTour } = useGuidedTour()

  // Use the new async state hook for notifications
  const notifications = useAsyncData<NotificationResponse['notifications']>({
    autoReset: true,
    resetDelay: 5000
  })

  const loadNotifications = useCallback(async (): Promise<void> => {
    const supabase = supabaseBrowserClient()
    if (!supabase) return

    try {
      const { data: { session } } = await supabase.auth.getSession()
      if (!session) {
        // Don't fetch notifications if not authenticated
        return
      }

      const response = await fetch('/api/notifications', {
        headers: { Authorization: `Bearer ${session.access_token}` }
      })
      if (!response.ok) {
        const errorData = await response.json() as NotificationError
        throw new Error(errorData.error || 'Failed to fetch notifications')
      }

      const data = await response.json() as NotificationResponse
      notifications.setData(data.notifications)
    } catch (error) {
      console.error('Failed to load notifications:', error)
      notifications.setError(error instanceof Error ? error.message : 'Failed to load notifications')
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []) // Intentionally empty - notifications object methods are stable

  // Load user data (profile, bookings, credits, favorites) in parallel
  useEffect(() => {
    const loadUserData = async () => {
      const supabase = supabaseBrowserClient()
      if (!supabase) return

      try {
        const { data: { session } } = await supabase.auth.getSession()
        if (!session?.user) {
          router.push('/login')
          return
        }

        // Load all data in parallel
        const [profileResult, bookingsResult, creditsResult, favoritesResult] = await Promise.allSettled([
          supabase
            .from('user_role_summary')
            .select('*')
            .eq('user_id', session.user.id)
            .maybeSingle(),
          supabase
            .from('bookings')
            .select('*')
            .eq('customer_id', session.user.id)
            .order('created_at', { ascending: false }),
          fetch('/api/credits/balance', {
            headers: { Authorization: `Bearer ${session.access_token}` }
          }).then(res => res.ok ? res.json() : null).catch(() => null),
          supabase
            .from('favorite_providers')
            .select('provider_id, providers(*)')
            .eq('user_id', session.user.id)
        ])

        // Handle profile - with fallback to profiles table if user_role_summary fails
        let profileData: UserProfile | null = null
        if (profileResult.status === 'fulfilled' && profileResult.value.data) {
          profileData = profileResult.value.data as UserProfile
        } else if (profileResult.status === 'rejected' || !profileResult.value?.data) {
          // Fallback: query profiles table directly
          try {
            const { data: fallbackProfile } = await supabase
              .from('profiles')
              .select('id, full_name, email, role, created_at, updated_at')
              .eq('id', session.user.id)
              .single()

            if (fallbackProfile) {
              // Map profiles table data to UserProfile format
              profileData = {
                id: fallbackProfile.id,
                name: fallbackProfile.full_name || fallbackProfile.email || 'User',
                email: fallbackProfile.email || '',
                phone: undefined,
                avatar_url: undefined,
                member_since: fallbackProfile.created_at || new Date().toISOString(),
                verification_status: 'unverified' as const,
                preferences: {
                  notifications: true,
                  marketing_emails: false,
                  sms_alerts: false
                },
                stats: {
                  total_bookings: 0,
                  total_spent_cents: 0,
                  favorite_providers: 0,
                  average_rating_given: 0
                }
              }
            }
          } catch (fallbackError) {
            console.error('Fallback profile query failed:', fallbackError)
          }
        }

        if (profileData) {
          setUserProfile(profileData)
        } else {
          // Create minimal profile if all queries fail to prevent infinite loading
          setUserProfile({
            id: session.user.id,
            name: session.user.email?.split('@')[0] || 'User',
            email: session.user.email || '',
            phone: undefined,
            avatar_url: undefined,
            member_since: new Date().toISOString(),
            verification_status: 'unverified' as const,
            preferences: {
              notifications: true,
              marketing_emails: false,
              sms_alerts: false
            },
            stats: {
              total_bookings: 0,
              total_spent_cents: 0,
              favorite_providers: 0,
              average_rating_given: 0
            }
          })
        }

        // Handle bookings
        if (bookingsResult.status === 'fulfilled' && bookingsResult.value.data) {
          setBookings(bookingsResult.value.data as Booking[])
        }

        // Handle credits
        if (creditsResult.status === 'fulfilled' && creditsResult.value?.success) {
          setCredits(creditsResult.value.credits)
        }

        // Handle favorites
        if (favoritesResult.status === 'fulfilled' && favoritesResult.value.data) {
          const providers = favoritesResult.value.data
            .map(fp => fp.providers)
            .filter(Boolean) as unknown as Provider[]
          setFavoriteProviders(providers)
        }

        // Load notifications
        loadNotifications()
      } catch (error) {
        console.error('Error loading user data:', error)
      }
    }

    loadUserData()
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [router]) // Only re-run if router changes

