'use client'

import { useState, useRef, useCallback, useEffect } from 'react'
import { Button } from '@/components/ui/button'
import { Card, CardContent } from '@/components/ui/card'
import { Badge } from '@/components/ui/badge'
import { Mic, MicOff, Volume2, VolumeX, RotateCcw } from 'lucide-react'
import { cn } from '@/lib/utils'

interface VoiceInputProps {
  onTranscriptAction: (transcript: string) => void;
  onError?: (error: string) => void;
  placeholder?: string;
  className?: string;
  disabled?: boolean;
  autoStart?: boolean;
}

interface SpeechRecognition extends EventTarget {
  continuous: boolean
  interimResults: boolean
  lang: string
  start(): void
  stop(): void
  abort(): void
  onresult: ((this: SpeechRecognition, ev: SpeechRecognitionEvent) => any) | null
  onerror: ((this: SpeechRecognition, ev: SpeechRecognitionErrorEvent) => any) | null
  onend: ((this: SpeechRecognition, ev: Event) => any) | null
  onstart: ((this: SpeechRecognition, ev: Event) => any) | null
}

interface SpeechRecognitionEvent {
  resultIndex: number
  results: SpeechRecognitionResultList
}

interface SpeechRecognitionResultList {
  length: number
  item(index: number): SpeechRecognitionResult
  [index: number]: SpeechRecognitionResult
}

interface SpeechRecognitionResult {
  isFinal: boolean
  length: number
  item(index: number): SpeechRecognitionAlternative
  [index: number]: SpeechRecognitionAlternative
}

interface SpeechRecognitionAlternative {
  transcript: string
  confidence: number
}

interface SpeechRecognitionErrorEvent {
  error: string
  message: string
}

declare global {
  interface Window {
    SpeechRecognition: new () => SpeechRecognition
    webkitSpeechRecognition: new () => SpeechRecognition
  }
}

export default function VoiceInput({ 
  onTranscriptAction, 
  onError, 
  disabled = false,
  className,
  placeholder = "Click to start voice input..."
}: VoiceInputProps) {
  const [isListening, setIsListening] = useState(false)
  const [isPaused, setIsPaused] = useState(false)
  const [transcript, setTranscript] = useState('')
  const [interimTranscript, setInterimTranscript] = useState('')
  const [confidence, setConfidence] = useState(0)
  const [error, setError] = useState<string | null>(null)
  const [isSupported, setIsSupported] = useState(false)
  
  const recognitionRef = useRef<SpeechRecognition | null>(null)
  const timeoutRef = useRef<NodeJS.Timeout | null>(null)
  const finalTranscriptRef = useRef('')

  // Check browser support
  useEffect(() => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition
    setIsSupported(!!SpeechRecognition)
    
    if (!SpeechRecognition) {
      setError('Speech recognition not supported in this browser')
      onError?.('Speech recognition not supported in this browser')
    }
  }, [onError])

  // Initialize speech recognition
  const initSpeechRecognition = useCallback(() => {
    if (!isSupported) return

    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition
    recognitionRef.current = new SpeechRecognition()
    
    const recognition = recognitionRef.current
    recognition.continuous = true
    recognition.interimResults = true
    recognition.lang = navigator.language || 'en-US'

    recognition.onstart = () => {
      setIsListening(true)
      setIsPaused(false)
      setError(null)
    }

    recognition.onresult = (event: SpeechRecognitionEvent) => {
      let interimTranscript = ''
      let finalTranscript = ''

      for (let i = event.resultIndex; i < event.results.length; i++) {
        const result = event.results[i]
        if (result.isFinal) {
          finalTranscript += result[0].transcript
        } else {
          interimTranscript += result[0].transcript
        }
      }

      // Update confidence for the most recent result
      if (event.results.length > 0) {
        const lastResult = event.results[event.results.length - 1]
        if (lastResult.length > 0) {
          setConfidence(lastResult[0].confidence)
        }
      }

      setInterimTranscript(interimTranscript)
      finalTranscriptRef.current += finalTranscript
      setTranscript(finalTranscriptRef.current)

      // Auto-stop after 30 seconds of silence or when confidence is high
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current)
      }
      
      timeoutRef.current = setTimeout(() => {
        if (isListening && confidence > 0.8) {
          stopListening()
        }
      }, 30000)
    }

    recognition.onerror = (event: SpeechRecognitionErrorEvent) => {
      console.error('Speech recognition error:', event.error)
      
      let errorMessage = 'Speech recognition error'
      switch (event.error) {
        case 'no-speech':
          errorMessage = 'No speech detected. Please try again.'
          break
        case 'audio-capture':
          errorMessage = 'Microphone access denied. Please check permissions.'
          break
        case 'not-allowed':
          errorMessage = 'Microphone access denied. Please allow microphone access.'
          break
        case 'network':
          errorMessage = 'Network error. Please check your connection.'
          break
        case 'service-not-allowed':
          errorMessage = 'Speech recognition service not available.'
          break
        default:
          errorMessage = `Speech recognition error: ${event.error}`
      }
      
      setError(errorMessage)
      onError?.(errorMessage)
      setIsListening(false)
      setIsPaused(false)
    }

    recognition.onend = () => {
      setIsListening(false)
      setIsPaused(false)
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current)
      }
    }
  }, [isSupported, onError])

  // Start listening
  const startListening = useCallback(() => {
    if (!isSupported || disabled) return
    
    try {
      if (!recognitionRef.current) {
        initSpeechRecognition()
      }
      
      if (recognitionRef.current) {
        recognitionRef.current.start()
        setTranscript('')
        setInterimTranscript('')
        finalTranscriptRef.current = ''
        setError(null)
      }
    } catch (err) {
      console.error('Failed to start speech recognition:', err)
      setError('Failed to start speech recognition')
      onError?.('Failed to start speech recognition')
    }
  }, [isSupported, disabled, initSpeechRecognition, onError])

  // Stop listening
  const stopListening = useCallback(() => {
    if (recognitionRef.current && isListening) {
      recognitionRef.current.stop()
    }
    setIsListening(false)
    setIsPaused(false)
    
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current)
    }
  }, [isListening])

  // Pause/resume listening
  const togglePause = useCallback(() => {
    if (!isListening) return
    
    if (isPaused) {
      // Resume
      if (recognitionRef.current) {
        recognitionRef.current.start()
      }
      setIsPaused(false)
    } else {
      // Pause
      if (recognitionRef.current) {
        recognitionRef.current.stop()
      }
      setIsPaused(true)
    }
  }, [isListening, isPaused])

  // Clear transcript
  const clearTranscript = useCallback(() => {
    setTranscript('')
    setInterimTranscript('')
    finalTranscriptRef.current = ''
    setError(null)
  }, [])

  // Confirm and send transcript
  const confirmTranscript = useCallback(() => {
    if (transcript.trim()) {
      onTranscriptAction(transcript.trim())
      clearTranscript()
    }
  }, [transcript, onTranscriptAction, clearTranscript])

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.stop()
      }
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current)
      }
    }
  }, [])

  if (!isSupported) {
    return (
      <Card className={cn("border-dashed", className)}>
        <CardContent className="p-4 text-center text-muted-foreground">
          <MicOff className="h-8 w-8 mx-auto mb-2 text-gray-400" />
          <p>Voice input not supported in this browser</p>
          <p className="text-sm">Try Chrome, Edge, or Safari</p>
        </CardContent>
      </Card>
    )
  }

  return (
    <div className={cn("space-y-4", className)}>
      {/* Voice Input Controls */}
      <div className="flex items-center gap-3">
        <Button
          onClick={isListening ? stopListening : startListening}
          disabled={disabled}
          variant={isListening ? "destructive" : "default"}
          size="lg"
          className="relative"
        >
          {isListening ? (
            <>
              <MicOff className="h-5 w-5 mr-2" />
              Stop
            </>
          ) : (
            <>
              <Mic className="h-5 w-5 mr-2" />
              Start Voice
            </>
          )}
          
          {isListening && (
            <div className="absolute -top-1 -right-1 w-3 h-3 bg-red-500 rounded-full animate-pulse" />
          )}
        </Button>

        {isListening && (
          <>
            <Button
              onClick={togglePause}
              variant="outline"
              size="sm"
              disabled={disabled}
            >
              {isPaused ? (
                <>
                  <Volume2 className="h-4 w-4 mr-2" />
                  Resume
                </>
              ) : (
                <>
                  <VolumeX className="h-4 w-4 mr-2" />
                  Pause
                </>
              )}
            </Button>

            <Button
              onClick={clearTranscript}
              variant="outline"
              size="sm"
              disabled={disabled}
            >
              <RotateCcw className="h-4 w-4 mr-2" />
              Clear
            </Button>
          </>
        )}
      </div>

      {/* Status Indicators */}
      {isListening && (
        <div className="flex items-center gap-4 text-sm">
          <Badge variant={isPaused ? "secondary" : "default"}>
            {isPaused ? "Paused" : "Listening..."}
          </Badge>
          
          {confidence > 0 && (
            <Badge variant="outline">
              Confidence: {Math.round(confidence * 100)}%
            </Badge>
          )}
          
          <div className="flex items-center gap-2">
            <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse" />
            <span className="text-muted-foreground">Voice detected</span>
          </div>
        </div>
      )}

      {/* Error Display */}
      {error && (
        <Card className="border-red-200 bg-red-50">
          <CardContent className="p-3">
            <p className="text-sm text-red-700">{error}</p>
          </CardContent>
        </Card>
      )}

      {/* Transcript Display */}
      {(transcript || interimTranscript) && (
        <Card>
          <CardContent className="p-4 space-y-3">
            <div className="flex justify-between items-center">
              <h4 className="font-medium">Voice Transcript</h4>
              <div className="flex gap-2">
                <Button
                  onClick={confirmTranscript}
                  size="sm"
                  disabled={!transcript.trim()}
                >
                  Confirm & Send
                </Button>
                <Button
                  onClick={clearTranscript}
                  variant="outline"
                  size="sm"
                >
                  Clear
                </Button>
              </div>
            </div>

            {/* Final transcript */}
            {transcript && (
              <div className="space-y-2">
                <p className="text-sm font-medium text-gray-700">Final:</p>
                <p className="p-3 bg-gray-50 rounded-lg border text-gray-900">
                  {transcript}
                </p>
              </div>
            )}

            {/* Interim transcript */}
            {interimTranscript && (
              <div className="space-y-2">
                <p className="text-sm font-medium text-gray-700">Interim:</p>
                <p className="p-3 bg-blue-50 rounded-lg border text-blue-900 italic">
                  {interimTranscript}
                </p>
              </div>
            )}

            {/* Confidence indicator */}
            {confidence > 0 && (
              <div className="flex items-center gap-2 text-sm text-muted-foreground">
                <div className="w-full bg-gray-200 rounded-full h-2">
                  <div
                    className="bg-blue-600 h-2 rounded-full transition-all duration-300"
                    style={{ width: `${confidence * 100}%` }}
                  />
                </div>
                <span>{Math.round(confidence * 100)}%</span>
              </div>
            )}
          </CardContent>
        </Card>
      )}

      {/* Instructions */}
      {!isListening && !transcript && (
        <Card className="border-dashed">
          <CardContent className="p-4 text-center text-muted-foreground">
            <Mic className="h-8 w-8 mx-auto mb-2 text-gray-400" />
            <p>{placeholder}</p>
            <p className="text-sm mt-1">
              Click the microphone button to start voice input
            </p>
          </CardContent>
        </Card>
      )}
    </div>
  )
}

'use client'

import React, { useState, useEffect, Suspense } from 'react'
import { useSearchParams } from 'next/navigation'
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Label } from '@/components/ui/label'
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select'
import { Badge } from '@/components/ui/badge'
import { Alert, AlertDescription } from '@/components/ui/alert'
import { Loader2, Eye, AlertTriangle, Calendar, User, Shield } from 'lucide-react'

interface AuditLog {
  id: number
  admin_user_id: string
  action: string
  resource_type: string
  resource_id: string
  old_values: any
  new_values: any
  ip_address: string
  user_agent: string
  request_id: string
  created_at: string
  profiles: {
    full_name: string
    email: string
  }
}

interface Pagination {
  page: number
  limit: number
  total: number
  totalPages: number
}

interface ApiResponse {
  data: AuditLog[]
  pagination: Pagination
  error?: string
  hint?: string
}

function AdminAuditViewerContent() {
  const searchParams = useSearchParams()
  const [auditLogs, setAuditLogs] = useState<AuditLog[]>([])
  const [pagination, setPagination] = useState<Pagination>({
    page: 1,
    limit: 50,
    total: 0,
    totalPages: 0
  })
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState<string | null>(null)
  const [errorHint, setErrorHint] = useState<string | null>(null)

  // Filters
  const [filters, setFilters] = useState({
    action: '',
    adminUserId: '',
    startDate: '',
    endDate: '',
    endpoint: ''
  })

  // Handle URL parameters for drill-through from performance dashboard
  useEffect(() => {
    const endpoint = searchParams.get('endpoint')
    const from = searchParams.get('from')
    const to = searchParams.get('to')

    if (endpoint || from || to) {
      setFilters(prev => ({
        ...prev,
        endpoint: endpoint || '',
        startDate: from || '',
        endDate: to || ''
      }))
    }
  }, [searchParams])

  const fetchAuditLogs = async (page = 1) => {
    setLoading(true)
    setError(null)
    setErrorHint(null)

    try {
      const params = new URLSearchParams({
        page: page.toString(),
        limit: pagination.limit.toString(),
        ...(filters.action && { action: filters.action }),
        ...(filters.adminUserId && { admin_user_id: filters.adminUserId }),
        ...(filters.startDate && { start_date: filters.startDate }),
        ...(filters.endDate && { end_date: filters.endDate }),
        ...(filters.endpoint && { endpoint: filters.endpoint })
      })

      const response = await fetch(`/api/admin/audit-log?${params}`)
      const data: ApiResponse = await response.json()

      if (!response.ok) {
        if (response.status === 403) {
          setError('Permission denied')
          setErrorHint(data.hint || 'Check admin role and RLS policies')
        } else {
          setError(data.error || 'Failed to fetch audit logs')
          setErrorHint(data.hint || null)
        }
        return
      }

      setAuditLogs(data.data)
      setPagination(data.pagination)
    } catch (err) {
      setError('Network error occurred')
      setErrorHint('Check network connection and try again')
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    fetchAuditLogs()
  }, [])

  const handleFilterChange = (key: string, value: string) => {
    setFilters(prev => ({ ...prev, [key]: value }))
  }

  const handleFilterSubmit = (e: React.FormEvent) => {
    e.preventDefault()
    setPagination(prev => ({ ...prev, page: 1 }))
    fetchAuditLogs(1)
  }

  const handlePageChange = (newPage: number) => {
    setPagination(prev => ({ ...prev, page: newPage }))
    fetchAuditLogs(newPage)
  }

  const getActionColor = (action: string) => {
    switch (action.toLowerCase()) {
      case 'create': return 'bg-green-100 text-green-800'
      case 'update': return 'bg-blue-100 text-blue-800'
      case 'delete': return 'bg-red-100 text-red-800'
      case 'approve': return 'bg-emerald-100 text-emerald-800'
      case 'reject': return 'bg-orange-100 text-orange-800'
      default: return 'bg-gray-100 text-gray-800'
    }
  }

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleString()
  }

  const truncateText = (text: string, maxLength: number = 100) => {
    if (text.length <= maxLength) return text
    return text.substring(0, maxLength) + '...'
  }

  if (error) {
    return (
      <Alert variant="destructive">
        <AlertTriangle className="h-4 w-4" />
        <AlertDescription>
          <div className="font-semibold">{error}</div>
          {errorHint && (
            <div className="mt-2 text-sm opacity-90">
              <Shield className="inline h-3 w-3 mr-1" />
              <strong>Hint:</strong> {errorHint}
            </div>
          )}
        </AlertDescription>
      </Alert>
    )
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold tracking-tight">Admin Audit Log</h2>
          <p className="text-muted-foreground">
            Monitor admin actions and troubleshoot RLS issues
          </p>
        </div>
        <Badge variant="outline" className="flex items-center gap-2">
          <Shield className="h-4 w-4" />
          RLS Protected
        </Badge>
      </div>

      {/* Filters */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Eye className="h-5 w-5" />
            Filters
          </CardTitle>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleFilterSubmit} className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <div>
              <Label htmlFor="action">Action</Label>
              <Select value={filters.action} onValueChange={(value) => handleFilterChange('action', value)}>
                <SelectTrigger>
                  <SelectValue placeholder="All actions" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="">All actions</SelectItem>
                  <SelectItem value="create">Create</SelectItem>
                  <SelectItem value="update">Update</SelectItem>
                  <SelectItem value="delete">Delete</SelectItem>
                  <SelectItem value="approve">Approve</SelectItem>
                  <SelectItem value="reject">Reject</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label htmlFor="adminUserId">Admin User ID</Label>
              <Input
                id="adminUserId"
                placeholder="Filter by admin user"
                value={filters.adminUserId}
                onChange={(e) => handleFilterChange('adminUserId', e.target.value)}
              />
            </div>

            <div>
              <Label htmlFor="startDate">Start Date</Label>
              <Input
                id="startDate"
                type="date"
                value={filters.startDate}
                onChange={(e) => handleFilterChange('startDate', e.target.value)}
              />
            </div>

            <div>
              <Label htmlFor="endDate">End Date</Label>
              <Input
                id="endDate"
                type="date"
                value={filters.endDate}
                onChange={(e) => handleFilterChange('endDate', e.target.value)}
              />
            </div>

            <div>
              <Label htmlFor="endpoint">Endpoint</Label>
              <Input
                id="endpoint"
                placeholder="Filter by API endpoint"
                value={filters.endpoint}
                onChange={(e) => handleFilterChange('endpoint', e.target.value)}
              />
            </div>

            <div className="md:col-span-4">
              <Button type="submit" disabled={loading}>
                {loading && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
                Apply Filters
              </Button>
            </div>
          </form>
        </CardContent>
      </Card>

      {/* Results */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center justify-between">
            <span>Audit Logs</span>
            <Badge variant="secondary">
              {pagination.total} total entries
            </Badge>
          </CardTitle>
        </CardHeader>
        <CardContent>
          {loading ? (
            <div className="flex items-center justify-center py-8">
              <Loader2 className="h-8 w-8 animate-spin" />
              <span className="ml-2">Loading audit logs...</span>
            </div>
          ) : auditLogs.length === 0 ? (
            <div className="text-center py-8 text-muted-foreground">
              No audit logs found
            </div>
          ) : (
            <div className="space-y-4">
              {auditLogs.map((log) => (
                <div key={log.id} className="border rounded-lg p-4 space-y-3">
                  <div className="flex items-start justify-between">
                    <div className="flex items-center gap-3">
                      <Badge className={getActionColor(log.action)}>
                        {log.action.toUpperCase()}
                      </Badge>
                      <Badge variant="outline">
                        {log.resource_type}
                      </Badge>
                      <span className="text-sm text-muted-foreground">
                        ID: {log.resource_id}
                      </span>
                    </div>
                    <div className="text-right text-sm text-muted-foreground">
                      <div className="flex items-center gap-1">
                        <Calendar className="h-3 w-3" />
                        {formatDate(log.created_at)}
                      </div>
                    </div>
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <Label className="text-sm font-medium">Admin User</Label>
                      <div className="flex items-center gap-2 mt-1">
                        <User className="h-4 w-4 text-muted-foreground" />
                        <span className="font-medium">
                          {log.profiles?.full_name || 'Unknown'}
                        </span>
                        <span className="text-sm text-muted-foreground">
                          ({log.profiles?.email || log.admin_user_id})
                        </span>
                      </div>
                    </div>

                    <div>
                      <Label className="text-sm font-medium">Request ID</Label>
                      <div className="mt-1 font-mono text-sm bg-muted p-2 rounded">
                        {log.request_id || 'N/A'}
                      </div>
                    </div>
                  </div>

                  {(log.old_values || log.new_values) && (
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      {log.old_values && (
                        <div>
                          <Label className="text-sm font-medium">Previous Values</Label>
                          <pre className="mt-1 text-xs bg-muted p-2 rounded overflow-auto max-h-32">
                            {JSON.stringify(log.old_values, null, 2)}
                          </pre>
                        </div>
                      )}
                      {log.new_values && (
                        <div>
                          <Label className="text-sm font-medium">New Values</Label>
                          <pre className="mt-1 text-xs bg-muted p-2 rounded overflow-auto max-h-32">
                            {JSON.stringify(log.new_values, null, 2)}
                          </pre>
                        </div>
                      )}
                    </div>
                  )}

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
                    <div>
                      <Label className="text-sm font-medium">IP Address</Label>
                      <div className="mt-1 font-mono">{log.ip_address || 'N/A'}</div>
                    </div>
                    <div>
                      <Label className="text-sm font-medium">User Agent</Label>
                      <div className="mt-1 text-xs text-muted-foreground">
                        {truncateText(log.user_agent || 'N/A', 80)}
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}

          {/* Enhanced Pagination */}
          {pagination.totalPages > 1 && (
            <div className="flex items-center justify-between mt-6">
              <div className="flex items-center gap-4">
                <div className="text-sm text-muted-foreground">
                  Page {pagination.page} of {pagination.totalPages}
                </div>
                <div className="text-sm text-muted-foreground">
                  Showing {((pagination.page - 1) * pagination.limit) + 1} - {Math.min(pagination.page * pagination.limit, pagination.total)} of {pagination.total} entries
                </div>
              </div>
              <div className="flex items-center gap-2">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => handlePageChange(1)}
                  disabled={pagination.page <= 1}
                >
                  First
                </Button>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => handlePageChange(pagination.page - 1)}
                  disabled={pagination.page <= 1}
                >
                  Previous
                </Button>
                <span className="px-3 py-2 text-sm">
                  {pagination.page}
                </span>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => handlePageChange(pagination.page + 1)}
                  disabled={pagination.page >= pagination.totalPages}
                >
                  Next
                </Button>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => handlePageChange(pagination.totalPages)}
                  disabled={pagination.page >= pagination.totalPages}
                >
                  Last
                </Button>
              </div>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  )
}

export default function AdminAuditViewer() {
  return (
    <Suspense fallback={
      <div className="p-6">
        <Card>
          <CardHeader>
            <CardTitle>Loading audit logs...</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex items-center justify-center py-8">
              <Loader2 className="h-8 w-8 animate-spin text-blue-600" />
            </div>
          </CardContent>
        </Card>
      </div>
    }>
      <AdminAuditViewerContent />
    </Suspense>
  )
}

'use client'

import React, { useState, useEffect } from 'react'
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select'
import { Badge } from '@/components/ui/badge'
import { Alert, AlertDescription } from '@/components/ui/alert'
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs'
import { 
  Loader2, TrendingUp, TrendingDown, Activity, Clock, AlertTriangle, 
  Zap, Database, Globe, RefreshCw, Play, Pause, Settings, BarChart3,
  Target, Gauge, History, Plus, Trash2, Edit3
} from 'lucide-react'
import { 
  CachePerformanceMetrics, 
  CacheInvalidationPattern, 
  CacheTTLOptimization 
} from '@/lib/cache/monitoring'
import { 
  CacheWarmingStrategy, 
  WarmingResult 
} from '@/lib/cache/warming'

interface CacheOverview {
  performance: CachePerformanceMetrics[]
  invalidationPatterns: CacheInvalidationPattern[]
  healthSummary: {
    overallHitRate: number
    totalCacheEntries: number
    avgResponseTime: number
    invalidationEfficiency: number
    recommendations: string[]
  }
  warmingMetrics: {
    totalStrategies: number
    enabledStrategies: number
    totalQueriesExecuted: number
    avgSuccessRate: number
    avgDuration: number
    lastWarmingRun?: string
  }
  timeRange: string
}

export default function CacheManagementDashboard() {
  const [overview, setOverview] = useState<CacheOverview | null>(null)
  const [strategies, setStrategies] = useState<CacheWarmingStrategy[]>([])
  const [warmingHistory, setWarmingHistory] = useState<WarmingResult[]>([])
  const [loading, setLoading] = useState(true)
  const [timeRange, setTimeRange] = useState('24h')
  const [activeTab, setActiveTab] = useState('overview')
  const [warmingServiceStatus, setWarmingServiceStatus] = useState<'running' | 'stopped'>('stopped')
  const [systemStatus, setSystemStatus] = useState<'healthy' | 'warning' | 'critical'>('healthy')
  const [showEmergencyControls, setShowEmergencyControls] = useState(false)

  const fetchCacheData = async () => {
    setLoading(true)
    try {
      const response = await fetch(`/api/admin/cache?timeRange=${timeRange}`)
      const data = await response.json()

      if (data.success) {
        setOverview(data.data)
      } else {
        console.error('Failed to fetch cache data:', data.error)
      }
    } catch (error) {
      console.error('Error fetching cache data:', error)
    } finally {
      setLoading(false)
    }
  }

  const fetchWarmingStrategies = async () => {
    try {
      const response = await fetch('/api/admin/cache?action=warming-strategies')
      const data = await response.json()

      if (data.success) {
        setStrategies(data.data)
      }
    } catch (error) {
      console.error('Error fetching warming strategies:', error)
    }
  }

  const fetchWarmingHistory = async () => {
    try {
      const response = await fetch('/api/admin/cache?action=warming-history&limit=100')
      const data = await response.json()

      if (data.success) {
        setWarmingHistory(data.data)
      }
    } catch (error) {
      console.error('Error fetching warming history:', error)
    }
  }

  const executeWarmingStrategy = async (strategyId: string) => {
    try {
      const response = await fetch('/api/admin/cache', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ action: 'execute-warming', strategyId })
      })

      const data = await response.json()
      if (data.success) {
        // Refresh data after execution
        await fetchCacheData()
        await fetchWarmingHistory()
      }
    } catch (error) {
      console.error('Error executing warming strategy:', error)
    }
  }

  const toggleWarmingStrategy = async (strategyId: string, enabled: boolean) => {
    try {
      const response = await fetch('/api/admin/cache', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ action: 'update-warming-strategy', strategyId, enabled })
      })

      const data = await response.json()
      if (data.success) {
        await fetchWarmingStrategies()
      }
    } catch (error) {
      console.error('Error updating warming strategy:', error)
    }
  }

  const toggleWarmingService = async () => {
    const action = warmingServiceStatus === 'running' ? 'stop-warming-service' : 'start-warming-service'
    
    try {
      const response = await fetch('/api/admin/cache', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ action })
      })

      const data = await response.json()
      if (data.success) {
        setWarmingServiceStatus(warmingServiceStatus === 'running' ? 'stopped' : 'running')
      }
    } catch (error) {
      console.error('Error toggling warming service:', error)
    }
  }

  const emergencyInvalidateAll = async () => {
    if (!confirm('‚ö†Ô∏è DANGER: This will invalidate ALL cache entries immediately. Continue?')) {
      return;
    }
    
    try {
      const response = await fetch('/api/admin/cache', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ action: 'emergency-invalidate-all' })
      });

      const data = await response.json();
      if (data.success) {
        alert(`‚úÖ Emergency invalidation completed. ${data.invalidatedCount} entries invalidated.`);
        await fetchCacheData();
      } else {
        alert(`‚ùå Emergency invalidation failed: ${data.error}`);
      }
    } catch (error) {
      console.error('Emergency invalidation error:', error);
      alert('‚ùå Emergency invalidation failed');
    }
  }

  const emergencyStopWarming = async () => {
    if (!confirm('‚ö†Ô∏è Stop all warming services immediately?')) {
      return;
    }
    
    try {
      const response = await fetch('/api/admin/cache', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ action: 'emergency-stop-warming' })
      });

      const data = await response.json();
      if (data.success) {
        setWarmingServiceStatus('stopped');
        alert('‚úÖ All warming services stopped immediately');
      } else {
        alert(`‚ùå Failed to stop warming services: ${data.error}`);
      }
    } catch (error) {
      console.error('Emergency stop error:', error);
      alert('‚ùå Failed to stop warming services');
    }
  }

  const resetCircuitBreaker = async () => {
    try {
      const response = await fetch('/api/admin/cache', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ action: 'emergency-reset-circuit-breaker' })
      });

      const data = await response.json();
      if (data.success) {
        alert('‚úÖ Circuit breaker reset - warming service restarted');
        await fetchCacheData();
      } else {
        alert(`‚ùå Failed to reset circuit breaker: ${data.error}`);
      }
    } catch (error) {
      console.error('Circuit breaker reset error:', error);
      alert('‚ùå Failed to reset circuit breaker');
    }
  }

  const getSystemStatusColor = (status: string) => {
    switch (status) {
      case 'healthy': return 'text-green-600';
      case 'warning': return 'text-yellow-600';
      case 'critical': return 'text-red-600';
      default: return 'text-gray-600';
    }
  }

  const getSystemStatusIcon = (status: string) => {
    switch (status) {
      case 'healthy': return 'üü¢';
      case 'warning': return 'üü°';
      case 'critical': return 'üî¥';
      default: return '‚ö™';
    }
  }

  useEffect(() => {
    fetchCacheData()
    fetchWarmingStrategies()
    fetchWarmingHistory()
  }, [timeRange])

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <Loader2 className="h-8 w-8 animate-spin" />
        <span className="ml-2">Loading cache data...</span>
      </div>
    )
  }

  if (!overview) {
    return (
      <Alert>
        <AlertTriangle className="h-4 w-4" />
        <AlertDescription>Failed to load cache management data</AlertDescription>
      </Alert>
    )
  }

  const getCacheHitRateColor = (rate: number) => {
    if (rate >= 80) return 'text-green-600'
    if (rate >= 60) return 'text-yellow-600'
    return 'text-red-600'
  }

  const getResponseTimeColor = (time: number) => {
    if (time <= 200) return 'text-green-600'
    if (time <= 500) return 'text-yellow-600'
    return 'text-red-600'
  }

  const formatDuration = (ms: number) => {
    if (ms < 1000) return `${ms}ms`
    return `${(ms / 1000).toFixed(1)}s`
  }

  const formatTime = (timestamp: string) => {
    return new Date(timestamp).toLocaleString()
  }

  return (
    <div className="space-y-6">
      {/* Header with Emergency Controls */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">Cache Management</h1>
          <p className="text-muted-foreground">Monitor and optimize cache performance</p>
        </div>
        
        <div className="flex items-center gap-4">
          {/* System Status Indicator */}
          <div className={`flex items-center gap-2 px-3 py-2 rounded-lg border ${getSystemStatusColor(systemStatus)}`}>
            <span className="text-lg">{getSystemStatusIcon(systemStatus)}</span>
            <span className="font-medium">System: {systemStatus}</span>
          </div>
          
          <Select value={timeRange} onValueChange={setTimeRange}>
            <SelectTrigger className="w-32">
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="1h">Last Hour</SelectItem>
              <SelectItem value="6h">Last 6 Hours</SelectItem>
              <SelectItem value="24h">Last 24 Hours</SelectItem>
              <SelectItem value="7d">Last 7 Days</SelectItem>
            </SelectContent>
          </Select>
          
          <Button onClick={fetchCacheData} variant="outline" size="sm">
            <RefreshCw className="h-4 w-4 mr-2" />
            Refresh
          </Button>

          {/* Emergency Controls Toggle */}
          <Button
            onClick={() => setShowEmergencyControls(!showEmergencyControls)}
            variant="destructive"
            size="sm"
          >
            üö® Emergency
          </Button>
        </div>
      </div>

      {/* Emergency Controls Panel */}
      {showEmergencyControls && (
        <Alert className="border-red-500 bg-red-50">
          <AlertTriangle className="h-4 w-4 text-red-600" />
          <AlertDescription>
            <div className="font-medium text-red-800 mb-3">üö® Emergency Controls</div>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-3">
              <Button
                onClick={emergencyInvalidateAll}
                variant="destructive"
                size="sm"
                className="w-full"
              >
                üí• Invalidate All Cache
              </Button>
              <Button
                onClick={emergencyStopWarming}
                variant="destructive"
                size="sm"
                className="w-full"
              >
                ‚èπÔ∏è Stop All Warming
              </Button>
              <Button
                onClick={resetCircuitBreaker}
                variant="outline"
                size="sm"
                className="w-full"
              >
                üîÑ Reset Circuit Breaker
              </Button>
            </div>
            <div className="text-xs text-red-600 mt-2">
              ‚ö†Ô∏è Use these controls only in emergency situations
            </div>
          </AlertDescription>
        </Alert>
      )}

      {/* Enhanced Cache Health Summary with System Status */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-6">
        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium text-muted-foreground flex items-center gap-2">
              <Target className="w-4 h-4" />
              Cache Hit Rate
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className={`text-2xl font-bold ${getCacheHitRateColor(overview.healthSummary.overallHitRate)}`}>
              {overview.healthSummary.overallHitRate.toFixed(1)}%
            </div>
            <div className="text-xs text-muted-foreground mt-1">
              {overview.healthSummary.totalCacheEntries} entries
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium text-muted-foreground flex items-center gap-2">
              <Clock className="w-4 h-4" />
              Avg Response Time
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className={`text-2xl font-bold ${getResponseTimeColor(overview.healthSummary.avgResponseTime)}`}>
              {formatDuration(overview.healthSummary.avgResponseTime)}
            </div>
            <div className="text-xs text-muted-foreground mt-1">
              Target: &lt;500ms
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium text-muted-foreground flex items-center gap-2">
              <Zap className="w-4 h-4" />
              Warming Strategies
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">
              {overview.warmingMetrics.enabledStrategies}/{overview.warmingMetrics.totalStrategies}
            </div>
            <div className="text-xs text-muted-foreground mt-1">
              {overview.warmingMetrics.avgSuccessRate.toFixed(1)}% success rate
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium text-muted-foreground flex items-center gap-2">
              <Gauge className="w-4 h-4" />
              Invalidation Efficiency
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className={`text-2xl font-bold ${overview.healthSummary.invalidationEfficiency > 20 ? 'text-red-600' : 'text-green-600'}`}>
              {overview.healthSummary.invalidationEfficiency.toFixed(1)}%
            </div>
            <div className="text-xs text-muted-foreground mt-1">
              Lower is better
            </div>
          </CardContent>
        </Card>
        
        {/* New System Status Card */}
        <Card className={`border-2 ${systemStatus === 'critical' ? 'border-red-500' : systemStatus === 'warning' ? 'border-yellow-500' : 'border-green-500'}`}>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium text-muted-foreground flex items-center gap-2">
              <Activity className="w-4 h-4" />
              System Status
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className={`text-2xl font-bold ${getSystemStatusColor(systemStatus)}`}>
              {getSystemStatusIcon(systemStatus)} {systemStatus.toUpperCase()}
            </div>
            <div className="text-xs text-muted-foreground mt-1">
              {systemStatus === 'healthy' && 'All systems operational'}
              {systemStatus === 'warning' && 'Performance degradation detected'}
              {systemStatus === 'critical' && 'Immediate attention required'}
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Recommendations */}
      {overview.healthSummary.recommendations.length > 0 && (
        <Alert>
          <AlertTriangle className="h-4 w-4" />
          <AlertDescription>
            <div className="font-medium mb-2">Cache Optimization Recommendations:</div>
            <ul className="list-disc list-inside space-y-1">
              {overview.healthSummary.recommendations.map((rec, index) => (
                <li key={index} className="text-sm">{rec}</li>
              ))}
            </ul>
          </AlertDescription>
        </Alert>
      )}

      {/* Main Content Tabs */}
      <Tabs value={activeTab} onValueChange={setActiveTab}>
        <TabsList className="grid w-full grid-cols-4">
          <TabsTrigger value="overview">Overview</TabsTrigger>
          <TabsTrigger value="performance">Performance</TabsTrigger>
          <TabsTrigger value="warming">Warming</TabsTrigger>
          <TabsTrigger value="optimization">TTL Optimization</TabsTrigger>
        </TabsList>

        {/* Overview Tab */}
        <TabsContent value="overview" className="space-y-6">
          {/* Performance by Query Type */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <BarChart3 className="h-5 w-5" />
                Cache Performance by Query Type
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {overview.performance.slice(0, 10).map((metric, index) => (
                  <div key={index} className="flex items-center justify-between p-3 border rounded-lg">
                    <div className="flex items-center gap-3">
                      <Badge variant="outline">{metric.queryType}</Badge>
                      <div className="text-sm text-muted-foreground">
                        {metric.totalRequests} requests
                      </div>
                    </div>
                    
                    <div className="flex items-center gap-4 text-sm">
                      <div className="text-right">
                        <div className={`font-mono ${getCacheHitRateColor(metric.cacheHitRate)}`}>
                          {metric.cacheHitRate.toFixed(1)}%
                        </div>
                        <div className="text-muted-foreground">Hit Rate</div>
                      </div>
                      <div className="text-right">
                        <div className={`font-mono ${getResponseTimeColor(metric.avgResponseTime)}`}>
                          {formatDuration(metric.avgResponseTime)}
                        </div>
                        <div className="text-muted-foreground">Response</div>
                      </div>
                      <div className="text-right">
                        <div className="font-mono">{metric.avgTTL}m</div>
                        <div className="text-muted-foreground">TTL</div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>

          {/* Invalidation Patterns */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Activity className="h-5 w-5" />
                Top Invalidation Patterns
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {overview.invalidationPatterns.slice(0, 5).map((pattern, index) => (
                  <div key={index} className="flex items-center justify-between p-3 border rounded-lg">
                    <div className="flex items-center gap-3">
                      <Badge variant="secondary">{pattern.tag}</Badge>
                      <div className="text-sm text-muted-foreground">
                        {pattern.frequency} times
                      </div>
                    </div>
                    
                    <div className="flex items-center gap-4 text-sm">
                      <div className="text-right">
                        <div className="font-mono">{pattern.avgBatchSize}</div>
                        <div className="text-muted-foreground">Avg Batch</div>
                      </div>
                      <div className="text-right">
                        <div className="font-mono">{pattern.impactScore.toFixed(1)}</div>
                        <div className="text-muted-foreground">Impact</div>
                      </div>
                      <div className="text-right text-xs text-muted-foreground">
                        {formatTime(pattern.lastInvalidated)}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Performance Tab */}
        <TabsContent value="performance" className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <BarChart3 className="h-5 w-5" />
                Detailed Performance Metrics
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead>
                    <tr className="border-b">
                      <th className="text-left p-2">Query Type</th>
                      <th className="text-left p-2">Hit Rate</th>
                      <th className="text-left p-2">Response Time</th>
                      <th className="text-left p-2">Requests</th>
                      <th className="text-left p-2">TTL (min)</th>
                      <th className="text-left p-2">Invalidations</th>
                    </tr>
                  </thead>
                  <tbody>
                    {overview.performance.map((metric, index) => (
                      <tr key={index} className="border-b">
                        <td className="p-2 font-medium">{metric.queryType}</td>
                        <td className={`p-2 ${getCacheHitRateColor(metric.cacheHitRate)}`}>
                          {metric.cacheHitRate.toFixed(1)}%
                        </td>
                        <td className={`p-2 ${getResponseTimeColor(metric.avgResponseTime)}`}>
                          {formatDuration(metric.avgResponseTime)}
                        </td>
                        <td className="p-2">{metric.totalRequests}</td>
                        <td className="p-2">{metric.avgTTL}</td>
                        <td className="p-2">{metric.invalidationCount}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Warming Tab */}
        <TabsContent value="warming" className="space-y-6">
          {/* Warming Service Control */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Zap className="h-5 w-5" />
                Warming Service Control
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="flex items-center gap-4">
                <div className="flex items-center gap-2">
                  <div className={`w-3 h-3 rounded-full ${warmingServiceStatus === 'running' ? 'bg-green-500' : 'bg-red-500'}`} />
                  <span className="text-sm">
                    Service: {warmingServiceStatus === 'running' ? 'Running' : 'Stopped'}
                  </span>
                </div>
                
                <Button 
                  onClick={toggleWarmingService}
                  variant={warmingServiceStatus === 'running' ? 'destructive' : 'default'}
                  size="sm"
                >
                  {warmingServiceStatus === 'running' ? (
                    <>
                      <Pause className="h-4 w-4 mr-2" />
                      Stop
                    </>
                  ) : (
                    <>
                      <Play className="h-4 w-4 mr-2" />
                      Start
                    </>
                  )}
                </Button>
              </div>
            </CardContent>
          </Card>

          {/* Warming Strategies */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Settings className="h-5 w-5" />
                Warming Strategies
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {strategies.map((strategy) => (
                  <div key={strategy.id} className="border rounded-lg p-4">
                    <div className="flex items-center justify-between mb-3">
                      <div>
                        <h4 className="font-medium">{strategy.name}</h4>
                        <p className="text-sm text-muted-foreground">{strategy.description}</p>
                      </div>
                      <div className="flex items-center gap-2">
                        <Badge variant={strategy.enabled ? 'default' : 'secondary'}>
                          {strategy.enabled ? 'Enabled' : 'Disabled'}
                        </Badge>
                        <Badge variant="outline">Priority {strategy.priority}</Badge>
                      </div>
                    </div>
                    
                    <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm mb-3">
                      <div>
                        <div className="text-muted-foreground">Schedule</div>
                        <div className="font-mono">
                          {strategy.schedule.type === 'interval' 
                            ? `Every ${strategy.schedule.value} min`
                            : strategy.schedule.type === 'cron'
                            ? 'Cron-based'
                            : 'Event-driven'
                          }
                        </div>
                      </div>
                      <div>
                        <div className="text-muted-foreground">Success Rate</div>
                        <div className="font-mono">{strategy.successRate.toFixed(1)}%</div>
                      </div>
                      <div>
                        <div className="text-muted-foreground">Avg Duration</div>
                        <div className="font-mono">{formatDuration(strategy.avgDuration)}</div>
                      </div>
                      <div>
                        <div className="text-muted-foreground">Queries</div>
                        <div className="font-mono">{strategy.queries.length}</div>
                      </div>
                    </div>
                    
                    <div className="flex items-center gap-2">
                      <Button
                        onClick={() => executeWarmingStrategy(strategy.id)}
                        size="sm"
                        variant="outline"
                      >
                        <RefreshCw className="h-4 w-4 mr-2" />
                        Execute Now
                      </Button>
                      
                      <Button
                        onClick={() => toggleWarmingStrategy(strategy.id, !strategy.enabled)}
                        size="sm"
                        variant={strategy.enabled ? 'destructive' : 'default'}
                      >
                        {strategy.enabled ? 'Disable' : 'Enable'}
                      </Button>
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>

          {/* Warming History */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <History className="h-5 w-5" />
                Warming History
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-3">
                {warmingHistory.slice(0, 20).map((result, index) => (
                  <div key={index} className="flex items-center justify-between p-3 border rounded-lg">
                    <div className="flex items-center gap-3">
                      <Badge variant="outline">
                        {strategies.find(s => s.id === result.strategyId)?.name || result.strategyId}
                      </Badge>
                      <div className="text-sm text-muted-foreground">
                        {result.queriesExecuted} queries
                      </div>
                    </div>
                    
                    <div className="flex items-center gap-4 text-sm">
                      <div className="text-right">
                        <div className="font-mono text-green-600">{result.queriesSucceeded}</div>
                        <div className="text-muted-foreground">Success</div>
                      </div>
                      <div className="text-right">
                        <div className="font-mono text-red-600">{result.queriesFailed}</div>
                        <div className="text-muted-foreground">Failed</div>
                      </div>
                      <div className="text-right">
                        <div className="font-mono">{formatDuration(result.totalDuration)}</div>
                        <div className="text-muted-foreground">Duration</div>
                      </div>
                      <div className="text-right text-xs text-muted-foreground">
                        {formatTime(result.timestamp)}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        {/* TTL Optimization Tab */}
        <TabsContent value="optimization" className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Target className="h-5 w-5" />
                TTL Optimization Recommendations
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {/* This would be populated by calling the TTL optimization endpoint */}
                <div className="text-center text-muted-foreground py-8">
                  <Target className="h-12 w-12 mx-auto mb-4 opacity-50" />
                  <p>TTL optimization analysis will appear here</p>
                  <p className="text-sm">Click "Analyze TTL" to generate recommendations</p>
                </div>
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  )
}

'use client'

import { motion } from 'framer-motion'
import Link from 'next/link'
import { Users, Calendar, DollarSign, AlertTriangle } from 'lucide-react'

interface DashboardStats {
  activeUsers: number
  bookingsToday: number
  revenue: number
  errors: number
}

interface DashboardCardsProps {
  stats: DashboardStats
}

const cardVariants = {
  hidden: { opacity: 0, y: 20, scale: 0.95 },
  visible: (i: number) => ({
    opacity: 1,
    y: 0,
    scale: 1,
    transition: {
      delay: i * 0.1,
      duration: 0.5,
      ease: "easeOut"
    }
  })
}

const cards = [
  {
    title: 'Active Users',
    value: 'activeUsers',
    icon: Users,
    color: 'from-blue-500 to-blue-600',
    bgColor: 'bg-blue-50',
    textColor: 'text-blue-700',
    href: '/admin/customers'
  },
  {
    title: 'Bookings Today',
    value: 'bookingsToday',
    icon: Calendar,
    color: 'from-green-500 to-green-600',
    bgColor: 'bg-green-50',
    textColor: 'text-green-700',
    href: '/admin/bookings'
  },
  {
    title: 'Revenue',
    value: 'revenue',
    icon: DollarSign,
    color: 'from-purple-500 to-purple-600',
    bgColor: 'bg-purple-50',
    textColor: 'text-purple-700',
    href: '/admin/analytics'
  },
  {
    title: 'Errors',
    value: 'errors',
    icon: AlertTriangle,
    color: 'from-red-500 to-red-600',
    bgColor: 'bg-red-50',
    textColor: 'text-red-700',
    href: '/admin/performance'
  }
]

export default function DashboardCards({ stats }: DashboardCardsProps) {
  // Add error handling for undefined stats
  if (!stats) {
    console.error('DashboardCards: stats prop is undefined')
    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <div className="bg-red-50 rounded-2xl p-6 shadow-sm border border-red-200">
          <p className="text-red-600">Error: Dashboard stats not available</p>
          <p className="text-xs text-red-500">Props received: {JSON.stringify({ stats })}</p>
        </div>
      </div>
    )
  }

  const getValue = (valuePath: string) => {
    const path = valuePath.split('.')
    let result: any = stats
    for (const key of path) {
      result = result[key]
    }
    return result
  }

  const formatValue = (title: string, value: any) => {
    if (title === 'Revenue') {
      return `$${value.toLocaleString()}`
    }
    return value.toLocaleString()
  }

  return (
    <div data-testid="dashboard-cards" className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
      {cards.map((card, index) => {
        const Icon = card.icon
        const value = getValue(card.value)

        const CardContent = (
          <>
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600 mb-1">
                  {card.title}
                </p>
                <p className={`text-2xl font-bold ${card.textColor}`}>
                  {formatValue(card.title, value)}
                </p>
              </div>
              <div className={`w-12 h-12 rounded-2xl bg-gradient-to-r ${card.color} flex items-center justify-center shadow-lg`}>
                <Icon className="text-white" size={24} />
              </div>
            </div>

            {/* Trend indicator */}
            <div className="mt-4 flex items-center gap-2">
              <div className={`w-2 h-2 rounded-full ${card.textColor.replace('text-', 'bg-')}`}></div>
              <span className="text-xs text-gray-500">
                {index === 0 && '+12% from last week'}
                {index === 1 && '+8% from yesterday'}
                {index === 2 && '+15% from last month'}
                {index === 3 && '-2 from yesterday'}
              </span>
            </div>
          </>
        )

        return (
          <motion.div
            key={card.title}
            custom={index}
            variants={cardVariants}
            initial="hidden"
            animate="visible"
            className={`${card.bgColor} rounded-2xl p-6 shadow-sm border border-gray-100 hover:shadow-md transition-all duration-300 ${card.href ? 'cursor-pointer' : ''}`}
          >
            {card.href ? (
              <Link href={card.href} className="block">
                {CardContent}
              </Link>
            ) : (
              CardContent
            )}
          </motion.div>
        )
      })}
    </div>
  )
}

'use client'

import { useState } from 'react'
import { motion, AnimatePresence } from 'framer-motion'
import { ChevronDown, ChevronRight, Search, Filter } from 'lucide-react'

interface Column {
  key: string
  label: string
  sortable?: boolean
  render?: (value: any, row: any) => React.ReactNode
}

interface DataTableProps {
  columns: Column[]
  data: any[]
  title: string
  expandable?: boolean
  expandedContent?: (row: any) => React.ReactNode
}

export default function DataTable({ columns, data, title, expandable = false, expandedContent }: DataTableProps) {
  const [searchTerm, setSearchTerm] = useState('')
  const [sortColumn, setSortColumn] = useState<string | null>(null)
  const [sortDirection, setSortDirection] = useState<'asc' | 'desc'>('asc')
  const [expandedRows, setExpandedRows] = useState<Set<string>>(new Set())

  const toggleRow = (id: string) => {
    const newExpanded = new Set(expandedRows)
    if (newExpanded.has(id)) {
      newExpanded.delete(id)
    } else {
      newExpanded.add(id)
    }
    setExpandedRows(newExpanded)
  }

  const handleSort = (columnKey: string) => {
    if (sortColumn === columnKey) {
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc')
    } else {
      setSortColumn(columnKey)
      setSortDirection('asc')
    }
  }

  const filteredAndSortedData = data
    .filter(row => 
      Object.values(row).some(value => 
        String(value).toLowerCase().includes(searchTerm.toLowerCase())
      )
    )
    .sort((a, b) => {
      if (!sortColumn) return 0

      const aVal = a[sortColumn]
      const bVal = b[sortColumn]

      if (aVal < bVal) return sortDirection === 'asc' ? -1 : 1
      if (aVal > bVal) return sortDirection === 'asc' ? 1 : -1
      return 0
    })

  const getStatusColor = (status: string) => {
    switch (status.toLowerCase()) {
      case 'active':
      case 'confirmed':
      case 'completed':
        return 'bg-green-100 text-green-800'
      case 'pending':
        return 'bg-yellow-100 text-yellow-800'
      case 'suspended':
      case 'cancelled':
        return 'bg-red-100 text-red-800'
      case 'open':
        return 'bg-blue-100 text-blue-800'
      case 'in_progress':
        return 'bg-purple-100 text-purple-800'
      default:
        return 'bg-gray-100 text-gray-800'
    }
  }

  return (
    <div className="bg-white rounded-2xl shadow-sm border border-gray-100">
      {/* Header */}
      <div className="p-6 border-b border-gray-200">
        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
          <h3 className="text-lg font-semibold text-gray-900">{title}</h3>

          <div className="flex flex-col sm:flex-row sm:items-center gap-3 w-full sm:w-auto">
            {/* Search */}
            <div className="relative w-full sm:w-auto">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={18} />
              <input
                type="text"
                placeholder="Search..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full sm:w-64 pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>

            {/* Filter Button */}
            <button className="p-2 border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors duration-200 self-start sm:self-auto">
              <Filter size={18} />
            </button>
          </div>
        </div>
      </div>

      {/* Table */}
      <div className="overflow-x-auto">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50">
            <tr>
              {expandable && (
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  <span className="sr-only">Expand</span>
                </th>
              )}
              {columns.map((column) => (
                <th
                  key={column.key}
                  className={`px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider ${
                    column.sortable ? 'cursor-pointer hover:text-gray-700' : ''
                  }`}
                  onClick={() => column.sortable && handleSort(column.key)}
                >
                  <div className="flex items-center gap-2">
                    {column.label}
                    {column.sortable && sortColumn === column.key && (
                      <span className="text-gray-400">
                        {sortDirection === 'asc' ? '‚Üë' : '‚Üì'}
                      </span>
                    )}
                  </div>
                </th>
              ))}
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {filteredAndSortedData.map((row, index) => (
              <motion.tr
                key={row.id || index}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: index * 0.05 }}
                className="hover:bg-gray-50 transition-colors duration-200"
              >
                {expandable && (
                  <td className="px-6 py-4">
                    <button
                      onClick={() => toggleRow(row.id || index.toString())}
                      className="p-1 hover:bg-gray-200 rounded transition-colors duration-200"
                    >
                      {expandedRows.has(row.id || index.toString()) ? (
                        <ChevronDown size={16} />
                      ) : (
                        <ChevronRight size={16} />
                      )}
                    </button>
                  </td>
                )}

                {columns.map((column) => (
                  <td key={column.key} className="px-6 py-4 whitespace-nowrap">
                    {column.render ? (
                      column.render(row[column.key], row)
                    ) : (
                      <div className="text-sm text-gray-900">
                        {column.key === 'status' ? (
                          <span className={`inline-flex px-2 py-1 text-xs font-medium rounded-full ${getStatusColor(row[column.key])}`}>
                            {row[column.key]}
                          </span>
                        ) : (
                          row[column.key]
                        )}
                      </div>
                    )}
                  </td>
                ))}
              </motion.tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Expanded Rows */}
      <AnimatePresence>
        {filteredAndSortedData.map((row, index) => {
          const rowId = row.id || index.toString()
          const isExpanded = expandedRows.has(rowId)

          if (!isExpanded || !expandedContent) return null

          return (
            <motion.tr
              key={`expanded-${rowId}`}
              initial={{ opacity: 0, height: 0 }}
              animate={{ opacity: 1, height: 'auto' }}
              exit={{ opacity: 0, height: 0 }}
              transition={{ duration: 0.3 }}
              className="bg-gray-50"
            >
              <td colSpan={columns.length + (expandable ? 1 : 0)} className="px-6 py-4">
                {expandedContent(row)}
              </td>
            </motion.tr>
          )
        })}
      </AnimatePresence>

      {/* Empty State */}
      {filteredAndSortedData.length === 0 && (
        <div className="text-center py-12">
          <p className="text-gray-500">No data found</p>
        </div>
      )}

      {/* Footer */}
      <div className="px-6 py-4 border-t border-gray-200 bg-gray-50 rounded-b-2xl">
        <div className="flex items-center justify-between text-sm text-gray-600">
          <span>Showing {filteredAndSortedData.length} of {data.length} results</span>
          <span>Page 1 of 1</span>
        </div>
      </div>
    </div>
  )
}

'use client'

import { useState, useEffect } from 'react'
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { AlertTriangle, Activity, TrendingUp, TrendingDown, DollarSign, Clock, HardDrive, Cpu } from 'lucide-react'
import { performanceGuardrails, PerformanceMetrics } from '@/lib/performance/guardrails'

interface PerformanceSummary {
  current: PerformanceMetrics | null
  average: Partial<PerformanceMetrics>
  violations: string[]
  recommendations: string[]
}

export function PerformanceMonitor() {
  const [summary, setSummary] = useState<PerformanceSummary | null>(null)
  const [isMonitoring, setIsMonitoring] = useState(true)
  const [refreshInterval, setRefreshInterval] = useState(5000) // 5 seconds
  const [alertConfig, setAlertConfig] = useState<any>(null)
  const [testingAlerts, setTestingAlerts] = useState(false)

  useEffect(() => {
    if (!isMonitoring) return

    const updateSummary = () => {
      const perfSummary = performanceGuardrails.getPerformanceSummary()
      setSummary(perfSummary)
    }

    // Initial update
    updateSummary()

    // Set up interval
    const interval = setInterval(updateSummary, refreshInterval)

    return () => clearInterval(interval)
  }, [isMonitoring, refreshInterval])

  // Load alert configuration
  useEffect(() => {
    const loadAlertConfig = async () => {
      try {
        const response = await fetch('/api/admin/test-alerts')
        if (response.ok) {
          const data = await response.json()
          setAlertConfig(data.config)
        }
      } catch (error) {
        console.error('Failed to load alert config:', error)
      }
    }
    loadAlertConfig()
  }, [])

  const toggleMonitoring = () => {
    setIsMonitoring(!isMonitoring)
    performanceGuardrails.setEnabled(!isMonitoring)
  }

  const clearMetrics = () => {
    performanceGuardrails.clear()
    setSummary(null)
  }

  const testAlerts = async () => {
    setTestingAlerts(true)
    try {
      const response = await fetch('/api/admin/test-alerts', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          severity: 'warning',
          testMessage: 'Test alert triggered from Performance Monitor'
        })
      })

      if (response.ok) {
        alert('Test alerts sent successfully! Check your configured channels.')
      } else {
        alert('Failed to send test alerts. Check console for details.')
      }
    } catch (error) {
      console.error('Failed to test alerts:', error)
      alert('Failed to send test alerts. Check console for details.')
    } finally {
      setTestingAlerts(false)
    }
  }

  const getStatusColor = (value: number, threshold: number) => {
    if (value >= threshold) return 'text-red-600'
    if (value >= threshold * 0.8) return 'text-yellow-600'
    return 'text-green-600'
  }

  const getStatusIcon = (value: number, threshold: number) => {
    if (value >= threshold) return <AlertTriangle className="w-4 h-4 text-red-600" />
    if (value >= threshold * 0.8) return <TrendingUp className="w-4 h-4 text-yellow-600" />
    return <TrendingDown className="w-4 h-4 text-green-600" />
  }

  if (!summary) {
    return (
      <div className="space-y-6">
        <div className="flex items-center justify-between">
          <h1 className="text-3xl font-bold">Performance Monitor</h1>
          <div className="space-x-2">
            <Button onClick={toggleMonitoring} variant="outline">
              {isMonitoring ? 'Stop Monitoring' : 'Start Monitoring'}
            </Button>
          </div>
        </div>
        <Card>
          <CardContent className="pt-6">
            <div className="text-center text-muted-foreground">
              <p>No performance data available</p>
              <p className="text-sm mt-2">Start monitoring to see real-time performance metrics</p>
            </div>
          </CardContent>
        </Card>
      </div>
    )
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h1 className="text-3xl font-bold">Performance Monitor</h1>
        <div className="space-x-2">
          <Button onClick={toggleMonitoring} variant={isMonitoring ? 'destructive' : 'default'}>
            {isMonitoring ? 'Stop Monitoring' : 'Start Monitoring'}
          </Button>
          <Button onClick={clearMetrics} variant="outline">
            Clear Metrics
          </Button>
          <Button 
            onClick={testAlerts} 
            variant="outline" 
            disabled={testingAlerts}
          >
            {testingAlerts ? 'Testing...' : 'Test Alerts'}
          </Button>
        </div>
      </div>

      {/* Current Performance Metrics */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium text-muted-foreground flex items-center gap-2">
              <Clock className="w-4 h-4" />
              Response Time
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold flex items-center gap-2">
              {summary.current?.responseTime ? `${summary.current.responseTime}ms` : 'N/A'}
              {summary.current?.responseTime && getStatusIcon(summary.current.responseTime, 5000)}
            </div>
            <div className="text-xs text-muted-foreground mt-1">
              Avg: {summary.average.responseTime ? `${summary.average.responseTime.toFixed(0)}ms` : 'N/A'}
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium text-muted-foreground flex items-center gap-2">
              <HardDrive className="w-4 h-4" />
              Memory Usage
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold flex items-center gap-2">
              {summary.current?.memoryUsage ? `${summary.current.memoryUsage.toFixed(1)}MB` : 'N/A'}
              {summary.current?.memoryUsage && getStatusIcon(summary.current.memoryUsage, 512)}
            </div>
            <div className="text-xs text-muted-foreground mt-1">
              Avg: {summary.average.memoryUsage ? `${summary.average.memoryUsage.toFixed(1)}MB` : 'N/A'}
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium text-muted-foreground flex items-center gap-2">
              <Cpu className="w-4 h-4" />
              CPU Usage
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold flex items-center gap-2">
              {summary.current?.cpuUsage ? `${summary.current.cpuUsage.toFixed(1)}%` : 'N/A'}
              {summary.current?.cpuUsage && getStatusIcon(summary.current.cpuUsage, 80)}
            </div>
            <div className="text-xs text-muted-foreground mt-1">
              Avg: {summary.average.cpuUsage ? `${summary.average.cpuUsage.toFixed(1)}%` : 'N/A'}
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium text-muted-foreground flex items-center gap-2">
              <Activity className="w-4 h-4" />
              Request Rate
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">
              {summary.average.requestCount ? `${(summary.average.requestCount * 60).toFixed(0)}/min` : 'N/A'}
            </div>
            <div className="text-xs text-muted-foreground mt-1">
              Total: {summary.average.requestCount || 0}
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Alerts and Violations */}
      {summary.violations.length > 0 && (
        <Card className="border-red-200 bg-red-50">
          <CardHeader>
            <CardTitle className="text-red-800 flex items-center gap-2">
              <AlertTriangle className="w-5 h-5" />
              Performance Alerts ({summary.violations.length})
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-2">
              {summary.violations.slice(-5).map((violation, index) => (
                <div key={index} className="flex items-start gap-2 p-3 border border-red-300 bg-red-100 rounded-lg">
                  <AlertTriangle className="h-4 w-4 text-red-600 mt-0.5" />
                  <p className="text-red-800 text-sm">
                    {violation}
                  </p>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Recommendations */}
      {summary.recommendations.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <TrendingUp className="w-5 h-5" />
              Performance Recommendations
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-2">
              {summary.recommendations.map((recommendation, index) => (
                <div key={index} className="flex items-start gap-2 p-3 bg-blue-50 rounded-lg">
                  <div className="w-2 h-2 bg-blue-500 rounded-full mt-2 flex-shrink-0" />
                  <p className="text-sm text-blue-800">{recommendation}</p>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Configuration */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <Card>
          <CardHeader>
            <CardTitle>Monitor Configuration</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="text-sm font-medium">Refresh Interval</label>
                <select
                  value={refreshInterval}
                  onChange={(e) => setRefreshInterval(Number(e.target.value))}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                >
                  <option value={1000}>1 second</option>
                  <option value={5000}>5 seconds</option>
                  <option value={10000}>10 seconds</option>
                  <option value={30000}>30 seconds</option>
                </select>
              </div>
              <div>
                <label className="text-sm font-medium">Status</label>
                <div className="mt-1 flex items-center gap-2">
                  <div className={`w-3 h-3 rounded-full ${isMonitoring ? 'bg-green-500' : 'bg-red-500'}`} />
                  <span className="text-sm">
                    {isMonitoring ? 'Active Monitoring' : 'Monitoring Stopped'}
                  </span>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Alert Configuration */}
        <Card>
          <CardHeader>
            <CardTitle>Alert Configuration</CardTitle>
          </CardHeader>
          <CardContent>
            {alertConfig ? (
              <div className="space-y-3">
                <div className="text-sm">
                  <strong>Environment:</strong> {alertConfig.environment}
                </div>
                <div className="space-y-2">
                  <div className="flex items-center gap-2">
                    <div className={`w-2 h-2 rounded-full ${alertConfig.slackEnabled ? 'bg-green-500' : 'bg-gray-300'}`} />
                    <span className="text-sm">Slack Alerts {alertConfig.slackEnabled ? 'Enabled' : 'Disabled'}</span>
                  </div>
                  <div className="flex items-center gap-2">
                    <div className={`w-2 h-2 rounded-full ${alertConfig.emailEnabled ? 'bg-green-500' : 'bg-gray-300'}`} />
                    <span className="text-sm">Email Alerts {alertConfig.emailEnabled ? 'Enabled' : 'Disabled'}</span>
                  </div>
                  <div className="flex items-center gap-2">
                    <div className={`w-2 h-2 rounded-full ${alertConfig.pagerDutyEnabled ? 'bg-green-500' : 'bg-gray-300'}`} />
                    <span className="text-sm">PagerDuty {alertConfig.pagerDutyEnabled ? 'Enabled' : 'Disabled'}</span>
                  </div>
                </div>
                <div className="pt-2">
                  <Button onClick={testAlerts} size="sm" variant="outline" disabled={testingAlerts}>
                    {testingAlerts ? 'Testing...' : 'Test All Channels'}
                  </Button>
                </div>
              </div>
            ) : (
              <div className="text-sm text-gray-500">Loading alert configuration...</div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  )
}

'use client'

import React, { useState, useEffect } from 'react'
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Badge } from '@/components/ui/badge'
import { RefreshCw, TrendingUp, TrendingDown, AlertTriangle, CheckCircle, Bell, X } from 'lucide-react'

interface ResilienceMetric {
  metric_name: string
  metric_value: number
  threshold: number
  status: 'healthy' | 'alert' | 'no_data'
}

interface Alert {
  id: string
  timestamp: string
  metric: string
  currentValue: number
  threshold: number
  severity: 'info' | 'warning' | 'critical'
  message: string
  acknowledged: boolean
  acknowledgedBy?: string
  acknowledgedAt?: string
}

interface ResilienceDashboardProps {
  className?: string
}

export function ResilienceDashboard({ className = '' }: ResilienceDashboardProps) {
  const [metrics, setMetrics] = useState<ResilienceMetric[]>([])
  const [alerts, setAlerts] = useState<Alert[]>([])
  const [isLoading, setIsLoading] = useState(false)
  const [lastUpdated, setLastUpdated] = useState<Date | null>(null)

  const fetchMetrics = async () => {
    setIsLoading(true)
    try {
      const response = await fetch('/api/admin/resilience-metrics')
      if (response.ok) {
        const data = await response.json()
        setMetrics(data.metrics || [])
        setAlerts(data.alerts || [])
        setLastUpdated(new Date())
      }
    } catch (error) {
      console.error('Failed to fetch resilience metrics:', error)
    } finally {
      setIsLoading(false)
    }
  }

  const acknowledgeAlert = async (alertId: string) => {
    try {
      const response = await fetch('/api/admin/resilience-metrics', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          alertId,
          acknowledgedBy: 'admin' // In production, use actual user ID
        })
      })

      if (response.ok) {
        // Update local state
        setAlerts(prev => prev.map(alert => 
          alert.id === alertId 
            ? { ...alert, acknowledged: true, acknowledgedBy: 'admin', acknowledgedAt: new Date().toISOString() }
            : alert
        ))
      }
    } catch (error) {
      console.error('Failed to acknowledge alert:', error)
    }
  }

  useEffect(() => {
    fetchMetrics()
    // Refresh every 5 minutes
    const interval = setInterval(fetchMetrics, 5 * 60 * 1000)
    return () => clearInterval(interval)
  }, [])

  const getMetricDisplay = (metric: ResilienceMetric) => {
    const { metric_name, metric_value, threshold, status } = metric

    const displayNames: Record<string, string> = {
      rollback_rate: 'Rollback Rate',
      retry_success_rate: 'Retry Success Rate',
      error_recovery_rate: 'Error Recovery Rate'
    }

    const formatValue = (value: number) => {
      if (metric_name.includes('rate')) {
        return `${value.toFixed(1)}%`
      }
      return value.toFixed(2)
    }

    const getStatusIcon = () => {
      switch (status) {
        case 'healthy':
          return <CheckCircle className="w-4 h-4 text-green-500" />
        case 'alert':
          return <AlertTriangle className="w-4 h-4 text-red-500" />
        case 'no_data':
          return <AlertTriangle className="w-4 h-4 text-yellow-500" />
        default:
          return null
      }
    }

    const getStatusColor = () => {
      switch (status) {
        case 'healthy':
          return 'bg-green-100 text-green-800'
        case 'alert':
          return 'bg-red-100 text-red-800'
        case 'no_data':
          return 'bg-yellow-100 text-yellow-800'
        default:
          return 'bg-gray-100 text-gray-800'
      }
    }

    const getTrendIcon = () => {
      if (status === 'no_data') return null

      if (metric_name.includes('rate') && metric_name !== 'rollback_rate') {
        // Higher is better for success/recovery rates
        return metric_value >= threshold ? 
          <TrendingUp className="w-4 h-4 text-green-500" /> : 
          <TrendingDown className="w-4 h-4 text-red-500" />
      } else {
        // Lower is better for rollback rate
        return metric_value <= threshold ? 
          <TrendingUp className="w-4 h-4 text-green-500" /> : 
          <TrendingDown className="w-4 h-4 text-red-500" />
      }
    }

    return (
      <Card key={metric_name} className="col-span-1">
        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
          <CardTitle className="text-sm font-medium">
            {displayNames[metric_name] || metric_name}
          </CardTitle>
          {getStatusIcon()}
        </CardHeader>
        <CardContent>
          <div className="text-2xl font-bold">{formatValue(metric_value)}</div>
          <div className="flex items-center justify-between mt-2">
            <Badge className={getStatusColor()}>
              {status === 'healthy' ? 'Healthy' : 
               status === 'alert' ? 'Alert' : 'No Data'}
            </Badge>
            <div className="flex items-center space-x-1">
              {getTrendIcon()}
              <span className="text-xs text-muted-foreground">
                Target: {formatValue(threshold)}
              </span>
            </div>
          </div>
        </CardContent>
      </Card>
    )
  }

  const getAlertDisplay = (alert: Alert) => {
    const getSeverityColor = () => {
      switch (alert.severity) {
        case 'critical': return 'bg-red-100 text-red-800 border-red-200'
        case 'warning': return 'bg-yellow-100 text-yellow-800 border-yellow-200'
        case 'info': return 'bg-blue-100 text-blue-800 border-blue-200'
        default: return 'bg-gray-100 text-gray-800 border-gray-200'
      }
    }

    const getSeverityIcon = () => {
      switch (alert.severity) {
        case 'critical': return <AlertTriangle className="w-4 h-4 text-red-600" />
        case 'warning': return <AlertTriangle className="w-4 h-4 text-yellow-600" />
        case 'info': return <Bell className="w-4 h-4 text-blue-600" />
        default: return <Bell className="w-4 h-4 text-gray-600" />
      }
    }

    return (
      <div key={alert.id} className={`p-4 border rounded-lg ${getSeverityColor()}`}>
        <div className="flex items-start justify-between">
          <div className="flex-1">
            <div className="flex items-center gap-2 mb-2">
              {getSeverityIcon()}
              <Badge variant="outline" className="capitalize">
                {alert.severity}
              </Badge>
              {alert.acknowledged && (
                <Badge variant="secondary" className="text-xs">
                  Acknowledged
                </Badge>
              )}
            </div>
            <h4 className="font-medium mb-1">
              {alert.metric.replace(/_/g, ' ').toUpperCase()}
            </h4>
            <p className="text-sm mb-2">{alert.message}</p>
            <div className="flex items-center gap-4 text-xs">
              <span>Current: {alert.currentValue.toFixed(1)}%</span>
              <span>Threshold: {alert.threshold.toFixed(1)}%</span>
              <span>Time: {new Date(alert.timestamp).toLocaleString()}</span>
            </div>
            {alert.acknowledged && (
              <div className="text-xs mt-2 text-gray-600">
                Acknowledged by {alert.acknowledgedBy} at {new Date(alert.acknowledgedAt!).toLocaleString()}
              </div>
            )}
          </div>
          {!alert.acknowledged && (
            <Button
              onClick={() => acknowledgeAlert(alert.id)}
              variant="outline"
              size="sm"
              className="ml-4"
            >
              <CheckCircle className="w-4 h-4 mr-1" />
              Acknowledge
            </Button>
          )}
        </div>
      </div>
    )
  }

  return (
    <div className={`space-y-6 ${className}`}>
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-3xl font-bold tracking-tight">Resilience Dashboard</h2>
          <p className="text-muted-foreground">
            Real-time monitoring of Bookiji's resilience patterns
          </p>
        </div>
        <div className="flex items-center space-x-4">
          {lastUpdated && (
            <span className="text-sm text-muted-foreground">
              Last updated: {lastUpdated.toLocaleTimeString()}
            </span>
          )}
          <Button 
            onClick={fetchMetrics} 
            disabled={isLoading}
            variant="outline"
            size="sm"
          >
            <RefreshCw className={`w-4 h-4 mr-2 ${isLoading ? 'animate-spin' : ''}`} />
            Refresh
          </Button>
        </div>
      </div>

      {/* Active Alerts Section */}
      {alerts.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Bell className="w-5 h-5" />
              Active Alerts ({alerts.filter(a => !a.acknowledged).length})
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {alerts.map(getAlertDisplay)}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Key Metrics Grid */}
      <div className="grid gap-4 md:grid-cols-3">
        {metrics.map(getMetricDisplay)}
      </div>

      {/* Health Summary */}
      <Card>
        <CardHeader>
          <CardTitle>System Health Summary</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {metrics.length === 0 ? (
              <div className="text-center py-8 text-muted-foreground">
                No metrics available
              </div>
            ) : (
              <>
                <div className="flex items-center justify-between">
                  <span>Overall Health</span>
                  <Badge className={
                    metrics.every(m => m.status === 'healthy') ? 'bg-green-100 text-green-800' :
                    metrics.some(m => m.status === 'alert') ? 'bg-red-100 text-red-800' :
                    'bg-yellow-100 text-yellow-800'
                  }>
                    {metrics.every(m => m.status === 'healthy') ? 'All Systems Healthy' :
                     metrics.some(m => m.status === 'alert') ? 'System Issues Detected' :
                     'Limited Data Available'}
                  </Badge>
                </div>

                <div className="grid gap-2 text-sm">
                  {metrics.map(metric => (
                    <div key={metric.metric_name} className="flex items-center justify-between">
                      <span className="capitalize">
                        {metric.metric_name.replace(/_/g, ' ')}
                      </span>
                      <span className={
                        metric.status === 'healthy' ? 'text-green-600' :
                        metric.status === 'alert' ? 'text-red-600' :
                        'text-yellow-600'
                      }>
                        {metric.status === 'healthy' ? '‚úì' : 
                         metric.status === 'alert' ? '‚úó' : '?'}
                      </span>
                    </div>
                  ))}
                </div>
              </>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Action Items */}
      <Card>
        <CardHeader>
          <CardTitle>Recommended Actions</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-3">
            {metrics.some(m => m.status === 'alert') ? (
              <>
                <div className="flex items-start space-x-3 p-3 bg-red-50 rounded-lg">
                  <AlertTriangle className="w-5 h-5 text-red-500 mt-0.5" />
                  <div>
                    <h4 className="font-medium text-red-800">Immediate Attention Required</h4>
                    <p className="text-sm text-red-700">
                      Some resilience metrics are outside healthy thresholds. 
                      Review system logs and consider scaling or optimization.
                    </p>
                  </div>
                </div>
              </>
            ) : metrics.every(m => m.status === 'healthy') ? (
              <div className="flex items-start space-x-3 p-3 bg-green-50 rounded-lg">
                <CheckCircle className="w-5 h-5 text-green-500 mt-0.5" />
                <div>
                  <h4 className="font-medium text-green-800">All Systems Operating Normally</h4>
                  <p className="text-sm text-green-700">
                    Resilience patterns are performing within expected parameters. 
                    Continue monitoring for any changes.
                  </p>
                </div>
              </div>
            ) : (
              <div className="flex items-start space-x-3 p-3 bg-yellow-50 rounded-lg">
                <AlertTriangle className="w-5 h-5 text-yellow-500 mt-0.5" />
                <div>
                  <h4 className="font-medium text-yellow-800">Limited Data Available</h4>
                  <p className="text-sm text-yellow-700">
                    Some metrics have insufficient data for health assessment. 
                    Wait for more data or investigate data collection.
                  </p>
                </div>
              </div>
            )}
          </div>
        </CardContent>
      </Card>
    </div>
  )
}

'use client'

import React, { useState, useEffect, useCallback } from 'react'
import StarRating from '@/components/ui/StarRating'

interface Review {
  id: string
  rating: number
  overall_quality?: number
  review_text?: string
  service_quality?: number
  communication?: number
  punctuality?: number
  value_for_money?: number
  would_recommend?: boolean
  photos?: string[]
  created_at: string
  status: string
  moderation_status: string
  spam_score: number
  helpful_count: number
  reported_count: number
  flagged_reason?: string
  reviewer: {
    name: string
    email: string
  }
  provider: {
    name: string
    email: string
  }
  booking: {
    service_name: string
    service_date: string
  }
}

interface ModerationAction {
  reviewId: string
  action: 'approve' | 'reject' | 'flag' | 'remove' | 'restore'
  reason?: string
  details?: Record<string, any>
}

export default function ReviewModeration() {
  const [reviews, setReviews] = useState<Review[]>([])
  const [loading, setLoading] = useState(true)
  const [filter, setFilter] = useState<'all' | 'pending' | 'flagged' | 'needs_review'>('pending')
  const [selectedReviews, setSelectedReviews] = useState<Set<string>>(new Set())
  const [bulkAction, setBulkAction] = useState<'approve' | 'reject' | 'flag' | 'remove'>('approve')
  const [bulkReason, setBulkReason] = useState('')
  const [processing, setProcessing] = useState(false)

  const loadReviews = useCallback(async () => {
    try {
      setLoading(true)
      const response = await fetch('/api/admin/reviews/moderation')
      const data = await response.json()

      if (data.reviews) {
        setReviews(data.reviews)
      }
    } catch (error) {
      console.error('Failed to load reviews:', error)
    } finally {
      setLoading(false)
    }
  }, [])

  useEffect(() => {
    loadReviews()
  }, [loadReviews])

  const handleBulkAction = async () => {
    if (selectedReviews.size === 0) return

    try {
      setProcessing(true)
      const response = await fetch('/api/admin/reviews/bulk-moderation', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          reviewIds: Array.from(selectedReviews),
          action: bulkAction,
          reason: bulkReason || undefined
        })
      })

      if (response.ok) {
        setSelectedReviews(new Set())
        setBulkReason('')
        loadReviews()
        alert(`Successfully processed ${selectedReviews.size} reviews`)
      } else {
        const error = await response.json()
        alert(`Error: ${error.message}`)
      }
    } catch (error) {
      console.error('Bulk action failed:', error)
      alert('Failed to process bulk action')
    } finally {
      setProcessing(false)
    }
  }

  const handleSingleAction = async (reviewId: string, action: string, reason?: string) => {
    try {
      const response = await fetch('/api/admin/reviews/moderation', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          reviewId,
          action,
          reason
        })
      })

      if (response.ok) {
        loadReviews()
        alert(`Review ${action}d successfully`)
      } else {
        const error = await response.json()
        alert(`Error: ${error.message}`)
      }
    } catch (error) {
      console.error('Action failed:', error)
      alert('Failed to process action')
    }
  }

  const toggleReviewSelection = (reviewId: string) => {
    const newSelection = new Set(selectedReviews)
    if (newSelection.has(reviewId)) {
      newSelection.delete(reviewId)
    } else {
      newSelection.add(reviewId)
    }
    setSelectedReviews(newSelection)
  }

  const getFilteredReviews = () => {
    switch (filter) {
      case 'pending':
        return reviews.filter(r => r.moderation_status === 'pending')
      case 'flagged':
        return reviews.filter(r => r.status === 'flagged')
      case 'needs_review':
        return reviews.filter(r => r.spam_score > 0.7 || r.reported_count > 0)
      default:
        return reviews
    }
  }

  const getSpamScoreColor = (score: number) => {
    if (score >= 0.8) return 'text-red-600 bg-red-100'
    if (score >= 0.6) return 'text-orange-600 bg-orange-100'
    if (score >= 0.4) return 'text-yellow-600 bg-yellow-100'
    return 'text-green-600 bg-green-100'
  }

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'published': return 'text-green-600 bg-green-100'
      case 'pending': return 'text-yellow-600 bg-yellow-100'
      case 'flagged': return 'text-red-600 bg-red-100'
      case 'removed': return 'text-gray-600 bg-gray-100'
      default: return 'text-gray-600 bg-gray-100'
    }
  }

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    })
  }

  const filteredReviews = getFilteredReviews()

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Review Moderation</h1>
          <p className="text-gray-600">Manage and moderate customer reviews with AI-powered spam detection</p>
        </div>
        <div className="flex items-center gap-4">
          <span className="text-sm text-gray-600">
            {filteredReviews.length} reviews to moderate
          </span>
        </div>
      </div>

      {/* Filters and Bulk Actions */}
      <div className="bg-white p-4 rounded-lg border border-gray-200">
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center gap-4">
            <select
              value={filter}
              onChange={(e) => setFilter(e.target.value as any)}
              className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              <option value="pending">Pending Review</option>
              <option value="flagged">Flagged Reviews</option>
              <option value="needs_review">Needs Attention</option>
              <option value="all">All Reviews</option>
            </select>
          </div>

          {selectedReviews.size > 0 && (
            <div className="flex items-center gap-3">
              <select
                value={bulkAction}
                onChange={(e) => setBulkAction(e.target.value as any)}
                className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              >
                <option value="approve">Approve</option>
                <option value="reject">Reject</option>
                <option value="flag">Flag</option>
                <option value="remove">Remove</option>
              </select>
              <input
                type="text"
                placeholder="Reason (optional)"
                value={bulkReason}
                onChange={(e) => setBulkReason(e.target.value)}
                className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent w-48"
              />
              <button
                onClick={handleBulkAction}
                disabled={processing}
                className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 transition-colors"
              >
                {processing ? 'Processing...' : `Apply to ${selectedReviews.size} reviews`}
              </button>
            </div>
          )}
        </div>

        {/* Statistics */}
        <div className="grid grid-cols-4 gap-4 text-sm">
          <div className="text-center p-3 bg-blue-50 rounded-lg">
            <div className="text-2xl font-bold text-blue-600">
              {reviews.filter(r => r.moderation_status === 'pending').length}
            </div>
            <div className="text-blue-600">Pending</div>
          </div>
          <div className="text-center p-3 bg-yellow-50 rounded-lg">
            <div className="text-2xl font-bold text-yellow-600">
              {reviews.filter(r => r.status === 'flagged').length}
            </div>
            <div className="text-yellow-600">Flagged</div>
          </div>
          <div className="text-center p-3 bg-red-50 rounded-lg">
            <div className="text-2xl font-bold text-red-600">
              {reviews.filter(r => r.spam_score > 0.7).length}
            </div>
            <div className="text-red-600">High Spam Risk</div>
          </div>
          <div className="text-center p-3 bg-green-50 rounded-lg">
            <div className="text-2xl font-bold text-green-600">
              {reviews.filter(r => r.moderation_status === 'approved').length}
            </div>
            <div className="text-green-600">Approved</div>
          </div>
        </div>
      </div>

      {/* Reviews List */}
      <div className="space-y-4">
        {loading ? (
          <div className="text-center py-8">
            <div className="animate-spin w-8 h-8 border-2 border-blue-600 border-t-transparent rounded-full mx-auto mb-4"></div>
            <p className="text-gray-600">Loading reviews...</p>
          </div>
        ) : filteredReviews.length === 0 ? (
          <div className="text-center py-8">
            <p className="text-gray-600">No reviews to moderate</p>
          </div>
        ) : (
          filteredReviews.map((review) => (
            <div key={review.id} className="bg-white p-6 rounded-lg border border-gray-200">
              <div className="flex items-start gap-4">
                {/* Selection Checkbox */}
                <input
                  type="checkbox"
                  checked={selectedReviews.has(review.id)}
                  onChange={() => toggleReviewSelection(review.id)}
                  className="mt-2 w-4 h-4 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
                />

                {/* Review Content */}
                <div className="flex-1">
                  <div className="flex items-start justify-between mb-3">
                    <div>
                      <h3 className="font-semibold text-gray-900">
                        Review by {review.reviewer.name}
                      </h3>
                      <p className="text-sm text-gray-600">
                        {review.booking.service_name} ‚Ä¢ {formatDate(review.booking.service_date)}
                      </p>
                    </div>
                    <div className="text-right space-y-1">
                      <div className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(review.status)}`}>
                        {review.status}
                      </div>
                      <div className={`px-2 py-1 rounded-full text-xs font-medium ${getSpamScoreColor(review.spam_score)}`}>
                        Spam: {(review.spam_score * 100).toFixed(0)}%
                      </div>
                      <div className="text-xs text-gray-500">
                        {formatDate(review.created_at)}
                      </div>
                    </div>
                  </div>

                  {/* Rating */}
                  <div className="mb-3">
                    <StarRating 
                      value={review.overall_quality || review.rating} 
                      readonly 
                      allowHalfStars={true}
                      showValue={false}
                    />
                  </div>

                  {/* Review Text */}
                  {review.review_text && (
                    <p className="text-gray-700 mb-3">{review.review_text}</p>
                  )}

                  {/* Detailed Ratings */}
                  {(review.service_quality || review.communication || review.punctuality || review.value_for_money) && (
                    <div className="grid grid-cols-2 gap-3 text-sm mb-3">
                      {review.service_quality && (
                        <div className="flex items-center justify-between">
                          <span className="text-gray-600">Service Quality:</span>
                          <StarRating 
                            value={review.service_quality} 
                            readonly 
                            size="small" 
                            allowHalfStars={true}
                            showValue={false}
                          />
                        </div>
                      )}
                      {review.communication && (
                        <div className="flex items-center justify-between">
                          <span className="text-gray-600">Communication:</span>
                          <StarRating 
                            value={review.communication} 
                            readonly 
                            size="small" 
                            allowHalfStars={true}
                            showValue={false}
                          />
                        </div>
                      )}
                      {review.punctuality && (
                        <div className="flex items-center justify-between">
                          <span className="text-gray-600">Punctuality:</span>
                          <StarRating 
                            value={review.punctuality} 
                            readonly 
                            size="small" 
                            allowHalfStars={true}
                            showValue={false}
                          />
                        </div>
                      )}
                      {review.value_for_money && (
                        <div className="flex items-center justify-between">
                          <span className="text-gray-600">Value for Money:</span>
                          <StarRating 
                            value={review.value_for_money} 
                            readonly 
                            size="small" 
                            allowHalfStars={true}
                            showValue={false}
                          />
                        </div>
                      )}
                    </div>
                  )}

                  {/* Additional Info */}
                  <div className="grid grid-cols-3 gap-4 text-sm text-gray-600 mb-4">
                    <div>
                      <span className="font-medium">Helpful:</span> {review.helpful_count}
                    </div>
                    <div>
                      <span className="font-medium">Reported:</span> {review.reported_count}
                    </div>
                    <div>
                      <span className="font-medium">Would Recommend:</span> {review.would_recommend ? 'Yes' : 'No'}
                    </div>
                  </div>

                  {/* Photos */}
                  {review.photos && review.photos.length > 0 && (
                    <div className="mb-4">
                      <p className="text-sm font-medium text-gray-700 mb-2">Photos:</p>
                      <div className="flex gap-2">
                        {review.photos.map((photo, index) => (
                          <img
                            key={index}
                            src={photo}
                            alt={`Review photo ${index + 1}`}
                            className="w-16 h-16 object-cover rounded-lg"
                          />
                        ))}
                      </div>
                    </div>
                  )}

                  {/* Action Buttons */}
                  <div className="flex items-center gap-3">
                    <button
                      onClick={() => handleSingleAction(review.id, 'approve')}
                      className="px-3 py-1 bg-green-600 text-white text-sm rounded hover:bg-green-700 transition-colors"
                    >
                      ‚úì Approve
                    </button>
                    <button
                      onClick={() => handleSingleAction(review.id, 'reject')}
                      className="px-3 py-1 bg-red-600 text-white text-sm rounded hover:bg-red-700 transition-colors"
                    >
                      ‚úó Reject
                    </button>
                    <button
                      onClick={() => handleSingleAction(review.id, 'flag')}
                      className="px-3 py-1 bg-yellow-600 text-white text-sm rounded hover:bg-yellow-700 transition-colors"
                    >
                      ‚ö†Ô∏è Flag
                    </button>
                    <button
                      onClick={() => handleSingleAction(review.id, 'remove')}
                      className="px-3 py-1 bg-gray-600 text-white text-sm rounded hover:bg-gray-700 transition-colors"
                    >
                      üóëÔ∏è Remove
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  )
}

