function assertString(value: unknown, field: string): string {
  if (typeof value !== "string" || value.trim().length === 0) {
    throw new Error(`Expected ${field} to be a non-empty string`);
  }
  return value;
}

function coerceArray<T>(value: unknown): T[] {
  return Array.isArray(value) ? (value as T[]) : [];
}

export function loadGenome(genomePath = path.join(process.cwd(), "genome", "master-genome.yaml")): GenomeSpec {
  if (!fs.existsSync(genomePath)) {
    throw new Error(`Genome spec not found at ${genomePath}`);
  }

  const raw = fs.readFileSync(genomePath, "utf8");
  const parsed = yaml.load(raw);

  if (!parsed || typeof parsed !== "object") {
    throw new Error("Genome spec is empty or invalid");
  }

  const root = parsed as Record<string, unknown>;
  const version = assertString(root.version, "version");
  const domainsRaw = root.domains;

  if (!domainsRaw || typeof domainsRaw !== "object") {
    throw new Error("Genome spec is missing required 'domains' section");
  }

  const domains = domainsRaw as Record<string, unknown>;

  const normalizeModules = coerceArray<ModuleSpec>(domains.core && (domains.core as any).modules).map((module) => {
    if (!module || typeof module !== "object") {
      throw new Error("Module entries must be objects");
    }
    const record = module as unknown as Record<string, unknown>;
    return {
      id: assertString(record.id, "domains.core.modules[].id"),
      name: typeof record.name === "string" ? record.name : undefined,
      path: assertString(record.path, "domains.core.modules[].path"),
      requiredFiles: coerceArray<string>(record.requiredFiles),
    } satisfies ModuleSpec;
  });

  if (normalizeModules.length === 0) {
    throw new Error("Genome spec must define at least one core module");
  }

  const genome: GenomeSpec = {
    version,
    codename: typeof root.codename === "string" ? root.codename : undefined,
    releaseDate: typeof root.releaseDate === "string" ? root.releaseDate : undefined,
    description: typeof root.description === "string" ? root.description : undefined,
    domains: {
      core: {
        modules: normalizeModules,
        runtimeProfiles: coerceArray<RuntimeProfileSpec>(domains.core && (domains.core as any).runtimeProfiles),
      },
      events: {
        contracts: coerceArray<ContractSpec>(domains.events && (domains.events as any).contracts),
        catalog: (domains.events && (domains.events as any).catalog) as CatalogSpec | undefined,
        telemetry: (domains.events && (domains.events as any).telemetry) as GenomeDomains["events"]["telemetry"],
      },
      analytics: {
        schema: typeof (domains.analytics as any)?.schema === "string" ? (domains.analytics as any).schema : undefined,
        required: coerceArray<string>(domains.analytics && (domains.analytics as any).required),
      },
      temporal: {
        audit: (domains.temporal && (domains.temporal as any).audit) as GenomeDomains["temporal"]["audit"],
        timelines: coerceArray<TimelineSpec>(domains.temporal && (domains.temporal as any).timelines),
      },
      opsai: {
        services: coerceArray<ServiceSpec>(domains.opsai && (domains.opsai as any).services),
        diagnostics: (domains.opsai && (domains.opsai as any).diagnostics) as OpsDiagnosticsSpec | undefined,
      },
      simcity: {
        scenarios: (domains.simcity && (domains.simcity as any).scenarios) as ScenarioSpec | undefined,
        traffic: (domains.simcity && (domains.simcity as any).traffic) as TrafficSpec | undefined,
      },
      helpCenter: {
        knowledgeBase: (domains.helpCenter && (domains.helpCenter as any).knowledgeBase) as KnowledgeBaseSpec | undefined,
        userGuides: (domains.helpCenter && (domains.helpCenter as any).userGuides) as GenomeDomains["helpCenter"]["userGuides"],
      },
      notifications: {
        channels: coerceArray<ChannelSpec>(domains.notifications && (domains.notifications as any).channels),
        requiredFiles: coerceArray<string>(domains.notifications && (domains.notifications as any).requiredFiles),
      },
      trustSafety: {
        ledgers: coerceArray<string>(domains.trustSafety && (domains.trustSafety as any).ledgers),
        drills: (domains.trustSafety && (domains.trustSafety as any).drills) as GenomeDomains["trustSafety"]["drills"],
      },
      businessIntelligence: {
        telemetry: (domains.businessIntelligence && (domains.businessIntelligence as any).telemetry) as GenomeDomains["businessIntelligence"]["telemetry"],
        qualityGates: coerceArray<string>(domains.businessIntelligence && (domains.businessIntelligence as any).qualityGates),
      },
      governance: {
        policies: coerceArray<string>(domains.governance && (domains.governance as any).policies),
        approvals: (domains.governance && (domains.governance as any).approvals) as GenomeDomains["governance"]["approvals"],
        contracts: coerceArray<string>(domains.governance && (domains.governance as any).contracts),
        required:
          typeof (domains.governance as any)?.required === "boolean"
            ? ((domains.governance as any).required as boolean)
            : false,
      },
      evolution: {
        flags: coerceArray<string>(domains.evolution && (domains.evolution as any).flags),
        roadmap: (domains.evolution && (domains.evolution as any).roadmap) as GenomeDomains["evolution"]["roadmap"],
      },
      notifications_2: {
        schema:
          typeof (domains as any)?.notifications_2?.schema === "string"
            ? (domains as any).notifications_2.schema
            : undefined,
        required: coerceArray<string>((domains as any)?.notifications_2?.required),
      },
      trust_safety: {
        schema:
          typeof (domains as any)?.trust_safety?.schema === "string"
            ? (domains as any).trust_safety.schema
            : undefined,
        required: coerceArray<string>((domains as any)?.trust_safety?.required),
      },
    },
  };

  return genome;
}

import { useCallback, useRef } from 'react';

export function useDebounce<T extends (...args: any[]) => any>(
  callback: T,
  delay: number
): T {
  const timeoutRef = useRef<NodeJS.Timeout | undefined>(undefined);

  const debouncedCallback = useCallback(
    (...args: Parameters<T>) => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }

      timeoutRef.current = setTimeout(() => {
        callback(...args);
      }, delay);
    },
    [callback, delay]
  ) as T;

  return debouncedCallback;
}

// Specialized hook for button clicks
export function useDebouncedClick(
  onClick: () => void | Promise<void>,
  delay: number = 300
) {
  const isProcessing = useRef(false);

  const debouncedClick = useCallback(async () => {
    if (isProcessing.current) return;
    
    isProcessing.current = true;
    
    try {
      await onClick();
    } finally {
      // Ensure we don't block future clicks
      setTimeout(() => {
        isProcessing.current = false;
      }, delay);
    }
  }, [onClick, delay]);

  return debouncedClick;
}

// Hook for form submissions
export function useDebouncedSubmit<T extends Record<string, any>>(
  onSubmit: (data: T) => Promise<void>,
  delay: number = 500
) {
  const isSubmitting = useRef(false);

  const debouncedSubmit = useCallback(async (data: T) => {
    if (isSubmitting.current) return;
    
    isSubmitting.current = true;
    
    try {
      await onSubmit(data);
    } finally {
      setTimeout(() => {
        isSubmitting.current = false;
      }, delay);
    }
  }, [onSubmit, delay]);

  return {
    submit: debouncedSubmit,
    isSubmitting: isSubmitting.current
  };
}

import { useCallback, useRef } from 'react'

interface UseDebouncedClickOptions {
  delay?: number
  onDuplicate?: () => void
}

export function useDebouncedClick<T extends (...args: any[]) => any>(
  callback: T,
  options: UseDebouncedClickOptions = {}
): T {
  const { delay = 300, onDuplicate } = options
  const lastClickTime = useRef<number>(0)
  const isProcessing = useRef<boolean>(false)

  const debouncedCallback = useCallback(
    (...args: Parameters<T>) => {
      const now = Date.now()
      
      // Check if we're already processing
      if (isProcessing.current) {
        onDuplicate?.()
        return
      }

      // Check if click is too soon after last click
      if (now - lastClickTime.current < delay) {
        onDuplicate?.()
        return
      }

      // Update state and execute
      lastClickTime.current = now
      isProcessing.current = true

      // Execute callback
      const result = callback(...args)

      // Reset processing flag after delay
      setTimeout(() => {
        isProcessing.current = false
      }, delay)

      return result
    },
    [callback, delay, onDuplicate]
  ) as T

  return debouncedCallback
}

import { useCallback, useRef } from 'react'
import { telemetry } from '@/lib/telemetry/resilienceTelemetry'

interface UseDebouncedClickWithTelemetryOptions {
  delay?: number
  onDuplicate?: () => void
  component: string // Required for telemetry
}

export function useDebouncedClickWithTelemetry<T extends (...args: any[]) => any>(
  callback: T,
  options: UseDebouncedClickWithTelemetryOptions
): T {
  const { delay = 300, onDuplicate, component } = options
  const lastClickTime = useRef<number>(0)
  const isProcessing = useRef<boolean>(false)
  const suppressedClicks = useRef<number>(0)

  const debouncedCallback = useCallback(
    (...args: Parameters<T>) => {
      const now = Date.now()
      
      // Check if we're already processing
      if (isProcessing.current) {
        suppressedClicks.current++
        telemetry.debouncedClickSuppressed(component, 'already_processing')
        onDuplicate?.()
        return
      }

      // Check if click is too soon after last click
      if (now - lastClickTime.current < delay) {
        suppressedClicks.current++
        telemetry.debouncedClickSuppressed(component, 'too_soon')
        onDuplicate?.()
        return
      }

      // Update state and execute
      lastClickTime.current = now
      isProcessing.current = true

      // Execute callback
      const result = callback(...args)

      // Reset processing flag after delay
      setTimeout(() => {
        isProcessing.current = false
      }, delay)

      return result
    },
    [callback, delay, onDuplicate, component]
  ) as T

  return debouncedCallback
}

import { useState, useEffect, useCallback } from 'react'
import { supabaseBrowserClient } from '@/lib/supabaseClient'
import { Notification } from '@/types/notification'
import { NotificationService } from '@/lib/services/notificationService'

interface NotificationState {
  data: Notification[];
  isLoading: boolean;
  error: string | null;
}

export function useNotifications() {
  const [state, setState] = useState<NotificationState>({
    data: [],
    isLoading: false,
    error: null
  })

  const notificationService = NotificationService.getInstance()

  const fetchNotifications = useCallback(async () => {
    const supabase = supabaseBrowserClient()
    if (!supabase) {
      setState(prev => ({ ...prev, isLoading: false, error: 'Supabase client not available' }))
      return
    }

    const { data: { session } } = await supabase.auth.getSession()
    if (!session) {
      // Don't set error for unauthenticated users - just return empty state
      setState(prev => ({
        ...prev,
        isLoading: false,
        error: null,
        data: []
      }))
      return
    }

    setState(prev => ({ ...prev, isLoading: true, error: null }))

    try {
      const notifications = await notificationService.fetchNotifications(session.access_token)
      setState({
        data: notifications,
        isLoading: false,
        error: null
      })
    } catch (error) {
      setState(prev => ({
        ...prev,
        isLoading: false,
        error: error instanceof Error ? error.message : 'Failed to load notifications'
      }))
    }
  }, [notificationService])

  const markAsRead = useCallback(async (notificationId: string) => {
    const supabase = supabaseBrowserClient()
    if (!supabase) return

    const { data: { session } } = await supabase.auth.getSession()
    if (!session) return
    try {
      await notificationService.markAsRead(notificationId, session.access_token)
      setState(prev => ({
        ...prev,
        data: prev.data.map(notification =>
          notification.id === notificationId

            ? { ...notification, read: true, read_at: new Date().toISOString() }

            : notification
        )
      }))
    } catch (error) {
      console.error('Failed to mark notification as read:', error)
    }
  }, [notificationService])

  const markAllAsRead = useCallback(async () => {
    const supabase = supabaseBrowserClient()
    if (!supabase) return

    const { data: { session } } = await supabase.auth.getSession()
    if (!session) return
    try {
      await notificationService.markAllAsRead(session.access_token)
      const timestamp = new Date().toISOString()
      setState(prev => ({
        ...prev,

        data: prev.data.map(notification => ({ ...notification, read: true, read_at: timestamp }))

      }))
    } catch (error) {
      console.error('Failed to mark all notifications as read:', error)
    }
  }, [notificationService])

  const deleteNotification = useCallback(async (notificationId: string) => {
    const supabase = supabaseBrowserClient()
    if (!supabase) return

    const { data: { session } } = await supabase.auth.getSession()
    if (!session) return
    try {
      await notificationService.deleteNotification(notificationId, session.access_token)
      setState(prev => ({
        ...prev,
        data: prev.data.filter(notification => notification.id !== notificationId)
      }))
    } catch (error) {
      console.error('Failed to delete notification:', error)
    }
  }, [notificationService])

  // Set up real-time notifications with reconnection logic
  useEffect(() => {
    let channel: ReturnType<NonNullable<ReturnType<typeof supabaseBrowserClient>>['channel']> | null = null
    let reconnectTimeout: ReturnType<typeof setTimeout> | null = null
    let currentSupabase: ReturnType<typeof supabaseBrowserClient> | null = null
    let reconnectAttempts = 0
    let lastErrorTime = 0
    const MAX_RECONNECT_ATTEMPTS = 5
    const INITIAL_RECONNECT_DELAY = 2000 // 2 seconds
    const MAX_RECONNECT_DELAY = 30000 // 30 seconds
    let isSubscribed = false

    const subscribe = async () => {
      const supabase = supabaseBrowserClient()
      if (!supabase) return
      currentSupabase = supabase

      const { data: { session } } = await supabase.auth.getSession()
      if (!session?.user?.id) return

      // Clean up existing channel if any
      if (channel) {
        supabase.removeChannel(channel)
        channel = null
      }

      try {
        channel = supabase
          .channel(`notifications-${session.user.id}`, {
            config: {
              broadcast: { self: false },
              presence: { key: session.user.id }
            }
          })
          .on(
            'postgres_changes',
            {
              event: 'INSERT',
              schema: 'public',
              table: 'notifications',
              filter: `user_id=eq.${session.user.id}`
            },
            (payload) => {
              setState(prev => ({
                ...prev,
                data: [payload.new as Notification, ...prev.data]
              }))
            }
          )
          .on(
            'postgres_changes',
            {
              event: 'UPDATE',
              schema: 'public',
              table: 'notifications',
              filter: `user_id=eq.${session.user.id}`
            },
            (payload) => {
              setState(prev => ({
                ...prev,
                data: prev.data.map(notification =>
                  notification.id === (payload.new as Notification).id
                    ? payload.new as Notification
                    : notification
                )
              }))
            }
          )
          .on(
            'postgres_changes',
            {
              event: 'DELETE',
              schema: 'public',
              table: 'notifications',
              filter: `user_id=eq.${session.user.id}`
            },
            (payload) => {
              setState(prev => ({
                ...prev,
                data: prev.data.filter(notification => notification.id !== payload.old.id)
              }))
            }
          )
          .subscribe((status) => {
            if (status === 'SUBSCRIBED') {
              isSubscribed = true
              reconnectAttempts = 0 // Reset on successful subscription
              lastErrorTime = 0
            } else if (status === 'CHANNEL_ERROR') {
              isSubscribed = false
              // Only log if it's been more than 10 seconds since last error (avoid spam)
              const now = Date.now()
              if (now - lastErrorTime > 10000) {
                console.warn('Notifications real-time channel error. Will retry automatically.')
                lastErrorTime = now
              }
              if (reconnectAttempts < MAX_RECONNECT_ATTEMPTS) {
                attemptReconnect()
              } else {
                console.warn('Max reconnection attempts reached. Real-time notifications disabled. Polling will continue.')
              }
            } else if (status === 'CLOSED') {
              isSubscribed = false
              // Only attempt reconnect if it wasn't intentionally closed
              if (reconnectAttempts < MAX_RECONNECT_ATTEMPTS) {
                attemptReconnect()
              }
            } else if (status === 'TIMED_OUT') {
              isSubscribed = false
              if (reconnectAttempts < MAX_RECONNECT_ATTEMPTS) {
                attemptReconnect()
              }
            }
          })
      } catch (error) {
        console.error('Failed to set up notifications channel:', error)
        isSubscribed = false
        if (reconnectAttempts < MAX_RECONNECT_ATTEMPTS) {
          attemptReconnect()
        }
      }
    }

    const attemptReconnect = () => {
      if (reconnectTimeout) return

      reconnectAttempts++
      // Exponential backoff: 2s, 4s, 8s, 16s, 30s (max)
      const delay = Math.min(
        INITIAL_RECONNECT_DELAY * Math.pow(2, reconnectAttempts - 1),
        MAX_RECONNECT_DELAY
      )

      reconnectTimeout = setTimeout(() => {
        reconnectTimeout = null
        const supabase = supabaseBrowserClient()
        if (!supabase) return

        subscribe()
      }, delay)
    }

    subscribe()

    return () => {
      if (channel && currentSupabase) {
        currentSupabase.removeChannel(channel)
        channel = null
      }
      if (reconnectTimeout) {
        clearTimeout(reconnectTimeout)
        reconnectTimeout = null
      }
      isSubscribed = false
    }
  }, [])

  // Initial fetch - only run once on mount
  useEffect(() => {
    fetchNotifications()
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []) // Intentionally empty - only fetch once on mount

  return {
    notifications: state.data,
    unreadCount: state.data.filter(n => !n.read).length,
    isLoading: state.isLoading,
    error: state.error,
    markAsRead,
    markAllAsRead,
    deleteNotification,
    refresh: fetchNotifications
  }
}

import { useState, useCallback } from 'react'
import { telemetry } from '@/lib/telemetry/resilienceTelemetry'

interface UseOptimisticActionWithTelemetryOptions<T, P = void> {
  action: (params: P) => Promise<T>
  onOptimistic?: (params: P) => void
  onSuccess?: (result: T, params: P) => void
  onError?: (error: Error, params: P) => void
  onRollback?: (params: P) => void
  component: string // Required for telemetry
}

interface UseOptimisticActionWithTelemetryReturn<T, P> {
  execute: (params: P) => Promise<T | null>
  isLoading: boolean
  error: Error | null
  status: 'idle' | 'optimistic' | 'loading' | 'success' | 'error'
  rollback: (params: P) => void
}

export function useOptimisticActionWithTelemetry<T, P = void>({
  action,
  onOptimistic,
  onSuccess,
  onError,
  onRollback,
  component
}: UseOptimisticActionWithTelemetryOptions<T, P>): UseOptimisticActionWithTelemetryReturn<T, P> {
  const [status, setStatus] = useState<'idle' | 'optimistic' | 'loading' | 'success' | 'error'>('idle')
  const [error, setError] = useState<Error | null>(null)
  const [actionStartTime, setActionStartTime] = useState<number>(0)

  const execute = useCallback(async (params: P): Promise<T | null> => {
    const actionId = `${component}_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`
    const startTime = Date.now()
    
    try {
      // 1. OPTIMISTIC UPDATE
      setStatus('optimistic')
      setActionStartTime(startTime)
      
      // Track optimistic action start
      telemetry.optimisticActionStart(component, actionId)
      
      onOptimistic?.(params)

      // 2. EXECUTE ACTION
      setStatus('loading')
      const result = await action(params)

      // 3. SUCCESS
      const duration = Date.now() - startTime
      setStatus('success')
      setError(null)
      
      // Track successful action
      telemetry.optimisticActionSuccess(component, actionId, duration)
      
      onSuccess?.(result, params)
      return result

    } catch (err) {
      // 4. ERROR - ROLLBACK
      const duration = Date.now() - startTime
      const error = err instanceof Error ? err : new Error('Action failed')
      
      setStatus('error')
      setError(error)
      
      // Track rollback with reason
      const rollbackReason = error.message || 'Unknown error'
      telemetry.optimisticActionRollback(component, actionId, duration, rollbackReason)
      
      onError?.(error, params)
      onRollback?.(params)
      return null
    }
  }, [action, onOptimistic, onSuccess, onError, onRollback, component])

  const rollback = useCallback((params: P) => {
    setStatus('idle')
    setError(null)
    onRollback?.(params)
  }, [onRollback])

  return {
    execute,
    isLoading: status === 'loading',
    error,
    status,
    rollback
  }
}

import { useCallback, useEffect, useRef } from 'react'
import { performanceGuardrails, withPerformanceMonitoring } from '@/lib/performance/guardrails'

interface UsePerformanceMonitoringOptions {
  operationName: string
  enabled?: boolean
  trackMemory?: boolean
  trackCpu?: boolean
}

export function usePerformanceMonitoring(options: UsePerformanceMonitoringOptions) {
  const { operationName, enabled = true, trackMemory = true, trackCpu = false } = options
  const startTimeRef = useRef<number>(0)
  const startMemoryRef = useRef<number>(0)

  const startMonitoring = useCallback(() => {
    if (!enabled) return
    
    startTimeRef.current = Date.now()
    if (trackMemory && typeof process !== 'undefined' && process.memoryUsage) {
      startMemoryRef.current = process.memoryUsage().heapUsed
    }
  }, [enabled, trackMemory])

  const stopMonitoring = useCallback((success: boolean = true) => {
    if (!enabled) return

    const responseTime = Date.now() - startTimeRef.current
    let memoryUsage = 0

    if (trackMemory && typeof process !== 'undefined' && process.memoryUsage) {
      const endMemory = process.memoryUsage().heapUsed
      memoryUsage = (endMemory - startMemoryRef.current) / 1024 / 1024 // Convert to MB
    }

    // Estimate CPU usage (simplified - in real apps you'd use system metrics)
    const cpuUsage = trackCpu ? Math.random() * 100 : 0

    performanceGuardrails.recordMetrics({
      responseTime,
      memoryUsage,
      cpuUsage,
      requestCount: 1,
      errorCount: success ? 0 : 1,
      costEstimate: 0
    })
  }, [enabled, trackMemory, trackCpu])

  const monitorFunction = useCallback(<T extends any[], R>(
    fn: (...args: T) => R | Promise<R>
  ): ((...args: T) => Promise<R>) => {
    return withPerformanceMonitoring(fn, operationName)
  }, [operationName])

  // Auto-monitor component lifecycle
  useEffect(() => {
    if (enabled) {
      startMonitoring()
      
      return () => {
        stopMonitoring(true)
      }
    }
  }, [enabled, startMonitoring, stopMonitoring])

  return {
    startMonitoring,
    stopMonitoring,
    monitorFunction,
    isEnabled: enabled
  }
}

// Convenience hook for API calls
export function useApiPerformanceMonitoring(operationName: string) {
  const { monitorFunction } = usePerformanceMonitoring({ operationName })

  const monitorApiCall = useCallback(<T extends any[], R>(
    apiFunction: (...args: T) => Promise<R>
  ) => {
    return monitorFunction(apiFunction)
  }, [monitorFunction])

  return { monitorApiCall }
}

// Hook for component render performance
export function useRenderPerformance(componentName: string) {
  const { startMonitoring, stopMonitoring } = usePerformanceMonitoring({
    operationName: `${componentName}_render`,
    enabled: process.env.NODE_ENV === 'development'
  })

  useEffect(() => {
    startMonitoring()
    
    // Stop monitoring after render
    const timer = setTimeout(() => {
      stopMonitoring(true)
    }, 0)

    return () => clearTimeout(timer)
  }, [startMonitoring, stopMonitoring])

  return { startMonitoring, stopMonitoring }
}

'use client'

import { useState, useEffect, useCallback } from 'react'
import { pushNotificationManager } from '@/lib/notifications/pushNotifications'

export interface PushSubscriptionState {
  isSupported: boolean
  isSubscribed: boolean
  isSubscribing: boolean
  error: string | null
}

/**
 * Hook for managing Web Push subscriptions
 */
export function usePushSubscription() {
  const [state, setState] = useState<PushSubscriptionState>({
    isSupported: false,
    isSubscribed: false,
    isSubscribing: false,
    error: null
  })

  useEffect(() => {
    // Check if push is supported
    const isSupported = 'serviceWorker' in navigator && 'PushManager' in window
    setState(prev => ({ ...prev, isSupported }))

    if (isSupported) {
      checkSubscriptionStatus()
    }
  }, [])

  const checkSubscriptionStatus = useCallback(async () => {
    try {
      if ('serviceWorker' in navigator) {
        const registration = await navigator.serviceWorker.ready
        const subscription = await registration.pushManager.getSubscription()
        setState(prev => ({ ...prev, isSubscribed: !!subscription }))
      }
    } catch (error) {
      console.error('Error checking subscription status:', error)
      setState(prev => ({ ...prev, error: 'Failed to check subscription status' }))
    }
  }, [])

  const subscribe = useCallback(async () => {
    if (!state.isSupported) {
      setState(prev => ({ ...prev, error: 'Push notifications are not supported' }))
      return false
    }

    setState(prev => ({ ...prev, isSubscribing: true, error: null }))

    try {
      // Get VAPID public key
      const vapidResponse = await fetch('/api/notifications/push/vapid-public-key')
      const { publicKey } = await vapidResponse.json()

      if (!publicKey) {
        throw new Error('VAPID public key not available')
      }

      // Register service worker
      const registration = await navigator.serviceWorker.ready

      // Subscribe to push
      const keyArray = urlBase64ToUint8Array(publicKey)
      const subscription = await registration.pushManager.subscribe({
        userVisibleOnly: true,
        applicationServerKey: keyArray as BufferSource
      })

      const p256dhKey = subscription.getKey('p256dh')
      const authKey = subscription.getKey('auth')

      if (!p256dhKey || !authKey) {
        throw new Error('Push subscription keys missing')
      }

      // Send subscription to server
      const response = await fetch('/api/notifications/push/subscribe', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          endpoint: subscription.endpoint,
          keys: {
            p256dh: uint8ArrayToBase64(new Uint8Array(p256dhKey)),
            auth: uint8ArrayToBase64(new Uint8Array(authKey))
          }
        })
      })

      if (!response.ok) {
        throw new Error('Failed to save subscription')
      }

      setState(prev => ({ ...prev, isSubscribed: true, isSubscribing: false }))
      return true
    } catch (error) {
      console.error('Error subscribing to push:', error)
      setState(prev => ({
        ...prev,
        error: error instanceof Error ? error.message : 'Failed to subscribe',
        isSubscribing: false
      }))
      return false
    }
  }, [state.isSupported])

  const unsubscribe = useCallback(async () => {
    setState(prev => ({ ...prev, isSubscribing: true, error: null }))

    try {
      const registration = await navigator.serviceWorker.ready
      const subscription = await registration.pushManager.getSubscription()

      if (subscription) {
        // Delete from server
        await fetch('/api/notifications/push/subscribe', {
          method: 'DELETE',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ endpoint: subscription.endpoint })
        })

        // Unsubscribe from push
        await subscription.unsubscribe()
      }

      setState(prev => ({ ...prev, isSubscribed: false, isSubscribing: false }))
      return true
    } catch (error) {
      console.error('Error unsubscribing from push:', error)
      setState(prev => ({
        ...prev,
        error: error instanceof Error ? error.message : 'Failed to unsubscribe',
        isSubscribing: false
      }))
      return false
    }
  }, [])

  return {
    ...state,
    subscribe,
    unsubscribe,
    refresh: checkSubscriptionStatus
  }
}

// Helper functions for VAPID key conversion
function urlBase64ToUint8Array(base64String: string): Uint8Array {
  const padding = '='.repeat((4 - (base64String.length % 4)) % 4)
  const base64 = (base64String + padding).replace(/-/g, '+').replace(/_/g, '/')
  const rawData = window.atob(base64)
  const outputArray = new Uint8Array(rawData.length)

  for (let i = 0; i < rawData.length; ++i) {
    outputArray[i] = rawData.charCodeAt(i)
  }
  return outputArray
}

function uint8ArrayToBase64(array: Uint8Array): string {
  const bytes = new Uint8Array(array)
  let binary = ''
  for (let i = 0; i < bytes.byteLength; i++) {
    binary += String.fromCharCode(bytes[i])
  }
  return window.btoa(binary)
}

import { useState, useEffect, useCallback, useRef } from 'react'

interface UseResilientQueryOptions<T> {
  key: string | string[]
  fetcher: () => Promise<T>
  retry?: {
    attempts: number
    backoff: 'exponential' | 'linear' | 'fixed'
    delay?: number
  }
  staleTime?: number
  cacheTime?: number
  enabled?: boolean
}

interface UseResilientQueryReturn<T> {
  data: T | null
  error: Error | null
  isLoading: boolean
  isStale: boolean
  refetch: () => Promise<void>
  retry: () => Promise<void>
}

interface CacheEntry<T> {
  data: T
  timestamp: number
  isStale: boolean
}

const queryCache = new Map<string, CacheEntry<any>>()

export function useResilientQuery<T>({
  key,
  fetcher,
  retry = { attempts: 3, backoff: 'exponential', delay: 1000 },
  staleTime = 30000, // 30 seconds
  cacheTime = 300000, // 5 minutes
  enabled = true
}: UseResilientQueryOptions<T>): UseResilientQueryReturn<T> {
  const [data, setData] = useState<T | null>(null)
  const [error, setError] = useState<Error | null>(null)
  const [isLoading, setIsLoading] = useState(false)
  const [isStale, setIsStale] = useState(false)
  const [attempts, setAttempts] = useState(0)
  const abortControllerRef = useRef<AbortController | null>(null)

  const cacheKey = Array.isArray(key) ? key.join(':') : key

  const calculateDelay = useCallback((attempt: number): number => {
    switch (retry.backoff) {
      case 'exponential':
        return retry.delay! * Math.pow(2, attempt)
      case 'linear':
        return retry.delay! * (attempt + 1)
      case 'fixed':
        return retry.delay!
      default:
        return retry.delay!
    }
  }, [retry])

  const executeQuery = useCallback(async (isRetry = false): Promise<void> => {
    if (!enabled) return

    try {
      setIsLoading(true)
      setError(null)

      // Check cache first
      const cached = queryCache.get(cacheKey)
      if (cached && !isRetry) {
        const age = Date.now() - cached.timestamp
        if (age < cacheTime) {
          setData(cached.data)
          setIsStale(age > staleTime)
          setIsLoading(false)
          return
        }
      }

      // Abort previous request if still running
      if (abortControllerRef.current) {
        abortControllerRef.current.abort()
      }

      abortControllerRef.current = new AbortController()
      const result = await fetcher()

      // Cache successful result
      queryCache.set(cacheKey, {
        data: result,
        timestamp: Date.now(),
        isStale: false
      })

      setData(result)
      setIsStale(false)
      setAttempts(0)

    } catch (err) {
      if (err instanceof Error && err.name === 'AbortError') {
        return // Request was aborted
      }

      const error = err instanceof Error ? err : new Error('Query failed')
      setError(error)

      // Retry logic
      if (attempts < retry.attempts && !isRetry) {
        const delay = calculateDelay(attempts)
        setTimeout(() => {
          setAttempts(prev => prev + 1)
          executeQuery(true)
        }, delay)
      }
    } finally {
      setIsLoading(false)
    }
  }, [key, fetcher, retry, staleTime, cacheTime, enabled, attempts, cacheKey, calculateDelay])

  const refetch = useCallback(async (): Promise<void> => {
    setAttempts(0)
    await executeQuery()
  }, [executeQuery])

  const retryQuery = useCallback(async (): Promise<void> => {
    setAttempts(0)
    await executeQuery(true)
  }, [executeQuery])

  useEffect(() => {
    executeQuery()
    
    return () => {
      if (abortControllerRef.current) {
        abortControllerRef.current.abort()
      }
    }
  }, [executeQuery])

  // Mark as stale after staleTime
  useEffect(() => {
    if (!data) return

    const timer = setTimeout(() => {
      setIsStale(true)
    }, staleTime)

    return () => clearTimeout(timer)
  }, [data, staleTime])

  // Clean up old cache entries
  useEffect(() => {
    const cleanup = setInterval(() => {
      const now = Date.now()
      for (const [key, entry] of queryCache.entries()) {
        if (now - entry.timestamp > cacheTime) {
          queryCache.delete(key)
        }
      }
    }, 60000) // Clean up every minute

    return () => clearInterval(cleanup)
  }, [cacheTime])

  return {
    data,
    error,
    isLoading,
    isStale,
    refetch,
    retry: retryQuery
  }
}

import { useState, useEffect } from 'react'
import { createClient } from '@supabase/supabase-js'
import { getSupabaseConfig } from '@/config/supabase'

interface ShoutOutData {
  date: string
  count: number
  revenue: number
}

interface UseShoutOutTimeseriesReturn {
  data: ShoutOutData[]
  loading: boolean
  error: string | null
  refetch: () => void
}

export function useShoutOutTimeseries(timeRange: '7d' | '30d' | '90d' = '7d'): UseShoutOutTimeseriesReturn {
  const [data, setData] = useState<ShoutOutData[]>([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)

  const fetchData = async () => {
    try {
      setLoading(true)
      setError(null)

      const config = getSupabaseConfig()
      if (!config?.url || !config?.publishableKey) {
        throw new Error('Invalid Supabase configuration')
      }

      const supabase = createClient(config.url, config.publishableKey)
      
      // Calculate date range
      const now = new Date()
      const startDate = new Date()
      switch (timeRange) {
        case '7d':
          startDate.setDate(now.getDate() - 7)
          break
        case '30d':
          startDate.setDate(now.getDate() - 30)
          break
        case '90d':
          startDate.setDate(now.getDate() - 90)
          break
      }

      // Mock data for now - replace with actual query when shout_out table exists
      const mockData: ShoutOutData[] = []
      const days = Math.ceil((now.getTime() - startDate.getTime()) / (1000 * 60 * 60 * 24))
      
      for (let i = 0; i < days; i++) {
        const date = new Date(startDate)
        date.setDate(date.getDate() + i)
        mockData.push({
          date: date.toISOString().split('T')[0],
          count: Math.floor(Math.random() * 10) + 1,
          revenue: Math.floor(Math.random() * 100) + 10,
        })
      }

      setData(mockData)
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to fetch shout out data')
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    fetchData()
  }, [timeRange])

  return {
    data,
    loading,
    error,
    refetch: fetchData,
  }
}

/**
 * Utility functions for exporting admin data to CSV/JSON
 */

export function exportToCSV(data: any[], filename: string) {
  if (!data || data.length === 0) {
    alert('No data to export')
    return
  }

  // Get headers from first object
  const headers = Object.keys(data[0])
  
  // Create CSV content
  const csvContent = [
    headers.join(','),
    ...data.map(row => 
      headers.map(header => {
        const value = row[header]
        // Handle nested objects and arrays
        if (value === null || value === undefined) return ''
        if (typeof value === 'object') return JSON.stringify(value).replace(/"/g, '""')
        return String(value).replace(/"/g, '""').replace(/,/g, ';')
      }).join(',')
    )
  ].join('\n')

  // Create blob and download
  const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' })
  const url = URL.createObjectURL(blob)
  const link = document.createElement('a')
  link.href = url
  link.download = `${filename}-${new Date().toISOString().split('T')[0]}.csv`
  link.click()
  URL.revokeObjectURL(url)
}

export function exportToJSON(data: any[], filename: string) {
  if (!data || data.length === 0) {
    alert('No data to export')
    return
  }

  const jsonContent = JSON.stringify(data, null, 2)
  const blob = new Blob([jsonContent], { type: 'application/json' })
  const url = URL.createObjectURL(blob)
  const link = document.createElement('a')
  link.href = url
  link.download = `${filename}-${new Date().toISOString().split('T')[0]}.json`
  link.click()
  URL.revokeObjectURL(url)
}

