export interface ProviderCandidate {
  provider_id: string;
  name: string;
  specialty: string;
  price_cents: number;
  rating: number;
  distance_km: number;
  eta_minutes: number;
  location: {
    lat: number;
    lon: number;
  };
}

export interface ProviderSearchCriteria {
  intent: string;
  location: {
    lat: number;
    lon: number;
  };
  when_iso?: string;
  max_distance_km?: number;
  max_price_cents?: number;
  min_rating?: number;
  limit?: number;
}

interface ProviderProfile {
  id: string;
  display_name: string | null;
  role: string;
  provider_locations: Array<{
    lat: number;
    lon: number;
  }>;
}

interface ServiceData {
  id: string;
  provider_id: string;
  title: string;
  price_cents: number;
  is_active: boolean;
}

interface ReviewData {
  provider_id: string;
  overall_quality: number | null;
}

export class ProviderMatchingService {
  /**
   * Find provider candidates based on search criteria
   */
  static async findCandidates(criteria: ProviderSearchCriteria): Promise<ProviderCandidate[]> {
    try {
      // First, find specialties that match the intent
      const { data: specialties, error: specialtyError } = await supabase
        .from('specialties')
        .select('id, name, slug')
        .or(`name.ilike.%${criteria.intent}%,slug.ilike.%${criteria.intent}%`)
        .limit(5);

      if (specialtyError) {
        console.error('Error finding specialties:', specialtyError);
        return [];
      }

      if (!specialties || specialties.length === 0) {
        // Fallback: search in services table
        return await this.searchInServices(criteria);
      }

      // Find providers with matching specialties
      const specialtyIds = specialties.map(s => s.id);
      
      const { data: vendorSpecialties, error: vsError } = await supabase
        .from('vendor_specialties')
        .select('app_user_id, specialty_id')
        .in('specialty_id', specialtyIds);

      if (vsError) {
        console.error('Error finding vendor specialties:', vsError);
        return [];
      }

      if (!vendorSpecialties || vendorSpecialties.length === 0) {
        return await this.searchInServices(criteria);
      }

      const providerIds = vendorSpecialties.map(vs => vs.app_user_id);

      // Get provider profiles and locations
      const { data: providers, error: providerError } = await supabase
        .from('profiles')
        .select(`
          id,
          display_name,
          role,
          provider_locations (
            lat,
            lon
          )
        `)
        .in('id', providerIds)
        .eq('role', 'provider')
        .eq('is_active', true) as { data: ProviderProfile[] | null; error: any };

      if (providerError) {
        console.error('Error finding providers:', providerError);
        return [];
      }

      // Get services and pricing
      const { data: services, error: serviceError } = await supabase
        .from('services')
        .select(`
          id,
          provider_id,
          title,
          price_cents,
          is_active
        `)
        .in('provider_id', providerIds)
        .eq('is_active', true) as { data: ServiceData[] | null; error: any };

      if (serviceError) {
        console.error('Error finding services:', serviceError);
        return [];
      }

      // Get provider ratings
      const { data: ratings, error: ratingError } = await supabase
        .from('reviews')
        .select(`
          provider_id,
          overall_quality
        `)
        .in('provider_id', providerIds)
        .eq('status', 'published') as { data: ReviewData[] | null; error: any };

      if (ratingError) {
        console.error('Error finding ratings:', ratingError);
      }

      // Build candidate list
      const candidates: ProviderCandidate[] = [];
      
      for (const provider of providers || []) {
        const providerServices = services?.filter(s => s.provider_id === provider.id) || [];
        const providerRatings = ratings?.filter(r => r.provider_id === provider.id) || [];
        
        if (providerServices.length === 0) continue;
        
        const location = provider.provider_locations?.[0];
        if (!location) continue;

        // Calculate distance
        const distance = this.calculateDistance(
          criteria.location.lat,
          criteria.location.lon,
          location.lat,
          location.lon
        );

        if (criteria.max_distance_km && distance > criteria.max_distance_km) {
          continue;
        }

        // Calculate average rating
        const avgRating = providerRatings.length > 0
          ? providerRatings.reduce((sum, r) => sum + (r.overall_quality || 0), 0) / providerRatings.length
          : 4.0; // Default rating

        if (criteria.min_rating && avgRating < criteria.min_rating) {
          continue;
        }

        // Use the first service for pricing (could be enhanced to find best match)
        const service = providerServices[0];
        
        if (criteria.max_price_cents && service.price_cents > criteria.max_price_cents) {
          continue;
        }

        // Estimate ETA based on distance
        const etaMinutes = Math.max(15, Math.round(distance * 2)); // 2 min per km, minimum 15 min

        candidates.push({
          provider_id: provider.id,
          name: provider.display_name || 'Provider',
          specialty: specialties.find(s => 
            vendorSpecialties.some(vs => 
              vs.app_user_id === provider.id && vs.specialty_id === s.id
            )
          )?.name || criteria.intent,
          price_cents: service.price_cents,
          rating: avgRating,
          distance_km: distance,
          eta_minutes: etaMinutes,
          location: {
            lat: location.lat,
            lon: location.lon
          }
        });
      }

      // Sort by relevance (rating, distance, price)
      candidates.sort((a, b) => {
        const scoreA = (a.rating * 0.4) + (1 / (a.distance_km + 1) * 0.4) + (1 / (a.price_cents / 100) * 0.2);
        const scoreB = (b.rating * 0.4) + (1 / (b.distance_km + 1) * 0.4) + (1 / (b.price_cents / 100) * 0.2);
        return scoreB - scoreA;
      });

      return candidates.slice(0, criteria.limit || 10);

    } catch (error) {
      console.error('Error in provider matching:', error);
      return [];
    }
  }

  /**
   * Fallback search in services table
   */
  private static async searchInServices(criteria: ProviderSearchCriteria): Promise<ProviderCandidate[]> {
    try {
      interface ServiceWithProfile {
        id: string;
        provider_id: string;
        title: string;
        price_cents: number;
        profiles: {
          id: string;
          display_name: string | null;
          role: string;
          provider_locations: Array<{
            lat: number;
            lon: number;
          }>;
        };
      }

      const { data: services, error } = await supabase
        .from('services')
        .select(`
          id,
          provider_id,
          title,
          price_cents,
          profiles!inner (
            id,
            display_name,
            role,
            provider_locations (
              lat,
              lon
            )
          )
        `)
        .or(`title.ilike.%${criteria.intent}%,description.ilike.%${criteria.intent}%`)
        .eq('is_active', true)
        .eq('profiles.role', 'provider')
        .eq('profiles.is_active', true)
        .limit(10) as { data: ServiceWithProfile[] | null; error: any };

      if (error || !services) {
        console.error('Error in fallback service search:', error);
        return [];
      }

      return services.map(service => {
        const location = service.profiles.provider_locations?.[0];
        const distance = location ? this.calculateDistance(
          criteria.location.lat,
          criteria.location.lon,
          location.lat,
          location.lon
        ) : 0;

        return {
          provider_id: service.provider_id,
          name: service.profiles.display_name || 'Provider',
          specialty: service.title,
          price_cents: service.price_cents,
          rating: 4.0, // Default rating
          distance_km: distance,
          eta_minutes: Math.max(15, Math.round(distance * 2)),
          location: location ? { lat: location.lat, lon: location.lon } : criteria.location
        };
      }).filter(c => !criteria.max_distance_km || c.distance_km <= criteria.max_distance_km)
        .filter(c => !criteria.max_price_cents || c.price_cents <= criteria.max_price_cents)
        .sort((a, b) => a.distance_km - b.distance_km)
        .slice(0, criteria.limit || 10);

    } catch (error) {
      console.error('Error in fallback search:', error);
      return [];
    }
  }

  /**
   * Calculate distance between two points using Haversine formula
   */
  private static calculateDistance(lat1: number, lon1: number, lat2: number, lon2: number): number {
    const R = 6371; // Earth's radius in kilometers
    const dLat = this.toRadians(lat2 - lat1);
    const dLon = this.toRadians(lon2 - lon1);
    
    const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) +
              Math.cos(this.toRadians(lat1)) * Math.cos(this.toRadians(lat2)) *
              Math.sin(dLon / 2) * Math.sin(dLon / 2);
    
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
  }

  private static toRadians(degrees: number): number {
    return degrees * (Math.PI / 180);
  }
}

import { createClient } from '@supabase/supabase-js'

import { supabaseAdmin as supabase } from '@/lib/supabaseProxies';

interface ReceiptData {
  booking_id: string
  customer_name: string
  provider_name: string
  service_type: string
  price_cents: number
  date_time: string
  duration_minutes: number
  receipt_number: string
}

export class ReceiptService {
  private static instance: ReceiptService

  static getInstance(): ReceiptService {
    if (!ReceiptService.instance) {
      ReceiptService.instance = new ReceiptService()
    }
    return ReceiptService.instance
  }

  /**
   * Generate receipt for a confirmed booking
   */
  async generateReceipt(bookingId: string): Promise<string> {
    try {
      // Get booking details
      const { data: booking, error: bookingError } = await supabase
        .from('bookings')
        .select(`
          *,
          customer:profiles!bookings_customer_id_fkey(full_name),
          provider:profiles!bookings_provider_id_fkey(full_name),
          quote:quotes(estimated_duration_minutes)
        `)
        .eq('id', bookingId)
        .eq('state', 'provider_confirmed')
        .single()

      if (bookingError || !booking) {
        throw new Error(`Booking not found or not confirmed: ${bookingId}`)
      }

      // Generate receipt number
      const receiptNumber = this.generateReceiptNumber(bookingId)

      // Create receipt data
      const receiptData: ReceiptData = {
        booking_id: booking.id,
        customer_name: booking.customer?.full_name || 'Unknown Customer',
        provider_name: booking.provider?.full_name || 'Unknown Provider',
        service_type: 'General Service', // Would come from service_id in real implementation
        price_cents: booking.price_cents || 0,
        date_time: booking.created_at,
        duration_minutes: booking.quote?.estimated_duration_minutes || 60,
        receipt_number: receiptNumber
      }

      // Generate receipt URL (in real implementation, this would create a PDF or HTML receipt)
      const receiptUrl = this.createReceiptUrl(receiptData)

      // Update booking with receipt URL and transition to receipt_issued
      const { error: updateError } = await supabase
        .from('bookings')
        .update({
          receipt_url: receiptUrl,
          state: 'receipt_issued'
        })
        .eq('id', bookingId)

      if (updateError) {
        throw new Error(`Failed to update booking with receipt: ${updateError.message}`)
      }

      // Log the state transition
      await supabase
        .from('booking_audit_log')
        .insert({
          booking_id: booking.id,
          from_state: 'provider_confirmed',
          to_state: 'receipt_issued',
          action: 'state_change',
          actor_type: 'system',
          actor_id: 'receipt_service',
          metadata: {
            receipt_url: receiptUrl,
            receipt_number: receiptNumber,
            generated_at: new Date().toISOString()
          }
        })

      console.log(`Receipt generated for booking ${bookingId}: ${receiptUrl}`)

      return receiptUrl

    } catch (error) {
      console.error(`Failed to generate receipt for booking ${bookingId}:`, error)
      throw error
    }
  }

  /**
   * Generate a unique receipt number
   */
  private generateReceiptNumber(bookingId: string): string {
    const timestamp = Date.now().toString(36)
    const shortId = bookingId.substring(0, 8)
    return `RCP-${timestamp}-${shortId}`.toUpperCase()
  }

  /**
   * Create receipt URL (placeholder - would generate actual receipt)
   */
  private createReceiptUrl(receiptData: ReceiptData): string {
    // In real implementation, this would:
    // 1. Generate a PDF receipt
    // 2. Upload to cloud storage
    // 3. Return the public URL

    // For now, return a placeholder URL with receipt data
    const baseUrl = process.env.NEXT_PUBLIC_APP_URL || 'https://bookiji.com'
    const encodedData = encodeURIComponent(JSON.stringify(receiptData))

    return `${baseUrl}/receipt/${receiptData.booking_id}?data=${encodedData}`
  }

  /**
   * Get receipt data for a booking
   */
  async getReceiptData(bookingId: string): Promise<ReceiptData | null> {
    try {
      const { data: booking, error } = await supabase
        .from('bookings')
        .select(`
          *,
          customer:profiles!bookings_customer_id_fkey(full_name),
          provider:profiles!bookings_provider_id_fkey(full_name),
          quote:quotes(estimated_duration_minutes)
        `)
        .eq('id', bookingId)
        .eq('state', 'receipt_issued')
        .single()

      if (error || !booking) {
        return null
      }

      return {
        booking_id: booking.id,
        customer_name: booking.customer?.full_name || 'Unknown Customer',
        provider_name: booking.provider?.full_name || 'Unknown Provider',
        service_type: 'General Service',
        price_cents: booking.price_cents || 0,
        date_time: booking.created_at,
        duration_minutes: booking.quote?.estimated_duration_minutes || 60,
        receipt_number: this.extractReceiptNumber(booking.receipt_url)
      }

    } catch (error) {
      console.error(`Failed to get receipt data for booking ${bookingId}:`, error)
      return null
    }
  }

  /**
   * Extract receipt number from receipt URL
   */
  private extractReceiptNumber(receiptUrl: string): string {
    // Extract from URL or generate a fallback
    const match = receiptUrl.match(/RCP-[A-Z0-9]+-[A-Z0-9]+/)
    return match ? match[0] : 'RCP-UNKNOWN'
  }

  /**
   * Send receipt notifications (email/SMS)
   */
  async sendReceiptNotifications(bookingId: string): Promise<void> {
    try {
      const receiptData = await this.getReceiptData(bookingId)
      if (!receiptData) {
        throw new Error(`Receipt data not found for booking ${bookingId}`)
      }

      // TODO: Implement email notification
      console.log(`Would send email receipt to customer for booking ${bookingId}`)

      // TODO: Implement SMS notification
      console.log(`Would send SMS receipt to customer for booking ${bookingId}`)

    } catch (error) {
      console.error(`Failed to send receipt notifications for booking ${bookingId}:`, error)
      // Don't throw - notifications are not critical to the core flow
    }
  }

  /**
   * Process all confirmed bookings and generate receipts
   */
  async processConfirmedBookings(): Promise<void> {
    try {
      // Get all provider_confirmed bookings without receipts
      const { data: confirmedBookings, error } = await supabase
        .from('bookings')
        .select('id, created_at')
        .eq('state', 'provider_confirmed')
        .is('receipt_url', null)
        .order('created_at', { ascending: true })

      if (error) {
        console.error('Failed to fetch confirmed bookings:', error)
        return
      }

      if (!confirmedBookings || confirmedBookings.length === 0) {
        return // No confirmed bookings to process
      }

      console.log(`Processing ${confirmedBookings.length} confirmed bookings for receipt generation`)

      for (const booking of confirmedBookings) {
        try {
          await this.generateReceipt(booking.id)
          await this.sendReceiptNotifications(booking.id)
        } catch (receiptError) {
          console.error(`Failed to process receipt for booking ${booking.id}:`, receiptError)
        }
      }

    } catch (error) {
      console.error('Error in processConfirmedBookings:', error)
    }
  }
}

