import { getServerSupabase } from '@/lib/supabaseServer'

const supabase = new Proxy({} as any, { get: (target, prop) => (getServerSupabase() as any)[prop] }) as ReturnType<typeof getServerSupabase>
import { refundPayment } from '@/lib/stripe'
import { type RefundOptions, type RefundStatus } from '@/types/booking'

export async function processRefund(
  bookingId: string,
  options: RefundOptions = {}
): Promise<{ status: RefundStatus; amount?: number; transactionId?: string; error?: string }> {
  try {
    const { data: booking, error: fetchError } = await supabase
      .from('bookings')
      .select('*')
      .eq('id', bookingId)
      .single()

    if (fetchError || !booking) {
      throw new Error(fetchError?.message || 'Booking not found')
    }

    if (!booking.payment_intent_id) {
      throw new Error('No payment intent found')
    }

    if (booking.refund_status === 'completed') {
      return {
        status: 'completed',
        amount: booking.refund_amount_cents || undefined,
        transactionId: booking.refund_transaction_id || undefined,
      }
    }

    await supabase
      .from('bookings')
      .update({
        refund_status: 'processing',
        updated_at: new Date().toISOString()
      })
      .eq('id', bookingId)

    const refund = await refundPayment(
      booking.payment_intent_id,
      undefined,
      options.idempotencyKey || booking.idempotency_key || undefined
    )

    await supabase
      .from('bookings')
      .update({
        refund_status: 'completed',
        refund_amount_cents: refund.amount,
        refund_transaction_id: refund.id,
        updated_at: new Date().toISOString()
      })
      .eq('id', bookingId)

    return { status: 'completed', amount: refund.amount, transactionId: refund.id }
  } catch (error) {
    console.error('Refund processing error:', error)

    await supabase
      .from('bookings')
      .update({
        refund_status: 'failed',
        refund_error: error instanceof Error ? error.message : 'Refund processing failed',
        updated_at: new Date().toISOString()
      })
      .eq('id', bookingId)

    return { status: 'failed', error: error instanceof Error ? error.message : 'Refund processing failed' }
  }
}

import Stripe from 'stripe';

let _stripe: Stripe | null = null;
let _isMockMode: boolean | null = null;

function getIsMockMode(): boolean {
  if (_isMockMode === null) {
    _isMockMode = !process.env.STRIPE_SECRET_KEY || process.env.NODE_ENV === 'development';
  }
  return _isMockMode;
}

function getStripe(): Stripe | null {
  if (getIsMockMode()) {
    return null;
  }
  if (!_stripe) {
    const secretKey = process.env.STRIPE_SECRET_KEY;
    if (!secretKey) {
      // Should technically be covered by isMockMode check, but for type safety
      console.warn('STRIPE_SECRET_KEY missing despite not being in mock mode. Defaulting to mock.');
      _isMockMode = true;
      return null;
    }
    _stripe = new Stripe(secretKey, {
      apiVersion: '2024-06-20',
    });
  }
  return _stripe;
}

