import Stripe from 'stripe'
import { loadStripe } from '@stripe/stripe-js'
import { getServerSupabase } from '@/lib/supabaseServer'

const supabase = new Proxy({} as any, { get: (target, prop) => (getServerSupabase() as any)[prop] }) as ReturnType<typeof getServerSupabase>

// Create a function to get Stripe instance instead of creating it at module load
let _stripe: Stripe | null = null

export function getStripeInstance(): Stripe | null {
  if (!_stripe && process.env.STRIPE_SECRET_KEY) {
    try {
      _stripe = new Stripe(process.env.STRIPE_SECRET_KEY, {
        apiVersion: '2024-06-20',
        typescript: true,
      })
    } catch (error) {
      console.warn('Failed to initialize Stripe:', error)
      return null
    }
  }
  return _stripe
}

export const stripe = getStripeInstance()

export const getStripe = () => {
  const pk = process.env.NEXT_PUBLIC_STRIPE_PUBLISHABLE_KEY
  return pk ? loadStripe(pk) : null
}

export const getStripeOrThrow = () => {
  const instance = getStripeInstance()
  if (!instance) {
    throw new Error('Stripe not configured - missing STRIPE_SECRET_KEY environment variable')
  }
  return instance
}

export async function createCommitmentFeePaymentIntent(amount: number, currency: string = 'usd', idempotencyKey?: string) {
  const instance = getStripeOrThrow()
  return instance.paymentIntents.create(
    {
      amount,
      currency,
      automatic_payment_methods: { enabled: true },
    },
    idempotencyKey ? { idempotencyKey } : undefined
  )
}

export async function createBookingPaymentIntent(amount: number, bookingId: string, customerId?: string, idempotencyKey?: string) {
  const instance = getStripeOrThrow()
  const key = idempotencyKey || `booking-${bookingId}`
  const intent = await instance.paymentIntents.create(
    {
      amount,
      currency: 'usd',
      customer: customerId,
      metadata: { bookingId },
      automatic_payment_methods: { enabled: true },
    },
    { idempotencyKey: key }
  )

  await supabase.from('bookings').update({ idempotency_key: key }).eq('id', bookingId)
  return intent
}

export async function refundPayment(paymentIntentId: string, amount?: number, idempotencyKey?: string) {
  const instance = getStripeOrThrow()
  return instance.refunds.create(
    { payment_intent: paymentIntentId, amount },
    idempotencyKey ? { idempotencyKey } : undefined
  )
}

import { cookies } from 'next/headers'
import { createServerClient } from '@supabase/ssr'
import { getSupabaseConfig } from '@/config/supabase'

/**
 * Creates a Supabase server client with proper cookie handling using getAll/setAll
 * This is the recommended approach for Next.js route handlers and server actions
 */
export async function createSupabaseServerClient() {
  const cookieStore = await cookies()
  const config = getSupabaseConfig()
  
  return createServerClient(
    config.url,
    config.publishableKey,
    {
      cookies: {
        getAll() {
          return cookieStore.getAll()
        },
        setAll(cookiesToSet) {
          try {
            cookiesToSet.forEach(({ name, value, options }) => {
              cookieStore.set(name, value, options)
            })
          } catch (error) {
            // The `setAll` method was called from a Server Component or Route Handler.
            // This can be ignored if you have middleware refreshing user sessions.
          }
        }
      }
    }
  )
}

import { createClient, SupabaseClient } from '@supabase/supabase-js'

// Cache instances to avoid creating new clients on every property access
let _adminClient: SupabaseClient | null = null
let _anonClient: SupabaseClient | null = null

export const supabaseAdmin = new Proxy({} as any, {
  get: (target, prop) => {
    if (!_adminClient) {
      const url = process.env.NEXT_PUBLIC_SUPABASE_URL
      const key = process.env.SUPABASE_SERVICE_ROLE_KEY

      if (!url || !key) {
        // Only throw if we are actually trying to use it (runtime)
        // If this happens during build, it might still throw if code tries to access properties
        // But simply importing this file won't throw.
        if (typeof window === 'undefined') {
           // We are on server
           throw new Error('Supabase admin configuration missing')
        }
        return undefined
      }
      _adminClient = createClient(url, key, {
        auth: {
          persistSession: false,
          autoRefreshToken: false,
          detectSessionInUrl: false
        }
      })
    }
    return (_adminClient as any)[prop]
  }
}) as SupabaseClient

export const supabaseAnon = new Proxy({} as any, {
  get: (target, prop) => {
    if (!_anonClient) {
      const url = process.env.NEXT_PUBLIC_SUPABASE_URL
      const key = process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY

      if (!url || !key) {
        throw new Error('Supabase anon configuration missing')
      }
      _anonClient = createClient(url, key)
    }
    return (_anonClient as any)[prop]
  }
}) as SupabaseClient

import { v4 as uuidv4 } from 'uuid'

// Core telemetry types
export interface ResilienceSignal {
  id: string
  timestamp: number
  user_id?: string
  session_id: string
  component: string
  signal_type: ResilienceSignalType
  data: Record<string, any>
}

export type ResilienceSignalType = 
  | 'optimistic_action_start'
  | 'optimistic_action_success'
  | 'optimistic_action_rollback'
  | 'retry_attempt'
  | 'retry_success'
  | 'retry_failure'
  | 'error_boundary_triggered'
  | 'error_boundary_recovered'
  | 'loading_skeleton_shown'
  | 'loading_skeleton_hidden'
  | 'debounced_click_suppressed'

// Telemetry client for sending signals
class ResilienceTelemetryClient {
  private sessionId: string
  private userId?: string
  private endpoint: string
  private batchSize: number = 10
  private batchTimeout: number = 5000
  private signalQueue: ResilienceSignal[] = []
  private batchTimer?: NodeJS.Timeout

  constructor(endpoint: string = '/api/telemetry/resilience') {
    this.sessionId = this.generateSessionId()
    this.endpoint = endpoint
    this.startBatchTimer()
  }

  private generateSessionId(): string {
    return `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`
  }

  setUserId(userId: string) {
    this.userId = userId
  }

  // Send individual signal immediately
  async sendSignal(signal: Omit<ResilienceSignal, 'id' | 'timestamp' | 'session_id'>): Promise<void> {
    const fullSignal: ResilienceSignal = {
      ...signal,
      id: uuidv4(),
      timestamp: Date.now(),
      session_id: this.sessionId,
      user_id: this.userId
    }

    // Add to batch queue
    this.signalQueue.push(fullSignal)

    // Send immediately if batch is full
    if (this.signalQueue.length >= this.batchSize) {
      await this.flushBatch()
    }
  }

  // Send batch of signals
  private async flushBatch(): Promise<void> {
    if (this.signalQueue.length === 0) return

    const signals = [...this.signalQueue]
    this.signalQueue = []

    try {
      // Use sendBeacon for reliable delivery
      if (navigator.sendBeacon) {
        const success = navigator.sendBeacon(
          this.endpoint,
          JSON.stringify({ signals })
        )
        if (!success) {
          // Fallback to fetch if sendBeacon fails
          await this.sendViaFetch(signals)
        }
      } else {
        // Fallback for older browsers
        await this.sendViaFetch(signals)
      }
    } catch (error) {
      console.warn('Failed to send resilience telemetry:', error)
      // Re-queue signals for retry
      this.signalQueue.unshift(...signals)
    }
  }

  private async sendViaFetch(signals: ResilienceSignal[]): Promise<void> {
    const response = await fetch(this.endpoint, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ signals })
    })

    if (!response.ok) {
      throw new Error(`Telemetry endpoint returned ${response.status}`)
    }
  }

  private startBatchTimer(): void {
    this.batchTimer = setInterval(() => {
      this.flushBatch()
    }, this.batchTimeout)
  }

  // Cleanup
  destroy(): void {
    if (this.batchTimer) {
      clearInterval(this.batchTimer)
    }
    this.flushBatch()
  }
}

// Global telemetry client instance
export const resilienceTelemetry = new ResilienceTelemetryClient()

// High-level telemetry functions
export const telemetry = {
  // Optimistic Actions
  optimisticActionStart: (component: string, actionId: string) => {
    resilienceTelemetry.sendSignal({
      component,
      signal_type: 'optimistic_action_start',
      data: { action_id: actionId }
    })
  },

  optimisticActionSuccess: (component: string, actionId: string, durationMs: number) => {
    resilienceTelemetry.sendSignal({
      component,
      signal_type: 'optimistic_action_success',
      data: { action_id: actionId, duration_ms: durationMs }
    })
  },

  optimisticActionRollback: (component: string, actionId: string, durationMs: number, reason: string) => {
    resilienceTelemetry.sendSignal({
      component,
      signal_type: 'optimistic_action_rollback',
      data: { 
        action_id: actionId, 
        duration_ms: durationMs, 
        rollback_reason: reason 
      }
    })
  },

  // Retry Logic
  retryAttempt: (component: string, endpoint: string, attempt: number, backoffMs: number) => {
    resilienceTelemetry.sendSignal({
      component,
      signal_type: 'retry_attempt',
      data: { 
        api_endpoint: endpoint, 
        retry_count: attempt, 
        backoff_ms: backoffMs 
      }
    })
  },

  retrySuccess: (component: string, endpoint: string, totalAttempts: number, totalDurationMs: number) => {
    resilienceTelemetry.sendSignal({
      component,
      signal_type: 'retry_success',
      data: { 
        api_endpoint: endpoint, 
        total_attempts: totalAttempts, 
        total_duration_ms: totalDurationMs 
      }
    })
  },

  retryFailure: (component: string, endpoint: string, totalAttempts: number, totalDurationMs: number, error: string) => {
    resilienceTelemetry.sendSignal({
      component,
      signal_type: 'retry_failure',
      data: { 
        api_endpoint: endpoint, 
        total_attempts: totalAttempts, 
        total_duration_ms: totalDurationMs,
        final_error: error
      }
    })
  },

  // Error Boundaries
  errorBoundaryTriggered: (component: string, errorType: string, errorMessage: string) => {
    resilienceTelemetry.sendSignal({
      component,
      signal_type: 'error_boundary_triggered',
      data: { 
        error_type: errorType, 
        error_message: errorMessage 
      }
    })
  },

  errorBoundaryRecovered: (component: string, recoveryMethod: string, recoveryTimeMs: number) => {
    resilienceTelemetry.sendSignal({
      component,
      signal_type: 'error_boundary_recovered',
      data: { 
        recovery_method: recoveryMethod, 
        recovery_time_ms: recoveryTimeMs 
      }
    })
  },

  // Loading States
  loadingSkeletonShown: (component: string) => {
    resilienceTelemetry.sendSignal({
      component,
      signal_type: 'loading_skeleton_shown',
      data: { show_time: Date.now() }
    })
  },

  loadingSkeletonHidden: (component: string, durationMs: number) => {
    resilienceTelemetry.sendSignal({
      component,
      signal_type: 'loading_skeleton_hidden',
      data: { duration_ms: durationMs }
    })
  },

  // Debounced Clicks
  debouncedClickSuppressed: (component: string, suppressionReason: string) => {
    resilienceTelemetry.sendSignal({
      component,
      signal_type: 'debounced_click_suppressed',
      data: { suppression_reason: suppressionReason }
    })
  }
}

