  if (entry.deprecation?.willDeprecate) {
    throw new Error(`Domain "${domain}" is marked deprecated${entry.deprecation.sunsetDate ? ` (sunsets ${entry.deprecation.sunsetDate})` : ""}`);
  }
  return entry;
}

export interface FraudSignal {
